  /**
   * Private helper methods
   */

  private identifyDecisionCandidates(messages: Message[]): Array<{
    messageIndex: number;
    message: Message;
    confidence: number;
    decisionText: string;
  }> {
    if (!messages || !Array.isArray(messages)) {
      return [];
    }

    const candidates: Array<{
      messageIndex: number;
      message: Message;
      confidence: number;
      decisionText: string;
    }> = [];

    for (let i = 0; i < messages.length; i++) {
      try {
        const message = messages[i];
        if (!message || !message.content || typeof message.content !== 'string') {
          continue;
        }

        const decisionIndicators = this.safeFindDecisionIndicators(message.content);

        if (decisionIndicators.length > 0) {
          for (const indicator of decisionIndicators) {
            try {
              if (indicator && isFinite(indicator.confidence) && indicator.text) {
                candidates.push({
                  messageIndex: i,
                  message,
                  confidence: Math.max(0, Math.min(1, indicator.confidence)),
                  decisionText: indicator.text
                });
              }
            } catch (indicatorError) {
              console.warn('DecisionTracker: Error processing decision indicator:', indicatorError);
              continue;
            }
          }
        }
      } catch (messageError) {
        console.warn('DecisionTracker: Error processing message for decision candidates:', messageError);
        continue;
      }
    }

    // Filter out low-confidence candidates with error handling
    return candidates.filter(c => {
      try {
        return c && c.confidence > 0.6;
      } catch (filterError) {
        return false;
      }
    });
  }

  private safeFindDecisionIndicators(content: string): Array<{ text: string; confidence: number }> {
    try {
      return this.findDecisionIndicators(content);
    } catch (error) {
      console.warn('DecisionTracker: Error finding decision indicators:', error);
      return [];
    }
  }

  private findDecisionIndicators(content: string): Array<{ text: string; confidence: number }> {
    try {
      if (!content || typeof content !== 'string') {
        return [];
      }

      const indicators: Array<{ text: string; confidence: number }> = [];
      const sentences = content.split(/[.!?]+/);

      for (const sentence of sentences) {
        try {
          const trimmed = sentence.trim();
          if (trimmed.length < 10) continue;

          const hasDecisionKeyword = this.DECISION_KEYWORDS.some(keyword => {
            try {
              return keyword && trimmed.toLowerCase().includes(keyword);
            } catch (keywordError) {
              return false;
            }
          });

          if (hasDecisionKeyword) {
            let confidence = 0.5;

            try {
              // Boost confidence for explicit decision language
              if (/\b(decided|decision|choose|chose)\b/i.test(trimmed)) {
                confidence += 0.3;
              }

              // Boost confidence for comparative language (options being weighed)
              if (/(better|worse|prefer|rather|instead)/.test(trimmed.toLowerCase())) {
                confidence += 0.2;
              }

              // Boost confidence for definitive language
              if (/(will|going to|plan to|definitely)/.test(trimmed.toLowerCase())) {
                confidence += 0.2;
              }

              // Reduce confidence for questions
              if (trimmed.includes('?')) {
                confidence -= 0.3;
              }
            } catch (confidenceError) {
              console.warn('DecisionTracker: Error calculating confidence:', confidenceError);
              confidence = 0.5; // Default confidence
            }

            if (confidence > 0.4) {
              indicators.push({
                text: trimmed,
                confidence: Math.min(1, Math.max(0, confidence))
              });
            }
          }
        } catch (sentenceError) {
          console.warn('DecisionTracker: Error processing sentence for indicators:', sentenceError);
          continue;
        }
      }

      return indicators;
    } catch (error) {
      console.warn('DecisionTracker: Error finding decision indicators:', error);
      return [];
    }
  }

  private async analyzeDecision(
    conversation: Conversation,
    messages: Message[],
    candidate: any
  ): Promise<TrackedDecision | null> {
    const decisionMessage = candidate.message;
    const messageIndex = candidate.messageIndex;

    // Get context messages around the decision
    const contextStart = Math.max(0, messageIndex - 5);
    const contextEnd = Math.min(messages.length, messageIndex + 5);
    const contextMessages = messages.slice(contextStart, contextEnd);

    // Extract decision details
    const decisionSummary = this.extractDecisionSummary(candidate.decisionText, contextMessages);
    const decisionType = this.classifyDecisionType(decisionSummary, contextMessages);

    // Analyze timeline
    const timeline = this.extractDecisionTimeline(messages, messageIndex);

    // Assess quality factors
    const clarityScore = this.assessDecisionClarity(candidate.decisionText, contextMessages);
    const confidenceLevel = this.assessConfidenceLevel(candidate.decisionText, contextMessages);
    const informationCompleteness = this.assessInformationCompleteness(contextMessages);

    // Analyze decision factors
    const factors = this.analyzeDecisionContext(contextMessages);

    // Look for outcomes
    const outcome = this.findDecisionOutcome(messages, messageIndex);

    const decision: TrackedDecision = {
      id: this.generateDecisionId(conversation.id, messageIndex),
      conversationId: conversation.id,
      decisionSummary,
      decisionType,
      context: this.extractContext(contextMessages),
      ...timeline,
      decisionMadeAt: decisionMessage.createdAt,
      clarityScore,
      confidenceLevel,
      consensusLevel: 100, // Assume single-person decision for now
      informationCompleteness,
      alternativesConsidered: factors.alternatives,
      stakeholderCount: factors.stakeholders,
      riskAssessment: factors.riskAssessed,
      timeConstraints: factors.timeConstraints,
      resourceConstraints: factors.resourceConstraints,
      outcomeScore: outcome.score,
      reversalCount: outcome.reversals,
      modificationCount: outcome.modifications,
      successFactors: this.extractSuccessFactors(contextMessages),
      failureFactors: this.extractFailureFactors(contextMessages),
      lessonsLearned: this.extractLessonsLearned(contextMessages),
      tags: this.generateDecisionTags(decisionSummary, contextMessages),
      priority: this.assessDecisionPriority(decisionSummary, contextMessages),
      complexity: this.assessDecisionComplexity(decisionSummary, contextMessages, factors)
    };

    return decision;
  }

  private extractDecisionSummary(decisionText: string, contextMessages: Message[]): string {
    // Extract a concise summary of what was decided
    let summary = decisionText.trim();

    // Clean up the text
    summary = summary.replace(/^(i've?|we've?|i'm|we're)/i, '').trim();
    summary = summary.replace(/^(decided|decision|choose|chose|selected)/i, '').trim();

    // If too short, look for more context
    if (summary.length < 20) {
      for (const message of contextMessages) {
        if (message.content.includes(decisionText.substring(0, 10))) {
          const sentences = message.content.split(/[.!?]+/);
          for (const sentence of sentences) {
            if (sentence.length > 20 && sentence.length < 200) {
              summary = sentence.trim();
              break;
            }
          }
          if (summary.length >= 20) break;
        }
      }
    }

    return summary.length > 150 ? summary.substring(0, 150) + '...' : summary;
  }

  private classifyDecisionType(
    summary: string,
    contextMessages: Message[]
  ): 'strategic' | 'tactical' | 'operational' | 'personal' {
    const content = summary + ' ' + contextMessages.map(m => m.content).join(' ');
    const lower = content.toLowerCase();

    // Strategic indicators
    if (/(long.term|strategy|vision|direction|future|goal)/.test(lower)) {
      return 'strategic';
    }

    // Tactical indicators
    if (/(approach|method|technique|plan|tactic)/.test(lower)) {
      return 'tactical';
    }

    // Operational indicators
    if (/(process|procedure|workflow|implementation|execution)/.test(lower)) {
      return 'operational';
    }

    // Default to personal for individual decisions
    return 'personal';
  }

  private extractDecisionTimeline(messages: Message[], decisionIndex: number): {
    problemIdentifiedAt?: number;
    optionsConsideredAt?: number;
  } {
    const timeline: any = {};

    // Look backwards for problem identification
    for (let i = Math.max(0, decisionIndex - 10); i < decisionIndex; i++) {
      const message = messages[i];
      if (this.PROBLEM_KEYWORDS.some(keyword => 
        message.content.toLowerCase().includes(keyword)
      )) {
        timeline.problemIdentifiedAt = message.createdAt;
        break;
      }
    }

    // Look backwards for option consideration
    for (let i = Math.max(0, decisionIndex - 5); i < decisionIndex; i++) {
      const message = messages[i];
      if (this.OPTION_KEYWORDS.some(keyword => 
        message.content.toLowerCase().includes(keyword)
      )) {
        timeline.optionsConsideredAt = message.createdAt;
        break;
      }
    }

    return timeline;
  }

  private assessDecisionClarity(decisionText: string, contextMessages: Message[]): number {
    let clarity = 50; // Base score

    // Clear decision language
    if (/\b(will|going to|decided to)\b/.test(decisionText.toLowerCase())) {
      clarity += 20;
    }

    // Specific actions mentioned
    if (/(implement|use|choose|select|go with)/.test(decisionText.toLowerCase())) {
      clarity += 15;
    }

    // Vague language reduces clarity
    if (/(maybe|perhaps|might|possibly|probably)/.test(decisionText.toLowerCase())) {
      clarity -= 20;
    }

    // Questions reduce clarity
    if (decisionText.includes('?')) {
      clarity -= 15;
    }

    // Context consistency
    const contextClarity = this.assessContextualClarity(contextMessages);
    clarity += contextClarity * 0.3;

    return Math.max(0, Math.min(100, clarity));
  }

  private assessConfidenceLevel(decisionText: string, contextMessages: Message[]): number {
    let confidence = 50;

    // Strong confidence indicators
    const strongConfidence = ['definitely', 'certainly', 'absolutely', 'confident', 'sure'];
    if (strongConfidence.some(word => decisionText.toLowerCase().includes(word))) {
      confidence += 25;
    }

    // Weak confidence indicators
    const weakConfidence = ['maybe', 'perhaps', 'not sure', 'uncertain', 'doubt'];
    if (weakConfidence.some(word => decisionText.toLowerCase().includes(word))) {
      confidence -= 25;
    }

    // Evidence of thorough consideration
    const considerationEvidence = contextMessages.filter(m => 
      this.OPTION_KEYWORDS.some(keyword => m.content.toLowerCase().includes(keyword))
    ).length;

    confidence += Math.min(20, considerationEvidence * 5);

    return Math.max(0, Math.min(100, confidence));
  }

  private assessInformationCompleteness(contextMessages: Message[]): number {
    let completeness = 30; // Base score

    // Information gathering indicators
    const infoKeywords = ['research', 'investigate', 'analyze', 'consider', 'evaluate'];
    const infoGathering = contextMessages.filter(m =>
      infoKeywords.some(keyword => m.content.toLowerCase().includes(keyword))
    ).length;

    completeness += Math.min(30, infoGathering * 6);

    // Questions asked (information seeking)
    const questions = contextMessages.filter(m => m.content.includes('?')).length;
    completeness += Math.min(25, questions * 3);

    // Detailed analysis
    const longMessages = contextMessages.filter(m => m.content.length > 200).length;
    completeness += Math.min(15, longMessages * 5);

    return Math.max(0, Math.min(100, completeness));
  }

  private analyzeDecisionContext(contextMessages: Message[]): {
    alternatives: number;
    stakeholders: number;
    riskAssessed: boolean;
    timeConstraints: boolean;
    resourceConstraints: boolean;
  } {
    const allContent = contextMessages.map(m => m.content).join(' ').toLowerCase();

    // Count alternatives mentioned
    const alternativeIndicators = ['option', 'alternative', 'choice', 'either', 'or'];
    const alternatives = alternativeIndicators.reduce((count, indicator) => {
      const matches = (allContent.match(new RegExp(indicator, 'g')) || []).length;
      return count + matches;
    }, 0);

    // Count stakeholders mentioned
    const stakeholderIndicators = ['team', 'client', 'user', 'manager', 'stakeholder'];
    const stakeholders = stakeholderIndicators.reduce((count, indicator) => {
      return count + (allContent.includes(indicator) ? 1 : 0);
    }, 0);

    // Risk assessment
    const riskKeywords = ['risk', 'danger', 'problem', 'issue', 'concern', 'downside'];
    const riskAssessed = riskKeywords.some(keyword => allContent.includes(keyword));

    // Time constraints
    const timeKeywords = ['deadline', 'urgent', 'quickly', 'asap', 'time constraint'];
    const timeConstraints = timeKeywords.some(keyword => allContent.includes(keyword));

    // Resource constraints
    const resourceKeywords = ['budget', 'cost', 'resource', 'limitation', 'constraint'];
    const resourceConstraints = resourceKeywords.some(keyword => allContent.includes(keyword));

    return {
      alternatives: Math.min(10, alternatives),
      stakeholders: Math.min(10, stakeholders),
      riskAssessed,
      timeConstraints,
      resourceConstraints
    };
  }

  private findDecisionOutcome(messages: Message[], decisionIndex: number): {
    score?: number;
    reversals: number;
    modifications: number;
  } {
    const outcome = { score: undefined as number | undefined, reversals: 0, modifications: 0 };

    // Look forward for outcome indicators
    const laterMessages = messages.slice(decisionIndex + 1);

    for (const message of laterMessages) {
      const lower = message.content.toLowerCase();

      // Success indicators
      if (['worked', 'successful', 'effective', 'good result'].some(indicator => lower.includes(indicator))) {
        outcome.score = Math.max(outcome.score || 0, 75);
      }

      // Failure indicators
      if (['failed', 'didn\'t work', 'unsuccessful', 'bad result', 'mistake'].some(indicator => lower.includes(indicator))) {
        outcome.score = Math.min(outcome.score || 100, 25);
      }

      // Reversal indicators
      if (['changed mind', 'reversed', 'different decision', 'wrong choice'].some(indicator => lower.includes(indicator))) {
        outcome.reversals++;
        outcome.score = Math.min(outcome.score || 50, 40);
      }

      // Modification indicators
      if (['modified', 'adjusted', 'tweaked', 'refined'].some(indicator => lower.includes(indicator))) {
        outcome.modifications++;
      }
    }

    return outcome;
  }

  // Additional helper methods...

  private generateDecisionId(conversationId: string, messageIndex: number): string {
    return `decision_${conversationId}_${messageIndex}`;
  }

  private extractContext(messages: Message[]): string {
    return messages.map(m => m.content).join(' ').substring(0, 500);
  }

  private extractSuccessFactors(messages: Message[]): string[] {
    const factors: string[] = [];
    const content = messages.map(m => m.content).join(' ').toLowerCase();

    if (content.includes('thorough')) factors.push('thorough analysis');
    if (content.includes('research')) factors.push('research conducted');
    if (content.includes('option') || content.includes('alternative')) factors.push('alternatives considered');
    if (content.includes('risk')) factors.push('risk assessment');

    return factors;
  }

  private extractFailureFactors(messages: Message[]): string[] {
    const factors: string[] = [];
    const content = messages.map(m => m.content).join(' ').toLowerCase();

    if (content.includes('rush')) factors.push('rushed decision');
    if (content.includes('incomplete')) factors.push('incomplete information');
    if (content.includes('pressure')) factors.push('external pressure');

    return factors;
  }

  private extractLessonsLearned(messages: Message[]): string {
    // Look for explicit learning statements
    const learningKeywords = ['learned', 'lesson', 'next time', 'should have'];
    for (const message of messages) {
      if (learningKeywords.some(keyword => message.content.toLowerCase().includes(keyword))) {
        return message.content.substring(0, 200);
      }
    }
    return '';
  }

  private generateDecisionTags(summary: string, messages: Message[]): string[] {
    const tags: string[] = [];
    const content = summary + ' ' + messages.map(m => m.content).join(' ');
    const lower = content.toLowerCase();

    if (lower.includes('technical')) tags.push('technical');
    if (lower.includes('business')) tags.push('business');
    if (lower.includes('design')) tags.push('design');
    if (lower.includes('process')) tags.push('process');

    return tags;
  }

  private assessDecisionPriority(summary: string, messages: Message[]): 'critical' | 'high' | 'medium' | 'low' {
    const content = summary + ' ' + messages.map(m => m.content).join(' ');
    const lower = content.toLowerCase();

    if (lower.includes('critical') || lower.includes('urgent')) return 'critical';
    if (lower.includes('important') || lower.includes('high priority')) return 'high';
    if (lower.includes('minor') || lower.includes('low priority')) return 'low';
    return 'medium';
  }

  private assessDecisionComplexity(
    summary: string,
    messages: Message[],
    factors: any
  ): number {
    let complexity = 3; // Base complexity

    complexity += Math.min(3, factors.alternatives);
    complexity += Math.min(2, factors.stakeholders);

    if (factors.riskAssessed) complexity += 1;
    if (factors.timeConstraints) complexity += 1;
    if (factors.resourceConstraints) complexity += 1;

    const technicalContent = (summary + ' ' + messages.map(m => m.content).join(' ')).toLowerCase();
    if (/(algorithm|architecture|system|framework)/.test(technicalContent)) {
      complexity += 2;
    }

    return Math.min(10, complexity);
  }

  private assessContextualClarity(messages: Message[]): number {
    // Assess how clear the overall context is
    let clarity = 50;

    const hasBackground = messages.some(m => m.content.toLowerCase().includes('background'));
    if (hasBackground) clarity += 15;

    const hasObjectives = messages.some(m => /(goal|objective|aim)/.test(m.content.toLowerCase()));
    if (hasObjectives) clarity += 15;

    const hasConstraints = messages.some(m => /(constraint|limitation|requirement)/.test(m.content.toLowerCase()));
    if (hasConstraints) clarity += 10;

    return clarity;
  }

  // Quality metrics calculation methods

  private createEmptyQualityMetrics(conversationId: string): DecisionQualityMetrics {
    return {
      conversationId,
      totalDecisions: 0,
      averageClarityScore: 0,
      averageConfidenceLevel: 0,
      averageInformationCompleteness: 0,
      averageDecisionTime: 0,
      averageImplementationTime: 0,
      successRate: 0,
      reversalRate: 0,
      modificationRate: 0,
      mostImportantFactors: [],
      biggestRiskFactors: []
    };
  }

  private calculateAverage(values: number[]): number {
    try {
      if (!values || !Array.isArray(values) || values.length === 0) {
        return 0;
      }

      const validValues = values.filter(v => v !== null && v !== undefined && isFinite(v));
      if (validValues.length === 0) {
        return 0;
      }

      const sum = validValues.reduce((acc, val) => acc + val, 0);
      return sum / validValues.length;
    } catch (error) {
      console.warn('DecisionTracker: Error calculating average:', error);
      return 0;
    }
  }

  private calculateDecisionTimes(decisions: TrackedDecision[]): { average: number; median: number } {
    const times = decisions
      .filter(d => d.problemIdentifiedAt && d.decisionMadeAt)
      .map(d => (d.decisionMadeAt - d.problemIdentifiedAt!) / (1000 * 60 * 60)); // hours

    if (times.length === 0) return { average: 0, median: 0 };

    const average = times.reduce((sum, time) => sum + time, 0) / times.length;
    const sorted = [...times].sort((a, b) => a - b);
    const median = sorted.length % 2 === 0
      ? (sorted[sorted.length / 2 - 1] + sorted[sorted.length / 2]) / 2
      : sorted[Math.floor(sorted.length / 2)];

    return { average, median };
  }

  private calculateImplementationTimes(decisions: TrackedDecision[]): { average: number; median: number } {
    const times = decisions
      .filter(d => d.implementationStartedAt && d.decisionMadeAt)
      .map(d => (d.implementationStartedAt! - d.decisionMadeAt) / (1000 * 60 * 60)); // hours

    if (times.length === 0) return { average: 0, median: 0 };

    const average = times.reduce((sum, time) => sum + time, 0) / times.length;
    return { average, median: average }; // Simplified
  }

  private calculateSuccessRate(decisions: TrackedDecision[]): number {
    const withOutcomes = decisions.filter(d => d.outcomeScore !== undefined);
    if (withOutcomes.length === 0) return 0;

    const successful = withOutcomes.filter(d => d.outcomeScore! > 60).length;
    return (successful / withOutcomes.length) * 100;
  }

  private calculateReversalRate(decisions: TrackedDecision[]): number {
    if (decisions.length === 0) return 0;
    const reversed = decisions.filter(d => d.reversalCount > 0).length;
    return (reversed / decisions.length) * 100;
  }

  private calculateModificationRate(decisions: TrackedDecision[]): number {
    if (decisions.length === 0) return 0;
    const modified = decisions.filter(d => d.modificationCount > 0).length;
    return (modified / decisions.length) * 100;
  }

  private analyzeDecisionFactors(decisions: TrackedDecision[]): {
    importantFactors: string[];
    riskFactors: string[];
  } {
    // Analyze what factors lead to successful decisions
    const successful = decisions.filter(d => d.outcomeScore && d.outcomeScore > 70);
    const failed = decisions.filter(d => d.outcomeScore && d.outcomeScore < 40);

    const importantFactors = [
      ...new Set(successful.flatMap(d => d.successFactors))
    ].slice(0, 5);

    const riskFactors = [
      ...new Set(failed.flatMap(d => d.failureFactors))
    ].slice(0, 5);

    return { importantFactors, riskFactors };
  }

  // Pattern detection methods

  private groupDecisionsByPattern(decisions: TrackedDecision[]): Map<string, TrackedDecision[]> {
    const groups = new Map<string, TrackedDecision[]>();

    for (const decision of decisions) {
      const pattern = this.extractDecisionPattern(decision);

      if (!groups.has(pattern)) {
        groups.set(pattern, []);
      }
      groups.get(pattern)!.push(decision);
    }

    return groups;
  }

  private extractDecisionPattern(decision: TrackedDecision): string {
    // Extract pattern based on decision characteristics
    let pattern = decision.decisionType;

    if (decision.alternativesConsidered > 3) {
      pattern += '_analytical';
    }

    if (decision.timeConstraints) {
      pattern += '_time_constrained';
    }

    if (decision.complexity > 7) {
      pattern += '_complex';
    }

    return pattern;
  }

  private analyzeDecisionPattern(patternKey: string, decisions: TrackedDecision[]): DecisionPattern {
    const successfulDecisions = decisions.filter(d => d.outcomeScore && d.outcomeScore > 60);
    const successRate = decisions.length > 0 ? successfulDecisions.length / decisions.length : 0;

    const averageQuality = this.calculateAverage(decisions.map(d => d.clarityScore));
    const averageOutcome = this.calculateAverage(
      decisions.filter(d => d.outcomeScore !== undefined).map(d => d.outcomeScore!)
    );

    const allFactors = decisions.flatMap(d => d.successFactors);
    const factorCounts = new Map<string, number>();

    for (const factor of allFactors) {
      factorCounts.set(factor, (factorCounts.get(factor) || 0) + 1);
    }

    const commonFactors = Array.from(factorCounts.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 3)
      .map(([factor]) => factor);

    return {
      pattern: patternKey,
      description: this.generatePatternDescription(patternKey, decisions),
      frequency: decisions.length,
      successRate: Math.round(successRate * 100) / 100,
      averageQuality: Math.round(averageQuality),
      averageOutcome: averageOutcome > 0 ? Math.round(averageOutcome) : undefined,
      commonFactors,
      successIndicators: this.identifySuccessIndicators(successfulDecisions),
      riskFactors: this.identifyRiskFactors(decisions.filter(d => 
        d.outcomeScore !== undefined && d.outcomeScore < 40
      )),
      typicalContext: this.extractTypicalContext(decisions),
      optimalConditions: this.identifyOptimalConditions(successfulDecisions),
      confidence: Math.min(0.95, Math.log(decisions.length) / Math.log(10))
    };
  }

  private generatePatternDescription(patternKey: string, decisions: TrackedDecision[]): string {
    return `Decision pattern: ${patternKey.replace(/_/g, ' ')} (${decisions.length} occurrences)`;
  }

  private identifySuccessIndicators(successfulDecisions: TrackedDecision[]): string[] {
    const indicators = new Set<string>();

    for (const decision of successfulDecisions) {
      if (decision.informationCompleteness > 70) indicators.add('thorough information gathering');
      if (decision.alternativesConsidered > 2) indicators.add('multiple alternatives considered');
      if (decision.riskAssessment) indicators.add('risk assessment conducted');
    }

    return Array.from(indicators);
  }

  private identifyRiskFactors(failedDecisions: TrackedDecision[]): string[] {
    const riskFactors = new Set<string>();

    for (const decision of failedDecisions) {
      if (decision.timeConstraints) riskFactors.add('time pressure');
      if (decision.informationCompleteness < 40) riskFactors.add('insufficient information');
      if (decision.alternativesConsidered < 2) riskFactors.add('limited alternatives');
    }

    return Array.from(riskFactors);
  }

  private extractTypicalContext(decisions: TrackedDecision[]): string {
    const _contexts = decisions.map(d => d.context);
    // In practice, would use NLP to find common themes
    return 'typical decision context'; // Simplified
  }

  private identifyOptimalConditions(successfulDecisions: TrackedDecision[]): string[] {
    const conditions: string[] = [];

    const avgInfo = this.calculateAverage(successfulDecisions.map(d => d.informationCompleteness));
    if (avgInfo > 70) conditions.push('thorough information gathering');

    const avgAlternatives = this.calculateAverage(successfulDecisions.map(d => d.alternativesConsidered));
    if (avgAlternatives > 2) conditions.push('consider multiple alternatives');

    return conditions;
  }

  // Timeline analysis methods

  private mapMessagesToPhases(messages: Message[], decision: TrackedDecision): Record<string, Message[]> {
    const phases: Record<string, Message[]> = {
      problem_identification: [],
      option_consideration: [],
      decision_making: [],
      implementation: [],
      outcome_assessment: []
    };

    for (const message of messages) {
      const messageTime = message.createdAt;
      const content = message.content.toLowerCase();

      // Classify message by phase
      if (this.PROBLEM_KEYWORDS.some(keyword => content.includes(keyword))) {
        phases.problem_identification.push(message);
      } else if (this.OPTION_KEYWORDS.some(keyword => content.includes(keyword))) {
        phases.option_consideration.push(message);
      } else if (this.DECISION_KEYWORDS.some(keyword => content.includes(keyword))) {
        phases.decision_making.push(message);
      } else if (this.OUTCOME_KEYWORDS.some(keyword => content.includes(keyword))) {
        phases.outcome_assessment.push(message);
      }

      // Also classify by timing relative to decision
      if (decision.implementationStartedAt && messageTime > decision.implementationStartedAt) {
        phases.implementation.push(message);
      }
    }

    return phases;
  }

  private analyzeDecisionPhase(
    phase: 'problem_identification' | 'option_consideration' | 'decision_making' | 'implementation' | 'outcome_assessment',
    messages: Message[]
  ): DecisionTimeline['phases'][0] {
    if (messages.length === 0) {
      throw new Error(`No messages for phase ${phase}`);
    }

    const timestamps = messages.map(m => m.createdAt);
    const startTime = Math.min(...timestamps);
    const endTime = Math.max(...timestamps);
    const duration = endTime - startTime;

    // Assess phase quality
    let quality = 50;
    const totalContent = messages.map(m => m.content).join(' ');

    switch (phase) {
      case 'problem_identification':
        if (totalContent.length > 200) quality += 20;
        if (messages.some(m => m.content.includes('?'))) quality += 15;
        break;
      case 'option_consideration': {
        const alternatives = (totalContent.match(/option|alternative|choice/gi) || []).length;
        quality += Math.min(30, alternatives * 10);
        break;
      }
      case 'decision_making':
        if (/decided|decision|choose/i.test(totalContent)) quality += 25;
        break;
    }

    // Extract evidence
    const evidence = messages.map(m => m.content.substring(0, 100));

    return {
      phase,
      timestamp: startTime,
      duration,
      quality: Math.min(100, quality),
      evidence
    };
  }

  private calculateTimelineEfficiency(phases: DecisionTimeline['phases']): number {
    if (phases.length === 0) return 0;

    // Efficiency based on appropriate time spent in each phase
    let efficiency = 50;

    // Bonus for having all key phases
    const phaseTypes = new Set(phases.map(p => p.phase));
    efficiency += phaseTypes.size * 5;

    // Penalty for excessive duration in any phase
    for (const phase of phases) {
      if (phase.duration && phase.duration > 24 * 60 * 60 * 1000) { // > 24 hours
        efficiency -= 10;
      }
    }

    return Math.max(0, Math.min(100, efficiency));
  }

  private calculateTimelineCompleteness(phases: DecisionTimeline['phases']): number {
    const expectedPhases = ['problem_identification', 'option_consideration', 'decision_making'];
    const presentPhases = new Set(phases.map(p => p.phase));

    const completeness = (presentPhases.size / expectedPhases.length) * 100;
    return Math.min(100, completeness);
  }
}

/**
 * Knowledge Gap Detector
 * 
 * Identifies and analyzes knowledge gaps in conversations:
 * - Question clustering and gap identification
 * - Topic coverage analysis
 * - Learning curve tracking
 * - Expertise mapping
 * - Gap resolution monitoring
 * - Personalized learning recommendations
 */

import { Message, Conversation } from '../../types/interfaces.js';

export interface DetectedKnowledgeGap {
  id: string;
  type: 'question' | 'topic' | 'skill' | 'concept';
  content: string;
  normalizedContent: string;

  // Gap metrics
  frequency: number;
  firstOccurrence: number;
  lastOccurrence: number;
  explorationDepth: number; // 0-100

  // Context information
  sourceConversations: string[];
  relatedQuestions: string[];
  relatedTopics: string[];

  // Analysis
  urgency: 'critical' | 'high' | 'medium' | 'low';
  resolutionComplexity: number; // 1-10
  learningPath: string[];

  // Recommendations
  suggestedActions: string[];
  recommendedResources: string[];
  estimatedLearningTime: number; // hours
}

export interface QuestionCluster {
  clusterId: string;
  centerQuestion: string;
  questions: Array<{
    content: string;
    conversationId: string;
    timestamp: number;
    similarity: number;
  }>;
  frequency: number;
  averageSimilarity: number;
  resolved: boolean;
  resolutionConfidence: number;
}

export interface TopicCoverageAnalysis {
  topic: string;
  normalizedTopic: string;

  // Coverage metrics
  mentionFrequency: number;
  explorationDepth: number; // 0-100
  coverageCompleteness: number; // 0-100

  // Time-based analysis
  firstMention: number;
  lastMention: number;
  developmentTrajectory: Array<{
    timestamp: number;
    understandingLevel: number;
  }>;

  // Gap identification
  identifiedGaps: string[];
  missingConcepts: string[];
  unclarifiedAspects: string[];

  // Relationships
  relatedTopics: string[];
  prerequisiteTopics: string[];
  dependentTopics: string[];
}

export interface LearningCurve {
  topic: string;
  dataPoints: Array<{
    timestamp: number;
    understandingLevel: number; // 0-100
    conversationId: string;
    evidence: string[];
  }>;

  // Curve characteristics
  gradient: number; // learning rate
  plateauLevel: number;
  timeToMastery: number; // estimated hours
  currentLevel: number;

  // Analysis
  learningPattern: 'rapid' | 'steady' | 'slow' | 'plateaued' | 'declining';
  challenges: string[];
  accelerators: string[];
  nextSteps: string[];
}

export interface ExpertiseDomain {
  domain: string;

  // Expertise levels
  knowledgeLevel: number; // 0-100
  confidenceLevel: number; // 0-100
  applicationLevel: number; // 0-100

  // Evidence
  strongAreas: string[];
  weakAreas: string[];
  gapAreas: string[];

  // Development
  growthPotential: number; // 0-100
  learningVelocity: number;
  priorityScore: number;
}

/**
 * Detects and analyzes knowledge gaps using NLP and clustering techniques
 */
export class KnowledgeGapDetector {
  private readonly MIN_CLUSTER_SIZE = 2;
  private readonly SIMILARITY_THRESHOLD = 0.6;
  private readonly EXPLORATION_THRESHOLD = 30; // Below this is considered a gap
  private readonly LEARNING_CURVE_MIN_POINTS = 3;

  /**
   * Detect knowledge gaps in a set of conversations
   */
  async detectGaps(conversations: Array<{
    conversation: Conversation;
    messages: Message[];
  }>): Promise<DetectedKnowledgeGap[]> {
    try {
      if (!conversations || conversations.length === 0) {
        console.warn('KnowledgeGapDetector: No conversations provided');
        return [];
      }

      // Safely extract messages with error handling
      const allMessages: Array<Message & { conversationId: string }> = [];
      for (const conversation of conversations) {
        try {
          if (!conversation?.conversation?.id || !conversation?.messages) {
            console.warn('KnowledgeGapDetector: Invalid conversation data, skipping');
            continue;
          }

          const messages = conversation.messages
            .filter(m => m && m.content && typeof m.content === 'string')
            .map(m => ({ ...m, conversationId: conversation.conversation.id }));

          allMessages.push(...messages);
        } catch (convError) {
          console.warn('KnowledgeGapDetector: Error processing conversation:', convError);
          continue;
        }
      }

      if (allMessages.length === 0) {
        console.warn('KnowledgeGapDetector: No valid messages found');
        return [];
      }

      const allGaps: DetectedKnowledgeGap[] = [];

      // Step 1: Extract questions and cluster them
      try {
        const questionClusters = await this.safeClusterQuestions(allMessages);
        const questionGaps = this.safeProcessQuestionClusters(questionClusters);
        allGaps.push(...questionGaps);
      } catch (questionError) {
        console.warn('KnowledgeGapDetector: Error clustering questions:', questionError);
      }

      // Step 2: Analyze topic coverage
      try {
        const topicCoverage = await this.safeAnalyzeTopicCoverage(allMessages);
        const topicGaps = this.safeProcessTopicCoverage(topicCoverage);
        allGaps.push(...topicGaps);
      } catch (topicError) {
        console.warn('KnowledgeGapDetector: Error analyzing topic coverage:', topicError);
      }

      // Step 3: Identify skill gaps
      try {
        const skillGaps = await this.safeIdentifySkillGaps(allMessages);
        allGaps.push(...skillGaps);
      } catch (skillError) {
        console.warn('KnowledgeGapDetector: Error identifying skill gaps:', skillError);
      }

      // Step 4: Find conceptual gaps
      try {
        const conceptGaps = await this.safeFindConceptualGaps(allMessages, []);
        allGaps.push(...conceptGaps);
      } catch (conceptError) {
        console.warn('KnowledgeGapDetector: Error finding conceptual gaps:', conceptError);
      }

      // Step 5: Analyze and enrich gaps
      for (const gap of allGaps) {
        try {
          await this.safeEnrichGapAnalysis(gap, allMessages);
        } catch (enrichError) {
          console.warn('KnowledgeGapDetector: Error enriching gap analysis:', enrichError);
        }
      }

      return this.safePrioritizeGaps(allGaps);
    } catch (error) {
      console.error('KnowledgeGapDetector: Failed to detect knowledge gaps:', {
        error: error instanceof Error ? error.message : 'Unknown error',
        conversationCount: conversations?.length
      });

      return [];
    }
  }

  /**
   * Safe wrapper methods for error handling
   */

  private async safeClusterQuestions(messages: Array<Message & { conversationId: string }>): Promise<QuestionCluster[]> {
    try {
      return await this.clusterQuestions(messages);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error clustering questions:', error);
      return [];
    }
  }

  private safeProcessQuestionClusters(clusters: QuestionCluster[]): DetectedKnowledgeGap[] {
    try {
      return this.clustersToGaps(clusters);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error processing question clusters:', error);
      return [];
    }
  }

  private async safeAnalyzeTopicCoverage(messages: Array<Message & { conversationId: string }>): Promise<TopicCoverageAnalysis[]> {
    try {
      return await this.analyzeTopicCoverage(messages);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error analyzing topic coverage:', error);
      return [];
    }
  }

  private safeProcessTopicCoverage(coverage: TopicCoverageAnalysis[]): DetectedKnowledgeGap[] {
    try {
      return this.topicsToGaps(coverage);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error processing topic coverage:', error);
      return [];
    }
  }

  private async safeIdentifySkillGaps(messages: Array<Message & { conversationId: string }>): Promise<DetectedKnowledgeGap[]> {
    try {
      return await this.identifySkillGaps(messages);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error identifying skill gaps:', error);
      return [];
    }
  }

  private async safeFindConceptualGaps(messages: Array<Message & { conversationId: string }>, topicCoverage: TopicCoverageAnalysis[]): Promise<DetectedKnowledgeGap[]> {
    try {
      return await this.findConceptualGaps(messages, topicCoverage);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error finding conceptual gaps:', error);
      return [];
    }
  }

  private async safeEnrichGapAnalysis(gap: DetectedKnowledgeGap, messages: Array<Message & { conversationId: string }>): Promise<void> {
    try {
      await this.enrichGapAnalysis(gap, messages);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error enriching gap analysis:', error);
    }
  }

  private safePrioritizeGaps(gaps: DetectedKnowledgeGap[]): DetectedKnowledgeGap[] {
    try {
      return this.prioritizeGaps(gaps);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error prioritizing gaps:', error);
      return gaps; // Return unsorted gaps as fallback
    }
  }

  /**
   * Cluster similar questions using content similarity
   */
  async clusterQuestions(messages: Array<Message & { conversationId: string }>): Promise<QuestionCluster[]> {
    try {
      const questions = this.safeExtractQuestions(messages);

      if (questions.length === 0) {
        return [];
      }

      // DBSCAN-like clustering with error handling
      const clusters: QuestionCluster[] = [];
      const processed = new Set<number>();

      for (let i = 0; i < questions.length; i++) {
        try {
          if (processed.has(i)) continue;

          const cluster: typeof questions = [questions[i]];
          processed.add(i);

          // Find similar questions
          for (let j = i + 1; j < questions.length; j++) {
            try {
              if (processed.has(j)) continue;

              const similarity = this.safeCalculateQuestionSimilarity(
                questions[i].content,
                questions[j].content
              );

              if (similarity >= this.SIMILARITY_THRESHOLD) {
                cluster.push(questions[j]);
                processed.add(j);
              }
            } catch (similarityError) {
              console.warn('KnowledgeGapDetector: Error calculating similarity:', similarityError);
              continue;
            }
          }

          // Create cluster if it has enough questions
          if (cluster.length >= this.MIN_CLUSTER_SIZE) {
            try {
              const clusterId = this.safeGenerateClusterId(cluster[0]?.content || '');
              const centerQuestion = this.safeFindCenterQuestion(cluster);
              const averageSimilarity = this.safeCalculateAverageSimilarity(cluster);
              const resolved = this.safeAssessClusterResolution(cluster, messages);
              const resolutionConfidence = this.safeCalculateResolutionConfidence(cluster, messages);

              clusters.push({
                clusterId,
                centerQuestion,
                questions: cluster.map(q => ({
                  content: q.content || '',
                  conversationId: q.conversationId || 'unknown',
                  timestamp: q.createdAt || Date.now(),
                  similarity: this.safeCalculateQuestionSimilarity(q.content, cluster[0]?.content || '')
                })),
                frequency: cluster.length,
                averageSimilarity: Math.max(0, Math.min(1, averageSimilarity)),
                resolved,
                resolutionConfidence: Math.max(0, Math.min(1, resolutionConfidence))
              });
            } catch (clusterError) {
              console.warn('KnowledgeGapDetector: Error creating cluster:', clusterError);
              continue;
            }
          }
        } catch (iterationError) {
          console.warn('KnowledgeGapDetector: Error in clustering iteration:', iterationError);
          continue;
        }
      }

      return clusters.sort((a, b) => {
        try {
          return (b.frequency || 0) - (a.frequency || 0);
        } catch (sortError) {
          return 0;
        }
      });
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error in clusterQuestions:', error);
      return [];
    }
  }

  private safeExtractQuestions(messages: Array<Message & { conversationId: string }>): Array<Message & { conversationId: string }> {
    try {
      return this.extractQuestions(messages);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error extracting questions:', error);
      return [];
    }
  }

  private safeCalculateQuestionSimilarity(q1: string, q2: string): number {
    try {
      return this.calculateQuestionSimilarity(q1, q2);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error calculating question similarity:', error);
      return 0;
    }
  }

  private safeGenerateClusterId(content: string): string {
    try {
      return this.generateClusterId(content);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error generating cluster ID:', error);
      return `cluster_${Date.now()}`;
    }
  }

  private safeFindCenterQuestion(cluster: Array<Message & { conversationId: string }>): string {
    try {
      return this.findCenterQuestion(cluster);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error finding center question:', error);
      return cluster[0]?.content || 'Unknown question';
    }
  }

  private safeCalculateAverageSimilarity(cluster: Array<Message & { conversationId: string }>): number {
    try {
      return this.calculateAverageSimilarity(cluster);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error calculating average similarity:', error);
      return 0.5;
    }
  }

  private safeAssessClusterResolution(cluster: Array<Message & { conversationId: string }>, allMessages: Array<Message & { conversationId: string }>): boolean {
    try {
      return this.assessClusterResolution(cluster, allMessages);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error assessing cluster resolution:', error);
      return false;
    }
  }

  private safeCalculateResolutionConfidence(cluster: Array<Message & { conversationId: string }>, allMessages: Array<Message & { conversationId: string }>): number {
    try {
      return this.calculateResolutionConfidence(cluster, allMessages);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error calculating resolution confidence:', error);
      return 0;
    }
  }

  /**
   * Analyze topic coverage and identify gaps
   */
  async analyzeTopicCoverage(messages: Array<Message & { conversationId: string }>): Promise<TopicCoverageAnalysis[]> {
    try {
      const topicMentions = this.safeExtractTopicMentions(messages);
      const topicAnalyses: TopicCoverageAnalysis[] = [];

      for (const [topic, mentions] of topicMentions.entries()) {
        try {
          if (!topic || !mentions || mentions.length === 0) {
            continue;
          }

          const analysis = await this.safeAnalyzeTopicEvolution(topic, mentions, messages);
          if (analysis) {
            topicAnalyses.push(analysis);
          }
        } catch (topicError) {
          console.warn(`KnowledgeGapDetector: Error analyzing topic '${topic}':`, topicError);
          continue;
        }
      }

      return topicAnalyses.sort((a, b) => {
        try {
          return (a.explorationDepth || 0) - (b.explorationDepth || 0);
        } catch (sortError) {
          return 0;
        }
      });
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error analyzing topic coverage:', error);
      return [];
    }
  }

  private safeExtractTopicMentions(messages: Array<Message & { conversationId: string }>): Map<string, Array<Message & { conversationId: string }>> {
    try {
      return this.extractTopicMentions(messages);
    } catch (error) {
      console.warn('KnowledgeGapDetector: Error extracting topic mentions:', error);
      return new Map();
    }
  }

  private async safeAnalyzeTopicEvolution(
    topic: string,
    mentions: Array<Message & { conversationId: string }>,
