export interface CacheEntry<T> {
  data: T;
  timestamp: number;
  ttl: number;
  hits: number;
  size: number;
}

/**
 * Multi-layer caching system for analytics operations
 */
class AnalyticsCache {
  private memoryCache: Map<string, CacheEntry<any>> = new Map();
  private cacheStats: Map<string, { hits: number; misses: number }> = new Map();
  private maxMemoryBytes: number;
  private currentMemoryUsage: number = 0;

  constructor(maxMemoryMB: number) {
    this.maxMemoryBytes = maxMemoryMB * 1024 * 1024;
  }

  async get<T>(key: string, predictiveCacheManager?: PredictiveCacheManager): Promise<T | null> {
    const entry = this.memoryCache.get(key);

    if (!entry) {
      this.recordCacheStats(key, 'miss');
      // Record cache miss for predictive analysis
      if (predictiveCacheManager) {
        predictiveCacheManager.recordCacheAccess(key, 'default', { type: 'miss' });
      }
      return null;
    }

    // Check TTL
    if (Date.now() - entry.timestamp > entry.ttl) {
      this.memoryCache.delete(key);
      this.currentMemoryUsage -= entry.size;
      this.recordCacheStats(key, 'miss');
      // Record cache miss for predictive analysis
      if (predictiveCacheManager) {
        predictiveCacheManager.recordCacheAccess(key, 'default', { type: 'miss_expired' });
      }
      return null;
    }

    entry.hits++;
    this.recordCacheStats(key, 'hit');
    // Record cache hit for predictive analysis
    if (predictiveCacheManager) {
      predictiveCacheManager.recordCacheAccess(key, 'default', { type: 'hit' });
    }
    return entry.data;
  }

  async set<T>(key: string, value: T, ttlMinutes: number = 60): Promise<void> {
    const size = this.estimateSize(value);
    const ttl = ttlMinutes * 60 * 1000;

    // Check memory limits and evict if necessary
    if (this.currentMemoryUsage + size > this.maxMemoryBytes) {
      await this.evictLRU(size);
    }

    const entry: CacheEntry<T> = {
      data: value,
      timestamp: Date.now(),
      ttl,
      hits: 0,
      size
    };

    this.memoryCache.set(key, entry);
    this.currentMemoryUsage += size;
  }

  invalidatePattern(pattern: string): number {
    let invalidated = 0;

    for (const [key, entry] of this.memoryCache) {
      if (key.includes(pattern)) {
        this.memoryCache.delete(key);
        this.currentMemoryUsage -= entry.size;
        invalidated++;
      }
    }

    return invalidated;
  }

  getCacheStats() {
    return {
      totalEntries: this.memoryCache.size,
      memoryUsageMB: this.currentMemoryUsage / (1024 * 1024),
      hitRates: Array.from(this.cacheStats.entries()).map(([key, stats]) => ({
        key,
        hitRate: stats.hits / (stats.hits + stats.misses),
        totalRequests: stats.hits + stats.misses
      }))
    };
  }

  private recordCacheStats(key: string, type: 'hit' | 'miss'): void {
    if (!this.cacheStats.has(key)) {
      this.cacheStats.set(key, { hits: 0, misses: 0 });
    }

    const stats = this.cacheStats.get(key)!;
    if (type === 'hit') {
      stats.hits++;
    } else {
      stats.misses++;
    }
  }

  private estimateSize(value: any): number {
    // Use enhanced size estimation with object overhead calculation
    try {
      return SizeEstimator.quickEstimate(value);
    } catch (error) {
      // Fallback to simplified estimation
      console.warn('Size estimation failed, using fallback:', error);
      return 1024; // Conservative fallback
    }
  }

  private async evictLRU(requiredSpace: number): Promise<void> {
    const entries = Array.from(this.memoryCache.entries())
      .map(([key, entry]) => ({ key, entry }))
      .sort((a, b) => {
        // Sort by last access time (timestamp + hits factor)
        const aScore = a.entry.timestamp + (a.entry.hits * 1000);
        const bScore = b.entry.timestamp + (b.entry.hits * 1000);
        return aScore - bScore;
      });

    let freedSpace = 0;
    for (const { key, entry } of entries) {
      this.memoryCache.delete(key);
      this.currentMemoryUsage -= entry.size;
      freedSpace += entry.size;

      if (freedSpace >= requiredSpace) {
        break;
      }
    }
  }
}

/**
 * Optimized query executor with prepared statements and connection pooling
 */
class OptimizedQueryExecutor {
  private preparedStatements: Map<string, any> = new Map();
  private queryStats: Map<string, number[]> = new Map();

  constructor(private databaseManager: DatabaseManager) {}

  async executeQuery<T>(
    queryId: string,
    sql: string,
    params: Record<string, any>
  ): Promise<T[]> {
    const startTime = performance.now();

    try {
      // Use prepared statement for better performance
      let stmt = this.preparedStatements.get(queryId);
      if (!stmt) {
        const db = (this.databaseManager as any).getDatabase();
        stmt = db.prepare(sql);
        this.preparedStatements.set(queryId, stmt);
      }

      const result = stmt.all(params) as T[];

      // Record performance metrics
      const executionTime = performance.now() - startTime;
      this.recordQueryPerformance(queryId, executionTime);

      return result;
    } catch (error) {
      console.error(`Query execution failed for ${queryId}:`, error);
      throw error;
    }
  }

  getQueryPerformanceStats() {
    const stats = new Map<string, { avgTime: number; minTime: number; maxTime: number; count: number }>();

    for (const [queryId, times] of this.queryStats) {
      const avgTime = times.reduce((sum, t) => sum + t, 0) / times.length;
      const minTime = Math.min(...times);
      const maxTime = Math.max(...times);

      stats.set(queryId, {
        avgTime,
        minTime,
        maxTime,
        count: times.length
      });
    }

    return stats;
  }

  private recordQueryPerformance(queryId: string, executionTime: number): void {
    if (!this.queryStats.has(queryId)) {
      this.queryStats.set(queryId, []);
    }

    const times = this.queryStats.get(queryId)!;
    times.push(executionTime);

    // Keep only recent measurements
    if (times.length > 1000) {
      times.splice(0, times.length - 1000);
    }
  }
}

/**
 * Parallel processing manager for analytics operations
 */
class ParallelAnalyticsProcessor {
  private workerPool: Array<{ terminate: () => void }> = [];
  private taskQueue: Array<() => Promise<any>> = [];
  private activeTasks = 0;

  constructor(private maxWorkers: number = 4) {
    // Initialize worker pool would go here in a real implementation
    // For now, we'll use Promise-based concurrency
  }

  async processInParallel<T, R>(
    items: T[],
    processor: (item: T) => Promise<R>,
    batchSize: number = 10
  ): Promise<R[]> {
    const results: R[] = [];
    const batches = this.createBatches(items, batchSize);

    const processBatch = async (batch: T[]): Promise<R[]> => {
      return Promise.all(batch.map(processor));
    };

    // Process batches in parallel
    const batchPromises = batches.map(processBatch);
    const batchResults = await Promise.all(batchPromises);

    // Flatten results
    return batchResults.flat();
  }

  async processConversationsInParallel(
    conversations: Array<{ conversation: Conversation; messages: Message[] }>,
    analyzer: ConversationFlowAnalyzer | ProductivityAnalyzer,
    method: string
  ): Promise<unknown[]> {
    const processConversation = async (item: { conversation: Conversation; messages: Message[] }): Promise<unknown> => {
      try {
        // Type-safe method calling
        if (analyzer instanceof ConversationFlowAnalyzer && method === 'analyzeFlow') {
          return await analyzer.analyzeFlow(item.conversation, item.messages);
        } else if (analyzer instanceof ProductivityAnalyzer && method === 'analyzeConversationProductivity') {
          return await analyzer.analyzeConversationProductivity(item.conversation, item.messages);
        }
        throw new Error(`Invalid analyzer/method combination: ${analyzer.constructor.name}/${method}`);
      } catch (error) {
        console.error(`Failed to process conversation ${item.conversation.id}:`, error);
        return null;
      }
    };

    const results = await this.processInParallel(
      conversations,
      processConversation,
      Math.min(this.maxWorkers, conversations.length)
    );

    return results.filter(result => result !== null);
  }

  private createBatches<T>(items: T[], batchSize: number): T[][] {
    const batches: T[][] = [];
    for (let i = 0; i < items.length; i += batchSize) {
      batches.push(items.slice(i, i + batchSize));
    }
    return batches;
  }
}

/**
 * Memory-efficient data streaming processor
 */
class StreamingDataProcessor {
  private memoryUsage = 0;
  private readonly maxMemoryMB: number;

  constructor(maxMemoryMB: number = 200) {
    this.maxMemoryMB = maxMemoryMB;
  }

  async *processLargeDataset<T, R>(
    dataSource: AsyncIterable<T> | T[],
    processor: (item: T) => Promise<R>,
    batchSize: number = 100
  ): AsyncGenerator<R[]> {
    let batch: T[] = [];

    for await (const item of this.ensureAsyncIterable(dataSource)) {
      batch.push(item);

      if (batch.length >= batchSize || this.isMemoryPressure()) {
        const results = await Promise.all(
          batch.map(async (item) => {
            try {
              return await processor(item);
            } catch (error) {
              console.error('Processing error:', error);
              return null;
            }
          })
        );

        yield results.filter(r => r !== null) as R[];
        batch = [];

        // Force garbage collection if available
        if (global.gc) {
          global.gc();
        }
      }
    }

    // Process remaining items
    if (batch.length > 0) {
      const results = await Promise.all(batch.map(processor));
      yield results;
    }
  }

  private async *ensureAsyncIterable<T>(source: AsyncIterable<T> | T[]): AsyncGenerator<T> {
    if (Symbol.asyncIterator in source) {
      yield* source as AsyncIterable<T>;
    } else {
      for (const item of source as T[]) {
        yield item;
      }
    }
  }

  private isMemoryPressure(): boolean {
    const usage = process.memoryUsage();
    const heapUsedMB = usage.heapUsed / (1024 * 1024);
    return heapUsedMB > this.maxMemoryMB * 0.8;
  }
}

/**
 * Optimized algorithms for specific analytics operations
 */
class OptimizedAlgorithms {
  /**
   * Optimized Tarjan's algorithm with early termination
   */
  static findStronglyConnectedComponentsOptimized(
    graph: Map<string, Set<string>>,
    maxComponents: number = 100
  ): string[][] {
    if (graph.size === 0) return [];

    const index = new Map<string, number>();
    const lowlink = new Map<string, number>();
    const onStack = new Set<string>();
    const stack: string[] = [];
    const sccs: string[][] = [];
    let currentIndex = 0;

    const strongConnect = (node: string) => {
      if (sccs.length >= maxComponents) return; // Early termination

      index.set(node, currentIndex);
      lowlink.set(node, currentIndex);
      currentIndex++;
      stack.push(node);
      onStack.add(node);

      const neighbors = graph.get(node) || new Set();
      for (const neighbor of neighbors) {
        if (sccs.length >= maxComponents) break; // Early termination

        if (!index.has(neighbor)) {
          strongConnect(neighbor);
          lowlink.set(node, Math.min(
            lowlink.get(node)!,
            lowlink.get(neighbor)!
          ));
        } else if (onStack.has(neighbor)) {
          lowlink.set(node, Math.min(
            lowlink.get(node)!,
            index.get(neighbor)!
          ));
        }
      }

      if (lowlink.get(node) === index.get(node)) {
        const scc: string[] = [];
        let w: string;
        do {
          w = stack.pop()!;
          onStack.delete(w);
          scc.push(w);
        } while (w !== node);
        sccs.push(scc);
      }
    };

    // Process nodes in degree order for better performance
    const nodesByDegree = Array.from(graph.keys())
      .map(node => ({ node, degree: (graph.get(node) || new Set()).size }))
      .sort((a, b) => b.degree - a.degree);

    for (const { node } of nodesByDegree) {
      if (!index.has(node) && sccs.length < maxComponents) {
        strongConnect(node);
      }
    }

    return sccs;
  }

  /**
   * Optimized clustering with spatial indexing
   */
  static optimizedClustering<T>(
    items: T[],
    similarityFunction: (a: T, b: T) => number,
    threshold: number = 0.6,
    maxClusters: number = 50
  ): T[][] {
    if (items.length === 0) return [];

    const clusters: T[][] = [];
    const processed = new Set<number>();

    // Pre-calculate similarity matrix for frequently accessed pairs
    const similarityCache = new Map<string, number>();

    const getCachedSimilarity = (i: number, j: number): number => {
      const key = `${Math.min(i, j)}-${Math.max(i, j)}`;
      if (similarityCache.has(key)) {
        return similarityCache.get(key)!;
      }

      const similarity = similarityFunction(items[i], items[j]);
      similarityCache.set(key, similarity);
      return similarity;
    };

    for (let i = 0; i < items.length && clusters.length < maxClusters; i++) {
      if (processed.has(i)) continue;

      const cluster = [items[i]];
      processed.add(i);

      // Find similar items using optimized search
      for (let j = i + 1; j < items.length; j++) {
        if (processed.has(j)) continue;

        const similarity = getCachedSimilarity(i, j);
        if (similarity >= threshold) {
          cluster.push(items[j]);
          processed.add(j);
        }
      }

      if (cluster.length >= 2) {
        clusters.push(cluster);
      }
    }

    return clusters;
  }

  /**
   * Optimized topic extraction with memoization
   */
  static extractTopicsWithMemoization(
    messages: Message[],
    cache: Map<string, string[]> = new Map()
  ): Map<string, Message[]> {
    const topicMap = new Map<string, Message[]>();

    for (const message of messages) {
      // Use enhanced cache key generation for content-based operations
      const cacheKey = CacheKeys.topicExtraction(message.content);
      let topics: string[];

      if (cache.has(cacheKey)) {
        topics = cache.get(cacheKey)!;
      } else {
        topics = this.extractMessageTopics(message.content);
        cache.set(cacheKey, topics);
      }

      for (const topic of topics) {
        if (!topicMap.has(topic)) {
          topicMap.set(topic, []);
        }
        topicMap.get(topic)!.push(message);
      }
    }

    return topicMap;
  }

  private static generateCacheKey(content: string): string {
    // Use enhanced content-based cache key generation
    return CacheKeyGenerator.generateContentKey('legacy_topic', content, {
      algorithm: 'sha1',
      maxLength: 200
    });
  }

  private static extractMessageTopics(content: string): string[] {
    // Simplified topic extraction - in production would use more sophisticated NLP
    const topics: string[] = [];
    const sentences = content.split(/[.!?]+/);

    for (const sentence of sentences) {
      const words = sentence.toLowerCase().split(/\s+/)
        .filter(w => w.length > 4 && !this.isStopWord(w));

      // Extract bigrams
      for (let i = 0; i < words.length - 1; i++) {
        const bigram = words.slice(i, i + 2).join(' ');
        if (this.isSignificantBigram(bigram)) {
          topics.push(bigram);
        }
      }
    }

    return [...new Set(topics)];
  }

  private static isStopWord(word: string): boolean {
    const stopWords = new Set([
      'the', 'be', 'to', 'of', 'and', 'a', 'in', 'that', 'have',
      'for', 'not', 'with', 'he', 'as', 'you', 'do', 'at', 'this'
    ]);
    return stopWords.has(word);
  }

  private static isSignificantBigram(bigram: string): boolean {
    return bigram.length > 8 && !bigram.includes('that') && !bigram.includes('this');
  }
}

/**
 * Main performance optimizer class
 */
export class AnalyticsPerformanceOptimizer {
  private cache: AnalyticsCache;
  private queryExecutor: OptimizedQueryExecutor;
  private parallelProcessor: ParallelAnalyticsProcessor;
  private streamProcessor: StreamingDataProcessor;
  private metrics: PerformanceMetrics;
  private config: OptimizationConfig;
  private predictiveCacheManager?: PredictiveCacheManager;

  constructor(
    private databaseManager: DatabaseManager,
    private analyticsEngine?: AnalyticsEngine,
    config: Partial<OptimizationConfig> = {}
  ) {
    this.config = {
      enableQueryCaching: true,
      enableMemoryOptimization: true,
      enableParallelProcessing: true,
      maxMemoryUsageMB: 200,
      queryCacheTTLMinutes: 60,
      parallelWorkers: 4,
      batchSize: 50,
      enablePerformanceMonitoring: true,
      enablePredictiveCaching: false,
      predictiveCache: DEFAULT_PREDICTIVE_CACHE_CONFIG,
      ...config
    };

    this.cache = new AnalyticsCache(this.config.maxMemoryUsageMB);
    this.queryExecutor = new OptimizedQueryExecutor(databaseManager);
    this.parallelProcessor = new ParallelAnalyticsProcessor(this.config.parallelWorkers);
    this.streamProcessor = new StreamingDataProcessor(this.config.maxMemoryUsageMB);

    this.metrics = {
      queryExecutionTimes: new Map(),
      cacheHitRates: new Map(),
      memoryUsage: { current: 0, peak: 0, gcEvents: 0 },
      algorithmPerformance: {
        averageCircularityTime: 0,
        averageClusteringTime: 0,
        averageFlowAnalysisTime: 0
      },
      parallelizationEfficiency: {
        parallelTasks: 0,
        speedupFactor: 1,
        efficiency: 1
      }
    };

    // Initialize predictive caching if enabled
    if (this.config.enablePredictiveCaching && this.analyticsEngine) {
      this.predictiveCacheManager = new PredictiveCacheManager(
        this.databaseManager,
        this.analyticsEngine,
        {
          ...DEFAULT_PREDICTIVE_CACHE_CONFIG,
          ...this.config.predictiveCache
        }
      );
    }

    this.startPerformanceMonitoring();
  }

  /**
   * Optimize conversation flow analysis with caching and parallel processing
   */
  async optimizeFlowAnalysis(
    conversations: Array<{ conversation: Conversation; messages: Message[] }>,
    analyzer: ConversationFlowAnalyzer
  ): Promise<any[]> {
    // Generate collision-resistant cache key
    const cacheKey = CacheKeys.flowAnalysis(conversations);

    // Check cache first
    if (this.config.enableQueryCaching) {
      const cached = await this.cache.get<any[]>(cacheKey, this.predictiveCacheManager);
      if (cached) {
        return cached;
      }
    }

    const startTime = performance.now();
    let results: any[];

    if (this.config.enableParallelProcessing && conversations.length > 10) {
      // Use parallel processing for large datasets
      results = await this.parallelProcessor.processConversationsInParallel(
        conversations,
        analyzer,
        'analyzeFlow'
      );
    } else {
      // Sequential processing for smaller datasets
      results = [];
      for (const item of conversations) {
        try {
          const result = await analyzer.analyzeFlow(item.conversation, item.messages);
          results.push(result);
        } catch (error) {
          console.error(`Flow analysis failed for conversation ${item.conversation.id}:`, error);
        }
      }
    }

    // Cache results
    if (this.config.enableQueryCaching) {
      await this.cache.set(cacheKey, results, this.config.queryCacheTTLMinutes);
    }

    // Record metrics
    const executionTime = performance.now() - startTime;
    this.metrics.algorithmPerformance.averageFlowAnalysisTime = 
      (this.metrics.algorithmPerformance.averageFlowAnalysisTime + executionTime) / 2;

    return results;
  }

  /**
   * Optimize productivity analysis with streaming for large datasets
   */
  async optimizeProductivityAnalysis(
    conversations: Array<{ conversation: Conversation; messages: Message[] }>,
    analyzer: ProductivityAnalyzer
  ): Promise<any[]> {
    const results: any[] = [];

    if (conversations.length > 1000) {
      // Use streaming for very large datasets
      const processConversation = async (item: { conversation: Conversation; messages: Message[] }) => {
        return await analyzer.analyzeConversationProductivity(item.conversation, item.messages);
      };

      for await (const batch of this.streamProcessor.processLargeDataset(
        conversations,
        processConversation,
        this.config.batchSize
      )) {
        results.push(...batch);
      }
    } else {
      // Use parallel processing for medium datasets
      const processedResults = await this.parallelProcessor.processConversationsInParallel(
        conversations,
        analyzer,
        'analyzeConversationProductivity'
      );
      results.push(...processedResults);
    }

    return results;
  }

  /**
   * Optimize knowledge gap detection with advanced clustering
   */
  async optimizeKnowledgeGapDetection(
    conversations: Array<{ conversation: Conversation; messages: Message[] }>,
    detector: KnowledgeGapDetector
  ): Promise<any[]> {
    // Generate collision-resistant cache key
    const cacheKey = CacheKeys.knowledgeGapDetection(conversations);

    if (this.config.enableQueryCaching) {
      const cached = await this.cache.get<any[]>(cacheKey, this.predictiveCacheManager);
      if (cached) {
        return cached;
      }
    }

    const startTime = performance.now();

    // Use optimized clustering algorithm
    const allMessages = conversations.flatMap(c => 
      c.messages.map(m => ({ ...m, conversationId: c.conversation.id }))
    );

    // Extract questions with memoization
    const questions = allMessages.filter(m => 
      m.role === 'user' && 
      m.content.includes('?') &&
      m.content.trim().length > 10
    );

    // Use optimized clustering
    const clusters = OptimizedAlgorithms.optimizedClustering(
      questions,
      (q1, q2) => this.calculateQuestionSimilarity(q1.content, q2.content),
      0.6,
      50
    );

    // Process gaps with the detector
    const gaps = await detector.detectGaps(conversations);

    // Cache results
    if (this.config.enableQueryCaching) {
      await this.cache.set(cacheKey, gaps, this.config.queryCacheTTLMinutes);
    }

    const executionTime = performance.now() - startTime;
    this.metrics.algorithmPerformance.averageClusteringTime = executionTime;

    return gaps;
  }

  /**
   * Optimize decision tracking with pattern caching
   */
  async optimizeDecisionTracking(
    conversations: Array<{ conversation: Conversation; messages: Message[] }>,
    tracker: DecisionTracker
  ): Promise<any[]> {
    const results: any[] = [];

    // Process in parallel batches
    const processBatch = async (batch: Array<{ conversation: Conversation; messages: Message[] }>) => {
      const batchResults: any[] = [];

      for (const item of batch) {
        try {
          const decisions = await tracker.trackDecisions(item.conversation, item.messages);
          batchResults.push(...decisions);
        } catch (error) {
          console.error(`Decision tracking failed for conversation ${item.conversation.id}:`, error);
        }
      }

      return batchResults;
    };

    // Split into batches and process in parallel
    const batches = this.createBatches(conversations, this.config.batchSize);
    const batchPromises = batches.map(processBatch);
    const batchResults = await Promise.all(batchPromises);

    results.push(...batchResults.flat());
    return results;
  }

  /**
   * Optimize database queries with connection pooling
   */
  async optimizeQuery<T>(
    queryId: string,
    sql: string,
    params: Record<string, any>
  ): Promise<T[]> {
    // Generate collision-resistant cache key with parameter normalization
    const cacheKey = CacheKeyGenerator.generateQueryKey(queryId, sql, params);

    if (this.config.enableQueryCaching) {
      const cached = await this.cache.get<T[]>(cacheKey, this.predictiveCacheManager);
      if (cached) {
        return cached;
      }
    }

    const result = await this.queryExecutor.executeQuery<T>(queryId, sql, params);

    if (this.config.enableQueryCaching) {
      await this.cache.set(cacheKey, result, this.config.queryCacheTTLMinutes);
    }

    return result;
  }

  /**
   * Get comprehensive performance report
   */
  getPerformanceReport(): {
    metrics: PerformanceMetrics;
    cacheStats: any;
    queryStats: any;
    predictiveCaching?: any;
    recommendations: string[];
  } {
    const queryStats = this.queryExecutor.getQueryPerformanceStats();
    const cacheStats = this.cache.getCacheStats();
    const predictiveCachingStatus = this.getPredictiveCachingStatus();

    const recommendations = this.generateOptimizationRecommendations(
      queryStats, 
      cacheStats, 
      predictiveCachingStatus
    );

    const report: any = {
      metrics: this.metrics,
      cacheStats,
      queryStats: Object.fromEntries(queryStats),
      recommendations
    };

    if (predictiveCachingStatus.enabled) {
      report.predictiveCaching = predictiveCachingStatus;
    }

    return report;
  }

  /**
   * Initialize predictive caching system
   */
  async initializePredictiveCaching(): Promise<void> {
    if (!this.predictiveCacheManager) {
      if (!this.analyticsEngine) {
        throw new Error('Analytics engine required for predictive caching');
      }

      this.predictiveCacheManager = new PredictiveCacheManager(
        this.databaseManager,
        this.analyticsEngine,
        {
          ...DEFAULT_PREDICTIVE_CACHE_CONFIG,
          ...this.config.predictiveCache
        }
      );
    }

    await this.predictiveCacheManager.initialize();
  }

  /**
   * Enable or disable predictive caching at runtime
   */
  async configurePredictiveCaching(
    enabled: boolean,
    config?: Partial<PredictiveCacheConfig>
  ): Promise<void> {
    this.config.enablePredictiveCaching = enabled;

    if (enabled && this.analyticsEngine) {
      if (!this.predictiveCacheManager) {
        this.predictiveCacheManager = new PredictiveCacheManager(
          this.databaseManager,
          this.analyticsEngine,
          {
            ...DEFAULT_PREDICTIVE_CACHE_CONFIG,
            ...this.config.predictiveCache,
            ...config
          }
        );
        await this.predictiveCacheManager.initialize();
      } else if (config) {
        this.predictiveCacheManager.updateConfiguration(config);
      }
    } else if (!enabled && this.predictiveCacheManager) {
      this.predictiveCacheManager.shutdown();
    }
  }

  /**
   * Manually trigger predictive cache warming
   */
  async triggerPredictiveCacheWarming(): Promise<CachePrediction[]> {
    if (!this.predictiveCacheManager || !this.config.enablePredictiveCaching) {
      return [];
    }

    return await this.predictiveCacheManager.triggerPredictiveWarming();
  }

  /**
   * Get predictive caching system status and metrics
   */
  getPredictiveCachingStatus(): {
    enabled: boolean;
    status: any | null;
    recommendations: string[];
  } {
    const enabled = this.config.enablePredictiveCaching && !!this.predictiveCacheManager;
    const status = enabled ? this.predictiveCacheManager!.getSystemStatus() : null;

    const recommendations: string[] = [];

    if (!enabled) {
      recommendations.push('Consider enabling predictive caching to improve performance');
    } else if (status) {
      // Analyze predictive cache performance
      if (status.warming.efficiency < 0.6) {
        recommendations.push('Predictive cache accuracy is low - consider adjusting prediction thresholds');
      }

      if (status.recentActivity.requestsPerHour > 100 && !status.enabled) {
        recommendations.push('High cache request volume detected - predictive caching could provide significant benefits');
      }

      if (status.patterns.averageConfidence < 0.5) {
        recommendations.push('Pattern confidence is low - allow more time for pattern learning');
      }

      if (status.warming.queueSize > 20) {
        recommendations.push('Large warming queue detected - consider increasing resource thresholds');
      }
    }

    return {
      enabled,
      status,
      recommendations
    };
  }

  /**
   * Validate prediction accuracy by checking if predicted cache entries were actually requested
   */
  async validatePredictionAccuracy(timeWindowHours: number = 24): Promise<{
    totalPredictions: number;
    accuratePredictions: number;
    accuracy: number;
    topPredictedQueries: Array<{ query: string; predicted: boolean; actual: boolean }>;
  }> {
    if (!this.predictiveCacheManager) {
      return {
        totalPredictions: 0,
        accuratePredictions: 0,
        accuracy: 0,
        topPredictedQueries: []
      };
    }

    const status = this.predictiveCacheManager.getSystemStatus();
    const warmingStats = status.warming;

    // In a real implementation, this would track predictions vs actual requests
    // For now, we'll use the warming efficiency as a proxy
    const totalPredictions = warmingStats.stats.successful + warmingStats.stats.failed;
    const accuratePredictions = warmingStats.stats.successful;
    const accuracy = totalPredictions > 0 ? accuratePredictions / totalPredictions : 0;

    return {
      totalPredictions,
      accuratePredictions,
      accuracy,
      topPredictedQueries: [] // Would be populated with actual tracking data
    };
  }

  /**
   * Clear caches and reset performance counters
   */
  resetPerformanceState(): void {
    this.cache.invalidatePattern('');

    if (this.predictiveCacheManager) {
      // Don't shutdown completely, just clear patterns to allow re-learning
      this.predictiveCacheManager.updateConfiguration({ 
        learningEnabled: false 
      });
      setTimeout(() => {
        if (this.predictiveCacheManager) {
          this.predictiveCacheManager.updateConfiguration({ 
            learningEnabled: true 
          });
        }
      }, 1000);
    }

    this.metrics = {
      queryExecutionTimes: new Map(),
      cacheHitRates: new Map(),
      memoryUsage: { current: 0, peak: 0, gcEvents: 0 },
      algorithmPerformance: {
        averageCircularityTime: 0,
        averageClusteringTime: 0,
        averageFlowAnalysisTime: 0
      },
      parallelizationEfficiency: {
        parallelTasks: 0,
        speedupFactor: 1,
        efficiency: 1
      }
    };
  }

  private calculateQuestionSimilarity(q1: string, q2: string): number {
    // Simple Jaccard similarity for demonstration
    const set1 = new Set(q1.toLowerCase().split(/\s+/));
    const set2 = new Set(q2.toLowerCase().split(/\s+/));

    const intersection = new Set([...set1].filter(x => set2.has(x)));
    const union = new Set([...set1, ...set2]);

    return intersection.size / union.size;
  }

  private createBatches<T>(items: T[], batchSize: number): T[][] {
    const batches: T[][] = [];
    for (let i = 0; i < items.length; i += batchSize) {
      batches.push(items.slice(i, i + batchSize));
    }
    return batches;
  }

  private startPerformanceMonitoring(): void {
    if (!this.config.enablePerformanceMonitoring) return;

    setInterval(() => {
      const memUsage = process.memoryUsage();
      this.metrics.memoryUsage.current = memUsage.heapUsed;
      this.metrics.memoryUsage.peak = Math.max(
        this.metrics.memoryUsage.peak,
        memUsage.heapUsed
      );
    }, 30000); // Every 30 seconds
  }

  private generateOptimizationRecommendations(
    queryStats: any,
    cacheStats: any,
    predictiveCachingStatus?: any
  ): string[] {
    const recommendations: string[] = [];

    // Analyze query performance
    for (const [queryId, stats] of Object.entries(queryStats as any)) {
      if ((stats as any).avgTime > 1000) { // > 1 second
        recommendations.push(`Query ${queryId} is slow (avg: ${(stats as any).avgTime.toFixed(2)}ms) - consider optimization`);
      }
    }

    // Analyze cache performance
    const lowHitRateKeys = cacheStats.hitRates.filter((hr: any) => hr.hitRate < 0.5);
    if (lowHitRateKeys.length > 0) {
      recommendations.push(`Low cache hit rates detected - review caching strategy for: ${lowHitRateKeys.map((k: any) => k.key).join(', ')}`);
    }

    // Memory usage recommendations
    if (this.metrics.memoryUsage.current > this.config.maxMemoryUsageMB * 0.8 * 1024 * 1024) {
      recommendations.push('High memory usage detected - consider increasing batch size limits or memory limits');
    }

    // Predictive caching recommendations
    if (predictiveCachingStatus) {
      recommendations.push(...predictiveCachingStatus.recommendations);
    }

    return recommendations;
  }
}

/**
 * Analytics Resource Manager
 * 
 * Comprehensive resource management for analytics operations:
 * - Database connection pooling and lifecycle management
 * - Memory cleanup and garbage collection optimization
 * - Background maintenance tasks and data retention
 * - Performance monitoring and alerting
 * - Resource usage optimization and throttling
 * - Automatic cleanup of temporary data and caches
 */

import { DatabaseManager } from '../../storage/Database.js';
import { AnalyticsPerformanceOptimizer } from './AnalyticsPerformanceOptimizer.js';

export interface ResourceManagerConfig {
  // Database settings
  maxDatabaseConnections: number;
  connectionTimeoutMs: number;
  idleConnectionTimeoutMs: number;

  // Memory management
  maxMemoryUsageMB: number;
  memoryCleanupThresholdMB: number;
  gcForceIntervalMs: number;

  // Maintenance settings
  maintenanceIntervalMs: number;
  dataRetentionDays: number;
  tempDataCleanupIntervalMs: number;

  // Performance monitoring
  performanceMonitoringIntervalMs: number;
  alertThresholds: {
    memoryUsageMB: number;
    queryTimeMs: number;
    errorRate: number;
  };

  // Resource throttling
  enableThrottling: boolean;
  maxConcurrentOperations: number;
  operationQueueSize: number;
}

export interface ResourceUsageStats {
  memory: {
    heapUsed: number;
    heapTotal: number;
    external: number;
    rss: number;
  };
  database: {
    activeConnections: number;
    totalQueries: number;
    averageQueryTime: number;
    slowQueries: number;
  };
  performance: {
    cacheHitRate: number;
    averageResponseTime: number;
    errorRate: number;
    throughput: number;
  };
  maintenance: {
    lastCleanup: number;
    dataRetained: number;
    tempDataSize: number;
  };
}

export interface MaintenanceTask {
  name: string;
  description: string;
  priority: 'critical' | 'high' | 'medium' | 'low';
  intervalMs: number;
  lastRun: number;
  nextRun: number;
  execute: () => Promise<void>;
}

/**
 * Comprehensive resource manager for analytics operations
 */
export class AnalyticsResourceManager {
  private config: ResourceManagerConfig;
  private maintenanceTasks: MaintenanceTask[] = [];
  private cleanupIntervals: NodeJS.Timeout[] = [];
  private performanceStats: Map<string, number[]> = new Map();
  private alertCallbacks: Array<(alert: ResourceAlert) => void> = [];
  private operationQueue: Array<() => Promise<any>> = [];
  private activeOperations = 0;
  private isShuttingDown = false;

  constructor(
    private databaseManager: DatabaseManager,
    private optimizer?: AnalyticsPerformanceOptimizer,
    config: Partial<ResourceManagerConfig> = {}
  ) {
    this.config = {
      maxDatabaseConnections: 10,
      connectionTimeoutMs: 30000,
      idleConnectionTimeoutMs: 60000,
      maxMemoryUsageMB: 500,
      memoryCleanupThresholdMB: 400,
      gcForceIntervalMs: 60000,
      maintenanceIntervalMs: 3600000, // 1 hour
      dataRetentionDays: 90,
      tempDataCleanupIntervalMs: 900000, // 15 minutes
      performanceMonitoringIntervalMs: 30000, // 30 seconds
      alertThresholds: {
        memoryUsageMB: 450,
        queryTimeMs: 5000,
        errorRate: 0.05
      },
      enableThrottling: true,
      maxConcurrentOperations: 5,
      operationQueueSize: 100,
      ...config
    };

    this.initializeMaintenanceTasks();
    this.startResourceMonitoring();
  }

  /**
   * Start the resource manager
   */
  start(): void {
    console.log('Starting Analytics Resource Manager...');

    // Start performance monitoring
    this.cleanupIntervals.push(
      setInterval(() => this.monitorPerformance(), this.config.performanceMonitoringIntervalMs)
    );

    // Start memory monitoring and cleanup
    this.cleanupIntervals.push(
      setInterval(() => this.monitorAndCleanupMemory(), this.config.gcForceIntervalMs)
    );

    // Start maintenance tasks
    this.cleanupIntervals.push(
      setInterval(() => this.runMaintenanceTasks(), this.config.maintenanceIntervalMs)
    );

    // Start temporary data cleanup
    this.cleanupIntervals.push(
      setInterval(() => this.cleanupTemporaryData(), this.config.tempDataCleanupIntervalMs)
    );

    console.log('Analytics Resource Manager started successfully');
  }

  /**
   * Stop the resource manager gracefully
   */
  async shutdown(): Promise<void> {
    console.log('Shutting down Analytics Resource Manager...');
    this.isShuttingDown = true;

    // Clear all intervals
    this.cleanupIntervals.forEach(interval => clearInterval(interval));
    this.cleanupIntervals = [];

    // Wait for active operations to complete
    let attempts = 0;
    while (this.activeOperations > 0 && attempts < 30) {
      console.log(`Waiting for ${this.activeOperations} active operations to complete...`);
      await this.sleep(1000);
      attempts++;
    }

    // Force cleanup if operations are still running
    if (this.activeOperations > 0) {
      console.warn(`Force shutting down with ${this.activeOperations} operations still running`);
    }

    // Run final cleanup
    await this.runFinalCleanup();

    console.log('Analytics Resource Manager shutdown complete');
  }

  /**
   * Execute operation with resource management
   */
  async executeWithResourceManagement<T>(
    operation: () => Promise<T>,
    operationName: string = 'anonymous',
    priority: 'high' | 'medium' | 'low' = 'medium'
  ): Promise<T> {
    if (this.isShuttingDown) {
      throw new Error('Resource manager is shutting down');
    }

    // Check if we can execute immediately or need to queue
    if (this.activeOperations >= this.config.maxConcurrentOperations) {
      if (this.operationQueue.length >= this.config.operationQueueSize) {
        throw new Error('Operation queue is full - system is overloaded');
      }

      // Queue the operation
      return new Promise<T>((resolve, reject) => {
        const queuedOperation = async () => {
          try {
            const result = await this.executeOperationWithMonitoring(operation, operationName);
            resolve(result);
          } catch (error) {
            reject(error);
          }
        };

        if (priority === 'high') {
          this.operationQueue.unshift(queuedOperation);
        } else {
          this.operationQueue.push(queuedOperation);
        }
      });
    }

    return await this.executeOperationWithMonitoring(operation, operationName);
  }

  /**
   * Get current resource usage statistics
   */
  getResourceUsageStats(): ResourceUsageStats {
    const memoryUsage = process.memoryUsage();
    const performanceReport = this.optimizer?.getPerformanceReport();

    return {
      memory: {
        heapUsed: memoryUsage.heapUsed,
        heapTotal: memoryUsage.heapTotal,
        external: memoryUsage.external,
        rss: memoryUsage.rss
      },
      database: {
        activeConnections: 1, // Simplified for SQLite
        totalQueries: this.getStatSum('total_queries') || 0,
        averageQueryTime: this.getStatAverage('query_time') || 0,
        slowQueries: this.getStatSum('slow_queries') || 0
      },
      performance: {
        cacheHitRate: this.getStatAverage('cache_hit_rate') || 0,
        averageResponseTime: this.getStatAverage('response_time') || 0,
        errorRate: this.getStatAverage('error_rate') || 0,
        throughput: this.getStatAverage('throughput') || 0
      },
      maintenance: {
        lastCleanup: this.getStatLatest('last_cleanup') || 0,
        dataRetained: this.getStatSum('data_retained') || 0,
        tempDataSize: this.getStatLatest('temp_data_size') || 0
      }
    };
  }

  /**
   * Register alert callback
   */
  onAlert(callback: (alert: ResourceAlert) => void): void {
    this.alertCallbacks.push(callback);
  }

  /**
   * Force memory cleanup
   */
  async forceMemoryCleanup(): Promise<{ beforeMB: number; afterMB: number; freedMB: number }> {
    const beforeMemory = process.memoryUsage().heapUsed;

    // Clear optimizer caches if available
    if (this.optimizer) {
      this.optimizer.resetPerformanceState();
    }

    // Clear internal caches
    this.clearInternalCaches();

    // Force garbage collection if available
    if (global.gc) {
      global.gc();
    }

    const afterMemory = process.memoryUsage().heapUsed;
    const freedMB = (beforeMemory - afterMemory) / (1024 * 1024);

    console.log(`Memory cleanup: freed ${freedMB.toFixed(2)} MB`);

    return {
      beforeMB: beforeMemory / (1024 * 1024),
      afterMB: afterMemory / (1024 * 1024),
      freedMB
    };
  }

  /**
   * Run database maintenance
   */
  async runDatabaseMaintenance(): Promise<void> {
    console.log('Running database maintenance...');

    try {
      const db = this.databaseManager.getDatabase();

      // Update database statistics
      db.exec('ANALYZE');

      // Optimize the database
      db.exec('PRAGMA optimize');

      // Clean up old analytics data
      const cutoffTime = Date.now() - (this.config.dataRetentionDays * 24 * 60 * 60 * 1000);

      // Clean up old conversation analytics
      const cleanupAnalytics = db.prepare(`
        DELETE FROM conversation_analytics 
        WHERE analyzed_at < ?
      `);
      const analyticsDeleted = cleanupAnalytics.run(cutoffTime).changes;

      // Clean up old productivity patterns
      const cleanupPatterns = db.prepare(`
        DELETE FROM productivity_patterns 
        WHERE window_end < ?
      `);
      const patternsDeleted = cleanupPatterns.run(cutoffTime).changes;

      // Clean up resolved knowledge gaps older than retention period
      const cleanupGaps = db.prepare(`
        DELETE FROM knowledge_gaps 
        WHERE resolved = TRUE AND resolution_date < ?
      `);
      const gapsDeleted = cleanupGaps.run(cutoffTime).changes;

      console.log(`Database maintenance completed: removed ${analyticsDeleted} analytics records, ${patternsDeleted} pattern records, ${gapsDeleted} resolved gaps`);

      this.recordStat('data_retained', analyticsDeleted + patternsDeleted + gapsDeleted);
      this.recordStat('last_cleanup', Date.now());

    } catch (error) {
      console.error('Database maintenance failed:', error);
      this.triggerAlert({
        type: 'error',
        severity: 'high',
        message: 'Database maintenance failed',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  }

  private initializeMaintenanceTasks(): void {
    this.maintenanceTasks = [
      {
        name: 'database_maintenance',
        description: 'Clean up old data and optimize database',
        priority: 'high',
        intervalMs: 24 * 60 * 60 * 1000, // Daily
        lastRun: 0,
        nextRun: Date.now() + 60000, // Start in 1 minute
        execute: () => this.runDatabaseMaintenance()
      },
      {
        name: 'memory_optimization',
        description: 'Force garbage collection and memory cleanup',
        priority: 'medium',
        intervalMs: 2 * 60 * 60 * 1000, // Every 2 hours
        lastRun: 0,
        nextRun: Date.now() + 120000, // Start in 2 minutes
        execute: async () => {
          await this.forceMemoryCleanup();
        }
      },
      {
        name: 'performance_analysis',
        description: 'Analyze performance trends and generate recommendations',
        priority: 'medium',
        intervalMs: 6 * 60 * 60 * 1000, // Every 6 hours
        lastRun: 0,
        nextRun: Date.now() + 300000, // Start in 5 minutes
        execute: () => this.analyzePerformanceTrends()
      },
      {
        name: 'cache_optimization',
        description: 'Optimize cache strategies based on usage patterns',
        priority: 'low',
        intervalMs: 12 * 60 * 60 * 1000, // Every 12 hours
        lastRun: 0,
        nextRun: Date.now() + 600000, // Start in 10 minutes
        execute: () => this.optimizeCacheStrategies()
      }
    ];
  }

  private startResourceMonitoring(): void {
    // Monitor memory usage continuously
    const memoryMonitor = () => {
      const memoryUsage = process.memoryUsage();
      const heapUsedMB = memoryUsage.heapUsed / (1024 * 1024);

      this.recordStat('memory_usage', heapUsedMB);

      if (heapUsedMB > this.config.alertThresholds.memoryUsageMB) {
        this.triggerAlert({
          type: 'resource',
          severity: 'high',
          message: `High memory usage: ${heapUsedMB.toFixed(2)} MB`,
          details: `Memory usage exceeds threshold of ${this.config.alertThresholds.memoryUsageMB} MB`
        });
      }
    };

    // Start monitoring immediately and then periodically
    memoryMonitor();
    this.cleanupIntervals.push(
      setInterval(memoryMonitor, 10000) // Every 10 seconds
    );
  }

  private async executeOperationWithMonitoring<T>(
    operation: () => Promise<T>,
    operationName: string
  ): Promise<T> {
    this.activeOperations++;
    const startTime = performance.now();
    const startMemory = process.memoryUsage().heapUsed;

    try {
      const result = await operation();

      const executionTime = performance.now() - startTime;
      const endMemory = process.memoryUsage().heapUsed;
      const memoryDelta = endMemory - startMemory;

      // Record performance metrics
      this.recordStat('response_time', executionTime);
      this.recordStat('memory_delta', memoryDelta);
      this.recordStat('total_queries', 1);

      // Check for slow operations
      if (executionTime > this.config.alertThresholds.queryTimeMs) {
        this.recordStat('slow_queries', 1);
        this.triggerAlert({
          type: 'performance',
          severity: 'medium',
          message: `Slow operation detected: ${operationName}`,
          details: `Execution time: ${executionTime.toFixed(2)}ms`
        });
      }

      return result;

    } catch (error) {
      this.recordStat('error_count', 1);

      this.triggerAlert({
        type: 'error',
        severity: 'high',
        message: `Operation failed: ${operationName}`,
        details: error instanceof Error ? error.message : 'Unknown error'
      });

      throw error;

    } finally {
      this.activeOperations--;
      this.processQueue();
    }
  }

  private processQueue(): void {
    if (this.operationQueue.length > 0 && this.activeOperations < this.config.maxConcurrentOperations) {
      const nextOperation = this.operationQueue.shift();
      if (nextOperation) {
        nextOperation();
      }
    }
  }

  private async monitorPerformance(): Promise<void> {
    const stats = this.getResourceUsageStats();

    // Check error rate
    const errorRate = this.calculateRecentErrorRate();
    this.recordStat('error_rate', errorRate);

    if (errorRate > this.config.alertThresholds.errorRate) {
      this.triggerAlert({
        type: 'performance',
        severity: 'high',
        message: `High error rate detected: ${(errorRate * 100).toFixed(2)}%`,
        details: `Error rate exceeds threshold of ${(this.config.alertThresholds.errorRate * 100).toFixed(2)}%`
      });
    }

    // Calculate throughput
    const recentQueries = this.getRecentStats('total_queries');
    const throughput = recentQueries.length > 0 ? recentQueries.reduce((sum, count) => sum + count, 0) / 30 : 0;
    this.recordStat('throughput', throughput);
  }

  private async monitorAndCleanupMemory(): Promise<void> {
    const memoryUsage = process.memoryUsage();
    const heapUsedMB = memoryUsage.heapUsed / (1024 * 1024);

    if (heapUsedMB > this.config.memoryCleanupThresholdMB) {
      console.log(`Memory usage (${heapUsedMB.toFixed(2)} MB) exceeds cleanup threshold, initiating cleanup...`);
      await this.forceMemoryCleanup();
    }
  }

  private async runMaintenanceTasks(): Promise<void> {
    const now = Date.now();

    for (const task of this.maintenanceTasks) {
      if (now >= task.nextRun) {
        console.log(`Running maintenance task: ${task.name}`);

        try {
          await task.execute();
          task.lastRun = now;
          task.nextRun = now + task.intervalMs;

          console.log(`Maintenance task ${task.name} completed successfully`);
        } catch (error) {
          console.error(`Maintenance task ${task.name} failed:`, error);

          // Retry in 30 minutes for failed tasks
          task.nextRun = now + (30 * 60 * 1000);

          this.triggerAlert({
            type: 'maintenance',
            severity: 'medium',
            message: `Maintenance task failed: ${task.name}`,
            details: error instanceof Error ? error.message : 'Unknown error'
          });
        }
      }
    }
  }

  private async cleanupTemporaryData(): Promise<void> {
    try {
      const db = this.databaseManager.getDatabase();

      // Clean up temporary analytics data
      const tempDataQuery = db.prepare(`
        SELECT COUNT(*) as count, SUM(LENGTH(metadata)) as size
        FROM conversation_analytics 
        WHERE analyzed_at < ?
      `);

      const cutoffTime = Date.now() - (24 * 60 * 60 * 1000); // 24 hours ago
      const tempDataInfo = tempDataQuery.get(cutoffTime) as { count: number; size: number };

      if (tempDataInfo.count > 0) {
        const cleanupQuery = db.prepare(`
          DELETE FROM conversation_analytics 
          WHERE analyzed_at < ? AND productivity_score = 0
        `);

        const deleted = cleanupQuery.run(cutoffTime).changes;
        console.log(`Cleaned up ${deleted} temporary analytics records`);

        this.recordStat('temp_data_size', tempDataInfo.size || 0);
      }

    } catch (error) {
      console.error('Temporary data cleanup failed:', error);
    }
  }

  private async runFinalCleanup(): Promise<void> {
    console.log('Running final cleanup...');

    try {
      // Final memory cleanup
      await this.forceMemoryCleanup();

      // Clear all performance statistics
      this.performanceStats.clear();

      // Final database optimization
      const db = this.databaseManager.getDatabase();
      db.exec('PRAGMA optimize');

      console.log('Final cleanup completed');
    } catch (error) {
      console.error('Final cleanup failed:', error);
    }
  }

  private async analyzePerformanceTrends(): Promise<void> {
    const trends = this.calculatePerformanceTrends();

    // Generate recommendations based on trends
    const recommendations: string[] = [];

    if (trends.memoryUsageIncrease > 20) {
      recommendations.push('Memory usage trending upward - consider more frequent cleanup');
    }

    if (trends.queryTimeIncrease > 50) {
      recommendations.push('Query performance degrading - review database indexes');
    }

    if (trends.errorRateIncrease > 10) {
      recommendations.push('Error rate increasing - investigate system stability');
    }

    if (recommendations.length > 0) {
      console.log('Performance analysis recommendations:', recommendations);
    }
  }

  private async optimizeCacheStrategies(): Promise<void> {
    if (!this.optimizer) return;

    const cacheStats = this.optimizer.getPerformanceReport().cacheStats;

    // Analyze cache effectiveness and suggest improvements
    console.log('Cache optimization analysis:', {
      totalEntries: cacheStats?.totalEntries || 0,
      averageHitRate: this.getStatAverage('cache_hit_rate') || 0
    });
  }

  private clearInternalCaches(): void {
    // Clear performance stats older than 1 hour
    const cutoff = Date.now() - (60 * 60 * 1000);

    for (const [key, values] of this.performanceStats.entries()) {
      // Keep only recent values (simplified - would use timestamps in real implementation)
      const recentValues = values.slice(-100); // Keep last 100 values
      this.performanceStats.set(key, recentValues);
    }
  }

  private recordStat(key: string, value: number): void {
    if (!this.performanceStats.has(key)) {
      this.performanceStats.set(key, []);
    }

    const stats = this.performanceStats.get(key)!;
    stats.push(value);

    // Keep only recent stats
    if (stats.length > 1000) {
      stats.splice(0, stats.length - 1000);
    }
  }

  private getStatSum(key: string): number | null {
    const stats = this.performanceStats.get(key);
    return stats ? stats.reduce((sum, val) => sum + val, 0) : null;
  }

  private getStatAverage(key: string): number | null {
    const stats = this.performanceStats.get(key);
    return stats && stats.length > 0 ? stats.reduce((sum, val) => sum + val, 0) / stats.length : null;
  }

  private getStatLatest(key: string): number | null {
    const stats = this.performanceStats.get(key);
    return stats && stats.length > 0 ? stats[stats.length - 1] : null;
  }

  private getRecentStats(key: string, periodMinutes: number = 30): number[] {
    const stats = this.performanceStats.get(key);
    if (!stats) return [];

    // Simplified - would use actual timestamps in production
    const recentCount = Math.min(stats.length, Math.floor(periodMinutes / 5)); // Assume 5-minute intervals
    return stats.slice(-recentCount);
  }

  private calculateRecentErrorRate(): number {
    const recentErrors = this.getRecentStats('error_count');
    const recentQueries = this.getRecentStats('total_queries');

    const totalErrors = recentErrors.reduce((sum, count) => sum + count, 0);
    const totalQueries = recentQueries.reduce((sum, count) => sum + count, 0);

    return totalQueries > 0 ? totalErrors / totalQueries : 0;
  }

  private calculatePerformanceTrends(): {
    memoryUsageIncrease: number;
    queryTimeIncrease: number;
    errorRateIncrease: number;
  } {
    // Simplified trend calculation
    const recentMemory = this.getRecentStats('memory_usage', 60);
    const recentQueryTime = this.getRecentStats('response_time', 60);
    const recentErrorRate = this.getRecentStats('error_rate', 60);

    return {
      memoryUsageIncrease: this.calculateTrendPercentage(recentMemory),
      queryTimeIncrease: this.calculateTrendPercentage(recentQueryTime),
      errorRateIncrease: this.calculateTrendPercentage(recentErrorRate)
    };
  }

  private calculateTrendPercentage(values: number[]): number {
    if (values.length < 2) return 0;

    const firstHalf = values.slice(0, Math.floor(values.length / 2));
    const secondHalf = values.slice(Math.floor(values.length / 2));

    const firstAvg = firstHalf.reduce((sum, val) => sum + val, 0) / firstHalf.length;
    const secondAvg = secondHalf.reduce((sum, val) => sum + val, 0) / secondHalf.length;

    return firstAvg > 0 ? ((secondAvg - firstAvg) / firstAvg) * 100 : 0;
  }

  private triggerAlert(alert: ResourceAlert): void {
    console.warn(`Resource Alert [${alert.severity}]: ${alert.message} - ${alert.details}`);

    this.alertCallbacks.forEach(callback => {
      try {
        callback(alert);
      } catch (error) {
        console.error('Alert callback failed:', error);
      }
    });
  }

  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }
}

export interface ResourceAlert {
  type: 'resource' | 'performance' | 'error' | 'maintenance';
  severity: 'low' | 'medium' | 'high' | 'critical';
  message: string;
  details: string;
  timestamp?: number;
}

/**
 * Index Monitoring Dashboard - Production Quality Performance Analytics
 * 
 * Real-time dashboard for database index performance monitoring:
 * - Live index usage statistics and effectiveness metrics
 * - Query performance trending and bottleneck identification
 * - Automated alerting and recommendation system
 * - Index maintenance scheduling and automation
 * - Performance impact analysis for write operations
 * - Historical trend analysis and capacity planning
 */

import { DatabaseManager } from '../../storage/Database.js';
import { IndexUsageMonitor, IndexUsageStats, QueryPlanAnalysis, IndexOptimizationRecommendation, PerformanceAlert } from './IndexUsageMonitor.js';

export interface DashboardMetrics {
  timestamp: number;
  overview: {
    totalIndexes: number;
    activeIndexes: number;
    unusedIndexes: number;
    slowQueries: number;
    averageQueryTime: number;
    totalDatabaseSize: number;
    indexStorageOverhead: number;
  };
  performance: {
    queryThroughput: number;
    cacheHitRate: number;
    indexEffectiveness: number;
    writePerformanceImpact: number;
    memoryUsage: number;
  };
  trends: {
    hourlyQueryTrend: Array<{ hour: number; count: number; avgTime: number }>;
    dailyPerformanceTrend: Array<{ date: string; score: number }>;
    indexUsageTrend: Array<{ index: string; trend: 'up' | 'down' | 'stable' }>;
  };
}

export interface IndexHealthReport {
  indexName: string;
  tableName: string;
  health: 'excellent' | 'good' | 'fair' | 'poor' | 'critical';
  healthScore: number;
  issues: string[];
  recommendations: string[];
  metrics: {
    usageFrequency: number;
    effectiveness: number;
    maintenanceCost: number;
    storageSize: number;
    lastUsed: number;
  };
}

export interface QueryPerformanceInsight {
  queryPattern: string;
  frequency: number;
  avgExecutionTime: number;
  impact: 'high' | 'medium' | 'low';
  optimizationPotential: number;
  suggestedIndexes: Array<{
    indexName: string;
    sql: string;
    expectedImprovement: number;
  }>;
}

export interface MaintenanceSchedule {
  task: 'reindex' | 'analyze' | 'vacuum' | 'optimize';
  target: string;
  scheduledTime: number;
  estimatedDuration: number;
  priority: 'critical' | 'high' | 'medium' | 'low';
  impact: string;
  prerequisites: string[];
}

/**
 * Comprehensive monitoring dashboard for database index performance
 */
export class IndexMonitoringDashboard {
  private monitor: IndexUsageMonitor;
  private metricsHistory: DashboardMetrics[] = [];
  private alertHistory: PerformanceAlert[] = [];
  private maintenanceSchedule: MaintenanceSchedule[] = [];

  constructor(private databaseManager: DatabaseManager) {
    this.monitor = new IndexUsageMonitor(databaseManager);
  }

  /**
   * Initialize the dashboard with monitoring
   */
  async initialize(): Promise<void> {
    await this.monitor.startMonitoring(10); // Monitor every 10 minutes

    // Schedule periodic dashboard updates
    setInterval(async () => {
      await this.updateDashboardMetrics();
    }, 5 * 60 * 1000); // Update every 5 minutes

    // Schedule maintenance planning
    setInterval(async () => {
      await this.updateMaintenanceSchedule();
    }, 60 * 60 * 1000); // Update every hour

    console.log('Index monitoring dashboard initialized');
  }

  /**
   * Get current dashboard metrics
   */
  async getCurrentMetrics(): Promise<DashboardMetrics> {
    const performanceReport = await this.monitor.generatePerformanceReport();
    const db = this.databaseManager.getConnection();

    // Calculate overview metrics
    const totalSize = await this.getDatabaseSize();
    const indexSize = await this.getIndexStorageSize();

    // Calculate performance metrics
    const queryThroughput = await this.calculateQueryThroughput();
    const cacheHitRate = await this.calculateCacheHitRate();

    const metrics: DashboardMetrics = {
      timestamp: Date.now(),
      overview: {
        totalIndexes: performanceReport.summary.totalIndexes,
        activeIndexes: performanceReport.summary.totalIndexes - performanceReport.summary.unusedIndexes,
        unusedIndexes: performanceReport.summary.unusedIndexes,
        slowQueries: performanceReport.summary.slowQueries,
        averageQueryTime: performanceReport.summary.averageQueryTime,
        totalDatabaseSize: totalSize,
        indexStorageOverhead: indexSize
      },
      performance: {
        queryThroughput,
        cacheHitRate,
        indexEffectiveness: this.calculateOverallEffectiveness(performanceReport.indexStats),
        writePerformanceImpact: performanceReport.summary.writeImpact,
        memoryUsage: process.memoryUsage().heapUsed / (1024 * 1024)
      },
      trends: {
        hourlyQueryTrend: await this.getHourlyQueryTrend(),
        dailyPerformanceTrend: await this.getDailyPerformanceTrend(),
        indexUsageTrend: await this.getIndexUsageTrend()
      }
    };

    // Store for historical analysis
    this.metricsHistory.push(metrics);

    // Keep only last 24 hours of metrics
    const cutoff = Date.now() - (24 * 60 * 60 * 1000);
    this.metricsHistory = this.metricsHistory.filter(m => m.timestamp > cutoff);

    return metrics;
  }

  /**
   * Generate comprehensive index health report
   */
  async getIndexHealthReport(): Promise<IndexHealthReport[]> {
    const indexStats = await this.monitor.getIndexUsageStats();
    const healthReports: IndexHealthReport[] = [];

    for (const [indexName, stats] of indexStats) {
      const healthReport = this.analyzeIndexHealth(stats);
      healthReports.push(healthReport);
    }

    return healthReports.sort((a, b) => {
      const healthOrder = { critical: 0, poor: 1, fair: 2, good: 3, excellent: 4 };
      return healthOrder[a.health] - healthOrder[b.health];
    });
  }

  /**
   * Analyze query performance and provide insights
   */
  async getQueryPerformanceInsights(): Promise<QueryPerformanceInsight[]> {
    const db = this.databaseManager.getConnection();
    const insights: QueryPerformanceInsight[] = [];

    // Get query patterns from monitoring data
    const queryPatterns = db.prepare(`
      SELECT 
        sql_pattern,
        COUNT(*) as frequency,
        AVG(execution_time) as avg_time,
        MAX(execution_time) as max_time,
        array_agg(table_scans) as table_scans
      FROM query_plan_analysis
      WHERE created_at > ?
      GROUP BY sql_pattern
      HAVING frequency > 5 OR avg_time > 500
      ORDER BY avg_time DESC, frequency DESC
      LIMIT 20
    `).all(Date.now() - (7 * 24 * 60 * 60 * 1000)); // Last 7 days

    for (const pattern of queryPatterns as any[]) {
      const insight = await this.analyzeQueryPattern(pattern);
      insights.push(insight);
    }

    return insights;
  }

  /**
   * Get active performance alerts with context
   */
  getActiveAlerts(): Array<PerformanceAlert & { context: any; actionPlan: string[] }> {
    const alerts = this.monitor.getPerformanceAlerts();

    return alerts.map(alert => ({
      ...alert,
      context: this.getAlertContext(alert),
      actionPlan: this.generateActionPlan(alert)
    }));
  }

  /**
   * Get scheduled maintenance tasks
   */
  getMaintenanceSchedule(): MaintenanceSchedule[] {
    return this.maintenanceSchedule.sort((a, b) => a.scheduledTime - b.scheduledTime);
  }

  /**
   * Execute maintenance task
   */
  async executeMaintenanceTask(taskId: string): Promise<{
    success: boolean;
    message: string;
    duration?: number;
    details?: any;
  }> {
    const task = this.maintenanceSchedule.find(t => 
      `${t.task}_${t.target}` === taskId
    );

    if (!task) {
      return {
        success: false,
        message: 'Maintenance task not found'
      };
    }

    const startTime = Date.now();

    try {
      await this.executeMaintenanceOperation(task);
      const duration = Date.now() - startTime;

      // Remove completed task
      this.maintenanceSchedule = this.maintenanceSchedule.filter(t => 
        `${t.task}_${t.target}` !== taskId
      );

      return {
        success: true,
        message: `Successfully completed ${task.task} on ${task.target}`,
        duration,
        details: { task }
      };
    } catch (error) {
      return {
        success: false,
        message: `Failed to execute ${task.task} on ${task.target}: ${error instanceof Error ? error.message : String(error)}`,
        details: { error: error instanceof Error ? error.message : String(error), task }
      };
    }
  }

  /**
   * Get optimization recommendations with priority scoring
   */
  async getOptimizationRecommendations(): Promise<Array<IndexOptimizationRecommendation & {
    costBenefitScore: number;
    riskAssessment: string;
  }>> {
    const recommendations = await this.monitor.generateIndexOptimizations();

    return recommendations.map(rec => ({
      ...rec,
      costBenefitScore: this.calculateCostBenefitScore(rec),
      riskAssessment: this.assessImplementationRisk(rec)
    })).sort((a, b) => b.costBenefitScore - a.costBenefitScore);
  }

  /**
   * Generate executive summary report
   */
  async generateExecutiveSummary(): Promise<{
    summary: string;
    keyMetrics: Record<string, number | string>;
    criticalIssues: Array<{ issue: string; impact: string; urgency: string }>;
    recommendations: Array<{ recommendation: string; expectedBenefit: string }>;
    trendAnalysis: {
      performanceTrend: 'improving' | 'stable' | 'degrading';
      efficiency: number;
      capacityUtilization: number;
    };
  }> {
    const currentMetrics = await this.getCurrentMetrics();
    const healthReports = await this.getIndexHealthReport();
    const alerts = this.getActiveAlerts();

    // Calculate key metrics
    const criticalIndexes = healthReports.filter(h => h.health === 'critical').length;
    const efficiencyScore = this.calculateOverallEfficiency(currentMetrics);
    const capacityUtilization = (currentMetrics.overview.indexStorageOverhead / currentMetrics.overview.totalDatabaseSize) * 100;

    // Generate summary
    const summary = this.generateSummaryText(currentMetrics, healthReports, alerts);

    // Identify critical issues
    const criticalIssues = this.identifyCriticalIssues(alerts, healthReports);

    // Get top recommendations
    const recommendations = await this.getTopRecommendations();

    return {
      summary,
      keyMetrics: {
        totalIndexes: currentMetrics.overview.totalIndexes,
        unusedIndexes: currentMetrics.overview.unusedIndexes,
        criticalIndexes,
        averageQueryTime: Math.round(currentMetrics.overview.averageQueryTime),
        efficiencyScore: Math.round(efficiencyScore * 100),
        capacityUtilization: Math.round(capacityUtilization)
      },
      criticalIssues,
      recommendations,
      trendAnalysis: {
        performanceTrend: this.analyzePerformanceTrend(),
        efficiency: efficiencyScore,
        capacityUtilization: capacityUtilization / 100
      }
    };
  }

  /**
   * Export monitoring data for external analysis
   */
  async exportMonitoringData(format: 'json' | 'csv' = 'json'): Promise<string> {
    const data = {
      timestamp: Date.now(),
      metrics: await this.getCurrentMetrics(),
      healthReports: await this.getIndexHealthReport(),
      alerts: this.getActiveAlerts(),
      recommendations: await this.getOptimizationRecommendations(),
      maintenanceSchedule: this.getMaintenanceSchedule(),
      historicalMetrics: this.metricsHistory.slice(-100) // Last 100 metrics
    };

    if (format === 'json') {
      return JSON.stringify(data, null, 2);
    } else {
      return this.convertToCSV(data);
    }
  }

  // Private implementation methods

  private async updateDashboardMetrics(): Promise<void> {
    try {
      await this.getCurrentMetrics();
    } catch (error) {
      console.error('Failed to update dashboard metrics:', error);
    }
  }

  private async updateMaintenanceSchedule(): Promise<void> {
    const healthReports = await this.getIndexHealthReport();
    const newTasks: MaintenanceSchedule[] = [];

    // Schedule reindexing for poor health indexes
    const poorIndexes = healthReports.filter(h => h.health === 'poor' || h.health === 'critical');
    for (const index of poorIndexes) {
      if (index.issues.includes('fragmentation') || index.issues.includes('outdated_stats')) {
        newTasks.push({
          task: 'reindex',
          target: index.indexName,
          scheduledTime: Date.now() + (24 * 60 * 60 * 1000), // Tomorrow
          estimatedDuration: this.estimateReindexDuration(index.metrics.storageSize),
          priority: index.health === 'critical' ? 'critical' : 'high',
          impact: `Improve ${index.indexName} effectiveness by ~${Math.round(index.metrics.effectiveness * 100)}%`,
          prerequisites: ['Low traffic period', 'Database backup']
        });
      }
    }

    // Schedule ANALYZE for frequently used indexes
    const frequentIndexes = healthReports.filter(h => h.metrics.usageFrequency > 1000);
    for (const index of frequentIndexes) {
      if (!this.isTaskScheduled('analyze', index.indexName)) {
        newTasks.push({
          task: 'analyze',
          target: index.tableName,
          scheduledTime: Date.now() + (7 * 24 * 60 * 60 * 1000), // Next week
          estimatedDuration: 5 * 60 * 1000, // 5 minutes
          priority: 'medium',
          impact: 'Update query planner statistics for better optimization',
          prerequisites: []
        });
      }
    }

    this.maintenanceSchedule.push(...newTasks);

    // Remove completed or expired tasks
    const now = Date.now();
    this.maintenanceSchedule = this.maintenanceSchedule.filter(task => 
      task.scheduledTime > now - (24 * 60 * 60 * 1000)
    );
  }

  private analyzeIndexHealth(stats: IndexUsageStats): IndexHealthReport {
    const issues: string[] = [];
    const recommendations: string[] = [];
    let healthScore = 100;

    // Check usage frequency
    if (stats.usageCount === 0) {
      issues.push('Never used');
      recommendations.push('Consider dropping this unused index');
      healthScore -= 50;
    } else if (stats.usageCount < 10) {
      issues.push('Rarely used');
      healthScore -= 20;
    }

    // Check effectiveness
    if (stats.effectivenessScore < 0.3) {
      issues.push('Low effectiveness');
      recommendations.push('Review index design and column order');
      healthScore -= 30;
    }

    // Check maintenance cost
    if (stats.maintenanceCost > 50) {
      issues.push('High maintenance cost');
      recommendations.push('Evaluate if performance benefit justifies cost');
      healthScore -= 15;
    }

    // Check last used
    const daysSinceUsed = (Date.now() - stats.lastUsed) / (24 * 60 * 60 * 1000);
    if (daysSinceUsed > 30) {
      issues.push('Not used recently');
      healthScore -= 25;
    }

    // Check size vs usage ratio
    if (stats.sizeBytes > 100000 && stats.usageCount < 100) {
      issues.push('Large size with low usage');
      recommendations.push('Consider index optimization or removal');
      healthScore -= 20;
    }

    // Determine health level
    let health: IndexHealthReport['health'];
    if (healthScore >= 80) health = 'excellent';
    else if (healthScore >= 60) health = 'good';
    else if (healthScore >= 40) health = 'fair';
    else if (healthScore >= 20) health = 'poor';
    else health = 'critical';

    return {
      indexName: stats.indexName,
      tableName: stats.tableName,
      health,
      healthScore: Math.max(0, healthScore),
      issues,
      recommendations,
      metrics: {
        usageFrequency: stats.usageCount,
        effectiveness: stats.effectivenessScore,
        maintenanceCost: stats.maintenanceCost,
        storageSize: stats.sizeBytes,
        lastUsed: stats.lastUsed
      }
    };
  }

  private async analyzeQueryPattern(pattern: any): Promise<QueryPerformanceInsight> {
    const impact = pattern.avg_time > 2000 ? 'high' : 
                  pattern.avg_time > 500 ? 'medium' : 'low';

    const optimizationPotential = Math.min(pattern.avg_time / 100, 100);

    return {
      queryPattern: pattern.sql_pattern,
      frequency: pattern.frequency,
      avgExecutionTime: pattern.avg_time,
      impact,
      optimizationPotential,
      suggestedIndexes: this.suggestIndexesForPattern(pattern)
    };
  }

  private suggestIndexesForPattern(pattern: any): Array<{
    indexName: string;
    sql: string;
    expectedImprovement: number;
  }> {
    // Simplified suggestion logic - production would be more sophisticated
    return [];
  }

  private getAlertContext(alert: PerformanceAlert): any {
    switch (alert.type) {
      case 'slow_query':
        return {
          affectedTables: alert.details.slowQueries?.map((q: any) => q.tables).flat() || [],
          queryCount: alert.details.slowQueries?.length || 0
        };
      case 'unused_index':
        return {
          storageWasted: alert.details.indexes?.length * 1024 || 0,
          maintenanceCostSaved: alert.details.indexes?.length * 10 || 0
        };
      default:
        return {};
    }
  }

  private generateActionPlan(alert: PerformanceAlert): string[] {
    const plans: Record<string, string[]> = {
      slow_query: [
        'Analyze slow query patterns',
        'Review missing indexes',
        'Consider query optimization',
        'Implement performance monitoring'
      ],
      unused_index: [
        'Review index usage statistics',
        'Verify index is not used by constraints',
        'Plan index removal during maintenance window',
        'Monitor performance after removal'
      ],
      index_degradation: [
        'Run ANALYZE to update statistics',
        'Consider REINDEX for fragmented indexes',
        'Review query pattern changes',
        'Implement more frequent monitoring'
      ],
      write_impact: [
        'Review index necessity',
        'Consider partial indexes',
        'Optimize index column order',
        'Monitor write performance trends'
      ]
    };

    return plans[alert.type] || ['Review alert details', 'Consult documentation'];
  }

  private calculateCostBenefitScore(rec: IndexOptimizationRecommendation): number {
    const impactWeight = { high: 3, medium: 2, low: 1 }[rec.expectedImpact];
    const riskPenalty = { low: 1, medium: 0.7, high: 0.3 }[rec.riskLevel];

    return rec.estimatedBenefit * impactWeight * riskPenalty / rec.implementationPriority;
  }

  private assessImplementationRisk(rec: IndexOptimizationRecommendation): string {
    if (rec.type === 'drop' && rec.riskLevel === 'low') {
      return 'Low risk - Index appears unused and not referenced by constraints';
    }
    if (rec.type === 'create') {
      return 'Medium risk - New index will impact write performance during creation';
    }
    if (rec.type === 'rebuild') {
      return 'Medium risk - Will lock table during rebuild process';
    }
    return 'Risk assessment needed';
  }

  private assessImplementationComplexity(rec: IndexOptimizationRecommendation): 'simple' | 'moderate' | 'complex' {
    if (rec.type === 'drop') return 'simple';
    if (rec.type === 'create' && rec.sql.split(',').length <= 2) return 'simple';
    if (rec.type === 'rebuild') return 'moderate';
    return 'complex';
  }

  private calculateOverallEffectiveness(indexStats: Map<string, IndexUsageStats>): number {
    if (indexStats.size === 0) return 0;

    const totalEffectiveness = Array.from(indexStats.values())
      .reduce((sum, stats) => sum + stats.effectivenessScore, 0);

    return totalEffectiveness / indexStats.size;
  }

