/**
 * Factory function for creating optimized analytics system
 */
export function createOptimizedAnalyticsSystem(
  databaseManager: DatabaseManager,
  analyticsEngine: AnalyticsEngine,
  config: Parameters<typeof createPerformanceMonitoringSetup>[2] = {}
): PerformanceMonitoringSetup {
  return createPerformanceMonitoringSetup(databaseManager, analyticsEngine, config);
}

export interface PerformanceMonitoringSetup {
  manager: ProductionPerformanceManager;
  dashboard: IndexMonitoringDashboard;
  monitor: IndexUsageMonitor;
  optimizer: AnalyticsPerformanceOptimizer;
}

export function createPerformanceMonitoringSetup(
  databaseManager: DatabaseManager,
  analyticsEngine: AnalyticsEngine,
  config: {
    monitoring?: {
      enabled?: boolean;
      intervalMinutes?: number;
      alertThresholds?: {
        slowQueryMs?: number;
        unusedIndexDays?: number;
        writeImpactThreshold?: number;
        memoryUsageThresholdMB?: number;
      };
      retentionDays?: number;
    };
    optimization?: {
      autoOptimizeEnabled?: boolean;
      autoDropUnusedIndexes?: boolean;
      maxConcurrentOptimizations?: number;
      maintenanceWindowHours?: number[];
      riskTolerance?: 'conservative' | 'moderate' | 'aggressive';
    };
    alerts?: {
      emailNotifications?: boolean;
      webhookUrl?: string;
      escalationThresholds?: {
        criticalAlertCount?: number;
        highAlertDurationMinutes?: number;
      };
    };
  } = {}
): PerformanceMonitoringSetup {
  const monitor = new IndexUsageMonitor(databaseManager);
  const dashboard = new IndexMonitoringDashboard(databaseManager);
  const optimizer = new AnalyticsPerformanceOptimizer(databaseManager, analyticsEngine, {
    enableQueryCaching: true,
    enableMemoryOptimization: true,
    enableParallelProcessing: true,
    maxMemoryUsageMB: config.monitoring?.alertThresholds?.memoryUsageThresholdMB || 500,
    queryCacheTTLMinutes: 60,
    parallelWorkers: 4,
    batchSize: 50,
    enablePerformanceMonitoring: true,
    enablePredictiveCaching: config.optimization?.riskTolerance === 'aggressive',
    predictiveCache: {
      enabled: config.optimization?.riskTolerance === 'aggressive',
      learningEnabled: true,
      warmingStrategy: {
        aggressiveness: config.optimization?.riskTolerance || 'moderate',
        maxWarmingOperationsPerMinute: 5,
        priorityWeighting: {
          frequency: 0.3,
          recency: 0.2,
          confidence: 0.3,
          userContext: 0.2
        }
      }
    }
  });

  const manager = new ProductionPerformanceManager(
    databaseManager,
    analyticsEngine,
    {
      monitoring: {
        enabled: true,
        intervalMinutes: 15,
        alertThresholds: {
          slowQueryMs: (config.monitoring?.alertThresholds?.slowQueryMs ?? 1000),
          unusedIndexDays: (config.monitoring?.alertThresholds?.unusedIndexDays ?? 30),
          writeImpactThreshold: (config.monitoring?.alertThresholds?.writeImpactThreshold ?? 0.5),
          memoryUsageThresholdMB: (config.monitoring?.alertThresholds?.memoryUsageThresholdMB ?? 500)
        },
        retentionDays: 30,
        ...config.monitoring
      },
      optimization: {
        autoOptimizeEnabled: false, // Conservative default
        autoDropUnusedIndexes: false,
        maxConcurrentOptimizations: 3,
        maintenanceWindowHours: [2, 3, 4], // 2-4 AM
        riskTolerance: 'conservative',
        ...config.optimization
      },
      alerts: {
        emailNotifications: false,
        escalationThresholds: {
          criticalAlertCount: (config.alerts?.escalationThresholds?.criticalAlertCount ?? 3),
          highAlertDurationMinutes: (config.alerts?.escalationThresholds?.highAlertDurationMinutes ?? 60)
        },
        ...config.alerts
      }
    }
  );

  return {
    manager,
    dashboard,
    monitor,
    optimizer
  };
}

/**
 * Initialize complete performance monitoring system
 */
export async function initializePerformanceMonitoring(
  setup: PerformanceMonitoringSetup,
  startMonitoring: boolean = true
): Promise<void> {
  console.log('Initializing comprehensive performance monitoring system...');

  try {
    if (startMonitoring) {
      // Initialize the production manager (which starts monitoring)
      await setup.manager.initialize();
      console.log(' Production performance manager initialized');

      // Initialize dashboard (if not already initialized by manager)
      try {
        await setup.dashboard.initialize();
        console.log(' Monitoring dashboard initialized');
      } catch (error) {
        console.warn('Dashboard already initialized or initialization warning:', error instanceof Error ? error.message : String(error));
      }
    }

    console.log(' Performance monitoring system ready');
    console.log('    Index usage tracking: Active');
    console.log('    Query performance analysis: Active');
    console.log('    Automated recommendations: Active');
    console.log('    Performance alerting: Active');

  } catch (error) {
    console.error(' Failed to initialize performance monitoring:', error);
    throw error;
  }
}

/**
 * Shutdown performance monitoring system gracefully
 */
export async function shutdownPerformanceMonitoring(
  setup: PerformanceMonitoringSetup
): Promise<void> {
  console.log('Shutting down performance monitoring system...');

  try {
    await setup.manager.shutdown();
    setup.monitor.stopMonitoring();
    setup.optimizer.resetPerformanceState();

    console.log(' Performance monitoring system shutdown complete');
  } catch (error) {
    console.error(' Error during performance monitoring shutdown:', error);
    throw error;
  }
}

/**
 * Quick health check for the monitoring system
 */
export async function performanceHealthCheck(
  setup: PerformanceMonitoringSetup
): Promise<{
  status: 'healthy' | 'warning' | 'critical';
  checks: Array<{
    component: string;
    status: 'pass' | 'warning' | 'fail';
    message: string;
  }>;
}> {
  const checks = [];

  try {
    // Check manager status
    const managerStatus = await setup.manager.getPerformanceStatus();
    checks.push({
      component: 'Performance Manager',
      status: (managerStatus.overall === 'excellent' || managerStatus.overall === 'good' ? 'pass' : 
              managerStatus.overall === 'warning' ? 'warning' : 'fail') as 'pass' | 'warning' | 'fail',
      message: `System status: ${managerStatus.overall}, Active optimizations: ${managerStatus.activeOptimizations}`
    });

    // Check dashboard metrics
    const dashboardMetrics = await setup.dashboard.getCurrentMetrics();
    const avgQueryTime = dashboardMetrics.overview.averageQueryTime;
    checks.push({
      component: 'Query Performance',
      status: (avgQueryTime < 500 ? 'pass' : avgQueryTime < 1000 ? 'warning' : 'fail') as 'pass' | 'warning' | 'fail',
      message: `Average query time: ${Math.round(avgQueryTime)}ms`
    });

    // Check index effectiveness
    const indexEffectiveness = dashboardMetrics.performance.indexEffectiveness;
    checks.push({
      component: 'Index Effectiveness',
      status: (indexEffectiveness > 0.8 ? 'pass' : indexEffectiveness > 0.6 ? 'warning' : 'fail') as 'pass' | 'warning' | 'fail',
      message: `Index effectiveness: ${Math.round(indexEffectiveness * 100)}%`
    });

    // Check for alerts
    const alerts = setup.dashboard.getActiveAlerts();
    const criticalAlerts = alerts.filter(a => a.severity === 'critical').length;
    checks.push({
      component: 'Alert Status',
      status: (criticalAlerts === 0 ? 'pass' : criticalAlerts < 3 ? 'warning' : 'fail') as 'pass' | 'warning' | 'fail',
      message: `Active alerts: ${alerts.length} (${criticalAlerts} critical)`
    });

  } catch (error) {
    checks.push({
      component: 'Health Check',
      status: 'fail' as const,
      message: `Health check failed: ${error instanceof Error ? error.message : String(error)}`
    });
  }

  // Determine overall status
  const failCount = checks.filter(c => c.status === 'fail').length;
  const warningCount = checks.filter(c => c.status === 'warning').length;

  let status: 'healthy' | 'warning' | 'critical' = 'healthy';
  if (failCount > 0) {
    status = 'critical';
  } else if (warningCount > 1) {
    status = 'warning';
  }

  return { status, checks };
}

/**
 * Analytics Repository - Base class for analytics-specific database operations
 * 
 * Provides common patterns and utilities for analytics data access:
 * - Time-based query helpers
 * - Aggregation utilities
 * - Metric calculation helpers
 * - Cache-aware operations
 */

import { BaseRepository } from '../../storage/repositories/BaseRepository.js';
import { DatabaseManager } from '../../storage/Database.js';

export interface TimeRange {
  start: number;  // timestamp in ms
  end: number;    // timestamp in ms
}

export interface PaginationOptions {
  limit?: number;
  offset?: number;
}

/**
 * Base analytics repository with common analytics operations
 */
export abstract class AnalyticsRepository extends BaseRepository {

  constructor(databaseManager: DatabaseManager) {
    super(databaseManager);
  }

  /**
   * Validate and normalize time range parameters
   */
  protected validateTimeRange(timeRange?: TimeRange): TimeRange {
    if (!timeRange) {
      // Default to last 30 days
      const end = Date.now();
      const start = end - (30 * 24 * 60 * 60 * 1000);
      return { start, end };
    }

    const start = Math.max(0, timeRange.start);
    const end = Math.max(start, timeRange.end);

    return { start, end };
  }

  /**
   * Get time window grouping SQL fragment
   */
  protected getTimeWindowGroupBy(windowType: 'hour' | 'day' | 'week' | 'month'): string {
    switch (windowType) {
      case 'hour':
        return "strftime('%Y-%m-%d %H', datetime(created_at / 1000, 'unixepoch'))";
      case 'day':
        return "DATE(created_at / 1000, 'unixepoch')";
      case 'week':
        return "strftime('%Y-%W', datetime(created_at / 1000, 'unixepoch'))";
      case 'month':
        return "strftime('%Y-%m', datetime(created_at / 1000, 'unixepoch'))";
      default:
        return "DATE(created_at / 1000, 'unixepoch')";
    }
  }

  /**
   * Calculate weighted average for metrics
   */
  protected calculateWeightedAverage(
    values: Array<{ value: number; weight: number }>
  ): number {
    if (values.length === 0) return 0;

    const totalWeight = values.reduce((sum, v) => sum + v.weight, 0);
    if (totalWeight === 0) return 0;

    const weightedSum = values.reduce((sum, v) => sum + (v.value * v.weight), 0);
    return weightedSum / totalWeight;
  }

  /**
   * Calculate standard deviation
   */
  protected calculateStandardDeviation(values: number[]): number {
    if (values.length === 0) return 0;

    const mean = values.reduce((sum, v) => sum + v, 0) / values.length;
    const squaredDifferences = values.map(v => Math.pow(v - mean, 2));
    const variance = squaredDifferences.reduce((sum, v) => sum + v, 0) / values.length;

    return Math.sqrt(variance);
  }

  /**
   * Convert database timestamps to JavaScript Date objects
   */
  protected parseTimestamp(timestamp: number): Date {
    return new Date(timestamp);
  }

  /**
   * Parse JSON metadata from analytics tables
   */
  protected parseAnalyticsMetadata<T = Record<string, any>>(
    metadataJson?: string
  ): T {
    if (!metadataJson) {
      return {} as T;
    }
    try {
      return JSON.parse(metadataJson) as T;
    } catch (error) {
      return {} as T;
    }
  }

  /**
   * Build where clause for time range filtering
   */
  protected buildTimeRangeWhere(
    timeRange: TimeRange, 
    timestampColumn: string = 'created_at'
  ): { sql: string; params: Record<string, any> } {
    return {
      sql: `${timestampColumn} BETWEEN @start AND @end`,
      params: {
        start: timeRange.start,
        end: timeRange.end
      }
    };
  }

  /**
   * Execute aggregation query with time grouping
   */
  protected executeTimeGroupedQuery<T>(
    baseQuery: string,
    timeRange: TimeRange,
    windowType: 'hour' | 'day' | 'week' | 'month',
    additionalParams: Record<string, any> = {}
  ): T[] {
    const timeGroupBy = this.getTimeWindowGroupBy(windowType);
    const query = baseQuery.replace('{{TIME_GROUP}}', timeGroupBy);

    const params = {
      start: timeRange.start,
      end: timeRange.end,
      ...additionalParams
    };

    return this.executeStatementAll<T>(
      `time_grouped_${windowType}_${Buffer.from(baseQuery).toString('base64').slice(0, 10)}`,
      query,
      params
    );
  }

  /**
   * Get conversation IDs for time range (common filter)
   */
  protected getConversationIdsInRange(timeRange: TimeRange): string[] {
    const sql = `
      SELECT DISTINCT conversation_id 
      FROM conversations 
      WHERE created_at BETWEEN @start AND @end
      ORDER BY created_at DESC
    `;

    const result = this.executeStatementAll<{ conversation_id: string }>(
      'conversations_in_range',
      sql,
      { start: timeRange.start, end: timeRange.end }
    );

    return result.map(r => r.conversation_id);
  }

  /**
   * Check if analytics data exists for time range
   */
  protected hasDataInRange(
    tableName: string,
    timeRange: TimeRange,
    timestampColumn: string = 'created_at'
  ): boolean {
    const sql = `
      SELECT 1 as exists_flag
      FROM ${tableName} 
      WHERE ${timestampColumn} BETWEEN @start AND @end 
      LIMIT 1
    `;

    const result = this.executeStatement<{ exists_flag: number }>(
      `has_data_${tableName}`,
      sql,
      { start: timeRange.start, end: timeRange.end }
    );

    return !!result?.exists_flag;
  }

  /**
   * Calculate percentile for a dataset
   */
  protected calculatePercentile(values: number[], percentile: number): number {
    if (values.length === 0) return 0;

    const sorted = [...values].sort((a, b) => a - b);
    const index = Math.ceil(sorted.length * (percentile / 100)) - 1;

    return sorted[Math.max(0, Math.min(index, sorted.length - 1))];
  }

  /**
   * Build SQL for trend calculation (using linear regression)
   */
  protected getTrendCalculationSQL(
    valueColumn: string,
    timeColumn: string = 'created_at'
  ): string {
    return `
      WITH trend_data AS (
        SELECT 
          ${valueColumn} as y,
          (${timeColumn} / 1000) as x
        FROM analytics_temp
        WHERE ${valueColumn} IS NOT NULL
      ),
      stats AS (
        SELECT 
          COUNT(*) as n,
          AVG(x) as x_mean,
          AVG(y) as y_mean,
          SUM(x * y) as sum_xy,
          SUM(x * x) as sum_x2
        FROM trend_data
      )
      SELECT 
        CASE 
          WHEN n > 1 AND (n * sum_x2 - (n * x_mean * x_mean)) != 0 
          THEN (n * sum_xy - (n * x_mean * y_mean)) / (n * sum_x2 - (n * x_mean * x_mean))
          ELSE 0 
        END as slope
      FROM stats
    `;
  }

  /**
   * Advanced batch insert with error handling and performance monitoring
   */
  protected batchInsert<T extends Record<string, any>>(
    tableName: string,
    records: T[],
    options: {
      batchSize?: number;
      conflictResolution?: 'IGNORE' | 'REPLACE' | 'FAIL';
      onProgress?: (processed: number, total: number) => void;
      enableRollback?: boolean;
    } = {}
  ): { inserted: number; failed: number; errors: Error[] } {
    if (records.length === 0) {
      return { inserted: 0, failed: 0, errors: [] };
    }

    const {
      batchSize = 100,
      conflictResolution = 'IGNORE',
      onProgress,
      enableRollback = true
    } = options;

    // Validate records have consistent schema
    const columns = Object.keys(records[0]);
    const invalidRecords = records.filter(record => 
      !columns.every(col => col in record)
    );

    if (invalidRecords.length > 0) {
      throw new Error(`Schema mismatch: ${invalidRecords.length} records have missing columns`);
    }

    const placeholders = columns.map(col => `@${col}`).join(', ');
    const conflictClause = conflictResolution !== 'FAIL' ? `OR ${conflictResolution}` : '';

    const sql = `
      INSERT ${conflictClause} INTO ${tableName} (${columns.join(', ')}) 
      VALUES (${placeholders})
    `;

    let inserted = 0;
    let failed = 0;
    const errors: Error[] = [];

    try {
      this.transaction((db) => {
        const stmt = db.prepare(sql);
        const insertMany = db.transaction((batch: T[]) => {
          for (const record of batch) {
            try {
              const result = stmt.run(record);
              if (result.changes > 0) {
                inserted++;
              }
            } catch (error) {
              failed++;
              errors.push(error as Error);

              if (enableRollback && errors.length > Math.ceil(batch.length * 0.1)) {
                throw new Error(`Batch failure rate too high: ${failed}/${batch.length} failed`);
              }
            }
          }
        });

        // Process in batches with progress tracking
        for (let i = 0; i < records.length; i += batchSize) {
          const batch = records.slice(i, i + batchSize);

          try {
            insertMany(batch);

            if (onProgress) {
              onProgress(Math.min(i + batchSize, records.length), records.length);
            }
          } catch (batchError) {
            // Handle batch-level errors
            failed += batch.length - (inserted % batchSize);
            errors.push(batchError as Error);

            if (enableRollback) {
              throw batchError;
            }
          }
        }
      });
    } catch (transactionError) {
      errors.push(transactionError as Error);
      throw new Error(`Batch insert failed: ${errors[errors.length - 1].message}`);
    }

    return { inserted, failed, errors };
  }

  /**
   * Optimized batch upsert (insert or update)
   */
  protected batchUpsert<T extends Record<string, any>>(
    tableName: string,
    records: T[],
    keyColumns: string[],
    options: {
      batchSize?: number;
      updateColumns?: string[];
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): { inserted: number; updated: number; failed: number } {
    if (records.length === 0) {
      return { inserted: 0, updated: 0, failed: 0 };
    }

    const { batchSize = 100, updateColumns, onProgress } = options;
    const columns = Object.keys(records[0]);
    const updateCols = updateColumns || columns.filter(col => !keyColumns.includes(col));

    const placeholders = columns.map(col => `@${col}`).join(', ');
    const updateSet = updateCols.map(col => `${col} = excluded.${col}`).join(', ');

    const sql = `
      INSERT INTO ${tableName} (${columns.join(', ')})
      VALUES (${placeholders})
      ON CONFLICT(${keyColumns.join(', ')}) DO UPDATE SET
        ${updateSet},
        updated_at = @currentTime
    `;

    let inserted = 0;
    let updated = 0;
    let failed = 0;

    this.transaction((db) => {
      const stmt = db.prepare(sql);
      const currentTime = this.getCurrentTimestamp();

      for (let i = 0; i < records.length; i += batchSize) {
        const batch = records.slice(i, i + batchSize);

        for (const record of batch) {
          try {
            const result = stmt.run({ ...record, currentTime });
            if (result.changes > 0) {
              // Check if it was insert or update based on last_insert_rowid
              if (result.lastInsertRowid) {
                inserted++;
              } else {
                updated++;
              }
            }
          } catch (error) {
            failed++;
            console.error('Upsert failed for record:', record, error);
          }
        }

        if (onProgress) {
          onProgress(Math.min(i + batchSize, records.length), records.length);
        }
      }
    });

    return { inserted, updated, failed };
  }

  /**
   * Streaming batch processor for large datasets
   */
  protected async streamingBatchProcess<T, R>(
    items: T[],
    processor: (batch: T[]) => Promise<R[]>,
    options: {
      batchSize?: number;
      maxConcurrency?: number;
      onProgress?: (processed: number, total: number) => void;
      onBatchComplete?: (results: R[], batchIndex: number) => void;
    } = {}
  ): Promise<R[]> {
    const {
      batchSize = 100,
      maxConcurrency = 4,
      onProgress,
      onBatchComplete
    } = options;

    if (items.length === 0) return [];

    const results: R[] = [];
    const batches: T[][] = [];

    // Create batches
    for (let i = 0; i < items.length; i += batchSize) {
      batches.push(items.slice(i, i + batchSize));
    }

    // Process batches with concurrency control
    const processBatch = async (batch: T[], batchIndex: number): Promise<R[]> => {
      try {
        const batchResults = await processor(batch);

        if (onBatchComplete) {
          onBatchComplete(batchResults, batchIndex);
        }

        if (onProgress) {
          const processed = Math.min((batchIndex + 1) * batchSize, items.length);
          onProgress(processed, items.length);
        }

        return batchResults;
      } catch (error) {
        console.error(`Batch ${batchIndex} processing failed:`, error);
        return [];
      }
    };

    // Process with controlled concurrency
    for (let i = 0; i < batches.length; i += maxConcurrency) {
      const concurrentBatches = batches.slice(i, i + maxConcurrency);
      const promises = concurrentBatches.map((batch, index) => 
        processBatch(batch, i + index)
      );

      const batchResults = await Promise.all(promises);
      results.push(...batchResults.flat());
    }

    return results;
  }

  /**
   * Batch delete with conditions
   */
  protected batchDelete(
    tableName: string,
    whereConditions: Array<{ column: string; value: any; operator?: string }>,
    options: {
      batchSize?: number;
      dryRun?: boolean;
    } = {}
  ): number {
    const { batchSize = 1000, dryRun = false } = options;

    if (whereConditions.length === 0) {
      throw new Error('No conditions provided for batch delete');
    }

    // Build WHERE clause
    const whereClause = whereConditions
      .map(({ column, operator = '=' }) => `${column} ${operator} ?`)
      .join(' AND ');

    const sql = `DELETE FROM ${tableName} WHERE ${whereClause}`;

    if (dryRun) {
      // Return count of rows that would be deleted
      const countSql = `SELECT COUNT(*) as count FROM ${tableName} WHERE ${whereClause}`;
      const values = whereConditions.map(({ value }) => value);
      const result = this.executeStatement<{ count: number }>(
        `count_${tableName}_delete`,
        countSql,
        values
      );
      return result?.count || 0;
    }

    let totalDeleted = 0;

    this.transaction((db) => {
      const stmt = db.prepare(sql);
      const values = whereConditions.map(({ value }) => value);

      // For large deletes, process in chunks to avoid locking issues
      let deleteCount;
      do {
        const result = stmt.run(values);
        deleteCount = result.changes;
        totalDeleted += deleteCount;
      } while (deleteCount === batchSize);
    });

    return totalDeleted;
  }

  /**
   * Clean up old analytics data beyond retention period
   */
  protected cleanupOldData(
    tableName: string,
    retentionDays: number,
    timestampColumn: string = 'created_at'
  ): number {
    const cutoffTime = Date.now() - (retentionDays * 24 * 60 * 60 * 1000);

    const sql = `
      DELETE FROM ${tableName} 
      WHERE ${timestampColumn} < @cutoff
    `;

    const result = this.executeStatementRun(
      `cleanup_${tableName}`,
      sql,
      { cutoff: cutoffTime }
    );

    return result.changes;
  }
}

