export interface ConversationAnalytics {
  id: string;
  conversationId: string;
  analyzedAt: number;

  // Flow metrics
  topicCount: number;
  topicTransitions: number;
  depthScore: number;
  circularityIndex: number;

  // Productivity metrics
  productivityScore: number;
  resolutionTime?: number;
  insightCount: number;
  breakthroughCount: number;

  // Quality metrics
  questionQualityAvg: number;
  responseQualityAvg: number;
  engagementScore: number;

  // Metadata
  metadata: Record<string, any>;
  createdAt: number;
  updatedAt: number;
}

export interface ConversationAnalyticsInput {
  conversationId: string;
  topicCount: number;
  topicTransitions: number;
  depthScore: number;
  circularityIndex: number;
  productivityScore: number;
  resolutionTime?: number;
  insightCount?: number;
  breakthroughCount?: number;
  questionQualityAvg?: number;
  responseQualityAvg?: number;
  engagementScore?: number;
  metadata?: Record<string, any>;
}

export interface ProductivitySummary {
  averageScore: number;
  medianScore: number;
  trendSlope: number;
  totalConversations: number;
  totalInsights: number;
  averageDepth: number;
  averageCircularity: number;
}

export interface TopicFlowSummary {
  averageTopicCount: number;
  averageTransitions: number;
  averageDepthScore: number;
  averageCircularityIndex: number;
  conversationsWithHighCircularity: number; // > 0.7
  conversationsWithDeepAnalysis: number; // depth > 80
}

/**
 * Repository for conversation analytics data
 */
export class ConversationAnalyticsRepository extends AnalyticsRepository {

  constructor(databaseManager: DatabaseManager) {
    super(databaseManager);
  }

  /**
   * Save conversation analytics
   */
  async saveAnalytics(input: ConversationAnalyticsInput): Promise<string> {
    const id = this.generateId();
    const now = this.getCurrentTimestamp();

    const sql = `
      INSERT INTO conversation_analytics (
        id, conversation_id, analyzed_at,
        topic_count, topic_transitions, depth_score, circularity_index,
        productivity_score, resolution_time, insight_count, breakthrough_count,
        question_quality_avg, response_quality_avg, engagement_score,
        metadata, created_at, updated_at
      ) VALUES (
        @id, @conversationId, @analyzedAt,
        @topicCount, @topicTransitions, @depthScore, @circularityIndex,
        @productivityScore, @resolutionTime, @insightCount, @breakthroughCount,
        @questionQualityAvg, @responseQualityAvg, @engagementScore,
        @metadata, @createdAt, @updatedAt
      )
    `;

    const params = {
      id,
      conversationId: input.conversationId,
      analyzedAt: now,
      topicCount: input.topicCount,
      topicTransitions: input.topicTransitions,
      depthScore: input.depthScore,
      circularityIndex: input.circularityIndex,
      productivityScore: input.productivityScore,
      resolutionTime: input.resolutionTime || null,
      insightCount: input.insightCount || 0,
      breakthroughCount: input.breakthroughCount || 0,
      questionQualityAvg: input.questionQualityAvg || 0,
      responseQualityAvg: input.responseQualityAvg || 0,
      engagementScore: input.engagementScore || 0,
      metadata: this.stringifyMetadata(input.metadata),
      createdAt: now,
      updatedAt: now
    };

    try {
      this.executeStatementRun('save_analytics', sql, params);
      return id;
    } catch (error) {
      this.handleConstraintError(error as Error, 'conversation analytics');
    }
  }

  /**
   * Get analytics for specific conversation
   */
  async getConversationAnalytics(conversationId: string): Promise<ConversationAnalytics | null> {
    const sql = `
      SELECT 
        id, conversation_id, analyzed_at,
        topic_count, topic_transitions, depth_score, circularity_index,
        productivity_score, resolution_time, insight_count, breakthrough_count,
        question_quality_avg, response_quality_avg, engagement_score,
        metadata, created_at, updated_at
      FROM conversation_analytics 
      WHERE conversation_id = @conversationId
      ORDER BY analyzed_at DESC 
      LIMIT 1
    `;

    const result = this.executeStatement<any>(
      'get_conversation_analytics',
      sql,
      { conversationId }
    );

    return result ? this.mapRowToAnalytics(result) : null;
  }

  /**
   * Get analytics for multiple conversations
   */
  async getMultipleAnalytics(
    conversationIds: string[]
  ): Promise<Map<string, ConversationAnalytics>> {
    if (conversationIds.length === 0) {
      return new Map();
    }

    const placeholders = conversationIds.map((_, i) => `$${i + 1}`).join(', ');
    const sql = `
      SELECT DISTINCT
        conversation_id, 
        id, analyzed_at, topic_count, topic_transitions, depth_score, circularity_index,
        productivity_score, resolution_time, insight_count, breakthrough_count,
        question_quality_avg, response_quality_avg, engagement_score,
        metadata, created_at, updated_at
      FROM conversation_analytics 
      WHERE conversation_id IN (${placeholders})
        AND analyzed_at = (
          SELECT MAX(analyzed_at) 
          FROM conversation_analytics ca2 
          WHERE ca2.conversation_id = conversation_analytics.conversation_id
        )
      ORDER BY analyzed_at DESC
    `;

    const results = this.executeStatementAll<any>(
      `get_multiple_analytics_${conversationIds.length}`,
      sql,
      conversationIds
    );

    const analyticsMap = new Map<string, ConversationAnalytics>();
    for (const row of results) {
      const analytics = this.mapRowToAnalytics(row);
      analyticsMap.set(analytics.conversationId, analytics);
    }

    return analyticsMap;
  }

  /**
   * Get productivity summary for time range
   */
  async getProductivitySummary(timeRange?: TimeRange): Promise<ProductivitySummary> {
    const validTimeRange = this.validateTimeRange(timeRange);

    const sql = `
      WITH latest_analytics AS (
        SELECT DISTINCT
          conversation_id,
          productivity_score,
          depth_score,
          circularity_index,
          insight_count,
          analyzed_at,
          ROW_NUMBER() OVER (
            PARTITION BY conversation_id 
            ORDER BY analyzed_at DESC
          ) as rn
        FROM conversation_analytics
        WHERE analyzed_at BETWEEN @start AND @end
      ),
      filtered_analytics AS (
        SELECT 
          productivity_score,
          depth_score,
          circularity_index,
          insight_count
        FROM latest_analytics 
        WHERE rn = 1
      ),
      trend_data AS (
        SELECT 
          productivity_score,
          (analyzed_at / 1000) as time_x
        FROM latest_analytics
        WHERE rn = 1 AND productivity_score IS NOT NULL
        ORDER BY analyzed_at
      ),
      trend_stats AS (
        SELECT 
          COUNT(*) as n,
          AVG(time_x) as x_mean,
          AVG(productivity_score) as y_mean,
          SUM(time_x * productivity_score) as sum_xy,
          SUM(time_x * time_x) as sum_x2
        FROM trend_data
      )
      SELECT 
        COALESCE(AVG(productivity_score), 0) as average_score,
        COALESCE(SUM(insight_count), 0) as total_insights,
        COALESCE(AVG(depth_score), 0) as average_depth,
        COALESCE(AVG(circularity_index), 0) as average_circularity,
        COUNT(*) as total_conversations,
        COALESCE(
          CASE 
            WHEN ts.n > 1 AND (ts.n * ts.sum_x2 - (ts.n * ts.x_mean * ts.x_mean)) != 0 
            THEN (ts.n * ts.sum_xy - (ts.n * ts.x_mean * ts.y_mean)) / 
                 (ts.n * ts.sum_x2 - (ts.n * ts.x_mean * ts.x_mean))
            ELSE 0 
          END, 0
        ) as trend_slope
      FROM filtered_analytics fa
      CROSS JOIN trend_stats ts
    `;

    const result = this.executeStatement<any>(
      'productivity_summary',
      sql,
      validTimeRange
    );

    // Calculate median separately (SQLite doesn't have built-in median)
    const scores = this.getProductivityScores(validTimeRange);
    const medianScore = this.calculatePercentile(scores, 50);

    return {
      averageScore: result?.average_score || 0,
      medianScore,
      trendSlope: result?.trend_slope || 0,
      totalConversations: result?.total_conversations || 0,
      totalInsights: result?.total_insights || 0,
      averageDepth: result?.average_depth || 0,
      averageCircularity: result?.average_circularity || 0
    };
  }

  /**
   * Get topic flow summary
   */
  async getTopicFlowSummary(timeRange?: TimeRange): Promise<TopicFlowSummary> {
    const validTimeRange = this.validateTimeRange(timeRange);

    const sql = `
      WITH latest_analytics AS (
        SELECT DISTINCT
          conversation_id,
          topic_count,
          topic_transitions,
          depth_score,
          circularity_index,
          analyzed_at,
          ROW_NUMBER() OVER (
            PARTITION BY conversation_id 
            ORDER BY analyzed_at DESC
          ) as rn
        FROM conversation_analytics
        WHERE analyzed_at BETWEEN @start AND @end
      ),
      filtered_analytics AS (
        SELECT 
          topic_count,
          topic_transitions,
          depth_score,
          circularity_index
        FROM latest_analytics 
        WHERE rn = 1
      )
      SELECT 
        COALESCE(AVG(topic_count), 0) as average_topic_count,
        COALESCE(AVG(topic_transitions), 0) as average_transitions,
        COALESCE(AVG(depth_score), 0) as average_depth_score,
        COALESCE(AVG(circularity_index), 0) as average_circularity_index,
        COUNT(CASE WHEN circularity_index > 0.7 THEN 1 END) as high_circularity_count,
        COUNT(CASE WHEN depth_score > 80 THEN 1 END) as deep_analysis_count
      FROM filtered_analytics
    `;

    const result = this.executeStatement<any>(
      'topic_flow_summary',
      sql,
      validTimeRange
    );

    return {
      averageTopicCount: result?.average_topic_count || 0,
      averageTransitions: result?.average_transitions || 0,
      averageDepthScore: result?.average_depth_score || 0,
      averageCircularityIndex: result?.average_circularity_index || 0,
      conversationsWithHighCircularity: result?.high_circularity_count || 0,
      conversationsWithDeepAnalysis: result?.deep_analysis_count || 0
    };
  }

  /**
   * Get top performing conversations by productivity
   */
  async getTopPerformingConversations(
    limit: number = 10,
    timeRange?: TimeRange
  ): Promise<ConversationAnalytics[]> {
    const validTimeRange = this.validateTimeRange(timeRange);
    const { limit: validLimit } = this.validatePagination(limit);

    const sql = `
      WITH latest_analytics AS (
        SELECT 
          id, conversation_id, analyzed_at,
          topic_count, topic_transitions, depth_score, circularity_index,
          productivity_score, resolution_time, insight_count, breakthrough_count,
          question_quality_avg, response_quality_avg, engagement_score,
          metadata, created_at, updated_at,
          ROW_NUMBER() OVER (
            PARTITION BY conversation_id 
            ORDER BY analyzed_at DESC
          ) as rn
        FROM conversation_analytics
        WHERE analyzed_at BETWEEN @start AND @end
      )
      SELECT 
        id, conversation_id, analyzed_at,
        topic_count, topic_transitions, depth_score, circularity_index,
        productivity_score, resolution_time, insight_count, breakthrough_count,
        question_quality_avg, response_quality_avg, engagement_score,
        metadata, created_at, updated_at
      FROM latest_analytics 
      WHERE rn = 1
      ORDER BY productivity_score DESC, insight_count DESC
      LIMIT @limit
    `;

    const results = this.executeStatementAll<any>(
      'top_performing_conversations',
      sql,
      { ...validTimeRange, limit: validLimit }
    );

    return results.map(row => this.mapRowToAnalytics(row));
  }

  /**
   * Get conversations needing analysis
   */
  async getConversationsNeedingAnalysis(limit: number = 50): Promise<string[]> {
    const sql = `
      SELECT c.id
      FROM conversations c
      LEFT JOIN conversation_analytics ca ON c.id = ca.conversation_id
      WHERE ca.conversation_id IS NULL 
        OR ca.analyzed_at < c.updated_at
      ORDER BY c.updated_at DESC
      LIMIT @limit
    `;

    const results = this.executeStatementAll<{ id: string }>(
      'conversations_needing_analysis',
      sql,
      { limit }
    );

    return results.map(row => row.id);
  }

  /**
   * Update existing analytics
   */
  async updateAnalytics(
    conversationId: string,
    updates: Partial<ConversationAnalyticsInput>
  ): Promise<void> {
    const setParts: string[] = [];
    const params: Record<string, any> = { conversationId };

    // Build dynamic update query
    for (const [key, value] of Object.entries(updates)) {
      if (value !== undefined) {
        const dbKey = this.camelToSnake(key);
        setParts.push(`${dbKey} = @${key}`);
        params[key] = key === 'metadata' ? this.stringifyMetadata(value as Record<string, any>) : value;
      }
    }

    if (setParts.length === 0) {
      return; // Nothing to update
    }

    setParts.push('updated_at = @updatedAt');
    params.updatedAt = this.getCurrentTimestamp();

    const sql = `
      UPDATE conversation_analytics 
      SET ${setParts.join(', ')}
      WHERE conversation_id = @conversationId
        AND analyzed_at = (
          SELECT MAX(analyzed_at) 
          FROM conversation_analytics ca2 
          WHERE ca2.conversation_id = @conversationId
        )
    `;

    this.executeStatementRun('update_analytics', sql, params);
  }

  /**
   * Delete analytics for conversation
   */
  async deleteAnalytics(conversationId: string): Promise<number> {
    const sql = `
      DELETE FROM conversation_analytics 
      WHERE conversation_id = @conversationId
    `;

    const result = this.executeStatementRun(
      'delete_analytics',
      sql,
      { conversationId }
    );

    return result.changes;
  }

  /**
   * Batch save conversation analytics with optimized performance
   */
  async batchSaveAnalytics(
    analyticsInputs: ConversationAnalyticsInput[],
    options: {
      batchSize?: number;
      conflictResolution?: 'IGNORE' | 'REPLACE' | 'UPDATE';
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ inserted: number; updated: number; failed: number; errors: Error[] }> {
    if (analyticsInputs.length === 0) {
      return { inserted: 0, updated: 0, failed: 0, errors: [] };
    }

    const { batchSize = 100, conflictResolution = 'UPDATE', onProgress } = options;
    const now = this.getCurrentTimestamp();

    // Prepare analytics records with IDs and timestamps
    const records = analyticsInputs.map(input => ({
      id: this.generateId(),
      conversationId: input.conversationId,
      analyzedAt: now,
      topicCount: input.topicCount,
      topicTransitions: input.topicTransitions,
      depthScore: input.depthScore,
      circularityIndex: input.circularityIndex,
      productivityScore: input.productivityScore,
      resolutionTime: input.resolutionTime || null,
      insightCount: input.insightCount || 0,
      breakthroughCount: input.breakthroughCount || 0,
      questionQualityAvg: input.questionQualityAvg || 0,
      responseQualityAvg: input.responseQualityAvg || 0,
      engagementScore: input.engagementScore || 0,
      metadata: this.stringifyMetadata(input.metadata),
      createdAt: now,
      updatedAt: now
    }));

    // Transform keys for database
    const dbRecords = records.map(record => ({
      id: record.id,
      conversation_id: record.conversationId,
      analyzed_at: record.analyzedAt,
      topic_count: record.topicCount,
      topic_transitions: record.topicTransitions,
      depth_score: record.depthScore,
      circularity_index: record.circularityIndex,
      productivity_score: record.productivityScore,
      resolution_time: record.resolutionTime,
      insight_count: record.insightCount,
      breakthrough_count: record.breakthroughCount,
      question_quality_avg: record.questionQualityAvg,
      response_quality_avg: record.responseQualityAvg,
      engagement_score: record.engagementScore,
      metadata: record.metadata,
      created_at: record.createdAt,
      updated_at: record.updatedAt
    }));

    try {
      if (conflictResolution === 'UPDATE') {
        // Use upsert for update behavior
        const result = this.batchUpsert(
          'conversation_analytics',
          dbRecords,
          ['conversation_id'],
          {
            batchSize,
            onProgress
          }
        );
        return { 
          inserted: result.inserted, 
          updated: result.updated, 
          failed: result.failed,
          errors: []
        };
      } else {
        // Use batch insert for other conflict resolutions
        const result = this.batchInsert(
          'conversation_analytics',
          dbRecords,
          {
            batchSize,
            conflictResolution: conflictResolution as 'IGNORE' | 'REPLACE',
            onProgress
          }
        );
        return {
          inserted: result.inserted,
          updated: 0,
          failed: result.failed,
          errors: result.errors
        };
      }
    } catch (error) {
      throw new Error(`Batch analytics save failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Batch update analytics with selective field updates
   */
  async batchUpdateAnalytics(
    updates: Array<{
      conversationId: string;
      updates: Partial<ConversationAnalyticsInput>;
    }>,
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ updated: number; failed: number }> {
    if (updates.length === 0) {
      return { updated: 0, failed: 0 };
    }

    const { batchSize = 100, onProgress } = options;
    let totalUpdated = 0;
    let totalFailed = 0;

    // Process updates in batches
    for (let i = 0; i < updates.length; i += batchSize) {
      const batch = updates.slice(i, i + batchSize);

      await this.transaction((db) => {
        for (const { conversationId, updates: updateFields } of batch) {
          try {
            const setParts: string[] = [];
            const params: Record<string, any> = { conversationId };

            // Build dynamic update query
            for (const [key, value] of Object.entries(updateFields)) {
              if (value !== undefined) {
                const dbKey = this.camelToSnake(key);
                setParts.push(`${dbKey} = @${key}`);
                params[key] = key === 'metadata' ? this.stringifyMetadata(value as Record<string, any>) : value;
              }
            }

            if (setParts.length > 0) {
              setParts.push('updated_at = @updatedAt');
              params.updatedAt = this.getCurrentTimestamp();

              const sql = `
                UPDATE conversation_analytics 
                SET ${setParts.join(', ')}
                WHERE conversation_id = @conversationId
                  AND analyzed_at = (
                    SELECT MAX(analyzed_at) 
                    FROM conversation_analytics ca2 
                    WHERE ca2.conversation_id = @conversationId
                  )
              `;

              const stmt = db.prepare(sql);
              const result = stmt.run(params);

              if (result.changes > 0) {
                totalUpdated++;
              }
            }
          } catch (error) {
            totalFailed++;
            console.error(`Failed to update analytics for conversation ${conversationId}:`, error);
          }
        }
      });

      if (onProgress) {
        onProgress(Math.min(i + batchSize, updates.length), updates.length);
      }
    }

    return { updated: totalUpdated, failed: totalFailed };
  }

  /**
   * Batch delete analytics for multiple conversations
   */
  async batchDeleteAnalytics(
    conversationIds: string[],
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ deleted: number; failed: number }> {
    if (conversationIds.length === 0) {
      return { deleted: 0, failed: 0 };
    }

    const { batchSize = 100, onProgress } = options;
    let totalDeleted = 0;
    let totalFailed = 0;

    // Process deletions in batches
    for (let i = 0; i < conversationIds.length; i += batchSize) {
      const batch = conversationIds.slice(i, i + batchSize);

      try {
        const placeholders = batch.map((_, index) => `@id${index}`).join(', ');
        const params = batch.reduce((acc, id, index) => {
          acc[`id${index}`] = id;
          return acc;
        }, {} as Record<string, string>);

        const sql = `
          DELETE FROM conversation_analytics 
          WHERE conversation_id IN (${placeholders})
        `;

        const result = this.executeStatementRun(
          `batch_delete_analytics_${i}`,
          sql,
          params
        );

        totalDeleted += result.changes;
      } catch (error) {
        totalFailed += batch.length;
        console.error(`Failed to delete analytics batch ${i}:`, error);
      }

      if (onProgress) {
        onProgress(Math.min(i + batchSize, conversationIds.length), conversationIds.length);
      }
    }

    return { deleted: totalDeleted, failed: totalFailed };
  }

  /**
   * Get productivity scores for percentile calculation
   */
  private getProductivityScores(timeRange: TimeRange): number[] {
    const sql = `
      WITH latest_analytics AS (
        SELECT DISTINCT
          conversation_id,
          productivity_score,
          ROW_NUMBER() OVER (
            PARTITION BY conversation_id 
            ORDER BY analyzed_at DESC
          ) as rn
        FROM conversation_analytics
        WHERE analyzed_at BETWEEN @start AND @end
          AND productivity_score IS NOT NULL
      )
      SELECT productivity_score
      FROM latest_analytics 
      WHERE rn = 1
      ORDER BY productivity_score
    `;

    const results = this.executeStatementAll<{ productivity_score: number }>(
      'productivity_scores',
      sql,
      timeRange
    );

    return results.map(r => r.productivity_score);
  }

  /**
   * Convert camelCase to snake_case
   */
  private camelToSnake(str: string): string {
    return str.replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);
  }

  /**
   * Map database row to ConversationAnalytics interface
   */
  private mapRowToAnalytics(row: any): ConversationAnalytics {
    return {
      id: row.id,
      conversationId: row.conversation_id,
      analyzedAt: row.analyzed_at,
      topicCount: row.topic_count,
      topicTransitions: row.topic_transitions,
      depthScore: row.depth_score,
      circularityIndex: row.circularity_index,
      productivityScore: row.productivity_score,
      resolutionTime: row.resolution_time,
      insightCount: row.insight_count,
      breakthroughCount: row.breakthrough_count,
      questionQualityAvg: row.question_quality_avg,
      responseQualityAvg: row.response_quality_avg,
      engagementScore: row.engagement_score,
      metadata: this.parseAnalyticsMetadata(row.metadata),
      createdAt: row.created_at,
      updatedAt: row.updated_at
    };
  }
}

/**
 * Decision Tracking Repository
 * 
 * Handles database operations for decision quality analytics:
 * - Decision identification and classification
 * - Timeline tracking (problem -> decision -> outcome)
 * - Quality metrics and factor analysis
 * - Outcome assessment and learning
 * - Decision pattern recognition
 */

import { AnalyticsRepository, TimeRange, PaginationOptions } from './AnalyticsRepository.js';
import { DatabaseManager } from '../../storage/Database.js';

export interface DecisionTracking {
  id: string;
  decisionSummary: string;
  decisionType?: 'strategic' | 'tactical' | 'operational' | 'personal';
  conversationIds: string[];

  // Timeline tracking
  problemIdentifiedAt?: number;
  optionsConsideredAt?: number;
  decisionMadeAt: number;
  implementationStartedAt?: number;
  outcomeAssessedAt?: number;

  // Quality metrics
  clarityScore: number;
  confidenceLevel: number;
  consensusLevel: number;

  // Outcome tracking
  reversalCount: number;
  modificationCount: number;
  outcomeScore?: number;
  outcomeAssessment: Record<string, any>;

  // Decision factors
  informationCompleteness: number;
  stakeholderCount: number;
  alternativesConsidered: number;
  riskAssessed: boolean;

  // Analysis
  successFactors: string[];
  failureFactors: string[];
  lessonsLearned: string;

  // Metadata
  tags: string[];
  priority: 'critical' | 'high' | 'medium' | 'low';
  status: 'pending' | 'decided' | 'implemented' | 'assessed' | 'reversed';

  createdAt: number;
  updatedAt: number;
}

export interface DecisionInput {
  decisionSummary: string;
  decisionType?: 'strategic' | 'tactical' | 'operational' | 'personal';
  conversationIds: string[];
  problemIdentifiedAt?: number;
  optionsConsideredAt?: number;
  decisionMadeAt?: number;
  clarityScore?: number;
  confidenceLevel?: number;
  informationCompleteness?: number;
  stakeholderCount?: number;
  alternativesConsidered?: number;
  riskAssessed?: boolean;
  tags?: string[];
  priority?: 'critical' | 'high' | 'medium' | 'low';
}

export interface DecisionOutcome {
  decisionId: string;
  outcomeScore: number; // 0-100
  implementationStartedAt?: number;
  outcomeAssessedAt: number;
  successFactors: string[];
  failureFactors: string[];
  lessonsLearned: string;
  modifications: string[];
}

export interface DecisionAnalysis {
  totalDecisions: number;
  averageQuality: number;
  averageOutcome: number;
  averageTimeToDecision: number; // hours
  averageTimeToImplementation: number; // hours
  reversalRate: number; // percentage
  topSuccessFactors: Array<{ factor: string; frequency: number; successRate: number }>;
  commonPitfalls: Array<{ pitfall: string; frequency: number; impactScore: number }>;
  decisionVelocityTrend: number; // decisions per week trend
}

export interface DecisionPattern {
  pattern: string;
  frequency: number;
  averageQuality: number;
  averageOutcome: number;
  confidence: number;
}

/**
 * Repository for decision tracking and quality analytics
 */
export class DecisionTrackingRepository extends AnalyticsRepository {

  constructor(databaseManager: DatabaseManager) {
    super(databaseManager);
  }

  /**
   * Save a new decision
   */
  async saveDecision(input: DecisionInput): Promise<string> {
    const id = this.generateId();
    const now = this.getCurrentTimestamp();

    const sql = `
      INSERT INTO decision_tracking (
        id, decision_summary, decision_type, conversation_ids,
        problem_identified_at, options_considered_at, decision_made_at,
        clarity_score, confidence_level, consensus_level,
        reversal_count, modification_count, 
        information_completeness, stakeholder_count, alternatives_considered, risk_assessed,
        success_factors, failure_factors, lessons_learned,
        tags, priority, status, created_at, updated_at
      ) VALUES (
        @id, @decisionSummary, @decisionType, @conversationIds,
        @problemIdentifiedAt, @optionsConsideredAt, @decisionMadeAt,
        @clarityScore, @confidenceLevel, @consensusLevel,
        @reversalCount, @modificationCount,
        @informationCompleteness, @stakeholderCount, @alternativesConsidered, @riskAssessed,
        @successFactors, @failureFactors, @lessonsLearned,
        @tags, @priority, @status, @createdAt, @updatedAt
      )
    `;

    const params = {
      id,
      decisionSummary: input.decisionSummary,
      decisionType: input.decisionType || null,
      conversationIds: JSON.stringify(input.conversationIds),
      problemIdentifiedAt: input.problemIdentifiedAt || null,
      optionsConsideredAt: input.optionsConsideredAt || null,
      decisionMadeAt: input.decisionMadeAt || now,
      clarityScore: input.clarityScore || 50,
      confidenceLevel: input.confidenceLevel || 50,
      consensusLevel: 100, // Default for single-person decisions
      reversalCount: 0,
      modificationCount: 0,
      informationCompleteness: input.informationCompleteness || 50,
      stakeholderCount: input.stakeholderCount || 1,
      alternativesConsidered: input.alternativesConsidered || 1,
      riskAssessed: input.riskAssessed || false,
      successFactors: JSON.stringify([]),
      failureFactors: JSON.stringify([]),
      lessonsLearned: '',
      tags: JSON.stringify(input.tags || []),
      priority: input.priority || 'medium',
      status: 'decided',
      createdAt: now,
      updatedAt: now
    };

    try {
      this.executeStatementRun('save_decision', sql, params);
      return id;
    } catch (error) {
      this.handleConstraintError(error as Error, 'decision');
    }
  }

  /**
   * Update decision outcome
   */
  async updateOutcome(outcome: DecisionOutcome): Promise<void> {
    const now = this.getCurrentTimestamp();

    const sql = `
      UPDATE decision_tracking 
      SET 
        outcome_score = @outcomeScore,
        implementation_started_at = @implementationStartedAt,
        outcome_assessed_at = @outcomeAssessedAt,
        success_factors = @successFactors,
        failure_factors = @failureFactors,
        lessons_learned = @lessonsLearned,
        modification_count = modification_count + @modificationCount,
        status = 'assessed',
        updated_at = @updatedAt
      WHERE id = @decisionId
    `;

    this.executeStatementRun('update_decision_outcome', sql, {
      decisionId: outcome.decisionId,
      outcomeScore: Math.max(0, Math.min(100, outcome.outcomeScore)),
      implementationStartedAt: outcome.implementationStartedAt || null,
      outcomeAssessedAt: outcome.outcomeAssessedAt,
      successFactors: JSON.stringify(outcome.successFactors),
      failureFactors: JSON.stringify(outcome.failureFactors),
      lessonsLearned: outcome.lessonsLearned,
      modificationCount: outcome.modifications.length,
      updatedAt: now
    });
  }

  /**
   * Mark decision as reversed
   */
  async markReversed(decisionId: string, reason: string): Promise<void> {
    const sql = `
      UPDATE decision_tracking 
      SET 
        reversal_count = reversal_count + 1,
        status = 'reversed',
        lessons_learned = CASE 
          WHEN lessons_learned = '' THEN @reason
          ELSE lessons_learned || '; ' || @reason
        END,
        updated_at = @updatedAt
      WHERE id = @decisionId
    `;

    this.executeStatementRun('mark_decision_reversed', sql, {
      decisionId,
      reason,
      updatedAt: this.getCurrentTimestamp()
    });
  }

  /**
   * Get decisions for analysis
   */
  async getDecisions(
    status?: 'pending' | 'decided' | 'implemented' | 'assessed' | 'reversed',
    timeRange?: TimeRange,
    options?: PaginationOptions
  ): Promise<DecisionTracking[]> {
    const validTimeRange = this.validateTimeRange(timeRange);
    const { limit, offset } = this.validatePagination(options?.limit, options?.offset);

    let whereClause = 'WHERE decision_made_at BETWEEN @start AND @end';
    const params: any = { ...validTimeRange, limit, offset };

    if (status) {
      whereClause += ' AND status = @status';
      params.status = status;
    }

    const sql = `
      SELECT 
        id, decision_summary, decision_type, conversation_ids,
        problem_identified_at, options_considered_at, decision_made_at,
        implementation_started_at, outcome_assessed_at,
        clarity_score, confidence_level, consensus_level,
        reversal_count, modification_count, outcome_score, outcome_assessment,
        information_completeness, stakeholder_count, alternatives_considered, risk_assessed,
        success_factors, failure_factors, lessons_learned,
        tags, priority, status, created_at, updated_at
      FROM decision_tracking
      ${whereClause}
      ORDER BY decision_made_at DESC
      LIMIT @limit OFFSET @offset
    `;

    const results = this.executeStatementAll<any>('get_decisions', sql, params);
    return results.map(row => this.mapRowToDecision(row));
  }

  /**
   * Get decision analysis summary
   */
  async getDecisionAnalysis(timeRange?: TimeRange): Promise<DecisionAnalysis> {
    const validTimeRange = this.validateTimeRange(timeRange);

    const sql = `
      WITH decision_metrics AS (
        SELECT 
          dt.*,
          CASE 
            WHEN problem_identified_at IS NOT NULL 
            THEN (decision_made_at - problem_identified_at) / (1000.0 * 60 * 60)
            ELSE NULL 
          END as hours_to_decision,
          CASE 
            WHEN implementation_started_at IS NOT NULL 
            THEN (implementation_started_at - decision_made_at) / (1000.0 * 60 * 60)
            ELSE NULL 
          END as hours_to_implementation
        FROM decision_tracking dt
        WHERE decision_made_at BETWEEN @start AND @end
      ),
      quality_stats AS (
        SELECT 
          COUNT(*) as total_decisions,
          AVG(clarity_score) as avg_quality,
          AVG(outcome_score) as avg_outcome,
          AVG(hours_to_decision) as avg_time_to_decision,
          AVG(hours_to_implementation) as avg_time_to_implementation,
          (SUM(CASE WHEN reversal_count > 0 THEN 1 ELSE 0 END) * 100.0 / COUNT(*)) as reversal_rate
        FROM decision_metrics
      ),
      velocity_trend AS (
        SELECT 
          COUNT(*) / ((@end - @start) / (1000.0 * 60 * 60 * 24 * 7)) as decisions_per_week
        FROM decision_metrics
      )
      SELECT 
        qs.total_decisions,
        qs.avg_quality,
        qs.avg_outcome,
        qs.avg_time_to_decision,
        qs.avg_time_to_implementation,
        qs.reversal_rate,
        vt.decisions_per_week
      FROM quality_stats qs
      CROSS JOIN velocity_trend vt
    `;

    const result = this.executeStatement<any>('decision_analysis', sql, validTimeRange);

    // Get success factors and pitfalls
    const successFactors = await this.getTopSuccessFactors(validTimeRange);
    const commonPitfalls = await this.getCommonPitfalls(validTimeRange);

    return {
      totalDecisions: result?.total_decisions || 0,
      averageQuality: Math.round((result?.avg_quality || 0) * 10) / 10,
      averageOutcome: Math.round((result?.avg_outcome || 0) * 10) / 10,
      averageTimeToDecision: Math.round((result?.avg_time_to_decision || 0) * 10) / 10,
      averageTimeToImplementation: Math.round((result?.avg_time_to_implementation || 0) * 10) / 10,
      reversalRate: Math.round((result?.reversal_rate || 0) * 10) / 10,
      topSuccessFactors: successFactors,
      commonPitfalls: commonPitfalls,
      decisionVelocityTrend: Math.round((result?.decisions_per_week || 0) * 10) / 10
    };
  }

  /**
   * Get decision patterns
   */
  async getDecisionPatterns(timeRange?: TimeRange): Promise<DecisionPattern[]> {
    const validTimeRange = this.validateTimeRange(timeRange);

    const sql = `
      WITH decision_patterns AS (
        SELECT 
          CASE 
            WHEN decision_type IS NOT NULL THEN decision_type
            WHEN priority = 'critical' THEN 'urgent'
            WHEN alternatives_considered > 3 THEN 'analytical'
            WHEN confidence_level > 80 THEN 'confident'
            WHEN information_completeness < 50 THEN 'incomplete-info'
            ELSE 'standard'
          END as pattern,
          clarity_score,
          outcome_score,
          1 as frequency
        FROM decision_tracking
        WHERE decision_made_at BETWEEN @start AND @end
          AND clarity_score IS NOT NULL
      ),
      pattern_stats AS (
        SELECT 
          pattern,
          SUM(frequency) as total_frequency,
          AVG(clarity_score) as avg_quality,
          AVG(outcome_score) as avg_outcome,
          COUNT(*) as sample_size
        FROM decision_patterns
        GROUP BY pattern
        HAVING total_frequency >= 3
      )
      SELECT 
        pattern,
        total_frequency as frequency,
        avg_quality,
        avg_outcome,
        CASE 
          WHEN sample_size >= 10 THEN 0.9
          WHEN sample_size >= 5 THEN 0.7
          ELSE 0.5
        END as confidence
      FROM pattern_stats
      ORDER BY total_frequency DESC, avg_outcome DESC
    `;

    const results = this.executeStatementAll<any>('decision_patterns', sql, validTimeRange);

    return results.map(row => ({
      pattern: row.pattern,
      frequency: row.frequency,
      averageQuality: Math.round((row.avg_quality || 0) * 10) / 10,
      averageOutcome: Math.round((row.avg_outcome || 0) * 10) / 10,
      confidence: row.confidence
    }));
  }

  /**
   * Get decisions needing follow-up
   */
  async getDecisionsNeedingFollowUp(daysOld: number = 30): Promise<DecisionTracking[]> {
    const cutoffTime = Date.now() - (daysOld * 24 * 60 * 60 * 1000);

    const sql = `
      SELECT 
        id, decision_summary, decision_type, conversation_ids,
        problem_identified_at, options_considered_at, decision_made_at,
        implementation_started_at, outcome_assessed_at,
        clarity_score, confidence_level, consensus_level,
        reversal_count, modification_count, outcome_score, outcome_assessment,
        information_completeness, stakeholder_count, alternatives_considered, risk_assessed,
        success_factors, failure_factors, lessons_learned,
        tags, priority, status, created_at, updated_at
      FROM decision_tracking
      WHERE status IN ('decided', 'implemented')
        AND decision_made_at < @cutoffTime
        AND (outcome_assessed_at IS NULL OR outcome_score IS NULL)
      ORDER BY decision_made_at ASC
      LIMIT 20
    `;

    const results = this.executeStatementAll<any>(
      'decisions_needing_followup',
      sql,
      { cutoffTime }
    );

    return results.map(row => this.mapRowToDecision(row));
  }

  /**
   * Get decision by ID
   */
  async getDecision(decisionId: string): Promise<DecisionTracking | null> {
    const sql = `
      SELECT 
        id, decision_summary, decision_type, conversation_ids,
        problem_identified_at, options_considered_at, decision_made_at,
        implementation_started_at, outcome_assessed_at,
        clarity_score, confidence_level, consensus_level,
        reversal_count, modification_count, outcome_score, outcome_assessment,
        information_completeness, stakeholder_count, alternatives_considered, risk_assessed,
        success_factors, failure_factors, lessons_learned,
        tags, priority, status, created_at, updated_at
      FROM decision_tracking
      WHERE id = @decisionId
    `;

    const result = this.executeStatement<any>('get_decision', sql, { decisionId });
    return result ? this.mapRowToDecision(result) : null;
  }

  /**
   * Delete decision
   */
  async deleteDecision(decisionId: string): Promise<number> {
    const sql = 'DELETE FROM decision_tracking WHERE id = @decisionId';
    const result = this.executeStatementRun('delete_decision', sql, { decisionId });
    return result.changes;
  }

  /**
   * Get top success factors
   */
  private async getTopSuccessFactors(
    timeRange: TimeRange
  ): Promise<Array<{ factor: string; frequency: number; successRate: number }>> {
    const sql = `
      WITH successful_decisions AS (
        SELECT success_factors
        FROM decision_tracking
        WHERE decision_made_at BETWEEN @start AND @end
          AND outcome_score >= 70
          AND success_factors != '[]'
      ),
      all_factors AS (
        SELECT 
          json_each.value as factor
        FROM successful_decisions, json_each(successful_decisions.success_factors)
      ),
      factor_stats AS (
        SELECT 
          factor,
          COUNT(*) as frequency
        FROM all_factors
        GROUP BY factor
        HAVING frequency >= 2
      )
      SELECT 
        factor,
        frequency,
        90.0 as success_rate -- Simplified - could be calculated more precisely
      FROM factor_stats
      ORDER BY frequency DESC
      LIMIT 10
    `;

    const results = this.executeStatementAll<any>('top_success_factors', sql, timeRange);

    return results.map(row => ({
      factor: row.factor,
      frequency: row.frequency,
      successRate: row.success_rate
    }));
  }

  /**
   * Get common pitfalls
   */
  private async getCommonPitfalls(
    timeRange: TimeRange
  ): Promise<Array<{ pitfall: string; frequency: number; impactScore: number }>> {
    const sql = `
      WITH failed_decisions AS (
        SELECT failure_factors, outcome_score
        FROM decision_tracking
        WHERE decision_made_at BETWEEN @start AND @end
          AND (outcome_score < 50 OR reversal_count > 0)
          AND failure_factors != '[]'
      ),
      all_pitfalls AS (
        SELECT 
          json_each.value as pitfall,
          outcome_score
        FROM failed_decisions, json_each(failed_decisions.failure_factors)
      ),
      pitfall_stats AS (
        SELECT 
          pitfall,
          COUNT(*) as frequency,
          AVG(100 - outcome_score) as avg_impact
        FROM all_pitfalls
        GROUP BY pitfall
        HAVING frequency >= 2
      )
      SELECT 
        pitfall,
        frequency,
        avg_impact as impact_score
      FROM pitfall_stats
      ORDER BY frequency DESC, avg_impact DESC
      LIMIT 10
    `;

    const results = this.executeStatementAll<any>('common_pitfalls', sql, timeRange);

    return results.map(row => ({
      pitfall: row.pitfall,
      frequency: row.frequency,
      impactScore: Math.round(row.impact_score)
    }));
  }

  /**
   * Map database row to DecisionTracking interface
   */
  private mapRowToDecision(row: any): DecisionTracking {
    return {
      id: row.id,
      decisionSummary: row.decision_summary,
      decisionType: row.decision_type,
      conversationIds: this.parseJSONArray(row.conversation_ids),
      problemIdentifiedAt: row.problem_identified_at,
      optionsConsideredAt: row.options_considered_at,
      decisionMadeAt: row.decision_made_at,
      implementationStartedAt: row.implementation_started_at,
      outcomeAssessedAt: row.outcome_assessed_at,
      clarityScore: row.clarity_score || 0,
      confidenceLevel: row.confidence_level || 0,
      consensusLevel: row.consensus_level || 0,
      reversalCount: row.reversal_count || 0,
      modificationCount: row.modification_count || 0,
      outcomeScore: row.outcome_score,
      outcomeAssessment: this.parseAnalyticsMetadata(row.outcome_assessment),
      informationCompleteness: row.information_completeness || 0,
      stakeholderCount: row.stakeholder_count || 0,
      alternativesConsidered: row.alternatives_considered || 0,
      riskAssessed: Boolean(row.risk_assessed),
      successFactors: this.parseJSONArray(row.success_factors),
      failureFactors: this.parseJSONArray(row.failure_factors),
      lessonsLearned: row.lessons_learned || '',
      tags: this.parseJSONArray(row.tags),
      priority: row.priority || 'medium',
      status: row.status || 'pending',
      createdAt: row.created_at,
      updatedAt: row.updated_at
    };
  }

  /**
   * Parse JSON array safely
   */
  private parseJSONArray(jsonString?: string): string[] {
    if (!jsonString) return [];
    try {
      const parsed = JSON.parse(jsonString);
      return Array.isArray(parsed) ? parsed : [];
    } catch {
      return [];
    }
  }

  /**
   * Batch save decisions with optimized performance
   */
  async batchSaveDecisions(
    decisionInputs: DecisionInput[],
    options: {
      batchSize?: number;
      conflictResolution?: 'IGNORE' | 'REPLACE' | 'UPDATE';
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ inserted: number; updated: number; failed: number; errors: Error[] }> {
    if (decisionInputs.length === 0) {
      return { inserted: 0, updated: 0, failed: 0, errors: [] };
    }

    const { batchSize = 100, conflictResolution = 'IGNORE', onProgress } = options;
    const now = this.getCurrentTimestamp();

    // Prepare decision records with IDs and timestamps
    const dbRecords = decisionInputs.map(input => ({
      id: this.generateId(),
      decision_summary: input.decisionSummary,
      decision_type: input.decisionType || null,
      conversation_ids: JSON.stringify(input.conversationIds),
      problem_identified_at: input.problemIdentifiedAt || null,
      options_considered_at: input.optionsConsideredAt || null,
      decision_made_at: input.decisionMadeAt || now,
      clarity_score: input.clarityScore || 50,
      confidence_level: input.confidenceLevel || 50,
      consensus_level: 100, // Default for single-person decisions
      reversal_count: 0,
      modification_count: 0,
      information_completeness: input.informationCompleteness || 50,
      stakeholder_count: input.stakeholderCount || 1,
      alternatives_considered: input.alternativesConsidered || 1,
      risk_assessed: input.riskAssessed || false,
      success_factors: JSON.stringify([]),
      failure_factors: JSON.stringify([]),
      lessons_learned: '',
      tags: JSON.stringify(input.tags || []),
      priority: input.priority || 'medium',
      status: 'decided',
      created_at: now,
      updated_at: now
    }));

    try {
      if (conflictResolution === 'UPDATE') {
        // Use upsert for update behavior
        const result = this.batchUpsert(
          'decision_tracking',
          dbRecords,
          ['decision_summary', 'decision_made_at'], // Unique constraint
          {
            batchSize,
            onProgress
          }
        );
        return { 
          inserted: result.inserted, 
          updated: result.updated, 
          failed: result.failed,
          errors: []
        };
      } else {
        // Use batch insert for other conflict resolutions
        const result = this.batchInsert(
          'decision_tracking',
          dbRecords,
          {
            batchSize,
            conflictResolution: conflictResolution as 'IGNORE' | 'REPLACE',
            onProgress
          }
        );
        return {
          inserted: result.inserted,
          updated: 0,
          failed: result.failed,
          errors: result.errors
        };
      }
    } catch (error) {
      throw new Error(`Batch decisions save failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Batch update decision outcomes
   */
  async batchUpdateOutcomes(
    outcomes: DecisionOutcome[],
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ updated: number; failed: number }> {
    if (outcomes.length === 0) {
      return { updated: 0, failed: 0 };
    }

    const { batchSize = 100, onProgress } = options;
    let totalUpdated = 0;
    let totalFailed = 0;

    // Process updates in batches
    for (let i = 0; i < outcomes.length; i += batchSize) {
      const batch = outcomes.slice(i, i + batchSize);

      await this.transaction((db) => {
        const stmt = db.prepare(`
          UPDATE decision_tracking 
          SET 
            outcome_score = @outcomeScore,
            implementation_started_at = @implementationStartedAt,
            outcome_assessed_at = @outcomeAssessedAt,
            success_factors = @successFactors,
            failure_factors = @failureFactors,
            lessons_learned = @lessonsLearned,
            modification_count = modification_count + @modificationCount,
            status = 'assessed',
            updated_at = @updatedAt
          WHERE id = @decisionId
        `);

        for (const outcome of batch) {
          try {
            const params = {
              decisionId: outcome.decisionId,
              outcomeScore: Math.max(0, Math.min(100, outcome.outcomeScore)),
              implementationStartedAt: outcome.implementationStartedAt || null,
              outcomeAssessedAt: outcome.outcomeAssessedAt,
              successFactors: JSON.stringify(outcome.successFactors),
              failureFactors: JSON.stringify(outcome.failureFactors),
              lessonsLearned: outcome.lessonsLearned,
              modificationCount: outcome.modifications.length,
              updatedAt: this.getCurrentTimestamp()
            };

            const result = stmt.run(params);
            if (result.changes > 0) {
              totalUpdated++;
            }
          } catch (error) {
            totalFailed++;
            console.error(`Failed to update outcome for decision ${outcome.decisionId}:`, error);
          }
        }
      });

      if (onProgress) {
        onProgress(Math.min(i + batchSize, outcomes.length), outcomes.length);
      }
    }

    return { updated: totalUpdated, failed: totalFailed };
  }

  /**
   * Batch mark decisions as reversed
   */
  async batchMarkReversed(
    reversals: Array<{
      decisionId: string;
      reason: string;
    }>,
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ updated: number; failed: number }> {
    if (reversals.length === 0) {
      return { updated: 0, failed: 0 };
    }

    const { batchSize = 100, onProgress } = options;
    let totalUpdated = 0;
    let totalFailed = 0;

    // Process updates in batches
    for (let i = 0; i < reversals.length; i += batchSize) {
      const batch = reversals.slice(i, i + batchSize);

      await this.transaction((db) => {
        const stmt = db.prepare(`
          UPDATE decision_tracking 
          SET 
            reversal_count = reversal_count + 1,
            status = 'reversed',
            lessons_learned = CASE 
              WHEN lessons_learned = '' THEN @reason
              ELSE lessons_learned || '; ' || @reason
            END,
            updated_at = @updatedAt
          WHERE id = @decisionId
        `);

        for (const { decisionId, reason } of batch) {
          try {
            const result = stmt.run({
              decisionId,
              reason,
              updatedAt: this.getCurrentTimestamp()
            });

            if (result.changes > 0) {
              totalUpdated++;
            }
          } catch (error) {
            totalFailed++;
            console.error(`Failed to mark decision ${decisionId} as reversed:`, error);
          }
        }
      });

      if (onProgress) {
        onProgress(Math.min(i + batchSize, reversals.length), reversals.length);
      }
    }

    return { updated: totalUpdated, failed: totalFailed };
  }

  /**
   * Batch delete decisions
   */
  async batchDeleteDecisions(
    decisionIds: string[],
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ deleted: number; failed: number }> {
    if (decisionIds.length === 0) {
      return { deleted: 0, failed: 0 };
    }

    const { batchSize = 100, onProgress } = options;
    let totalDeleted = 0;
    let totalFailed = 0;

    // Process deletions in batches
    for (let i = 0; i < decisionIds.length; i += batchSize) {
      const batch = decisionIds.slice(i, i + batchSize);

      try {
        const placeholders = batch.map((_, index) => `@id${index}`).join(', ');
        const params = batch.reduce((acc, id, index) => {
          acc[`id${index}`] = id;
          return acc;
        }, {} as Record<string, string>);

        const sql = `
          DELETE FROM decision_tracking 
          WHERE id IN (${placeholders})
        `;

        const result = this.executeStatementRun(
          `batch_delete_decisions_${i}`,
          sql,
          params
        );

        totalDeleted += result.changes;
      } catch (error) {
        totalFailed += batch.length;
        console.error(`Failed to delete decisions batch ${i}:`, error);
      }

      if (onProgress) {
        onProgress(Math.min(i + batchSize, decisionIds.length), decisionIds.length);
      }
    }

    return { deleted: totalDeleted, failed: totalFailed };
  }

  /**
   * Batch track decisions from conversation analysis
   */
  async batchTrackDecisions(
    conversationDecisions: Array<{
      conversationId: string;
      decisions: any[];
      conversationMetadata?: any;
    }>,
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ tracked: number; failed: number }> {
    if (conversationDecisions.length === 0) {
      return { tracked: 0, failed: 0 };
    }

    const { batchSize = 50, onProgress } = options;
    let totalTracked = 0;
    let totalFailed = 0;

    // Flatten all decisions into batch-ready format
    const allDecisionInputs: DecisionInput[] = [];

    for (const { conversationId, decisions, conversationMetadata } of conversationDecisions) {
      for (const decision of decisions) {
        const decisionInput: DecisionInput = {
          decisionSummary: decision.summary || decision.content || 'Automated decision detection',
          decisionType: decision.type || 'operational',
          conversationIds: [conversationId],
          problemIdentifiedAt: decision.problemIdentifiedAt,
          optionsConsideredAt: decision.optionsConsideredAt,
          decisionMadeAt: decision.timestamp || decision.decisionMadeAt || Date.now(),
          clarityScore: decision.clarityScore || this.estimateDecisionClarity(decision),
          confidenceLevel: decision.confidenceLevel || this.estimateConfidenceLevel(decision),
          informationCompleteness: decision.informationCompleteness || 
            this.estimateInformationCompleteness(decision, conversationMetadata),
          stakeholderCount: decision.stakeholderCount || 1,
          alternativesConsidered: decision.alternativesConsidered || 
            this.countAlternatives(decision),
          riskAssessed: decision.riskAssessed || this.detectRiskAssessment(decision),
          tags: decision.tags || this.extractDecisionTags(decision),
          priority: decision.priority || this.assessDecisionPriority(decision)
        };

        allDecisionInputs.push(decisionInput);
      }
    }

    if (allDecisionInputs.length > 0) {
      try {
        const result = await this.batchSaveDecisions(allDecisionInputs, {
          batchSize,
          conflictResolution: 'IGNORE', // Avoid duplicates
          onProgress
        });

        totalTracked = result.inserted + result.updated;
        totalFailed = result.failed;
      } catch (error) {
        console.error('Batch decision tracking failed:', error);
        totalFailed = allDecisionInputs.length;
      }
    }

    return { tracked: totalTracked, failed: totalFailed };
  }

  /**
   * Estimate decision clarity from content
   */
  private estimateDecisionClarity(decision: any): number {
    const content = (decision.content || decision.summary || '').toLowerCase();
    let clarity = 50;

    // Positive indicators
    if (content.includes('decided') || content.includes('choose') || content.includes('select')) {
      clarity += 20;
    }
    if (content.includes('because') || content.includes('since') || content.includes('due to')) {
      clarity += 15;
    }
    if (content.includes('will') || content.includes('plan to') || content.includes('going to')) {
      clarity += 10;
    }

    // Negative indicators
    if (content.includes('maybe') || content.includes('might') || content.includes('perhaps')) {
      clarity -= 20;
    }
    if (content.includes('not sure') || content.includes('unsure') || content.includes('unclear')) {
      clarity -= 15;
    }

    return Math.max(0, Math.min(100, clarity));
  }

  /**
   * Estimate confidence level from content
   */
  private estimateConfidenceLevel(decision: any): number {
    const content = (decision.content || decision.summary || '').toLowerCase();
    let confidence = 50;

    // High confidence indicators
    if (content.includes('definitely') || content.includes('certainly') || content.includes('absolutely')) {
      confidence += 30;
    }
    if (content.includes('confident') || content.includes('sure') || content.includes('convinced')) {
      confidence += 20;
    }

    // Low confidence indicators
    if (content.includes('uncertain') || content.includes('doubt') || content.includes('hesitant')) {
      confidence -= 25;
    }
    if (content.includes('risky') || content.includes('gamble') || content.includes('unsure')) {
      confidence -= 20;
    }

    return Math.max(0, Math.min(100, confidence));
  }

  /**
   * Estimate information completeness
   */
  private estimateInformationCompleteness(decision: any, metadata?: any): number {
    let completeness = 40;

    const content = (decision.content || decision.summary || '').toLowerCase();

    // Information indicators
    if (content.includes('research') || content.includes('analysis') || content.includes('data')) {
      completeness += 20;
    }
    if (content.includes('compare') || content.includes('evaluate') || content.includes('consider')) {
      completeness += 15;
    }
    if (content.includes('expert') || content.includes('advisor') || content.includes('consultant')) {
      completeness += 10;
    }

    // Incomplete information indicators
    if (content.includes('assume') || content.includes('guess') || content.includes('estimate')) {
      completeness -= 15;
    }
    if (content.includes('limited info') || content.includes('not enough') || content.includes('incomplete')) {
      completeness -= 20;
    }

    // Boost based on conversation depth
    if (metadata?.depthScore && metadata.depthScore > 70) {
      completeness += 15;
    }

    return Math.max(0, Math.min(100, completeness));
  }

  /**
   * Count alternatives mentioned in decision
   */
  private countAlternatives(decision: any): number {
    const content = (decision.content || decision.summary || '').toLowerCase();
    let alternatives = 1;

    // Look for alternative indicators
    const altIndicators = ['or', 'option', 'alternative', 'choice', 'instead', 'versus', 'vs'];
    for (const indicator of altIndicators) {
      const regex = new RegExp(indicator, 'gi');
      const matches = content.match(regex);
      if (matches) {
        alternatives += matches.length;
      }
    }

    return Math.min(alternatives, 10); // Cap at 10
  }

  /**
   * Detect if risk was assessed
   */
  private detectRiskAssessment(decision: any): boolean {
    const content = (decision.content || decision.summary || '').toLowerCase();
    const riskKeywords = [
      'risk', 'danger', 'threat', 'consequence', 'downside', 
      'problem', 'issue', 'concern', 'drawback', 'disadvantage'
    ];

    return riskKeywords.some(keyword => content.includes(keyword));
  }

  /**
   * Extract decision tags from content
   */
  private extractDecisionTags(decision: any): string[] {
    const content = (decision.content || decision.summary || '').toLowerCase();
    const tags: string[] = [];

    // Category tags
    if (content.includes('strategic') || content.includes('strategy')) tags.push('strategic');
    if (content.includes('technical') || content.includes('technology')) tags.push('technical');
    if (content.includes('business') || content.includes('commercial')) tags.push('business');
    if (content.includes('personal') || content.includes('individual')) tags.push('personal');
    if (content.includes('financial') || content.includes('budget') || content.includes('cost')) tags.push('financial');
    if (content.includes('urgent') || content.includes('immediate')) tags.push('urgent');
    if (content.includes('long-term') || content.includes('future')) tags.push('long-term');
    if (content.includes('team') || content.includes('group') || content.includes('collaboration')) tags.push('collaborative');

    return tags;
  }

  /**
   * Assess decision priority
   */
  private assessDecisionPriority(decision: any): 'critical' | 'high' | 'medium' | 'low' {
    const content = (decision.content || decision.summary || '').toLowerCase();

    // Critical priority indicators
    if (content.includes('critical') || content.includes('urgent') || content.includes('emergency')) {
      return 'critical';
    }
    if (content.includes('deadline') || content.includes('asap') || content.includes('immediately')) {
      return 'critical';
    }

    // High priority indicators
    if (content.includes('important') || content.includes('significant') || content.includes('major')) {
      return 'high';
    }
    if (content.includes('soon') || content.includes('quickly') || content.includes('priority')) {
      return 'high';
    }

    // Low priority indicators
    if (content.includes('eventually') || content.includes('someday') || content.includes('later')) {
      return 'low';
    }
    if (content.includes('minor') || content.includes('small') || content.includes('trivial')) {
      return 'low';
    }

    return 'medium'; // Default
  }
}

/**
 * Knowledge Gaps Repository
 * 
 * Handles database operations for knowledge gap detection and tracking:
 * - Gap identification and clustering
 * - Learning curve tracking
 * - Resolution monitoring
 * - Topic coverage analysis
 * - Expertise mapping
 */

import { AnalyticsRepository, TimeRange, PaginationOptions } from './AnalyticsRepository.js';
import { DatabaseManager } from '../../storage/Database.js';

export interface KnowledgeGap {
  id: string;
  gapType: 'question' | 'topic' | 'skill' | 'concept';
  content: string;
  normalizedContent: string;

  // Metrics
  frequency: number;
  firstOccurrence: number;
  lastOccurrence: number;
  explorationDepth: number;

  // Resolution tracking
  resolved: boolean;
  resolutionConversationId?: string;
  resolutionDate?: number;
  resolutionQuality: number;

  // Learning metrics
  learningCurveGradient: number;
  estimatedTimeToMastery?: number; // hours

  // Related information
  relatedEntities: string[];
  relatedGaps: string[];
  suggestedActions: string[];
  suggestedResources: string[];

  createdAt: number;
  updatedAt: number;
}

export interface KnowledgeGapInput {
  gapType: 'question' | 'topic' | 'skill' | 'concept';
  content: string;
  normalizedContent?: string;
  frequency?: number;
  firstOccurrence?: number;
  lastOccurrence?: number;
  explorationDepth?: number;
  relatedEntities?: string[];
  suggestedActions?: string[];
  suggestedResources?: string[];
}

export interface GapCluster {
  clusterId: string;
  gaps: KnowledgeGap[];
  centroidContent: string;
  totalFrequency: number;
  averageExploration: number;
  resolvedCount: number;
  priority: 'critical' | 'high' | 'medium' | 'low';
}

export interface LearningProgress {
  gapId: string;
  topic: string;
  progressPoints: Array<{
    timestamp: number;
    understandingLevel: number;
    conversationId: string;
  }>;
  currentLevel: number;
  gradient: number;
  plateauDetected: boolean;
  estimatedCompletion?: number;
}

export interface TopicCoverage {
  topic: string;
  mentionCount: number;
  explorationDepth: number;
  relatedTopics: string[];
  gapCount: number;
  averageResolutionTime: number;
  coverageScore: number; // 0-100
}

/**
 * Repository for knowledge gap detection and learning analytics
 */
export class KnowledgeGapsRepository extends AnalyticsRepository {

  constructor(databaseManager: DatabaseManager) {
    super(databaseManager);
  }

  /**
   * Save or update a knowledge gap
   */
  async saveGap(input: KnowledgeGapInput): Promise<string> {
    // Check if gap already exists (similar content)
    const existingGap = await this.findSimilarGap(input.normalizedContent || input.content);

    if (existingGap) {
      // Update existing gap
      await this.incrementGapFrequency(existingGap.id, input.lastOccurrence || Date.now());
      return existingGap.id;
    }

    // Create new gap
    const id = this.generateId();
    const now = this.getCurrentTimestamp();

    const sql = `
      INSERT INTO knowledge_gaps (
        id, gap_type, content, normalized_content,
        frequency, first_occurrence, last_occurrence, exploration_depth,
        resolved, resolution_quality, learning_curve_gradient,
        estimated_time_to_mastery, related_entities, related_gaps,
        suggested_actions, suggested_resources, created_at, updated_at
      ) VALUES (
        @id, @gapType, @content, @normalizedContent,
        @frequency, @firstOccurrence, @lastOccurrence, @explorationDepth,
        @resolved, @resolutionQuality, @learningCurveGradient,
        @estimatedTimeToMastery, @relatedEntities, @relatedGaps,
        @suggestedActions, @suggestedResources, @createdAt, @updatedAt
      )
    `;

    const params = {
      id,
      gapType: input.gapType,
      content: input.content,
      normalizedContent: input.normalizedContent || this.normalizeContent(input.content),
      frequency: input.frequency || 1,
      firstOccurrence: input.firstOccurrence || now,
      lastOccurrence: input.lastOccurrence || now,
      explorationDepth: input.explorationDepth || 0,
      resolved: false,
      resolutionQuality: 0,
      learningCurveGradient: 0,
      estimatedTimeToMastery: null,
      relatedEntities: JSON.stringify(input.relatedEntities || []),
      relatedGaps: JSON.stringify([]),
      suggestedActions: JSON.stringify(input.suggestedActions || []),
      suggestedResources: JSON.stringify(input.suggestedResources || []),
      createdAt: now,
      updatedAt: now
    };

    try {
      this.executeStatementRun('save_knowledge_gap', sql, params);
      return id;
    } catch (error) {
      this.handleConstraintError(error as Error, 'knowledge gap');
    }
  }

  /**
   * Find gaps that need resolution
   */
  async getUnresolvedGaps(
    gapType?: 'question' | 'topic' | 'skill' | 'concept',
    options?: PaginationOptions
  ): Promise<KnowledgeGap[]> {
    const { limit, offset } = this.validatePagination(options?.limit, options?.offset);

    let whereClause = 'WHERE resolved = FALSE';
    const params: any = { limit, offset };

    if (gapType) {
      whereClause += ' AND gap_type = @gapType';
      params.gapType = gapType;
    }

    const sql = `
      SELECT 
        id, gap_type, content, normalized_content,
        frequency, first_occurrence, last_occurrence, exploration_depth,
        resolved, resolution_conversation_id, resolution_date, resolution_quality,
        learning_curve_gradient, estimated_time_to_mastery,
        related_entities, related_gaps, suggested_actions, suggested_resources,
        created_at, updated_at
      FROM knowledge_gaps
      ${whereClause}
      ORDER BY frequency DESC, last_occurrence DESC
      LIMIT @limit OFFSET @offset
    `;

    const results = this.executeStatementAll<any>('get_unresolved_gaps', sql, params);
    return results.map(row => this.mapRowToGap(row));
  }

  /**
   * Get gaps by priority (frequency and recency weighted)
   */
  async getGapsByPriority(
    timeRange?: TimeRange,
    options?: PaginationOptions
  ): Promise<Array<KnowledgeGap & { priority: number }>> {
    const validTimeRange = this.validateTimeRange(timeRange);
    const { limit, offset } = this.validatePagination(options?.limit, options?.offset);

    const sql = `
      WITH gap_priorities AS (
        SELECT 
          *,
          -- Priority calculation: frequency weight + recency weight + exploration deficit
          (
            -- Frequency weight (0-40 points)
            LEAST(frequency * 5.0, 40) +
            -- Recency weight (0-30 points) 
            (30 * (1 - ((@end - last_occurrence) / CAST(@end - @start AS REAL)))) +
            -- Exploration deficit weight (0-30 points)
            (30 * (1 - exploration_depth / 100.0))
          ) as priority_score
        FROM knowledge_gaps
        WHERE resolved = FALSE
          AND last_occurrence BETWEEN @start AND @end
      )
      SELECT 
        id, gap_type, content, normalized_content,
        frequency, first_occurrence, last_occurrence, exploration_depth,
        resolved, resolution_conversation_id, resolution_date, resolution_quality,
        learning_curve_gradient, estimated_time_to_mastery,
        related_entities, related_gaps, suggested_actions, suggested_resources,
        created_at, updated_at, priority_score
      FROM gap_priorities
      ORDER BY priority_score DESC, frequency DESC
      LIMIT @limit OFFSET @offset
    `;

    const results = this.executeStatementAll<any>(
      'gaps_by_priority',
      sql,
      { ...validTimeRange, limit, offset }
    );

    return results.map(row => ({
      ...this.mapRowToGap(row),
      priority: Math.round(row.priority_score * 10) / 10
    }));
  }

  /**
   * Cluster similar gaps
   */
  async getGapClusters(
    minClusterSize: number = 2,
    similarityThreshold: number = 0.7
  ): Promise<GapCluster[]> {
    // Get unresolved gaps
    const gaps = await this.getUnresolvedGaps();

    if (gaps.length === 0) {
      return [];
    }

    // Simple clustering based on normalized content similarity
    const clusters = new Map<string, KnowledgeGap[]>();
    const processed = new Set<string>();

    for (const gap of gaps) {
      if (processed.has(gap.id)) {
        continue;
      }

      const cluster: KnowledgeGap[] = [gap];
      processed.add(gap.id);

      // Find similar gaps
      for (const otherGap of gaps) {
        if (processed.has(otherGap.id)) {
          continue;
        }

        const similarity = this.calculateStringSimilarity(
          gap.normalizedContent,
          otherGap.normalizedContent
        );

        if (similarity >= similarityThreshold) {
          cluster.push(otherGap);
          processed.add(otherGap.id);
        }
      }

      if (cluster.length >= minClusterSize) {
        clusters.set(gap.id, cluster);
      }
    }

    // Convert to GapCluster objects
    const gapClusters: GapCluster[] = [];

    for (const [clusterId, clusterGaps] of clusters.entries()) {
      const totalFrequency = clusterGaps.reduce((sum, g) => sum + g.frequency, 0);
      const averageExploration = clusterGaps.reduce((sum, g) => sum + g.explorationDepth, 0) / clusterGaps.length;
      const resolvedCount = clusterGaps.filter(g => g.resolved).length;

      // Determine priority
      let priority: 'critical' | 'high' | 'medium' | 'low' = 'low';
      if (totalFrequency >= 10 && averageExploration < 30) {
        priority = 'critical';
      } else if (totalFrequency >= 5 && averageExploration < 50) {
        priority = 'high';
      } else if (totalFrequency >= 3) {
        priority = 'medium';
      }

      gapClusters.push({
        clusterId,
        gaps: clusterGaps,
        centroidContent: clusterGaps[0].content, // Use first as representative
        totalFrequency,
        averageExploration,
        resolvedCount,
        priority
      });
    }

    return gapClusters.sort((a, b) => b.totalFrequency - a.totalFrequency);
  }

  /**
   * Track learning progress for a gap
   */
  async getLearningProgress(gapId: string): Promise<LearningProgress | null> {
    const gap = await this.getGap(gapId);
    if (!gap) {
      return null;
    }

    // Get related conversations and extract understanding progression
    const sql = `
      WITH gap_conversations AS (
        SELECT DISTINCT c.id, c.created_at, c.title
        FROM conversations c
        JOIN messages m ON c.id = m.conversation_id
        WHERE LOWER(m.content) LIKE '%' || LOWER(@searchTerm) || '%'
          OR LOWER(c.title) LIKE '%' || LOWER(@searchTerm) || '%'
        ORDER BY c.created_at
      ),
      progress_points AS (
        SELECT 
          gc.id as conversation_id,
          gc.created_at,
          -- Estimate understanding level based on message complexity and insights
          CASE 
            WHEN ca.insight_count > 0 AND ca.depth_score > 70 THEN 85
            WHEN ca.depth_score > 60 THEN 70
            WHEN ca.depth_score > 40 THEN 55
            WHEN ca.depth_score > 20 THEN 35
            ELSE 15
          END as understanding_level
        FROM gap_conversations gc
        LEFT JOIN conversation_analytics ca ON gc.id = ca.conversation_id
      )
      SELECT 
        conversation_id,
        created_at,
        understanding_level
      FROM progress_points
      ORDER BY created_at
    `;

    const progressResults = this.executeStatementAll<{
      conversation_id: string;
      created_at: number;
      understanding_level: number;
    }>('learning_progress', sql, { 
      searchTerm: gap.normalizedContent.split(' ').slice(0, 3).join(' ') 
    });

    if (progressResults.length === 0) {
      return null;
    }

    const progressPoints = progressResults.map(row => ({
      timestamp: row.created_at,
      understandingLevel: row.understanding_level,
      conversationId: row.conversation_id
    }));

    // Calculate gradient (learning rate)
    const gradient = this.calculateLearningGradient(progressPoints);

    // Detect plateau (no improvement in last 3 data points)
    const plateauDetected = this.detectPlateau(progressPoints);

    // Estimate completion time
    const currentLevel = progressPoints[progressPoints.length - 1]?.understandingLevel || 0;
    const estimatedCompletion = this.estimateCompletionTime(gradient, currentLevel);

    return {
      gapId: gap.id,
      topic: gap.content,
      progressPoints,
      currentLevel,
      gradient,
      plateauDetected,
      estimatedCompletion
    };
  }

  /**
   * Get topic coverage analysis
   */
  async getTopicCoverage(timeRange?: TimeRange): Promise<TopicCoverage[]> {
    const validTimeRange = this.validateTimeRange(timeRange);

    const sql = `
      WITH topic_mentions AS (
        SELECT 
          kg.normalized_content as topic,
          COUNT(DISTINCT m.conversation_id) as mention_count,
          AVG(kg.exploration_depth) as avg_exploration,
          COUNT(kg.id) as gap_count,
          AVG(CASE WHEN kg.resolved THEN 
                (kg.resolution_date - kg.first_occurrence) / (1000 * 60 * 60.0)
              ELSE NULL END) as avg_resolution_hours
        FROM knowledge_gaps kg
        LEFT JOIN messages m ON (
          LOWER(m.content) LIKE '%' || LOWER(kg.normalized_content) || '%'
          AND m.created_at BETWEEN @start AND @end
        )
        WHERE kg.gap_type IN ('topic', 'concept')
        GROUP BY kg.normalized_content
        HAVING mention_count > 0
      )
      SELECT 
        topic,
        mention_count,
        avg_exploration as exploration_depth,
        gap_count,
        COALESCE(avg_resolution_hours, 0) as avg_resolution_time,
        -- Coverage score: high mentions + high exploration - many gaps
        LEAST(100, 
          (mention_count * 10) + 
          (avg_exploration * 0.8) - 
          (gap_count * 5)
        ) as coverage_score
      FROM topic_mentions
      WHERE mention_count >= 2
      ORDER BY coverage_score DESC, mention_count DESC
      LIMIT 50
    `;

    const results = this.executeStatementAll<any>('topic_coverage', sql, validTimeRange);

    return results.map(row => ({
      topic: row.topic,
      mentionCount: row.mention_count,
      explorationDepth: Math.round(row.exploration_depth),
      relatedTopics: [], // Could be enhanced with similarity analysis
      gapCount: row.gap_count,
      averageResolutionTime: Math.round(row.avg_resolution_time * 10) / 10,
      coverageScore: Math.max(0, Math.round(row.coverage_score))
    }));
  }

  /**
   * Mark gap as resolved
   */
  async markResolved(
    gapId: string,
    resolutionConversationId: string,
    resolutionQuality: number = 80
  ): Promise<void> {
    const now = this.getCurrentTimestamp();

    const sql = `
      UPDATE knowledge_gaps 
      SET 
        resolved = TRUE,
        resolution_conversation_id = @resolutionConversationId,
        resolution_date = @resolutionDate,
        resolution_quality = @resolutionQuality,
        updated_at = @updatedAt
      WHERE id = @gapId
    `;

    this.executeStatementRun('mark_gap_resolved', sql, {
      gapId,
      resolutionConversationId,
      resolutionDate: now,
      resolutionQuality,
      updatedAt: now
    });
  }

  /**
   * Update gap exploration depth
   */
  async updateExplorationDepth(gapId: string, depth: number): Promise<void> {
    const sql = `
      UPDATE knowledge_gaps 
      SET 
        exploration_depth = @depth,
        updated_at = @updatedAt
      WHERE id = @gapId
    `;

    this.executeStatementRun('update_exploration_depth', sql, {
      gapId,
      depth: Math.max(0, Math.min(100, depth)),
      updatedAt: this.getCurrentTimestamp()
    });
  }

  /**
   * Get gap by ID
   */
  async getGap(gapId: string): Promise<KnowledgeGap | null> {
    const sql = `
      SELECT 
        id, gap_type, content, normalized_content,
        frequency, first_occurrence, last_occurrence, exploration_depth,
        resolved, resolution_conversation_id, resolution_date, resolution_quality,
        learning_curve_gradient, estimated_time_to_mastery,
        related_entities, related_gaps, suggested_actions, suggested_resources,
        created_at, updated_at
      FROM knowledge_gaps
      WHERE id = @gapId
    `;

    const result = this.executeStatement<any>('get_gap', sql, { gapId });
    return result ? this.mapRowToGap(result) : null;
  }

  /**
   * Find similar gap by content
   */
  private async findSimilarGap(content: string): Promise<KnowledgeGap | null> {
    const normalizedContent = this.normalizeContent(content);

    const sql = `
      SELECT 
        id, gap_type, content, normalized_content,
        frequency, first_occurrence, last_occurrence, exploration_depth,
        resolved, resolution_conversation_id, resolution_date, resolution_quality,
        learning_curve_gradient, estimated_time_to_mastery,
        related_entities, related_gaps, suggested_actions, suggested_resources,
        created_at, updated_at
      FROM knowledge_gaps
      WHERE normalized_content = @normalizedContent
        OR (
          LENGTH(@normalizedContent) > 10 
          AND normalized_content LIKE '%' || @normalizedContent || '%'
        )
      ORDER BY frequency DESC
      LIMIT 1
    `;

    const result = this.executeStatement<any>('find_similar_gap', sql, { normalizedContent });
    return result ? this.mapRowToGap(result) : null;
  }

  /**
   * Increment gap frequency
   */
  private async incrementGapFrequency(gapId: string, lastOccurrence: number): Promise<void> {
    const sql = `
      UPDATE knowledge_gaps 
      SET 
        frequency = frequency + 1,
        last_occurrence = @lastOccurrence,
        updated_at = @updatedAt
      WHERE id = @gapId
    `;

    this.executeStatementRun('increment_gap_frequency', sql, {
      gapId,
      lastOccurrence,
      updatedAt: this.getCurrentTimestamp()
    });
  }

  /**
   * Normalize content for similarity comparison
   */
  private normalizeContent(content: string): string {
    return content
      .toLowerCase()
      .replace(/[^\w\s]/g, '')
      .replace(/\s+/g, ' ')
      .trim();
  }

  /**
   * Calculate string similarity (simple Jaccard similarity)
   */
  private calculateStringSimilarity(str1: string, str2: string): number {
    const words1 = new Set(str1.split(' '));
    const words2 = new Set(str2.split(' '));

    const intersection = new Set([...words1].filter(word => words2.has(word)));
    const union = new Set([...words1, ...words2]);

    return union.size > 0 ? intersection.size / union.size : 0;
  }

  /**
   * Calculate learning gradient from progress points
   */
  private calculateLearningGradient(
    points: Array<{ timestamp: number; understandingLevel: number }>
  ): number {
    if (points.length < 2) {
      return 0;
    }

    // Simple linear regression
    const n = points.length;
    const sumX = points.reduce((sum, p) => sum + p.timestamp, 0);
    const sumY = points.reduce((sum, p) => sum + p.understandingLevel, 0);
    const sumXY = points.reduce((sum, p) => sum + p.timestamp * p.understandingLevel, 0);
    const sumX2 = points.reduce((sum, p) => sum + p.timestamp * p.timestamp, 0);

    const denominator = n * sumX2 - sumX * sumX;
    if (denominator === 0) {
      return 0;
    }

    const slope = (n * sumXY - sumX * sumY) / denominator;

    // Convert to understanding points per day
    return slope * (24 * 60 * 60 * 1000);
  }

  /**
   * Detect if learning has plateaued
   */
  private detectPlateau(
    points: Array<{ timestamp: number; understandingLevel: number }>
  ): boolean {
    if (points.length < 4) {
      return false;
    }

    // Check if last 3 points show minimal improvement
    const recentPoints = points.slice(-3);
    const improvements = [];

    for (let i = 1; i < recentPoints.length; i++) {
      improvements.push(recentPoints[i].understandingLevel - recentPoints[i - 1].understandingLevel);
    }

    // Plateau if average improvement is less than 2 points
    const avgImprovement = improvements.reduce((sum, imp) => sum + imp, 0) / improvements.length;
    return avgImprovement < 2;
  }

  /**
   * Estimate completion time based on gradient and current level
   */
  private estimateCompletionTime(gradient: number, currentLevel: number): number | undefined {
    if (gradient <= 0 || currentLevel >= 90) {
      return undefined;
    }

    const targetLevel = 85;
    const remainingPoints = targetLevel - currentLevel;

    // Days to completion
    const daysToCompletion = remainingPoints / gradient;

    // Return hours, capped at reasonable maximum
    return Math.min(daysToCompletion * 24, 720); // Max 30 days
  }

  /**
   * Map database row to KnowledgeGap interface
   */
  private mapRowToGap(row: any): KnowledgeGap {
    return {
      id: row.id,
      gapType: row.gap_type,
      content: row.content,
      normalizedContent: row.normalized_content,
      frequency: row.frequency,
      firstOccurrence: row.first_occurrence,
      lastOccurrence: row.last_occurrence,
      explorationDepth: row.exploration_depth,
      resolved: Boolean(row.resolved),
      resolutionConversationId: row.resolution_conversation_id,
      resolutionDate: row.resolution_date,
      resolutionQuality: row.resolution_quality || 0,
      learningCurveGradient: row.learning_curve_gradient || 0,
      estimatedTimeToMastery: row.estimated_time_to_mastery,
      relatedEntities: this.parseJSONArray(row.related_entities),
      relatedGaps: this.parseJSONArray(row.related_gaps),
      suggestedActions: this.parseJSONArray(row.suggested_actions),
      suggestedResources: this.parseJSONArray(row.suggested_resources),
      createdAt: row.created_at,
      updatedAt: row.updated_at
    };
  }

  /**
   * Parse JSON array safely
   */
  private parseJSONArray(jsonString?: string): string[] {
    if (!jsonString) return [];
    try {
      const parsed = JSON.parse(jsonString);
      return Array.isArray(parsed) ? parsed : [];
    } catch {
      return [];
    }
  }

  /**
   * Batch save knowledge gaps with optimized performance
   */
  async batchSaveGaps(
    gapInputs: KnowledgeGapInput[],
    options: {
      batchSize?: number;
      conflictResolution?: 'IGNORE' | 'REPLACE' | 'UPDATE';
      onProgress?: (processed: number, total: number) => void;
      deduplication?: boolean;
    } = {}
  ): Promise<{ inserted: number; updated: number; failed: number; duplicates: number; errors: Error[] }> {
    if (gapInputs.length === 0) {
      return { inserted: 0, updated: 0, failed: 0, duplicates: 0, errors: [] };
    }

    const { batchSize = 100, conflictResolution = 'UPDATE', onProgress, deduplication = true } = options;
    const now = this.getCurrentTimestamp();

    let processedInputs = gapInputs;
    let duplicates = 0;

    // Deduplicate similar gaps if enabled
    if (deduplication) {
      const { uniqueGaps, duplicateCount } = await this.deduplicateGaps(gapInputs);
      processedInputs = uniqueGaps;
      duplicates = duplicateCount;
    }

    // Prepare gap records with IDs and timestamps
    const dbRecords = processedInputs.map(input => ({
      id: this.generateId(),
      gap_type: input.gapType,
      content: input.content,
      normalized_content: input.normalizedContent || this.normalizeContent(input.content),
      frequency: input.frequency || 1,
      first_occurrence: input.firstOccurrence || now,
      last_occurrence: input.lastOccurrence || now,
      exploration_depth: input.explorationDepth || 0,
      resolved: false,
      resolution_quality: 0,
      learning_curve_gradient: 0,
      estimated_time_to_mastery: null,
      related_entities: JSON.stringify(input.relatedEntities || []),
      related_gaps: JSON.stringify([]),
      suggested_actions: JSON.stringify(input.suggestedActions || []),
      suggested_resources: JSON.stringify(input.suggestedResources || []),
      created_at: now,
      updated_at: now
    }));

    try {
      if (conflictResolution === 'UPDATE') {
        // Use upsert for update behavior based on normalized content
        const result = this.batchUpsert(
          'knowledge_gaps',
          dbRecords,
          ['normalized_content', 'gap_type'],
          {
            batchSize,
            onProgress
          }
        );
        return { 
          inserted: result.inserted, 
          updated: result.updated, 
          failed: result.failed,
          duplicates,
          errors: []
        };
      } else {
        // Use batch insert for other conflict resolutions
        const result = this.batchInsert(
          'knowledge_gaps',
          dbRecords,
          {
            batchSize,
            conflictResolution: conflictResolution as 'IGNORE' | 'REPLACE',
            onProgress
          }
        );
        return {
          inserted: result.inserted,
          updated: 0,
          failed: result.failed,
          duplicates,
          errors: result.errors
        };
      }
    } catch (error) {
      throw new Error(`Batch knowledge gaps save failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Batch update gap exploration depth
   */
  async batchUpdateExplorationDepth(
    updates: Array<{
      gapId: string;
      depth: number;
    }>,
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ updated: number; failed: number }> {
    if (updates.length === 0) {
      return { updated: 0, failed: 0 };
    }

    const { batchSize = 100, onProgress } = options;
    let totalUpdated = 0;
    let totalFailed = 0;

    // Process updates in batches
    for (let i = 0; i < updates.length; i += batchSize) {
      const batch = updates.slice(i, i + batchSize);

      await this.transaction((db) => {
        const stmt = db.prepare(`
          UPDATE knowledge_gaps 
          SET 
            exploration_depth = @depth,
            updated_at = @updatedAt
          WHERE id = @gapId
        `);

        for (const { gapId, depth } of batch) {
          try {
            const result = stmt.run({
              gapId,
              depth: Math.max(0, Math.min(100, depth)),
              updatedAt: this.getCurrentTimestamp()
            });

            if (result.changes > 0) {
              totalUpdated++;
            }
          } catch (error) {
            totalFailed++;
            console.error(`Failed to update exploration depth for gap ${gapId}:`, error);
          }
        }
      });

      if (onProgress) {
        onProgress(Math.min(i + batchSize, updates.length), updates.length);
      }
    }

    return { updated: totalUpdated, failed: totalFailed };
  }

  /**
   * Batch mark gaps as resolved
   */
  async batchMarkResolved(
    resolutions: Array<{
      gapId: string;
      resolutionConversationId: string;
      resolutionQuality?: number;
    }>,
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ updated: number; failed: number }> {
    if (resolutions.length === 0) {
      return { updated: 0, failed: 0 };
    }

    const { batchSize = 100, onProgress } = options;
    let totalUpdated = 0;
    let totalFailed = 0;

    // Process updates in batches
    for (let i = 0; i < resolutions.length; i += batchSize) {
      const batch = resolutions.slice(i, i + batchSize);

      await this.transaction((db) => {
        const stmt = db.prepare(`
          UPDATE knowledge_gaps 
          SET 
            resolved = TRUE,
            resolution_conversation_id = @resolutionConversationId,
            resolution_date = @resolutionDate,
            resolution_quality = @resolutionQuality,
            updated_at = @updatedAt
          WHERE id = @gapId
        `);

        for (const { gapId, resolutionConversationId, resolutionQuality = 80 } of batch) {
          try {
            const result = stmt.run({
              gapId,
              resolutionConversationId,
              resolutionDate: this.getCurrentTimestamp(),
              resolutionQuality,
              updatedAt: this.getCurrentTimestamp()
            });

            if (result.changes > 0) {
              totalUpdated++;
            }
          } catch (error) {
            totalFailed++;
            console.error(`Failed to mark gap ${gapId} as resolved:`, error);
          }
        }
      });

      if (onProgress) {
        onProgress(Math.min(i + batchSize, resolutions.length), resolutions.length);
      }
    }

    return { updated: totalUpdated, failed: totalFailed };
  }

  /**
   * Batch increment gap frequencies
   */
  async batchIncrementFrequency(
    gapIds: string[],
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
    } = {}
  ): Promise<{ updated: number; failed: number }> {
    if (gapIds.length === 0) {
      return { updated: 0, failed: 0 };
    }

    const { batchSize = 100, onProgress } = options;
    let totalUpdated = 0;
    let totalFailed = 0;
    const now = this.getCurrentTimestamp();

    // Process updates in batches
    for (let i = 0; i < gapIds.length; i += batchSize) {
      const batch = gapIds.slice(i, i + batchSize);

      await this.transaction((db) => {
        const stmt = db.prepare(`
          UPDATE knowledge_gaps 
          SET 
            frequency = frequency + 1,
            last_occurrence = @lastOccurrence,
            updated_at = @updatedAt
          WHERE id = @gapId
        `);

        for (const gapId of batch) {
          try {
            const result = stmt.run({
              gapId,
              lastOccurrence: now,
              updatedAt: now
            });

            if (result.changes > 0) {
              totalUpdated++;
            }
          } catch (error) {
            totalFailed++;
            console.error(`Failed to increment frequency for gap ${gapId}:`, error);
          }
        }
      });

      if (onProgress) {
        onProgress(Math.min(i + batchSize, gapIds.length), gapIds.length);
      }
    }

    return { updated: totalUpdated, failed: totalFailed };
  }

  /**
   * Batch process knowledge gaps from conversation analysis
   */
  async batchProcessGapsFromConversations(
    conversationGaps: Array<{
      conversationId: string;
      gaps: any[];
      conversationMetadata?: any;
    }>,
    options: {
      batchSize?: number;
      onProgress?: (processed: number, total: number) => void;
      deduplication?: boolean;
    } = {}
  ): Promise<{ processed: number; failed: number; duplicates: number }> {
    if (conversationGaps.length === 0) {
      return { processed: 0, failed: 0, duplicates: 0 };
    }

    const { batchSize = 50, onProgress, deduplication = true } = options;
    let totalProcessed = 0;
    let totalFailed = 0;
    let totalDuplicates = 0;

    // Flatten all gaps into batch-ready format
    const allGapInputs: KnowledgeGapInput[] = [];

    for (const { conversationId, gaps, conversationMetadata } of conversationGaps) {
      for (const gap of gaps) {
        const gapInput: KnowledgeGapInput = {
          gapType: gap.type || 'question',
          content: gap.content || gap.description || gap.question || 'Unknown gap',
          normalizedContent: gap.normalizedContent,
          frequency: gap.frequency || 1,
          firstOccurrence: gap.firstOccurrence || Date.now(),
          lastOccurrence: gap.lastOccurrence || Date.now(),
          explorationDepth: gap.explorationDepth || this.estimateExplorationDepth(gap, conversationMetadata),
          relatedEntities: gap.relatedEntities || this.extractRelatedEntities(gap),
          suggestedActions: gap.suggestedActions || this.generateSuggestedActions(gap),
          suggestedResources: gap.suggestedResources || this.generateSuggestedResources(gap)
        };

        allGapInputs.push(gapInput);
      }
    }

    if (allGapInputs.length > 0) {
      try {
        const result = await this.batchSaveGaps(allGapInputs, {
          batchSize,
          conflictResolution: 'UPDATE', // Merge with existing gaps
          onProgress,
          deduplication
        });

        totalProcessed = result.inserted + result.updated;
        totalFailed = result.failed;
        totalDuplicates = result.duplicates;
      } catch (error) {
        console.error('Batch gap processing failed:', error);
        totalFailed = allGapInputs.length;
      }
    }

    return { processed: totalProcessed, failed: totalFailed, duplicates: totalDuplicates };
  }

  /**
   * Deduplicate similar gaps based on normalized content
   */
  private async deduplicateGaps(
    gaps: KnowledgeGapInput[]
  ): Promise<{ uniqueGaps: KnowledgeGapInput[]; duplicateCount: number }> {
    const uniqueGaps: KnowledgeGapInput[] = [];
    const seenContent = new Set<string>();
    let duplicateCount = 0;

    // Group by normalized content and merge similar gaps
    const contentGroups = new Map<string, KnowledgeGapInput[]>();

    for (const gap of gaps) {
      const normalizedContent = gap.normalizedContent || this.normalizeContent(gap.content);

      if (!contentGroups.has(normalizedContent)) {
        contentGroups.set(normalizedContent, []);
      }
      contentGroups.get(normalizedContent)!.push(gap);
    }

    // Merge similar gaps in each group
    for (const [normalizedContent, groupGaps] of contentGroups.entries()) {
      if (seenContent.has(normalizedContent)) {
        duplicateCount += groupGaps.length;
        continue;
      }

      if (groupGaps.length === 1) {
        uniqueGaps.push(groupGaps[0]);
      } else {
        // Merge multiple similar gaps
        const mergedGap = this.mergeGaps(groupGaps);
        uniqueGaps.push(mergedGap);
        duplicateCount += groupGaps.length - 1;
      }

      seenContent.add(normalizedContent);
    }

    return { uniqueGaps, duplicateCount };
  }

  /**
   * Merge multiple similar gaps into one
   */
  private mergeGaps(gaps: KnowledgeGapInput[]): KnowledgeGapInput {
    const firstGap = gaps[0];

    return {
      gapType: firstGap.gapType,
      content: firstGap.content, // Use first gap's content as primary
      normalizedContent: firstGap.normalizedContent,
      frequency: gaps.reduce((sum, gap) => sum + (gap.frequency || 1), 0),
      firstOccurrence: Math.min(...gaps.map(gap => gap.firstOccurrence || Date.now())),
      lastOccurrence: Math.max(...gaps.map(gap => gap.lastOccurrence || Date.now())),
      explorationDepth: Math.max(...gaps.map(gap => gap.explorationDepth || 0)),
      relatedEntities: Array.from(new Set(gaps.flatMap(gap => gap.relatedEntities || []))),
      suggestedActions: Array.from(new Set(gaps.flatMap(gap => gap.suggestedActions || []))),
      suggestedResources: Array.from(new Set(gaps.flatMap(gap => gap.suggestedResources || [])))
    };
  }

  /**
   * Estimate exploration depth from gap and conversation metadata
   */
  private estimateExplorationDepth(gap: any, conversationMetadata?: any): number {
    let depth = 10; // Base depth

    const content = (gap.content || gap.description || '').toLowerCase();

    // Content complexity indicators
    if (content.length > 200) depth += 20;
    if (content.includes('complex') || content.includes('complicated')) depth += 15;
    if (content.includes('detailed') || content.includes('thorough')) depth += 15;
    if (content.includes('analysis') || content.includes('research')) depth += 20;

    // Question depth indicators  
    if (content.includes('why') || content.includes('how')) depth += 10;
    if (content.includes('what if') || content.includes('suppose')) depth += 15;
    if (content.includes('implications') || content.includes('consequences')) depth += 20;

    // Conversation context boost
    if (conversationMetadata?.depthScore) {
      depth += Math.floor(conversationMetadata.depthScore * 0.3);
    }
    if (conversationMetadata?.messageCount && conversationMetadata.messageCount > 10) {
      depth += 10;
    }

    return Math.min(100, depth);
  }

  /**
   * Extract related entities from gap content
   */
  private extractRelatedEntities(gap: any): string[] {
    const content = (gap.content || gap.description || '').toLowerCase();
    const entities: string[] = [];

    // Technical terms and concepts
    const techPatterns = [
      /\b(api|database|algorithm|framework|library|protocol)\b/gi,
      /\b(machine learning|ai|neural network|deep learning)\b/gi,
      /\b(blockchain|cryptocurrency|web3|smart contract)\b/gi
    ];

    for (const pattern of techPatterns) {
      const matches = content.match(pattern);
      if (matches) {
        entities.push(...matches.map((m: string) => m.toLowerCase()));
      }
    }

    // Programming languages and technologies
    const languages = ['python', 'javascript', 'java', 'react', 'node', 'sql', 'html', 'css'];
    for (const lang of languages) {
      if (content.includes(lang)) {
        entities.push(lang);
      }
    }

    return Array.from(new Set(entities));
  }

  /**
   * Generate suggested actions for a gap
   */
  private generateSuggestedActions(gap: any): string[] {
    const content = (gap.content || gap.description || '').toLowerCase();
    const actions: string[] = [];

    // Research actions
    if (content.includes('what is') || content.includes('define')) {
      actions.push('Research basic concepts and definitions');
    }
    if (content.includes('how to') || content.includes('how do')) {
      actions.push('Find tutorials or step-by-step guides');
    }
    if (content.includes('why') || content.includes('reason')) {
      actions.push('Investigate underlying principles and causes');
    }

    // Learning actions
    actions.push('Review relevant documentation');
    actions.push('Find practical examples');

    if (gap.gapType === 'skill') {
      actions.push('Practice with hands-on exercises');
      actions.push('Build a small project to apply the concept');
    }

    if (gap.gapType === 'concept') {
      actions.push('Break down into smaller sub-concepts');
      actions.push('Connect to existing knowledge');
    }

    return actions;
  }

  /**
   * Generate suggested resources for a gap
   */
  private generateSuggestedResources(gap: any): string[] {
    const content = (gap.content || gap.description || '').toLowerCase();
    const resources: string[] = [];

    // Resource type suggestions based on content
    if (content.includes('programming') || content.includes('code')) {
      resources.push('Official documentation');
      resources.push('GitHub repositories with examples');
      resources.push('Stack Overflow discussions');
    }

    if (content.includes('concept') || content.includes('theory')) {
      resources.push('Academic papers or articles');
      resources.push('Educational videos or courses');
      resources.push('Book chapters on the topic');
    }

    if (content.includes('tool') || content.includes('software')) {
      resources.push('User manuals and guides');
      resources.push('Community forums');
      resources.push('Video tutorials');
    }

    // General resources
    resources.push('Expert blogs and articles');
    resources.push('Community discussions');

    return Array.from(new Set(resources));
  }
}

/**
 * Productivity Patterns Repository
 * 
 * Handles database operations for productivity analytics:
 * - Time-based productivity patterns
 * - Peak hour detection
 * - Question effectiveness metrics
 * - Session length analysis
 * - Breakthrough pattern tracking
 */

import { AnalyticsRepository, TimeRange, PaginationOptions } from './AnalyticsRepository.js';
import { DatabaseManager } from '../../storage/Database.js';

export interface ProductivityPattern {
  id: string;
  windowStart: number;
  windowEnd: number;
  windowType: 'hour' | 'day' | 'week' | 'month';

  // Aggregate metrics
  totalConversations: number;
  totalMessages: number;
  totalDecisions: number;
  totalInsights: number;

  // Productivity scores
  avgProductivityScore: number;
  peakProductivityScore: number;
  minProductivityScore: number;

  // Patterns
  peakHours: number[];
  effectiveQuestionPatterns: QuestionPattern[];
  breakthroughIndicators: string[];
  optimalSessionLength: number; // minutes

  // Metadata
  sampleSize: number;
  confidenceLevel: number;

  createdAt: number;
  updatedAt: number;
}

export interface QuestionPattern {
  pattern: string;
  effectivenessScore: number;
  insightProbability: number;
  frequency: number;
}

export interface ProductivityPatternInput {
  windowStart: number;
  windowEnd: number;
  windowType: 'hour' | 'day' | 'week' | 'month';
  totalConversations: number;
  totalMessages: number;
  totalDecisions?: number;
  totalInsights?: number;
  avgProductivityScore: number;
  peakProductivityScore?: number;
  minProductivityScore?: number;
  peakHours?: number[];
  effectiveQuestionPatterns?: QuestionPattern[];
  breakthroughIndicators?: string[];
  optimalSessionLength?: number;
  sampleSize: number;
  confidenceLevel?: number;
}

export interface HourlyProductivity {
  hour: number;
  avgScore: number;
  conversationCount: number;
  messageCount: number;
  insightCount: number;
  confidenceLevel: number;
}

export interface SessionLengthAnalysis {
  optimalLength: number; // minutes
  averageLength: number;
  medianLength: number;
  efficiencyByLength: Array<{
    lengthRange: string;
    efficiency: number;
    sampleSize: number;
  }>;
}

/**
 * Repository for productivity patterns and time-based analytics
 */
export class ProductivityPatternsRepository extends AnalyticsRepository {

  constructor(databaseManager: DatabaseManager) {
    super(databaseManager);
  }

  /**
   * Save productivity pattern
   */
  async savePattern(input: ProductivityPatternInput): Promise<string> {
    const id = this.generateId();
    const now = this.getCurrentTimestamp();

    const sql = `
      INSERT INTO productivity_patterns (
        id, window_start, window_end, window_type,
        total_conversations, total_messages, total_decisions, total_insights,
        avg_productivity_score, peak_productivity_score, min_productivity_score,
        peak_hours, effective_question_patterns, breakthrough_indicators, 
        optimal_session_length, sample_size, confidence_level,
        created_at, updated_at
      ) VALUES (
        @id, @windowStart, @windowEnd, @windowType,
        @totalConversations, @totalMessages, @totalDecisions, @totalInsights,
        @avgProductivityScore, @peakProductivityScore, @minProductivityScore,
        @peakHours, @effectiveQuestionPatterns, @breakthroughIndicators,
        @optimalSessionLength, @sampleSize, @confidenceLevel,
        @createdAt, @updatedAt
      )
    `;

    const params = {
      id,
      windowStart: input.windowStart,
      windowEnd: input.windowEnd,
      windowType: input.windowType,
      totalConversations: input.totalConversations,
      totalMessages: input.totalMessages,
      totalDecisions: input.totalDecisions || 0,
      totalInsights: input.totalInsights || 0,
      avgProductivityScore: input.avgProductivityScore,
      peakProductivityScore: input.peakProductivityScore || input.avgProductivityScore,
      minProductivityScore: input.minProductivityScore || input.avgProductivityScore,
      peakHours: JSON.stringify(input.peakHours || []),
      effectiveQuestionPatterns: JSON.stringify(input.effectiveQuestionPatterns || []),
      breakthroughIndicators: JSON.stringify(input.breakthroughIndicators || []),
      optimalSessionLength: input.optimalSessionLength || 60,
      sampleSize: input.sampleSize,
      confidenceLevel: input.confidenceLevel || 0.5,
      createdAt: now,
      updatedAt: now
    };

    try {
      this.executeStatementRun('save_productivity_pattern', sql, params);
      return id;
    } catch (error) {
      this.handleConstraintError(error as Error, 'productivity pattern');
    }
  }

  /**
   * Get productivity patterns for time range and window type
   */
  async getPatterns(
    windowType: 'hour' | 'day' | 'week' | 'month',
    timeRange?: TimeRange,
    options?: PaginationOptions
  ): Promise<ProductivityPattern[]> {
    const validTimeRange = this.validateTimeRange(timeRange);
    const { limit, offset } = this.validatePagination(options?.limit, options?.offset);

    const sql = `
      SELECT 
        id, window_start, window_end, window_type,
        total_conversations, total_messages, total_decisions, total_insights,
        avg_productivity_score, peak_productivity_score, min_productivity_score,
        peak_hours, effective_question_patterns, breakthrough_indicators,
        optimal_session_length, sample_size, confidence_level,
        created_at, updated_at
      FROM productivity_patterns
      WHERE window_type = @windowType
        AND window_end >= @start 
        AND window_start <= @end
      ORDER BY window_start DESC
      LIMIT @limit OFFSET @offset
    `;

    const results = this.executeStatementAll<any>(
      'get_productivity_patterns',
      sql,
      { 
        windowType, 
        ...validTimeRange, 
        limit, 
        offset 
      }
    );

    return results.map(row => this.mapRowToPattern(row));
  }

  /**
   * Get hourly productivity analysis
   */
  async getHourlyProductivity(timeRange?: TimeRange): Promise<HourlyProductivity[]> {
    const validTimeRange = this.validateTimeRange(timeRange);

    const sql = `
      WITH hourly_data AS (
        SELECT 
          CAST(strftime('%H', datetime(c.created_at / 1000, 'unixepoch')) as INTEGER) as hour,
          COALESCE(ca.productivity_score, 50) as productivity_score,
          COUNT(DISTINCT c.id) as conversation_count,
          COUNT(m.id) as message_count,
          COALESCE(ca.insight_count, 0) as insight_count
        FROM conversations c
        LEFT JOIN conversation_analytics ca ON c.id = ca.conversation_id
        LEFT JOIN messages m ON c.id = m.conversation_id
        WHERE c.created_at BETWEEN @start AND @end
        GROUP BY 
          CAST(strftime('%H', datetime(c.created_at / 1000, 'unixepoch')) as INTEGER),
          c.id
      ),
      hourly_aggregated AS (
        SELECT 
          hour,
          AVG(productivity_score) as avg_score,
          SUM(conversation_count) as total_conversations,
          SUM(message_count) as total_messages,
          SUM(insight_count) as total_insights,
          COUNT(*) as sample_size
        FROM hourly_data
        GROUP BY hour
      )
      SELECT 
        hour,
        avg_score,
        total_conversations as conversation_count,
        total_messages as message_count,
        total_insights as insight_count,
        CASE 
          WHEN sample_size >= 10 THEN 0.9
          WHEN sample_size >= 5 THEN 0.7
          WHEN sample_size >= 3 THEN 0.5
          ELSE 0.3
        END as confidence_level
      FROM hourly_aggregated
      WHERE total_conversations > 0
      ORDER BY hour
    `;

    const results = this.executeStatementAll<any>(
      'hourly_productivity',
      sql,
      validTimeRange
    );

    // Fill missing hours with default values
    const hourlyMap = new Map<number, HourlyProductivity>();
    results.forEach(row => {
      hourlyMap.set(row.hour, {
        hour: row.hour,
        avgScore: row.avg_score || 0,
        conversationCount: row.conversation_count || 0,
        messageCount: row.message_count || 0,
        insightCount: row.insight_count || 0,
        confidenceLevel: row.confidence_level || 0
      });
    });

    // Fill in missing hours (0-23)
    const fullHourlyData: HourlyProductivity[] = [];
    for (let hour = 0; hour < 24; hour++) {
      if (hourlyMap.has(hour)) {
        fullHourlyData.push(hourlyMap.get(hour)!);
      } else {
        fullHourlyData.push({
          hour,
          avgScore: 0,
          conversationCount: 0,
          messageCount: 0,
          insightCount: 0,
          confidenceLevel: 0
        });
      }
    }

    return fullHourlyData;
  }

  /**
   * Get peak productivity hours
   */
  async getPeakHours(timeRange?: TimeRange): Promise<number[]> {
    const hourlyData = await this.getHourlyProductivity(timeRange);

    // Filter hours with sufficient confidence
    const reliableHours = hourlyData.filter(h => 
      h.confidenceLevel >= 0.5 && h.conversationCount >= 3
    );

    if (reliableHours.length === 0) {
      return [];
    }

    // Calculate mean and standard deviation
    const scores = reliableHours.map(h => h.avgScore);
    const mean = scores.reduce((sum, score) => sum + score, 0) / scores.length;
    const stdDev = this.calculateStandardDeviation(scores);

    // Peak hours are those with scores > mean + 0.5 * stdDev
    const threshold = mean + (0.5 * stdDev);

    return reliableHours
      .filter(h => h.avgScore > threshold)
      .map(h => h.hour)
      .sort();
  }

  /**
   * Analyze session lengths and find optimal duration
   */
  async getSessionLengthAnalysis(timeRange?: TimeRange): Promise<SessionLengthAnalysis> {
    const validTimeRange = this.validateTimeRange(timeRange);

    const sql = `
      WITH conversation_durations AS (
        SELECT 
          c.id,
          (MAX(m.created_at) - MIN(m.created_at)) / (1000 * 60.0) as duration_minutes,
          COALESCE(ca.productivity_score, 50) as productivity_score,
          COUNT(m.id) as message_count
        FROM conversations c
        JOIN messages m ON c.id = m.conversation_id
        LEFT JOIN conversation_analytics ca ON c.id = ca.conversation_id
        WHERE c.created_at BETWEEN @start AND @end
        GROUP BY c.id
        HAVING COUNT(m.id) >= 3 -- At least 3 messages for meaningful duration
          AND duration_minutes > 1 -- At least 1 minute
          AND duration_minutes < 480 -- Less than 8 hours (filter outliers)
      ),
      duration_buckets AS (
        SELECT 
          duration_minutes,
          productivity_score,
          CASE 
            WHEN duration_minutes <= 15 THEN '1-15 min'
            WHEN duration_minutes <= 30 THEN '16-30 min'
            WHEN duration_minutes <= 60 THEN '31-60 min'
            WHEN duration_minutes <= 120 THEN '61-120 min'
            WHEN duration_minutes <= 240 THEN '121-240 min'
            ELSE '240+ min'
          END as length_range
        FROM conversation_durations
      ),
      bucket_analysis AS (
        SELECT 
          length_range,
          AVG(productivity_score) as avg_efficiency,
          COUNT(*) as sample_size
        FROM duration_buckets
        GROUP BY length_range
        HAVING sample_size >= 3
      ),
      optimal_analysis AS (
        SELECT 
          duration_minutes,
          productivity_score,
          -- Weight efficiency by sample size proximity to optimal range
          productivity_score * (1 - ABS(duration_minutes - 45) / 200.0) as weighted_score
        FROM conversation_durations
        WHERE duration_minutes BETWEEN 10 AND 180 -- Focus on reasonable range
      )
      SELECT 
        AVG(duration_minutes) as avg_duration,
        (
          SELECT AVG(duration_minutes)
          FROM (
            SELECT duration_minutes 
            FROM conversation_durations 
            ORDER BY duration_minutes
            LIMIT 2 - (SELECT COUNT(*) FROM conversation_durations) % 2
            OFFSET (SELECT (COUNT(*) - 1) / 2 FROM conversation_durations)
          )
        ) as median_duration,
        (
          SELECT duration_minutes
          FROM optimal_analysis
          ORDER BY weighted_score DESC
          LIMIT 1
        ) as optimal_duration
      FROM conversation_durations
    `;

    const result = this.executeStatement<any>(
      'session_length_analysis',
      sql,
      validTimeRange
    );

    // Get efficiency by length ranges
    const bucketSql = `
      WITH conversation_durations AS (
        SELECT 
          c.id,
          (MAX(m.created_at) - MIN(m.created_at)) / (1000 * 60.0) as duration_minutes,
          COALESCE(ca.productivity_score, 50) as productivity_score
        FROM conversations c
        JOIN messages m ON c.id = m.conversation_id
        LEFT JOIN conversation_analytics ca ON c.id = ca.conversation_id
        WHERE c.created_at BETWEEN @start AND @end
        GROUP BY c.id
        HAVING COUNT(m.id) >= 3 AND duration_minutes > 1 AND duration_minutes < 480
      ),
      duration_buckets AS (
        SELECT 
          CASE 
            WHEN duration_minutes <= 15 THEN '1-15 min'
            WHEN duration_minutes <= 30 THEN '16-30 min'
            WHEN duration_minutes <= 60 THEN '31-60 min'
            WHEN duration_minutes <= 120 THEN '61-120 min'
            WHEN duration_minutes <= 240 THEN '121-240 min'
            ELSE '240+ min'
          END as length_range,
          productivity_score
        FROM conversation_durations
      )
      SELECT 
        length_range,
        AVG(productivity_score) as efficiency,
        COUNT(*) as sample_size
      FROM duration_buckets
      GROUP BY length_range
      ORDER BY 
        CASE length_range
          WHEN '1-15 min' THEN 1
          WHEN '16-30 min' THEN 2
          WHEN '31-60 min' THEN 3
          WHEN '61-120 min' THEN 4
          WHEN '121-240 min' THEN 5
          ELSE 6
        END
    `;

    const buckets = this.executeStatementAll<{
      length_range: string;
      efficiency: number;
      sample_size: number;
    }>('efficiency_by_length', bucketSql, validTimeRange);

    return {
      optimalLength: Math.round(result?.optimal_duration || 45),
      averageLength: Math.round(result?.avg_duration || 60),
      medianLength: Math.round(result?.median_duration || 45),
      efficiencyByLength: buckets.map(bucket => ({
        lengthRange: bucket.length_range,
        efficiency: Math.round(bucket.efficiency * 10) / 10,
        sampleSize: bucket.sample_size
      }))
    };
  }

  /**
   * Get question effectiveness patterns
   */
  async getQuestionPatterns(
    timeRange?: TimeRange,
    minFrequency: number = 3
  ): Promise<QuestionPattern[]> {
    const validTimeRange = this.validateTimeRange(timeRange);

    const sql = `
      WITH question_messages AS (
        SELECT 
          m.content,
          m.conversation_id,
          COALESCE(ca.productivity_score, 50) as productivity_score,
          COALESCE(ca.insight_count, 0) > 0 as has_insights
        FROM messages m
        JOIN conversations c ON m.conversation_id = c.id
        LEFT JOIN conversation_analytics ca ON c.id = ca.conversation_id
        WHERE m.role = 'user'
          AND (m.content LIKE '%?%' OR 
               m.content LIKE 'how %' OR 
               m.content LIKE 'what %' OR 
               m.content LIKE 'why %' OR 
               m.content LIKE 'when %' OR 
               m.content LIKE 'where %')
          AND c.created_at BETWEEN @start AND @end
      ),
      question_patterns AS (
        SELECT 
          CASE 
            WHEN LOWER(content) LIKE 'how might%' THEN 'how might'
            WHEN LOWER(content) LIKE 'how can%' OR LOWER(content) LIKE 'how do%' THEN 'how to'
            WHEN LOWER(content) LIKE 'what if%' THEN 'what if'
            WHEN LOWER(content) LIKE 'what are%' OR LOWER(content) LIKE 'what is%' THEN 'what about'
            WHEN LOWER(content) LIKE 'why %' THEN 'why'
            WHEN LOWER(content) LIKE 'can you help%' OR LOWER(content) LIKE 'help me%' THEN 'help request'
            WHEN LOWER(content) LIKE 'explain%' OR LOWER(content) LIKE 'describe%' THEN 'explanation'
            WHEN content LIKE '%compare%' OR content LIKE '%difference%' THEN 'comparison'
            WHEN content LIKE '%example%' OR content LIKE '%instance%' THEN 'example request'
            ELSE 'general question'
          END as pattern,
          productivity_score,
          CAST(has_insights as INTEGER) as insight_flag
        FROM question_messages
      ),
      pattern_stats AS (
        SELECT 
          pattern,
          COUNT(*) as frequency,
          AVG(productivity_score) as avg_effectiveness,
          AVG(CAST(insight_flag as REAL)) as insight_probability
        FROM question_patterns
        GROUP BY pattern
        HAVING frequency >= @minFrequency
      )
      SELECT 
        pattern,
        avg_effectiveness as effectiveness_score,
        insight_probability,
        frequency
      FROM pattern_stats
      ORDER BY avg_effectiveness DESC, insight_probability DESC
    `;

    const results = this.executeStatementAll<{
      pattern: string;
      effectiveness_score: number;
      insight_probability: number;
      frequency: number;
    }>('question_patterns', sql, { ...validTimeRange, minFrequency });

    return results.map(row => ({
      pattern: row.pattern,
      effectivenessScore: Math.round(row.effectiveness_score * 10) / 10,
      insightProbability: Math.round(row.insight_probability * 100) / 100,
      frequency: row.frequency
    }));
  }

