/**
 * Configuration selector based on environment variables or deployment size
 */
export function getProductionConfig(): ProductionConfig {
  const env = process.env.NODE_ENV || 'development';
  const deploymentSize = process.env.DEPLOYMENT_SIZE || 'small';

  if (env === 'development') {
    return DEVELOPMENT_CONFIG;
  }

  switch (deploymentSize.toLowerCase()) {
    case 'small':
      return SMALL_DEPLOYMENT_CONFIG;
    case 'medium':
      return MEDIUM_DEPLOYMENT_CONFIG;
    case 'large':
      return LARGE_DEPLOYMENT_CONFIG;
    default:
      console.warn(`Unknown deployment size: ${deploymentSize}, using small config`);
      return SMALL_DEPLOYMENT_CONFIG;
  }
}

/**
 * Validate configuration against system resources
 */
export function validateConfiguration(config: ProductionConfig): {
  isValid: boolean;
  warnings: string[];
  recommendations: string[];
} {
  const warnings: string[] = [];
  const recommendations: string[] = [];
  const isValid = true;

  // Check available memory
  const totalMemory = require('os').totalmem();
  const configuredMemory = config.memory.maxRssBytes;

  if (configuredMemory > totalMemory * 0.8) {
    warnings.push(`Configured memory limit (${Math.round(configuredMemory / 1024 / 1024)}MB) exceeds 80% of system memory`);
    recommendations.push('Reduce maxRssBytes or increase system memory');
  }

  // Check database cache size vs available memory
  const dbCacheBytes = (config.database.cacheSize || 0) * 1024; // cacheSize is in KB
  if (dbCacheBytes > configuredMemory * 0.3) {
    warnings.push('Database cache size is very large relative to memory limit');
    recommendations.push('Consider reducing database cache size');
  }

  // Check embedding cache vs memory
  const embeddingCacheBytes = config.search.embedding.maxCacheSize * 1024 * 1024;
  if (embeddingCacheBytes > configuredMemory * 0.4) {
    warnings.push('Embedding cache size is large relative to memory limit');
    recommendations.push('Consider reducing embedding cache size');
  }

  // Check performance targets vs deployment size
  if (config.search.embedding.performanceTarget < 50 && config.database.maxConnections > 10) {
    warnings.push('Aggressive performance targets with high concurrency may cause resource contention');
    recommendations.push('Consider relaxing performance targets or reducing connection count');
  }

  // Check monitoring overhead
  if (config.monitoring.enabled && config.monitoring.metricsInterval < 15) {
    warnings.push('Very frequent metrics collection may impact performance');
    recommendations.push('Consider increasing metrics collection interval');
  }

  return {
    isValid,
    warnings,
    recommendations
  };
}

/**
 * Apply runtime optimizations based on current system state
 */
export function applyRuntimeOptimizations(config: ProductionConfig): ProductionConfig {
  const optimizedConfig = { ...config };

  // Adjust memory settings based on available memory
  const totalMemory = require('os').totalmem();
  const availableMemory = totalMemory - process.memoryUsage().rss;

  // If available memory is low, reduce cache sizes
  if (availableMemory < optimizedConfig.memory.maxRssBytes * 0.5) {
    optimizedConfig.search.embedding.maxCacheSize = Math.floor(optimizedConfig.search.embedding.maxCacheSize * 0.7);
    optimizedConfig.database.cacheSize = Math.floor((optimizedConfig.database.cacheSize || 0) * 0.8);
    console.log('Applied memory-constrained optimizations');
  }

  // Adjust performance targets based on CPU count
  const cpuCount = require('os').cpus().length;
  if (cpuCount <= 2) {
    // Relax performance targets on low-CPU systems
    optimizedConfig.search.embedding.performanceTarget = Math.max(
      optimizedConfig.search.embedding.performanceTarget * 1.5,
      200
    );
  }

  return optimizedConfig;
}

/**
 * Generate startup report with configuration summary
 */
export function generateStartupReport(config: ProductionConfig): string {
  const validation = validateConfiguration(config);

  let report = '=== MCP Persistence Server Configuration ===\n\n';
  report += `Environment: ${config.environment}\n`;
  report += `Database: ${config.database.databasePath}\n`;
  report += `Max Connections: ${config.database.maxConnections}\n`;
  report += `Database Cache: ${Math.round((config.database.cacheSize || 0) / 1024)}MB\n`;
  report += `Embedding Model: ${config.search.embedding.modelName}\n`;
  report += `Embedding Cache: ${config.search.embedding.maxCacheSize}MB\n`;
  report += `Memory Limit: ${Math.round(config.memory.maxRssBytes / 1024 / 1024)}MB\n`;
  report += `Monitoring: ${config.monitoring.enabled ? 'Enabled' : 'Disabled'}\n\n`;

  if (validation.warnings.length > 0) {
    report += 'Warnings:\n';
    validation.warnings.forEach(warning => report += `  - ${warning}\n`);
    report += '\n';
  }

  if (validation.recommendations.length > 0) {
    report += 'Recommendations:\n';
    validation.recommendations.forEach(rec => report += `  - ${rec}\n`);
    report += '\n';
  }

  report += 'Configuration validated successfully.\n';

  return report;
}

/**
 * Context Assembler - Intelligent context assembly for queries
 * 
 * This module provides:
 * - Multi-factor relevance scoring
 * - Token budget management
 * - Progressive context building
 * - Different assembly strategies
 * - Optimal context selection
 */

import { Message, ConversationSummary } from '../types/interfaces.js';
import { EmbeddingManager } from '../search/EmbeddingManager.js';
import { MessageRepository } from '../storage/repositories/MessageRepository.js';
import { SummaryRepository } from '../storage/repositories/SummaryRepository.js';
import { TokenCounter, createTokenCounter } from './TokenCounter.js';
import { RelevanceScorer, ScoredItem } from './scoring/RelevanceScorer.js';
import { TokenOptimizer, TokenBudget } from './optimization/TokenOptimizer.js';
import { AssemblyStrategy, StrategyType } from './strategies/AssemblyStrategy.js';
import { TemporalStrategy } from './strategies/TemporalStrategy.js';
import { TopicalStrategy } from './strategies/TopicalStrategy.js';
import { EntityCentricStrategy } from './strategies/EntityCentricStrategy.js';
import { HybridStrategy } from './strategies/HybridStrategy.js';

/**
 * Context assembly request parameters
 */
export interface ContextAssemblyRequest {
  /** The user's query */
  query: string;
  /** Optional conversation ID to limit scope */
  conversationId?: string;
  /** Maximum token budget for assembled context */
  maxTokens?: number;
  /** Assembly strategy to use */
  strategy?: StrategyType;
  /** Minimum relevance threshold */
  minRelevance?: number;
  /** Include recent messages regardless of relevance */
  includeRecent?: boolean;
  /** Entity names to focus on */
  focusEntities?: string[];
  /** Time window for context (in milliseconds) */
  timeWindow?: number;
  /** Model name for token counting */
  model?: string;
}

/**
 * Normalized request with all required fields filled
 */
type NormalizedRequest = ContextAssemblyRequest & {
  query: string;
  maxTokens: number;
  strategy: StrategyType;
  minRelevance: number;
  includeRecent: boolean;
  focusEntities: string[];
  timeWindow: number;
  model: string;
};

/**
 * Assembled context result
 */
export interface AssembledContext {
  /** The assembled context text */
  text: string;
  /** Token count of the assembled context */
  tokenCount: number;
  /** Breakdown of token usage */
  tokenBreakdown: {
    query: number;
    summaries: number;
    messages: number;
    metadata: number;
    buffer: number;
  };
  /** Items included in the context */
  includedItems: Array<{
    type: 'summary' | 'message';
    id: string;
    relevanceScore: number;
    tokenCount: number;
    position: number;
  }>;
  /** Assembly strategy used */
  strategy: StrategyType;
  /** Performance metrics */
  metrics: {
    processingTimeMs: number;
    itemsEvaluated: number;
    itemsIncluded: number;
    averageRelevance: number;
    tokenEfficiency: number; // tokens used / max tokens
  };
}

/**
 * Context assembly configuration
 */
export interface ContextAssemblerConfig {
  /** Default maximum token budget */
  defaultMaxTokens: number;
  /** Default assembly strategy */
  defaultStrategy: StrategyType;
  /** Default minimum relevance threshold */
  defaultMinRelevance: number;
  /** Token allocation ratios */
  tokenAllocation: {
    query: number;
    summaries: number;
    messages: number;
    metadata: number;
    buffer: number;
  };
  /** Performance thresholds */
  performance: {
    maxProcessingTimeMs: number;
    maxItemsToEvaluate: number;
  };
}

/**
 * Default configuration
 */
const DEFAULT_CONFIG: ContextAssemblerConfig = {
  defaultMaxTokens: 4000,
  defaultStrategy: 'hybrid',
  defaultMinRelevance: 0.3,
  tokenAllocation: {
    query: 0.05,      // 5% for query context
    summaries: 0.30,  // 30% for summaries
    messages: 0.50,   // 50% for message details
    metadata: 0.10,   // 10% for metadata
    buffer: 0.05      // 5% buffer
  },
  performance: {
    maxProcessingTimeMs: 5000, // 5 second timeout
    maxItemsToEvaluate: 1000   // Limit evaluation for performance
  }
};

/**
 * Context Assembler - Main service for intelligent context assembly
 */
export class ContextAssembler {
  private config: ContextAssemblerConfig;
  private embeddingManager: EmbeddingManager;
  private messageRepository: MessageRepository;
  private summaryRepository: SummaryRepository;
  private relevanceScorer: RelevanceScorer;
  private tokenOptimizer: TokenOptimizer;
  private strategies: Map<StrategyType, AssemblyStrategy>;

  constructor(
    embeddingManager: EmbeddingManager,
    messageRepository: MessageRepository,
    summaryRepository: SummaryRepository,
    config?: Partial<ContextAssemblerConfig>
  ) {
    this.config = { ...DEFAULT_CONFIG, ...config };
    this.embeddingManager = embeddingManager;
    this.messageRepository = messageRepository;
    this.summaryRepository = summaryRepository;

    // Initialize components
    this.relevanceScorer = new RelevanceScorer(embeddingManager);
    this.tokenOptimizer = new TokenOptimizer();

    // Initialize strategies
    this.strategies = new Map();
    this.strategies.set('temporal', new TemporalStrategy());
    this.strategies.set('topical', new TopicalStrategy());
    this.strategies.set('entity-centric', new EntityCentricStrategy());
    this.strategies.set('hybrid', new HybridStrategy());
  }

  /**
   * Assemble optimal context for a query
   */
  async assembleContext(request: ContextAssemblyRequest): Promise<AssembledContext> {
    const startTime = Date.now();

    try {
      // Validate and normalize parameters
      const normalizedRequest = this.normalizeRequest(request);

      // Create token counter for the specified model
      const tokenCounter = createTokenCounter(normalizedRequest.model || 'default');

      // Calculate token budget
      const tokenBudget = this.calculateTokenBudget(normalizedRequest, tokenCounter);

      // Get assembly strategy
      const strategy = this.strategies.get(normalizedRequest.strategy!)!;

      // Collect and score candidate items
      const candidates = await this.collectCandidates(normalizedRequest);
      const scoredItems = await this.scoreItems(candidates, normalizedRequest);

      // Apply strategy-specific selection and ordering
      const selectedItems = await strategy.selectItems(
        scoredItems,
        normalizedRequest,
        tokenBudget
      );

      // Optimize token usage and assemble final context
      const optimizedItems = this.tokenOptimizer.optimizeSelection(
        selectedItems,
        tokenBudget,
        tokenCounter
      );

      // Build the final context text
      const contextText = await this.buildContextText(
        optimizedItems,
        normalizedRequest,
        tokenCounter
      );

      // Calculate metrics
      const processingTime = Date.now() - startTime;
      const metrics = this.calculateMetrics(
        candidates,
        optimizedItems,
        processingTime,
        tokenBudget.total
      );

      return {
        text: contextText,
        tokenCount: tokenCounter.countText(contextText).count,
        tokenBreakdown: {
          query: tokenBudget.query,
          summaries: tokenBudget.summaries,
          messages: tokenBudget.messages,
          metadata: tokenBudget.metadata,
          buffer: tokenBudget.buffer
        },
        includedItems: optimizedItems.map((item, index) => ({
          type: item.type,
          id: item.id,
          relevanceScore: item.relevanceScore,
          tokenCount: item.tokenCount,
          position: index
        })),
        strategy: normalizedRequest.strategy!,
        metrics
      };

    } catch (error) {
      const processingTime = Date.now() - startTime;
      console.error('Context assembly failed:', error);

      // Return minimal fallback context
      return this.createFallbackContext(request, processingTime);
    }
  }

  /**
   * Normalize and validate request parameters
   */
  private normalizeRequest(request: ContextAssemblyRequest): NormalizedRequest {
    return {
      query: request.query.trim(),
      conversationId: request.conversationId,
      maxTokens: request.maxTokens || this.config.defaultMaxTokens,
      strategy: request.strategy || this.config.defaultStrategy,
      minRelevance: request.minRelevance || this.config.defaultMinRelevance,
      includeRecent: request.includeRecent ?? true,
      focusEntities: request.focusEntities || [],
      timeWindow: request.timeWindow || (7 * 24 * 60 * 60 * 1000), // 7 days default
      model: request.model || 'default'
    };
  }

  /**
   * Calculate token budget allocation
   */
  private calculateTokenBudget(
    request: NormalizedRequest,
    tokenCounter: TokenCounter
  ): TokenBudget {
    const maxTokens = request.maxTokens;
    const allocation = this.config.tokenAllocation;

    // Calculate query tokens
    const queryTokens = Math.min(
      Math.ceil(maxTokens * allocation.query),
      tokenCounter.countText(request.query).count + 50 // Query + some context
    );

    // Allocate remaining tokens
    const remainingTokens = maxTokens - queryTokens;

    return {
      total: maxTokens,
      query: queryTokens,
      summaries: Math.ceil(remainingTokens * allocation.summaries),
      messages: Math.ceil(remainingTokens * allocation.messages),
      metadata: Math.ceil(remainingTokens * allocation.metadata),
      buffer: Math.ceil(remainingTokens * allocation.buffer)
    };
  }

  /**
   * Collect candidate summaries and messages
   */
  private async collectCandidates(
    request: NormalizedRequest
  ): Promise<Array<{ type: 'summary' | 'message'; item: ConversationSummary | Message }>> {
    const candidates: Array<{ type: 'summary' | 'message'; item: ConversationSummary | Message }> = [];

    // Collect summaries
    const summaryResult = await this.summaryRepository.findRecent(
      Math.min(100, this.config.performance.maxItemsToEvaluate / 2),
      0.5 // Minimum quality score
    );

    for (const summary of summaryResult) {
      if (!request.conversationId || summary.conversationId === request.conversationId) {
        candidates.push({ type: 'summary', item: summary });
      }
    }

    // Collect messages
    const messageLimit = Math.min(
      200,
      this.config.performance.maxItemsToEvaluate - candidates.length
    );

    let messageResult;
    if (request.conversationId) {
      messageResult = await this.messageRepository.findByConversation(
        request.conversationId,
        messageLimit,
        0,
        'created_at',
        'DESC'
      );
    } else {
      // Find recent messages across all conversations
      messageResult = await this.messageRepository.findWithEmbeddings(
        undefined,
        messageLimit,
        0
      );
    }

    for (const message of messageResult.data) {
      // Apply time window filter
      if (request.timeWindow && 
          Date.now() - message.createdAt > request.timeWindow) {
        continue;
      }

      candidates.push({ type: 'message', item: message });
    }

    return candidates;
  }

  /**
   * Score items for relevance using multi-factor scoring
   */
  private async scoreItems(
    candidates: Array<{ type: 'summary' | 'message'; item: ConversationSummary | Message }>,
    request: NormalizedRequest
  ): Promise<ScoredItem[]> {
    const scoredItems: ScoredItem[] = [];

    for (const candidate of candidates) {
      const item = candidate.item;
      const content = candidate.type === 'summary' 
        ? (item as ConversationSummary).summaryText
        : (item as Message).content;

      // Score the item
      const relevanceScore = await this.relevanceScorer.scoreItem({
        id: item.id,
        type: candidate.type,
        content,
        createdAt: candidate.type === 'summary' 
          ? (item as ConversationSummary).generatedAt
          : (item as Message).createdAt,
        conversationId: candidate.type === 'summary'
          ? (item as ConversationSummary).conversationId
          : (item as Message).conversationId,
        metadata: candidate.type === 'summary'
          ? (item as ConversationSummary).metadata
          : (item as Message).metadata
      }, request);

      // Skip items below relevance threshold
      if (relevanceScore < request.minRelevance) {
        continue;
      }

      scoredItems.push({
        id: item.id,
        type: candidate.type,
        content,
        relevanceScore,
        tokenCount: 0, // Will be calculated by token optimizer
        createdAt: candidate.type === 'summary' 
          ? (item as ConversationSummary).generatedAt
          : (item as Message).createdAt,
        conversationId: candidate.type === 'summary'
          ? (item as ConversationSummary).conversationId
          : (item as Message).conversationId,
        metadata: candidate.type === 'summary'
          ? (item as ConversationSummary).metadata
          : (item as Message).metadata,
        originalItem: item
      });
    }

    return scoredItems;
  }

  /**
   * Build the final context text from selected items
   */
  private async buildContextText(
    items: ScoredItem[],
    request: NormalizedRequest,
    _tokenCounter: TokenCounter
  ): Promise<string> {
    const sections: string[] = [];

    // Add query context
    sections.push(`Query: ${request.query}\n`);

    // Group items by type
    const summaries = items.filter(item => item.type === 'summary');
    const messages = items.filter(item => item.type === 'message');

    // Add conversation summaries
    if (summaries.length > 0) {
      sections.push('## Conversation Summaries\n');

      for (const summary of summaries) {
        const summaryObj = summary.originalItem as ConversationSummary;
        sections.push(
          `### ${summaryObj.level} Summary (${summaryObj.messageCount} messages)\n` +
          `${summary.content}\n`
        );
      }
    }

    // Add detailed messages
    if (messages.length > 0) {
      sections.push('## Detailed Messages\n');

      // Group messages by conversation
      const messagesByConversation = new Map<string, ScoredItem[]>();
      for (const message of messages) {
        const convId = message.conversationId;
        if (!messagesByConversation.has(convId)) {
          messagesByConversation.set(convId, []);
        }
        messagesByConversation.get(convId)!.push(message);
      }

      for (const [conversationId, convMessages] of messagesByConversation) {
        if (messagesByConversation.size > 1) {
          sections.push(`### Conversation ${conversationId.slice(0, 8)}\n`);
        }

        // Sort messages by timestamp
        convMessages.sort((a, b) => a.createdAt - b.createdAt);

        for (const message of convMessages) {
          const messageObj = message.originalItem as Message;
          const timestamp = new Date(messageObj.createdAt).toISOString();
          sections.push(
            `**${messageObj.role}** (${timestamp}):\n${message.content}\n`
          );
        }
      }
    }

    return sections.join('\n');
  }

  /**
   * Calculate performance metrics
   */
  private calculateMetrics(
    totalCandidates: any[],
    includedItems: ScoredItem[],
    processingTime: number,
    maxTokens: number
  ): AssembledContext['metrics'] {
    const averageRelevance = includedItems.length > 0
      ? includedItems.reduce((sum, item) => sum + item.relevanceScore, 0) / includedItems.length
      : 0;

    const totalTokensUsed = includedItems.reduce((sum, item) => sum + item.tokenCount, 0);
    const tokenEfficiency = totalTokensUsed / maxTokens;

    return {
      processingTimeMs: processingTime,
      itemsEvaluated: totalCandidates.length,
      itemsIncluded: includedItems.length,
      averageRelevance,
      tokenEfficiency
    };
  }

  /**
   * Create fallback context when assembly fails
   */
  private createFallbackContext(
    request: ContextAssemblyRequest,
    processingTime: number
  ): AssembledContext {
    const fallbackText = `Query: ${request.query}\n\n[Context assembly failed - using minimal context]`;
    const tokenCounter = createTokenCounter(request.model || 'default');
    const tokenCount = tokenCounter.countText(fallbackText).count;

    return {
      text: fallbackText,
      tokenCount,
      tokenBreakdown: {
        query: tokenCount,
        summaries: 0,
        messages: 0,
        metadata: 0,
        buffer: 0
      },
      includedItems: [],
      strategy: request.strategy || 'hybrid',
      metrics: {
        processingTimeMs: processingTime,
        itemsEvaluated: 0,
        itemsIncluded: 0,
        averageRelevance: 0,
        tokenEfficiency: 0
      }
    };
  }

  /**
   * Update configuration
   */
  updateConfig(updates: Partial<ContextAssemblerConfig>): void {
    this.config = { ...this.config, ...updates };
  }

  /**
   * Get current configuration
   */
  getConfig(): ContextAssemblerConfig {
    return { ...this.config };
  }

  /**
   * Get available strategies
   */
  getAvailableStrategies(): StrategyType[] {
    return Array.from(this.strategies.keys());
  }

  /**
   * Get embedding manager instance
   */
  getEmbeddingManager(): EmbeddingManager {
    return this.embeddingManager;
  }
}

/**
 * Provider Manager
 * 
 * Manages multiple LLM providers with fallback chains, health monitoring,
 * and intelligent provider selection for conversation summarization.
 */

import { 
  LLMProvider, 
  ProviderConfig, 
  SummaryRequest, 
  SummaryResponse,
  ProviderError,
  ProviderUnavailableError,
  ProviderQuotaError
} from './providers/LLMProvider.js';
import { OllamaProvider } from './providers/OllamaProvider.js';
import { OpenAIProvider } from './providers/OpenAIProvider.js';

/**
 * Provider selection strategy
 */
export type ProviderStrategy = 
  | 'priority'      // Use highest priority available provider
  | 'cost-optimal'  // Use cheapest available provider
  | 'performance'   // Use fastest available provider
  | 'quality'       // Use highest quality provider
  | 'fallback';     // Use fallback chain

/**
 * Provider manager configuration
 */
export interface ProviderManagerConfig {
  defaultStrategy: ProviderStrategy;
  maxRetries: number;
  retryDelay: number;
  healthCheckInterval: number;
  costLimit?: number; // Per-day cost limit in USD
}

/**
 * Provider selection result
 */
interface ProviderSelection {
  provider: LLMProvider;
  reason: string;
  fallbackChain: string[];
}

/**
 * Provider health status
 */
interface ProviderStatus {
  provider: LLMProvider;
  isHealthy: boolean;
  lastChecked: Date;
  consecutiveFailures: number;
  avgLatency: number;
  totalCost: number;
}

/**
 * Daily usage tracking
 */
interface DailyUsage {
  date: string;
  totalCost: number;
  requestCount: number;
  providerUsage: Record<string, { cost: number; requests: number; }>;
}

/**
 * Provider Manager class
 */
export class ProviderManager {
  private providers: Map<string, LLMProvider> = new Map();
  private providerStatus: Map<string, ProviderStatus> = new Map();
  private config: ProviderManagerConfig;
  private healthCheckTimer?: NodeJS.Timeout;
  private dailyUsage: DailyUsage;

  constructor(config: ProviderManagerConfig) {
    this.config = config;
    this.dailyUsage = this.initializeDailyUsage();
    this.startHealthChecking();
  }

  /**
   * Register a provider
   */
  async registerProvider(config: ProviderConfig): Promise<void> {
    try {
      const provider = await this.createProvider(config);
      await provider.initialize();

      this.providers.set(config.id, provider);
      this.providerStatus.set(config.id, {
        provider,
        isHealthy: true,
        lastChecked: new Date(),
        consecutiveFailures: 0,
        avgLatency: 0,
        totalCost: 0
      });

      console.log(`Provider registered: ${config.name} (${config.id})`);
    } catch (error) {
      console.error(`Failed to register provider ${config.name}:`, error);
      throw error;
    }
  }

  /**
   * Unregister a provider
   */
  async unregisterProvider(providerId: string): Promise<void> {
    const provider = this.providers.get(providerId);
    if (provider) {
      await provider.cleanup();
      this.providers.delete(providerId);
      this.providerStatus.delete(providerId);
      console.log(`Provider unregistered: ${providerId}`);
    }
  }

  /**
   * Generate summary using best available provider
   */
  async generateSummary(
    summaryRequest: SummaryRequest, 
    strategy: ProviderStrategy = this.config.defaultStrategy
  ): Promise<SummaryResponse> {
    const selection = await this.selectProvider(strategy, summaryRequest);
    let lastError: Error | undefined;

    // Try primary provider and fallback chain
    for (const providerId of [selection.provider.getId(), ...selection.fallbackChain]) {
      const provider = this.providers.get(providerId);
      if (!provider) continue;

      const status = this.providerStatus.get(providerId);
      if (!status?.isHealthy) continue;

      // Check cost limits
      if (await this.wouldExceedCostLimit(provider, summaryRequest)) {
        console.log(`Skipping provider ${providerId} due to cost limit`);
        continue;
      }

      try {
        const startTime = Date.now();
        const response = await this.executeWithRetry(provider, summaryRequest);
        const latency = Date.now() - startTime;

        // Update status
        this.updateProviderStatus(providerId, true, latency, response.cost || 0);
        this.updateDailyUsage(providerId, response.cost || 0);

        console.log(`Summary generated by ${provider.getName()} in ${latency}ms`);
        return response;

      } catch (error) {
        lastError = error instanceof Error ? error : new Error('Unknown error');
        console.warn(`Provider ${providerId} failed:`, lastError.message);

        this.updateProviderStatus(providerId, false, 0, 0);

        // Skip to next provider unless it's a quota error (permanent)
        if (error instanceof ProviderQuotaError) {
          this.markProviderUnhealthy(providerId, 'Quota exceeded');
        }
      }
    }

    // All providers failed
    throw new ProviderError(
      `All providers failed. Last error: ${lastError?.message || 'Unknown error'}`,
      'ALL_PROVIDERS_FAILED',
      'ProviderManager',
      lastError
    );
  }

  /**
   * Get provider statistics
   */
  getProviderStats(): Array<{
    id: string;
    name: string;
    type: 'local' | 'external';
    isHealthy: boolean;
    consecutiveFailures: number;
    avgLatency: number;
    totalCost: number;
    dailyCost: number;
    dailyRequests: number;
  }> {
    return Array.from(this.providers.values()).map(provider => {
      const status = this.providerStatus.get(provider.getId());
      const dailyUsage = this.dailyUsage.providerUsage[provider.getId()];
      const stats = provider.getStats();

      return {
        id: provider.getId(),
        name: provider.getName(),
        type: provider.getType(),
        isHealthy: status?.isHealthy || false,
        consecutiveFailures: status?.consecutiveFailures || 0,
        avgLatency: status?.avgLatency || 0,
        totalCost: stats.totalCost,
        dailyCost: dailyUsage?.cost || 0,
        dailyRequests: dailyUsage?.requests || 0
      };
    });
  }

  /**
   * Get daily usage summary
   */
  getDailyUsage(): DailyUsage {
    return { ...this.dailyUsage };
  }

  /**
   * Get healthy providers
   */
  getHealthyProviders(): LLMProvider[] {
    return Array.from(this.providers.values()).filter(provider => {
      const status = this.providerStatus.get(provider.getId());
      return status?.isHealthy;
    });
  }

  /**
   * Manual health check for all providers
   */
  async checkAllProviders(): Promise<void> {
    console.log('Performing health check on all providers...');

    const promises = Array.from(this.providers.entries()).map(async ([id, provider]) => {
      try {
        const health = await provider.getHealth();
        const status = this.providerStatus.get(id);

        if (status) {
          status.isHealthy = health.isAvailable;
          status.lastChecked = new Date();
          status.avgLatency = health.latency || status.avgLatency;

          if (health.isAvailable) {
            status.consecutiveFailures = 0;
          }
        }

        console.log(`Provider ${provider.getName()}: ${health.isAvailable ? 'healthy' : 'unhealthy'}`);
      } catch (error) {
        console.error(`Health check failed for ${provider.getName()}:`, error);
        this.markProviderUnhealthy(id, error instanceof Error ? error.message : 'Health check failed');
      }
    });

    await Promise.all(promises);
  }

  /**
   * Cleanup resources
   */
  async cleanup(): Promise<void> {
    if (this.healthCheckTimer) {
      clearInterval(this.healthCheckTimer);
    }

    const cleanupPromises = Array.from(this.providers.values()).map(provider => 
      provider.cleanup().catch(error => 
        console.error(`Cleanup failed for ${provider.getName()}:`, error)
      )
    );

    await Promise.all(cleanupPromises);
    this.providers.clear();
    this.providerStatus.clear();
  }

  /**
   * Create provider instance based on configuration
   */
  private async createProvider(config: ProviderConfig): Promise<LLMProvider> {
    switch (config.type) {
      case 'local':
        if (config.endpoint?.includes('ollama') || config.name.toLowerCase().includes('ollama')) {
          return new OllamaProvider(config);
        }
        throw new Error(`Unsupported local provider: ${config.name}`);

      case 'external':
        if (config.endpoint?.includes('openai') || config.name.toLowerCase().includes('openai')) {
          return new OpenAIProvider(config);
        }
        throw new Error(`Unsupported external provider: ${config.name}`);

      default:
        throw new Error(`Unknown provider type: ${config.type}`);
    }
  }

  /**
   * Select best provider based on strategy
   */
  private async selectProvider(
    strategy: ProviderStrategy, 
    _request: SummaryRequest
  ): Promise<ProviderSelection> {
    const healthyProviders = this.getHealthyProviders();

    if (healthyProviders.length === 0) {
      throw new ProviderUnavailableError('ProviderManager');
    }

    let selectedProvider: LLMProvider;
    let reason: string;

    switch (strategy) {
      case 'priority':
        selectedProvider = healthyProviders.sort((a, b) => b.getPriority() - a.getPriority())[0];
        reason = 'Highest priority';
        break;

      case 'cost-optimal':
        selectedProvider = healthyProviders.sort((a, b) => {
          const costA = a.estimateCost(1000); // Compare cost per 1k tokens
          const costB = b.estimateCost(1000);
          return costA - costB;
        })[0];
        reason = 'Lowest cost';
        break;

      case 'performance':
        selectedProvider = healthyProviders.sort((a, b) => {
          const statusA = this.providerStatus.get(a.getId());
          const statusB = this.providerStatus.get(b.getId());
          return (statusA?.avgLatency || Infinity) - (statusB?.avgLatency || Infinity);
        })[0];
        reason = 'Fastest response';
        break;

      case 'quality':
        // Prefer external providers for quality, local for privacy
        const externalProviders = healthyProviders.filter(p => p.getType() === 'external');
        selectedProvider = externalProviders.length > 0 ? externalProviders[0] : healthyProviders[0];
        reason = externalProviders.length > 0 ? 'Highest quality (external)' : 'Best available';
        break;

      case 'fallback':
      default:
        selectedProvider = healthyProviders.sort((a, b) => b.getPriority() - a.getPriority())[0];
        reason = 'Fallback strategy';
        break;
    }

    // Create fallback chain (excluding selected provider)
    const fallbackChain = healthyProviders
      .filter(p => p.getId() !== selectedProvider.getId())
      .sort((a, b) => b.getPriority() - a.getPriority())
      .map(p => p.getId());

    return {
      provider: selectedProvider,
      reason,
      fallbackChain
    };
  }

  /**
   * Execute request with retry logic
   */
  private async executeWithRetry(
    provider: LLMProvider, 
    request: SummaryRequest
  ): Promise<SummaryResponse> {
    let lastError: Error | undefined;

    for (let attempt = 1; attempt <= this.config.maxRetries; attempt++) {
      try {
        return await provider.generateSummary(request);
      } catch (error) {
        lastError = error instanceof Error ? error : new Error('Unknown error');

        // Don't retry on certain errors
        if (error instanceof ProviderQuotaError || 
            error instanceof ProviderUnavailableError) {
          throw error;
        }

        // Wait before retry (exponential backoff)
        if (attempt < this.config.maxRetries) {
          const delay = this.config.retryDelay * Math.pow(2, attempt - 1);
          console.log(`Retry ${attempt} for ${provider.getName()} in ${delay}ms`);
          await this.sleep(delay);
        }
      }
    }

    throw lastError || new Error('Max retries exceeded');
  }

  /**
   * Check if request would exceed cost limit
   */
  private async wouldExceedCostLimit(
    provider: LLMProvider, 
    request: SummaryRequest
  ): Promise<boolean> {
    if (!this.config.costLimit || provider.getType() === 'local') {
      return false; // No limit or local provider (free)
    }

    // Estimate cost for this request
    const inputTokens = await provider.countTokens(
      request.messages.map(m => m.content).join(' ')
    );
    const estimatedCost = provider.estimateCost(inputTokens.count + 200); // +200 for output

    return (this.dailyUsage.totalCost + estimatedCost) > this.config.costLimit;
  }

  /**
   * Update provider status
   */
  private updateProviderStatus(
    providerId: string, 
    success: boolean, 
    latency: number, 
    cost: number
  ): void {
    const status = this.providerStatus.get(providerId);
    if (!status) return;

    if (success) {
      status.isHealthy = true;
      status.consecutiveFailures = 0;
      status.avgLatency = (status.avgLatency + latency) / 2; // Simple average
      status.totalCost += cost;
    } else {
      status.consecutiveFailures++;
      if (status.consecutiveFailures >= 3) {
        status.isHealthy = false;
      }
    }

    status.lastChecked = new Date();
  }

  /**
   * Mark provider as unhealthy
   */
  private markProviderUnhealthy(providerId: string, reason: string): void {
    const status = this.providerStatus.get(providerId);
    if (status) {
      status.isHealthy = false;
      status.consecutiveFailures = 99; // High number to indicate permanent failure
      console.warn(`Provider ${providerId} marked unhealthy: ${reason}`);
    }
  }

  /**
   * Update daily usage tracking
   */
  private updateDailyUsage(providerId: string, cost: number): void {
    const today = new Date().toISOString().split('T')[0];

    if (this.dailyUsage.date !== today) {
      this.dailyUsage = this.initializeDailyUsage();
    }

    this.dailyUsage.totalCost += cost;
    this.dailyUsage.requestCount++;

    if (!this.dailyUsage.providerUsage[providerId]) {
      this.dailyUsage.providerUsage[providerId] = { cost: 0, requests: 0 };
    }

    this.dailyUsage.providerUsage[providerId].cost += cost;
    this.dailyUsage.providerUsage[providerId].requests++;
  }

  /**
   * Initialize daily usage tracking
   */
  private initializeDailyUsage(): DailyUsage {
    return {
      date: new Date().toISOString().split('T')[0],
      totalCost: 0,
      requestCount: 0,
      providerUsage: {}
    };
  }

  /**
   * Start health checking timer
   */
  private startHealthChecking(): void {
    this.healthCheckTimer = setInterval(
      () => this.checkAllProviders().catch(console.error),
      this.config.healthCheckInterval
    );
  }

  /**
   * Sleep utility
   */
  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }
}

/**
 * LLM Provider Interface
 * 
 * Abstract interface for Large Language Model providers that can generate
 * conversation summaries. Supports both local and external providers.
 */

import { Message } from '../../types/interfaces.js';

/**
 * Configuration for an LLM provider
 */
export interface ProviderConfig {
  id: string;
  name: string;
  type: 'local' | 'external';
  endpoint?: string;
  apiKeyEnv?: string;
  modelName: string;
  maxTokens: number;
  temperature?: number;
  isActive?: boolean;
  priority?: number;
  costPer1kTokens?: number;
  metadata?: Record<string, any>;
}

/**
 * Request for summary generation
 */
export interface SummaryRequest {
  messages: Message[];
  level: 'brief' | 'standard' | 'detailed';
  maxTokens: number;
  focusTopics?: string[];
  previousSummary?: string;
  context?: {
    conversationId: string;
    timeRange?: { start: Date; end: Date };
    userPreferences?: Record<string, any>;
  };
}

/**
 * Response from summary generation
 */
export interface SummaryResponse {
  summary: string;
  tokenCount: number;
  inputTokens: number;
  outputTokens: number;
  cost?: number;
  qualityScore?: number;
  metadata?: Record<string, any>;
  processingTime?: number;
}

/**
 * Provider health status
 */
export interface ProviderHealth {
  isAvailable: boolean;
  latency?: number;
  lastChecked: Date;
  error?: string;
  modelInfo?: ModelInfo;
}

/**
 * Information about the model
 */
export interface ModelInfo {
  name: string;
  version?: string;
  contextLength: number;
  tokensPerSecond?: number;
  memoryUsage?: number;
  capabilities?: string[];
}

/**
 * Token counting result
 */
export interface TokenCount {
  count: number;
  model: string;
  estimatedCost?: number;
}

/**
 * Provider statistics
 */
export interface ProviderStats {
  totalRequests: number;
  successfulRequests: number;
  failedRequests: number;
  averageLatency: number;
  totalCost: number;
  lastUsed?: Date;
}

/**
 * Abstract base class for LLM providers
 */
export abstract class LLMProvider {
  protected config: ProviderConfig;
  protected stats: ProviderStats;

  constructor(config: ProviderConfig) {
    this.config = config;
    this.stats = {
      totalRequests: 0,
      successfulRequests: 0,
      failedRequests: 0,
      averageLatency: 0,
      totalCost: 0
    };
  }

  // Abstract methods that must be implemented by concrete providers
  abstract initialize(): Promise<void>;
  abstract isAvailable(): Promise<boolean>;
  abstract generateSummary(request: SummaryRequest): Promise<SummaryResponse>;
  abstract countTokens(text: string): Promise<TokenCount>;
  abstract getHealth(): Promise<ProviderHealth>;

  // Concrete methods with default implementations
  getId(): string {
    return this.config.id;
  }

  getName(): string {
    return this.config.name;
  }

  getType(): 'local' | 'external' {
    return this.config.type;
  }

  getConfig(): ProviderConfig {
    return { ...this.config };
  }

  getMaxTokens(): number {
    return this.config.maxTokens;
  }

  getModelName(): string {
    return this.config.modelName;
  }

  getPriority(): number {
    return this.config.priority || 0;
  }

  isActive(): boolean {
    return this.config.isActive !== false;
  }

  getStats(): ProviderStats {
    return { ...this.stats };
  }

  estimateCost(tokens: number): number {
    if (!this.config.costPer1kTokens) {
      return 0;
    }
    return (tokens / 1000) * this.config.costPer1kTokens;
  }

  /**
   * Update provider configuration
   */
  updateConfig(updates: Partial<ProviderConfig>): void {
    this.config = { ...this.config, ...updates };
  }

  /**
   * Record successful request
   */
  protected recordSuccess(latency: number, cost: number = 0): void {
    this.stats.totalRequests++;
    this.stats.successfulRequests++;
    this.stats.totalCost += cost;
    this.stats.lastUsed = new Date();

    // Update average latency
    const totalSuccessful = this.stats.successfulRequests;
    this.stats.averageLatency = 
      (this.stats.averageLatency * (totalSuccessful - 1) + latency) / totalSuccessful;
  }

  /**
   * Record failed request
   */
  protected recordFailure(): void {
    this.stats.totalRequests++;
    this.stats.failedRequests++;
  }

  /**
   * Validate request parameters
   */
  protected validateRequest(request: SummaryRequest): void {
    if (!request.messages || request.messages.length === 0) {
      throw new Error('No messages provided for summarization');
    }

    if (request.maxTokens <= 0 || request.maxTokens > this.config.maxTokens) {
      throw new Error(`Invalid token count: ${request.maxTokens}. Must be between 1 and ${this.config.maxTokens}`);
    }

    if (!['brief', 'standard', 'detailed'].includes(request.level)) {
      throw new Error(`Invalid summary level: ${request.level}`);
    }
  }

  /**
   * Get model information (optional implementation)
   */
  async getModelInfo(): Promise<ModelInfo | undefined> {
    return undefined;
  }

  /**
   * Cleanup resources (optional implementation)
   */
  async cleanup(): Promise<void> {
    // Default: no cleanup needed
  }

  /**
   * Test the provider with a simple request (optional implementation)
   */
  async testConnection(): Promise<boolean> {
    try {
      const health = await this.getHealth();
      return health.isAvailable;
    } catch {
      return false;
    }
  }
}

/**
 * Provider error types
 */
export class ProviderError extends Error {
  constructor(
    message: string,
    public readonly code: string,
    public readonly provider: string,
    public readonly cause?: Error
  ) {
    super(message);
    this.name = 'ProviderError';
  }
}

export class ProviderUnavailableError extends ProviderError {
  constructor(provider: string, cause?: Error) {
    super(`Provider ${provider} is not available`, 'PROVIDER_UNAVAILABLE', provider, cause);
    this.name = 'ProviderUnavailableError';
  }
}

export class ProviderTimeoutError extends ProviderError {
  constructor(provider: string, timeout: number) {
    super(`Provider ${provider} timed out after ${timeout}ms`, 'PROVIDER_TIMEOUT', provider);
    this.name = 'ProviderTimeoutError';
  }
}

export class ProviderAuthError extends ProviderError {
  constructor(provider: string, cause?: Error) {
    super(`Authentication failed for provider ${provider}`, 'PROVIDER_AUTH_ERROR', provider, cause);
    this.name = 'ProviderAuthError';
  }
}

export class ProviderRateLimitError extends ProviderError {
  constructor(provider: string, retryAfter?: number) {
    super(
      `Rate limit exceeded for provider ${provider}${retryAfter ? `. Retry after ${retryAfter}s` : ''}`,
      'PROVIDER_RATE_LIMIT',
      provider
    );
    this.name = 'ProviderRateLimitError';
  }
}

export class ProviderQuotaError extends ProviderError {
  constructor(provider: string, cause?: Error) {
    super(`Quota exceeded for provider ${provider}`, 'PROVIDER_QUOTA_EXCEEDED', provider, cause);
    this.name = 'ProviderQuotaError';
  }
}

/**
 * Ollama Provider
 * 
 * Local LLM provider using Ollama API for conversation summarization.
 * Provides privacy-preserving inference with various open-source models.
 */

import { 
  LLMProvider, 
  ProviderConfig, 
  SummaryRequest, 
  SummaryResponse, 
  ProviderHealth, 
  ModelInfo, 
  TokenCount,
  ProviderError,
  ProviderUnavailableError,
  ProviderTimeoutError
} from './LLMProvider.js';
import { createTokenCounter } from '../TokenCounter.js';

/**
 * Ollama API response interfaces
 */
interface OllamaGenerateRequest {
  model: string;
  prompt: string;
  stream?: boolean;
  options?: {
    temperature?: number;
    top_p?: number;
    max_tokens?: number;
    stop?: string[];
  };
}

interface OllamaGenerateResponse {
  model: string;
  created_at: string;
  response: string;
  done: boolean;
  context?: number[];
  total_duration?: number;
  load_duration?: number;
  prompt_eval_count?: number;
  prompt_eval_duration?: number;
  eval_count?: number;
  eval_duration?: number;
}

interface OllamaModelInfo {
  name: string;
  modified_at: string;
  size: number;
  digest: string;
  details: {
    format?: string;
    family?: string;
    families?: string[];
    parameter_size?: string;
    quantization_level?: string;
  };
}

/**
 * Ollama provider implementation
 */
export class OllamaProvider extends LLMProvider {
  private endpoint: string;
  private timeout: number;
  private tokenCounter: ReturnType<typeof createTokenCounter>;

  constructor(config: ProviderConfig) {
    super(config);
    this.endpoint = config.endpoint || 'http://localhost:11434';
    this.timeout = config.metadata?.timeout || 30000;
    this.tokenCounter = createTokenCounter(config.modelName);
  }

  async initialize(): Promise<void> {
    try {
      // Check if Ollama is running
      const health = await this.getHealth();
      if (!health.isAvailable) {
        throw new Error('Ollama service is not available');
      }

      // Try to pull the model if it doesn't exist
      await this.ensureModelExists();

    } catch (error) {
      throw new ProviderError(
        `Failed to initialize Ollama provider: ${error instanceof Error ? error.message : 'Unknown error'}`,
        'INITIALIZATION_ERROR',
        this.getName(),
        error instanceof Error ? error : undefined
      );
    }
  }

  async isAvailable(): Promise<boolean> {
    try {
      const response = await this.fetchWithTimeout('/api/tags', {
        method: 'GET'
      });
      return response.ok;
    } catch {
      return false;
    }
  }

  async generateSummary(request: SummaryRequest): Promise<SummaryResponse> {
    const startTime = Date.now();

    try {
      this.validateRequest(request);

      // Count input tokens
      const inputTokens = this.tokenCounter.countMessages(request.messages).count;

      // Generate prompt for summarization
      const prompt = this.createSummaryPrompt(request);

      // Make API request
      const ollamaRequest: OllamaGenerateRequest = {
        model: this.config.modelName,
        prompt,
        stream: false,
        options: {
          temperature: this.config.temperature || 0.7,
          max_tokens: request.maxTokens,
          stop: ['</summary>', '[END]', '---']
        }
      };

      const response = await this.fetchWithTimeout('/api/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(ollamaRequest)
      });

      if (!response.ok) {
        throw new Error(`Ollama API error: ${response.status} ${response.statusText}`);
      }

      const result = await response.json() as OllamaGenerateResponse;

      // Extract and clean summary
      const summary = this.extractSummary(result.response);
      const outputTokens = this.tokenCounter.countText(summary).count;
      const processingTime = Date.now() - startTime;

      // Calculate quality score based on length and content
      const qualityScore = this.calculateQualityScore(summary, request.level);

      // Record success
      this.recordSuccess(processingTime, 0); // No cost for local model

      return {
        summary,
        tokenCount: outputTokens,
        inputTokens,
        outputTokens,
        cost: 0, // Local model, no cost
        qualityScore,
        processingTime,
        metadata: {
          model: result.model,
          promptEvalCount: result.prompt_eval_count,
          evalCount: result.eval_count,
          totalDuration: result.total_duration,
          loadDuration: result.load_duration
        }
      };

    } catch (error) {
      this.recordFailure();

      if (error instanceof ProviderError) {
        throw error;
      }

      throw new ProviderError(
        `Summary generation failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        'GENERATION_ERROR',
        this.getName(),
        error instanceof Error ? error : undefined
      );
    }
  }

  async countTokens(text: string): Promise<TokenCount> {
    const result = this.tokenCounter.countText(text);
    return {
      count: result.count,
      model: this.config.modelName,
      estimatedCost: 0 // No cost for local counting
    };
  }

  async getHealth(): Promise<ProviderHealth> {
    const startTime = Date.now();

    try {
      const response = await this.fetchWithTimeout('/api/version', {
        method: 'GET'
      });

      if (!response.ok) {
        return {
          isAvailable: false,
          lastChecked: new Date(),
          error: `HTTP ${response.status}: ${response.statusText}`
        };
      }

      const latency = Date.now() - startTime;
      const modelInfo = await this.getModelInfo();

      return {
        isAvailable: true,
        latency,
        lastChecked: new Date(),
        modelInfo
      };

    } catch (error) {
      return {
        isAvailable: false,
        lastChecked: new Date(),
        error: error instanceof Error ? error.message : 'Unknown error'
      };
    }
  }

  async getModelInfo(): Promise<ModelInfo | undefined> {
    try {
      const response = await this.fetchWithTimeout('/api/show', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ name: this.config.modelName })
      });

      if (!response.ok) {
        return undefined;
      }

      const info = await response.json() as OllamaModelInfo;

      return {
        name: info.name,
        version: info.digest.slice(0, 12),
        contextLength: this.getContextLength(info.details.family),
        capabilities: this.getModelCapabilities(info.details.family),
        memoryUsage: info.size
      };

    } catch {
      return undefined;
    }
  }

  /**
   * Create summarization prompt based on request
   */
  private createSummaryPrompt(request: SummaryRequest): string {
    const { messages, level, focusTopics, previousSummary } = request;

    // Format conversation
    const conversation = messages.map(msg => 
      `${msg.role}: ${msg.content}`
    ).join('\n\n');

    // Base prompt templates
    const templates = {
      brief: `Please provide a very brief 1-2 sentence summary of this conversation:

${conversation}

Focus on the main outcome or decision. Be concise and clear.

Summary:`,

      standard: `Please provide a clear summary of this conversation in 1-2 paragraphs:

${conversation}

Include the main topics discussed, key points made, and any outcomes or decisions reached.

${focusTopics ? `Pay special attention to: ${focusTopics.join(', ')}` : ''}

Summary:`,

      detailed: `Please provide a comprehensive summary of this conversation:

${conversation}

Include:
- Main topics and themes discussed
- Key arguments and perspectives presented
- Important decisions made or conclusions reached
- Any action items or next steps mentioned
- Context and background information

${focusTopics ? `Focus particularly on: ${focusTopics.join(', ')}` : ''}

${previousSummary ? `Previous summary to build upon:\n${previousSummary}\n` : ''}

Detailed Summary:`
    };

    return templates[level];
  }

  /**
   * Extract clean summary from model response
   */
  private extractSummary(response: string): string {
    // Remove any markdown formatting or extra text
    let summary = response.trim();

    // Remove common prefixes
    const prefixes = ['Summary:', 'summary:', 'Brief:', 'brief:', 'Here is', 'Here\'s'];
    for (const prefix of prefixes) {
      if (summary.toLowerCase().startsWith(prefix.toLowerCase())) {
        summary = summary.slice(prefix.length).trim();
      }
    }

    // Remove any trailing punctuation repetition
    summary = summary.replace(/[.!?]{2,}$/, '.');

    // Ensure it ends with proper punctuation
    if (!/[.!?]$/.test(summary)) {
      summary += '.';
    }

    return summary;
  }

  /**
   * Calculate quality score for generated summary
   */
  private calculateQualityScore(summary: string, level: 'brief' | 'standard' | 'detailed'): number {
    let score = 0.5; // Base score

    // Length appropriateness
    const lengthTargets = {
      brief: { min: 10, ideal: 50, max: 150 },
      standard: { min: 50, ideal: 200, max: 500 },
      detailed: { min: 100, ideal: 400, max: 1000 }
    };

    const target = lengthTargets[level];
    const length = summary.length;

    if (length >= target.min && length <= target.max) {
      score += 0.2;
      if (Math.abs(length - target.ideal) / target.ideal < 0.3) {
        score += 0.1; // Bonus for ideal length
      }
    }

    // Content quality indicators
    const hasProperStructure = /^[A-Z]/.test(summary) && /[.!?]$/.test(summary);
    if (hasProperStructure) score += 0.1;

    const hasCoherentSentences = !summary.includes('...');
    if (hasCoherentSentences) score += 0.1;

    const notTooRepetitive = !/((.+)\2{2,})/.test(summary);
    if (notTooRepetitive) score += 0.1;

    return Math.min(1.0, Math.max(0.0, score));
  }

  /**
   * Ensure model exists, pull if necessary
   */
  private async ensureModelExists(): Promise<void> {
    try {
      // Check if model exists
      const response = await this.fetchWithTimeout('/api/show', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ name: this.config.modelName })
      });

      if (response.ok) {
        return; // Model exists
      }

      // Try to pull the model
      console.log(`Pulling Ollama model: ${this.config.modelName}`);
      const pullResponse = await this.fetchWithTimeout('/api/pull', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ name: this.config.modelName })
      }, 300000); // 5 minute timeout for pulling

      if (!pullResponse.ok) {
        throw new Error(`Failed to pull model ${this.config.modelName}`);
      }

    } catch (error) {
      throw new ProviderUnavailableError(
        this.getName(),
        error instanceof Error ? error : undefined
      );
    }
  }

  /**
   * Get context length for model family
   */
  private getContextLength(family?: string): number {
    const contextLengths: Record<string, number> = {
      'llama': 4096,
      'mistral': 4096,
      'codellama': 16384,
      'vicuna': 2048,
      'alpaca': 2048
    };

    if (family && family in contextLengths) {
      return contextLengths[family];
    }

    return 4096; // Default
  }

  /**
   * Get model capabilities
   */
  private getModelCapabilities(family?: string): string[] {
    const capabilities: Record<string, string[]> = {
      'llama': ['text-generation', 'conversation', 'reasoning'],
      'mistral': ['text-generation', 'conversation', 'reasoning', 'instruction-following'],
      'codellama': ['text-generation', 'code-generation', 'code-explanation'],
      'vicuna': ['text-generation', 'conversation'],
      'alpaca': ['text-generation', 'instruction-following']
    };

    if (family && family in capabilities) {
      return capabilities[family];
    }

    return ['text-generation', 'conversation'];
  }

  /**
   * Fetch with timeout support
   */
  private async fetchWithTimeout(
    path: string, 
    options: RequestInit, 
    timeoutMs: number = this.timeout
  ): Promise<Response> {
    const url = `${this.endpoint}${path}`;

    const timeoutPromise = new Promise<never>((_, reject) => {
      setTimeout(() => reject(new ProviderTimeoutError(this.getName(), timeoutMs)), timeoutMs);
    });

    try {
      const response = await Promise.race([
        fetch(url, options),
        timeoutPromise
      ]);

      return response;
    } catch (error) {
      if (error instanceof ProviderTimeoutError) {
        throw error;
      }

      throw new ProviderUnavailableError(
        this.getName(),
        error instanceof Error ? error : undefined
      );
    }
  }
}

/**
 * OpenAI Provider
 * 
 * External LLM provider using OpenAI API for conversation summarization.
 * Provides high-quality summaries with cost tracking and rate limiting.
 */

import { 
  LLMProvider, 
  ProviderConfig, 
  SummaryRequest, 
  SummaryResponse, 
  ProviderHealth, 
  ModelInfo, 
  TokenCount,
  ProviderError,
  ProviderUnavailableError,
  ProviderTimeoutError,
  ProviderAuthError,
  ProviderRateLimitError,
  ProviderQuotaError
} from './LLMProvider.js';
import { createTokenCounter } from '../TokenCounter.js';

/**
 * OpenAI API interfaces
 */
interface OpenAIMessage {
  role: 'system' | 'user' | 'assistant';
  content: string;
}

interface OpenAIChatRequest {
  model: string;
  messages: OpenAIMessage[];
  temperature?: number;
  max_tokens?: number;
  top_p?: number;
  frequency_penalty?: number;
  presence_penalty?: number;
  stop?: string[];
}

interface OpenAIChatResponse {
  id: string;
  object: string;
  created: number;
  model: string;
  usage: {
    prompt_tokens: number;
    completion_tokens: number;
    total_tokens: number;
  };
  choices: Array<{
    index: number;
    message: {
      role: string;
      content: string;
    };
    finish_reason: string;
  }>;
}

interface OpenAIError {
  error: {
    message: string;
    type: string;
    code?: string;
  };
}

/**
 * OpenAI model configurations
 */
const OPENAI_MODELS = {
  'gpt-3.5-turbo': {
    contextLength: 4096,
    costPer1kTokens: 0.002,
    maxTokens: 4096
  },
  'gpt-3.5-turbo-16k': {
    contextLength: 16384,
    costPer1kTokens: 0.004,
    maxTokens: 16384
  },
  'gpt-4': {
    contextLength: 8192,
    costPer1kTokens: 0.03,
    maxTokens: 8192
  },
  'gpt-4-turbo': {
    contextLength: 128000,
    costPer1kTokens: 0.01,
    maxTokens: 4096
  },
  'gpt-4o': {
    contextLength: 128000,
    costPer1kTokens: 0.005,
    maxTokens: 4096
  }
};

/**
 * OpenAI provider implementation
 */
export class OpenAIProvider extends LLMProvider {
  private apiKey: string;
  private endpoint: string;
  private timeout: number;
  private tokenCounter: ReturnType<typeof createTokenCounter>;
  private modelConfig: typeof OPENAI_MODELS[keyof typeof OPENAI_MODELS];

  constructor(config: ProviderConfig) {
    super(config);

    // Get API key from environment
    const apiKeyEnv = config.apiKeyEnv || 'PERSISTENCE_OPENAI_API_KEY';
    const apiKey = process.env[apiKeyEnv];

    if (!apiKey) {
      throw new ProviderError(
        `OpenAI API key not found in environment variable: ${apiKeyEnv}`,
        'MISSING_API_KEY',
        this.getName()
      );
    }

    this.apiKey = apiKey;
    this.endpoint = config.endpoint || 'https://api.openai.com/v1';
    this.timeout = config.metadata?.timeout || 60000;
    this.tokenCounter = createTokenCounter(config.modelName);

    // Get model configuration
    this.modelConfig = OPENAI_MODELS[config.modelName as keyof typeof OPENAI_MODELS];
    if (!this.modelConfig) {
      throw new ProviderError(
        `Unsupported OpenAI model: ${config.modelName}`,
        'UNSUPPORTED_MODEL',
        this.getName()
      );
    }
  }

  async initialize(): Promise<void> {
    try {
      // Validate API key by making a simple request
      const health = await this.getHealth();
      if (!health.isAvailable) {
        throw new Error('OpenAI API is not accessible');
      }
    } catch (error) {
      throw new ProviderError(
        `Failed to initialize OpenAI provider: ${error instanceof Error ? error.message : 'Unknown error'}`,
        'INITIALIZATION_ERROR',
        this.getName(),
        error instanceof Error ? error : undefined
      );
    }
  }

  async isAvailable(): Promise<boolean> {
    try {
      const response = await this.fetchWithTimeout('/models', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json'
        }
      });
      return response.ok;
    } catch {
      return false;
    }
  }

  async generateSummary(request: SummaryRequest): Promise<SummaryResponse> {
    const startTime = Date.now();

    try {
      this.validateRequest(request);

      // Count input tokens (for tracking)
      this.tokenCounter.countMessages(request.messages).count;

      // Create messages for OpenAI API
      const messages = this.createChatMessages(request);

      // Calculate max output tokens
      const maxOutputTokens = Math.min(request.maxTokens, 1000); // Reasonable limit for summaries

      // Make API request
      const chatRequest: OpenAIChatRequest = {
        model: this.config.modelName,
        messages,
        temperature: this.config.temperature || 0.7,
        max_tokens: maxOutputTokens,
        top_p: 0.9,
        frequency_penalty: 0.1,
        presence_penalty: 0.1,
        stop: ['---', '[END]', '</summary>']
      };

      const response = await this.fetchWithTimeout('/chat/completions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(chatRequest)
      });

      if (!response.ok) {
        await this.handleApiError(response);
      }

      const result = await response.json() as OpenAIChatResponse;

      if (!result.choices || result.choices.length === 0) {
        throw new Error('No response from OpenAI API');
      }

      // Extract summary
      const summary = result.choices[0].message.content.trim();
      const processingTime = Date.now() - startTime;

      // Calculate actual cost
      const actualCost = (result.usage.total_tokens / 1000) * this.modelConfig.costPer1kTokens;

      // Calculate quality score
      const qualityScore = this.calculateQualityScore(summary, request.level);

      // Record success
      this.recordSuccess(processingTime, actualCost);

      return {
        summary,
        tokenCount: result.usage.completion_tokens,
        inputTokens: result.usage.prompt_tokens,
        outputTokens: result.usage.completion_tokens,
        cost: actualCost,
        qualityScore,
        processingTime,
        metadata: {
          model: result.model,
          finishReason: result.choices[0].finish_reason,
          totalTokens: result.usage.total_tokens,
          requestId: result.id
        }
      };

    } catch (error) {
      this.recordFailure();

      if (error instanceof ProviderError) {
        throw error;
      }

      throw new ProviderError(
        `Summary generation failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        'GENERATION_ERROR',
        this.getName(),
        error instanceof Error ? error : undefined
      );
    }
  }

  async countTokens(text: string): Promise<TokenCount> {
    const result = this.tokenCounter.countText(text);
    const estimatedCost = this.estimateCost(result.count);

    return {
      count: result.count,
      model: this.config.modelName,
      estimatedCost
    };
  }

  async getHealth(): Promise<ProviderHealth> {
    const startTime = Date.now();

    try {
      const response = await this.fetchWithTimeout('/models', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const error = await response.text();
        return {
          isAvailable: false,
          lastChecked: new Date(),
          error: `HTTP ${response.status}: ${error}`
        };
      }

      const latency = Date.now() - startTime;

      return {
        isAvailable: true,
        latency,
        lastChecked: new Date(),
        modelInfo: {
          name: this.config.modelName,
          contextLength: this.modelConfig.contextLength,
          capabilities: ['text-generation', 'conversation', 'reasoning', 'summarization']
        }
      };

    } catch (error) {
      return {
        isAvailable: false,
        lastChecked: new Date(),
        error: error instanceof Error ? error.message : 'Unknown error'
      };
    }
  }

  async getModelInfo(): Promise<ModelInfo> {
    return {
      name: this.config.modelName,
      contextLength: this.modelConfig.contextLength,
      capabilities: ['text-generation', 'conversation', 'reasoning', 'summarization']
    };
  }

  /**
   * Create chat messages for OpenAI API
   */
  private createChatMessages(request: SummaryRequest): OpenAIMessage[] {
    const { messages, level, focusTopics, previousSummary } = request;

    // System message with instructions
    const systemMessage: OpenAIMessage = {
      role: 'system',
      content: this.createSystemPrompt(level, focusTopics, previousSummary)
    };

    // Format conversation as user message
    const conversationText = messages.map(msg => 
      `${msg.role}: ${msg.content}`
    ).join('\n\n');

    const userMessage: OpenAIMessage = {
      role: 'user',
      content: `Please summarize this conversation:\n\n${conversationText}`
    };

    return [systemMessage, userMessage];
  }

  /**
   * Create system prompt based on summary level
   */
  private createSystemPrompt(
    level: 'brief' | 'standard' | 'detailed',
    focusTopics?: string[],
    previousSummary?: string
  ): string {
    const basePrompt = "You are an expert at creating clear, accurate conversation summaries.";

    const levelInstructions = {
      brief: "Create a very brief 1-2 sentence summary focusing on the main outcome or decision.",
      standard: "Create a clear 1-2 paragraph summary including main topics, key points, and outcomes.",
      detailed: "Create a comprehensive summary including topics, arguments, decisions, action items, and context."
    };

    let prompt = `${basePrompt} ${levelInstructions[level]}`;

    if (focusTopics && focusTopics.length > 0) {
      prompt += ` Pay special attention to: ${focusTopics.join(', ')}.`;
    }

    if (previousSummary) {
      prompt += ` Build upon this previous summary: ${previousSummary}`;
    }

    prompt += " Be accurate, concise, and objective. Do not add information not present in the conversation.";

    return prompt;
  }

  /**
   * Calculate quality score for generated summary
   */
  private calculateQualityScore(summary: string, level: 'brief' | 'standard' | 'detailed'): number {
    let score = 0.7; // Base score for OpenAI (generally high quality)

    // Length appropriateness (OpenAI is usually good at this)
    const lengthTargets = {
      brief: { min: 20, ideal: 80, max: 200 },
      standard: { min: 100, ideal: 300, max: 600 },
      detailed: { min: 200, ideal: 500, max: 1200 }
    };

    const target = lengthTargets[level];
    const length = summary.length;

    if (length >= target.min && length <= target.max) {
      score += 0.15;
    }

    // Structure quality
    const hasGoodStructure = /^[A-Z]/.test(summary) && /[.!?]$/.test(summary);
    if (hasGoodStructure) score += 0.1;

    // Content quality (OpenAI typically produces coherent text)
    if (!summary.includes('I cannot') && !summary.includes('I am unable')) {
      score += 0.05;
    }

    return Math.min(1.0, score);
  }

  /**
   * Handle API errors and convert to appropriate exceptions
   */
  private async handleApiError(response: Response): Promise<never> {
    let errorDetails: OpenAIError;

    try {
      errorDetails = await response.json() as OpenAIError;
    } catch {
      throw new ProviderError(
        `OpenAI API error: ${response.status} ${response.statusText}`,
        'API_ERROR',
        this.getName()
      );
    }

    const { error } = errorDetails;

    switch (response.status) {
      case 401:
        throw new ProviderAuthError(this.getName());

      case 429:
        const retryAfter = response.headers.get('retry-after');
        throw new ProviderRateLimitError(
          this.getName(), 
          retryAfter ? parseInt(retryAfter) : undefined
        );

      case 402:
      case 403:
        if (error.code === 'insufficient_quota') {
          throw new ProviderQuotaError(this.getName());
        }
        throw new ProviderAuthError(this.getName());

      default:
        throw new ProviderError(
          error.message || `OpenAI API error: ${response.status}`,
          error.code || 'API_ERROR',
          this.getName()
        );
    }
  }

  /**
   * Fetch with timeout support
   */
  private async fetchWithTimeout(
    path: string, 
    options: RequestInit, 
    timeoutMs: number = this.timeout
  ): Promise<Response> {
    const url = `${this.endpoint}${path}`;

    const timeoutPromise = new Promise<never>((_, reject) => {
      setTimeout(() => reject(new ProviderTimeoutError(this.getName(), timeoutMs)), timeoutMs);
    });

    try {
      const response = await Promise.race([
        fetch(url, options),
        timeoutPromise
      ]);

      return response;
    } catch (error) {
      if (error instanceof ProviderTimeoutError) {
        throw error;
      }

      throw new ProviderUnavailableError(
        this.getName(),
        error instanceof Error ? error : undefined
      );
    }
  }

  /**
   * Get supported models
   */
  static getSupportedModels(): string[] {
    return Object.keys(OPENAI_MODELS);
  }

  /**
   * Get model configuration
   */
  static getModelConfig(model: string): typeof OPENAI_MODELS[keyof typeof OPENAI_MODELS] | undefined {
    return OPENAI_MODELS[model as keyof typeof OPENAI_MODELS];
  }
}

/**
 * Relevance Scorer - Multi-factor relevance scoring for context assembly
 * 
 * This module provides:
 * - Semantic similarity scoring using embeddings
 * - Temporal relevance with decay functions
 * - Entity overlap scoring
 * - Structural relevance (conversation position)
 * - Query-specific weighting
 */

import { EmbeddingManager } from '../../search/EmbeddingManager.js';
import { ContextAssemblyRequest } from '../ContextAssembler.js';

/**
 * Item to be scored for relevance
 */
export interface ScoredItem {
  id: string;
  type: 'summary' | 'message';
  content: string;
  relevanceScore: number;
  tokenCount: number;
  createdAt: number;
  conversationId: string;
  metadata?: Record<string, any>;
  originalItem?: any; // Reference to original ConversationSummary or Message
}

/**
 * Item input for scoring
 */
export interface ItemToScore {
  id: string;
  type: 'summary' | 'message';
  content: string;
  createdAt: number;
  conversationId: string;
  metadata?: Record<string, any>;
}

/**
 * Relevance scoring configuration
 */
export interface RelevanceScorerConfig {
  /** Weight for semantic similarity (0-1) */
  semanticWeight: number;
  /** Weight for temporal relevance (0-1) */
  temporalWeight: number;
  /** Weight for entity overlap (0-1) */
  entityWeight: number;
  /** Weight for structural relevance (0-1) */
  structuralWeight: number;
  /** Temporal decay half-life in milliseconds */
  temporalDecayHalfLife: number;
  /** Minimum semantic similarity threshold */
  minSemanticSimilarity: number;
  /** Entity extraction patterns */
  entityPatterns: RegExp[];
}

/**
 * Default scoring configuration
 */
const DEFAULT_CONFIG: RelevanceScorerConfig = {
  semanticWeight: 0.4,
  temporalWeight: 0.2,
  entityWeight: 0.2,
  structuralWeight: 0.2,
  temporalDecayHalfLife: 7 * 24 * 60 * 60 * 1000, // 7 days
  minSemanticSimilarity: 0.1,
  entityPatterns: [
    /\b[A-Z][a-z]+ [A-Z][a-z]+\b/g, // Person names
    /\b[A-Z][a-zA-Z]+(?:\.[a-zA-Z]+)+\b/g, // Domain names
    /\b[A-Z]{2,}\b/g, // Acronyms
    /@\w+/g, // Mentions
    /#\w+/g, // Hashtags
    /\b\d{4}-\d{2}-\d{2}\b/g, // Dates
    /\$[A-Z]{3,4}\b/g // Currency codes
  ]
};

/**
 * Relevance Scorer for context items
 */
export class RelevanceScorer {
  private config: RelevanceScorerConfig;
  private embeddingManager: EmbeddingManager;
  private queryEmbeddingCache: Map<string, number[]>;

  constructor(
    embeddingManager: EmbeddingManager,
    config?: Partial<RelevanceScorerConfig>
  ) {
    this.config = { ...DEFAULT_CONFIG, ...config };
    this.embeddingManager = embeddingManager;
    this.queryEmbeddingCache = new Map();

    // Validate weights sum approximately to 1
    const totalWeight = this.config.semanticWeight + this.config.temporalWeight + 
                       this.config.entityWeight + this.config.structuralWeight;
    if (Math.abs(totalWeight - 1.0) > 0.1) {
      console.warn(`Relevance scorer weights sum to ${totalWeight}, should be close to 1.0`);
    }
  }

  /**
   * Score an item for relevance to a query
   */
  async scoreItem(
    item: ItemToScore,
    request: ContextAssemblyRequest
  ): Promise<number> {
    try {
      // Calculate individual scores
      const semanticScore = await this.calculateSemanticScore(item, request);
      const temporalScore = this.calculateTemporalScore(item, request);
      const entityScore = this.calculateEntityScore(item, request);
      const structuralScore = this.calculateStructuralScore(item, request);

      // Apply weights and combine scores
      const totalScore = 
        (semanticScore * this.config.semanticWeight) +
        (temporalScore * this.config.temporalWeight) +
        (entityScore * this.config.entityWeight) +
        (structuralScore * this.config.structuralWeight);

      // Apply query-specific boosting
      const boostedScore = this.applyQuerySpecificBoosting(
        totalScore,
        item,
        request
      );

      // Clamp to [0, 1] range
      return Math.max(0, Math.min(1, boostedScore));

    } catch (error) {
      console.error(`Failed to score item ${item.id}:`, error);
      return 0; // Return minimum score on error
    }
  }

  /**
   * Calculate semantic similarity score using embeddings
   */
  private async calculateSemanticScore(
    item: ItemToScore,
    request: ContextAssemblyRequest
  ): Promise<number> {
    try {
      // Get or generate query embedding
      let queryEmbedding = this.queryEmbeddingCache.get(request.query);
      if (!queryEmbedding) {
        queryEmbedding = await this.embeddingManager.generateEmbedding(request.query);
        this.queryEmbeddingCache.set(request.query, queryEmbedding);
      }

      // Generate content embedding
      const contentEmbedding = await this.embeddingManager.generateEmbedding(item.content);

      // Calculate cosine similarity
      const similarity = this.embeddingManager.cosineSimilarity(queryEmbedding, contentEmbedding);

      // Apply minimum threshold
      if (similarity < this.config.minSemanticSimilarity) {
        return 0;
      }

      // Normalize to [0, 1] range
      return Math.max(0, Math.min(1, similarity));

    } catch (error) {
      console.warn(`Failed to calculate semantic score for item ${item.id}:`, error);
      return 0;
    }
  }

  /**
   * Calculate temporal relevance score with decay
   */
  private calculateTemporalScore(
    item: ItemToScore,
    _request: ContextAssemblyRequest
  ): number {
    const now = Date.now();
    const age = now - item.createdAt;

    // Handle negative ages (future items)
    if (age < 0) {
      return 1.0; // Future items get maximum temporal score
    }

    // Apply exponential decay with configurable half-life
    const decayConstant = Math.log(2) / this.config.temporalDecayHalfLife;
    const decayScore = Math.exp(-decayConstant * age);

    // Recent items get boosted score
    const recentBoost = age < (24 * 60 * 60 * 1000) ? 1.2 : 1.0; // 24 hours

    return Math.max(0, Math.min(1, decayScore * recentBoost));
  }

  /**
   * Calculate entity overlap score
   */
  private calculateEntityScore(
    item: ItemToScore,
    request: ContextAssemblyRequest
  ): number {
    const queryEntities = this.extractEntities(request.query);
    const contentEntities = this.extractEntities(item.content);

    // Add focus entities from request
    if (request.focusEntities && request.focusEntities.length > 0) {
      queryEntities.push(...request.focusEntities.map(e => e.toLowerCase()));
    }

    if (queryEntities.length === 0) {
      return 0.5; // Neutral score when no entities to compare
    }

    // Calculate Jaccard similarity for entity overlap
    const querySet = new Set(queryEntities);
    const contentSet = new Set(contentEntities);

    const intersection = new Set([...querySet].filter(e => contentSet.has(e)));
    const union = new Set([...querySet, ...contentSet]);

    const jaccardSimilarity = union.size > 0 ? intersection.size / union.size : 0;

    // Boost score for exact entity matches
    const exactMatches = [...intersection].length;
    const exactBoost = exactMatches > 0 ? 1 + (exactMatches * 0.1) : 1;

    return Math.max(0, Math.min(1, jaccardSimilarity * exactBoost));
  }

  /**
   * Calculate structural relevance score
   */
  private calculateStructuralScore(
    item: ItemToScore,
    request: ContextAssemblyRequest
  ): number {
    let structuralScore = 0.5; // Base score

    // Boost summaries as they provide high-level context
    if (item.type === 'summary') {
      structuralScore += 0.2;
    }

    // Boost items in the target conversation
    if (request.conversationId && item.conversationId === request.conversationId) {
      structuralScore += 0.3;
    }

    // Boost items with rich metadata
    if (item.metadata && Object.keys(item.metadata).length > 0) {
      structuralScore += 0.1;
    }

    // Consider message role for messages
    if (item.type === 'message' && item.metadata?.role) {
      const role = item.metadata.role;
      if (role === 'assistant') {
        structuralScore += 0.1; // Assistant responses often contain valuable info
      } else if (role === 'system') {
        structuralScore += 0.05; // System messages provide context
      }
    }

    return Math.max(0, Math.min(1, structuralScore));
  }

  /**
   * Apply query-specific boosting rules
   */
  private applyQuerySpecificBoosting(
    baseScore: number,
    item: ItemToScore,
    request: ContextAssemblyRequest
  ): number {
    let boostedScore = baseScore;

    // Boost for question words in query
    const questionWords = ['what', 'how', 'why', 'when', 'where', 'who', 'which'];
    const queryLower = request.query.toLowerCase();
    const hasQuestionWord = questionWords.some(word => queryLower.includes(word));

    if (hasQuestionWord) {
      // Look for answers or explanations in content
      const contentLower = item.content.toLowerCase();
      const answerIndicators = ['because', 'therefore', 'due to', 'result', 'answer', 'solution'];
      const hasAnswerIndicator = answerIndicators.some(indicator => 
        contentLower.includes(indicator)
      );

      if (hasAnswerIndicator) {
        boostedScore *= 1.15; // 15% boost for potential answers
      }
    }

    // Boost for code-related queries
    if (this.isCodeRelatedQuery(request.query)) {
      const hasCodeContent = this.hasCodeContent(item.content);
      if (hasCodeContent) {
        boostedScore *= 1.2; // 20% boost for code content
      }
    }

    // Boost for error-related queries
    if (this.isErrorRelatedQuery(request.query)) {
      const hasErrorContent = this.hasErrorContent(item.content);
      if (hasErrorContent) {
        boostedScore *= 1.25; // 25% boost for error-related content
      }
    }

    // Boost recent items for time-sensitive queries
    if (request.includeRecent && this.isTimeSensitiveQuery(request.query)) {
      const age = Date.now() - item.createdAt;
      const isRecent = age < (24 * 60 * 60 * 1000); // 24 hours

      if (isRecent) {
        boostedScore *= 1.1; // 10% boost for recent items
      }
    }

    return boostedScore;
  }

  /**
   * Extract entities from text using patterns
   */
  private extractEntities(text: string): string[] {
    const entities: string[] = [];

    for (const pattern of this.config.entityPatterns) {
      const matches = text.match(pattern);
      if (matches) {
        entities.push(...matches.map(match => match.toLowerCase().trim()));
      }
    }

    // Remove duplicates and empty strings
    return [...new Set(entities)].filter(entity => entity.length > 1);
  }

  /**
   * Check if query is code-related
   */
  private isCodeRelatedQuery(query: string): boolean {
    const codeKeywords = [
      'function', 'class', 'method', 'variable', 'algorithm', 'code', 'programming',
      'debug', 'error', 'exception', 'api', 'database', 'sql', 'javascript',
      'python', 'typescript', 'react', 'node', 'npm', 'git', 'repository'
    ];

    const queryLower = query.toLowerCase();
    return codeKeywords.some(keyword => queryLower.includes(keyword));
  }

  /**
   * Check if content contains code
   */
  private hasCodeContent(content: string): boolean {
    // Look for code indicators
    const codeIndicators = [
      /```[\s\S]*?```/g, // Code blocks
      /`[^`]+`/g, // Inline code
      /\b(function|class|const|let|var|import|export)\b/g, // JS keywords
      /\b(def|class|import|from)\b/g, // Python keywords
      /[{}();]/g, // Programming punctuation
      /\b\w+\(\)/g, // Function calls
      /\b[a-zA-Z_]\w*\.[a-zA-Z_]\w*/g // Object property access
    ];

    return codeIndicators.some(pattern => pattern.test(content));
  }

  /**
   * Check if query is error-related
   */
  private isErrorRelatedQuery(query: string): boolean {
    const errorKeywords = [
      'error', 'exception', 'bug', 'issue', 'problem', 'fail', 'crash',
      'broken', 'fix', 'resolve', 'troubleshoot', 'debug'
    ];

    const queryLower = query.toLowerCase();
    return errorKeywords.some(keyword => queryLower.includes(keyword));
  }

  /**
   * Check if content contains error information
   */
  private hasErrorContent(content: string): boolean {
    const errorIndicators = [
      /error|exception|stack trace|traceback/gi,
      /\b\d{3}\s*(error|status)/gi, // HTTP status codes
      /failed|crashed|broken/gi,
      /at line \d+/gi // Line number references
    ];

    return errorIndicators.some(pattern => pattern.test(content));
  }

  /**
   * Check if query is time-sensitive
   */
  private isTimeSensitiveQuery(query: string): boolean {
    const timeKeywords = [
      'recent', 'latest', 'new', 'current', 'today', 'yesterday',
      'this week', 'last', 'now', 'updated', 'changed'
    ];

    const queryLower = query.toLowerCase();
    return timeKeywords.some(keyword => queryLower.includes(keyword));
  }

  /**
   * Update configuration
   */
  updateConfig(updates: Partial<RelevanceScorerConfig>): void {
    this.config = { ...this.config, ...updates };
  }

  /**
   * Get current configuration
   */
  getConfig(): RelevanceScorerConfig {
    return { ...this.config };
  }

  /**
   * Clear query embedding cache
   */
  clearCache(): void {
    this.queryEmbeddingCache.clear();
  }

  /**
   * Get cache statistics
   */
  getCacheStats(): { size: number; keys: string[] } {
    return {
      size: this.queryEmbeddingCache.size,
      keys: Array.from(this.queryEmbeddingCache.keys())
    };
  }
}

/**
 * Entity-Centric Strategy - Focus on mentioned entities
 * 
 * This strategy prioritizes content that mentions specific entities,
 * making it ideal for queries about people, places, products, or concepts.
 */

import { AssemblyStrategy, StrategySelectionCriteria } from './AssemblyStrategy.js';
import { ScoredItem } from '../scoring/RelevanceScorer.js';
import { ContextAssemblyRequest } from '../ContextAssembler.js';
import { TokenBudget } from '../optimization/TokenOptimizer.js';

/**
 * Entity mention information
 */
interface EntityMention {
  entity: string;
  normalizedEntity: string;
  count: number;
  positions: number[];
  type: EntityType;
}

/**
 * Entity types for classification
 */
type EntityType = 'person' | 'organization' | 'product' | 'concept' | 'location' | 'technical' | 'unknown';

/**
 * Entity-centric assembly strategy implementation
 */
export class EntityCentricStrategy extends AssemblyStrategy {
  private entityPatterns: Map<EntityType, RegExp[]> = new Map();

  constructor() {
    super('entity-centric');
    this.initializeEntityPatterns();
  }

  /**
   * Initialize entity recognition patterns
   */
  private initializeEntityPatterns(): void {
    this.entityPatterns = new Map();

    // Person names (simple pattern)
    this.entityPatterns.set('person', [
      /\b[A-Z][a-z]+ [A-Z][a-z]+\b/g, // First Last
      /\b[A-Z][a-z]+ [A-Z]\. [A-Z][a-z]+\b/g, // First M. Last
      /\b(Mr|Ms|Mrs|Dr|Prof)\. [A-Z][a-z]+\b/g // Title Name
    ]);

    // Organizations
    this.entityPatterns.set('organization', [
      /\b[A-Z][A-Z0-9&]+(?:\s+[A-Z][A-Za-z]+)*\b/g, // IBM, AT&T, etc.
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:Inc|Corp|LLC|Ltd|Co)\b/g, // Company Inc
      /\b[A-Z][a-z]+\s+(?:University|College|Institute)\b/g // Educational institutions
    ]);

    // Products/Technologies
    this.entityPatterns.set('product', [
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+\d+(?:\.\d+)*\b/g, // Version numbers
      /\b[A-Z][a-z]+(?:[A-Z][a-z]*)*\b/g // CamelCase products
    ]);

    // Technical terms
    this.entityPatterns.set('technical', [
      /\b[a-z]+\([^)]*\)/g, // Functions
      /\b[A-Z]{2,}(?:[A-Z][a-z]*)*\b/g, // Acronyms like HTTP, API
      /\b\w+\.\w+(?:\.\w+)*\b/g, // Namespaces/domains
      /\b[a-zA-Z_]\w*::[a-zA-Z_]\w*\b/g // C++ style namespaces
    ]);

    // Locations
    this.entityPatterns.set('location', [
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*,\s*[A-Z]{2}\b/g, // City, ST
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:Street|St|Avenue|Ave|Road|Rd|Boulevard|Blvd)\b/g
    ]);

    // Concepts (capitalized multi-word terms)
    this.entityPatterns.set('concept', [
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+){1,3}\b/g // Multi-word concepts
    ]);
  }

  /**
   * Select items with entity-centric prioritization
   */
  async selectItems(
    scoredItems: ScoredItem[],
    request: ContextAssemblyRequest,
    _budget: TokenBudget
  ): Promise<ScoredItem[]> {
    // Get strategy criteria
    const criteria = this.getEntityCentricCriteria(request);

    // Filter by minimum relevance
    const relevantItems = this.filterByRelevance(scoredItems, criteria.minRelevance!);

    if (relevantItems.length === 0) {
      return [];
    }

    // Extract entities from query and focus entities
    const targetEntities = this.extractTargetEntities(request);

    if (targetEntities.length === 0) {
      // Fall back to relevance-based selection if no entities found
      return relevantItems
        .sort((a, b) => b.relevanceScore - a.relevanceScore)
        .slice(0, criteria.maxItems!);
    }

    // Score items based on entity mentions
    const entityScoredItems = this.scoreItemsByEntityMentions(
      relevantItems,
      targetEntities
    );

    // Apply diversity selection to avoid entity echo chambers
    const diverseItems = criteria.diversityFactor! > 0
      ? this.applyDiversitySelection(
          entityScoredItems,
          criteria.diversityFactor!,
          criteria.maxItems!
        )
      : entityScoredItems.slice(0, criteria.maxItems!);

    // Balance between summaries and messages
    const balanced = this.balanceTypeSelection(
      diverseItems,
      criteria.summaryMessageRatio!,
      criteria.maxItems!
    );

    // Final entity-centric ordering
    return this.applyEntityCentricOrdering(balanced, targetEntities);
  }

  /**
   * Get entity-centric selection criteria
   */
  private getEntityCentricCriteria(request: ContextAssemblyRequest): Required<StrategySelectionCriteria> {
    return {
      maxItems: this.calculateMaxItems(request),
      minRelevance: request.minRelevance || 0.25, // Moderate threshold
      diversityFactor: 0.3, // Moderate diversity to avoid entity repetition
      preferRecent: false,
      summaryMessageRatio: 0.4 // Favor messages for entity details
    };
  }

  /**
   * Calculate maximum items for entity-centric strategy
   */
  private calculateMaxItems(request: ContextAssemblyRequest): number {
    let maxItems = 18; // Base number

    // Increase for multiple focus entities
    if (request.focusEntities && request.focusEntities.length > 1) {
      maxItems += request.focusEntities.length * 2;
    }

    // Increase for entity-rich queries
    const entityCount = this.countEntitiesInQuery(request.query);
    if (entityCount > 2) {
      maxItems += entityCount;
    }

    return Math.min(maxItems, 25); // Cap for performance
  }

  /**
   * Extract target entities from request
   */
  private extractTargetEntities(request: ContextAssemblyRequest): string[] {
    const entities = new Set<string>();

    // Add focus entities
    if (request.focusEntities) {
      for (const entity of request.focusEntities) {
        entities.add(entity.toLowerCase().trim());
      }
    }

    // Extract entities from query
    const queryEntities = this.extractEntitiesFromText(request.query);
    for (const entity of queryEntities) {
      entities.add(entity.normalizedEntity);
    }

    return Array.from(entities);
  }

  /**
   * Extract entities from text with type classification
   */
  private extractEntitiesFromText(text: string): EntityMention[] {
    const entities: EntityMention[] = [];
    const processed = new Set<string>();

    for (const [entityType, patterns] of this.entityPatterns) {
      for (const pattern of patterns) {
        const matches = text.matchAll(pattern);

        for (const match of matches) {
          if (match[0] && match.index !== undefined) {
            const entity = match[0].trim();
            const normalized = entity.toLowerCase();

            if (processed.has(normalized) || entity.length < 2) {
              continue;
            }

            processed.add(normalized);

            // Count occurrences and find positions
            const allMatches = [...text.matchAll(new RegExp(this.escapeRegex(entity), 'gi'))];

            entities.push({
              entity,
              normalizedEntity: normalized,
              count: allMatches.length,
              positions: allMatches.map(m => m.index!),
              type: entityType
            });
          }
        }
      }
    }

    return entities.sort((a, b) => b.count - a.count); // Sort by frequency
  }

  /**
   * Escape special regex characters
   */
  private escapeRegex(text: string): string {
    return text.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
  }

  /**
   * Score items based on entity mentions
   */
  private scoreItemsByEntityMentions(
    items: ScoredItem[],
    targetEntities: string[]
  ): Array<ScoredItem & { entityScore: number }> {
    return items.map(item => {
      const entityScore = this.calculateEntityScore(item.content, targetEntities);
      const combinedScore = (item.relevanceScore * 0.6) + (entityScore * 0.4);

      return {
        ...item,
        entityScore,
        relevanceScore: combinedScore // Update relevance score with entity weighting
      };
    }).sort((a, b) => b.relevanceScore - a.relevanceScore);
  }

  /**
   * Calculate entity score for content
   */
  private calculateEntityScore(content: string, targetEntities: string[]): number {
    if (targetEntities.length === 0) {
      return 0;
    }

    const contentLower = content.toLowerCase();
    let totalScore = 0;
    let maxPossibleScore = 0;

    for (const entity of targetEntities) {
      maxPossibleScore += 1;

      // Exact matches get full points
      const exactMatches = (contentLower.match(new RegExp(this.escapeRegex(entity), 'g')) || []).length;
      if (exactMatches > 0) {
        totalScore += Math.min(1, exactMatches * 0.3); // Diminishing returns for multiple mentions
        continue;
      }

      // Partial matches get partial points
      const entityWords = entity.split(/\s+/);
      if (entityWords.length > 1) {
        const partialMatches = entityWords.filter(word => 
          contentLower.includes(word) && word.length > 2
        ).length;

        if (partialMatches > 0) {
          totalScore += (partialMatches / entityWords.length) * 0.7; // 70% for partial matches
        }
      }
    }

    return maxPossibleScore > 0 ? totalScore / maxPossibleScore : 0;
  }

  /**
   * Count entities in query
   */
  private countEntitiesInQuery(query: string): number {
    const entities = this.extractEntitiesFromText(query);
    return entities.length;
  }

  /**
   * Apply entity-centric ordering
   */
  private applyEntityCentricOrdering(
    items: ScoredItem[],
    targetEntities: string[]
  ): ScoredItem[] {
    // Group items by entity mentions
    const entityGroups = this.groupItemsByEntityMentions(items, targetEntities);

    const orderedItems: ScoredItem[] = [];

    // Process entity groups in order of importance
    const sortedEntityGroups = Array.from(entityGroups.entries())
      .sort((a, b) => {
        // Sort by number of items mentioning this entity
        return b[1].length - a[1].length;
      });

    const processedItems = new Set<string>();

    // Add items from entity groups
    for (const [_entity, groupItems] of sortedEntityGroups) {
      const unprocessedItems = groupItems.filter(item => 
        !processedItems.has(item.id)
      );

      // Sort items within group by relevance
      unprocessedItems.sort((a, b) => b.relevanceScore - a.relevanceScore);

      // Add up to 3 items per entity to avoid over-concentration
      const itemsToAdd = unprocessedItems.slice(0, 3);

      for (const item of itemsToAdd) {
        if (!processedItems.has(item.id)) {
          orderedItems.push(item);
          processedItems.add(item.id);
        }
      }
    }

    // Add any remaining items
    const remainingItems = items.filter(item => !processedItems.has(item.id));
    remainingItems.sort((a, b) => b.relevanceScore - a.relevanceScore);
    orderedItems.push(...remainingItems);

    return orderedItems;
  }

  /**
   * Group items by entity mentions
   */
  private groupItemsByEntityMentions(
    items: ScoredItem[],
    targetEntities: string[]
  ): Map<string, ScoredItem[]> {
    const groups = new Map<string, ScoredItem[]>();

    for (const entity of targetEntities) {
      groups.set(entity, []);
    }

    for (const item of items) {
      const contentLower = item.content.toLowerCase();

      for (const entity of targetEntities) {
        if (contentLower.includes(entity)) {
          groups.get(entity)!.push(item);
        }
      }
    }

    return groups;
  }

  /**
   * Get strategy description
   */
  getDescription(): string {
    return 'Focuses on content mentioning specific entities. Best for queries about people, places, products, or concepts.';
  }

  /**
   * Check if strategy is suitable for the request
   */
  isSuitableFor(request: ContextAssemblyRequest): boolean {
    // Has explicit focus entities
    if (request.focusEntities && request.focusEntities.length > 0) {
      return true;
    }

    // Query contains named entities
    const entityCount = this.countEntitiesInQuery(request.query);
    if (entityCount > 0) {
      return true;
    }

    // Entity-related query patterns
    const query = request.query.toLowerCase();
    const entityKeywords = [
      'who is', 'what is', 'about', 'regarding', 'concerning',
      'tell me about', 'information about', 'details about',
      'company', 'person', 'product', 'service', 'organization'
    ];

    const hasEntityKeywords = entityKeywords.some(keyword => 
      query.includes(keyword)
    );

    return hasEntityKeywords;
  }
}

/**
 * Temporal Strategy - Recent-first with time decay
 * 
 * This strategy prioritizes recent content with time-based decay,
 * making it ideal for queries about current state or recent changes.
 */

import { AssemblyStrategy, StrategySelectionCriteria } from './AssemblyStrategy.js';
import { ScoredItem } from '../scoring/RelevanceScorer.js';
import { ContextAssemblyRequest } from '../ContextAssembler.js';
import { TokenBudget } from '../optimization/TokenOptimizer.js';

/**
 * Temporal assembly strategy implementation
 */
export class TemporalStrategy extends AssemblyStrategy {
  constructor() {
    super('temporal');
  }

  /**
   * Select items with temporal prioritization
   */
  async selectItems(
    scoredItems: ScoredItem[],
    request: ContextAssemblyRequest,
_budget: TokenBudget
  ): Promise<ScoredItem[]> {
    // Get strategy criteria
    const criteria = this.getTemporalCriteria(request);

    // Filter by minimum relevance
    const relevantItems = this.filterByRelevance(scoredItems, criteria.minRelevance!);

    if (relevantItems.length === 0) {
      return [];
    }

    // Apply temporal scoring boost
    const temporallyScored = this.applyTemporalScoring(relevantItems, request);

    // Sort by combined temporal + relevance score
    temporallyScored.sort((a, b) => {
      const scoreA = (a.relevanceScore * 0.7) + (a.temporalScore * 0.3);
      const scoreB = (b.relevanceScore * 0.7) + (b.temporalScore * 0.3);
      return scoreB - scoreA;
    });

    // Apply diversity selection if needed
    const diverseItems = criteria.diversityFactor! > 0
      ? this.applyDiversitySelection(
          temporallyScored,
          criteria.diversityFactor!,
          criteria.maxItems!
        )
      : temporallyScored.slice(0, criteria.maxItems!);

    // Balance between summaries and messages
    const balanced = this.balanceTypeSelection(
      diverseItems,
      criteria.summaryMessageRatio!,
      criteria.maxItems!
    );

    // Final temporal ordering within groups
    return this.applyFinalTemporalOrdering(balanced);
  }

  /**
   * Get temporal-specific selection criteria
   */
  private getTemporalCriteria(request: ContextAssemblyRequest): Required<StrategySelectionCriteria> {
    // const baseCriteria = this.getDefaultCriteria();

    return {
      maxItems: this.calculateMaxItems(request),
      minRelevance: request.minRelevance || 0.2, // Lower threshold for temporal strategy
      diversityFactor: 0.2, // Lower diversity to focus on recent items
      preferRecent: true,
      summaryMessageRatio: 0.3 // Favor messages for temporal strategy
    };
  }

  /**
   * Calculate maximum items based on time window and request
   */
  private calculateMaxItems(request: ContextAssemblyRequest): number {
    let maxItems = 15; // Base number

    // Increase for longer time windows
    if (request.timeWindow && request.timeWindow > 7 * 24 * 60 * 60 * 1000) {
      maxItems = 25;
    }

    // Increase for conversation-specific requests
    if (request.conversationId) {
      maxItems += 5;
    }

    // Increase for recent-focused queries
    if (request.includeRecent) {
      maxItems += 3;
    }

    return maxItems;
  }

  /**
   * Apply temporal scoring to items
   */
  private applyTemporalScoring(
    items: ScoredItem[],
    request: ContextAssemblyRequest
  ): Array<ScoredItem & { temporalScore: number }> {
    const now = Date.now();
    const timeWindow = request.timeWindow || (7 * 24 * 60 * 60 * 1000); // 7 days default

    return items.map(item => {
      const age = now - item.createdAt;
      const temporalScore = this.calculateTemporalScore(age, timeWindow);

      return {
        ...item,
        temporalScore
      };
    });
  }

  /**
   * Calculate temporal score based on age and time window
   */
  private calculateTemporalScore(age: number, timeWindow: number): number {
    if (age < 0) {
      return 1.0; // Future items get maximum score
    }

    // Items within the time window get full consideration
    if (age <= timeWindow) {
      // Exponential decay within the time window
      const decayRate = 0.5; // Half-life factor
      const normalizedAge = age / timeWindow;
      return Math.exp(-decayRate * normalizedAge);
    }

    // Items outside the time window get heavily penalized
    const excessAge = age - timeWindow;
    const penaltyDecayRate = 2.0; // Faster decay outside window
    const normalizedExcess = excessAge / timeWindow;
    return 0.1 * Math.exp(-penaltyDecayRate * normalizedExcess);
  }

  /**
   * Apply final temporal ordering to maintain chronological flow
   */
  private applyFinalTemporalOrdering(items: ScoredItem[]): ScoredItem[] {
    // Group by conversation
    const groupedItems = this.groupByConversation(items);

    const orderedItems: ScoredItem[] = [];

    // Process each conversation group
    for (const [_conversationId, groupItems] of groupedItems) {
      // Sort group items by timestamp (newest first for temporal strategy)
      const temporallySorted = groupItems.sort((a, b) => b.createdAt - a.createdAt);

      // For temporal strategy, interleave items to maintain flow
      orderedItems.push(...this.interleaveTemporalItems(temporallySorted));
    }

    // Final sort by relevance score to ensure best items are first
    return orderedItems.sort((a, b) => b.relevanceScore - a.relevanceScore);
  }

  /**
   * Interleave items to maintain temporal flow while preserving relevance
   */
  private interleaveTemporalItems(items: ScoredItem[]): ScoredItem[] {
    if (items.length <= 3) {
      return items; // No need to interleave small groups
    }

    const summaries = items.filter(item => item.type === 'summary');
    const messages = items.filter(item => item.type === 'message');

    // Start with highest relevance summary if available
    const interleaved: ScoredItem[] = [];

    if (summaries.length > 0) {
      interleaved.push(summaries[0]);
    }

    // Add recent messages
    const recentMessages = messages.slice(0, 3);
    interleaved.push(...recentMessages);

    // Add remaining summaries
    if (summaries.length > 1) {
      interleaved.push(...summaries.slice(1));
    }

    // Add remaining messages
    if (messages.length > 3) {
      interleaved.push(...messages.slice(3));
    }

    return interleaved;
  }

  /**
   * Get strategy description
   */
  getDescription(): string {
    return 'Prioritizes recent content with time-based decay. Best for queries about current state or recent changes.';
  }

  /**
   * Check if strategy is suitable for the request
   */
  isSuitableFor(request: ContextAssemblyRequest): boolean {
    const query = request.query.toLowerCase();

    // Temporal indicators in query
    const temporalKeywords = [
      'recent', 'latest', 'current', 'now', 'today', 'yesterday',
      'this week', 'last', 'new', 'updated', 'changed', 'just'
    ];

    const hasTemporalKeywords = temporalKeywords.some(keyword => 
      query.includes(keyword)
    );

    // Time-sensitive requests
    const isTimeSensitive = request.includeRecent || 
                           (request.timeWindow && request.timeWindow < 7 * 24 * 60 * 60 * 1000);

    // Conversation-specific requests often benefit from temporal ordering
    const isConversationSpecific = !!request.conversationId;

    return hasTemporalKeywords || isTimeSensitive || isConversationSpecific;
  }
}

/**
 * Summary Validator - Quality validation and scoring for conversation summaries
 * 
 * This module provides comprehensive validation of generated summaries to ensure
 * they meet quality standards, preserve important information, and maintain
 * consistency with the original conversation.
 */

import { Message, ConversationSummary } from '../../types/interfaces.js';
import { TokenCounter } from '../TokenCounter.js';

/**
 * Validation result with detailed scoring
 */
export interface ValidationResult {
  /** Overall quality score (0-1) */
  score: number;
  /** Individual metric scores */
  metrics: {
    /** Information coverage score (0-1) */
    informationCoverage: number;
    /** Entity preservation score (0-1) */
    entityPreservation: number;
    /** Consistency score (0-1) */
    consistency: number;
    /** Token count compliance (0-1) */
    tokenCompliance: number;
    /** Factual accuracy (0-1) */
    factualAccuracy: number;
  };
  /** Validation warnings */
  warnings: string[];
  /** Validation errors */
  errors: string[];
  /** Additional metadata */
  metadata: {
    /** Total validation time in ms */
    validationTime: number;
    /** Detected entities */
    detectedEntities: EntityAnalysis;
    /** Content analysis */
    contentAnalysis: ContentAnalysis;
  };
}

/**
 * Entity analysis results
 */
export interface EntityAnalysis {
  /** People mentioned */
  people: string[];
  /** Dates mentioned */
  dates: string[];
  /** Numbers/quantities */
  numbers: string[];
  /** Technical terms */
  technicalTerms: string[];
  /** Projects/systems */
  projects: string[];
  /** Preservation rate (0-1) */
  preservationRate: number;
}

/**
 * Content analysis results
 */
export interface ContentAnalysis {
  /** Key topics identified */
  keyTopics: string[];
  /** Sentiment preservation */
  sentimentPreserved: boolean;
  /** Structure quality */
  structureQuality: number;
  /** Redundancy level */
  redundancyLevel: number;
  /** Completeness score */
  completenessScore: number;
}

/**
 * Validation configuration
 */
export interface ValidationConfig {
  /** Minimum acceptable score */
  minScore: number;
  /** Enable entity extraction */
  enableEntityExtraction: boolean;
  /** Enable content analysis */
  enableContentAnalysis: boolean;
  /** Enable factual accuracy checks */
  enableFactualChecks: boolean;
  /** Token tolerance (percentage) */
  tokenTolerance: number;
  /** Weights for different metrics */
  weights: {
    informationCoverage: number;
    entityPreservation: number;
    consistency: number;
    tokenCompliance: number;
    factualAccuracy: number;
  };
}

/**
 * Summary quality validator
 */
export class SummaryValidator {
  private readonly config: ValidationConfig;

  constructor(_tokenCounter: TokenCounter, config: Partial<ValidationConfig> = {}) {

    // Default configuration
    this.config = {
      minScore: 0.7,
      enableEntityExtraction: true,
      enableContentAnalysis: true,
      enableFactualChecks: true,
      tokenTolerance: 0.2, // 20% tolerance
      weights: {
        informationCoverage: 0.3,
        entityPreservation: 0.25,
        consistency: 0.25,
        tokenCompliance: 0.1,
        factualAccuracy: 0.1
      },
      ...config
    };
  }

  /**
   * Validate a conversation summary
   */
  async validateSummary(
    summary: ConversationSummary,
    originalMessages: Message[]
  ): Promise<ValidationResult> {
    const startTime = Date.now();
    const warnings: string[] = [];
    const errors: string[] = [];

    // Extract entities and analyze content
    const entityAnalysis = this.config.enableEntityExtraction 
      ? await this.analyzeEntities(summary, originalMessages)
      : this.createEmptyEntityAnalysis();

    const contentAnalysis = this.config.enableContentAnalysis
      ? await this.analyzeContent(summary, originalMessages)
      : this.createEmptyContentAnalysis();

    // Calculate individual metrics
    const metrics = {
      informationCoverage: await this.calculateInformationCoverage(summary, originalMessages, contentAnalysis),
      entityPreservation: this.calculateEntityPreservation(entityAnalysis),
      consistency: await this.calculateConsistency(summary, originalMessages),
      tokenCompliance: this.calculateTokenCompliance(summary),
      factualAccuracy: this.config.enableFactualChecks 
        ? await this.calculateFactualAccuracy(summary, originalMessages)
        : 1.0
    };

    // Check for errors and warnings
    this.checkValidationIssues(summary, originalMessages, metrics, warnings, errors);

    // Calculate weighted overall score
    const score = this.calculateOverallScore(metrics);

    const validationTime = Date.now() - startTime;

    return {
      score,
      metrics,
      warnings,
      errors,
      metadata: {
        validationTime,
        detectedEntities: entityAnalysis,
        contentAnalysis
      }
    };
  }

  /**
   * Analyze entities in summary vs original messages
   */
  private async analyzeEntities(
    summary: ConversationSummary,
    originalMessages: Message[]
  ): Promise<EntityAnalysis> {
    const originalText = originalMessages.map(m => m.content).join(' ');
    const summaryText = summary.summaryText;

    // Extract entities using simple regex patterns
    const entities = {
      people: this.extractPeople(originalText),
      dates: this.extractDates(originalText),
      numbers: this.extractNumbers(originalText),
      technicalTerms: this.extractTechnicalTerms(originalText),
      projects: this.extractProjects(originalText)
    };

    const summaryEntities = {
      people: this.extractPeople(summaryText),
      dates: this.extractDates(summaryText),
      numbers: this.extractNumbers(summaryText),
      technicalTerms: this.extractTechnicalTerms(summaryText),
      projects: this.extractProjects(summaryText)
    };

    // Calculate preservation rate
    const totalOriginal = Object.values(entities).reduce((sum, arr) => sum + arr.length, 0);
    const totalPreserved = Object.keys(entities).reduce((sum, key) => {
      const originalSet = new Set(entities[key as keyof typeof entities]);
      const summarySet = new Set(summaryEntities[key as keyof typeof summaryEntities]);
      const preserved = [...originalSet].filter(entity => summarySet.has(entity));
      return sum + preserved.length;
    }, 0);

    const preservationRate = totalOriginal > 0 ? totalPreserved / totalOriginal : 1.0;

    return {
      people: entities.people,
      dates: entities.dates,
      numbers: entities.numbers,
      technicalTerms: entities.technicalTerms,
      projects: entities.projects,
      preservationRate
    };
  }

  /**
   * Analyze content quality and structure
   */
  private async analyzeContent(
    summary: ConversationSummary,
    originalMessages: Message[]
  ): Promise<ContentAnalysis> {
    const originalText = originalMessages.map(m => m.content).join(' ');
    const summaryText = summary.summaryText;

    return {
      keyTopics: this.extractKeyTopics(originalText),
      sentimentPreserved: this.checkSentimentPreservation(originalText, summaryText),
      structureQuality: this.assessStructureQuality(summaryText),
      redundancyLevel: this.calculateRedundancy(summaryText),
      completenessScore: this.assessCompleteness(originalText, summaryText, summary.level)
    };
  }

  /**
   * Calculate information coverage score
   */
  private async calculateInformationCoverage(
    summary: ConversationSummary,
    originalMessages: Message[],
    contentAnalysis: ContentAnalysis
  ): Promise<number> {
    // Base score from completeness
    let score = contentAnalysis.completenessScore;

    // Adjust based on summary level expectations
    const expectedCoverage = this.getExpectedCoverage(summary.level);
    const actualCoverage = this.estimateCoverage(summary, originalMessages);

    const coverageRatio = Math.min(actualCoverage / expectedCoverage, 1.0);
    score = (score + coverageRatio) / 2;

    // Penalty for too much redundancy
    if (contentAnalysis.redundancyLevel > 0.3) {
      score *= (1 - contentAnalysis.redundancyLevel * 0.5);
    }

    return Math.max(0, Math.min(1, score));
  }

  /**
   * Calculate entity preservation score
   */
  private calculateEntityPreservation(entityAnalysis: EntityAnalysis): number {
    return entityAnalysis.preservationRate;
  }

  /**
   * Calculate consistency score
   */
  private async calculateConsistency(
    summary: ConversationSummary,
    originalMessages: Message[]
  ): Promise<number> {
    const originalText = originalMessages.map(m => m.content).join(' ');
    const summaryText = summary.summaryText;

    // Check for contradictions
    const contradictions = this.detectContradictions(originalText, summaryText);
    const contradictionPenalty = contradictions.length * 0.2;

    // Check temporal consistency
    const temporalConsistency = this.checkTemporalConsistency(originalMessages, summaryText);

    // Check factual consistency (simple keyword matching)
    const factualConsistency = this.checkFactualConsistency(originalText, summaryText);

    const score = Math.max(0, 1 - contradictionPenalty) * temporalConsistency * factualConsistency;
    return Math.max(0, Math.min(1, score));
  }

  /**
   * Calculate token compliance score
   */
  private calculateTokenCompliance(summary: ConversationSummary): number {
    const targetTokens = this.getTargetTokenCount(summary.level);
    const actualTokens = summary.tokenCount;
    const tolerance = targetTokens * this.config.tokenTolerance;

    const minTokens = targetTokens - tolerance;
    const maxTokens = targetTokens + tolerance;

    if (actualTokens >= minTokens && actualTokens <= maxTokens) {
      return 1.0;
    } else if (actualTokens < minTokens) {
      // Penalty for being too short
      const shortfall = minTokens - actualTokens;
      return Math.max(0, 1 - (shortfall / targetTokens));
    } else {
      // Penalty for being too long
      const excess = actualTokens - maxTokens;
      return Math.max(0, 1 - (excess / targetTokens));
    }
  }

  /**
   * Calculate factual accuracy score
   */
  private async calculateFactualAccuracy(
    summary: ConversationSummary,
    originalMessages: Message[]
  ): Promise<number> {
    const originalText = originalMessages.map(m => m.content).join(' ');
    const summaryText = summary.summaryText;

    // Check for hallucinations (content not in original)
    const hallucinationScore = this.detectHallucinations(originalText, summaryText);

    // Check factual statements
    const factualScore = this.verifyFactualStatements(originalText, summaryText);

    return (hallucinationScore + factualScore) / 2;
  }

  /**
   * Calculate overall weighted score
   */
  private calculateOverallScore(metrics: ValidationResult['metrics']): number {
    const weights = this.config.weights;

    return (
      metrics.informationCoverage * weights.informationCoverage +
      metrics.entityPreservation * weights.entityPreservation +
      metrics.consistency * weights.consistency +
      metrics.tokenCompliance * weights.tokenCompliance +
      metrics.factualAccuracy * weights.factualAccuracy
    );
  }

  /**
   * Check for validation warnings and errors
   */
  private checkValidationIssues(
    summary: ConversationSummary,
    originalMessages: Message[],
    metrics: ValidationResult['metrics'],
    warnings: string[],
    errors: string[]
  ): void {
    // Token count issues
    const targetTokens = this.getTargetTokenCount(summary.level);
    if (summary.tokenCount < targetTokens * 0.5) {
      warnings.push(`Summary is significantly shorter than expected (${summary.tokenCount} vs ~${targetTokens} tokens)`);
    } else if (summary.tokenCount > targetTokens * 1.5) {
      warnings.push(`Summary is significantly longer than expected (${summary.tokenCount} vs ~${targetTokens} tokens)`);
    }

    // Quality issues
    if (metrics.informationCoverage < 0.6) {
      warnings.push('Low information coverage - summary may be missing important content');
    }

    if (metrics.entityPreservation < 0.7) {
      warnings.push('Poor entity preservation - important names, dates, or numbers may be missing');
    }

    if (metrics.consistency < 0.7) {
      errors.push('Consistency issues detected - summary may contradict original content');
    }

    if (metrics.factualAccuracy < 0.8) {
      errors.push('Factual accuracy concerns - summary may contain hallucinated information');
    }

    // Content length check
    if (originalMessages.length === 0) {
      errors.push('No original messages provided for validation');
    }

    if (summary.summaryText.trim().length === 0) {
      errors.push('Summary is empty');
    }
  }

  // Entity extraction methods
  private extractPeople(text: string): string[] {
    // Simple regex for names (capitalized words, common patterns)
    const namePattern = /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\b/g;
    const matches = text.match(namePattern) || [];

    // Filter out common false positives
    const commonWords = new Set(['The', 'This', 'That', 'Then', 'When', 'Where', 'What', 'Why', 'How']);
    return [...new Set(matches.filter(match => !commonWords.has(match)))];
  }

  private extractDates(text: string): string[] {
    const datePatterns = [
      /\b\d{1,2}\/\d{1,2}\/\d{2,4}\b/g,           // MM/DD/YYYY
      /\b\d{1,2}-\d{1,2}-\d{2,4}\b/g,            // MM-DD-YYYY
      /\b\d{4}-\d{1,2}-\d{1,2}\b/g,              // YYYY-MM-DD
      /\b(?:Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)[a-z]*\s+\d{1,2},?\s+\d{4}\b/gi,
      /\b\d{1,2}\s+(?:Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)[a-z]*\s+\d{4}\b/gi
    ];

    const dates: string[] = [];
    datePatterns.forEach(pattern => {
      const matches = text.match(pattern) || [];
      dates.push(...matches);
    });

    return [...new Set(dates)];
  }

  private extractNumbers(text: string): string[] {
    const numberPattern = /\b\d+(?:\.\d+)?(?:\s*%|\s*percent|k|K|M|B)?\b/g;
    const matches = text.match(numberPattern) || [];
    return [...new Set(matches)];
  }

  private extractTechnicalTerms(text: string): string[] {
    // Common technical patterns
    const patterns = [
      /\b[a-zA-Z]+\.[a-zA-Z]+\b/g,                // file.extension
      /\b[A-Z][a-zA-Z]*[A-Z][a-zA-Z]*\b/g,       // CamelCase
      /\b[a-z]+_[a-z_]+\b/g,                     // snake_case
      /\b[A-Z]{2,}\b/g,                          // ACRONYMS
      /\b(?:API|SDK|URL|HTTP|JSON|XML|SQL|HTML|CSS|JS)\b/gi
    ];

    const terms: string[] = [];
    patterns.forEach(pattern => {
      const matches = text.match(pattern) || [];
      terms.push(...matches);
    });

    return [...new Set(terms)];
  }

  private extractProjects(text: string): string[] {
    // Look for project-like patterns
    const projectPattern = /\b[A-Z][a-zA-Z]*(?:\s+[A-Z][a-zA-Z]*)*(?:\s+(?:Project|System|App|Service|Tool|Platform))\b/g;
    const matches = text.match(projectPattern) || [];
    return [...new Set(matches)];
  }

  private extractKeyTopics(text: string): string[] {
    // Simple topic extraction using frequency analysis
    const words = text.toLowerCase().match(/\b[a-z]{3,}\b/g) || [];
    const frequency: Record<string, number> = {};

    words.forEach(word => {
      if (!this.isStopWord(word)) {
        frequency[word] = (frequency[word] || 0) + 1;
      }
    });

    return Object.entries(frequency)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 10)
      .map(([word]) => word);
  }

  private isStopWord(word: string): boolean {
    const stopWords = new Set([
      'the', 'a', 'an', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with',
      'by', 'from', 'as', 'is', 'was', 'are', 'were', 'be', 'been', 'have', 'has', 'had',
      'do', 'does', 'did', 'will', 'would', 'could', 'should', 'may', 'might', 'can',
      'this', 'that', 'these', 'those', 'i', 'you', 'he', 'she', 'it', 'we', 'they'
    ]);
    return stopWords.has(word);
  }

  // Analysis methods
  private checkSentimentPreservation(original: string, summary: string): boolean {
    // Simple sentiment check based on positive/negative word counts
    const getScore = (text: string) => {
      const positive = (text.match(/\b(good|great|excellent|positive|success|achieve|complete)\b/gi) || []).length;
      const negative = (text.match(/\b(bad|terrible|negative|fail|problem|error|issue)\b/gi) || []).length;
      return positive - negative;
    };

    const originalScore = getScore(original);
    const summaryScore = getScore(summary);

    // Allow some tolerance
    return Math.abs(originalScore - summaryScore) <= Math.max(1, Math.abs(originalScore) * 0.5);
  }

  private assessStructureQuality(text: string): number {
    let score = 0.5; // Base score

    // Check for paragraph structure
    const paragraphs = text.split('\n\n').filter(p => p.trim().length > 0);
    if (paragraphs.length > 1) score += 0.2;

    // Check for logical flow indicators
    const flowIndicators = /\b(first|second|then|next|finally|however|therefore|additionally|furthermore)\b/gi;
    const flowMatches = text.match(flowIndicators) || [];
    score += Math.min(0.2, flowMatches.length * 0.05);

    // Check for bullet points or numbered lists
    if (/^\s*[-*]\s+/m.test(text) || /^\s*\d+\.\s+/m.test(text)) {
      score += 0.1;
    }

    return Math.min(1, score);
  }

  private calculateRedundancy(text: string): number {
    const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);
    if (sentences.length < 2) return 0;

    let redundantPairs = 0;
    for (let i = 0; i < sentences.length; i++) {
      for (let j = i + 1; j < sentences.length; j++) {
        const similarity = this.calculateSimilarity(sentences[i], sentences[j]);
        if (similarity > 0.7) redundantPairs++;
      }
    }

    return redundantPairs / (sentences.length * (sentences.length - 1) / 2);
  }

  private calculateSimilarity(text1: string, text2: string): number {
    const words1 = new Set(text1.toLowerCase().match(/\b\w+\b/g) || []);
    const words2 = new Set(text2.toLowerCase().match(/\b\w+\b/g) || []);

    const intersection = new Set([...words1].filter(word => words2.has(word)));
    const union = new Set([...words1, ...words2]);

    return intersection.size / union.size;
  }

  private assessCompleteness(original: string, summary: string, level: 'brief' | 'standard' | 'detailed'): number {
    const originalWords = new Set(original.toLowerCase().match(/\b\w+\b/g) || []);
    const summaryWords = new Set(summary.toLowerCase().match(/\b\w+\b/g) || []);

    const coverage = [...originalWords].filter(word => summaryWords.has(word)).length / originalWords.size;

    // Adjust expectations based on summary level
    const expectedCoverage = {
      brief: 0.2,
      standard: 0.4,
      detailed: 0.6
    };

    return Math.min(1, coverage / expectedCoverage[level]);
  }

  // Validation helper methods
  private detectContradictions(_original: string, _summary: string): string[] {
    // Simple contradiction detection - this would need more sophisticated NLP in practice
    const contradictions: string[] = [];

    // This is a simplified approach - real implementation would need semantic analysis
    // For now, return empty array as placeholder
    return contradictions;
  }

  private checkTemporalConsistency(_messages: Message[], summary: string): number {
    // Look for temporal indicators in summary
    const temporalWords = summary.match(/\b(first|then|next|later|finally|before|after)\b/gi) || [];

    // If no temporal words, assume consistency
    if (temporalWords.length === 0) return 1.0;

    // More sophisticated temporal analysis would be needed for a complete implementation
    return 0.9; // Placeholder
  }

  private checkFactualConsistency(original: string, summary: string): number {
    const originalFacts = this.extractFactualStatements(original);
    const summaryFacts = this.extractFactualStatements(summary);

    if (summaryFacts.length === 0) return 1.0;

    const consistentFacts = summaryFacts.filter(fact => 
      originalFacts.some(originalFact => this.factsAreConsistent(fact, originalFact))
    );

    return consistentFacts.length / summaryFacts.length;
  }

  private extractFactualStatements(text: string): string[] {
    // Extract statements that look like facts
    const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);
    return sentences.filter(sentence => {
      // Look for factual indicators
      return /\b(is|was|has|have|will|can|did|does|are|were)\b/i.test(sentence);
    });
  }

  private factsAreConsistent(fact1: string, fact2: string): boolean {
    // Simple consistency check based on word overlap
    return this.calculateSimilarity(fact1, fact2) > 0.3;
  }

  private detectHallucinations(original: string, summary: string): number {
    const originalWords = new Set(original.toLowerCase().match(/\b\w+\b/g) || []);
    const summaryWords = summary.toLowerCase().match(/\b\w+\b/g) || [];

    const uniqueSummaryWords = summaryWords.filter(word => !originalWords.has(word));
    const hallucinationRate = uniqueSummaryWords.length / summaryWords.length;

    // Allow some hallucination for natural language generation
    return Math.max(0, 1 - Math.max(0, hallucinationRate - 0.3) * 2);
  }

  private verifyFactualStatements(_original: string, _summary: string): number {
    // Placeholder for factual verification
    return 0.9;
  }

  // Helper methods
  private getExpectedCoverage(level: 'brief' | 'standard' | 'detailed'): number {
    switch (level) {
      case 'brief': return 0.3;
      case 'standard': return 0.6;
      case 'detailed': return 0.8;
      default: return 0.6;
    }
  }

  private estimateCoverage(summary: ConversationSummary, messages: Message[]): number {
    const originalText = messages.map(m => m.content).join(' ');
    const originalWords = new Set(originalText.toLowerCase().match(/\b\w+\b/g) || []);
    const summaryWords = new Set(summary.summaryText.toLowerCase().match(/\b\w+\b/g) || []);

    const coveredWords = [...originalWords].filter(word => summaryWords.has(word));
    return coveredWords.length / originalWords.size;
  }

  private getTargetTokenCount(level: 'brief' | 'standard' | 'detailed'): number {
    switch (level) {
      case 'brief': return 75;
      case 'standard': return 250;
      case 'detailed': return 750;
      default: return 250;
    }
  }

  private createEmptyEntityAnalysis(): EntityAnalysis {
    return {
      people: [],
      dates: [],
      numbers: [],
      technicalTerms: [],
      projects: [],
      preservationRate: 1.0
    };
  }

  private createEmptyContentAnalysis(): ContentAnalysis {
    return {
      keyTopics: [],
      sentimentPreserved: true,
      structureQuality: 1.0,
      redundancyLevel: 0,
      completenessScore: 1.0
    };
  }
}

/**
 * Entity Extraction Service for Phase 3
 * 
 * Extracts entities from message text using pattern matching.
 * Integrates with EntityRepository for storage and management.
 */

import { EntityRepository, EntityType, Entity } from '../storage/repositories/EntityRepository.js';
import { DatabaseManager } from '../storage/Database.js';

export interface ExtractedEntity {
  text: string;
  normalizedText: string;
  type: EntityType;
  confidence: number;
  startPosition: number;
  endPosition: number;
  context?: string;
}

export interface EntityMention {
  entityId: string;
  messageId: string;
  conversationId: string;
  mentionText: string;
  startPosition: number;
  endPosition: number;
  confidence: number;
}

export interface ExtractionConfig {
  minConfidence: number;
  maxEntitiesPerMessage: number;
  enableContextCapture: boolean;
  contextWindowSize: number;
}

export class EntityExtractionService {
  private entityRepository: EntityRepository;
  private dbManager: DatabaseManager;
  private config: ExtractionConfig;
  private entityPatterns: Map<EntityType, RegExp[]> = new Map();
  private stopWords: Set<string> = new Set();

  constructor(dbManager: DatabaseManager, config: Partial<ExtractionConfig> = {}) {
    this.dbManager = dbManager;
    this.entityRepository = new EntityRepository(dbManager);
    this.config = {
      minConfidence: 0.6,
      maxEntitiesPerMessage: 20,
      enableContextCapture: true,
      contextWindowSize: 50,
      ...config
    };

    this.initializePatterns();
    this.initializeStopWords();
  }

  /**
   * Extract and process entities from message text
   */
  async processMessage(
    messageId: string,
    conversationId: string,
    content: string
  ): Promise<{
    extractedEntities: ExtractedEntity[];
    createdEntities: Entity[];
    mentions: EntityMention[];
  }> {
    // Extract entities using pattern matching
    const extractedEntities = this.extractEntitiesFromText(content);

    const createdEntities: Entity[] = [];
    const mentions: EntityMention[] = [];

    // Process each extracted entity
    for (const extracted of extractedEntities) {
      try {
        // Check if entity already exists
        let entity = await this.entityRepository.findByNormalizedName(
          extracted.normalizedText,
          extracted.type
        );

        // Create new entity if not found
        if (!entity) {
          entity = await this.entityRepository.create({
            name: extracted.text,
            type: extracted.type,
            confidenceScore: extracted.confidence,
            metadata: {
              firstSeenIn: conversationId,
              extractionMethod: 'pattern'
            }
          });
          createdEntities.push(entity);
        }

        // Create mention record
        const mention: EntityMention = {
          entityId: entity.id,
          messageId,
          conversationId,
          mentionText: extracted.text,
          startPosition: extracted.startPosition,
          endPosition: extracted.endPosition,
          confidence: extracted.confidence
        };

        // Store mention in database
        await this.storeMention(mention);
        mentions.push(mention);

      } catch (error) {
        console.warn(`Failed to process entity "${extracted.text}":`, error);
      }
    }

    return {
      extractedEntities,
      createdEntities,
      mentions
    };
  }

  /**
   * Extract entities from text using pattern matching
   */
  private extractEntitiesFromText(text: string): ExtractedEntity[] {
    const entities: ExtractedEntity[] = [];
    const processedText = new Set<string>();
    const occupiedPositions: Array<{ start: number; end: number }> = [];

    // Process each entity type in order (more specific first)
    for (const [entityType, patterns] of this.entityPatterns.entries()) {
      for (const pattern of patterns) {
        const matches = this.findMatches(text, pattern);

        for (const match of matches) {
          const normalized = this.normalizeEntityText(match.text);

          // Skip if already processed, too short, or is a stop word
          if (processedText.has(normalized) || 
              normalized.length < 2 || 
              this.stopWords.has(normalized.toLowerCase())) {
            continue;
          }

          // Skip if this position overlaps with an already-matched entity
          const overlaps = occupiedPositions.some(pos => 
            (match.startPosition >= pos.start && match.startPosition < pos.end) ||
            (match.endPosition > pos.start && match.endPosition <= pos.end) ||
            (match.startPosition <= pos.start && match.endPosition >= pos.end)
          );

          if (overlaps) {
            continue;
          }

          processedText.add(normalized);

          const confidence = this.calculateConfidence(match.text, entityType);

          // Only include entities above confidence threshold
          if (confidence >= this.config.minConfidence) {
            const entity: ExtractedEntity = {
              text: match.text,
              normalizedText: normalized,
              type: entityType,
              confidence,
              startPosition: match.startPosition,
              endPosition: match.endPosition
            };

            // Add context if enabled
            if (this.config.enableContextCapture) {
              entity.context = this.extractContext(text, match.startPosition, match.endPosition);
            }

            entities.push(entity);

            // Mark this position as occupied
            occupiedPositions.push({
              start: match.startPosition,
              end: match.endPosition
            });
          }
        }
      }
    }

    // Sort by confidence and position, limit results
    return entities
      .sort((a, b) => {
        if (b.confidence !== a.confidence) {
          return b.confidence - a.confidence;
        }
        return a.startPosition - b.startPosition;
      })
      .slice(0, this.config.maxEntitiesPerMessage);
  }

  /**
   * Store entity mention in database
   */
  private async storeMention(mention: EntityMention): Promise<void> {
    const db = this.dbManager.getConnection();

    // Use raw SQL for now since we don't have EntityMentionRepository
    const stmt = db.prepare(`
      INSERT OR IGNORE INTO entity_mentions (
        id, entity_id, message_id, conversation_id,
        mention_text, start_position, end_position,
        confidence_score, extraction_method, created_at
      ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
    `);

    const id = this.generateId();
    const now = Date.now();

    stmt.run(
      id,
      mention.entityId,
      mention.messageId,
      mention.conversationId,
      mention.mentionText,
      mention.startPosition,
      mention.endPosition,
      mention.confidence,
      'pattern',
      now
    );
  }

  /**
   * Initialize entity recognition patterns
   * Order matters! More specific patterns should come before general ones.
   */
  private initializePatterns(): void {
    // Technical terms (process first to catch specific technical terms)
    this.entityPatterns.set('technical', [
      // Specific technical terms (case-sensitive)
      /\b(?:JavaScript|TypeScript|Python|Java|React|Node\.js|Docker|Kubernetes|AWS|Azure|SQL|GraphQL|REST|API|Git|GitHub)\b/g,
      // Technical concepts (case-insensitive)
      /\b(?:Machine Learning|Artificial Intelligence|Data Science|Cloud Computing|DevOps|Microservices|Database|Backend|Frontend)\b/gi,
      // Additional patterns for compound terms
      /\b(?:frontend|backend|fullstack|full-stack)\b/gi
    ]);

    // Organizations (process before general person names)
    this.entityPatterns.set('organization', [
      // Company names with suffixes
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:Inc|Corp|Corporation|LLC|Ltd|Limited|Co|Company|Group)\b/g,
      // Universities and schools
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:University|College|Institute|School)\b/g
    ]);

    // Locations (process before general patterns)
    this.entityPatterns.set('location', [
      // City, State/Country
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*,\s*[A-Z]{2,3}\b/g,
      // Well-known places
      /\b(?:United States|United Kingdom|Canada|Australia|Germany|France|Japan|China|California|New York|Texas|Florida|London|Paris|Tokyo)\b/g
    ]);

    // Events (process before general patterns)
    this.entityPatterns.set('event', [
      // Meeting types
      /\b(?:meeting|conference|workshop|seminar|webinar|standup|retrospective|sprint planning|demo|review)\b/gi,
      // Named events
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:Conference|Summit|Expo|Convention)\b/g
    ]);

    // Products and technologies
    this.entityPatterns.set('product', [
      // CamelCase product names (but exclude already matched technical terms)
      /\b[A-Z][a-z]*[A-Z][A-Za-z]*\b/g,
      // Quoted product names
      /"([A-Z][^"]{2,30})"/g
    ]);

    // Concepts (specific business/technical concepts)
    this.entityPatterns.set('concept', [
      // Business and technical terms (case-insensitive)
      /\b(?:Agile|Scrum|Kanban|Design Thinking|User Experience|Digital Transformation|Business Intelligence)\b/gi
    ]);

    // Person names (process last to avoid false positives)
    this.entityPatterns.set('person', [
      // Names with titles (high confidence)
      /\b(?:Mr|Ms|Mrs|Dr|Prof|President|CEO|CTO|Director|Manager)\.?\s+[A-Z][a-z]+(?:\s+[A-Z][a-z]+)?\b/g,
      // Full names (but avoid common technical/business terms)
      /\b[A-Z][a-z]+ [A-Z][a-z]+\b/g
    ]);

    // Decisions
    this.entityPatterns.set('decision', [
      // Decision language patterns
      /\b(?:decided|decision|resolved|agreed|approved)\s+(?:to|that|on)\s+([^.!?]{5,50})/gi,
      // Action items
      /\b(?:action item|todo|task):\s*([^.!?\n]{5,50})/gi
    ]);
  }

  /**
   * Initialize stop words
   */
  private initializeStopWords(): void {
    this.stopWords = new Set([
      'the', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with',
      'by', 'from', 'up', 'about', 'into', 'this', 'that', 'these', 'those',
      'i', 'you', 'he', 'she', 'it', 'we', 'they', 'me', 'him', 'her', 'us',
      'them', 'my', 'your', 'his', 'her', 'its', 'our', 'their', 'a', 'an',
      'is', 'am', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has',
      'had', 'do', 'does', 'did', 'will', 'would', 'could', 'should', 'can',
      'when', 'where', 'why', 'how', 'what', 'which', 'who', 'very', 'more',
      'most', 'good', 'bad', 'new', 'old', 'big', 'small'
    ]);
  }

  /**
   * Find pattern matches in text
   */
  private findMatches(text: string, pattern: RegExp): Array<{
    text: string;
    startPosition: number;
    endPosition: number;
  }> {
    const matches: Array<{ text: string; startPosition: number; endPosition: number }> = [];
    let match;

    // Create a new regex to avoid modifying the original
    const regex = new RegExp(pattern.source, pattern.flags);

    while ((match = regex.exec(text)) !== null) {
      const matchText = match[1] || match[0]; // Use capture group if available

      if (matchText && matchText.trim().length > 0) {
        matches.push({
          text: matchText.trim(),
          startPosition: match.index!,
          endPosition: match.index! + matchText.length
        });
      }

      // Prevent infinite loops on global regexes
      if (!pattern.global) break;
    }

    return matches;
  }

  /**
   * Calculate confidence score for an entity
   */
  private calculateConfidence(text: string, type: EntityType): number {
    let confidence = 0.5; // Base confidence

    // Length-based adjustments
    if (text.length >= 3 && text.length <= 50) {
      confidence += 0.1;
    }
    if (text.length >= 5 && text.length <= 30) {
      confidence += 0.1;
    }

    // Type-specific adjustments
    switch (type) {
      case 'person':
        if (/^(?:Mr|Ms|Mrs|Dr|Prof|President|CEO|CTO)\.?\s+/.test(text)) {
          confidence += 0.2;
        }
        if (/^[A-Z][a-z]+\s+[A-Z][a-z]+$/.test(text)) {
          confidence += 0.15;
        }
        break;

      case 'organization':
        if (/(?:Inc|Corp|LLC|Ltd|Company)$/i.test(text)) {
          confidence += 0.2;
        }
        break;

      case 'technical':
        // Well-known technical terms get higher confidence
        const wellKnownTech = [
          'JavaScript', 'TypeScript', 'Python', 'React', 'Node.js',
          'Machine Learning', 'Artificial Intelligence', 'Database'
        ];
        if (wellKnownTech.some(term => text.toLowerCase().includes(term.toLowerCase()))) {
          confidence += 0.2;
        }
        break;
    }

    return Math.min(1.0, confidence);
  }

  /**
   * Extract context around entity mention
   */
  private extractContext(text: string, startPos: number, endPos: number): string {
    const contextStart = Math.max(0, startPos - this.config.contextWindowSize);
    const contextEnd = Math.min(text.length, endPos + this.config.contextWindowSize);
    return text.substring(contextStart, contextEnd).trim();
  }

  /**
   * Normalize entity text for consistent matching
   */
  private normalizeEntityText(text: string): string {
    return text
      .toLowerCase()
      .trim()
      .replace(/[^\w\s-]/g, '')
      .replace(/\s+/g, ' ');
  }

  /**
   * Generate UUID for mentions
   */
  private generateId(): string {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
      const r = Math.random() * 16 | 0;
      const v = c === 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  }

  /**
   * Batch process multiple messages
   */
  async processMessages(messages: Array<{
    id: string;
    conversationId: string;
    content: string;
  }>): Promise<{
    totalEntitiesExtracted: number;
    totalEntitiesCreated: number;
    totalMentions: number;
    processingStats: Map<string, number>;
  }> {
    let totalExtracted = 0;
    let totalCreated = 0;
    let totalMentions = 0;
    const processingStats = new Map<string, number>();

    for (const message of messages) {
      try {
        const result = await this.processMessage(
          message.id,
          message.conversationId,
          message.content
        );

        totalExtracted += result.extractedEntities.length;
        totalCreated += result.createdEntities.length;
        totalMentions += result.mentions.length;

        // Track stats by entity type
        for (const entity of result.extractedEntities) {
          const count = processingStats.get(entity.type) || 0;
          processingStats.set(entity.type, count + 1);
        }

      } catch (error) {
        console.warn(`Failed to process message ${message.id}:`, error);
      }
    }

    return {
      totalEntitiesExtracted: totalExtracted,
      totalEntitiesCreated: totalCreated,
      totalMentions,
      processingStats
    };
  }
}

/**
 * Entity Linking Service
 * 
 * Handles fuzzy matching, alias resolution, and entity consolidation.
 * Links entity variations across conversations (e.g., "John", "John Doe", "JD").
 */

import { EntityRepository, Entity, EntityType } from '../storage/repositories/EntityRepository.js';
import { DatabaseManager } from '../storage/Database.js';

export interface EntityAlias {
  id: string;
  entityId: string;
  alias: string;
  aliasType: AliasType;
  confidenceScore: number;
  createdAt: number;
}

export type AliasType = 'formal' | 'informal' | 'abbreviation' | 'nickname' | 'variation';

export interface LinkingCandidate {
  entity: Entity;
  similarity: number;
  matchType: 'exact' | 'fuzzy' | 'semantic' | 'alias' | 'pattern';
  explanation: string;
}

export interface LinkingConfig {
  fuzzyThreshold: number;
  enableAliasGeneration: boolean;
  enableSemanticMatching: boolean;
  maxCandidates: number;
}

export class EntityLinker {
  private entityRepository: EntityRepository;
  private dbManager: DatabaseManager;
  private config: LinkingConfig;

  constructor(dbManager: DatabaseManager, config: Partial<LinkingConfig> = {}) {
    this.dbManager = dbManager;
    this.entityRepository = new EntityRepository(dbManager);
    this.config = {
      fuzzyThreshold: 0.8,
      enableAliasGeneration: true,
      enableSemanticMatching: false, // Can be enabled when we have embeddings
      maxCandidates: 5,
      ...config
    };
  }

  /**
   * Find the best matching entity for a given text and type
   */
  async linkEntity(
    text: string, 
    type: EntityType, 
    context?: string
  ): Promise<{
    linkedEntity: Entity | null;
    candidates: LinkingCandidate[];
    shouldCreateNew: boolean;
    suggestedAliases: string[];
  }> {
    const normalizedText = this.normalizeText(text);

    // 1. Try exact match first
    const exactMatch = await this.entityRepository.findByNormalizedName(normalizedText, type);
    if (exactMatch) {
      return {
        linkedEntity: exactMatch,
        candidates: [{ 
          entity: exactMatch, 
          similarity: 1.0, 
          matchType: 'exact',
          explanation: 'Exact normalized name match'
        }],
        shouldCreateNew: false,
        suggestedAliases: []
      };
    }

    // 2. Check for alias matches
    const aliasMatch = await this.findByAlias(text, type);
    if (aliasMatch) {
      return {
        linkedEntity: aliasMatch,
        candidates: [{ 
          entity: aliasMatch, 
          similarity: 0.95, 
          matchType: 'alias',
          explanation: 'Matched existing alias'
        }],
        shouldCreateNew: false,
        suggestedAliases: []
      };
    }

    // 3. Find fuzzy matches
    const candidates = await this.findFuzzyCandidates(text, type, context);

    // 4. Determine best candidate
    const bestCandidate = candidates.length > 0 ? candidates[0] : null;
    const shouldLink = bestCandidate && bestCandidate.similarity >= this.config.fuzzyThreshold;

    // 5. Generate suggested aliases if we're linking
    const suggestedAliases = shouldLink && this.config.enableAliasGeneration
      ? this.generateAliases(text, bestCandidate!.entity.name, type)
      : [];

    return {
      linkedEntity: shouldLink ? bestCandidate!.entity : null,
      candidates: candidates.slice(0, this.config.maxCandidates),
      shouldCreateNew: !shouldLink,
      suggestedAliases
    };
  }

  /**
   * Create aliases for an entity
   */
  async createAlias(
    entityId: string,
    alias: string,
    aliasType: AliasType,
    confidence: number = 0.9
  ): Promise<EntityAlias> {
    const aliasRecord: EntityAlias = {
      id: this.generateId(),
      entityId,
      alias: alias.trim(),
      aliasType,
      confidenceScore: confidence,
      createdAt: Date.now()
    };

    const db = this.dbManager.getConnection();
    const stmt = db.prepare(`
      INSERT INTO entity_aliases (id, entity_id, alias, alias_type, confidence_score, created_at)
      VALUES (?, ?, ?, ?, ?, ?)
    `);

    stmt.run(
      aliasRecord.id,
      aliasRecord.entityId,
      aliasRecord.alias,
      aliasRecord.aliasType,
      aliasRecord.confidenceScore,
      aliasRecord.createdAt
    );

    return aliasRecord;
  }

  /**
   * Batch create aliases for an entity
   */
  async createAliases(entityId: string, aliases: Array<{
    alias: string;
    type: AliasType;
    confidence?: number;
  }>): Promise<EntityAlias[]> {
    const results: EntityAlias[] = [];

    for (const aliasData of aliases) {
      try {
        const alias = await this.createAlias(
          entityId,
          aliasData.alias,
          aliasData.type,
          aliasData.confidence
        );
        results.push(alias);
      } catch (error) {
        // Skip duplicates or errors
        console.warn(`Failed to create alias "${aliasData.alias}":`, error);
      }
    }

    return results;
  }

  /**
   * Merge two entities by moving all mentions to the target entity
   */
  async mergeEntities(sourceEntityId: string, targetEntityId: string): Promise<boolean> {
    const sourceEntity = await this.entityRepository.getById(sourceEntityId);
    const targetEntity = await this.entityRepository.getById(targetEntityId);

    if (!sourceEntity || !targetEntity) {
      return false;
    }

    const db = this.dbManager.getConnection();

    try {
      const transaction = db.transaction(() => {
        // Move all mentions to target entity
        db.prepare(`
          UPDATE entity_mentions 
          SET entity_id = ? 
          WHERE entity_id = ?
        `).run(targetEntityId, sourceEntityId);

        // Move all relationship references
        db.prepare(`
          UPDATE entity_relationships 
          SET source_entity_id = ? 
          WHERE source_entity_id = ?
        `).run(targetEntityId, sourceEntityId);

        db.prepare(`
          UPDATE entity_relationships 
          SET target_entity_id = ? 
          WHERE target_entity_id = ?
        `).run(targetEntityId, sourceEntityId);

        // Create alias from source to target
        const aliasId = this.generateId();
        db.prepare(`
          INSERT OR IGNORE INTO entity_aliases (id, entity_id, alias, alias_type, confidence_score, created_at)
          VALUES (?, ?, ?, ?, ?, ?)
        `).run(aliasId, targetEntityId, sourceEntity.name, 'variation', 0.9, Date.now());

        // Delete source entity
        db.prepare('DELETE FROM entities WHERE id = ?').run(sourceEntityId);
      });

      transaction();
      return true;

    } catch (error) {
      console.error('Failed to merge entities:', error);
      return false;
    }
  }

  /**
   * Get all aliases for an entity
   */
  async getEntityAliases(entityId: string): Promise<EntityAlias[]> {
    const db = this.dbManager.getConnection();
    const stmt = db.prepare(`
      SELECT * FROM entity_aliases 
      WHERE entity_id = ? 
      ORDER BY confidence_score DESC, created_at ASC
    `);

    const rows = stmt.all(entityId) as any[];
    return rows.map(row => this.mapRowToAlias(row));
  }

  /**
   * Find entity by alias
   */
  private async findByAlias(text: string, type: EntityType): Promise<Entity | null> {
    const db = this.dbManager.getConnection();
    const stmt = db.prepare(`
      SELECT e.* FROM entities e
      JOIN entity_aliases ea ON e.id = ea.entity_id
      WHERE ea.alias = ? AND e.type = ?
      ORDER BY ea.confidence_score DESC
      LIMIT 1
    `);

    const row = stmt.get(text.trim(), type) as any;
    if (!row) return null;

    return {
      id: row.id,
      name: row.name,
      normalizedName: row.normalized_name,
      type: row.type as EntityType,
      canonicalForm: row.canonical_form,
      confidenceScore: row.confidence_score,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      metadata: JSON.parse(row.metadata || '{}'),
      mentionCount: row.mention_count,
      lastMentionedAt: row.last_mentioned_at
    };
  }

  /**
   * Find fuzzy matching candidates
   */
  private async findFuzzyCandidates(
    text: string,
    type: EntityType,
    _context?: string
  ): Promise<LinkingCandidate[]> {
    const candidates: LinkingCandidate[] = [];

    // Get all entities of the same type
    const entities = await this.entityRepository.search({ 
      type, 
      limit: 50 // Reasonable limit for fuzzy matching
    });

    for (const entity of entities.entities) {
      // First check for pattern matches (abbreviations, nicknames)
      let isPatternMatch = false;
      let matchType: LinkingCandidate['matchType'] = 'fuzzy';
      let explanation = '';
      let similarity = 0;

      // Check for abbreviation pattern
      if (this.isAbbreviation(text, entity.name)) {
        isPatternMatch = true;
        matchType = 'pattern';
        explanation = 'Detected abbreviation pattern';
        similarity = 0.8; // High confidence for abbreviations
      } 
      // Check for nickname pattern
      else if (entity.type === 'person' && this.isNickname(text, entity.name, type)) {
        isPatternMatch = true;
        matchType = 'pattern';
        explanation = 'Detected nickname pattern';
        similarity = 0.75; // Good confidence for nicknames
      }

      // If no pattern match, calculate text similarity
      if (!isPatternMatch) {
        const nameSimilarity = this.calculateLevenshteinSimilarity(text, entity.name);
        const normalizedSimilarity = this.calculateLevenshteinSimilarity(
          this.normalizeText(text), 
          entity.normalizedName
        );
        similarity = Math.max(nameSimilarity, normalizedSimilarity);
        explanation = `${Math.round(similarity * 100)}% text similarity`;
      }

      // Add as candidate if pattern match OR similarity is high enough
      if (isPatternMatch || similarity > 0.5) {
        candidates.push({
          entity,
          similarity,
          matchType,
          explanation
        });
      }
    }

    // Sort by similarity descending
    return candidates.sort((a, b) => b.similarity - a.similarity);
  }

  /**
   * Generate potential aliases for an entity
   */
  private generateAliases(newText: string, existingName: string, type: EntityType): string[] {
    const aliases: string[] = [];

    // For persons, generate common nickname patterns
    if (type === 'person') {
      const parts = existingName.split(' ');
      if (parts.length >= 2) {
        // First name only
        aliases.push(parts[0]);
        // First + last initial
        aliases.push(`${parts[0]} ${parts[parts.length - 1][0]}.`);
        // Initials
        aliases.push(parts.map(p => p[0]).join('.'));
      }
    }

    // For technical terms, generate variations
    if (type === 'technical') {
      const variations = this.generateTechnicalVariations(existingName);
      aliases.push(...variations);
    }

    // For organizations, generate abbreviations
    if (type === 'organization') {
      const abbrev = this.generateOrganizationAbbreviation(existingName);
      if (abbrev) aliases.push(abbrev);
    }

    // Always add the new text as a potential alias
    if (!aliases.includes(newText) && newText !== existingName) {
      aliases.push(newText);
    }

    return aliases.filter(alias => alias.length > 1); // Filter out single characters
  }

  /**
   * Generate technical term variations
   */
  private generateTechnicalVariations(name: string): string[] {
    const variations: string[] = [];

    // Common technical variations
    const techPatterns = [
      [/JavaScript/gi, ['JS', 'javascript']],
      [/TypeScript/gi, ['TS', 'typescript']],
      [/React/gi, ['ReactJS', 'React.js']],
      [/Node\.js/gi, ['NodeJS', 'Node', 'node']],
      [/Machine Learning/gi, ['ML', 'machine learning']],
      [/Artificial Intelligence/gi, ['AI', 'artificial intelligence']]
    ];

    for (const [pattern, vars] of techPatterns) {
      if ((pattern as RegExp).test(name)) {
        variations.push(...(vars as string[]));
      }
    }

    return variations;
  }

  /**
   * Generate organization abbreviation
   */
  private generateOrganizationAbbreviation(name: string): string | null {
    // Extract capital letters from organization names
    const words = name.split(' ').filter(word => 
      !['Inc', 'Corp', 'Corporation', 'LLC', 'Ltd', 'Company', 'Group'].includes(word)
    );

    if (words.length >= 2) {
      return words.map(word => word[0]).join('').toUpperCase();
    }

    return null;
  }

  /**
   * Check if text is an abbreviation of name
   */
  private isAbbreviation(text: string, name: string): boolean {
    if (text.length >= name.length) return false;

    const nameWords = name.split(' ').filter(word => word.length > 0);
    const textUpper = text.toUpperCase().replace(/\./g, '');

    // Check if text matches first letters of words
    const initials = nameWords.map(word => word[0].toUpperCase()).join('');
    return textUpper === initials;
  }

  /**
   * Check if text is a nickname of name
   */
  private isNickname(text: string, name: string, type: EntityType): boolean {
    if (type !== 'person') return false;

    const nameParts = name.split(' ');
    const firstName = nameParts[0];

    // Common nickname patterns
    return text === firstName || 
           firstName.toLowerCase().startsWith(text.toLowerCase()) ||
           text.toLowerCase().startsWith(firstName.toLowerCase());
  }

  /**
   * Calculate Levenshtein similarity between two strings
   */
  private calculateLevenshteinSimilarity(str1: string, str2: string): number {
    const distance = this.levenshteinDistance(str1.toLowerCase(), str2.toLowerCase());
    const maxLength = Math.max(str1.length, str2.length);
    return maxLength === 0 ? 1 : 1 - (distance / maxLength);
  }

  /**
   * Calculate Levenshtein distance
   */
  private levenshteinDistance(str1: string, str2: string): number {
    const matrix: number[][] = [];

    for (let i = 0; i <= str2.length; i++) {
      matrix[i] = [i];
    }

    for (let j = 0; j <= str1.length; j++) {
      matrix[0][j] = j;
    }

    for (let i = 1; i <= str2.length; i++) {
      for (let j = 1; j <= str1.length; j++) {
        if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
          matrix[i][j] = matrix[i - 1][j - 1];
        } else {
          matrix[i][j] = Math.min(
            matrix[i - 1][j - 1] + 1, // substitution
            matrix[i][j - 1] + 1,     // insertion
            matrix[i - 1][j] + 1      // deletion
          );
        }
      }
    }

    return matrix[str2.length][str1.length];
  }

  /**
   * Normalize text for comparison
   */
  private normalizeText(text: string): string {
    return text
      .toLowerCase()
      .trim()
      .replace(/[^\w\s-]/g, '')
      .replace(/\s+/g, ' ');
  }

  /**
   * Generate UUID
   */
  private generateId(): string {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
      const r = Math.random() * 16 | 0;
      const v = c === 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  }

  /**
   * Map database row to EntityAlias
   */
  private mapRowToAlias(row: any): EntityAlias {
    return {
      id: row.id,
      entityId: row.entity_id,
      alias: row.alias,
      aliasType: row.alias_type as AliasType,
      confidenceScore: row.confidence_score,
      createdAt: row.created_at
    };
  }
}

/**
 * Knowledge Graph Service - Phase 3
 * 
 * Orchestrates entity extraction, linking, and relationship detection to build
 * a comprehensive knowledge graph from conversations. This is the main entry
 * point for all knowledge graph operations.
 */

import { DatabaseManager } from '../storage/Database.js';
import { EntityExtractionService } from './EntityExtractionService.js';
import { EntityLinker } from './EntityLinker.js';
import { RelationshipDetector } from './RelationshipDetector.js';
import { EntityRepository, Entity, EntityType } from '../storage/repositories/EntityRepository.js';

export interface KnowledgeGraphConfig {
  extraction: {
    minConfidence: number;
    maxEntitiesPerMessage: number;
    enableContextCapture: boolean;
  };
  linking: {
    fuzzyThreshold: number;
    enableAliasGeneration: boolean;
    maxCandidates: number;
  };
  relationships: {
    maxCoOccurrenceDistance: number;
    minRelationshipStrength: number;
    enableSemanticAnalysis: boolean;
  };
  future: {
    enableSentimentAnalysis: boolean;
    enableTopicModeling: boolean;
    enableTemporalAnalysis: boolean;
    enableCausalInference: boolean;
  };
}

export interface ProcessingResult {
  entitiesExtracted: number;
  entitiesLinked: number;
  relationshipsDetected: number;
  aliasesCreated: number;
  processingTime: number;
  insights: KnowledgeInsight[];
}

export interface KnowledgeInsight {
  type: InsightType;
  confidence: number;
  description: string;
  entities: Entity[];
  evidence: string[];
}

export type InsightType = 
  | 'key_person'           // Important person in conversations
  | 'hub_entity'           // Entity with many relationships
  | 'emerging_topic'       // New topic gaining mentions
  | 'relationship_cluster' // Group of related entities
  | 'expertise_area'       // Person's area of expertise
  | 'decision_pattern'     // Recurring decision-making pattern
  | 'collaboration_network'// Group collaboration pattern
  | 'technology_stack'     // Set of related technologies

// Future relationship types to consider
export type FutureRelationshipType = 
  | 'influences'           // Person/Entity influences another
  | 'competes_with'        // Competitive relationship
  | 'successor_of'         // Version/iteration relationship
  | 'prerequisite_for'     // Dependency relationship
  | 'contradicts'          // Conflicting information
  | 'supports'             // Supporting evidence
  | 'criticizes'           // Negative relationship
  | 'recommends'           // Endorsement relationship
  | 'learns_from'          // Knowledge transfer
  | 'reports_to'           // Hierarchical relationship
  | 'funded_by'            // Financial relationship
  | 'owns'                 // Ownership relationship
  | 'publishes'            // Authorship relationship
  | 'hosts'                // Event/location hosting
  | 'integrates_with'      // Technical integration

// Future information types to extract
export interface FutureExtractionTypes {
  // Sentiment and Opinion
  sentiment: {
    polarity: 'positive' | 'negative' | 'neutral' | 'mixed';
    intensity: number;
    target: string;
    aspect: string;
  };

  // Temporal Information
  temporal: {
    type: 'deadline' | 'milestone' | 'duration' | 'frequency';
    value: string;
    reference: 'past' | 'present' | 'future';
    entities: string[];
  };

  // Quantitative Data
  metrics: {
    name: string;
    value: number;
    unit: string;
    trend: 'increasing' | 'decreasing' | 'stable';
    context: string;
  };

  // Action Items and Commitments
  commitments: {
    actor: string;
    action: string;
    deadline?: string;
    status: 'proposed' | 'committed' | 'completed' | 'cancelled';
    dependencies: string[];
  };

  // Questions and Uncertainties
  uncertainties: {
    question: string;
    askedBy: string;
    answeredBy?: string;
    resolved: boolean;
    importance: 'high' | 'medium' | 'low';
  };

  // Risks and Issues
  risks: {
    description: string;
    severity: 'critical' | 'high' | 'medium' | 'low';
    likelihood: number;
    mitigation?: string;
    owner?: string;
  };
}

export class KnowledgeGraphService {
  private dbManager: DatabaseManager;
  private extractionService: EntityExtractionService;
  private entityLinker: EntityLinker;
  private relationshipDetector: RelationshipDetector;
  private entityRepository: EntityRepository;
  private config: KnowledgeGraphConfig;

  constructor(dbManager: DatabaseManager, config?: Partial<KnowledgeGraphConfig>) {
    this.dbManager = dbManager;
    this.config = this.mergeConfig(config);

    // Initialize services
    this.extractionService = new EntityExtractionService(dbManager, this.config.extraction);
    this.entityLinker = new EntityLinker(dbManager, this.config.linking);
    this.relationshipDetector = new RelationshipDetector(dbManager, this.config.relationships);
    this.entityRepository = new EntityRepository(dbManager);
  }

  /**
   * Process a single message through the full knowledge graph pipeline
   */
  async processMessage(
    messageId: string,
    conversationId: string,
    content: string,
    metadata?: {
      author?: string;
      timestamp?: number;
      context?: Record<string, any>;
    }
  ): Promise<ProcessingResult> {
    const startTime = Date.now();
    const insights: KnowledgeInsight[] = [];

    try {
      // Step 1: Extract entities from message
      const extractionResult = await this.extractionService.processMessage(
        messageId,
        conversationId,
        content
      );

      // Step 2: Link entities and resolve aliases
      let linkedCount = 0;
      let aliasesCreated = 0;

      for (const extracted of extractionResult.extractedEntities) {
        const linkingResult = await this.entityLinker.linkEntity(
          extracted.text,
          extracted.type,
          extracted.context
        );

        if (linkingResult.linkedEntity && linkingResult.suggestedAliases.length > 0) {
          linkedCount++;

          // Create aliases for better future matching
          const aliases = await this.entityLinker.createAliases(
            linkingResult.linkedEntity.id,
            linkingResult.suggestedAliases.map(alias => ({
              alias,
              type: 'variation',
              confidence: 0.8
            }))
          );
          aliasesCreated += aliases.length;
        }
      }

      // Step 3: Detect relationships between entities
      const entityMentions = extractionResult.mentions.map(mention => ({
        entityId: mention.entityId,
        startPosition: mention.startPosition,
        endPosition: mention.endPosition,
        type: extractionResult.extractedEntities.find(e => 
          e.startPosition === mention.startPosition
        )?.type || 'concept' as EntityType
      }));

      const relationshipResult = await this.relationshipDetector.analyzeMessage(
        messageId,
        conversationId,
        content,
        entityMentions
      );

      // Store detected relationships
      for (const relationship of relationshipResult.detectedRelationships) {
        await this.relationshipDetector.storeRelationship(relationship);
      }

      // Step 4: Generate insights
      const messageInsights = await this.generateInsights(
        extractionResult.extractedEntities,
        relationshipResult.detectedRelationships,
        metadata
      );
      insights.push(...messageInsights);

      return {
        entitiesExtracted: extractionResult.extractedEntities.length,
        entitiesLinked: linkedCount,
        relationshipsDetected: relationshipResult.detectedRelationships.length,
        aliasesCreated,
        processingTime: Date.now() - startTime,
        insights
      };

    } catch (error) {
      console.error('Failed to process message for knowledge graph:', error);
      throw error;
    }
  }

  /**
   * Process an entire conversation
   */
  async processConversation(conversationId: string): Promise<{
    totalMessages: number;
    totalEntities: number;
    totalRelationships: number;
    keyInsights: KnowledgeInsight[];
    processingTime: number;
  }> {
    const startTime = Date.now();

    // Get all messages in conversation
    const db = this.dbManager.getConnection();
    const messages = db.prepare(`
      SELECT id, content, created_at, role 
      FROM messages 
      WHERE conversation_id = ? 
      ORDER BY created_at ASC
    `).all(conversationId) as any[];

    let totalEntities = 0;
    let totalRelationships = 0;
    const allInsights: KnowledgeInsight[] = [];

    // Process each message
    for (const message of messages) {
      const result = await this.processMessage(
        message.id,
        conversationId,
        message.content,
        {
          timestamp: message.created_at
        }
      );

      totalEntities += result.entitiesExtracted;
      totalRelationships += result.relationshipsDetected;
      allInsights.push(...result.insights);
    }

    // Analyze conversation-level patterns
    const keyInsights = await this.analyzeConversationPatterns(conversationId, allInsights);

    return {
      totalMessages: messages.length,
      totalEntities,
      totalRelationships,
      keyInsights,
      processingTime: Date.now() - startTime
    };
  }

  /**
   * Generate insights from extracted entities and relationships
   */
  private async generateInsights(
    entities: any[],
    relationships: any[],
    _metadata?: any
  ): Promise<KnowledgeInsight[]> {
    const insights: KnowledgeInsight[] = [];

    // Detect key persons (mentioned multiple times or in important contexts)
    const personEntities = entities.filter(e => e.type === 'person');
    if (personEntities.length > 2) {
      insights.push({
        type: 'collaboration_network',
        confidence: 0.7,
        description: `Detected collaboration between ${personEntities.length} people`,
        entities: personEntities,
        evidence: personEntities.map(p => p.context || p.text)
      });
    }

    // Detect technology stacks
    const techEntities = entities.filter(e => e.type === 'technical');
    if (techEntities.length >= 3) {
      insights.push({
        type: 'technology_stack',
        confidence: 0.8,
        description: `Identified technology stack with ${techEntities.length} components`,
        entities: techEntities,
        evidence: techEntities.map(t => t.context || t.text)
      });
    }

    // Detect hub entities (entities with multiple relationships)
    const relationshipCounts = new Map<string, number>();
    for (const rel of relationships) {
      relationshipCounts.set(
        rel.sourceEntity.id,
        (relationshipCounts.get(rel.sourceEntity.id) || 0) + 1
      );
      relationshipCounts.set(
        rel.targetEntity.id,
        (relationshipCounts.get(rel.targetEntity.id) || 0) + 1
      );
    }

    for (const [entityId, count] of relationshipCounts) {
      if (count >= 3) {
        const entity = await this.entityRepository.getById(entityId);
        if (entity) {
          insights.push({
            type: 'hub_entity',
            confidence: Math.min(0.9, 0.6 + count * 0.1),
            description: `${entity.name} is connected to ${count} other entities`,
            entities: [entity],
            evidence: relationships
              .filter(r => r.sourceEntity.id === entityId || r.targetEntity.id === entityId)
              .map(r => r.suggestedContext)
          });
        }
      }
    }

    return insights;
  }

  /**
   * Analyze patterns across an entire conversation
   */
  private async analyzeConversationPatterns(
    _conversationId: string,
    messageInsights: KnowledgeInsight[]
  ): Promise<KnowledgeInsight[]> {
    const keyInsights: KnowledgeInsight[] = [];

    // Find most mentioned entities
    const entities = await this.entityRepository.getMostMentioned(5);
    if (entities.length > 0) {
      const keyPerson = entities.find(e => e.type === 'person');
      if (keyPerson) {
        keyInsights.push({
          type: 'key_person',
          confidence: 0.85,
          description: `${keyPerson.name} is a key person with ${keyPerson.mentionCount} mentions`,
          entities: [keyPerson],
          evidence: [`Mentioned ${keyPerson.mentionCount} times across conversations`]
        });
      }
    }

    // Aggregate technology insights
    const techInsights = messageInsights.filter(i => i.type === 'technology_stack');
    if (techInsights.length > 0) {
      const allTechEntities = new Set<string>();
      techInsights.forEach(insight => {
        insight.entities.forEach(e => allTechEntities.add(e.name));
      });

      if (allTechEntities.size >= 5) {
        keyInsights.push({
          type: 'technology_stack',
          confidence: 0.9,
          description: `Comprehensive technology stack identified with ${allTechEntities.size} components`,
          entities: [],
          evidence: Array.from(allTechEntities)
        });
      }
    }

    return keyInsights;
  }

  /**
   * Get entity with all its relationships and aliases
   */
  async getEntityGraph(entityId: string): Promise<{
    entity: Entity | null;
    aliases: any[];
    relationships: any[];
    relatedEntities: Entity[];
  }> {
    const entity = await this.entityRepository.getById(entityId);
    if (!entity) {
      return {
        entity: null,
        aliases: [],
        relationships: [],
        relatedEntities: []
      };
    }

    const aliases = await this.entityLinker.getEntityAliases(entityId);
    const relationships = await this.relationshipDetector.getEntityRelationships(entityId);

    // Get all related entities
    const relatedEntityIds = new Set<string>();
    relationships.forEach(rel => {
      if (rel.sourceEntityId !== entityId) relatedEntityIds.add(rel.sourceEntityId);
      if (rel.targetEntityId !== entityId) relatedEntityIds.add(rel.targetEntityId);
    });

    const relatedEntities = await Promise.all(
      Array.from(relatedEntityIds).map(id => this.entityRepository.getById(id))
    );

    return {
      entity,
      aliases,
      relationships,
      relatedEntities: relatedEntities.filter(e => e !== null) as Entity[]
    };
  }

  /**
   * Search for entities and their relationships
   */
  async searchKnowledgeGraph(
    query: string,
    options?: {
      entityTypes?: EntityType[];
      relationshipTypes?: string[];
      minConfidence?: number;
      limit?: number;
    }
  ): Promise<{
    entities: Entity[];
    relationships: any[];
    insights: KnowledgeInsight[];
  }> {
    // Search for matching entities
    // If multiple entity types specified, we need to do multiple searches
    let allEntities: Entity[] = [];

    if (options?.entityTypes && options.entityTypes.length > 0) {
      // Search each type separately and combine results
      for (const entityType of options.entityTypes) {
        const searchResult = await this.entityRepository.search({
          query,
          type: entityType,
          limit: options?.limit || 10
        });
        allEntities.push(...searchResult.entities);
      }
      // Remove duplicates
      const uniqueEntities = new Map<string, Entity>();
      allEntities.forEach(entity => uniqueEntities.set(entity.id, entity));
      allEntities = Array.from(uniqueEntities.values());
    } else {
      // Search all types
      const searchResult = await this.entityRepository.search({
        query,
        limit: options?.limit || 10
      });
      allEntities = searchResult.entities;
    }

    // Get relationships for found entities
    const allRelationships: any[] = [];
    for (const entity of allEntities) {
      const relationships = await this.relationshipDetector.getEntityRelationships(entity.id);
      allRelationships.push(...relationships);
    }

    // Filter relationships by type if specified
    const filteredRelationships = options?.relationshipTypes
      ? allRelationships.filter(r => options.relationshipTypes!.includes(r.relationshipType))
      : allRelationships;

    // Generate search-specific insights
    const insights: KnowledgeInsight[] = [];
    if (allEntities.length > 0) {
      insights.push({
        type: 'relationship_cluster',
        confidence: 0.7,
        description: `Found ${allEntities.length} entities related to "${query}"`,
        entities: allEntities,
        evidence: allEntities.map(e => `${e.name} (${e.type})`)
      });
    }

    return {
      entities: allEntities,
      relationships: filteredRelationships,
      insights
    };
  }

  /**
   * Export knowledge graph in various formats
   */
  async exportKnowledgeGraph(format: 'json' | 'graphml' | 'cypher'): Promise<string> {
    const allEntities = await this.entityRepository.search({ limit: 1000 });
    const allRelationships: any[] = [];

    for (const entity of allEntities.entities) {
      const relationships = await this.relationshipDetector.getEntityRelationships(entity.id);
      allRelationships.push(...relationships);
    }

    switch (format) {
      case 'json':
        return JSON.stringify({
          entities: allEntities.entities,
          relationships: allRelationships,
          metadata: {
            exportDate: new Date().toISOString(),
            entityCount: allEntities.total,
            relationshipCount: allRelationships.length
          }
        }, null, 2);

      case 'graphml':
        // GraphML format for graph visualization tools
        let graphml = '<?xml version="1.0" encoding="UTF-8"?>\n';
        graphml += '<graphml xmlns="http://graphml.graphdrawing.org/xmlns">\n';
        graphml += '  <graph id="G" edgedefault="directed">\n';

        // Add nodes
        for (const entity of allEntities.entities) {
          graphml += `    <node id="${entity.id}">\n`;
          graphml += `      <data key="name">${entity.name}</data>\n`;
          graphml += `      <data key="type">${entity.type}</data>\n`;
          graphml += `    </node>\n`;
        }

        // Add edges
        for (const rel of allRelationships) {
          graphml += `    <edge source="${rel.sourceEntityId}" target="${rel.targetEntityId}">\n`;
          graphml += `      <data key="type">${rel.relationshipType}</data>\n`;
          graphml += `      <data key="strength">${rel.strength}</data>\n`;
          graphml += `    </edge>\n`;
        }

        graphml += '  </graph>\n</graphml>';
        return graphml;

      case 'cypher':
        // Neo4j Cypher query format
        let cypher = '// Knowledge Graph Export - Neo4j Cypher Queries\n\n';

        // Create nodes
        cypher += '// Create Entities\n';
        for (const entity of allEntities.entities) {
          cypher += `CREATE (n:${entity.type} {id: '${entity.id}', name: '${entity.name}'});\n`;
        }

        cypher += '\n// Create Relationships\n';
        for (const rel of allRelationships) {
          cypher += `MATCH (a {id: '${rel.sourceEntityId}'}), (b {id: '${rel.targetEntityId}'}) `;
          cypher += `CREATE (a)-[:${rel.relationshipType.toUpperCase()} {strength: ${rel.strength}}]->(b);\n`;
        }

        return cypher;

      default:
        throw new Error(`Unsupported export format: ${format}`);
    }
  }

  /**
   * Merge default config with provided config
   */
  private mergeConfig(config?: Partial<KnowledgeGraphConfig>): KnowledgeGraphConfig {
    return {
      extraction: {
        minConfidence: 0.6,
        maxEntitiesPerMessage: 20,
        enableContextCapture: true,
        ...config?.extraction
      },
      linking: {
        fuzzyThreshold: 0.8,
        enableAliasGeneration: true,
        maxCandidates: 5,
        ...config?.linking
      },
      relationships: {
        maxCoOccurrenceDistance: 500,
        minRelationshipStrength: 0.3,
        enableSemanticAnalysis: true,
        ...config?.relationships
      },
      future: {
        enableSentimentAnalysis: false,
        enableTopicModeling: false,
        enableTemporalAnalysis: false,
        enableCausalInference: false,
        ...config?.future
      }
    };
  }
}

/**
 * Relationship Detection Service for Phase 3
 * 
 * Analyzes co-occurrence patterns and detects relationships between entities.
 * Builds the knowledge graph by identifying semantic connections.
 */

import { DatabaseManager } from '../storage/Database.js';
import { EntityRepository, Entity, EntityType } from '../storage/repositories/EntityRepository.js';

export interface EntityRelationship {
  id: string;
  sourceEntityId: string;
  targetEntityId: string;
  relationshipType: RelationshipType;
  strength: number;
  context: string;
  createdAt: number;
  updatedAt: number;
}

export type RelationshipType = 
  | 'works_for'         // Person-Organization employment (from migration)
  | 'created_by'        // Product/Technical-Person/Organization creation
  | 'discussed_with'    // Person-Person discussion
  | 'related_to'        // Generic semantic relationship  
  | 'part_of'           // Entity-Entity hierarchical relationship
  | 'mentioned_with'    // Co-occurrence relationship
  | 'temporal_sequence' // Time-based relationship
  | 'cause_effect'      // Causal relationship

export interface CoOccurrence {
  entityId1: string;
  entityId2: string;
  messageId: string;
  conversationId: string;
  distance: number; // Character distance between entities
  context: string;
  timestamp: number;
}

export interface RelationshipCandidate {
  sourceEntity: Entity;
  targetEntity: Entity;
  relationshipType: RelationshipType;
  confidence: number;
  evidence: CoOccurrence[];
  suggestedContext: string;
}

export interface DetectionConfig {
  maxCoOccurrenceDistance: number;
  minRelationshipStrength: number;
  contextWindowSize: number;
  enableSemanticAnalysis: boolean;
}

export class RelationshipDetector {
  private dbManager: DatabaseManager;
  private entityRepository: EntityRepository;
  private config: DetectionConfig;

  constructor(dbManager: DatabaseManager, config: Partial<DetectionConfig> = {}) {
    this.dbManager = dbManager;
    this.entityRepository = new EntityRepository(dbManager);
    this.config = {
      maxCoOccurrenceDistance: 500, // Characters
      minRelationshipStrength: 0.3,
      contextWindowSize: 100,
      enableSemanticAnalysis: true,
      ...config
    };
  }

  /**
   * Analyze a message for entity relationships
   */
  async analyzeMessage(
    messageId: string,
    conversationId: string,
    content: string,
    extractedEntities: Array<{
      entityId: string;
      startPosition: number;
      endPosition: number;
      type: EntityType;
    }>
  ): Promise<{
    coOccurrences: CoOccurrence[];
    detectedRelationships: RelationshipCandidate[];
  }> {
    // Find co-occurrences between entities in this message
    const coOccurrences = this.findCoOccurrences(
      messageId,
      conversationId,
      content,
      extractedEntities
    );

    // Analyze co-occurrences to detect relationships
    const detectedRelationships = await this.detectRelationships(coOccurrences, content);

    return {
      coOccurrences,
      detectedRelationships
    };
  }

  /**
   * Find co-occurrences between entities in a message
   */
  private findCoOccurrences(
    messageId: string,
    conversationId: string,
    content: string,
    entities: Array<{
      entityId: string;
      startPosition: number;
      endPosition: number;
      type: EntityType;
    }>
  ): CoOccurrence[] {
    const coOccurrences: CoOccurrence[] = [];

    // Compare each entity with every other entity
    for (let i = 0; i < entities.length; i++) {
      for (let j = i + 1; j < entities.length; j++) {
        const entity1 = entities[i];
        const entity2 = entities[j];

        // Calculate distance between entities
        const distance = Math.abs(entity1.startPosition - entity2.startPosition);

        // Only consider entities within co-occurrence distance
        if (distance <= this.config.maxCoOccurrenceDistance) {
          // Extract context around both entities
          const contextStart = Math.min(entity1.startPosition, entity2.startPosition) - this.config.contextWindowSize;
          const contextEnd = Math.max(entity1.endPosition, entity2.endPosition) + this.config.contextWindowSize;

          const context = content.substring(
            Math.max(0, contextStart),
            Math.min(content.length, contextEnd)
          ).trim();

          coOccurrences.push({
            entityId1: entity1.entityId,
            entityId2: entity2.entityId,
            messageId,
            conversationId,
            distance,
            context,
            timestamp: Date.now()
          });
        }
      }
    }

    return coOccurrences;
  }

  /**
   * Analyze co-occurrences to detect semantic relationships
   */
  private async detectRelationships(
    coOccurrences: CoOccurrence[],
    messageContent: string
  ): Promise<RelationshipCandidate[]> {
    const relationships: RelationshipCandidate[] = [];

    for (const coOccurrence of coOccurrences) {
      try {
        // Get entity details
        const sourceEntity = await this.entityRepository.getById(coOccurrence.entityId1);
        const targetEntity = await this.entityRepository.getById(coOccurrence.entityId2);

        if (!sourceEntity || !targetEntity) continue;

        // Determine relationship type based on entity types and context
        const relationshipTypes = this.inferRelationshipTypes(
          sourceEntity,
          targetEntity,
          coOccurrence.context
        );

        // Create relationship candidates
        for (const relType of relationshipTypes) {
          const confidence = this.calculateRelationshipConfidence(
            sourceEntity,
            targetEntity,
            relType,
            coOccurrence,
            messageContent
          );

          if (confidence >= this.config.minRelationshipStrength) {
            relationships.push({
              sourceEntity,
              targetEntity,
              relationshipType: relType.type,
              confidence,
              evidence: [coOccurrence],
              suggestedContext: coOccurrence.context
            });
          }
        }

      } catch (error) {
        console.warn('Failed to detect relationship for co-occurrence:', error);
      }
    }

    return relationships;
  }

  /**
   * Infer possible relationship types based on entity types
   */
  private inferRelationshipTypes(
    sourceEntity: Entity,
    targetEntity: Entity,
    context: string
  ): Array<{ type: RelationshipType; baseConfidence: number }> {
    const relationships: Array<{ type: RelationshipType; baseConfidence: number }> = [];

    // Person-Person relationships
    if (sourceEntity.type === 'person' && targetEntity.type === 'person') {
      if (this.hasWorkIndicators(context)) {
        relationships.push({ type: 'discussed_with', baseConfidence: 0.7 });
      }
      relationships.push({ type: 'mentioned_with', baseConfidence: 0.5 });
    }

    // Person-Organization relationships  
    else if (sourceEntity.type === 'person' && targetEntity.type === 'organization') {
      if (this.hasEmploymentIndicators(context)) {
        relationships.push({ type: 'works_for', baseConfidence: 0.8 });
      }
      relationships.push({ type: 'related_to', baseConfidence: 0.4 });
    }

    // Person-Technical relationships
    else if (sourceEntity.type === 'person' && targetEntity.type === 'technical') {
      relationships.push({ type: 'mentioned_with', baseConfidence: 0.4 });
    }

    // Organization-Technical relationships
    else if (sourceEntity.type === 'organization' && targetEntity.type === 'technical') {
      if (this.hasDevelopmentIndicators(context)) {
        relationships.push({ type: 'created_by', baseConfidence: 0.6 });
      }
      relationships.push({ type: 'related_to', baseConfidence: 0.5 });
    }

    // Product-Organization relationships (reverse of above)
    else if (sourceEntity.type === 'technical' && targetEntity.type === 'organization') {
      if (this.hasDevelopmentIndicators(context)) {
        relationships.push({ type: 'created_by', baseConfidence: 0.6 });
      }
      relationships.push({ type: 'related_to', baseConfidence: 0.5 });
    }

    // Generic relationships for any other combinations
    else {
      relationships.push({ type: 'related_to', baseConfidence: 0.3 });
    }

    return relationships;
  }

  /**
   * Calculate confidence score for a relationship
   */
  private calculateRelationshipConfidence(
    sourceEntity: Entity,
    targetEntity: Entity,
    relationshipType: { type: RelationshipType; baseConfidence: number },
    coOccurrence: CoOccurrence,
    _messageContent: string
  ): number {
    let confidence = relationshipType.baseConfidence;

    // Distance-based adjustment (closer entities = higher confidence)
    const distanceBonus = Math.max(0, (this.config.maxCoOccurrenceDistance - coOccurrence.distance) / this.config.maxCoOccurrenceDistance) * 0.2;
    confidence += distanceBonus;

    // Context strength adjustment
    const contextStrength = this.analyzeContextStrength(
      coOccurrence.context,
      relationshipType.type
    );
    confidence += contextStrength * 0.3;

    // Entity confidence adjustment
    const entityConfidenceAvg = (sourceEntity.confidenceScore + targetEntity.confidenceScore) / 2;
    confidence *= entityConfidenceAvg;

    return Math.min(1.0, confidence);
  }

  /**
   * Analyze context for relationship-specific indicators
   */
  private analyzeContextStrength(context: string, relationshipType: RelationshipType): number {
    const lowerContext = context.toLowerCase();

    switch (relationshipType) {
      case 'discussed_with':
        return this.countIndicators(lowerContext, [
          'collaborate', 'team', 'together', 'partner', 'worked with', 'project with'
        ]) * 0.3;

      case 'works_for':
        return this.countIndicators(lowerContext, [
          'employee', 'works at', 'job at', 'position at', 'hired by', 'employed by'
        ]) * 0.4;

      case 'created_by':
        return this.countIndicators(lowerContext, [
          'develops', 'created', 'built', 'developed', 'maintains', 'working on'
        ]) * 0.3;

      case 'mentioned_with':
        return this.countIndicators(lowerContext, [
          'with', 'and', 'also', 'together'
        ]) * 0.2;

      default:
        return 0.1; // Base context strength
    }
  }

  /**
   * Count relationship indicators in text
   */
  private countIndicators(text: string, indicators: string[]): number {
    let count = 0;
    for (const indicator of indicators) {
      if (text.includes(indicator)) {
        count++;
      }
    }
    return Math.min(1.0, count / indicators.length);
  }

  /**
   * Check for work-related indicators
   */
  private hasWorkIndicators(context: string): boolean {
    const workPatterns = /\b(collaborate|team|together|partner|project|work)\b/i;
    return workPatterns.test(context);
  }

  /**
   * Check for employment indicators
   */
  private hasEmploymentIndicators(context: string): boolean {
    const employmentPatterns = /\b(works at|employed by|job at|position at|hired by|employee)\b/i;
    return employmentPatterns.test(context);
  }

  /**
   * Check for development indicators
   */
  private hasDevelopmentIndicators(context: string): boolean {
    const devPatterns = /\b(develops|created|built|developed|maintains|working on)\b/i;
    return devPatterns.test(context);
  }

  /**
   * Store relationship in database
   */
  async storeRelationship(relationship: RelationshipCandidate): Promise<EntityRelationship> {
    const db = this.dbManager.getConnection();

    const now = Date.now();
    const relationshipRecord: EntityRelationship = {
      id: this.generateId(),
      sourceEntityId: relationship.sourceEntity.id,
      targetEntityId: relationship.targetEntity.id,
      relationshipType: relationship.relationshipType,
      strength: relationship.confidence,
      context: relationship.suggestedContext,
      createdAt: now,
      updatedAt: now
    };

    const stmt = db.prepare(`
      INSERT OR REPLACE INTO entity_relationships (
        id, source_entity_id, target_entity_id, relationship_type,
        strength, first_mentioned_at, last_mentioned_at, mention_count,
        context_messages, created_at, updated_at
      ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
    `);

    stmt.run(
      relationshipRecord.id,
      relationshipRecord.sourceEntityId,
      relationshipRecord.targetEntityId,
      relationshipRecord.relationshipType,
      relationshipRecord.strength,
      now, // first_mentioned_at
      now, // last_mentioned_at  
      1,   // mention_count
      JSON.stringify([relationship.suggestedContext]), // context_messages as JSON array
      relationshipRecord.createdAt,
      relationshipRecord.updatedAt
    );

    return relationshipRecord;
  }

  /**
   * Get relationships for an entity
   */
  async getEntityRelationships(entityId: string): Promise<EntityRelationship[]> {
    const db = this.dbManager.getConnection();
    const stmt = db.prepare(`
      SELECT * FROM entity_relationships 
      WHERE source_entity_id = ? OR target_entity_id = ?
      ORDER BY strength DESC, created_at DESC
    `);

    const rows = stmt.all(entityId, entityId) as any[];
    return rows.map(row => ({
      id: row.id,
      sourceEntityId: row.source_entity_id,
      targetEntityId: row.target_entity_id,
      relationshipType: row.relationship_type as RelationshipType,
      strength: row.strength,
      context: row.context,
      createdAt: row.created_at,
      updatedAt: row.updated_at
    }));
  }

  /**
   * Batch analyze relationships across multiple messages
   */
  async analyzeConversation(_conversationId: string): Promise<{
    totalCoOccurrences: number;
    totalRelationships: number;
    strongestRelationships: RelationshipCandidate[];
  }> {
    // This would integrate with message processing to analyze entire conversations
    // For now, return a placeholder structure
    return {
      totalCoOccurrences: 0,
      totalRelationships: 0,
      strongestRelationships: []
    };
  }

  /**
   * Generate UUID
   */
  private generateId(): string {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
      const r = Math.random() * 16 | 0;
      const v = c === 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  }
}

#!/usr/bin/env node

/**
 * MCP Persistence Server - Main Entry Point
 * 
 * This is the main entry point for the MCP (Model Context Protocol) Persistence Server.
 * It initializes and starts the server with proper configuration, error handling,
 * and environment variable support.
 * 
 * Environment Variables:
 * - PERSISTENCE_DB_PATH: Database file path (default: ./conversations.db)
 * - PERSISTENCE_LOG_LEVEL: Logging level (default: info)
 * - PERSISTENCE_MAX_DB_SIZE_MB: Maximum database size in MB (default: 1000)
 * - PERSISTENCE_DEBUG: Enable debug mode (default: false)
 * - PERSISTENCE_TOOL_TIMEOUT_MS: Tool execution timeout in ms (default: 30000)
 * - PERSISTENCE_EMBEDDINGS_ENABLED: Enable semantic search features (default: true)
 * - PERSISTENCE_EMBEDDINGS_BATCH_SIZE: Batch size for embedding generation (default: 100)
 * 
 * Usage:
 *   node dist/index.js                    # Start server with default config
 *   PERSISTENCE_DEBUG=true node dist/index.js   # Start with debug logging
 */

import { MCPServer, MCPServerConfig, createMCPServer } from './server/MCPServer.js';
import { readFileSync } from 'fs';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';

// ES module equivalent of __dirname
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

/**
 * Load configuration from environment variables
 */
function loadConfigFromEnvironment(): MCPServerConfig {
  const config: MCPServerConfig = {
    name: 'mcp-persistence-server',
    version: '1.0.0',
    databasePath: process.env.PERSISTENCE_DB_PATH || './conversations.db',
    logLevel: (process.env.PERSISTENCE_LOG_LEVEL as any) || 'info',
    maxDatabaseSizeMB: parseInt(process.env.PERSISTENCE_MAX_DB_SIZE_MB || '1000', 10),
    debug: process.env.PERSISTENCE_DEBUG === 'true' || process.env.NODE_ENV === 'development',
    toolTimeoutMs: parseInt(process.env.PERSISTENCE_TOOL_TIMEOUT_MS || '30000', 10)
  };

  return config;
}

/**
 * Validate configuration
 */
function validateConfig(config: MCPServerConfig): void {
  const errors: string[] = [];

  // Validate database path
  if (!config.databasePath) {
    errors.push('Database path is required');
  }

  // Validate max database size
  if (config.maxDatabaseSizeMB && (config.maxDatabaseSizeMB < 10 || config.maxDatabaseSizeMB > 10000)) {
    errors.push('Max database size must be between 10 and 10000 MB');
  }

  // Validate tool timeout
  if (config.toolTimeoutMs && (config.toolTimeoutMs < 1000 || config.toolTimeoutMs > 300000)) {
    errors.push('Tool timeout must be between 1000 and 300000 ms');
  }

  // Validate log level
  if (config.logLevel && !['debug', 'info', 'warn', 'error'].includes(config.logLevel)) {
    errors.push('Log level must be one of: debug, info, warn, error');
  }

  if (errors.length > 0) {
    throw new Error(`Configuration validation failed:\n${errors.map(e => `  - ${e}`).join('\n')}`);
  }
}

/**
 * Log configuration (without sensitive data)
 */
function logConfiguration(config: MCPServerConfig): void {
  console.log(`[INFO] Starting ${config.name} v${config.version}`);
  console.log(`[INFO] Configuration:`);
  console.log(`  - Database Path: ${config.databasePath}`);
  console.log(`  - Log Level: ${config.logLevel}`);
  console.log(`  - Max DB Size: ${config.maxDatabaseSizeMB} MB`);
  console.log(`  - Debug Mode: ${config.debug ? 'enabled' : 'disabled'}`);
  console.log(`  - Tool Timeout: ${config.toolTimeoutMs} ms`);
}

/**
 * Setup error handling for uncaught exceptions
 */
function setupErrorHandling(): void {
  process.on('uncaughtException', (error) => {
    console.error('[ERROR] Uncaught Exception:', error);
    process.exit(1);
  });

  process.on('unhandledRejection', (reason, promise) => {
    console.error('[ERROR] Unhandled Rejection at:', promise, 'reason:', reason);
    process.exit(1);
  });
}

/**
 * Handle command line arguments
 */
function handleCommandLineArgs(): void {
  const args = process.argv.slice(2);

  if (args.includes('--help') || args.includes('-h')) {
    console.log(`
MCP Persistence Server

A Model Context Protocol server for conversation persistence and search.

Usage:
  node dist/index.js [options]

Options:
  -h, --help       Show this help message
  --version        Show version information
  --health-check   Perform a health check and exit

Environment Variables:
  PERSISTENCE_DB_PATH               Database file path (default: ./conversations.db)
  PERSISTENCE_LOG_LEVEL             Log level: debug|info|warn|error (default: info)
  PERSISTENCE_MAX_DB_SIZE_MB        Maximum database size in MB (default: 1000)
  PERSISTENCE_DEBUG                 Enable debug mode: true|false (default: false)
  PERSISTENCE_TOOL_TIMEOUT_MS       Tool execution timeout in ms (default: 30000)
  PERSISTENCE_EMBEDDINGS_ENABLED    Enable semantic search features: true|false (default: true)
  PERSISTENCE_EMBEDDINGS_BATCH_SIZE Batch size for embedding generation (default: 100)

Examples:
  node dist/index.js
  PERSISTENCE_DEBUG=true node dist/index.js
  PERSISTENCE_DB_PATH=/data/conversations.db node dist/index.js
`);
    process.exit(0);
  }

  if (args.includes('--version')) {
    try {
      const packageJsonPath = join(__dirname, '../package.json');
      const packageJson = JSON.parse(readFileSync(packageJsonPath, 'utf8'));
      console.log(`${packageJson.name} v${packageJson.version}`);
    } catch (error) {
      console.log('mcp-persistence-server v1.0.0');
    }
    process.exit(0);
  }
}

/**
 * Perform health check and exit
 */
async function performHealthCheck(server: MCPServer): Promise<void> {
  try {
    console.log('[INFO] Performing health check...');

    const health = await server.healthCheck();

    console.log(`[INFO] Health Status: ${health.status}`);
    console.log('[INFO] Component Status:');
    console.log(`  - Database: ${health.checks.database}`);
    console.log(`  - Tools: ${health.checks.tools}`);
    console.log(`  - Search: ${health.checks.search}`);

    if (health.error) {
      console.log(`[ERROR] Health Check Error: ${health.error}`);
    }

    process.exit(health.status === 'healthy' ? 0 : 1);

  } catch (error) {
    console.error('[ERROR] Health check failed:', error);
    process.exit(1);
  }
}

/**
 * Main function
 */
async function main(): Promise<void> {
  try {
    // Handle command line arguments
    handleCommandLineArgs();

    // Setup error handling
    setupErrorHandling();

    // Load and validate configuration
    const config = loadConfigFromEnvironment();
    validateConfig(config);

    // Log configuration
    logConfiguration(config);

    // Create and start server
    const server = createMCPServer(config);

    // Handle health check argument
    if (process.argv.includes('--health-check')) {
      await server.start();
      await performHealthCheck(server);
      return;
    }

    // Start the server
    await server.start();

    // Log successful startup
    console.log('[INFO] MCP Persistence Server is running');
    console.log('[INFO] Listening for MCP requests on stdio...');

    // Keep the process alive
    process.stdin.resume();

  } catch (error) {
    console.error('[ERROR] Failed to start server:', error instanceof Error ? error.message : error);
    process.exit(1);
  }
}

/**
 * Handle startup in different environments
 */
// For ES modules, we can use import.meta.url to detect if this is the main module
const isMainModule = process.argv[1] && new URL(process.argv[1], 'file://').href === import.meta.url;

if (isMainModule) {
  // Only run main() if this file is executed directly
  main().catch(error => {
    console.error('[ERROR] Unexpected error in main():', error);
    process.exit(1);
  });
}

// Export functions for module use
export {
  main,
  loadConfigFromEnvironment,
  validateConfig,
  createMCPServer
};

/**
 * Entity Extraction Service
 * 
 * Extracts entities from text using pattern matching and NLP approaches.
 * Integrates with the existing EntityCentricStrategy patterns and extends
 * them for knowledge graph population.
 */

import { EntityType, ExtractionMethod } from '../storage/repositories/KnowledgeGraphRepository.js';

/**
 * Extracted entity with position and confidence information
 */
export interface ExtractedEntity {
  text: string;
  normalizedText: string;
  type: EntityType;
  confidence: number;
  startPosition: number;
  endPosition: number;
  extractionMethod: ExtractionMethod;
  context?: string; // Surrounding text context
}

/**
 * Entity extraction configuration
 */
export interface ExtractionConfig {
  minConfidence: number;
  maxEntitiesPerMessage: number;
  enableContextCapture: boolean;
  contextWindowSize: number;
}

/**
 * Entity extraction service
 */
export class EntityExtractor {
  private entityPatterns: Map<EntityType, RegExp[]> = new Map();
  private stopWords: Set<string> = new Set();
  private config: ExtractionConfig;

  constructor(config: Partial<ExtractionConfig> = {}) {
    this.config = {
      minConfidence: 0.5,
      maxEntitiesPerMessage: 50,
      enableContextCapture: true,
      contextWindowSize: 50,
      ...config
    };

    this.initializePatterns();
    this.initializeStopWords();
  }

  /**
   * Extract entities from text
   */
  extractEntities(text: string, messageId?: string): ExtractedEntity[] {
    const entities: ExtractedEntity[] = [];
    const processed = new Set<string>();

    // Extract entities using pattern matching
    for (const [entityType, patterns] of this.entityPatterns) {
      for (const pattern of patterns) {
        const matches = this.findMatches(text, pattern, entityType);

        for (const match of matches) {
          const normalized = this.normalizeEntityText(match.text);

          // Skip if already processed, too short, or is a stop word
          if (processed.has(normalized) || 
              normalized.length < 2 || 
              this.stopWords.has(normalized.toLowerCase())) {
            continue;
          }

          processed.add(normalized);

          const entity: ExtractedEntity = {
            text: match.text,
            normalizedText: normalized,
            type: entityType,
            confidence: this.calculateConfidence(match.text, entityType, pattern),
            startPosition: match.startPosition,
            endPosition: match.endPosition,
            extractionMethod: 'pattern'
          };

          // Add context if enabled
          if (this.config.enableContextCapture) {
            entity.context = this.extractContext(text, match.startPosition, match.endPosition);
          }

          // Only include entities above confidence threshold
          if (entity.confidence >= this.config.minConfidence) {
            entities.push(entity);
          }
        }
      }
    }

    // Sort by confidence and position, limit results
    return entities
      .sort((a, b) => {
        if (b.confidence !== a.confidence) {
          return b.confidence - a.confidence;
        }
        return a.startPosition - b.startPosition;
      })
      .slice(0, this.config.maxEntitiesPerMessage);
  }

  /**
   * Initialize entity recognition patterns
   * Based on EntityCentricStrategy patterns but extended for knowledge graph
   */
  private initializePatterns(): void {
    // Person names - enhanced patterns
    this.entityPatterns.set('person', [
      // Full names
      /\b[A-Z][a-z]+ [A-Z][a-z]+(?:\s+[A-Z][a-z]+)?\b/g,
      // Names with middle initial
      /\b[A-Z][a-z]+ [A-Z]\. [A-Z][a-z]+\b/g,
      // Titles with names
      /\b(?:Mr|Ms|Mrs|Dr|Prof|President|CEO|CTO|Director|Manager)\.?\s+[A-Z][a-z]+(?:\s+[A-Z][a-z]+)?\b/g,
      // @mentions (social media style)
      /@([A-Za-z0-9_]+)/g
    ]);

    // Organizations - comprehensive patterns
    this.entityPatterns.set('organization', [
      // Company names with suffixes
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:Inc|Corp|Corporation|LLC|Ltd|Limited|Co|Company|Group|Industries|Systems|Solutions|Technologies|Tech)\b/g,
      // Acronym companies
      /\b[A-Z]{2,}(?:\s+[A-Z][a-z]+)*\b/g,
      // Educational institutions
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:University|College|Institute|School|Academy)\b/g,
      // Government agencies
      /\b(?:Department|Ministry|Agency|Bureau|Office)\s+of\s+[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\b/g
    ]);

    // Products and technologies
    this.entityPatterns.set('product', [
      // Software products with versions
      /\b[A-Z][a-z]+(?:[A-Z][a-z]*)*\s+(?:\d+(?:\.\d+)*|v\d+(?:\.\d+)*)\b/g,
      // Brand names (CamelCase)
      /\b[A-Z][a-z]*[A-Z][A-Za-z]*\b/g,
      // Quoted product names
      /"([A-Z][^"]{2,30})"/g,
      // API/SDK names
      /\b[A-Z][a-z]+(?:[A-Z][a-z]*)*\s*(?:API|SDK|Framework|Library|Tool|Platform)\b/g
    ]);

    // Technical terms and concepts
    this.entityPatterns.set('technical', [
      // Programming languages and technologies
      /\b(?:JavaScript|TypeScript|Python|Java|React|Node\.js|Docker|Kubernetes|AWS|Azure|GCP|SQL|NoSQL|GraphQL|REST|API|HTTP|HTTPS|JSON|XML|HTML|CSS|Git|GitHub|GitLab)\b/g,
      // Function calls
      /\b[a-zA-Z_]\w*\([^)]*\)/g,
      // File extensions and paths
      /\b\w+\.[a-zA-Z]{2,4}\b/g,
      // URLs and domains
      /\b(?:https?:\/\/)?(?:www\.)?[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z]{2,})+/g,
      // Technical acronyms
      /\b[A-Z]{2,6}(?:\s+[A-Z]{2,6})*\b/g
    ]);

    // Locations
    this.entityPatterns.set('location', [
      // City, State/Country
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*,\s*[A-Z]{2,3}\b/g,
      // Street addresses
      /\b\d+\s+[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:Street|St|Avenue|Ave|Road|Rd|Boulevard|Blvd|Drive|Dr|Lane|Ln|Way|Court|Ct)\b/g,
      // Countries and states
      /\b(?:United States|United Kingdom|Canada|Australia|Germany|France|Japan|China|India|Brazil|California|New York|Texas|Florida)\b/g
    ]);

    // Concepts and topics
    this.entityPatterns.set('concept', [
      // Multi-word capitalized concepts
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+){1,3}\b/g,
      // Business terms
      /\b(?:Machine Learning|Artificial Intelligence|Data Science|Cloud Computing|Digital Transformation|Agile|DevOps|Microservices|Blockchain|Cryptocurrency)\b/gi,
      // Quoted concepts
      /"([A-Z][^"]{5,50})"/g
    ]);

    // Events and meetings
    this.entityPatterns.set('event', [
      // Meeting types
      /\b(?:meeting|conference|workshop|seminar|webinar|standup|retrospective|sprint planning|demo|review)\b/gi,
      // Named events
      /\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*\s+(?:Conference|Summit|Expo|Convention|Symposium|Workshop)\b/g,
      // Date-based events
      /\b(?:Q[1-4]|quarter|quarterly|annual|monthly|weekly|daily)\s+[a-z]+\b/gi
    ]);

    // Decisions and outcomes
    this.entityPatterns.set('decision', [
      // Decision language
      /\b(?:decided|decision|resolved|agreed|approved|rejected|postponed|deferred)\s+(?:to|that|on)\s+([^.!?]{5,50})/gi,
      // Action items
      /\b(?:action item|todo|task|follow-up|next step):\s*([^.!?\n]{5,100})/gi,
      // Outcomes
      /\b(?:outcome|result|conclusion|resolution):\s*([^.!?\n]{5,100})/gi
    ]);
  }

  /**
   * Initialize stop words to filter out common words
   */
  private initializeStopWords(): void {
    this.stopWords = new Set([
      'the', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 
      'by', 'from', 'up', 'about', 'into', 'through', 'during', 'before', 
      'after', 'above', 'below', 'between', 'among', 'within', 'without',
      'this', 'that', 'these', 'those', 'i', 'you', 'he', 'she', 'it', 'we', 
      'they', 'me', 'him', 'her', 'us', 'them', 'my', 'your', 'his', 'her', 
      'its', 'our', 'their', 'mine', 'yours', 'ours', 'theirs',
      'a', 'an', 'some', 'any', 'all', 'each', 'every', 'no', 'none', 'one',
      'two', 'three', 'first', 'second', 'last', 'next', 'previous',
      'is', 'am', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has', 
      'had', 'do', 'does', 'did', 'will', 'would', 'could', 'should', 'may', 
      'might', 'must', 'can', 'cannot', 'can\'t', 'won\'t', 'don\'t', 'doesn\'t',
      'when', 'where', 'why', 'how', 'what', 'which', 'who', 'whom', 'whose',
      'if', 'then', 'else', 'while', 'since', 'until', 'unless', 'because', 'so',
      'yes', 'no', 'not', 'now', 'here', 'there', 'today', 'tomorrow', 'yesterday',
      'very', 'more', 'most', 'less', 'least', 'much', 'many', 'few', 'little',
      'good', 'bad', 'better', 'best', 'worse', 'worst', 'new', 'old', 'young',
      'big', 'small', 'large', 'great', 'long', 'short', 'high', 'low'
    ]);
  }

  /**
   * Find pattern matches in text
   */
  private findMatches(text: string, pattern: RegExp, entityType: EntityType): Array<{
    text: string;
    startPosition: number;
    endPosition: number;
  }> {
    const matches: Array<{ text: string; startPosition: number; endPosition: number }> = [];
    let match;

    // Reset regex lastIndex
    pattern.lastIndex = 0;

    while ((match = pattern.exec(text)) !== null) {
      const matchText = match[1] || match[0]; // Use capture group if available

      if (matchText && matchText.trim().length > 0) {
        matches.push({
          text: matchText.trim(),
          startPosition: match.index!,
          endPosition: match.index! + matchText.length
        });
      }

      // Prevent infinite loops on global regexes
      if (!pattern.global) break;
    }

    return matches;
  }

  /**
   * Calculate confidence score for an extracted entity
   */
  private calculateConfidence(text: string, type: EntityType, pattern: RegExp): number {
    let confidence = 0.5; // Base confidence

    // Length-based adjustments
    if (text.length >= 3 && text.length <= 50) {
      confidence += 0.1;
    }
    if (text.length >= 5 && text.length <= 30) {
      confidence += 0.1;
    }

    // Type-specific adjustments
    switch (type) {
      case 'person':
        // Names with titles get higher confidence
        if (/^(?:Mr|Ms|Mrs|Dr|Prof|President|CEO|CTO)\.?\s+/.test(text)) {
          confidence += 0.2;
        }
        // Full names (first + last) get higher confidence
        if (/^[A-Z][a-z]+\s+[A-Z][a-z]+$/.test(text)) {
          confidence += 0.15;
        }
        break;

      case 'organization':
        // Company suffixes increase confidence
        if (/(?:Inc|Corp|LLC|Ltd|Company)$/i.test(text)) {
          confidence += 0.2;
        }
        // Well-known patterns
        if (/^[A-Z]{2,}$/.test(text) && text.length <= 6) {
          confidence += 0.1;
        }
        break;

      case 'technical':
        // Known technical terms get high confidence
        const knownTech = ['JavaScript', 'TypeScript', 'Python', 'React', 'Node.js', 'Docker', 'AWS', 'API', 'HTTP', 'JSON'];
        if (knownTech.some(tech => text.toLowerCase() === tech.toLowerCase())) {
          confidence += 0.3;
        }
        break;

      case 'product':
        // Version numbers increase confidence
        if (/\d+(?:\.\d+)+/.test(text)) {
          confidence += 0.2;
        }
        break;

      case 'location':
        // City, State format gets high confidence
        if (/^[A-Z][a-z]+,\s*[A-Z]{2}$/.test(text)) {
          confidence += 0.3;
        }
        break;
    }

    // Capitalization patterns
    if (/^[A-Z][a-z]+(?:\s+[A-Z][a-z]+)*$/.test(text)) {
      confidence += 0.1; // Proper case
    }

    // Penalize very common words even if they match patterns
    const commonWords = ['Home', 'User', 'Data', 'Test', 'Main', 'New', 'Old', 'First', 'Last'];
    if (commonWords.includes(text)) {
      confidence -= 0.2;
    }

    return Math.max(0.0, Math.min(1.0, confidence));
  }

  /**
   * Normalize entity text for consistent matching
   */
  private normalizeEntityText(text: string): string {
    return text
      .trim()
      .replace(/\s+/g, ' ') // Normalize whitespace
      .toLowerCase();
  }

  /**
   * Extract context around an entity mention
   */
  private extractContext(text: string, startPos: number, endPos: number): string {
    const contextStart = Math.max(0, startPos - this.config.contextWindowSize);
    const contextEnd = Math.min(text.length, endPos + this.config.contextWindowSize);

    return text.substring(contextStart, contextEnd).trim();
  }

  /**
   * Update extraction configuration
   */
  updateConfig(config: Partial<ExtractionConfig>): void {
    this.config = { ...this.config, ...config };
  }

  /**
   * Get current configuration
   */
  getConfig(): ExtractionConfig {
    return { ...this.config };
  }

  /**
   * Add custom pattern for entity type
   */
  addCustomPattern(entityType: EntityType, pattern: RegExp): void {
    if (!this.entityPatterns.has(entityType)) {
      this.entityPatterns.set(entityType, []);
    }
    this.entityPatterns.get(entityType)!.push(pattern);
  }

  /**
   * Get statistics about pattern usage
   */
  getPatternStats(): Record<EntityType, number> {
    const stats: Record<string, number> = {};

    for (const [type, patterns] of this.entityPatterns) {
      stats[type] = patterns.length;
    }

    return stats as Record<EntityType, number>;
  }
}

/**
 * Knowledge Graph Service
 * 
 * Main service that coordinates entity extraction, relationship detection,
 * and graph operations for cross-conversation intelligence.
 */

import { EntityExtractor, ExtractedEntity } from './EntityExtractor.js';
import { RelationshipDetector, DetectedRelationship } from './RelationshipDetector.js';
import { KnowledgeGraphRepository, Entity, EntityRelationship, GraphTraversalResult, EntityCluster } from '../storage/repositories/KnowledgeGraphRepository.js';
import Database from 'better-sqlite3';

/**
 * Message processing result
 */
export interface MessageProcessingResult {
  entitiesExtracted: number;
  relationshipsDetected: number;
  entitiesCreated: number;
  relationshipsCreated: number;
  processingTimeMs: number;
}

/**
 * Cross-conversation analysis result
 */
export interface CrossConversationAnalysis {
  totalEntities: number;
  totalRelationships: number;
  topEntities: Array<{
    entity: Entity;
    relationshipCount: number;
    conversationCount: number;
  }>;
  entityClusters: EntityCluster[];
  temporalPatterns: Array<{
    period: string;
    entityCount: number;
    relationshipCount: number;
    topTopics: string[];
  }>;
}

/**
 * Entity history result
 */
export interface EntityHistory {
  entity: Entity;
  mentions: Array<{
    messageId: string;
    conversationId: string;
    conversationTitle: string;
    content: string;
    mentionText: string;
    createdAt: number;
    confidence: number;
  }>;
  relationships: Array<{
    relatedEntity: Entity;
    relationshipType: string;
    strength: number;
    firstMentioned: number;
    lastMentioned: number;
  }>;
  evolution: Array<{
    evolutionType: string;
    previousValue?: string;
    newValue?: string;
    conversationId: string;
    createdAt: number;
  }>;
}

/**
 * Knowledge graph service configuration
 */
export interface KnowledgeGraphConfig {
  enableAutoProcessing: boolean;
  batchProcessingSize: number;
  maxEntitiesPerMessage: number;
  minEntityConfidence: number;
  minRelationshipConfidence: number;
  enableRelationshipDecay: boolean;
  relationshipDecayDays: number;
}

/**
 * Main knowledge graph service implementation
 */
export class KnowledgeGraphService {
  private entityExtractor: EntityExtractor;
  private relationshipDetector: RelationshipDetector;
  private repository: KnowledgeGraphRepository;
  private config: KnowledgeGraphConfig;

  constructor(db: Database.Database, config: Partial<KnowledgeGraphConfig> = {}) {
    this.config = {
      enableAutoProcessing: true,
      batchProcessingSize: 100,
      maxEntitiesPerMessage: 20,
      minEntityConfidence: 0.5,
      minRelationshipConfidence: 0.4,
      enableRelationshipDecay: true,
      relationshipDecayDays: 30,
      ...config
    };

    this.repository = new KnowledgeGraphRepository(db);

    this.entityExtractor = new EntityExtractor({
      maxEntitiesPerMessage: this.config.maxEntitiesPerMessage,
      minConfidence: this.config.minEntityConfidence
    });

    this.relationshipDetector = new RelationshipDetector({
      minConfidence: this.config.minRelationshipConfidence
    });
  }

  /**
   * Process a message to extract entities and detect relationships
   */
  async processMessage(
    messageId: string,
    conversationId: string,
    content: string,
    createdAt: number
  ): Promise<MessageProcessingResult> {
    const startTime = Date.now();

    try {
      // Extract entities from message content
      const extractedEntities = this.entityExtractor.extractEntities(content, messageId);

      // Create or update entities in the database
      const entityMap = new Map<string, Entity>();
      let entitiesCreated = 0;

      for (const extractedEntity of extractedEntities) {
        const existingEntity = await this.repository.findEntityByName(extractedEntity.normalizedText);

        let entity: Entity;
        if (existingEntity) {
          entity = existingEntity;
        } else {
          entity = await this.repository.createEntity({
            name: extractedEntity.text,
            normalized_name: extractedEntity.normalizedText,
            type: extractedEntity.type,
            confidence_score: extractedEntity.confidence,
            metadata: {
              first_seen_message: messageId,
              first_seen_conversation: conversationId,
              extraction_method: extractedEntity.extractionMethod
            }
          });
          entitiesCreated++;
        }

        entityMap.set(extractedEntity.normalizedText, entity);

        // Create entity mention
        await this.repository.createEntityMention({
          entity_id: entity.id,
          message_id: messageId,
          conversation_id: conversationId,
          mention_text: extractedEntity.text,
          start_position: extractedEntity.startPosition,
          end_position: extractedEntity.endPosition,
          confidence_score: extractedEntity.confidence,
          extraction_method: extractedEntity.extractionMethod
        });
      }

      // Detect relationships between entities
      const detectedRelationships = this.relationshipDetector.detectRelationships(
        extractedEntities,
        content,
        messageId,
        conversationId
      );

      // Create or update relationships
      let relationshipsCreated = 0;
      for (const detectedRelationship of detectedRelationships) {
        const sourceEntity = this.findEntityByExtractedId(detectedRelationship.sourceEntityId, entityMap);
        const targetEntity = this.findEntityByExtractedId(detectedRelationship.targetEntityId, entityMap);

        if (sourceEntity && targetEntity) {
          await this.repository.createOrUpdateRelationship(
            sourceEntity.id,
            targetEntity.id,
            detectedRelationship.relationshipType,
            detectedRelationship.confidence,
            detectedRelationship.contextMessageIds
          );
          relationshipsCreated++;
        }
      }

      const processingTimeMs = Date.now() - startTime;

      return {
        entitiesExtracted: extractedEntities.length,
        relationshipsDetected: detectedRelationships.length,
        entitiesCreated,
        relationshipsCreated,
        processingTimeMs
      };

    } catch (error) {
      console.error('Error processing message for knowledge graph:', error);
      throw error;
    }
  }

  /**
   * Get entity history across conversations
   */
  async getEntityHistory(entityName: string): Promise<EntityHistory | null> {
    const normalizedName = entityName.toLowerCase().trim();
    const entity = await this.repository.findEntityByName(normalizedName);

    if (!entity) {
      return null;
    }

    // Get entity mentions
    const mentions = await this.repository.getEntityMentions(entity.id);

    // Get entity relationships
    const relationships = await this.repository.getEntityRelationships(entity.id);

    // TODO: Get entity evolution (would need to implement evolution tracking)
    const evolution: any[] = [];

    return {
      entity,
      mentions: mentions.map(mention => ({
        messageId: mention.id,
        conversationId: mention.conversation_id,
        conversationTitle: mention.conversation_title,
        content: mention.content,
        mentionText: mention.mention_text,
        createdAt: mention.created_at,
        confidence: mention.confidence_score
      })),
      relationships: relationships.map(rel => ({
        relatedEntity: {
          id: rel.source_entity_id === entity.id ? rel.target_entity_id : rel.source_entity_id,
          name: rel.source_entity_id === entity.id ? rel.target_name : rel.source_name
        } as Entity,
        relationshipType: rel.relationship_type,
        strength: rel.strength,
        firstMentioned: rel.first_mentioned_at,
        lastMentioned: rel.last_mentioned_at
      })),
      evolution
    };
  }

  /**
   * Find conversations related to specific entities
   */
  async findRelatedConversations(
    entityNames: string[],
    options: {
      minRelationshipStrength?: number;
      timeRange?: { start: number; end: number };
      relationshipTypes?: string[];
      limit?: number;
    } = {}
  ): Promise<Array<{
    conversationId: string;
    conversationTitle: string;
    relevanceScore: number;
    relatedEntities: string[];
    relationshipCount: number;
  }>> {
    const {
      minRelationshipStrength = 0.3,
      limit = 50
    } = options;

    // Find entities by names
    const entities = await Promise.all(
      entityNames.map(name => this.repository.findEntityByName(name.toLowerCase().trim()))
    );

    const validEntities = entities.filter(e => e !== null) as Entity[];
    if (validEntities.length === 0) {
      return [];
    }

    // Find connected entities
    const connectedEntityIds = new Set<string>();
    for (const entity of validEntities) {
      const connected = await this.repository.findConnectedEntities(
        entity.id,
        2, // 2 degrees of separation
        minRelationshipStrength
      );

      connectedEntityIds.add(entity.id);
      connected.forEach(c => connectedEntityIds.add(c.entity_id));
    }

    // TODO: Implement conversation relevance query
    // This would require a complex query joining entity_mentions, conversations, and relationships
    // For now, returning empty array as placeholder
    return [];
  }

  /**
   * Get knowledge graph analysis across all conversations
   */
  async getCrossConversationAnalysis(): Promise<CrossConversationAnalysis> {
    // Get total counts
    const totalEntities = await this.repository.db.prepare('SELECT COUNT(*) as count FROM entities').get() as { count: number };
    const totalRelationships = await this.repository.db.prepare('SELECT COUNT(*) as count FROM entity_relationships').get() as { count: number };

    // Get top entities by mention count and relationships
    const topEntitiesQuery = `
      SELECT 
        e.*,
        COUNT(DISTINCT r1.target_entity_id) + COUNT(DISTINCT r2.source_entity_id) as relationship_count,
        COUNT(DISTINCT em.conversation_id) as conversation_count
      FROM entities e
      LEFT JOIN entity_relationships r1 ON e.id = r1.source_entity_id
      LEFT JOIN entity_relationships r2 ON e.id = r2.target_entity_id
      LEFT JOIN entity_mentions em ON e.id = em.entity_id
      GROUP BY e.id
      ORDER BY relationship_count DESC, e.mention_count DESC
      LIMIT 20
    `;

    const topEntitiesRows = this.repository.db.prepare(topEntitiesQuery).all() as any[];
    const topEntities = topEntitiesRows.map(row => ({
      entity: this.mapRowToEntity(row),
      relationshipCount: row.relationship_count || 0,
      conversationCount: row.conversation_count || 0
    }));

    // Get entity clusters
    const entityClusters = await this.repository.findEntityClusters();

    // TODO: Implement temporal patterns analysis
    const temporalPatterns: any[] = [];

    return {
      totalEntities: totalEntities.count,
      totalRelationships: totalRelationships.count,
      topEntities,
      entityClusters,
      temporalPatterns
    };
  }

  /**
   * Search entities and relationships
   */
  async searchKnowledgeGraph(
    query: string,
    options: {
      includeEntities?: boolean;
      includeRelationships?: boolean;
      maxDegrees?: number;
      minStrength?: number;
      limit?: number;
    } = {}
  ): Promise<{
    entities: Entity[];
    connectedEntities: GraphTraversalResult[];
    relationships: EntityRelationship[];
  }> {
    const {
      includeEntities = true,
      includeRelationships = true,
      maxDegrees = 2,
      minStrength = 0.3,
      limit = 50
    } = options;

    const result = {
      entities: [] as Entity[],
      connectedEntities: [] as GraphTraversalResult[],
      relationships: [] as EntityRelationship[]
    };

    if (includeEntities) {
      result.entities = await this.repository.searchEntities(query, limit);
    }

    // For each found entity, get connected entities
    if (result.entities.length > 0 && includeRelationships) {
      const connectedEntitySets = await Promise.all(
        result.entities.slice(0, 5).map(entity => // Limit to first 5 to avoid performance issues
          this.repository.findConnectedEntities(entity.id, maxDegrees, minStrength)
        )
      );

      result.connectedEntities = connectedEntitySets.flat();
    }

    return result;
  }

  /**
   * Get shortest path between two entities
   */
  async getEntityPath(sourceEntityName: string, targetEntityName: string): Promise<GraphTraversalResult | null> {
    const sourceEntity = await this.repository.findEntityByName(sourceEntityName.toLowerCase().trim());
    const targetEntity = await this.repository.findEntityByName(targetEntityName.toLowerCase().trim());

    if (!sourceEntity || !targetEntity) {
      return null;
    }

    return this.repository.findShortestPath(sourceEntity.id, targetEntity.id);
  }

  /**
   * Batch process multiple messages
   */
  async batchProcessMessages(
    messages: Array<{
      messageId: string;
      conversationId: string;
      content: string;
      createdAt: number;
    }>
  ): Promise<MessageProcessingResult[]> {
    const results: MessageProcessingResult[] = [];
    const batchSize = this.config.batchProcessingSize;

    for (let i = 0; i < messages.length; i += batchSize) {
      const batch = messages.slice(i, i + batchSize);

      const batchResults = await Promise.all(
        batch.map(msg => 
          this.processMessage(msg.messageId, msg.conversationId, msg.content, msg.createdAt)
        )
      );

      results.push(...batchResults);
    }

    return results;
  }

  /**
   * Update service configuration
   */
  updateConfig(config: Partial<KnowledgeGraphConfig>): void {
    this.config = { ...this.config, ...config };

    // Update sub-component configurations
    this.entityExtractor.updateConfig({
      maxEntitiesPerMessage: this.config.maxEntitiesPerMessage,
      minConfidence: this.config.minEntityConfidence
    });

    this.relationshipDetector.updateConfig({
      minConfidence: this.config.minRelationshipConfidence
    });
  }

  /**
   * Get service statistics
   */
  async getServiceStats(): Promise<{
    totalEntities: number;
    totalRelationships: number;
    totalMentions: number;
    entityTypes: Record<string, number>;
    relationshipTypes: Record<string, number>;
    extractorStats: Record<string, number>;
    detectorStats: Record<string, number>;
  }> {
    const totalEntities = await this.repository.db.prepare('SELECT COUNT(*) as count FROM entities').get() as { count: number };
    const totalRelationships = await this.repository.db.prepare('SELECT COUNT(*) as count FROM entity_relationships').get() as { count: number };
    const totalMentions = await this.repository.db.prepare('SELECT COUNT(*) as count FROM entity_mentions').get() as { count: number };

    const entityTypesRows = this.repository.db.prepare('SELECT type, COUNT(*) as count FROM entities GROUP BY type').all() as any[];
    const entityTypes = Object.fromEntries(entityTypesRows.map(row => [row.type, row.count]));

    const relationshipTypesRows = this.repository.db.prepare('SELECT relationship_type, COUNT(*) as count FROM entity_relationships GROUP BY relationship_type').all() as any[];
    const relationshipTypes = Object.fromEntries(relationshipTypesRows.map(row => [row.relationship_type, row.count]));

    return {
      totalEntities: totalEntities.count,
      totalRelationships: totalRelationships.count,
      totalMentions: totalMentions.count,
      entityTypes,
      relationshipTypes,
      extractorStats: this.entityExtractor.getPatternStats(),
      detectorStats: this.relationshipDetector.getPatternStats()
    };
  }

  /**
   * Helper method to find entity by extracted entity ID
   */
  private findEntityByExtractedId(extractedId: string, entityMap: Map<string, Entity>): Entity | null {
    // The extracted ID format is: entity_{type}_{base64_normalized_name}
    // We need to decode it to find the matching entity
    const parts = extractedId.split('_');
    if (parts.length < 3) return null;

    const base64Name = parts.slice(2).join('_');
    try {
      const normalizedName = Buffer.from(base64Name, 'base64').toString('utf8');
      return entityMap.get(normalizedName) || null;
    } catch {
      return null;
    }
  }

  /**
   * Helper method to map database row to Entity object
   */
  private mapRowToEntity(row: any): Entity {
    return {
      id: row.id,
      name: row.name,
      normalized_name: row.normalized_name,
      type: row.type,
      canonical_form: row.canonical_form,
      confidence_score: row.confidence_score,
      created_at: row.created_at,
      updated_at: row.updated_at,
      metadata: JSON.parse(row.metadata || '{}'),
      mention_count: row.mention_count,
      last_mentioned_at: row.last_mentioned_at
    };
  }
}

/**
 * Relationship Detection Service
 * 
 * Analyzes entity co-occurrences and contextual patterns to detect
 * relationships between entities across conversations.
 */

import { EntityType, RelationshipType } from '../storage/repositories/KnowledgeGraphRepository.js';
import { ExtractedEntity } from './EntityExtractor.js';

/**
 * Detected relationship between entities
 */
export interface DetectedRelationship {
  sourceEntityId: string;
  targetEntityId: string;
  relationshipType: RelationshipType;
  confidence: number;
  evidence: string[];
  contextMessageIds: string[];
}

/**
 * Co-occurrence information for entity pairs
 */
interface EntityCooccurrence {
  entity1: ExtractedEntity;
  entity2: ExtractedEntity;
  distance: number; // Character distance between entities
  sentenceDistance: number; // Sentence distance
  context: string;
  messageId: string;
  conversationId: string;
}

/**
 * Relationship pattern for detection
 */
interface RelationshipPattern {
  type: RelationshipType;
  patterns: RegExp[];
  entityTypes: [EntityType[], EntityType[]]; // [source types, target types]
  contextWindow: number;
  minConfidence: number;
}

/**
 * Relationship detection configuration
 */
export interface RelationshipDetectionConfig {
  maxEntityDistance: number;
  maxSentenceDistance: number;
  minCooccurrenceCount: number;
  minConfidence: number;
  enableTemporalAnalysis: boolean;
  temporalWindowDays: number;
}

/**
 * Relationship detector implementation
 */
export class RelationshipDetector {
  private relationshipPatterns: RelationshipPattern[] = [];
  private config: RelationshipDetectionConfig;

  constructor(config: Partial<RelationshipDetectionConfig> = {}) {
    this.config = {
      maxEntityDistance: 200, // characters
      maxSentenceDistance: 2, // sentences
      minCooccurrenceCount: 2,
      minConfidence: 0.4,
      enableTemporalAnalysis: true,
      temporalWindowDays: 30,
      ...config
    };

    this.initializePatterns();
  }

  /**
   * Detect relationships between entities in text
   */
  detectRelationships(
    entities: ExtractedEntity[],
    text: string,
    messageId: string,
    conversationId: string
  ): DetectedRelationship[] {
    const relationships: DetectedRelationship[] = [];

    // Find entity co-occurrences
    const cooccurrences = this.findEntityCooccurrences(entities, text, messageId, conversationId);

    // Analyze each co-occurrence for potential relationships
    for (const cooccurrence of cooccurrences) {
      const detectedRelationships = this.analyzeCooccurrence(cooccurrence);
      relationships.push(...detectedRelationships);
    }

    // Remove duplicates and merge evidence
    return this.mergeRelationships(relationships);
  }

  /**
   * Initialize relationship detection patterns
   */
  private initializePatterns(): void {
    this.relationshipPatterns = [
      // Work relationships
      {
        type: 'works_for',
        patterns: [
          /(\w+)\s+(?:works? (?:for|at)|employed (?:by|at)|hired by)\s+(\w+)/gi,
          /(\w+)\s+(?:is|was)\s+(?:a|an|the)\s+[\w\s]*(?:employee|worker|staff|member)\s+(?:of|at)\s+(\w+)/gi,
          /(\w+)['']s?\s+(?:employee|worker|staff|team member)\s+(\w+)/gi
        ],
        entityTypes: [['person'], ['organization']],
        contextWindow: 100,
        minConfidence: 0.7
      },

      // Creation relationships
      {
        type: 'created_by',
        patterns: [
          /(\w+)\s+(?:created|developed|built|designed|authored|wrote)\s+(?:by\s+)?(\w+)/gi,
          /(\w+)\s+(?:is|was)\s+(?:created|developed|built|designed)\s+by\s+(\w+)/gi,
          /(\w+)['']s?\s+(?:creation|development|product)\s+(\w+)/gi
        ],
        entityTypes: [['product', 'technical'], ['person', 'organization']],
        contextWindow: 150,
        minConfidence: 0.8
      },

      // Discussion relationships
      {
        type: 'discussed_with',
        patterns: [
          /(?:discussed|talked|spoke|met)\s+(?:with|to)\s+(\w+)\s+about\s+(\w+)/gi,
          /(\w+)\s+and\s+(\w+)\s+(?:discussed|talked about|reviewed)/gi,
          /(?:conversation|meeting|discussion)\s+(?:with|between)\s+(\w+)\s+and\s+(\w+)/gi
        ],
        entityTypes: [['person'], ['person']],
        contextWindow: 200,
        minConfidence: 0.6
      },

      // Part-of relationships
      {
        type: 'part_of',
        patterns: [
          /(\w+)\s+(?:is|was)\s+(?:a )?(?:part of|component of|module of|feature of)\s+(\w+)/gi,
          /(\w+)['']s?\s+(\w+)\s+(?:component|module|feature|part)/gi,
          /(\w+)\s+(?:belongs to|is part of)\s+(\w+)/gi
        ],
        entityTypes: [['technical', 'product', 'concept'], ['technical', 'product', 'concept']],
        contextWindow: 100,
        minConfidence: 0.7
      },

      // Related-to (general relationships)
      {
        type: 'related_to',
        patterns: [
          /(\w+)\s+(?:and|with|alongside)\s+(\w+)/gi,
          /(?:both|either)\s+(\w+)\s+(?:and|or)\s+(\w+)/gi,
          /(\w+)(?:,|\s+as well as)\s+(\w+)/gi
        ],
        entityTypes: [['concept', 'technical', 'product'], ['concept', 'technical', 'product']],
        contextWindow: 50,
        minConfidence: 0.3
      },

      // Mentioned together (co-occurrence)
      {
        type: 'mentioned_with',
        patterns: [],
        entityTypes: [['person', 'organization', 'product', 'concept'], ['person', 'organization', 'product', 'concept']],
        contextWindow: 300,
        minConfidence: 0.2
      },

      // Temporal sequences
      {
        type: 'temporal_sequence',
        patterns: [
          /(\w+)\s+(?:before|after|then|next|following)\s+(\w+)/gi,
          /(?:first|initially)\s+(\w+)(?:,|\s+then)\s+(\w+)/gi,
          /(\w+)\s+(?:led to|resulted in|caused)\s+(\w+)/gi
        ],
        entityTypes: [['event', 'decision', 'concept'], ['event', 'decision', 'concept']],
        contextWindow: 150,
        minConfidence: 0.6
      },

      // Cause-effect relationships
      {
        type: 'cause_effect',
        patterns: [
          /(\w+)\s+(?:caused|led to|resulted in|triggered)\s+(\w+)/gi,
          /(?:because of|due to)\s+(\w+)(?:,|\s+we|\s+the)\s+(\w+)/gi,
          /(\w+)\s+(?:impact(?:ed)?|affect(?:ed)?|influenced)\s+(\w+)/gi
        ],
        entityTypes: [['event', 'decision', 'concept'], ['event', 'decision', 'concept']],
        contextWindow: 200,
        minConfidence: 0.7
      }
    ];
  }

  /**
   * Find entity co-occurrences within reasonable distance
   */
  private findEntityCooccurrences(
    entities: ExtractedEntity[],
    text: string,
    messageId: string,
    conversationId: string
  ): EntityCooccurrence[] {
    const cooccurrences: EntityCooccurrence[] = [];
    const sentences = this.splitIntoSentences(text);

    for (let i = 0; i < entities.length; i++) {
      for (let j = i + 1; j < entities.length; j++) {
        const entity1 = entities[i];
        const entity2 = entities[j];

        // Calculate distances
        const charDistance = Math.abs(entity1.startPosition - entity2.startPosition);
        const sentenceDistance = this.calculateSentenceDistance(entity1, entity2, sentences);

        // Skip if entities are too far apart
        if (charDistance > this.config.maxEntityDistance || 
            sentenceDistance > this.config.maxSentenceDistance) {
          continue;
        }

        // Extract context around both entities
        const contextStart = Math.min(entity1.startPosition, entity2.startPosition) - 50;
        const contextEnd = Math.max(entity1.endPosition, entity2.endPosition) + 50;
        const context = text.substring(
          Math.max(0, contextStart),
          Math.min(text.length, contextEnd)
        ).trim();

        cooccurrences.push({
          entity1,
          entity2,
          distance: charDistance,
          sentenceDistance,
          context,
          messageId,
          conversationId
        });
      }
    }

    return cooccurrences;
  }

  /**
   * Analyze a co-occurrence for potential relationships
   */
  private analyzeCooccurrence(cooccurrence: EntityCooccurrence): DetectedRelationship[] {
    const relationships: DetectedRelationship[] = [];

    for (const pattern of this.relationshipPatterns) {
      // Check if entity types match pattern requirements
      if (!this.entityTypesMatch(cooccurrence.entity1.type, cooccurrence.entity2.type, pattern)) {
        continue;
      }

      let confidence = this.calculateBaseConfidence(cooccurrence, pattern);
      let evidence: string[] = [];

      // Pattern-based analysis
      if (pattern.patterns.length > 0) {
        const patternMatches = this.findPatternMatches(cooccurrence.context, pattern.patterns);
        if (patternMatches.length === 0) {
          continue; // No pattern match found
        }

        confidence *= 1.2; // Boost confidence for explicit patterns
        evidence = patternMatches;
      }

      // Additional confidence adjustments
      confidence = this.adjustConfidenceByContext(confidence, cooccurrence, pattern);

      if (confidence >= pattern.minConfidence) {
        // Determine relationship direction
        const [sourceEntity, targetEntity] = this.determineRelationshipDirection(
          cooccurrence.entity1,
          cooccurrence.entity2,
          pattern
        );

        relationships.push({
          sourceEntityId: this.generateEntityId(sourceEntity),
          targetEntityId: this.generateEntityId(targetEntity),
          relationshipType: pattern.type,
          confidence,
          evidence,
          contextMessageIds: [cooccurrence.messageId]
        });
      }
    }

    return relationships;
  }

  /**
   * Check if entity types match pattern requirements
   */
  private entityTypesMatch(
    type1: EntityType,
    type2: EntityType,
    pattern: RelationshipPattern
  ): boolean {
    const [sourceTypes, targetTypes] = pattern.entityTypes;

    return (sourceTypes.includes(type1) && targetTypes.includes(type2)) ||
           (sourceTypes.includes(type2) && targetTypes.includes(type1));
  }

  /**
   * Calculate base confidence for relationship
   */
  private calculateBaseConfidence(
    cooccurrence: EntityCooccurrence,
    pattern: RelationshipPattern
  ): number {
    let confidence = 0.3; // Base confidence

    // Distance-based adjustments
    const normalizedDistance = cooccurrence.distance / this.config.maxEntityDistance;
    confidence += (1 - normalizedDistance) * 0.2; // Closer entities get higher confidence

    // Sentence distance adjustment
    if (cooccurrence.sentenceDistance === 0) {
      confidence += 0.2; // Same sentence
    } else if (cooccurrence.sentenceDistance === 1) {
      confidence += 0.1; // Adjacent sentences
    }

    // Entity confidence adjustment
    const avgEntityConfidence = (cooccurrence.entity1.confidence + cooccurrence.entity2.confidence) / 2;
    confidence += avgEntityConfidence * 0.2;

    return Math.min(1.0, confidence);
  }

  /**
   * Adjust confidence based on contextual factors
   */
  private adjustConfidenceByContext(
    baseConfidence: number,
    cooccurrence: EntityCooccurrence,
    pattern: RelationshipPattern
  ): number {
    let confidence = baseConfidence;

    // Context quality indicators
    const context = cooccurrence.context.toLowerCase();

    // Positive indicators
    const positiveIndicators = [
      'specifically', 'particularly', 'exactly', 'precisely', 'clearly',
      'definitely', 'certainly', 'obviously', 'indeed', 'actually'
    ];

    // Negative indicators
    const negativeIndicators = [
      'maybe', 'perhaps', 'possibly', 'might', 'could', 'potentially',
      'not', 'never', 'rarely', 'unlikely', 'uncertain'
    ];

    // Check for positive indicators
    const positiveCount = positiveIndicators.filter(indicator => 
      context.includes(indicator)
    ).length;

    // Check for negative indicators
    const negativeCount = negativeIndicators.filter(indicator => 
      context.includes(indicator)
    ).length;

    confidence += positiveCount * 0.1;
    confidence -= negativeCount * 0.15;

    // Question context reduces confidence
    if (context.includes('?')) {
      confidence *= 0.8;
    }

    // Hypothetical context reduces confidence
    if (context.includes('if ') || context.includes('would ') || context.includes('could ')) {
      confidence *= 0.7;
    }

    return Math.max(0.0, Math.min(1.0, confidence));
  }

  /**
   * Find pattern matches in context
   */
  private findPatternMatches(context: string, patterns: RegExp[]): string[] {
    const matches: string[] = [];

    for (const pattern of patterns) {
      const patternMatches = context.match(pattern);
      if (patternMatches) {
        matches.push(...patternMatches);
      }
    }

    return matches;
  }

  /**
   * Determine relationship direction based on entity types and patterns
   */
  private determineRelationshipDirection(
    entity1: ExtractedEntity,
    entity2: ExtractedEntity,
    pattern: RelationshipPattern
  ): [ExtractedEntity, ExtractedEntity] {
    const [sourceTypes, targetTypes] = pattern.entityTypes;

    // Check if entity1 should be source
    if (sourceTypes.includes(entity1.type) && targetTypes.includes(entity2.type)) {
      return [entity1, entity2];
    }

    // Check if entity2 should be source
    if (sourceTypes.includes(entity2.type) && targetTypes.includes(entity1.type)) {
      return [entity2, entity1];
    }

    // Default: earlier entity is source
    return entity1.startPosition < entity2.startPosition ? [entity1, entity2] : [entity2, entity1];
  }

  /**
   * Split text into sentences
   */
  private splitIntoSentences(text: string): string[] {
    return text.split(/[.!?]+/).map(s => s.trim()).filter(s => s.length > 0);
  }

  /**
   * Calculate sentence distance between two entities
   */
  private calculateSentenceDistance(
    entity1: ExtractedEntity,
    entity2: ExtractedEntity,
    sentences: string[]
  ): number {
    let sentence1 = -1;
    let sentence2 = -1;
    let currentPos = 0;

    for (let i = 0; i < sentences.length; i++) {
      const sentenceEnd = currentPos + sentences[i].length;

      if (sentence1 === -1 && entity1.startPosition >= currentPos && entity1.startPosition <= sentenceEnd) {
        sentence1 = i;
      }

      if (sentence2 === -1 && entity2.startPosition >= currentPos && entity2.startPosition <= sentenceEnd) {
        sentence2 = i;
      }

      if (sentence1 !== -1 && sentence2 !== -1) {
        break;
      }

      currentPos = sentenceEnd + 1; // +1 for sentence delimiter
    }

    return sentence1 !== -1 && sentence2 !== -1 ? Math.abs(sentence1 - sentence2) : this.config.maxSentenceDistance + 1;
  }

  /**
   * Generate consistent entity ID from extracted entity
   */
  private generateEntityId(entity: ExtractedEntity): string {
    return `entity_${entity.type}_${Buffer.from(entity.normalizedText).toString('base64').replace(/[^a-zA-Z0-9]/g, '')}`;
  }

  /**
   * Merge relationships with same source, target, and type
   */
  private mergeRelationships(relationships: DetectedRelationship[]): DetectedRelationship[] {
    const merged = new Map<string, DetectedRelationship>();

    for (const relationship of relationships) {
      const key = `${relationship.sourceEntityId}_${relationship.targetEntityId}_${relationship.relationshipType}`;

      if (merged.has(key)) {
        const existing = merged.get(key)!;
        existing.confidence = Math.max(existing.confidence, relationship.confidence);
        existing.evidence.push(...relationship.evidence);
        existing.contextMessageIds.push(...relationship.contextMessageIds);
      } else {
        merged.set(key, { ...relationship });
      }
    }

    return Array.from(merged.values())
      .filter(r => r.confidence >= this.config.minConfidence)
      .sort((a, b) => b.confidence - a.confidence);
  }

  /**
   * Update detection configuration
   */
  updateConfig(config: Partial<RelationshipDetectionConfig>): void {
    this.config = { ...this.config, ...config };
  }

  /**
   * Add custom relationship pattern
   */
  addCustomPattern(pattern: RelationshipPattern): void {
    this.relationshipPatterns.push(pattern);
  }

  /**
   * Get detection statistics
   */
  getPatternStats(): Record<RelationshipType, number> {
    const stats: Record<string, number> = {};

    for (const pattern of this.relationshipPatterns) {
      stats[pattern.type] = (stats[pattern.type] || 0) + pattern.patterns.length;
    }

    return stats as Record<RelationshipType, number>;
  }
}

/**
 * Context-Aware Alert System
 * 
 * Provides intelligent alerting that considers:
 * - Current system load and conditions
 * - Historical patterns and trends
 * - Alert fatigue prevention
 * - Contextual alert suppression and enhancement
 * - Smart notification routing and prioritization
 */

import { EventEmitter } from 'events';

interface AlertContext {
  systemLoad: {
    cpu: number;
    memory: number;
    io: number;
  };
  recentActivity: {
    queryVolume: number;
    errorRate: number;
    userActivity: number;
  };
  timeContext: {
    hour: number;
    dayOfWeek: number;
    isBusinessHours: boolean;
    isMaintenanceWindow: boolean;
  };
  historicalBaseline: {
    typicalValueAtThisTime: number;
    standardDeviation: number;
    percentileRank: number; // Where current value ranks historically
  };
}

interface SmartAlert {
  id: string;
  originalSeverity: 'low' | 'medium' | 'high' | 'critical';
  adjustedSeverity: 'low' | 'medium' | 'high' | 'critical';
  metric: string;
  category: string;
  value: number;
  threshold: number;
  message: string;
  context: AlertContext;
  suppressionReason?: string;
  enhancementReason?: string;
  actionableInsights: string[];
  predictedDuration: number; // How long this condition might last
  correlatedAlerts: string[]; // IDs of related alerts
  rootCauseAnalysis: {
    suspectedCause: string;
    confidence: number;
    supportingEvidence: string[];
  };
  timestamp: number;
  acknowledged?: boolean;
  resolvedAt?: number;
}

interface AlertPattern {
  pattern: string;
  frequency: number;
  typicalDuration: number;
  commonCauses: Array<{
    cause: string;
    probability: number;
    typicalResolution: string;
  }>;
  seasonality: {
    hourly: number[];
    daily: number[];
    weekly: number[];
  };
}

interface AlertSuppressionRule {
  id: string;
  name: string;
  conditions: Array<{
    metric: string;
    operator: 'and' | 'or';
    checks: Array<{
      field: string; // e.g., 'systemLoad.cpu', 'timeContext.isMaintenanceWindow'
      operator: '>' | '<' | '=' | '!=' | 'between';
      value: any;
    }>;
  }>;
  action: 'suppress' | 'downgrade' | 'delay' | 'enhance';
  reason: string;
  priority: number;
  enabled: boolean;
}

interface NotificationChannel {
  id: string;
  name: string;
  type: 'email' | 'webhook' | 'log' | 'console' | 'sms';
  config: any;
  severityFilter: Array<'low' | 'medium' | 'high' | 'critical'>;
  timeRestrictions?: {
    quietHours: { start: number; end: number }; // Hours of day
    weekendPolicy: 'normal' | 'critical-only' | 'suppress';
  };
  rateLimiting: {
    maxAlertsPerHour: number;
    cooldownMinutes: number;
  };
}

interface AlertCorrelationResult {
  primaryAlert: SmartAlert;
  relatedAlerts: SmartAlert[];
  correlationType: 'cascade' | 'cluster' | 'pattern';
  rootCauseHypothesis: {
    cause: string;
    confidence: number;
    recommendedAction: string;
  };
}

export class ContextAwareAlertSystem extends EventEmitter {
  private activeAlerts: Map<string, SmartAlert> = new Map();
  private alertHistory: SmartAlert[] = [];
  private alertPatterns: Map<string, AlertPattern> = new Map();
  private suppressionRules: Map<string, AlertSuppressionRule> = new Map();
  private notificationChannels: Map<string, NotificationChannel> = new Map();
  private alertFatigueScores: Map<string, number> = new Map(); // Tracks fatigue per alert type

  private readonly MAX_HISTORY_SIZE = 10000;
  private readonly CORRELATION_WINDOW_MINUTES = 30;
  private readonly PATTERN_LEARNING_WINDOW_DAYS = 30;

  constructor() {
    super();
    this.initializeDefaultRules();
    this.initializeDefaultChannels();
  }

  /**
   * Process a raw alert and apply context-aware intelligence
   */
  async processAlert(
    metric: string,
    category: string,
    value: number,
    threshold: number,
    originalSeverity: 'low' | 'medium' | 'high' | 'critical',
    rawMessage: string
  ): Promise<SmartAlert | null> {
    const context = await this.gatherAlertContext(metric, category, value);

    // Create base alert
    const alert: SmartAlert = {
      id: `alert_${category}_${metric}_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      originalSeverity,
      adjustedSeverity: originalSeverity,
      metric,
      category,
      value,
      threshold,
      message: rawMessage,
      context,
      actionableInsights: [],
      predictedDuration: 0,
      correlatedAlerts: [],
      rootCauseAnalysis: {
        suspectedCause: 'Unknown',
        confidence: 0,
        supportingEvidence: []
      },
      timestamp: Date.now()
    };

    // Apply context-aware processing
    await this.applyContextualAdjustments(alert);

    // Check suppression rules
    if (await this.shouldSuppressAlert(alert)) {
      console.log(` Alert suppressed: ${alert.message} (${alert.suppressionReason})`);
      return null; // Alert is suppressed
    }

    // Correlate with existing alerts
    await this.correlateAlert(alert);

    // Generate actionable insights
    this.generateActionableInsights(alert);

    // Predict duration
    alert.predictedDuration = this.predictAlertDuration(alert);

    // Store and process
    this.activeAlerts.set(alert.id, alert);
    this.alertHistory.push(alert);
    this.cleanupHistory();

    // Update patterns for learning
    this.updateAlertPatterns(alert);

    // Route notifications
    await this.routeNotifications(alert);

    console.log(` Context-aware alert: [${alert.adjustedSeverity.toUpperCase()}] ${alert.message}`);
    if (alert.actionableInsights.length > 0) {
      console.log(` Insights: ${alert.actionableInsights.join('; ')}`);
    }

    this.emit('alert:processed', alert);
    return alert;
  }

  /**
   * Gather contextual information for alert processing
   */
  private async gatherAlertContext(metric: string, category: string, value: number): Promise<AlertContext> {
    const now = new Date();
    const hour = now.getHours();
    const dayOfWeek = now.getDay();

    // Get system load (would be injected from monitoring system)
    const systemLoad = {
      cpu: 0.5, // Placeholder - would come from actual monitoring
      memory: 0.6,
      io: 0.4
    };

    const recentActivity = {
      queryVolume: 100, // Placeholder
      errorRate: 0.02,
      userActivity: 50
    };

    const timeContext = {
      hour,
      dayOfWeek,
      isBusinessHours: hour >= 9 && hour <= 17 && dayOfWeek >= 1 && dayOfWeek <= 5,
      isMaintenanceWindow: this.isMaintenanceWindow(now)
    };

    // Calculate historical baseline
    const historicalData = this.getHistoricalDataForMetric(metric, category, now);
    const historicalBaseline = {
      typicalValueAtThisTime: historicalData.mean,
      standardDeviation: historicalData.stdDev,
      percentileRank: this.calculatePercentileRank(value, historicalData.values)
    };

    return {
      systemLoad,
      recentActivity,
      timeContext,
      historicalBaseline
    };
  }

  /**
   * Apply contextual adjustments to alert severity and messaging
   */
  private async applyContextualAdjustments(alert: SmartAlert): Promise<void> {
    let severityAdjustment = 0;
    const adjustmentReasons: string[] = [];

    // System load adjustments
    if (alert.context.systemLoad.cpu > 0.9) {
      severityAdjustment += 1;
      adjustmentReasons.push('High CPU load detected');
    }

    if (alert.context.systemLoad.memory > 0.9) {
      severityAdjustment += 1;
      adjustmentReasons.push('High memory pressure detected');
    }

    // Time-based adjustments
    if (!alert.context.timeContext.isBusinessHours) {
      severityAdjustment -= 1;
      adjustmentReasons.push('Outside business hours');
    }

    if (alert.context.timeContext.isMaintenanceWindow) {
      severityAdjustment -= 2;
      adjustmentReasons.push('During maintenance window');
      alert.suppressionReason = 'Maintenance window active';
    }

    // Historical context adjustments
    if (alert.context.historicalBaseline.percentileRank < 0.8) {
      severityAdjustment -= 1;
      adjustmentReasons.push('Value within historical normal range');
    } else if (alert.context.historicalBaseline.percentileRank > 0.95) {
      severityAdjustment += 1;
      adjustmentReasons.push('Value in extreme historical range');
    }

    // Activity-based adjustments
    if (alert.context.recentActivity.errorRate > 0.1) {
      severityAdjustment += 1;
      adjustmentReasons.push('High recent error rate');
    }

    // Apply severity adjustment
    const severityLevels: Array<'low' | 'medium' | 'high' | 'critical'> = ['low', 'medium', 'high', 'critical'];
    const currentIndex = severityLevels.indexOf(alert.originalSeverity);
    const newIndex = Math.max(0, Math.min(3, currentIndex + severityAdjustment));
    alert.adjustedSeverity = severityLevels[newIndex];

    // Record enhancement or degradation reason
    if (severityAdjustment > 0) {
      alert.enhancementReason = adjustmentReasons.join(', ');
    } else if (severityAdjustment < 0) {
      alert.suppressionReason = adjustmentReasons.join(', ');
    }

    // Update message with context
    if (alert.adjustedSeverity !== alert.originalSeverity) {
      alert.message += ` [Severity adjusted: ${alert.originalSeverity}  ${alert.adjustedSeverity}]`;
    }
  }

  /**
   * Check if alert should be suppressed based on rules
   */
  private async shouldSuppressAlert(alert: SmartAlert): Promise<boolean> {
    // Check alert fatigue
    const fatigueKey = `${alert.category}:${alert.metric}`;
    const fatigueScore = this.alertFatigueScores.get(fatigueKey) || 0;
    if (fatigueScore > 10 && alert.adjustedSeverity !== 'critical') {
      alert.suppressionReason = 'Alert fatigue threshold exceeded';
      return true;
    }

    // Check suppression rules
    for (const rule of this.suppressionRules.values()) {
      if (!rule.enabled) continue;

      if (await this.evaluateSuppressionRule(rule, alert)) {
        if (rule.action === 'suppress') {
          alert.suppressionReason = rule.reason;
          return true;
        } else if (rule.action === 'downgrade') {
          const levels: Array<'low' | 'medium' | 'high' | 'critical'> = ['low', 'medium', 'high', 'critical'];
          const currentIndex = levels.indexOf(alert.adjustedSeverity);
          if (currentIndex > 0) {
            alert.adjustedSeverity = levels[currentIndex - 1];
            alert.suppressionReason = rule.reason;
          }
        }
      }
    }

    return false;
  }

  /**
   * Evaluate suppression rule against alert
   */
  private async evaluateSuppressionRule(rule: AlertSuppressionRule, alert: SmartAlert): Promise<boolean> {
    for (const condition of rule.conditions) {
      let conditionResult = false;

      for (const check of condition.checks) {
        const fieldValue = this.getNestedValue(alert, check.field);
        let checkResult = false;

        switch (check.operator) {
          case '>':
            checkResult = fieldValue > check.value;
            break;
          case '<':
            checkResult = fieldValue < check.value;
            break;
          case '=':
            checkResult = fieldValue === check.value;
            break;
          case '!=':
            checkResult = fieldValue !== check.value;
            break;
          case 'between':
            checkResult = fieldValue >= check.value[0] && fieldValue <= check.value[1];
            break;
        }

        if (condition.operator === 'or' && checkResult) {
          conditionResult = true;
          break;
        } else if (condition.operator === 'and' && !checkResult) {
          conditionResult = false;
          break;
        } else if (condition.operator === 'and') {
          conditionResult = true;
        }
      }

      if (!conditionResult) {
        return false;
      }
    }

    return true;
  }

  /**
   * Correlate alert with existing alerts to find patterns
   */
  private async correlateAlert(alert: SmartAlert): Promise<void> {
    const correlationWindow = Date.now() - (this.CORRELATION_WINDOW_MINUTES * 60 * 1000);
    const recentAlerts = Array.from(this.activeAlerts.values())
      .filter(a => a.timestamp > correlationWindow && a.id !== alert.id);

    // Find related alerts by category
    const categoryAlerts = recentAlerts.filter(a => a.category === alert.category);
    if (categoryAlerts.length > 0) {
      alert.correlatedAlerts.push(...categoryAlerts.map(a => a.id));
    }

    // Find cascade patterns (one alert leading to others)
    const cascadeAlerts = recentAlerts.filter(a => 
      this.isCascadePattern(a.category, a.metric, alert.category, alert.metric)
    );
    if (cascadeAlerts.length > 0) {
      alert.correlatedAlerts.push(...cascadeAlerts.map(a => a.id));
    }

    // Perform root cause analysis if multiple correlated alerts
    if (alert.correlatedAlerts.length > 0) {
      this.performRootCauseAnalysis(alert, recentAlerts);
    }
  }

  /**
   * Perform root cause analysis for correlated alerts
   */
  private performRootCauseAnalysis(alert: SmartAlert, recentAlerts: SmartAlert[]): void {
    const correlatedAlerts = recentAlerts.filter(a => alert.correlatedAlerts.includes(a.id));

    // Simple root cause heuristics
    if (correlatedAlerts.some(a => a.category === 'database' && a.metric.includes('latency'))) {
      if (alert.category === 'memory' || alert.category === 'system') {
        alert.rootCauseAnalysis = {
          suspectedCause: 'Database performance issue causing resource pressure',
          confidence: 0.8,
          supportingEvidence: ['High database latency detected', 'Subsequent resource pressure']
        };
      }
    } else if (correlatedAlerts.some(a => a.category === 'memory')) {
      alert.rootCauseAnalysis = {
        suspectedCause: 'Memory pressure affecting system performance',
        confidence: 0.7,
        supportingEvidence: ['Memory alerts preceding performance degradation']
      };
    } else if (correlatedAlerts.length > 3) {
      alert.rootCauseAnalysis = {
        suspectedCause: 'Cascading system failure or external load spike',
        confidence: 0.6,
        supportingEvidence: [`${correlatedAlerts.length} related alerts within ${this.CORRELATION_WINDOW_MINUTES} minutes`]
      };
    }
  }

  /**
   * Generate actionable insights for the alert
   */
  private generateActionableInsights(alert: SmartAlert): void {
    const insights: string[] = [];

    // Metric-specific insights
    if (alert.metric.includes('latency') || alert.metric.includes('duration')) {
      if (alert.context.systemLoad.cpu > 0.8) {
        insights.push('Check for CPU-intensive operations or consider scaling');
      }
      if (alert.correlatedAlerts.length > 0) {
        insights.push('Multiple performance issues detected - investigate system-wide bottlenecks');
      }
    }

    if (alert.metric.includes('memory')) {
      insights.push('Monitor for memory leaks or increase memory allocation');
      if (alert.value > alert.threshold * 1.2) {
        insights.push('Critical memory usage - immediate cleanup recommended');
      }
    }

    if (alert.category === 'database') {
      insights.push('Review recent queries for optimization opportunities');
      if (alert.context.recentActivity.queryVolume > 1000) {
        insights.push('High query volume detected - consider connection pooling or caching');
      }
    }

    // Context-based insights
    if (!alert.context.timeContext.isBusinessHours && alert.adjustedSeverity === 'critical') {
      insights.push('Critical issue outside business hours - may require immediate attention');
    }

    if (alert.context.historicalBaseline.percentileRank > 0.99) {
      insights.push('Value is in the extreme historical range - unprecedented situation');
    }

    // Pattern-based insights
    const pattern = this.alertPatterns.get(`${alert.category}:${alert.metric}`);
    if (pattern) {
      insights.push(`Typical duration: ${Math.round(pattern.typicalDuration / 60000)} minutes`);
      if (pattern.commonCauses.length > 0) {
        const topCause = pattern.commonCauses[0];
        insights.push(`Common cause: ${topCause.cause} (${topCause.typicalResolution})`);
      }
    }

    alert.actionableInsights = insights;
  }

  /**
   * Predict how long the alert condition might last
   */
  private predictAlertDuration(alert: SmartAlert): number {
    const pattern = this.alertPatterns.get(`${alert.category}:${alert.metric}`);
    if (pattern) {
      return pattern.typicalDuration;
    }

    // Default predictions based on category
    const defaultDurations = {
      database: 10 * 60 * 1000, // 10 minutes
      memory: 30 * 60 * 1000,   // 30 minutes
      search: 5 * 60 * 1000,    // 5 minutes
      system: 15 * 60 * 1000    // 15 minutes
    };

    return defaultDurations[alert.category as keyof typeof defaultDurations] || 15 * 60 * 1000;
  }

  /**
   * Route notifications to appropriate channels
   */
  private async routeNotifications(alert: SmartAlert): Promise<void> {
    for (const channel of this.notificationChannels.values()) {
      if (await this.shouldNotifyChannel(channel, alert)) {
        await this.sendNotification(channel, alert);

        // Update fatigue score
        const fatigueKey = `${alert.category}:${alert.metric}`;
        this.alertFatigueScores.set(fatigueKey, (this.alertFatigueScores.get(fatigueKey) || 0) + 1);
      }
    }
  }

  /**
   * Check if channel should receive this alert
   */
  private async shouldNotifyChannel(channel: NotificationChannel, alert: SmartAlert): Promise<boolean> {
    // Check severity filter
    if (!channel.severityFilter.includes(alert.adjustedSeverity)) {
      return false;
    }

    // Check time restrictions
    if (channel.timeRestrictions) {
      const now = new Date();
      const hour = now.getHours();
      const isWeekend = now.getDay() === 0 || now.getDay() === 6;

      if (channel.timeRestrictions.quietHours &&
          hour >= channel.timeRestrictions.quietHours.start &&
          hour <= channel.timeRestrictions.quietHours.end &&
          alert.adjustedSeverity !== 'critical') {
        return false;
      }

      if (isWeekend && channel.timeRestrictions.weekendPolicy === 'suppress') {
        return false;
      }

      if (isWeekend && 
          channel.timeRestrictions.weekendPolicy === 'critical-only' && 
          alert.adjustedSeverity !== 'critical') {
        return false;
      }
    }

    // Check rate limiting
    const recentNotifications = this.getRecentNotificationsForChannel(channel.id);
    if (recentNotifications >= channel.rateLimiting.maxAlertsPerHour) {
      return false;
    }

    return true;
  }

  /**
   * Send notification through channel
   */
  private async sendNotification(channel: NotificationChannel, alert: SmartAlert): Promise<void> {
    const message = this.formatAlertForChannel(alert, channel);

    switch (channel.type) {
      case 'console':
        console.log(`[${channel.name}] ${message}`);
        break;
      case 'log':
        // Would integrate with logging system
        console.log(`[LOG:${channel.name}] ${message}`);
        break;
      case 'webhook':
        // Would make HTTP request
        console.log(`[WEBHOOK:${channel.name}] ${message}`);
        break;
      default:
        console.log(`[${channel.name}] ${message}`);
    }

    this.emit('notification:sent', { channel: channel.name, alert, message });
  }

  /**
   * Format alert message for specific channel
   */
  private formatAlertForChannel(alert: SmartAlert, channel: NotificationChannel): string {
    let message = `[${alert.adjustedSeverity.toUpperCase()}] ${alert.message}`;

    if (alert.rootCauseAnalysis.confidence > 0.6) {
      message += `\n Suspected cause: ${alert.rootCauseAnalysis.suspectedCause} (${Math.round(alert.rootCauseAnalysis.confidence * 100)}% confidence)`;
    }

    if (alert.actionableInsights.length > 0) {
      message += `\n Insights: ${alert.actionableInsights.slice(0, 3).join('; ')}`;
    }

    if (alert.predictedDuration > 0) {
      const durationMinutes = Math.round(alert.predictedDuration / 60000);
      message += `\n Expected duration: ~${durationMinutes} minutes`;
    }

    return message;
  }

  /**
   * Initialize default suppression rules
   */
  private initializeDefaultRules(): void {
    const defaultRules: AlertSuppressionRule[] = [
      {
        id: 'maintenance_window',
