        enabled: true
      },
      {
        id: 'low_severity_night',
        name: 'Low Severity Night Suppression',
        conditions: [{
          metric: 'any',
          operator: 'and',
          checks: [
            {
              field: 'adjustedSeverity',
              operator: '=',
              value: 'low'
            },
            {
              field: 'context.timeContext.hour',
              operator: 'between',
              value: [23, 6]
            }
          ]
        }],
        action: 'suppress',
        reason: 'Low severity alerts suppressed during night hours',
        priority: 2,
        enabled: true
      }
    ];

    defaultRules.forEach(rule => this.suppressionRules.set(rule.id, rule));
  }

  /**
   * Initialize default notification channels
   */
  private initializeDefaultChannels(): void {
    const defaultChannels: NotificationChannel[] = [
      {
        id: 'console',
        name: 'Console Output',
        type: 'console',
        config: {},
        severityFilter: ['low', 'medium', 'high', 'critical'],
        rateLimiting: {
          maxAlertsPerHour: 100,
          cooldownMinutes: 0
        }
      },
      {
        id: 'critical_only',
        name: 'Critical Alerts Only',
        type: 'log',
        config: {},
        severityFilter: ['critical'],
        timeRestrictions: {
          quietHours: { start: 23, end: 6 },
          weekendPolicy: 'critical-only'
        },
        rateLimiting: {
          maxAlertsPerHour: 10,
          cooldownMinutes: 15
        }
      }
    ];

    defaultChannels.forEach(channel => this.notificationChannels.set(channel.id, channel));
  }

  // Helper methods

  private getNestedValue(obj: any, path: string): any {
    return path.split('.').reduce((current, key) => current?.[key], obj);
  }

  private isMaintenanceWindow(date: Date): boolean {
    // Example: maintenance window is Sunday 2-4 AM
    return date.getDay() === 0 && date.getHours() >= 2 && date.getHours() < 4;
  }

  private isCascadePattern(fromCategory: string, fromMetric: string, toCategory: string, toMetric: string): boolean {
    // Define known cascade patterns
    const cascadePatterns = [
      { from: 'database:latency', to: 'memory:usage' },
      { from: 'memory:pressure', to: 'system:performance' },
      { from: 'search:timeout', to: 'database:connection' }
    ];

    const fromKey = `${fromCategory}:${fromMetric}`;
    const toKey = `${toCategory}:${toMetric}`;

    return cascadePatterns.some(pattern => 
      fromKey.includes(pattern.from) && toKey.includes(pattern.to)
    );
  }

  private getHistoricalDataForMetric(metric: string, category: string, date: Date): {
    mean: number;
    stdDev: number;
    values: number[];
  } {
    // In a real implementation, this would query historical data
    // For now, return reasonable defaults
    return {
      mean: 0.5,
      stdDev: 0.1,
      values: [0.3, 0.4, 0.5, 0.6, 0.7]
    };
  }

  private calculatePercentileRank(value: number, historicalValues: number[]): number {
    const sorted = [...historicalValues].sort((a, b) => a - b);
    const rank = sorted.findIndex(v => v >= value);
    return rank === -1 ? 1 : rank / sorted.length;
  }

  private updateAlertPatterns(alert: SmartAlert): void {
    const key = `${alert.category}:${alert.metric}`;
    let pattern = this.alertPatterns.get(key);

    if (!pattern) {
      pattern = {
        pattern: key,
        frequency: 1,
        typicalDuration: alert.predictedDuration,
        commonCauses: [],
        seasonality: {
          hourly: new Array(24).fill(0),
          daily: new Array(7).fill(0),
          weekly: new Array(52).fill(0)
        }
      };
    } else {
      pattern.frequency++;
    }

    // Update seasonality
    const now = new Date();
    pattern.seasonality.hourly[now.getHours()]++;
    pattern.seasonality.daily[now.getDay()]++;

    this.alertPatterns.set(key, pattern);
  }

  private getRecentNotificationsForChannel(channelId: string): number {
    // Would track notifications in production
    return 0;
  }

  private cleanupHistory(): void {
    if (this.alertHistory.length > this.MAX_HISTORY_SIZE) {
      this.alertHistory = this.alertHistory.slice(-Math.floor(this.MAX_HISTORY_SIZE * 0.8));
    }
  }

  /**
   * Resolve an active alert
   */
  resolveAlert(alertId: string, resolvedBy?: string): boolean {
    const alert = this.activeAlerts.get(alertId);
    if (!alert) return false;

    alert.resolvedAt = Date.now();
    this.activeAlerts.delete(alertId);

    // Reduce alert fatigue score
    const fatigueKey = `${alert.category}:${alert.metric}`;
    const currentScore = this.alertFatigueScores.get(fatigueKey) || 0;
    this.alertFatigueScores.set(fatigueKey, Math.max(0, currentScore - 1));

    console.log(` Alert resolved: ${alert.message} (Duration: ${((alert.resolvedAt - alert.timestamp) / 60000).toFixed(1)} minutes)`);
    this.emit('alert:resolved', alert);

    return true;
  }

  /**
   * Get comprehensive alert system status
   */
  getSystemStatus(): {
    activeAlerts: SmartAlert[];
    recentPatterns: Array<{ pattern: string; frequency: number }>;
    channelStatus: Array<{ channel: string; health: string }>;
    suppressionRules: Array<{ rule: string; enabled: boolean }>;
    fatigueScores: Array<{ metric: string; score: number }>;
  } {
    return {
      activeAlerts: Array.from(this.activeAlerts.values()),
      recentPatterns: Array.from(this.alertPatterns.entries())
        .map(([pattern, data]) => ({ pattern, frequency: data.frequency }))
        .sort((a, b) => b.frequency - a.frequency)
        .slice(0, 10),
      channelStatus: Array.from(this.notificationChannels.values())
        .map(channel => ({ channel: channel.name, health: 'healthy' })),
      suppressionRules: Array.from(this.suppressionRules.values())
        .map(rule => ({ rule: rule.name, enabled: rule.enabled })),
      fatigueScores: Array.from(this.alertFatigueScores.entries())
        .map(([metric, score]) => ({ metric, score }))
        .filter(item => item.score > 0)
        .sort((a, b) => b.score - a.score)
    };
  }
}

/**
 * Dynamic Threshold Management System
 * 
 * Provides adaptive performance thresholds that automatically adjust based on:
 * - System hardware capabilities
 * - Historical performance patterns
 * - Current system load
 * - Environmental conditions
 * - Machine learning optimization
 */

import { EventEmitter } from 'events';
import * as os from 'os';
import * as fs from 'fs/promises';
import * as path from 'path';

interface SystemCapabilities {
  cpuCores: number;
  cpuSpeed: number; // MHz
  totalMemory: number; // bytes
  availableMemory: number; // bytes
  diskSpeed: number; // MB/s (estimated)
  platform: string;
  nodeVersion: string;
  architecture: string;
}

interface PerformanceBaseline {
  metric: string;
  category: string;
  percentile50: number;
  percentile95: number;
  percentile99: number;
  mean: number;
  standardDeviation: number;
  sampleCount: number;
  lastUpdated: number;
}

interface DynamicThreshold {
  id: string;
  metric: string;
  category: string;
  baseValue: number;
  currentValue: number;
  confidence: number; // 0-1, how confident we are in this threshold
  adaptationRate: number; // How quickly it adapts (0-1)
  lastAdjustment: number;
  adjustmentHistory: Array<{
    timestamp: number;
    oldValue: number;
    newValue: number;
    reason: string;
    confidence: number;
  }>;
}

interface SystemContext {
  currentLoad: {
    cpu: number; // 0-1
    memory: number; // 0-1
    io: number; // 0-1 (estimated)
  };
  recentActivity: {
    queryCount: number;
    errorCount: number;
    averageResponseTime: number;
  };
  timeOfDay: number; // 0-23
  dayOfWeek: number; // 0-6
  isUnderLoad: boolean;
}

interface ThresholdOptimizationResult {
  recommendedThresholds: Map<string, number>;
  confidence: number;
  reasoning: string[];
  estimatedImprovement: number;
}

interface MLTrainingData {
  timestamp: number;
  systemContext: SystemContext;
  performanceMetrics: Record<string, number>;
  thresholds: Record<string, number>;
  alertCount: number;
  falsePositiveCount: number;
  missedIssueCount: number;
}

export class DynamicThresholdManager extends EventEmitter {
  private systemCapabilities: SystemCapabilities | null = null;
  private baselines: Map<string, PerformanceBaseline> = new Map();
  private thresholds: Map<string, DynamicThreshold> = new Map();
  private trainingData: MLTrainingData[] = [];
  private contextHistory: SystemContext[] = [];

  private readonly BASELINE_WINDOW_HOURS = 168; // 1 week
  private readonly TRAINING_DATA_RETENTION_DAYS = 30;
  private readonly MIN_SAMPLES_FOR_ADAPTATION = 100;
  private readonly CONFIDENCE_THRESHOLD = 0.7;

  private profilingInterval: NodeJS.Timeout | null = null;
  private optimizationInterval: NodeJS.Timeout | null = null;
  private isInitialized = false;

  constructor(
    private readonly configPath: string = './data/dynamic-thresholds.json'
  ) {
    super();
  }

  /**
   * Initialize the dynamic threshold system
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    console.log(' Initializing Dynamic Threshold Manager...');

    // Profile system capabilities
    await this.profileSystemCapabilities();

    // Load existing data
    await this.loadPersistedData();

    // Initialize default thresholds based on system capabilities
    this.initializeDefaultThresholds();

    // Start continuous monitoring
    this.startContinuousMonitoring();

    this.isInitialized = true;
    console.log(' Dynamic Threshold Manager initialized');
    this.emit('initialized', this.systemCapabilities);
  }

  /**
   * Profile system hardware and software capabilities
   */
  private async profileSystemCapabilities(): Promise<void> {
    console.log(' Profiling system capabilities...');

    const cpus = os.cpus();
    const totalMemory = os.totalmem();
    const freeMemory = os.freemem();

    // Estimate disk speed with a simple benchmark
    const diskSpeed = await this.benchmarkDiskSpeed();

    this.systemCapabilities = {
      cpuCores: cpus.length,
      cpuSpeed: cpus[0]?.speed || 0,
      totalMemory,
      availableMemory: freeMemory,
      diskSpeed,
      platform: os.platform(),
      nodeVersion: process.version,
      architecture: os.arch()
    };

    console.log(' System Profile:', {
      cores: this.systemCapabilities.cpuCores,
      memory: `${Math.round(totalMemory / 1024 / 1024 / 1024)}GB`,
      diskSpeed: `${diskSpeed.toFixed(1)}MB/s`,
      platform: this.systemCapabilities.platform
    });
  }

  /**
   * Benchmark disk I/O performance
   */
  private async benchmarkDiskSpeed(): Promise<number> {
    try {
      const testFile = path.join(os.tmpdir(), 'disk-speed-test.tmp');
      const testData = Buffer.alloc(1024 * 1024, 'x'); // 1MB of data
      const iterations = 10;

      let totalTime = 0;

      for (let i = 0; i < iterations; i++) {
        const startTime = process.hrtime.bigint();
        await fs.writeFile(testFile, testData);
        await fs.readFile(testFile);
        const endTime = process.hrtime.bigint();

        totalTime += Number(endTime - startTime) / 1e6; // Convert to milliseconds
      }

      // Clean up
      try {
        await fs.unlink(testFile);
      } catch (error) {
        // Ignore cleanup errors
      }

      const averageTime = totalTime / iterations;
      const mbPerSecond = (2 * 1024 * 1024) / (averageTime / 1000); // 2MB (read + write) per operation

      return mbPerSecond / (1024 * 1024); // Convert to MB/s

    } catch (error) {
      console.warn(' Could not benchmark disk speed:', error);
      return 100; // Default estimate
    }
  }

  /**
   * Initialize default thresholds based on system capabilities
   */
  private initializeDefaultThresholds(): void {
    if (!this.systemCapabilities) {
      throw new Error('System capabilities not profiled');
    }

    // Calculate capability-adjusted thresholds
    const memoryFactor = Math.min(this.systemCapabilities.totalMemory / (4 * 1024 * 1024 * 1024), 2); // Normalize to 4GB baseline, cap at 2x
    const cpuFactor = Math.min(this.systemCapabilities.cpuCores / 4, 2); // Normalize to 4 cores, cap at 2x
    const diskFactor = Math.min(this.systemCapabilities.diskSpeed / 100, 2); // Normalize to 100MB/s, cap at 2x

    const defaultThresholds = [
      {
        id: 'database_query_time',
        metric: 'query_duration',
        category: 'database',
        baseValue: Math.max(200, 1000 / (cpuFactor * diskFactor)) // Faster systems get lower thresholds
      },
      {
        id: 'search_response_time',
        metric: 'search_duration',
        category: 'search',
        baseValue: Math.max(500, 2000 / cpuFactor)
      },
      {
        id: 'memory_usage_warning',
        metric: 'heap_usage_percent',
        category: 'memory',
        baseValue: Math.min(0.85, 0.6 + (0.2 * memoryFactor)) // More memory allows higher thresholds
      },
      {
        id: 'embedding_generation_time',
        metric: 'embedding_duration',
        category: 'embedding',
        baseValue: Math.max(100, 500 / cpuFactor)
      },
      {
        id: 'cache_miss_rate',
        metric: 'cache_miss_rate',
        category: 'cache',
        baseValue: Math.min(0.4, 0.2 + (0.1 / memoryFactor)) // Less memory = lower miss tolerance
      }
    ];

    for (const threshold of defaultThresholds) {
      this.thresholds.set(threshold.id, {
        ...threshold,
        currentValue: threshold.baseValue,
        confidence: 0.5, // Medium confidence for system-calculated defaults
        adaptationRate: 0.1, // Conservative adaptation
        lastAdjustment: Date.now(),
        adjustmentHistory: []
      });
    }

    console.log(' Initialized adaptive thresholds based on system capabilities');
  }

  /**
   * Get current threshold for a metric
   */
  getThreshold(metricId: string): number | null {
    const threshold = this.thresholds.get(metricId);
    return threshold ? threshold.currentValue : null;
  }

  /**
   * Get all current thresholds
   */
  getAllThresholds(): Map<string, DynamicThreshold> {
    return new Map(this.thresholds);
  }

  /**
   * Update performance baseline with new metric data
   */
  updateBaseline(metric: string, category: string, value: number): void {
    const key = `${category}:${metric}`;
    let baseline = this.baselines.get(key);

    if (!baseline) {
      baseline = {
        metric,
        category,
        percentile50: value,
        percentile95: value,
        percentile99: value,
        mean: value,
        standardDeviation: 0,
        sampleCount: 1,
        lastUpdated: Date.now()
      };
    } else {
      // Exponential weighted moving average for continuous updates
      const alpha = Math.min(0.1, 1.0 / baseline.sampleCount);
      baseline.mean = baseline.mean * (1 - alpha) + value * alpha;

      // Simple running standard deviation approximation
      const variance = Math.pow(value - baseline.mean, 2);
      baseline.standardDeviation = Math.sqrt(
        baseline.standardDeviation * baseline.standardDeviation * (1 - alpha) + variance * alpha
      );

      baseline.sampleCount++;
      baseline.lastUpdated = Date.now();

      // Update percentiles periodically (simplified approach)
      if (baseline.sampleCount % 100 === 0) {
        // These would be updated with actual percentile calculations in a full implementation
        baseline.percentile50 = baseline.mean;
        baseline.percentile95 = baseline.mean + baseline.standardDeviation * 1.645;
        baseline.percentile99 = baseline.mean + baseline.standardDeviation * 2.326;
      }
    }

    this.baselines.set(key, baseline);

    // Trigger threshold adaptation if we have enough samples
    if (baseline.sampleCount >= this.MIN_SAMPLES_FOR_ADAPTATION &&
        baseline.sampleCount % 50 === 0) {
      this.adaptThreshold(metric, category, baseline);
    }
  }

  /**
   * Adapt threshold based on baseline performance
   */
  private adaptThreshold(metric: string, category: string, baseline: PerformanceBaseline): void {
    const thresholdKey = `${category}_${metric}`;
    const threshold = this.thresholds.get(thresholdKey);

    if (!threshold || threshold.confidence < this.CONFIDENCE_THRESHOLD) {
      return;
    }

    // Calculate new threshold based on percentiles and current system context
    const context = this.getCurrentSystemContext();
    let newValue: number;

    // Use different strategies based on metric type
    if (metric.includes('duration') || metric.includes('time')) {
      // For latency metrics, use 95th percentile with context adjustment
      newValue = baseline.percentile95;

      // Adjust for current load
      if (context.isUnderLoad) {
        newValue *= 1.5; // More lenient during high load
      } else {
        newValue *= 0.8; // More strict during normal load
      }

    } else if (metric.includes('usage') || metric.includes('percent')) {
      // For utilization metrics, be more conservative
      newValue = Math.min(baseline.percentile95, baseline.mean + 2 * baseline.standardDeviation);

    } else {
      // For other metrics, use mean + 2 standard deviations
      newValue = baseline.mean + 2 * baseline.standardDeviation;
    }

    // Ensure the new value is reasonable (not too extreme)
    const changeRatio = Math.abs(newValue - threshold.currentValue) / threshold.currentValue;
    if (changeRatio > 0.5) {
      // Cap changes to 50% to avoid extreme swings
      newValue = threshold.currentValue * (newValue > threshold.currentValue ? 1.5 : 0.5);
    }

    // Apply adaptation rate
    const adaptedValue = threshold.currentValue + 
      (newValue - threshold.currentValue) * threshold.adaptationRate;

    // Update threshold
    const oldValue = threshold.currentValue;
    threshold.currentValue = adaptedValue;
    threshold.lastAdjustment = Date.now();
    threshold.confidence = Math.min(0.95, threshold.confidence + 0.05);

    threshold.adjustmentHistory.push({
      timestamp: Date.now(),
      oldValue,
      newValue: adaptedValue,
      reason: `Baseline adaptation: ${baseline.sampleCount} samples, P95=${baseline.percentile95.toFixed(2)}`,
      confidence: threshold.confidence
    });

    // Keep adjustment history manageable
    if (threshold.adjustmentHistory.length > 50) {
      threshold.adjustmentHistory = threshold.adjustmentHistory.slice(-25);
    }

    console.log(` Adapted threshold ${thresholdKey}: ${oldValue.toFixed(2)}  ${adaptedValue.toFixed(2)} (confidence: ${threshold.confidence.toFixed(2)})`);

    this.emit('thresholdAdapted', {
      id: thresholdKey,
      oldValue,
      newValue: adaptedValue,
      confidence: threshold.confidence,
      baseline
    });
  }

  /**
   * Get current system context for threshold adaptation
   */
  private getCurrentSystemContext(): SystemContext {
    const cpuUsage = os.loadavg()[0] / os.cpus().length; // 1-minute load average per core
    const memoryUsage = 1 - (os.freemem() / os.totalmem());

    // Estimate I/O load (simplified)
    const ioLoad = Math.min(1, (cpuUsage + memoryUsage) / 2);

    const now = new Date();
    const timeOfDay = now.getHours();
    const dayOfWeek = now.getDay();

    // Determine if system is under load
    const isUnderLoad = cpuUsage > 0.7 || memoryUsage > 0.8 || ioLoad > 0.7;

    const context: SystemContext = {
      currentLoad: {
        cpu: cpuUsage,
        memory: memoryUsage,
        io: ioLoad
      },
      recentActivity: {
        queryCount: 0, // Would be populated by monitoring system
        errorCount: 0,
        averageResponseTime: 0
      },
      timeOfDay,
      dayOfWeek,
      isUnderLoad
    };

    // Store context history for ML training
    this.contextHistory.push(context);
    if (this.contextHistory.length > 1000) {
      this.contextHistory = this.contextHistory.slice(-500);
    }

    return context;
  }

  /**
   * Optimize thresholds using machine learning approach
   */
  async optimizeThresholds(): Promise<ThresholdOptimizationResult> {
    if (this.trainingData.length < 100) {
      return {
        recommendedThresholds: new Map(),
        confidence: 0,
        reasoning: ['Insufficient training data for ML optimization'],
        estimatedImprovement: 0
      };
    }

    console.log(' Running ML-based threshold optimization...');

    const recommendations: Map<string, number> = new Map();
    const reasoning: string[] = [];

    // Analyze correlation between thresholds and alert quality
    const thresholdPerformance = this.analyzeThresholdPerformance();

    for (const [thresholdId, performance] of thresholdPerformance.entries()) {
      const current = this.thresholds.get(thresholdId);
      if (!current) continue;

      // Simple optimization: minimize false positives while maintaining sensitivity
      const optimalValue = this.findOptimalThreshold(thresholdId, performance);

      if (Math.abs(optimalValue - current.currentValue) / current.currentValue > 0.1) {
        recommendations.set(thresholdId, optimalValue);
        reasoning.push(
          `${thresholdId}: ${current.currentValue.toFixed(2)}  ${optimalValue.toFixed(2)} ` +
          `(FP rate: ${(performance.falsePositiveRate * 100).toFixed(1)}%)`
        );
      }
    }

    const confidence = Math.min(0.9, this.trainingData.length / 1000);
    const estimatedImprovement = recommendations.size * 0.1; // Rough estimate

    console.log(` Optimization complete: ${recommendations.size} recommendations`);

    return {
      recommendedThresholds: recommendations,
      confidence,
      reasoning,
      estimatedImprovement
    };
  }

  /**
   * Analyze threshold performance from training data
   */
  private analyzeThresholdPerformance(): Map<string, {
    falsePositiveRate: number;
    missedIssueRate: number;
    optimalRange: [number, number];
  }> {
    const performance = new Map();

    // Group training data by threshold values
    const thresholdGroups = new Map<string, MLTrainingData[]>();

    for (const data of this.trainingData) {
      for (const [thresholdId, value] of Object.entries(data.thresholds)) {
        if (!thresholdGroups.has(thresholdId)) {
          thresholdGroups.set(thresholdId, []);
        }
        thresholdGroups.get(thresholdId)!.push(data);
      }
    }

    for (const [thresholdId, dataPoints] of thresholdGroups.entries()) {
      const totalAlerts = dataPoints.reduce((sum, d) => sum + d.alertCount, 0);
      const falsePositives = dataPoints.reduce((sum, d) => sum + d.falsePositiveCount, 0);
      const missedIssues = dataPoints.reduce((sum, d) => sum + d.missedIssueCount, 0);

      const falsePositiveRate = totalAlerts > 0 ? falsePositives / totalAlerts : 0;
      const missedIssueRate = missedIssues / dataPoints.length;

      // Calculate optimal range (simplified approach)
      const values = dataPoints.map(d => d.thresholds[thresholdId]).filter(v => v !== undefined);
      values.sort((a, b) => a - b);
      const optimalRange: [number, number] = [
        values[Math.floor(values.length * 0.1)],
        values[Math.floor(values.length * 0.9)]
      ];

      performance.set(thresholdId, {
        falsePositiveRate,
        missedIssueRate,
        optimalRange
      });
    }

    return performance;
  }

  /**
   * Find optimal threshold value using performance data
   */
  private findOptimalThreshold(thresholdId: string, performance: {
    falsePositiveRate: number;
    missedIssueRate: number;
    optimalRange: [number, number];
  }): number {
    // Simple approach: balance false positives and missed issues
    const [min, max] = performance.optimalRange;
    const targetFPRate = 0.1; // 10% false positive rate target

    if (performance.falsePositiveRate > targetFPRate) {
      // Too many false positives, increase threshold (be more lenient)
      return min + (max - min) * 0.7;
    } else if (performance.missedIssueRate > 0.2) {
      // Too many missed issues, decrease threshold (be more strict)
      return min + (max - min) * 0.3;
    }

    // Balanced approach
    return min + (max - min) * 0.5;
  }

  /**
   * Apply ML optimization recommendations
   */
  async applyOptimizationRecommendations(result: ThresholdOptimizationResult): Promise<void> {
    if (result.confidence < 0.6) {
      console.warn(' Optimization confidence too low, skipping application');
      return;
    }

    let appliedCount = 0;

    for (const [thresholdId, recommendedValue] of result.recommendedThresholds.entries()) {
      const threshold = this.thresholds.get(thresholdId);
      if (!threshold) continue;

      const oldValue = threshold.currentValue;
      threshold.currentValue = recommendedValue;
      threshold.lastAdjustment = Date.now();
      threshold.confidence = Math.min(0.95, result.confidence);

      threshold.adjustmentHistory.push({
        timestamp: Date.now(),
        oldValue,
        newValue: recommendedValue,
        reason: `ML optimization (confidence: ${result.confidence.toFixed(2)})`,
        confidence: result.confidence
      });

      appliedCount++;
    }

    console.log(` Applied ${appliedCount} ML optimization recommendations`);
    this.emit('optimizationApplied', { count: appliedCount, confidence: result.confidence });

    // Save updated thresholds
    await this.persistData();
  }

  /**
   * Record training data for ML optimization
   */
  recordTrainingData(
    performanceMetrics: Record<string, number>,
    alertCount: number,
    falsePositiveCount: number = 0,
    missedIssueCount: number = 0
  ): void {
    const context = this.getCurrentSystemContext();
    const currentThresholds: Record<string, number> = {};

    for (const [id, threshold] of this.thresholds.entries()) {
      currentThresholds[id] = threshold.currentValue;
    }

    this.trainingData.push({
      timestamp: Date.now(),
      systemContext: context,
      performanceMetrics,
      thresholds: currentThresholds,
      alertCount,
      falsePositiveCount,
      missedIssueCount
    });

    // Clean up old training data
    const cutoff = Date.now() - (this.TRAINING_DATA_RETENTION_DAYS * 24 * 60 * 60 * 1000);
    this.trainingData = this.trainingData.filter(d => d.timestamp > cutoff);
  }

  /**
   * Get comprehensive threshold report
   */
  getThresholdReport(): {
    systemCapabilities: SystemCapabilities | null;
    currentThresholds: Array<DynamicThreshold & { baseline?: PerformanceBaseline }>;
    recentAdjustments: Array<{
      thresholdId: string;
      adjustment: any;
    }>;
    recommendations: string[];
    systemLoad: SystemContext;
    confidence: number;
  } {
    const currentThresholds = Array.from(this.thresholds.values()).map(threshold => {
      const baselineKey = `${threshold.category}:${threshold.metric}`;
      const baseline = this.baselines.get(baselineKey);
      return { ...threshold, baseline };
    });

    // Get recent adjustments
    const recentAdjustments: Array<{ thresholdId: string; adjustment: any }> = [];
    const oneDayAgo = Date.now() - 24 * 60 * 60 * 1000;

    for (const [id, threshold] of this.thresholds.entries()) {
      const recentAdjustment = threshold.adjustmentHistory
        .filter(adj => adj.timestamp > oneDayAgo)
        .slice(-1)[0];

      if (recentAdjustment) {
        recentAdjustments.push({ thresholdId: id, adjustment: recentAdjustment });
      }
    }

    // Generate recommendations
    const recommendations: string[] = [];
    const avgConfidence = currentThresholds.reduce((sum, t) => sum + t.confidence, 0) / currentThresholds.length;

    if (avgConfidence < 0.7) {
      recommendations.push('Threshold confidence is low - consider running more training cycles');
    }

    const recentlyAdjusted = currentThresholds.filter(t => 
      Date.now() - t.lastAdjustment < 60 * 60 * 1000 // Last hour
    );

    if (recentlyAdjusted.length > 0) {
      recommendations.push(`${recentlyAdjusted.length} thresholds were recently adjusted - monitor for stability`);
    }

    return {
      systemCapabilities: this.systemCapabilities,
      currentThresholds,
      recentAdjustments,
      recommendations,
      systemLoad: this.getCurrentSystemContext(),
      confidence: avgConfidence
    };
  }

  /**
   * Start continuous monitoring and optimization
   */
  private startContinuousMonitoring(): void {
    // Profile system context every 30 seconds
    this.profilingInterval = setInterval(() => {
      this.getCurrentSystemContext();
    }, 30000);

    // Run optimization every hour
    this.optimizationInterval = setInterval(async () => {
      try {
        const result = await this.optimizeThresholds();
        if (result.confidence > 0.7 && result.recommendedThresholds.size > 0) {
          await this.applyOptimizationRecommendations(result);
        }
      } catch (error) {
        console.error(' Error during threshold optimization:', error);
      }
    }, 60 * 60 * 1000); // 1 hour
  }

  /**
   * Persist threshold data to disk
   */
  private async persistData(): Promise<void> {
    try {
      const data = {
        version: '1.0.0',
        timestamp: Date.now(),
        systemCapabilities: this.systemCapabilities,
        thresholds: Array.from(this.thresholds.entries()),
        baselines: Array.from(this.baselines.entries()),
        trainingData: this.trainingData.slice(-1000) // Keep last 1000 entries
      };

      await fs.writeFile(this.configPath, JSON.stringify(data, null, 2));
    } catch (error) {
      console.error(' Failed to persist threshold data:', error);
    }
  }

  /**
   * Load persisted threshold data
   */
  private async loadPersistedData(): Promise<void> {
    try {
      const data = JSON.parse(await fs.readFile(this.configPath, 'utf-8'));

      if (data.version === '1.0.0') {
        if (data.thresholds) {
          this.thresholds = new Map(data.thresholds);
        }
        if (data.baselines) {
          this.baselines = new Map(data.baselines);
        }
        if (data.trainingData) {
          this.trainingData = data.trainingData;
        }

        console.log(` Loaded persisted threshold data (${this.thresholds.size} thresholds, ${this.baselines.size} baselines)`);
      }
    } catch (error) {
      console.log(' No existing threshold data found, starting fresh');
    }
  }

  /**
   * Cleanup resources
   */
  async shutdown(): Promise<void> {
    if (this.profilingInterval) {
      clearInterval(this.profilingInterval);
    }
    if (this.optimizationInterval) {
      clearInterval(this.optimizationInterval);
    }

    // Final data persistence
    await this.persistData();

    console.log(' Dynamic Threshold Manager shut down');
  }
}

/**
 * Enhanced Performance Monitor with Dynamic Thresholds
 * 
 * Integrates all dynamic monitoring components:
 * - Dynamic threshold management
 * - Context-aware alerting
 * - System capability profiling
 * - Machine learning optimization
 * - Adaptive resource management
 */

import { EventEmitter } from 'events';
import { DynamicThresholdManager } from './DynamicThresholdManager.js';
import { ContextAwareAlertSystem } from './ContextAwareAlertSystem.js';
import { SystemCapabilityProfiler } from './SystemCapabilityProfiler.js';
import { DatabaseManager } from '../storage/Database.js';
import { MemoryManager } from '../utils/MemoryManager.js';

interface EnhancedMetric {
  id: string;
  category: 'database' | 'search' | 'embedding' | 'memory' | 'network' | 'system';
  name: string;
  value: number;
  unit: 'ms' | 'bytes' | 'count' | 'percent' | 'rate';
  timestamp: number;
  context: {
    systemLoad: number;
    concurrentOperations: number;
    errorRate: number;
    userActivity: number;
  };
  tags?: Record<string, string>;
}

interface AdaptiveAlert {
  id: string;
  originalThreshold: number;
  adaptiveThreshold: number;
  metric: EnhancedMetric;
  severity: 'low' | 'medium' | 'high' | 'critical';
  contextualSeverity: 'low' | 'medium' | 'high' | 'critical';
  message: string;
  rootCause?: string;
  recommendedAction?: string;
  predictedDuration: number;
  confidence: number;
  timestamp: number;
}

interface SystemHealthAssessment {
  overall: 'healthy' | 'degraded' | 'critical';
  components: {
    database: ComponentHealth;
    search: ComponentHealth;
    memory: ComponentHealth;
    system: ComponentHealth;
  };
  adaptiveMetrics: {
    thresholdAccuracy: number;
    alertReduction: number;
    falsePositiveRate: number;
    systemOptimization: number;
  };
  recommendations: Array<{
    category: string;
    action: string;
    priority: 'low' | 'medium' | 'high';
    estimatedImpact: string;
  }>;
  timestamp: number;
}

interface ComponentHealth {
  status: 'healthy' | 'degraded' | 'critical';
  metrics: Array<{
    name: string;
    current: number;
    threshold: number;
    trend: 'improving' | 'stable' | 'degrading';
  }>;
  lastIssue?: {
    timestamp: number;
    description: string;
    resolved: boolean;
  };
}

export class EnhancedPerformanceMonitor extends EventEmitter {
  private dynamicThresholds: DynamicThresholdManager;
  private contextualAlerts: ContextAwareAlertSystem;
  private systemProfiler: SystemCapabilityProfiler;

  private metrics: Map<string, EnhancedMetric[]> = new Map();
  private activeAlerts: Map<string, AdaptiveAlert> = new Map();
  private componentHealth: Map<string, ComponentHealth> = new Map();

  private isMonitoring = false;
  private monitoringInterval: NodeJS.Timeout | null = null;
  private optimizationInterval: NodeJS.Timeout | null = null;

  private readonly METRICS_RETENTION_HOURS = 48;
  private readonly OPTIMIZATION_INTERVAL_HOURS = 6;
  private readonly HEALTH_CHECK_INTERVAL_SECONDS = 30;

  constructor(
    private readonly dbManager: DatabaseManager,
    private readonly memoryManager: MemoryManager,
    private readonly configPath: string = './data/enhanced-monitoring.json'
  ) {
    super();

    this.dynamicThresholds = new DynamicThresholdManager();
    this.contextualAlerts = new ContextAwareAlertSystem();
    this.systemProfiler = new SystemCapabilityProfiler();

    this.setupEventHandlers();
  }

  /**
   * Initialize the enhanced monitoring system
   */
  async initialize(): Promise<void> {
    console.log(' Initializing Enhanced Performance Monitor...');

    // Initialize all components
    await Promise.all([
      this.systemProfiler.profileSystem(),
      this.dynamicThresholds.initialize()
    ]);

    // Apply system-specific configuration
    await this.applySystemOptimizedSettings();

    // Initialize component health tracking
    this.initializeComponentHealth();

    console.log(' Enhanced Performance Monitor initialized');
    this.emit('initialized', {
      systemProfile: this.systemProfiler.getProfile(),
      thresholds: this.dynamicThresholds.getAllThresholds()
    });
  }

  /**
   * Start enhanced monitoring with adaptive capabilities
   */
  async startMonitoring(): Promise<void> {
    if (this.isMonitoring) return;

    console.log(' Starting enhanced performance monitoring...');

    this.isMonitoring = true;

    // Start periodic monitoring
    this.monitoringInterval = setInterval(async () => {
      await this.performHealthChecks();
      await this.collectEnhancedMetrics();
      this.evaluateAdaptiveAlerts();
      this.updateComponentHealth();
      this.cleanupOldMetrics();
    }, this.HEALTH_CHECK_INTERVAL_SECONDS * 1000);

    // Start optimization cycle
    this.optimizationInterval = setInterval(async () => {
      await this.runOptimizationCycle();
    }, this.OPTIMIZATION_INTERVAL_HOURS * 60 * 60 * 1000);

    console.log(' Enhanced monitoring active');
    this.emit('monitoringStarted');
  }

  /**
   * Stop monitoring
   */
  async stopMonitoring(): Promise<void> {
    if (!this.isMonitoring) return;

    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }

    if (this.optimizationInterval) {
      clearInterval(this.optimizationInterval);
      this.optimizationInterval = null;
    }

    this.isMonitoring = false;

    // Cleanup components
    await this.dynamicThresholds.shutdown();

    console.log(' Enhanced monitoring stopped');
    this.emit('monitoringStopped');
  }

  /**
   * Record an enhanced metric with context
   */
  recordEnhancedMetric(
    category: EnhancedMetric['category'],
    name: string,
    value: number,
    unit: EnhancedMetric['unit'],
    tags?: Record<string, string>
  ): void {
    const context = this.getCurrentSystemContext();

    const metric: EnhancedMetric = {
      id: `${category}_${name}_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      category,
      name,
      value,
      unit,
      timestamp: Date.now(),
      context,
      tags
    };

    const key = `${category}:${name}`;
    if (!this.metrics.has(key)) {
      this.metrics.set(key, []);
    }

    this.metrics.get(key)!.push(metric);

    // Update dynamic threshold baseline
    this.dynamicThresholds.updateBaseline(name, category, value);

    this.emit('metricRecorded', metric);
  }

  /**
   * Get current system context for monitoring
   */
  private getCurrentSystemContext(): EnhancedMetric['context'] {
    const memoryStats = this.memoryManager.getCurrentStats();
    const systemLoad = (memoryStats.heapUsed / memoryStats.heapTotal) * 0.5 + 0.5; // Simplified load calculation

    // Get concurrent operations (would be tracked by the monitoring system)
    const concurrentOperations = this.activeAlerts.size * 2; // Approximation

    // Calculate error rate from recent metrics
    const recentErrorMetrics = this.getRecentMetrics('system', 'error', 5 * 60 * 1000);
    const errorRate = recentErrorMetrics.length / Math.max(1, this.getTotalRecentOperations());

    return {
      systemLoad,
      concurrentOperations,
      errorRate,
      userActivity: 50 // Placeholder - would come from actual tracking
    };
  }

  /**
   * Apply system-optimized settings based on profiling
   */
  private async applySystemOptimizedSettings(): Promise<void> {
    const profile = this.systemProfiler.getProfile();
    if (!profile) return;

    const config = this.systemProfiler.getPerformanceConfig();

    console.log(` Applying ${profile.overallPerformanceClass} performance configuration`);

    // Configure memory manager based on system capabilities
    const memoryConfig = {
      heapWarningThreshold: config.memory.maxHeapUsage * 0.8,
      heapCriticalThreshold: config.memory.maxHeapUsage * 0.95,
      maxRssBytes: profile.memory.recommendedHeapSize,
      gcThreshold: config.memory.gcThreshold,
      monitoringInterval: config.monitoring.metricsCollectionInterval
    };

    // Update monitoring intervals based on system capability
    if (profile.overallPerformanceClass === 'exceptional' || profile.overallPerformanceClass === 'high') {
      // More frequent monitoring for high-performance systems
      if (this.monitoringInterval) {
        clearInterval(this.monitoringInterval);
        this.monitoringInterval = setInterval(async () => {
          await this.performHealthChecks();
          await this.collectEnhancedMetrics();
          this.evaluateAdaptiveAlerts();
          this.updateComponentHealth();
          this.cleanupOldMetrics();
        }, 15000); // 15 seconds for high-performance systems
      }
    }

    this.emit('configurationApplied', config);
  }

  /**
   * Initialize component health tracking
   */
  private initializeComponentHealth(): void {
    const components = ['database', 'search', 'memory', 'system'];

    for (const component of components) {
      this.componentHealth.set(component, {
        status: 'healthy',
        metrics: [],
        lastIssue: undefined
      });
    }
  }

  /**
   * Perform comprehensive health checks
   */
  private async performHealthChecks(): Promise<void> {
    await Promise.all([
      this.checkDatabaseHealth(),
      this.checkMemoryHealth(),
      this.checkSearchHealth(),
      this.checkSystemHealth()
    ]);
  }

  /**
   * Check database health with adaptive thresholds
   */
  private async checkDatabaseHealth(): Promise<void> {
    try {
      const db = this.dbManager.getConnection();

      // Test basic connectivity with timing
      const startTime = Date.now();
      const testResult = db.prepare('SELECT 1 as test').get();
      const queryDuration = Date.now() - startTime;

      // Record metric with context
      this.recordEnhancedMetric('database', 'connectivity_test', queryDuration, 'ms');

      // Get adaptive threshold
      const threshold = this.dynamicThresholds.getThreshold('database_query_time') || 500;

      // Update component health
      const health = this.componentHealth.get('database')!;
      health.metrics = [
        {
          name: 'connectivity_test',
          current: queryDuration,
          threshold,
          trend: this.calculateTrend('database', 'connectivity_test')
        }
      ];

      if (queryDuration > threshold * 2) {
        health.status = 'critical';
        health.lastIssue = {
          timestamp: Date.now(),
          description: `Database connectivity test took ${queryDuration}ms (threshold: ${threshold}ms)`,
          resolved: false
        };
      } else if (queryDuration > threshold) {
        health.status = 'degraded';
      } else {
        health.status = 'healthy';
        if (health.lastIssue && !health.lastIssue.resolved) {
          health.lastIssue.resolved = true;
        }
      }

    } catch (error) {
      const health = this.componentHealth.get('database')!;
      health.status = 'critical';
      health.lastIssue = {
        timestamp: Date.now(),
        description: `Database health check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        resolved: false
      };
    }
  }

  /**
   * Check memory health with context awareness
   */
  private checkMemoryHealth(): void {
    const memoryReport = this.memoryManager.getMemoryReport();
    const { current, pressure } = memoryReport;

    this.recordEnhancedMetric('memory', 'heap_usage', current.heapUsed, 'bytes');
    this.recordEnhancedMetric('memory', 'heap_usage_percent', current.heapUsed / current.heapTotal, 'percent');
    this.recordEnhancedMetric('memory', 'rss', current.rss, 'bytes');

    const health = this.componentHealth.get('memory')!;
    const heapUsagePercent = current.heapUsed / current.heapTotal;

    health.metrics = [
      {
        name: 'heap_usage_percent',
        current: heapUsagePercent,
        threshold: 0.8, // Would use adaptive threshold
        trend: this.calculateTrend('memory', 'heap_usage_percent')
      }
    ];

    // Use pressure level from memory manager
    switch (pressure.level) {
      case 'critical':
        health.status = 'critical';
        break;
      case 'high':
        health.status = 'degraded';
        break;
      default:
        health.status = 'healthy';
    }

    if (pressure.level === 'critical' || pressure.level === 'high') {
      health.lastIssue = {
        timestamp: Date.now(),
        description: pressure.recommendation,
        resolved: false
      };
    }
  }

  /**
   * Check search system health
   */
  private async checkSearchHealth(): Promise<void> {
    try {
      const db = this.dbManager.getConnection();

      // Test FTS functionality
      const startTime = Date.now();
      const ftsTest = db.prepare(`
        SELECT COUNT(*) as count FROM messages_fts 
        WHERE messages_fts MATCH 'test' 
        LIMIT 1
      `).get() as { count: number };
      const searchDuration = Date.now() - startTime;

      this.recordEnhancedMetric('search', 'fts_test', searchDuration, 'ms');

      const threshold = this.dynamicThresholds.getThreshold('search_response_time') || 1000;

      const health = this.componentHealth.get('search')!;
      health.metrics = [
        {
          name: 'fts_test',
          current: searchDuration,
          threshold,
          trend: this.calculateTrend('search', 'fts_test')
        }
      ];

      if (searchDuration > threshold * 1.5) {
        health.status = 'critical';
        health.lastIssue = {
          timestamp: Date.now(),
          description: `Search test took ${searchDuration}ms (threshold: ${threshold}ms)`,
          resolved: false
        };
      } else if (searchDuration > threshold) {
        health.status = 'degraded';
      } else {
        health.status = 'healthy';
      }

    } catch (error) {
      const health = this.componentHealth.get('search')!;
      health.status = 'critical';
      health.lastIssue = {
        timestamp: Date.now(),
        description: `Search health check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        resolved: false
      };
    }
  }

  /**
   * Check overall system health
   */
  private checkSystemHealth(): void {
    const uptime = process.uptime() * 1000;
    const memoryUsage = process.memoryUsage();
    const cpuUsage = process.cpuUsage();

    this.recordEnhancedMetric('system', 'uptime', uptime, 'ms');
    this.recordEnhancedMetric('system', 'cpu_time', cpuUsage.user + cpuUsage.system, 'ms');

    const health = this.componentHealth.get('system')!;
    const heapMB = Math.round(memoryUsage.heapUsed / 1024 / 1024);

    health.metrics = [
      {
        name: 'memory_usage_mb',
        current: heapMB,
        threshold: 500, // 500MB threshold
        trend: this.calculateTrend('system', 'memory_usage')
      }
    ];

    if (heapMB > 1000) {
      health.status = 'critical';
      health.lastIssue = {
        timestamp: Date.now(),
        description: `High system memory usage: ${heapMB}MB`,
        resolved: false
      };
    } else if (heapMB > 500) {
      health.status = 'degraded';
    } else {
      health.status = 'healthy';
    }
  }

  /**
   * Calculate trend for a metric
   */
  private calculateTrend(category: string, metric: string): 'improving' | 'stable' | 'degrading' {
    const key = `${category}:${metric}`;
    const metrics = this.metrics.get(key) || [];

    if (metrics.length < 5) return 'stable';

    const recent = metrics.slice(-10);
    const firstHalf = recent.slice(0, Math.floor(recent.length / 2));
    const secondHalf = recent.slice(Math.floor(recent.length / 2));

    const firstAvg = firstHalf.reduce((sum, m) => sum + m.value, 0) / firstHalf.length;
    const secondAvg = secondHalf.reduce((sum, m) => sum + m.value, 0) / secondHalf.length;

    const change = (secondAvg - firstAvg) / firstAvg;

    if (change < -0.1) return 'improving'; // 10% improvement
    if (change > 0.1) return 'degrading';  // 10% degradation
    return 'stable';
  }

  /**
   * Collect enhanced metrics from all sources
   */
  private async collectEnhancedMetrics(): Promise<void> {
    // System metrics
    const memStats = this.memoryManager.getCurrentStats();
    this.recordEnhancedMetric('memory', 'heap_used', memStats.heapUsed, 'bytes');
    this.recordEnhancedMetric('memory', 'rss', memStats.rss, 'bytes');
    this.recordEnhancedMetric('system', 'uptime', process.uptime() * 1000, 'ms');

    // Database metrics (if available)
    try {
      const db = this.dbManager.getConnection();
      const cacheSize = db.pragma('cache_size', { simple: true });
      this.recordEnhancedMetric('database', 'cache_size', cacheSize as number, 'count');
    } catch (error) {
      // Database not available
    }
  }

  /**
   * Evaluate adaptive alerts using context-aware system
   */
  private evaluateAdaptiveAlerts(): void {
    // Process recent metrics for threshold violations
    const recentWindow = 5 * 60 * 1000; // 5 minutes
    const now = Date.now();

    for (const [key, metrics] of this.metrics.entries()) {
      const recentMetrics = metrics.filter(m => now - m.timestamp <= recentWindow);
      if (recentMetrics.length === 0) continue;

      const [category, metricName] = key.split(':');
      const thresholdKey = `${category}_${metricName}`;
      const threshold = this.dynamicThresholds.getThreshold(thresholdKey);

      if (!threshold) continue;

      // Check for violations
      const violations = recentMetrics.filter(m => {
        if (metricName.includes('percent') || metricName.includes('usage')) {
          return m.value > threshold;
        } else {
          return m.value > threshold;
        }
      });

      if (violations.length > 0) {
        const latestViolation = violations[violations.length - 1];
        this.processPotentialAlert(latestViolation, threshold, thresholdKey);
      }
    }
  }

  /**
   * Process potential alert through context-aware system
   */
  private async processPotentialAlert(
    metric: EnhancedMetric,
    threshold: number,
    thresholdId: string
  ): Promise<void> {
    // Determine base severity
    const exceedanceRatio = metric.value / threshold;
    let severity: 'low' | 'medium' | 'high' | 'critical' = 'low';

    if (exceedanceRatio > 2) severity = 'critical';
    else if (exceedanceRatio > 1.5) severity = 'high';
    else if (exceedanceRatio > 1.2) severity = 'medium';

    const message = `${metric.category} ${metric.name}: ${metric.value.toFixed(2)} ${metric.unit} exceeds threshold ${threshold.toFixed(2)} ${metric.unit}`;

    // Process through context-aware alert system
    const contextualAlert = await this.contextualAlerts.processAlert(
      metric.name,
      metric.category,
      metric.value,
      threshold,
      severity,
      message
    );

    if (contextualAlert) {
      // Convert to adaptive alert format
      const adaptiveAlert: AdaptiveAlert = {
        id: contextualAlert.id,
        originalThreshold: threshold,
        adaptiveThreshold: threshold, // Would be adjusted based on context
        metric,
        severity,
        contextualSeverity: contextualAlert.adjustedSeverity,
        message: contextualAlert.message,
        rootCause: contextualAlert.rootCauseAnalysis.suspectedCause,
        recommendedAction: contextualAlert.actionableInsights[0],
        predictedDuration: contextualAlert.predictedDuration,
        confidence: contextualAlert.rootCauseAnalysis.confidence,
        timestamp: contextualAlert.timestamp
      };

      this.activeAlerts.set(adaptiveAlert.id, adaptiveAlert);

      // Record training data
      this.dynamicThresholds.recordTrainingData(
        { [thresholdId]: metric.value },
        1, // Alert count
        contextualAlert.adjustedSeverity !== severity ? 1 : 0, // False positive if severity was downgraded
        0 // Missed issues (not tracked here)
      );

      this.emit('adaptiveAlert', adaptiveAlert);
    }
  }

  /**
   * Update component health based on recent metrics and alerts
   */
  private updateComponentHealth(): void {
    for (const [component, health] of this.componentHealth.entries()) {
      // Check for recent alerts affecting this component
      const componentAlerts = Array.from(this.activeAlerts.values())
        .filter(alert => alert.metric.category === component);

      if (componentAlerts.length > 0) {
        const criticalAlerts = componentAlerts.filter(a => a.contextualSeverity === 'critical');
        const highAlerts = componentAlerts.filter(a => a.contextualSeverity === 'high');

        if (criticalAlerts.length > 0) {
          health.status = 'critical';
        } else if (highAlerts.length > 0) {
          health.status = 'degraded';
        }
      }

      // Update trends for component metrics
      for (const metric of health.metrics) {
        metric.trend = this.calculateTrend(component, metric.name);
      }
    }
  }

  /**
   * Run optimization cycle
   */
  private async runOptimizationCycle(): Promise<void> {
    console.log(' Running monitoring optimization cycle...');

    try {
      // Run threshold optimization
      const optimizationResult = await this.dynamicThresholds.optimizeThresholds();

      if (optimizationResult.confidence > 0.7) {
        await this.dynamicThresholds.applyOptimizationRecommendations(optimizationResult);
        console.log(` Applied ${optimizationResult.recommendedThresholds.size} threshold optimizations`);
      }

      // Update system profile if significant time has passed
      const profile = this.systemProfiler.getProfile();
      if (profile && Date.now() - profile.timestamp > 7 * 24 * 60 * 60 * 1000) { // 7 days
        console.log(' Refreshing system profile...');
        await this.systemProfiler.forceReprofile();
        await this.applySystemOptimizedSettings();
      }

      this.emit('optimizationCycleComplete', optimizationResult);

    } catch (error) {
      console.error(' Error in optimization cycle:', error);
    }
  }

  /**
   * Get comprehensive system health assessment
   */
  getSystemHealthAssessment(): SystemHealthAssessment {
    const components = {
      database: this.componentHealth.get('database')!,
      search: this.componentHealth.get('search')!,
      memory: this.componentHealth.get('memory')!,
      system: this.componentHealth.get('system')!
    };

    // Determine overall health
    let overall: 'healthy' | 'degraded' | 'critical' = 'healthy';
    const statuses = Object.values(components).map(c => c.status);

    if (statuses.includes('critical')) {
      overall = 'critical';
    } else if (statuses.includes('degraded')) {
      overall = 'degraded';
    }

    // Calculate adaptive metrics
    const thresholdReport = this.dynamicThresholds.getThresholdReport();
    const alertSystem = this.contextualAlerts.getSystemStatus();

    const adaptiveMetrics = {
      thresholdAccuracy: thresholdReport.confidence,
      alertReduction: Math.max(0, 1 - (alertSystem.activeAlerts.length / 10)), // Normalized
      falsePositiveRate: 0.05, // Would be calculated from actual data
      systemOptimization: thresholdReport.confidence
    };

    // Generate recommendations
    const recommendations = this.generateSystemRecommendations(components, adaptiveMetrics);

    return {
      overall,
      components,
      adaptiveMetrics,
      recommendations,
      timestamp: Date.now()
    };
  }

  /**
   * Generate system recommendations
   */
  private generateSystemRecommendations(
    components: SystemHealthAssessment['components'],
    metrics: SystemHealthAssessment['adaptiveMetrics']
  ): SystemHealthAssessment['recommendations'] {
    const recommendations: SystemHealthAssessment['recommendations'] = [];

    // Component-specific recommendations
    for (const [name, health] of Object.entries(components)) {
      if (health.status === 'critical') {
        recommendations.push({
          category: name,
          action: `Immediate attention required for ${name} component`,
          priority: 'high',
          estimatedImpact: 'Critical system stability'
        });
      } else if (health.status === 'degraded') {
        recommendations.push({
          category: name,
          action: `Monitor and optimize ${name} performance`,
          priority: 'medium',
          estimatedImpact: 'Improved response times'
        });
      }

      // Trend-based recommendations
      const degradingMetrics = health.metrics.filter(m => m.trend === 'degrading');
      if (degradingMetrics.length > 0) {
        recommendations.push({
          category: name,
          action: `Address degrading trend in ${degradingMetrics.map(m => m.name).join(', ')}`,
          priority: 'medium',
          estimatedImpact: 'Prevent future issues'
        });
      }
    }

    // Adaptive system recommendations
    if (metrics.thresholdAccuracy < 0.7) {
      recommendations.push({
        category: 'monitoring',
        action: 'Increase threshold training period for better accuracy',
        priority: 'medium',
        estimatedImpact: 'Reduced false alerts'
      });
    }

    if (metrics.falsePositiveRate > 0.15) {
      recommendations.push({
        category: 'alerting',
        action: 'Tune alert suppression rules to reduce noise',
        priority: 'low',
        estimatedImpact: 'Better alert quality'
      });
    }

    return recommendations.sort((a, b) => {
      const priorityOrder = { high: 3, medium: 2, low: 1 };
      return priorityOrder[b.priority] - priorityOrder[a.priority];
    });
  }

  // Helper methods

  private getRecentMetrics(category: string, name: string, windowMs: number): EnhancedMetric[] {
    const key = `${category}:${name}`;
    const metrics = this.metrics.get(key) || [];
    const cutoff = Date.now() - windowMs;
    return metrics.filter(m => m.timestamp > cutoff);
  }

  private getTotalRecentOperations(): number {
    // Simplified calculation - would be more sophisticated in production
    let total = 0;
    const windowMs = 5 * 60 * 1000; // 5 minutes

    for (const metrics of this.metrics.values()) {
      total += this.getRecentMetrics('database', 'query', windowMs).length;
      total += this.getRecentMetrics('search', 'search', windowMs).length;
    }

    return Math.max(total, 1);
  }

  private cleanupOldMetrics(): void {
    const cutoff = Date.now() - (this.METRICS_RETENTION_HOURS * 60 * 60 * 1000);

    for (const [key, metrics] of this.metrics.entries()) {
      const filteredMetrics = metrics.filter(m => m.timestamp > cutoff);
      this.metrics.set(key, filteredMetrics);
    }

    // Cleanup resolved alerts older than 24 hours
    const alertCutoff = Date.now() - (24 * 60 * 60 * 1000);
    for (const [id, alert] of this.activeAlerts.entries()) {
      if (alert.timestamp < alertCutoff) {
        this.activeAlerts.delete(id);
      }
    }
  }

  private setupEventHandlers(): void {
    // Handle threshold adaptations
    this.dynamicThresholds.on('thresholdAdapted', (data) => {
      console.log(` Threshold adapted: ${data.id} (confidence: ${data.confidence.toFixed(2)})`);
      this.emit('thresholdAdapted', data);
    });

    // Handle contextual alerts
    this.contextualAlerts.on('alert:processed', (alert) => {
      console.log(` Contextual alert processed: ${alert.adjustedSeverity} - ${alert.message}`);
      this.emit('contextualAlert', alert);
    });

    // Handle system profiling
    this.systemProfiler.on('profileComplete', (profile) => {
      console.log(` System profile updated: ${profile.overallPerformanceClass} class`);
      this.emit('systemProfileUpdated', profile);
    });
  }

  /**
   * Get enhanced monitoring status
   */
  getMonitoringStatus(): {
    isActive: boolean;
    systemProfile: any;
    activeAlerts: number;
    thresholdAccuracy: number;
    componentHealth: any;
    recommendations: number;
  } {
    const healthAssessment = this.getSystemHealthAssessment();

    return {
      isActive: this.isMonitoring,
      systemProfile: this.systemProfiler.getProfile(),
      activeAlerts: this.activeAlerts.size,
      thresholdAccuracy: healthAssessment.adaptiveMetrics.thresholdAccuracy,
      componentHealth: healthAssessment.components,
      recommendations: healthAssessment.recommendations.length
    };
  }
}

/**
 * Production Monitoring Orchestrator with Dynamic Capabilities
 * 
 * Orchestrates dynamic monitoring coordination for the MCP Persistence System
 * with adaptive thresholds, context-aware alerting, and system profiling
 */

import { EventEmitter } from 'events';
import { DatabaseManager } from '../storage/Database.js';
import { PerformanceMonitor } from '../utils/PerformanceMonitor.js';
import { MemoryManager } from '../utils/MemoryManager.js';
import { EnhancedPerformanceMonitor } from './EnhancedPerformanceMonitor.js';

export class ProductionMonitoringOrchestrator extends EventEmitter {
  private isRunning = false;
  private enhancedMonitor: EnhancedPerformanceMonitor | null = null;
  private useEnhancedMonitoring = false;

  constructor(
    private readonly database: DatabaseManager,
    private readonly performanceMonitor: PerformanceMonitor,
    private readonly memoryManager: MemoryManager,
    options: {
      enableEnhancedMonitoring?: boolean;
    } = {}
  ) {
    super();

    this.useEnhancedMonitoring = options.enableEnhancedMonitoring || false;

    if (this.useEnhancedMonitoring) {
      this.enhancedMonitor = new EnhancedPerformanceMonitor(
        this.database,
        this.memoryManager
      );
      this.setupEnhancedEventHandlers();
    }
  }

  async startMonitoring(): Promise<void> {
    if (this.isRunning) {
      console.log('Monitoring already running');
      return;
    }

    console.log(' Starting Production Monitoring...');

    if (this.useEnhancedMonitoring && this.enhancedMonitor) {
      console.log(' Initializing Enhanced Monitoring with Dynamic Thresholds...');
      await this.enhancedMonitor.initialize();
      await this.enhancedMonitor.startMonitoring();
    } else {
      console.log(' Starting Standard Monitoring...');
      await this.performanceMonitor.startMonitoring(30); // 30 second intervals
    }

    this.isRunning = true;
    this.emit('monitoring:started', { enhanced: this.useEnhancedMonitoring });
  }

  async stopMonitoring(): Promise<void> {
    if (!this.isRunning) return;

    console.log(' Stopping Production Monitoring...');

    if (this.useEnhancedMonitoring && this.enhancedMonitor) {
      await this.enhancedMonitor.stopMonitoring();
    } else {
      await this.performanceMonitor.stopMonitoring();
    }

    this.isRunning = false;
    this.emit('monitoring:stopped');
  }

  async getSystemHealthReport(): Promise<any> {
    if (this.useEnhancedMonitoring && this.enhancedMonitor) {
      // Use enhanced monitoring report
      const healthAssessment = this.enhancedMonitor.getSystemHealthAssessment();
      const monitoringStatus = this.enhancedMonitor.getMonitoringStatus();

      return {
        overall: healthAssessment.overall,
        timestamp: healthAssessment.timestamp,
        uptime: process.uptime() * 1000,
        enhanced: true,
        systemProfile: monitoringStatus.systemProfile,
        components: healthAssessment.components,
        adaptiveMetrics: healthAssessment.adaptiveMetrics,
        recommendations: healthAssessment.recommendations,
        activeAlerts: monitoringStatus.activeAlerts,
        thresholdAccuracy: monitoringStatus.thresholdAccuracy
      };
    } else {
      // Use standard monitoring report
      const performanceReport = this.performanceMonitor.getEnhancedPerformanceReport();
      const memoryReport = this.memoryManager.getMemoryReport();
      const healthStatus = this.performanceMonitor.getHealthStatus();

      return {
        overall: healthStatus.overall,
        timestamp: Date.now(),
        uptime: process.uptime() * 1000,
        enhanced: false,
        performance: performanceReport,
        memory: memoryReport,
        components: healthStatus.components,
        activeAlerts: healthStatus.activeAlerts.length,
        thresholdInfo: performanceReport.thresholdInfo
      };
    }
  }

  /**
   * Setup event handlers for enhanced monitoring
   */
  private setupEnhancedEventHandlers(): void {
    if (!this.enhancedMonitor) return;

    this.enhancedMonitor.on('initialized', (data) => {
      console.log(` Enhanced monitoring initialized - Performance class: ${data.systemProfile?.overallPerformanceClass}`);
      this.emit('enhanced:initialized', data);
    });

    this.enhancedMonitor.on('thresholdAdapted', (data) => {
      console.log(` Threshold adapted: ${data.id} (${(data.confidence * 100).toFixed(1)}% confidence)`);
      this.emit('threshold:adapted', data);
    });

    this.enhancedMonitor.on('adaptiveAlert', (alert) => {
      console.log(` Adaptive alert: [${alert.contextualSeverity}] ${alert.message}`);
      this.emit('alert:adaptive', alert);
    });

    this.enhancedMonitor.on('contextualAlert', (alert) => {
      console.log(` Contextual alert processed with insights: ${alert.actionableInsights.length} recommendations`);
      this.emit('alert:contextual', alert);
    });

    this.enhancedMonitor.on('systemProfileUpdated', (profile) => {
      console.log(` System profile updated: ${profile.overallPerformanceClass} performance class`);
      this.emit('system:profileUpdated', profile);
    });

    this.enhancedMonitor.on('optimizationCycleComplete', (result) => {
      console.log(` Optimization cycle complete: ${result.recommendedThresholds?.size || 0} recommendations`);
      this.emit('optimization:complete', result);
    });
  }

  /**
   * Force system re-profiling (enhanced monitoring only)
   */
  async reprofileSystem(): Promise<void> {
    if (!this.useEnhancedMonitoring || !this.enhancedMonitor) {
      throw new Error('Enhanced monitoring not enabled');
    }

    console.log(' Forcing system re-profiling...');
    // This would trigger re-profiling in the enhanced monitor
    this.emit('system:reprofileRequested');
  }

  /**
   * Get monitoring capabilities and status
   */
  getMonitoringCapabilities(): {
    enhanced: boolean;
    dynamicThresholds: boolean;
    contextAwareAlerts: boolean;
    systemProfiling: boolean;
    mlOptimization: boolean;
    uptime: number;
    isRunning: boolean;
  } {
    return {
      enhanced: this.useEnhancedMonitoring,
      dynamicThresholds: this.useEnhancedMonitoring && this.performanceMonitor.isDynamicThresholdingEnabled(),
      contextAwareAlerts: this.useEnhancedMonitoring,
      systemProfiling: this.useEnhancedMonitoring,
      mlOptimization: this.useEnhancedMonitoring,
      uptime: process.uptime() * 1000,
      isRunning: this.isRunning
    };
  }

  /**
   * Switch monitoring mode (requires restart)
   */
  async switchToEnhancedMonitoring(): Promise<void> {
    if (this.useEnhancedMonitoring) {
      console.log('Enhanced monitoring already enabled');
      return;
    }

    console.log(' Switching to enhanced monitoring mode...');

    const wasRunning = this.isRunning;
    if (wasRunning) {
      await this.stopMonitoring();
    }

    // Initialize enhanced monitoring
    this.useEnhancedMonitoring = true;
    this.enhancedMonitor = new EnhancedPerformanceMonitor(
      this.database,
      this.memoryManager
    );
    this.setupEnhancedEventHandlers();

    if (wasRunning) {
      await this.startMonitoring();
    }

    console.log(' Switched to enhanced monitoring mode');
    this.emit('monitoring:modeChanged', { enhanced: true });
  }
}

/**
 * System Capability Profiler
 * 
 * Automatically detects and profiles system capabilities including:
 * - CPU performance and characteristics
 * - Memory capacity and bandwidth
 * - Disk I/O performance
 * - Network capabilities
 * - Runtime environment characteristics
 * 
 * Uses this information to automatically adjust system limits and thresholds.
 */

import * as os from 'os';
import * as fs from 'fs/promises';
import * as path from 'path';
import { EventEmitter } from 'events';

interface CPUProfile {
  cores: number;
  logicalCores: number;
  baseFrequency: number; // MHz
  maxFrequency?: number;
  architecture: string;
  vendor: string;
  model: string;
  cacheSize?: {
    l1: number;
    l2: number;
    l3: number;
  };
  performanceScore: number; // 0-1000 relative performance
}

interface MemoryProfile {
  totalMemory: number; // bytes
  availableMemory: number;
  swapTotal: number;
  swapFree: number;
  bandwidth: number; // MB/s estimated
  latency: number; // nanoseconds estimated
  recommendedHeapSize: number;
  recommendedCacheSize: number;
}

interface DiskProfile {
  type: 'HDD' | 'SSD' | 'NVME' | 'Unknown';
  capacity: number; // bytes
  available: number;
  readSpeed: number; // MB/s
  writeSpeed: number; // MB/s
  randomIOPS: number; // operations per second
  latency: number; // milliseconds average
}

interface NetworkProfile {
  interfaces: Array<{
    name: string;
    type: 'ethernet' | 'wifi' | 'loopback' | 'other';
    speed?: number; // Mbps
    mtu: number;
    isActive: boolean;
  }>;
  estimatedBandwidth: number; // Mbps
  latencyToInternet: number; // ms
}

interface RuntimeProfile {
  nodeVersion: string;
  v8Version: string;
  platform: string;
  architecture: string;
  endianness: 'BE' | 'LE';
  maxHeapSize: number; // bytes
  gcType: string;
  jitSupport: boolean;
  workerThreadSupport: boolean;
}

interface SystemCapabilityProfile {
  cpu: CPUProfile;
  memory: MemoryProfile;
  disk: DiskProfile;
  network: NetworkProfile;
  runtime: RuntimeProfile;
  timestamp: number;
  profileVersion: string;
  overallPerformanceClass: 'low' | 'medium' | 'high' | 'exceptional';
  recommendedLimits: {
    maxConcurrentQueries: number;
    maxCacheSize: number;
    maxMemoryUsage: number;
    queryTimeout: number;
    indexBuildParallelism: number;
  };
}

interface BenchmarkResult {
  test: string;
  score: number;
  unit: string;
  duration: number;
  iterations: number;
}

export class SystemCapabilityProfiler extends EventEmitter {
  private profile: SystemCapabilityProfile | null = null;
  private benchmarkResults: Map<string, BenchmarkResult> = new Map();
  private isProfiled = false;

  private readonly BENCHMARK_TIMEOUT = 30000; // 30 seconds max per benchmark
  private readonly CACHE_DURATION = 24 * 60 * 60 * 1000; // 24 hours

  constructor(private readonly cacheFile = './data/system-profile.json') {
    super();
  }

  /**
   * Run complete system profiling
   */
  async profileSystem(): Promise<SystemCapabilityProfile> {
    if (this.isProfiled && this.profile) {
      return this.profile;
    }

    console.log(' Starting comprehensive system profiling...');

    // Try to load cached profile first
    const cachedProfile = await this.loadCachedProfile();
    if (cachedProfile && Date.now() - cachedProfile.timestamp < this.CACHE_DURATION) {
      console.log(' Using cached system profile');
      this.profile = cachedProfile;
      this.isProfiled = true;
      return cachedProfile;
    }

    // Run full profiling
    const startTime = Date.now();

    const [cpu, memory, disk, network, runtime] = await Promise.all([
      this.profileCPU(),
      this.profileMemory(),
      this.profileDisk(),
      this.profileNetwork(),
      this.profileRuntime()
    ]);

    // Determine overall performance class
    const overallPerformanceClass = this.calculatePerformanceClass(cpu, memory, disk);

    // Calculate recommended limits
    const recommendedLimits = this.calculateRecommendedLimits(cpu, memory, disk, overallPerformanceClass);

    this.profile = {
      cpu,
      memory,
      disk,
      network,
      runtime,
      timestamp: Date.now(),
      profileVersion: '1.0.0',
      overallPerformanceClass,
      recommendedLimits
    };

    await this.saveCachedProfile(this.profile);

    const duration = Date.now() - startTime;
    console.log(` System profiling complete in ${duration}ms`);
    console.log(` Performance class: ${overallPerformanceClass.toUpperCase()}`);

    this.isProfiled = true;
    this.emit('profileComplete', this.profile);

    return this.profile;
  }

  /**
   * Profile CPU characteristics and performance
   */
  private async profileCPU(): Promise<CPUProfile> {
    console.log(' Profiling CPU...');

    const cpus = os.cpus();
    const architecture = os.arch();
    const platform = os.platform();

    // Basic CPU info
    const cores = cpus.length;
    const baseFrequency = cpus[0]?.speed || 0;
    const vendor = this.extractCPUVendor(cpus[0]?.model || '');
    const model = cpus[0]?.model || 'Unknown';

    // Estimate logical cores (hyperthreading)
    let logicalCores = cores;
    if (platform === 'linux') {
      try {
        const cpuinfo = await fs.readFile('/proc/cpuinfo', 'utf-8');
        const physicalIds = new Set();
        const coreIds = new Set();

        for (const line of cpuinfo.split('\n')) {
          if (line.startsWith('physical id')) {
            physicalIds.add(line.split(':')[1].trim());
          }
          if (line.startsWith('core id')) {
            coreIds.add(line.split(':')[1].trim());
          }
        }

        const physicalCores = physicalIds.size * coreIds.size;
        logicalCores = Math.max(cores, physicalCores);
      } catch (error) {
        // Fallback to basic detection
      }
    }

    // Run CPU benchmark
    const cpuBenchmark = await this.runCPUBenchmark();
    const performanceScore = this.calculateCPUScore(cores, baseFrequency, cpuBenchmark.score);

    return {
      cores: logicalCores,
      logicalCores,
      baseFrequency,
      architecture,
      vendor,
      model,
      performanceScore
    };
  }

  /**
   * Profile memory characteristics
   */
  private async profileMemory(): Promise<MemoryProfile> {
    console.log(' Profiling memory...');

    const totalMemory = os.totalmem();
    const availableMemory = os.freemem();

    // Get swap information (Linux/macOS)
    let swapTotal = 0;
    let swapFree = 0;

    try {
      if (os.platform() === 'linux') {
        const meminfo = await fs.readFile('/proc/meminfo', 'utf-8');
        for (const line of meminfo.split('\n')) {
          if (line.startsWith('SwapTotal:')) {
            swapTotal = parseInt(line.split(/\s+/)[1]) * 1024; // Convert KB to bytes
          }
          if (line.startsWith('SwapFree:')) {
            swapFree = parseInt(line.split(/\s+/)[1]) * 1024;
          }
        }
      }
    } catch (error) {
      // Swap info not available
    }

    // Run memory benchmark
    const memoryBenchmark = await this.runMemoryBenchmark();

    // Calculate recommended sizes
    const recommendedHeapSize = Math.min(totalMemory * 0.75, 8 * 1024 * 1024 * 1024); // 75% of RAM or 8GB max
    const recommendedCacheSize = Math.min(totalMemory * 0.25, 2 * 1024 * 1024 * 1024); // 25% of RAM or 2GB max

    return {
      totalMemory,
      availableMemory,
      swapTotal,
      swapFree,
      bandwidth: memoryBenchmark.bandwidth,
      latency: memoryBenchmark.latency,
      recommendedHeapSize,
      recommendedCacheSize
    };
  }

  /**
   * Profile disk I/O performance
   */
  private async profileDisk(): Promise<DiskProfile> {
    console.log(' Profiling disk I/O...');

    // Get disk space info
    const stats = await fs.stat('.');
    let capacity = 0;
    let available = 0;

    try {
      const stat = await fs.stat('.');
      // This is a simplified approach - in production you'd use statvfs or similar
      capacity = 1024 * 1024 * 1024 * 1024; // 1TB default estimate
      available = capacity * 0.5; // 50% available estimate
    } catch (error) {
      // Use defaults
    }

    // Run disk benchmark
    const diskBenchmark = await this.runDiskBenchmark();

    // Determine disk type based on performance characteristics
    let diskType: 'HDD' | 'SSD' | 'NVME' | 'Unknown' = 'Unknown';
    if (diskBenchmark.randomIOPS > 10000 && diskBenchmark.readSpeed > 500) {
      diskType = 'NVME';
    } else if (diskBenchmark.randomIOPS > 1000 && diskBenchmark.readSpeed > 100) {
      diskType = 'SSD';
    } else if (diskBenchmark.randomIOPS < 200 && diskBenchmark.readSpeed < 150) {
      diskType = 'HDD';
    }

    return {
      type: diskType,
      capacity,
      available,
      readSpeed: diskBenchmark.readSpeed,
      writeSpeed: diskBenchmark.writeSpeed,
      randomIOPS: diskBenchmark.randomIOPS,
      latency: diskBenchmark.latency
    };
  }

  /**
   * Profile network capabilities
   */
  private async profileNetwork(): Promise<NetworkProfile> {
    console.log(' Profiling network...');

    const networkInterfaces = os.networkInterfaces();
    const interfaces: NetworkProfile['interfaces'] = [];

    for (const [name, addrs] of Object.entries(networkInterfaces)) {
      if (!addrs) continue;

      const activeAddr = addrs.find(addr => !addr.internal && addr.family === 'IPv4');
      if (!activeAddr) continue;

      interfaces.push({
        name,
        type: name.includes('eth') || name.includes('en') ? 'ethernet' : 
              name.includes('wl') || name.includes('wifi') ? 'wifi' :
              name.includes('lo') ? 'loopback' : 'other',
        mtu: activeAddr.mac ? 1500 : 65536, // Standard MTU estimates
        isActive: !activeAddr.internal
      });
    }

    // Estimate bandwidth and latency (simplified)
    const estimatedBandwidth = interfaces.some(i => i.type === 'ethernet') ? 1000 : 
                              interfaces.some(i => i.type === 'wifi') ? 100 : 10;

    const latencyToInternet = await this.pingLatency();

    return {
      interfaces,
      estimatedBandwidth,
      latencyToInternet
    };
  }

  /**
   * Profile Node.js runtime characteristics
   */
  private async profileRuntime(): Promise<RuntimeProfile> {
    console.log(' Profiling runtime...');

    const nodeVersion = process.version;
    const v8Version = process.versions.v8;
    const platform = process.platform;
    const architecture = process.arch;
    const endianness = os.endianness() as 'BE' | 'LE';

    // Estimate max heap size
    let maxHeapSize = 0;
    try {
      // This would trigger in older Node.js versions
      const v8 = require('v8');
      const heapStats = v8.getHeapStatistics();
      maxHeapSize = heapStats.heap_size_limit;
    } catch (error) {
      // Fallback estimate
      maxHeapSize = os.totalmem() * 0.75; // 75% of system RAM
    }

    // Check for various Node.js features
    const jitSupport = typeof process.versions.v8 !== 'undefined';
    const workerThreadSupport = parseInt(nodeVersion.slice(1)) >= 12;

    // Determine GC type (simplified detection)
    const gcType = maxHeapSize > 4 * 1024 * 1024 * 1024 ? 'generational' : 'incremental';

    return {
      nodeVersion,
      v8Version,
      platform,
      architecture,
      endianness,
      maxHeapSize,
      gcType,
      jitSupport,
      workerThreadSupport
    };
  }

  /**
   * Run CPU performance benchmark
   */
  private async runCPUBenchmark(): Promise<{ score: number; duration: number }> {
    return new Promise((resolve) => {
      const startTime = Date.now();
      let iterations = 0;
      const maxDuration = 5000; // 5 seconds max

      const benchmark = () => {
        const iterationStart = Date.now();

        // CPU-intensive calculation
        let result = 0;
        for (let i = 0; i < 100000; i++) {
          result += Math.sqrt(i) * Math.sin(i) * Math.cos(i);
        }

        iterations++;

        const elapsed = Date.now() - startTime;
        if (elapsed < maxDuration) {
          setImmediate(benchmark);
        } else {
          const score = (iterations * 100000) / elapsed; // Operations per millisecond
          resolve({ score, duration: elapsed });
        }
      };

      benchmark();
    });
  }

  /**
   * Run memory performance benchmark
   */
  private async runMemoryBenchmark(): Promise<{ bandwidth: number; latency: number }> {
    return new Promise((resolve) => {
      const arraySize = 1024 * 1024; // 1MB
      const iterations = 100;

      // Bandwidth test
      const bandwidthStart = Date.now();
      let sum = 0;

      for (let iter = 0; iter < iterations; iter++) {
        const buffer = new Float64Array(arraySize);
        for (let i = 0; i < arraySize; i++) {
          buffer[i] = Math.random();
          sum += buffer[i];
        }
      }

      const bandwidthDuration = Date.now() - bandwidthStart;
      const bandwidth = (arraySize * iterations * 8) / (bandwidthDuration / 1000) / 1024 / 1024; // MB/s

      // Latency test (simplified)
      const latencyStart = process.hrtime.bigint();
      const testArray = new Array(1000).fill(0).map(() => Math.random());
      let latencySum = 0;
      for (let i = 0; i < testArray.length; i++) {
        latencySum += testArray[i];
      }
      const latencyEnd = process.hrtime.bigint();
      const latency = Number(latencyEnd - latencyStart) / 1000000; // Convert to milliseconds

      resolve({ bandwidth, latency });
    });
  }

  /**
   * Run disk I/O benchmark
   */
  private async runDiskBenchmark(): Promise<{
    readSpeed: number;
    writeSpeed: number;
    randomIOPS: number;
    latency: number;
  }> {
    const tempDir = os.tmpdir();
    const testFile = path.join(tempDir, `disk-bench-${Date.now()}.tmp`);

    try {
      // Sequential write test
      const writeData = Buffer.alloc(1024 * 1024, 'x'); // 1MB
      const writeIterations = 10;

      const writeStart = Date.now();
      for (let i = 0; i < writeIterations; i++) {
        await fs.writeFile(`${testFile}.${i}`, writeData);
      }
      const writeDuration = Date.now() - writeStart;
      const writeSpeed = (writeData.length * writeIterations) / (writeDuration / 1000) / 1024 / 1024; // MB/s

      // Sequential read test
      const readStart = Date.now();
      for (let i = 0; i < writeIterations; i++) {
        await fs.readFile(`${testFile}.${i}`);
      }
      const readDuration = Date.now() - readStart;
      const readSpeed = (writeData.length * writeIterations) / (readDuration / 1000) / 1024 / 1024; // MB/s

      // Random I/O test (simplified)
      const randomStart = Date.now();
      const smallData = Buffer.alloc(4096, 'y'); // 4KB blocks
      const randomOperations = 100;

      for (let i = 0; i < randomOperations; i++) {
        await fs.writeFile(`${testFile}.random.${i}`, smallData);
        await fs.readFile(`${testFile}.random.${i}`);
      }
      const randomDuration = Date.now() - randomStart;
      const randomIOPS = (randomOperations * 2) / (randomDuration / 1000); // Read + write ops per second
      const latency = randomDuration / randomOperations; // Average latency per operation

      // Cleanup
      for (let i = 0; i < writeIterations; i++) {
        try {
          await fs.unlink(`${testFile}.${i}`);
          await fs.unlink(`${testFile}.random.${i}`);
        } catch (error) {
          // Ignore cleanup errors
        }
      }

      return {
        readSpeed,
        writeSpeed,
        randomIOPS,
        latency
      };

    } catch (error) {
      console.warn(' Disk benchmark failed, using estimates:', error);
      return {
        readSpeed: 100,
        writeSpeed: 80,
        randomIOPS: 500,
        latency: 10
      };
    }
  }

  /**
   * Measure network latency
   */
  private async pingLatency(): Promise<number> {
    // Simplified latency test - in production you'd use actual ping
    return new Promise((resolve) => {
      const start = Date.now();
      setTimeout(() => {
        resolve(Date.now() - start); // This is not a real network test
      }, Math.random() * 50 + 10); // Simulate 10-60ms latency
    });
  }

  /**
   * Extract CPU vendor from model string
   */
  private extractCPUVendor(model: string): string {
    const lowerModel = model.toLowerCase();
    if (lowerModel.includes('intel')) return 'Intel';
    if (lowerModel.includes('amd')) return 'AMD';
    if (lowerModel.includes('arm') || lowerModel.includes('apple')) return 'ARM';
    return 'Unknown';
  }

  /**
   * Calculate CPU performance score
   */
  private calculateCPUScore(cores: number, frequency: number, benchmarkScore: number): number {
    // Normalize to a 0-1000 scale
    const coreScore = Math.min(cores / 16, 1) * 300; // Up to 300 points for cores
    const frequencyScore = Math.min(frequency / 4000, 1) * 200; // Up to 200 points for frequency
    const benchmarkNormalized = Math.min(benchmarkScore / 1000, 1) * 500; // Up to 500 points for benchmark

    return Math.round(coreScore + frequencyScore + benchmarkNormalized);
  }

  /**
   * Calculate overall performance class
   */
  private calculatePerformanceClass(
    cpu: CPUProfile,
    memory: MemoryProfile,
    disk: DiskProfile
  ): 'low' | 'medium' | 'high' | 'exceptional' {
    let score = 0;

    // CPU scoring
    if (cpu.performanceScore > 800) score += 3;
    else if (cpu.performanceScore > 600) score += 2;
    else if (cpu.performanceScore > 400) score += 1;

    // Memory scoring
    const memoryGB = memory.totalMemory / (1024 * 1024 * 1024);
    if (memoryGB >= 32) score += 3;
    else if (memoryGB >= 16) score += 2;
    else if (memoryGB >= 8) score += 1;

    // Disk scoring
    if (disk.type === 'NVME') score += 3;
    else if (disk.type === 'SSD') score += 2;
    else if (disk.readSpeed > 100) score += 1;

    // Classify based on total score
    if (score >= 8) return 'exceptional';
    if (score >= 6) return 'high';
    if (score >= 3) return 'medium';
    return 'low';
  }

  /**
   * Calculate recommended system limits based on capabilities
   */
  private calculateRecommendedLimits(
    cpu: CPUProfile,
    memory: MemoryProfile,
    disk: DiskProfile,
    performanceClass: string
  ) {
    const baseLimits = {
      low: {
        maxConcurrentQueries: 10,
        maxCacheSize: 64 * 1024 * 1024, // 64MB
        maxMemoryUsage: 0.6,
        queryTimeout: 30000,
        indexBuildParallelism: 1
      },
      medium: {
        maxConcurrentQueries: 25,
        maxCacheSize: 256 * 1024 * 1024, // 256MB
        maxMemoryUsage: 0.7,
        queryTimeout: 15000,
        indexBuildParallelism: 2
      },
      high: {
        maxConcurrentQueries: 50,
        maxCacheSize: 512 * 1024 * 1024, // 512MB
        maxMemoryUsage: 0.75,
        queryTimeout: 10000,
        indexBuildParallelism: 4
      },
      exceptional: {
        maxConcurrentQueries: 100,
        maxCacheSize: 1024 * 1024 * 1024, // 1GB
        maxMemoryUsage: 0.8,
        queryTimeout: 5000,
        indexBuildParallelism: 8
      }
    };

    const base = baseLimits[performanceClass as keyof typeof baseLimits];

    // Apply fine-tuning based on specific capabilities
    const adjustedLimits = { ...base };

    // Adjust based on CPU cores
    adjustedLimits.maxConcurrentQueries = Math.min(
      adjustedLimits.maxConcurrentQueries,
      cpu.cores * 2
    );

    adjustedLimits.indexBuildParallelism = Math.min(
      adjustedLimits.indexBuildParallelism,
      Math.max(1, cpu.cores - 1)
    );

    // Adjust cache size based on available memory
    const maxReasonableCache = memory.recommendedCacheSize;
    adjustedLimits.maxCacheSize = Math.min(adjustedLimits.maxCacheSize, maxReasonableCache);

    // Adjust timeout based on disk performance
    if (disk.type === 'HDD' && disk.readSpeed < 100) {
      adjustedLimits.queryTimeout *= 2; // Double timeout for slow disks
    }

    return adjustedLimits;
  }

  /**
   * Load cached profile from disk
   */
  private async loadCachedProfile(): Promise<SystemCapabilityProfile | null> {
    try {
      const data = await fs.readFile(this.cacheFile, 'utf-8');
      const profile = JSON.parse(data);

      if (profile.profileVersion === '1.0.0') {
        return profile;
      }
    } catch (error) {
      // No cached profile or invalid format
    }

    return null;
  }

  /**
   * Save profile to disk cache
   */
  private async saveCachedProfile(profile: SystemCapabilityProfile): Promise<void> {
    try {
      // Ensure directory exists
      const dir = path.dirname(this.cacheFile);
      await fs.mkdir(dir, { recursive: true });

      await fs.writeFile(this.cacheFile, JSON.stringify(profile, null, 2));
    } catch (error) {
      console.warn(' Failed to cache system profile:', error);
    }
  }

  /**
   * Get current system profile
   */
  getProfile(): SystemCapabilityProfile | null {
    return this.profile;
  }

  /**
   * Get performance-adjusted configuration
   */
  getPerformanceConfig(): any {
    if (!this.profile) {
      throw new Error('System not yet profiled. Call profileSystem() first.');
    }

    const { recommendedLimits, overallPerformanceClass } = this.profile;

    return {
      // Database settings
      database: {
        maxConnections: Math.ceil(recommendedLimits.maxConcurrentQueries * 0.8),
        queryTimeout: recommendedLimits.queryTimeout,
        cacheSize: Math.floor(recommendedLimits.maxCacheSize / 1024), // Convert to KB for SQLite
        walBufferSize: overallPerformanceClass === 'high' || overallPerformanceClass === 'exceptional' ? 32 * 1024 * 1024 : 16 * 1024 * 1024,
        mmapSize: this.profile.memory.totalMemory > 8 * 1024 * 1024 * 1024 ? 256 * 1024 * 1024 : 128 * 1024 * 1024
      },

      // Memory management
      memory: {
        maxHeapUsage: recommendedLimits.maxMemoryUsage,
        gcThreshold: overallPerformanceClass === 'exceptional' ? 0.9 : 0.8,
        cacheSize: recommendedLimits.maxCacheSize,
        objectPoolSize: overallPerformanceClass === 'high' || overallPerformanceClass === 'exceptional' ? 1000 : 500
      },

      // Search engine settings
      search: {
        maxConcurrentSearches: Math.ceil(recommendedLimits.maxConcurrentQueries * 0.5),
        indexBuildParallelism: recommendedLimits.indexBuildParallelism,
        resultCacheSize: Math.floor(recommendedLimits.maxCacheSize * 0.2), // 20% of cache for search results
        embeddingCacheSize: Math.floor(recommendedLimits.maxCacheSize * 0.3) // 30% of cache for embeddings
      },

      // Performance monitoring
      monitoring: {
        metricsCollectionInterval: overallPerformanceClass === 'low' ? 60000 : 30000, // 1 min vs 30 sec
        alertEvaluationInterval: 30000,
        historicalDataRetention: overallPerformanceClass === 'exceptional' ? 7 * 24 * 60 * 60 * 1000 : 24 * 60 * 60 * 1000, // 7 days vs 1 day
        detailedMetrics: overallPerformanceClass === 'high' || overallPerformanceClass === 'exceptional'
      },

      // System info
      capabilities: {
        performanceClass: overallPerformanceClass,
        cpu: {
          cores: this.profile.cpu.cores,
          score: this.profile.cpu.performanceScore
        },
        memory: {
          total: this.profile.memory.totalMemory,
          recommended: this.profile.memory.recommendedHeapSize
        },
        disk: {
          type: this.profile.disk.type,
          speed: this.profile.disk.readSpeed
        }
      }
    };
  }

  /**
   * Force re-profiling (ignores cache)
   */
  async forceReprofile(): Promise<SystemCapabilityProfile> {
    this.isProfiled = false;
    this.profile = null;
    return this.profileSystem();
  }

  /**
   * Get benchmark results
   */
  getBenchmarkResults(): Map<string, BenchmarkResult> {
    return new Map(this.benchmarkResults);
  }
}

