  constructor(dbManager: DatabaseManager) {
    super(dbManager);
    this.validator = new PatternAccuracyValidator(dbManager);
  }

  /**
   * Initialize the production pattern detector
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    console.log(' Initializing Production Pattern Detector...');

    await this.validator.initialize();
    await this.initializePatternTables();

    this.isInitialized = true;
    console.log(' Production Pattern Detector initialized');
  }

  /**
   * Detect commitments with enhanced accuracy validation
   */
  async detectCommitments(
    messages: Message[],
    options: {
      minConfidence?: number;
      enableValidation?: boolean;
      includeContext?: boolean;
    } = {}
  ): Promise<EnhancedCommitment[]> {
    const {
      minConfidence = 0.75,
      enableValidation = true,
      includeContext = true
    } = options;

    const commitments: EnhancedCommitment[] = [];

    for (const message of messages) {
      // Only analyze assistant messages for commitments
      if (message.role !== 'assistant') continue;

      const context = includeContext ? await this.buildConversationContext(message, messages) : this.createMinimalContext(message);

      // Apply enhanced patterns
      for (const patternConfig of ProductionPatternDetector.ENHANCED_COMMITMENT_PATTERNS) {
        const matches = message.content.matchAll(patternConfig.pattern);

        for (const match of matches) {
          const rawCommitment = await this.createRawCommitment(message, match, patternConfig, context);

          // Skip if doesn't meet minimum confidence
          if (rawCommitment.confidence < minConfidence) continue;

          // Apply accuracy validation if enabled
          if (enableValidation) {
            const patternMatch: PatternMatch = {
              id: rawCommitment.id,
              pattern: patternConfig.pattern.source,
              text: message.content,
              matchedText: match[0],
              confidence: rawCommitment.confidence,
              contextualConfidence: 0,
              type: 'commitment',
              subtype: patternConfig.type,
              startPosition: match.index || 0,
              endPosition: (match.index || 0) + match[0].length,
              conversationContext: context,
              linguisticFeatures: this.extractLinguisticFeatures(message.content, match.index || 0),
              timestamp: Date.now()
            };

            const validationResult = await this.validator.validatePattern(patternMatch);

            // Apply validation results
            rawCommitment.validatedConfidence = validationResult.confidence;
            rawCommitment.validationResult = {
              isValid: validationResult.isValid,
              falsePositiveRisk: validationResult.falsePositiveRisk,
              issues: validationResult.issues.map(issue => issue.description),
              qualityScore: this.calculateQualityScore(validationResult)
            };

            // Filter out invalid patterns
            if (!validationResult.isValid || validationResult.falsePositiveRisk > 0.15) {
              continue;
            }
          }

          commitments.push(rawCommitment);
        }
      }
    }

    // Sort by validated confidence and quality
    return commitments.sort((a, b) => {
      const scoreA = enableValidation ? a.validatedConfidence : a.confidence;
      const scoreB = enableValidation ? b.validatedConfidence : b.confidence;
      return scoreB - scoreA;
    });
  }

  /**
   * Detect questions with enhanced accuracy validation
   */
  async detectQuestions(
    messages: Message[],
    options: {
      minConfidence?: number;
      enableValidation?: boolean;
      includeContext?: boolean;
    } = {}
  ): Promise<EnhancedQuestion[]> {
    const {
      minConfidence = 0.75,
      enableValidation = true,
      includeContext = true
    } = options;

    const questions: EnhancedQuestion[] = [];

    for (const message of messages) {
      // Typically analyze user messages for questions, but allow assistant clarifying questions
      if (message.role === 'system') continue;

      const context = includeContext ? await this.buildConversationContext(message, messages) : this.createMinimalContext(message);

      // Apply enhanced question patterns
      for (const patternConfig of ProductionPatternDetector.ENHANCED_QUESTION_PATTERNS) {
        const matches = message.content.matchAll(patternConfig.pattern);

        for (const match of matches) {
          const rawQuestion = await this.createRawQuestion(message, match, patternConfig, context);

          // Skip if doesn't meet minimum confidence
          if (rawQuestion.confidence < minConfidence) continue;

          // Apply accuracy validation if enabled
          if (enableValidation) {
            const patternMatch: PatternMatch = {
              id: rawQuestion.id,
              pattern: patternConfig.pattern.source,
              text: message.content,
              matchedText: match[0],
              confidence: rawQuestion.confidence,
              contextualConfidence: 0,
              type: 'question',
              subtype: patternConfig.type,
              startPosition: match.index || 0,
              endPosition: (match.index || 0) + match[0].length,
              conversationContext: context,
              linguisticFeatures: this.extractLinguisticFeatures(message.content, match.index || 0),
              timestamp: Date.now()
            };

            const validationResult = await this.validator.validatePattern(patternMatch);

            // Apply validation results
            rawQuestion.validatedConfidence = validationResult.confidence;
            rawQuestion.validationResult = {
              isValid: validationResult.isValid,
              qualityScore: this.calculateQualityScore(validationResult),
              ambiguityRisk: this.calculateAmbiguityRisk(validationResult)
            };

            // Filter out invalid patterns
            if (!validationResult.isValid || validationResult.falsePositiveRisk > 0.12) {
              continue;
            }
          }

          questions.push(rawQuestion);
        }
      }
    }

    return questions.sort((a, b) => {
      const scoreA = enableValidation ? a.validatedConfidence : a.confidence;
      const scoreB = enableValidation ? b.validatedConfidence : b.confidence;
      return scoreB - scoreA;
    });
  }

  /**
   * Run comprehensive pattern quality assessment
   */
  async assessPatternQuality(messages: Message[]): Promise<PatternQualityReport> {
    console.log(' Running comprehensive pattern quality assessment...');

    const startTime = Date.now();

    // Detect all patterns with validation
    const commitments = await this.detectCommitments(messages, { enableValidation: true, minConfidence: 0.5 });
    const questions = await this.detectQuestions(messages, { enableValidation: true, minConfidence: 0.5 });

    const allPatterns = [...commitments, ...questions];
    const validPatterns = allPatterns.filter(p => 
      'validationResult' in p && p.validationResult.isValid
    );

    // Calculate quality distribution
    const qualityDistribution = {
      high: 0,
      medium: 0,
      low: 0,
      invalid: 0
    };

    for (const pattern of allPatterns) {
      if (!('validationResult' in pattern) || !pattern.validationResult.isValid) {
        qualityDistribution.invalid++;
        continue;
      }

      const quality = pattern.validationResult.qualityScore;
      if (quality >= 0.8) qualityDistribution.high++;
      else if (quality >= 0.6) qualityDistribution.medium++;
      else qualityDistribution.low++;
    }

    // Run accuracy test suite
    const testResults = await this.validator.runAccuracyTests();

    // Collect common issues
    const issueMap = new Map<string, { count: number; severity: string }>();

    for (const pattern of allPatterns) {
      if ('validationResult' in pattern && pattern.validationResult.issues.length > 0) {
        for (const issue of pattern.validationResult.issues) {
          const key = issue.replace(/[:.]/g, '').substring(0, 50); // Normalize issue text
          if (!issueMap.has(key)) {
            issueMap.set(key, { count: 0, severity: 'medium' });
          }
          issueMap.get(key)!.count++;
        }
      }
    }

    const commonIssues = Array.from(issueMap.entries())
      .sort((a, b) => b[1].count - a[1].count)
      .slice(0, 10)
      .map(([issue, data]) => ({
        issue,
        frequency: data.count,
        severity: data.severity
      }));

    // Generate recommendations
    const recommendations = this.generateQualityRecommendations(testResults, qualityDistribution, commonIssues);

    const report: PatternQualityReport = {
      timestamp: Date.now(),
      totalPatterns: allPatterns.length,
      validatedPatterns: validPatterns.length,
      qualityDistribution,
      accuracyMetrics: {
        precision: testResults.precision,
        recall: testResults.recall,
        f1Score: testResults.f1Score,
        falsePositiveRate: testResults.falsePositiveRate,
        contextualAccuracy: testResults.contextualAccuracy
      },
      commonIssues,
      recommendations
    };

    // Store quality report
    await this.storeQualityReport(report);

    console.log(` Pattern quality assessment completed in ${Date.now() - startTime}ms`);
    console.log(` Results: ${validPatterns.length}/${allPatterns.length} valid patterns (${(validPatterns.length / Math.max(1, allPatterns.length) * 100).toFixed(1)}%)`);
    console.log(` Accuracy: P=${(testResults.precision * 100).toFixed(1)}% R=${(testResults.recall * 100).toFixed(1)}% F1=${(testResults.f1Score * 100).toFixed(1)}%`);
    console.log(` False Positive Rate: ${(testResults.falsePositiveRate * 100).toFixed(1)}%`);

    return report;
  }

  /**
   * Get production readiness status
   */
  async getProductionReadiness(): Promise<{
    isReady: boolean;
    readinessScore: number;
    criticalIssues: string[];
    recommendations: string[];
    metrics: {
      accuracy: number;
      falsePositiveRate: number;
      validationCoverage: number;
      qualityScore: number;
    };
  }> {
    const testResults = await this.validator.runAccuracyTests();
    const accuracyMetrics = this.validator.getAccuracyMetrics();
    const validationIssues = await this.validator.getValidationIssues(24);

    const criticalIssues: string[] = [];
    const recommendations: string[] = [];

    // Check false positive rate
    if (testResults.falsePositiveRate > 0.05) {
      criticalIssues.push(`False positive rate (${(testResults.falsePositiveRate * 100).toFixed(1)}%) exceeds 5% threshold`);
      recommendations.push('Improve pattern specificity and contextual validation');
    }

    // Check accuracy
    if (testResults.precision < 0.85) {
      criticalIssues.push(`Precision (${(testResults.precision * 100).toFixed(1)}%) below 85% threshold`);
      recommendations.push('Enhance pattern accuracy with better linguistic analysis');
    }

    // Check validation coverage
    const validationRate = accuracyMetrics.validationRate;
    if (validationRate < 0.95) {
      criticalIssues.push(`Validation coverage (${(validationRate * 100).toFixed(1)}%) below 95% threshold`);
      recommendations.push('Ensure all patterns are validated before production use');
    }

    // Check critical validation issues
    if (validationIssues.criticalIssues > 0) {
      criticalIssues.push(`${validationIssues.criticalIssues} critical validation issues detected`);
      recommendations.push('Resolve critical validation issues before deployment');
    }

    const readinessScore = this.calculateReadinessScore(testResults, accuracyMetrics, validationIssues);
    const isReady = criticalIssues.length === 0 && readinessScore >= 0.85;

    return {
      isReady,
      readinessScore,
      criticalIssues,
      recommendations,
      metrics: {
        accuracy: testResults.f1Score,
        falsePositiveRate: testResults.falsePositiveRate,
        validationCoverage: validationRate,
        qualityScore: readinessScore
      }
    };
  }

  // Private implementation methods

  private async createRawCommitment(
    message: Message, 
    match: RegExpMatchArray, 
    patternConfig: any, 
    context: ConversationContext
  ): Promise<EnhancedCommitment> {
    const commitmentText = match[1] || match[0];
    const timeframeText = match[2];

    // Calculate base confidence with boosts/penalties
    let confidence = patternConfig.baseConfidence;

    // Apply boost factors
    if (patternConfig.boostFactors) {
      confidence += this.calculateBoosts(commitmentText, patternConfig.boostFactors);
    }

    // Apply penalty factors
    if (patternConfig.penaltyFactors) {
      confidence += this.calculatePenalties(commitmentText, patternConfig.penaltyFactors);
    }

    // Extract action items
    const actionItems = this.extractActionItems(commitmentText);

    // Analyze urgency
    const urgencyLevel = this.analyzeUrgency(match[0], context);

    // Parse timeframe
    const timeframe = timeframeText ? this.parseTimeframe(timeframeText) : undefined;

    // Analyze contextual factors
    const contextualFactors = this.analyzeContextualFactors(context, commitmentText);

    return {
      id: this.generateId(),
      message,
      commitmentText: commitmentText.trim(),
      commitmentType: this.mapPatternTypeToCommitmentType(patternConfig.type),
      confidence: Math.max(0, Math.min(1, confidence)),
      validatedConfidence: confidence, // Will be updated by validation
      urgencyLevel,
      timeframe,
      actionItems,
      contextualFactors,
      validationResult: {
        isValid: true, // Will be updated by validation
        falsePositiveRisk: 0,
        issues: [],
        qualityScore: confidence
      },
      extractedAt: Date.now()
    };
  }

  private async createRawQuestion(
    message: Message,
    match: RegExpMatchArray,
    patternConfig: any,
    context: ConversationContext
  ): Promise<EnhancedQuestion> {
    const questionText = match[0];
    const subject = match[1] || this.extractQuestionSubject(questionText);

    const confidence = patternConfig.baseConfidence;
    const complexity = this.analyzeQuestionComplexity(questionText);
    const urgencyIndicators = this.extractUrgencyIndicators(questionText);
    const intentClassification = this.classifyQuestionIntent(questionText, patternConfig.type);
    const semanticFeatures = this.extractSemanticFeatures(questionText);

    return {
      id: this.generateId(),
      message,
      questionText: questionText.trim(),
      questionType: patternConfig.type,
      confidence,
      validatedConfidence: confidence, // Will be updated by validation
      subject: subject.trim(),
      complexity,
      urgencyIndicators,
      contextualRelevance: this.calculateContextualRelevance(context, questionText),
      intentClassification,
      semanticFeatures,
      validationResult: {
        isValid: true, // Will be updated by validation
        qualityScore: confidence,
        ambiguityRisk: 0
      },
      extractedAt: Date.now()
    };
  }

  private async buildConversationContext(message: Message, allMessages: Message[]): Promise<ConversationContext> {
    // Get previous messages for context
    const messageIndex = allMessages.findIndex(m => m.id === message.id);
    const previousMessages = allMessages.slice(Math.max(0, messageIndex - 3), messageIndex)
      .map(m => m.content);

    // Analyze semantic context
    const semanticContext = await this.analyzeSemanticContext(message, previousMessages);

    // Analyze temporal context
    const temporalContext = this.analyzeTemporalContext(message.content);

    return {
      previousMessages,
      currentTopic: this.extractCurrentTopic(message, previousMessages),
      userRole: message.role,
      conversationStage: this.determineConversationStage(messageIndex, allMessages.length),
      semanticContext,
      temporalContext
    };
  }

  private createMinimalContext(message: Message): ConversationContext {
    return {
      previousMessages: [],
      currentTopic: 'general',
      userRole: message.role,
      conversationStage: 'development',
      semanticContext: {
        entities: [],
        topics: [],
        sentiment: { polarity: 0, confidence: 0.5 },
        intentFlow: [],
        hypotheticalIndicators: this.detectHypotheticalLanguage(message.content)
      },
      temporalContext: {
        timeReferences: [],
        sequenceIndicators: [],
        conditionalMarkers: []
      }
    };
  }

  private extractLinguisticFeatures(text: string, position: number): LinguisticFeatures {
    // Implementation would extract linguistic features around the match position
    // For now, returning basic analysis
    return {
      sentenceType: 'declarative',
      modalVerbs: this.extractModalVerbs(text),
      certaintyMarkers: this.extractCertaintyMarkers(text),
      hedgingLanguage: this.extractHedgingLanguage(text),
      intensifiers: [],
      negationMarkers: this.extractNegationMarkers(text),
      conditionalMarkers: this.extractConditionalMarkers(text),
      temporalMarkers: this.extractTemporalMarkers(text)
    };
  }

  private calculateBoosts(text: string, boostFactors: any): number {
    let boost = 0;

    if (boostFactors.actionVerbs) {
      const actionVerbs = ['check', 'update', 'review', 'send', 'create', 'fix', 'investigate', 'analyze'];
      if (actionVerbs.some(verb => text.toLowerCase().includes(verb))) {
        boost += boostFactors.actionVerbs;
      }
    }

    if (boostFactors.timeSpecificity) {
      const timePatterns = /\b(?:today|tomorrow|this week|by friday|within \d+ days)\b/gi;
      if (timePatterns.test(text)) {
        boost += boostFactors.timeSpecificity;
      }
    }

    if (boostFactors.certaintyMarkers) {
      const certaintyWords = ['definitely', 'certainly', 'absolutely', 'guarantee'];
      if (certaintyWords.some(word => text.toLowerCase().includes(word))) {
        boost += boostFactors.certaintyMarkers;
      }
    }

    return boost;
  }

  private calculatePenalties(text: string, penaltyFactors: any): number {
    let penalty = 0;

    if (penaltyFactors.hedging) {
      const hedgeWords = ['maybe', 'perhaps', 'possibly', 'probably', 'might'];
      if (hedgeWords.some(word => text.toLowerCase().includes(word))) {
        penalty += penaltyFactors.hedging;
      }
    }

    if (penaltyFactors.uncertainty) {
      const uncertaintyWords = ['uncertain', 'not sure', 'unclear', 'unsure'];
      if (uncertaintyWords.some(word => text.toLowerCase().includes(word))) {
        penalty += penaltyFactors.uncertainty;
      }
    }

    if (penaltyFactors.conditionals) {
      const conditionalWords = ['if', 'unless', 'provided', 'assuming'];
      if (conditionalWords.some(word => text.toLowerCase().includes(word))) {
        penalty += penaltyFactors.conditionals;
      }
    }

    return penalty;
  }

  private extractActionItems(text: string): Array<{ action: string; specificity: 'vague' | 'specific' | 'detailed'; confidence: number }> {
    const actionItems = [];
    const actionVerbs = ['check', 'update', 'review', 'send', 'create', 'fix', 'investigate', 'analyze', 'implement', 'test'];

    for (const verb of actionVerbs) {
      if (text.toLowerCase().includes(verb)) {
        // Extract action phrase around the verb
        const verbIndex = text.toLowerCase().indexOf(verb);
        const actionPhrase = text.substring(Math.max(0, verbIndex - 10), verbIndex + 30).trim();

        const specificity = this.determineActionSpecificity(actionPhrase);
        const confidence = specificity === 'detailed' ? 0.9 : specificity === 'specific' ? 0.7 : 0.5;

        actionItems.push({
          action: actionPhrase,
          specificity,
          confidence
        });
      }
    }

    return actionItems;
  }

  private analyzeUrgency(text: string, context: ConversationContext): 'low' | 'medium' | 'high' | 'critical' {
    const criticalMarkers = ['urgent', 'emergency', 'critical', 'asap', 'immediately'];
    const highMarkers = ['soon', 'quickly', 'priority', 'today', 'this morning'];
    const mediumMarkers = ['this week', 'when possible', 'by end of week'];

    const lowerText = text.toLowerCase();

    if (criticalMarkers.some(marker => lowerText.includes(marker))) return 'critical';
    if (highMarkers.some(marker => lowerText.includes(marker))) return 'high';
    if (mediumMarkers.some(marker => lowerText.includes(marker))) return 'medium';

    return 'low';
  }

  private parseTimeframe(timeframeText: string): { type: 'absolute' | 'relative'; value: string; estimatedDays?: number; confidence: number } {
    const text = timeframeText.toLowerCase().trim();

    // Absolute dates
    const absolutePatterns = [
      { pattern: /\b(today)\b/, days: 0, confidence: 0.95 },
      { pattern: /\b(tomorrow)\b/, days: 1, confidence: 0.95 },
      { pattern: /\b(monday|tuesday|wednesday|thursday|friday|saturday|sunday)\b/, days: 3, confidence: 0.8 },
    ];

    // Relative dates
    const relativePatterns = [
      { pattern: /\b(\d+)\s*(hours?)\b/, days: 0.1, confidence: 0.9 },
      { pattern: /\b(\d+)\s*(days?)\b/, multiplier: true, confidence: 0.9 },
      { pattern: /\b(\d+)\s*(weeks?)\b/, multiplier: 7, confidence: 0.85 },
    ];

    for (const { pattern, days, confidence } of absolutePatterns) {
      if (pattern.test(text)) {
        return {
          type: 'absolute',
          value: timeframeText,
          estimatedDays: days,
          confidence
        };
      }
    }

    for (const { pattern, days, multiplier, confidence } of relativePatterns) {
      const match = text.match(pattern);
      if (match) {
        const number = parseInt(match[1]) || 1;
        return {
          type: 'relative',
          value: timeframeText,
          estimatedDays: multiplier === true ? number : typeof multiplier === 'number' ? number * multiplier : days,
          confidence
        };
      }
    }

    return {
      type: 'relative',
      value: timeframeText,
      confidence: 0.5
    };
  }

  private analyzeContextualFactors(context: ConversationContext, text: string) {
    return {
      conversationStage: context.conversationStage,
      topicRelevance: this.calculateTopicRelevance(context, text),
      hypotheticalRisk: context.semanticContext.hypotheticalIndicators ? 0.8 : 0.1,
      linguisticCertainty: this.calculateLinguisticCertainty(text)
    };
  }

  private calculateTopicRelevance(context: ConversationContext, text: string): number {
    if (context.semanticContext.topics.length === 0) return 0.5;

    const topicWords = context.semanticContext.topics.flatMap(t => t.name.split(' '));
    const textWords = text.toLowerCase().split(/\s+/);

    const overlap = topicWords.filter(word => textWords.includes(word.toLowerCase())).length;
    return Math.min(1, overlap / Math.max(1, topicWords.length));
  }

  private calculateLinguisticCertainty(text: string): number {
    const certaintyMarkers = ['will', 'shall', 'definitely', 'certainly', 'absolutely'];
    const uncertaintyMarkers = ['might', 'could', 'maybe', 'perhaps', 'possibly'];

    const lowerText = text.toLowerCase();
    const certaintyCount = certaintyMarkers.filter(marker => lowerText.includes(marker)).length;
    const uncertaintyCount = uncertaintyMarkers.filter(marker => lowerText.includes(marker)).length;

    return Math.max(0, Math.min(1, 0.5 + (certaintyCount * 0.2) - (uncertaintyCount * 0.15)));
  }

  private mapPatternTypeToCommitmentType(patternType: string): 'promise' | 'deadline' | 'action' | 'deliverable' | 'investigation' {
    const mapping = {
      'promise': 'promise' as const,
      'action': 'action' as const,
      'deliverable': 'deliverable' as const,
      'investigation': 'investigation' as const,
      'deadline': 'deadline' as const
    };

    return mapping[patternType as keyof typeof mapping] || 'action';
  }

  private determineActionSpecificity(actionPhrase: string): 'vague' | 'specific' | 'detailed' {
    if (actionPhrase.length < 15) return 'vague';
    if (actionPhrase.length > 40) return 'detailed';
    return 'specific';
  }

  private extractQuestionSubject(questionText: string): string {
    // Extract the main subject of the question
    const words = questionText.split(/\s+/);
    const contentWords = words.filter(word => 
      !['what', 'how', 'when', 'where', 'why', 'who', 'which', 'is', 'are', 'do', 'does', 'can', 'could', 'would', 'should'].includes(word.toLowerCase())
    );

    return contentWords.slice(0, 5).join(' ') || 'general';
  }

  private analyzeQuestionComplexity(questionText: string): 'simple' | 'moderate' | 'complex' {
    const wordCount = questionText.split(/\s+/).length;
    const hasMultipleClauses = (questionText.match(/,/g) || []).length >= 2;
    const hasSubQuestions = questionText.includes('?') && (questionText.match(/\?/g) || []).length > 1;

    if (wordCount > 20 || hasMultipleClauses || hasSubQuestions) return 'complex';
    if (wordCount > 8) return 'moderate';
    return 'simple';
  }

  private extractUrgencyIndicators(text: string): string[] {
    const urgencyWords = ['urgent', 'quickly', 'asap', 'immediately', 'soon', 'fast', 'right away'];
    return urgencyWords.filter(word => text.toLowerCase().includes(word));
  }

  private classifyQuestionIntent(text: string, baseType: string): { primary: string; secondary?: string; confidence: number } {
    const intentPatterns = {
      'information_seeking': /\b(?:what|who|when|where)\b/gi,
      'procedure_request': /\b(?:how|steps|process|method)\b/gi,
      'explanation_request': /\b(?:why|explain|describe|clarify)\b/gi,
      'opinion_seeking': /\b(?:think|opinion|recommend|suggest)\b/gi,
      'confirmation_seeking': /\b(?:is|are|do|does|did|can|could)\b/gi
    };

    for (const [intent, pattern] of Object.entries(intentPatterns)) {
      if (pattern.test(text)) {
        return {
          primary: intent,
          confidence: 0.8
        };
      }
    }

    return {
      primary: baseType,
      confidence: 0.6
    };
  }

  private extractSemanticFeatures(text: string) {
    // Simplified semantic feature extraction
    const words = text.toLowerCase().split(/\s+/);
    const entities = words.filter(word => word.length > 4 && /^[A-Z]/.test(word));
    const concepts = words.filter(word => ['system', 'process', 'method', 'approach', 'technique'].includes(word));

    return {
      entities,
      concepts,
      relationships: []
    };
  }

  private calculateContextualRelevance(context: ConversationContext, text: string): number {
    // Simplified contextual relevance calculation
    return 0.7; // Placeholder
  }

  // Additional helper methods for context analysis
  private async analyzeSemanticContext(message: Message, previousMessages: string[]): Promise<SemanticContext> {
    return {
      entities: [],
      topics: [{ name: 'general', relevance: 0.5 }],
      sentiment: { polarity: 0, confidence: 0.5 },
      intentFlow: [],
      hypotheticalIndicators: this.detectHypotheticalLanguage(message.content)
    };
  }

  private analyzeTemporalContext(content: string): TemporalContext {
    const timeReferences = this.extractTimeReferences(content);
    const sequenceIndicators = this.extractSequenceIndicators(content);
    const conditionalMarkers = this.extractConditionalMarkers(content);

    return {
      timeReferences,
      sequenceIndicators,
      conditionalMarkers
    };
  }

  private extractCurrentTopic(message: Message, previousMessages: string[]): string {
    // Simplified topic extraction
    return 'general';
  }

  private determineConversationStage(messageIndex: number, totalMessages: number): 'opening' | 'development' | 'resolution' | 'closing' {
    const ratio = messageIndex / Math.max(1, totalMessages);
    if (ratio < 0.2) return 'opening';
    if (ratio < 0.8) return 'development';
    if (ratio < 0.95) return 'resolution';
    return 'closing';
  }

  private detectHypotheticalLanguage(text: string): boolean {
    const hypotheticalMarkers = ['if', 'suppose', 'imagine', 'what if', 'hypothetically', 'theoretically'];
    return hypotheticalMarkers.some(marker => text.toLowerCase().includes(marker));
  }

  private extractTimeReferences(content: string): Array<{ text: string; type: 'absolute' | 'relative'; confidence: number }> {
    const timePatterns = [
      { pattern: /\b(?:today|tomorrow|yesterday)\b/gi, type: 'absolute' as const, confidence: 0.9 },
      { pattern: /\b(?:next|last|this)\s+(?:week|month|year)\b/gi, type: 'relative' as const, confidence: 0.8 },
      { pattern: /\bin\s+\d+\s+(?:minutes?|hours?|days?|weeks?)\b/gi, type: 'relative' as const, confidence: 0.85 }
    ];

    const references = [];
    for (const { pattern, type, confidence } of timePatterns) {
      const matches = content.matchAll(pattern);
      for (const match of matches) {
        references.push({
          text: match[0],
          type,
          confidence
        });
      }
    }

    return references;
  }

  private extractSequenceIndicators(content: string): string[] {
    const sequenceWords = ['first', 'then', 'next', 'finally', 'after', 'before', 'meanwhile', 'subsequently'];
    return sequenceWords.filter(word => content.toLowerCase().includes(word));
  }

  private extractModalVerbs(text: string): string[] {
    const modals = ['will', 'would', 'shall', 'should', 'can', 'could', 'may', 'might', 'must'];
    const words = text.toLowerCase().split(/\s+/);
    return words.filter(word => modals.includes(word));
  }

  private extractCertaintyMarkers(text: string): string[] {
    const certaintyWords = ['definitely', 'certainly', 'absolutely', 'guarantee', 'promise', 'commit'];
    return certaintyWords.filter(word => text.toLowerCase().includes(word));
  }

  private extractHedgingLanguage(text: string): string[] {
    const hedgeWords = ['maybe', 'perhaps', 'possibly', 'probably', 'likely', 'sort of', 'kind of'];
    return hedgeWords.filter(word => text.toLowerCase().includes(word));
  }

  private extractNegationMarkers(text: string): string[] {
    const negationWords = ['not', 'never', 'no', "won't", "can't", "don't", 'unable', 'impossible'];
    return negationWords.filter(word => text.toLowerCase().includes(word));
  }

  private extractConditionalMarkers(text: string): string[] {
    const conditionalWords = ['if', 'unless', 'provided', 'assuming', 'given', 'when'];
    return conditionalWords.filter(word => text.toLowerCase().includes(word));
  }

  private extractTemporalMarkers(text: string): string[] {
    const temporalWords = ['now', 'soon', 'later', 'eventually', 'immediately', 'shortly', 'presently'];
    return temporalWords.filter(word => text.toLowerCase().includes(word));
  }

  private calculateQualityScore(validationResult: any): number {
    // Calculate overall quality score from validation result
    let score = validationResult.confidence;

    // Penalty for high false positive risk
    score -= validationResult.falsePositiveRisk * 0.5;

    // Bonus for high contextual score
    score += validationResult.contextualScore * 0.2;

    // Penalty for issues
    const issueCount = validationResult.issues.length;
    score -= issueCount * 0.1;

    return Math.max(0, Math.min(1, score));
  }

  private calculateAmbiguityRisk(validationResult: any): number {
    // Calculate risk of ambiguous interpretation
    let risk = 0.1; // Base risk

    // Higher risk for low linguistic score
    if (validationResult.linguisticScore < 0.5) {
      risk += 0.2;
    }

    // Higher risk if issues present
    const ambiguityIssues = validationResult.issues.filter((issue: any) => 
      issue.type === 'ambiguity'
    ).length;

    risk += ambiguityIssues * 0.15;

    return Math.min(1, risk);
  }

  private generateQualityRecommendations(
    testResults: any,
    qualityDistribution: any,
    commonIssues: any[]
  ): string[] {
    const recommendations = [];

    if (testResults.falsePositiveRate > 0.05) {
      recommendations.push('Reduce false positive rate by improving contextual validation');
    }

    if (testResults.precision < 0.85) {
      recommendations.push('Enhance pattern specificity to improve precision');
    }

    if (qualityDistribution.low > qualityDistribution.high) {
      recommendations.push('Focus on improving low-quality pattern detection');
    }

    if (commonIssues.length > 0) {
      const topIssue = commonIssues[0];
      recommendations.push(`Address most common issue: ${topIssue.issue}`);
    }

    if (recommendations.length === 0) {
      recommendations.push('Pattern quality is within acceptable parameters');
    }

    return recommendations;
  }

  private calculateReadinessScore(testResults: any, accuracyMetrics: any, validationIssues: any): number {
    let score = 1.0;

    // Accuracy component (40% weight)
    score *= 0.6 + (testResults.f1Score * 0.4);

    // False positive penalty (30% weight)
    const fpPenalty = Math.min(1, testResults.falsePositiveRate / 0.05); // Normalize to 5% threshold
    score *= 0.7 + (0.3 * (1 - fpPenalty));

    // Validation coverage (20% weight)
    score *= 0.8 + (accuracyMetrics.validationRate * 0.2);

    // Critical issues penalty (10% weight)
    const criticalPenalty = Math.min(1, validationIssues.criticalIssues / 10); // Normalize to 10 issues
    score *= 0.9 + (0.1 * (1 - criticalPenalty));

    return Math.max(0, Math.min(1, score));
  }

  private async initializePatternTables(): Promise<void> {
    // Create tables for enhanced pattern storage
    this.executeStatement(
      'create_enhanced_commitments_table',
      `CREATE TABLE IF NOT EXISTS enhanced_commitments (
        id TEXT PRIMARY KEY,
        message_id TEXT NOT NULL,
        commitment_text TEXT NOT NULL,
        commitment_type TEXT NOT NULL,
        confidence REAL NOT NULL,
        validated_confidence REAL NOT NULL,
        urgency_level TEXT NOT NULL,
        timeframe TEXT,
        action_items TEXT,
        contextual_factors TEXT,
        validation_result TEXT,
        extracted_at INTEGER NOT NULL,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (message_id) REFERENCES messages(id)
      )`
    );

    this.executeStatement(
      'create_enhanced_questions_table',
      `CREATE TABLE IF NOT EXISTS enhanced_questions (
        id TEXT PRIMARY KEY,
        message_id TEXT NOT NULL,
        question_text TEXT NOT NULL,
        question_type TEXT NOT NULL,
        confidence REAL NOT NULL,
        validated_confidence REAL NOT NULL,
        subject TEXT NOT NULL,
        complexity TEXT NOT NULL,
        urgency_indicators TEXT,
        contextual_relevance REAL NOT NULL,
        intent_classification TEXT,
        semantic_features TEXT,
        validation_result TEXT,
        extracted_at INTEGER NOT NULL,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (message_id) REFERENCES messages(id)
      )`
    );

    this.executeStatement(
      'create_quality_reports_table',
      `CREATE TABLE IF NOT EXISTS pattern_quality_reports (
        id TEXT PRIMARY KEY,
        timestamp INTEGER NOT NULL,
        total_patterns INTEGER NOT NULL,
        validated_patterns INTEGER NOT NULL,
        quality_distribution TEXT NOT NULL,
        accuracy_metrics TEXT NOT NULL,
        common_issues TEXT NOT NULL,
        recommendations TEXT NOT NULL,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )`
    );
  }

  private async storeQualityReport(report: PatternQualityReport): Promise<void> {
    this.executeStatement(
      'store_quality_report',
      `INSERT INTO pattern_quality_reports 
       (id, timestamp, total_patterns, validated_patterns, quality_distribution,
        accuracy_metrics, common_issues, recommendations)
       VALUES (?, ?, ?, ?, ?, ?, ?, ?)`,
      [
        this.generateId(),
        report.timestamp,
        report.totalPatterns,
        report.validatedPatterns,
        JSON.stringify(report.qualityDistribution),
        JSON.stringify(report.accuracyMetrics),
        JSON.stringify(report.commonIssues),
        JSON.stringify(report.recommendations)
      ]
    );
  }
}

import { BaseRepository } from '../../../storage/repositories/BaseRepository.js';
import { DatabaseManager } from '../../../storage/Database.js';
import { Entity, EntityType } from '../../../storage/repositories/EntityRepository.js';
import { EntityMention } from '../../../storage/repositories/KnowledgeGraphRepository.js';

/**
 * Auto-tagging service that automatically classifies and tags conversations
 * based on entity analysis, activity patterns, and urgency signals
 */

// Topic tag based on entity analysis
export interface TopicTag {
  name: string;
  type: 'entity' | 'theme' | 'domain';
  relevance: number;
  source: 'primary_entity' | 'entity_cluster' | 'keyword_analysis';
}

// Activity classification
export interface ActivityClassification {
  type: 'discussion' | 'decision' | 'planning' | 'problem_solving' | 'learning' | 'review' | 'brainstorming';
  confidence: number;
  indicators: string[];
}

// Urgency analysis
export interface UrgencyAnalysis {
  level: 'none' | 'low' | 'medium' | 'high' | 'critical';
  score: number;
  signals: string[];
  deadline?: Date;
}

// Project context from entity clustering
export interface ProjectContext {
  name: string;
  entities: Entity[];
  confidence: number;
  type: 'ongoing' | 'new' | 'completed';
}

// Complete auto-tagging result
export interface AutoTaggingResult {
  conversationId: string;
  topicTags: TopicTag[];
  activity: ActivityClassification;
  urgency: UrgencyAnalysis;
  projectContexts: ProjectContext[];
  generatedAt: Date;
}

// Configuration options
export interface AutoTaggingConfig {
  minEntityRelevance?: number;
  maxTopicTags?: number;
  minProjectConfidence?: number;
  urgencyKeywords?: string[];
  activityPatterns?: Record<string, RegExp[]>;
}

export class AutoTaggingService {
  private config: Required<AutoTaggingConfig>;
  private dbManager: DatabaseManager;

  // Activity patterns for classification
  private readonly ACTIVITY_PATTERNS = {
    discussion: [
      /discuss(?:ing|ed)?/i,
      /talk(?:ing|ed)? about/i,
      /conversation about/i,
      /thoughts on/i
    ],
    decision: [
      /decid(?:e|ed|ing)/i,
      /choos(?:e|ing|en)/i,
      /select(?:ed|ing)?/i,
      /option(?:s)? (?:is|are)/i,
      /go with/i
    ],
    planning: [
      /plan(?:ning|ned)?/i,
      /schedul(?:e|ing|ed)/i,
      /roadmap/i,
      /timeline/i,
      /milestone/i
    ],
    problem_solving: [
      /problem/i,
      /issue/i,
      /fix(?:ing|ed)?/i,
      /solv(?:e|ing|ed)/i,
      /debug(?:ging)?/i,
      /error/i
    ],
    learning: [
      /learn(?:ing|ed)?/i,
      /understand(?:ing)?/i,
      /explain/i,
      /how (?:does|do|to)/i,
      /what (?:is|are)/i
    ],
    review: [
      /review(?:ing|ed)?/i,
      /evaluat(?:e|ing|ed)/i,
      /assess(?:ing|ed)?/i,
      /retrospective/i
    ],
    brainstorming: [
      /brainstorm(?:ing)?/i,
      /ideas?/i,
      /what if/i,
      /could we/i,
      /creativ(?:e|ity)/i
    ]
  };

  // Urgency keywords with scores
  private readonly URGENCY_KEYWORDS = {
    critical: [
      { pattern: /urgent(?:ly)?/i, score: 1.0 },
      { pattern: /critical/i, score: 0.9 },
      { pattern: /emergency/i, score: 1.0 },
      { pattern: /asap/i, score: 0.9 },
      { pattern: /immediately/i, score: 0.9 }
    ],
    high: [
      { pattern: /important/i, score: 0.7 },
      { pattern: /priority/i, score: 0.7 },
      { pattern: /deadline/i, score: 0.8 },
      { pattern: /by (?:tomorrow|today|tonight)/i, score: 0.8 },
      { pattern: /needs? (?:to be|this)/i, score: 0.6 }
    ],
    medium: [
      { pattern: /soon/i, score: 0.5 },
      { pattern: /this week/i, score: 0.5 },
      { pattern: /when you (?:can|get a chance)/i, score: 0.4 },
      { pattern: /next (?:week|sprint)/i, score: 0.4 }
    ]
  };

  constructor(dbManager: DatabaseManager, config: Partial<AutoTaggingConfig> = {}) {
    this.dbManager = dbManager;
    this.config = {
      minEntityRelevance: config.minEntityRelevance ?? 0.3,
      maxTopicTags: config.maxTopicTags ?? 5,
      minProjectConfidence: config.minProjectConfidence ?? 0.6,
      urgencyKeywords: config.urgencyKeywords ?? [],
      activityPatterns: config.activityPatterns ?? this.ACTIVITY_PATTERNS
    };
  }

  /**
   * Generate topic tags from entity analysis
   */
  async generateTopicTags(conversationId: string): Promise<TopicTag[]> {
    // Get entities from conversation
    const entities = await this.getConversationEntities(conversationId);

    const tags: TopicTag[] = [];

    // Primary entities become topic tags
    const primaryEntities = entities
      .filter(e => e.mentions >= 3)
      .sort((a, b) => b.mentions - a.mentions)
      .slice(0, this.config.maxTopicTags);

    for (const entity of primaryEntities) {
      tags.push({
        name: entity.name,
        type: 'entity',
        relevance: this.calculateEntityRelevance(entity, entities.length),
        source: 'primary_entity'
      });
    }

    // Find entity clusters (frequently co-occurring entities)
    const clusters = await this.findEntityClusters(conversationId);
    for (const cluster of clusters) {
      if (cluster.entities.length >= 3) {
        const clusterName = this.generateClusterName(cluster.entities);
        tags.push({
          name: clusterName,
          type: 'theme',
          relevance: cluster.strength,
          source: 'entity_cluster'
        });
      }
    }

    // Domain-based tags from entity types
    const domainTags = this.generateDomainTags(entities);
    tags.push(...domainTags);

    // Sort by relevance and limit
    return tags
      .sort((a, b) => b.relevance - a.relevance)
      .slice(0, this.config.maxTopicTags);
  }

  /**
   * Classify conversation activity type
   */
  async classifyActivity(conversationId: string): Promise<ActivityClassification> {
    const messages = await this.getConversationMessages(conversationId);
    const messageText = messages.map(m => m.content).join(' ');

    const scores: Record<string, { score: number; indicators: string[] }> = {};

    // Check each activity pattern
    for (const [activity, patterns] of Object.entries(this.config.activityPatterns)) {
      let score = 0;
      const indicators: string[] = [];

      for (const pattern of patterns) {
        const matches = messageText.match(pattern);
        if (matches) {
          score += matches.length;
          indicators.push(matches[0]);
        }
      }

      scores[activity] = { score, indicators };
    }

    // Find the highest scoring activity
    let bestActivity = 'discussion';
    let bestScore = 0;
    let bestIndicators: string[] = [];

    for (const [activity, data] of Object.entries(scores)) {
      if (data.score > bestScore) {
        bestActivity = activity;
        bestScore = data.score;
        bestIndicators = data.indicators;
      }
    }

    // Calculate confidence based on score and message count
    const confidence = Math.min(1, bestScore / (messages.length * 0.2));

    return {
      type: bestActivity as ActivityClassification['type'],
      confidence,
      indicators: bestIndicators.slice(0, 5)
    };
  }

  /**
   * Detect urgency signals in conversation
   */
  async detectUrgencySignals(conversationId: string): Promise<UrgencyAnalysis> {
    const messages = await this.getConversationMessages(conversationId);
    const recentMessages = messages.slice(-10); // Focus on recent messages

    let maxScore = 0;
    const allSignals: string[] = [];
    let deadline: Date | undefined;

    for (const message of recentMessages) {
      for (const [level, keywords] of Object.entries(this.URGENCY_KEYWORDS)) {
        for (const { pattern, score } of keywords) {
          const matches = message.content.match(pattern);
          if (matches) {
            maxScore = Math.max(maxScore, score);
            allSignals.push(matches[0]);
          }
        }
      }

      // Check for deadline mentions
      const deadlineMatch = message.content.match(/by\s+(\d{1,2}\/\d{1,2}(?:\/\d{2,4})?|\w+\s+\d{1,2}(?:st|nd|rd|th)?)/i);
      if (deadlineMatch && !deadline) {
        deadline = this.parseDeadline(deadlineMatch[1]);
      }
    }

    // Add custom urgency keywords from config
    for (const keyword of this.config.urgencyKeywords) {
      const pattern = new RegExp(keyword, 'i');
      const matches = messages.some(m => pattern.test(m.content));
      if (matches) {
        maxScore = Math.max(maxScore, 0.6);
        allSignals.push(keyword);
      }
    }

    // Determine urgency level
    let level: UrgencyAnalysis['level'] = 'none';
    if (maxScore >= 0.9) level = 'critical';
    else if (maxScore >= 0.7) level = 'high';
    else if (maxScore >= 0.5) level = 'medium';
    else if (maxScore >= 0.3) level = 'low';

    return {
      level,
      score: maxScore,
      signals: [...new Set(allSignals)].slice(0, 5),
      deadline
    };
  }

  /**
   * Identify project contexts from entity clustering
   */
  async identifyProjectContexts(conversationId: string): Promise<ProjectContext[]> {
    const clusters = await this.findEntityClusters(conversationId);
    const projects: ProjectContext[] = [];

    for (const cluster of clusters) {
      if (cluster.strength >= this.config.minProjectConfidence) {
        // Determine project type based on entity relationships
        const projectType = await this.determineProjectType(cluster.entities);

        projects.push({
          name: this.generateClusterName(cluster.entities),
          entities: cluster.entities,
          confidence: cluster.strength,
          type: projectType
        });
      }
    }

    return projects.sort((a, b) => b.confidence - a.confidence);
  }

  /**
   * Perform complete auto-tagging for a conversation
   */
  async autoTagConversation(conversationId: string): Promise<AutoTaggingResult> {
    const [topicTags, activity, urgency, projectContexts] = await Promise.all([
      this.generateTopicTags(conversationId),
      this.classifyActivity(conversationId),
      this.detectUrgencySignals(conversationId),
      this.identifyProjectContexts(conversationId)
    ]);

    return {
      conversationId,
      topicTags,
      activity,
      urgency,
      projectContexts,
      generatedAt: new Date()
    };
  }

  // Helper methods

  private async getConversationEntities(conversationId: string): Promise<Array<Entity & { mentions: number }>> {
    const query = `
      SELECT 
        e.*,
        COUNT(em.id) as mentions
      FROM entities e
      JOIN entity_mentions em ON e.id = em.entity_id
      WHERE em.conversation_id = ?
      GROUP BY e.id
      ORDER BY mentions DESC
    `;

    return this.dbManager.getConnection().prepare(query).all(conversationId) as Array<Entity & { mentions: number }>;
  }

  private async getConversationMessages(conversationId: string): Promise<Array<{ content: string; created_at: number }>> {
    const query = `
      SELECT content, created_at
      FROM messages
      WHERE conversation_id = ?
      ORDER BY created_at
    `;

    return this.dbManager.getConnection().prepare(query).all(conversationId) as Array<{ content: string; created_at: number }>;
  }

  private calculateEntityRelevance(entity: Entity & { mentions: number }, totalEntities: number): number {
    // Relevance based on mentions, entity type importance, and relative frequency
    const mentionScore = Math.min(1, entity.mentions / 10);
    const typeScore = this.getEntityTypeScore(entity.type);
    const frequencyScore = entity.mentions / Math.max(totalEntities, 1);

    return (mentionScore * 0.4 + typeScore * 0.3 + frequencyScore * 0.3);
  }

  private getEntityTypeScore(type: EntityType): number {
    const scores: Record<EntityType, number> = {
      person: 0.8,
      organization: 0.9,
      technical: 1.0,
      product: 0.9,
      location: 0.6,
      event: 0.7,
      decision: 0.9,
      concept: 0.8
    };
    return scores[type] || 0.5;
  }

  private async findEntityClusters(conversationId: string): Promise<Array<{ entities: Entity[]; strength: number }>> {
    // Find entities that frequently appear together
    const query = `
      WITH cooccurrence AS (
        SELECT 
          em1.entity_id as entity1_id,
          em2.entity_id as entity2_id,
          COUNT(*) as cooccurrence_count
        FROM entity_mentions em1
        JOIN entity_mentions em2 ON em1.message_id = em2.message_id
        WHERE em1.conversation_id = ?
          AND em2.conversation_id = ?
          AND em1.entity_id < em2.entity_id
        GROUP BY em1.entity_id, em2.entity_id
        HAVING cooccurrence_count >= 2
      )
      SELECT 
        e1.id as e1_id, e1.name as e1_name, e1.type as e1_type,
        e2.id as e2_id, e2.name as e2_name, e2.type as e2_type,
        c.cooccurrence_count
      FROM cooccurrence c
      JOIN entities e1 ON c.entity1_id = e1.id
      JOIN entities e2 ON c.entity2_id = e2.id
      ORDER BY c.cooccurrence_count DESC
    `;

    const rows = this.dbManager.getConnection().prepare(query).all(conversationId, conversationId) as Array<{
      e1_id: string; e1_name: string; e1_type: EntityType;
      e2_id: string; e2_name: string; e2_type: EntityType;
      cooccurrence_count: number;
    }>;

    // Simple clustering algorithm
    const clusters: Map<string, Set<string>> = new Map();
    const entityMap: Map<string, Entity> = new Map();

    for (const row of rows) {
      // Store entity info
      entityMap.set(row.e1_id, { id: row.e1_id, name: row.e1_name, type: row.e1_type } as Entity);
      entityMap.set(row.e2_id, { id: row.e2_id, name: row.e2_name, type: row.e2_type } as Entity);

      // Find or create cluster
      let clusterId: string | null = null;
      for (const [id, cluster] of clusters) {
        if (cluster.has(row.e1_id) || cluster.has(row.e2_id)) {
          clusterId = id;
          break;
        }
      }

      if (clusterId) {
        clusters.get(clusterId)!.add(row.e1_id);
        clusters.get(clusterId)!.add(row.e2_id);
      } else {
        const newCluster = new Set([row.e1_id, row.e2_id]);
        clusters.set(row.e1_id, newCluster);
      }
    }

    // Convert to result format
    const result: Array<{ entities: Entity[]; strength: number }> = [];
    for (const [_, entityIds] of clusters) {
      const entities = Array.from(entityIds).map(id => entityMap.get(id)!);
      const strength = Math.min(1, entities.length / 10);
      result.push({ entities, strength });
    }

    return result;
  }

  private generateClusterName(entities: Entity[]): string {
    // Generate a meaningful name from entity cluster
    const technicalEntities = entities.filter(e => e.type === 'technical');
    const productEntities = entities.filter(e => e.type === 'product');
    const conceptEntities = entities.filter(e => e.type === 'concept');

    if (technicalEntities.length > 0) {
      return technicalEntities.map(e => e.name).join(' + ');
    } else if (productEntities.length > 0) {
      return productEntities[0].name + ' Project';
    } else if (conceptEntities.length > 0) {
      return conceptEntities[0].name + ' Discussion';
    } else {
      return entities.slice(0, 2).map(e => e.name).join(' & ');
    }
  }

  private generateDomainTags(entities: Array<Entity & { mentions: number }>): TopicTag[] {
    const domainCounts: Record<string, number> = {};

    for (const entity of entities) {
      const domain = this.getEntityDomain(entity.type);
      domainCounts[domain] = (domainCounts[domain] || 0) + entity.mentions;
    }

    return Object.entries(domainCounts)
      .filter(([_, count]) => count >= 3)
      .map(([domain, count]) => ({
        name: domain,
        type: 'domain' as const,
        relevance: Math.min(1, count / 20),
        source: 'keyword_analysis' as const
      }));
  }

  private getEntityDomain(type: EntityType): string {
    const domains: Record<EntityType, string> = {
      person: 'People & Teams',
      organization: 'Organizations',
      technical: 'Technology',
      product: 'Products',
      location: 'Places',
      event: 'Events',
      decision: 'Decisions',
      concept: 'Concepts'
    };
    return domains[type] || 'General';
  }

  private parseDeadline(text: string): Date | undefined {
    try {
      // Simple deadline parsing - could be enhanced
      const today = new Date();

      if (text.toLowerCase().includes('tomorrow')) {
        const tomorrow = new Date(today);
        tomorrow.setDate(tomorrow.getDate() + 1);
        return tomorrow;
      }

      if (text.toLowerCase().includes('today')) {
        return today;
      }

      // Try to parse date format
      const parsed = new Date(text);
      if (!isNaN(parsed.getTime())) {
        return parsed;
      }
    } catch {
      // Ignore parsing errors
    }

    return undefined;
  }

  private async determineProjectType(entities: Entity[]): Promise<ProjectContext['type']> {
    // Check if project has decision entities (likely completed)
    const hasDecisions = entities.some(e => e.type === 'decision');
    if (hasDecisions) {
      return 'completed';
    }

    // Check entity creation dates to determine if new
    const query = `
      SELECT MIN(created_at) as first_seen, MAX(created_at) as last_seen
      FROM entities
      WHERE id IN (${entities.map(() => '?').join(',')})
    `;

    const result = this.dbManager.getConnection().prepare(query).get(...entities.map(e => e.id)) as {
      first_seen: number;
      last_seen: number;
    };

    const ageInDays = (Date.now() - result.first_seen) / (1000 * 60 * 60 * 24);

    if (ageInDays < 7) {
      return 'new';
    }

    return 'ongoing';
  }
}

/**
 * Context Change Detector - Intelligence Services Expert specializing in context analysis
 * 
 * This service detects when conversation context shifts, identifies relevant historical
 * context, finds conflicting information, and optimizes context windows for current
 * conversations by leveraging our knowledge graph infrastructure.
 */

import { BaseRepository } from '../../../storage/repositories/BaseRepository.js';
import { EntityRepository, Entity, EntityType } from '../../../storage/repositories/EntityRepository.js';
import { KnowledgeGraphRepository, EntityMention, EntityRelationship } from '../../../storage/repositories/KnowledgeGraphRepository.js';
import { DatabaseManager } from '../../../storage/Database.js';
import { Message, Conversation } from '../../../types/interfaces.js';

/**
 * Represents a detected topic shift in conversation
 */
export interface TopicShift {
  /** Unique identifier for this shift */
  id: string;
  /** Message where shift was detected */
  shiftMessage: Message;
  /** Previous dominant entities */
  previousEntities: Entity[];
  /** New dominant entities */
  newEntities: Entity[];
  /** Confidence of shift detection (0-1) */
  shiftConfidence: number;
  /** Type of shift detected */
  shiftType: 'entity_replacement' | 'entity_addition' | 'topic_pivot' | 'context_expansion';
  /** Entities that triggered the shift */
  triggerEntities: Entity[];
  /** Timestamp when shift was detected */
  detectedAt: number;
}

/**
 * Represents relevant historical context for current conversation
 */
export interface RelevantHistory {
  /** Unique identifier for this history entry */
  id: string;
  /** Related conversation */
  conversation: Conversation;
  /** Messages relevant to current context */
  relevantMessages: Message[];
  /** Entities connecting this history to current context */
  connectingEntities: Entity[];
  /** Relevance score (0-1) */
  relevanceScore: number;
  /** Type of relevance */
  relevanceType: 'entity_overlap' | 'relationship_chain' | 'topic_continuation' | 'problem_resolution';
  /** Time since this history was last mentioned */
  daysSinceLastMention: number;
}

/**
 * Represents conflicting information about entities
 */
export interface ConflictingInformation {
  /** Unique identifier for this conflict */
  id: string;
  /** Entity that has conflicting information */
  entity: Entity;
  /** Messages containing conflicting statements */
  conflictingMessages: Array<{
    message: Message;
    extractedClaim: string;
    confidence: number;
  }>;
  /** Type of conflict */
  conflictType: 'property_contradiction' | 'status_inconsistency' | 'relationship_conflict' | 'temporal_impossibility';
  /** Severity of conflict (0-1) */
  conflictSeverity: number;
  /** Suggested resolution */
  suggestedResolution?: string;
  /** Timestamp when conflict was detected */
  detectedAt: number;
}

/**
 * Represents optimal context window analysis
 */
export interface ContextWindow {
  /** Unique identifier for this context window */
  id: string;
  /** Core entities for current conversation */
  coreEntities: Entity[];
  /** Recommended messages to include in context */
  recommendedMessages: Message[];
  /** Context relevance score (0-1) */
  contextRelevance: number;
  /** Estimated token count for this context */
  estimatedTokens: number;
  /** Context freshness score (0-1) */
  freshness: number;
  /** Entities that might become relevant soon */
  potentialEntities: Entity[];
}

/**
 * Entity appearance pattern in conversation
 */
export interface EntityPattern {
  entity: Entity;
  frequency: number;
  firstMention: number;
  lastMention: number;
  mentionTrend: 'increasing' | 'decreasing' | 'stable' | 'sporadic';
  averageGapBetweenMentions: number;
  coOccurringEntities: Array<{ entity: Entity; coOccurrenceCount: number }>;
}

/**
 * Configuration for context change detection
 */
export interface ContextDetectionConfig {
  /** Minimum confidence threshold for detecting shifts */
  minShiftConfidence: number;
  /** Window size for analyzing entity patterns (in messages) */
  entityPatternWindow: number;
  /** Minimum relevance score for historical context */
  minRelevanceScore: number;
  /** Maximum age of historical context in days */
  maxHistoryAgeDays: number;
  /** Minimum conflict severity to report */
  minConflictSeverity: number;
  /** Maximum context window size in estimated tokens */
  maxContextTokens: number;
}

/**
 * Context Change Detection Service
 */
export class ContextChangeDetector extends BaseRepository {
  private entityRepository: EntityRepository;
  private knowledgeGraphRepo: KnowledgeGraphRepository;
  private config: ContextDetectionConfig;

  constructor(databaseManager: DatabaseManager, entityRepository: EntityRepository, knowledgeGraphRepo: KnowledgeGraphRepository, config: Partial<ContextDetectionConfig> = {}) {
    super(databaseManager);
    this.entityRepository = entityRepository;
    this.knowledgeGraphRepo = knowledgeGraphRepo;

    this.config = {
      minShiftConfidence: 0.6,
      entityPatternWindow: 20,
      minRelevanceScore: 0.4,
      maxHistoryAgeDays: 90,
      minConflictSeverity: 0.5,
      maxContextTokens: 4000,
      ...config
    };
  }

  /**
   * Detect topic shifts by analyzing entity frequency changes
   */
  async detectTopicShifts(
    conversationId: string,
    options: {
      lookbackMessages?: number;
      minShiftConfidence?: number;
    } = {}
  ): Promise<TopicShift[]> {
    const {
      lookbackMessages = this.config.entityPatternWindow,
      minShiftConfidence = this.config.minShiftConfidence
    } = options;

    // Get recent messages for this conversation
    const messages = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
    }>(
      'get_recent_messages_for_shift_detection',
      `SELECT * FROM messages 
       WHERE conversation_id = ? 
       ORDER BY created_at DESC 
       LIMIT ?`,
      [conversationId, lookbackMessages]
    );

    if (messages.length < 4) {
      return []; // Need sufficient messages to detect shifts
    }

    const shifts: TopicShift[] = [];
    const messageObjects = messages.map(row => this.mapRowToMessage(row)).reverse(); // Chronological order

    // Analyze entity patterns in sliding windows
    const windowSize = Math.min(6, Math.floor(messages.length / 3));

    for (let i = windowSize; i < messageObjects.length - windowSize; i++) {
      const previousWindow = messageObjects.slice(i - windowSize, i);
      const currentWindow = messageObjects.slice(i, i + windowSize);

      const shift = await this.analyzeWindowShift(
        previousWindow,
        currentWindow,
        messageObjects[i],
        minShiftConfidence
      );

      if (shift) {
        shifts.push(shift);
      }
    }

    return shifts.sort((a, b) => b.shiftConfidence - a.shiftConfidence);
  }

  /**
   * Identify relevant historical conversations about current entities
   */
  async identifyRelevantHistory(
    conversationId: string,
    options: {
      maxHistoryAge?: number;
      minRelevanceScore?: number;
      limit?: number;
    } = {}
  ): Promise<RelevantHistory[]> {
    const {
      maxHistoryAge = this.config.maxHistoryAgeDays,
      minRelevanceScore = this.config.minRelevanceScore,
      limit = 10
    } = options;

    // Get current conversation entities
    const currentEntities = await this.getCurrentConversationEntities(conversationId);
    if (currentEntities.length === 0) {
      return [];
    }

    const cutoffTimestamp = Date.now() - (maxHistoryAge * 24 * 60 * 60 * 1000);
    const entityIds = currentEntities.map(e => e.id);

    // Find conversations with overlapping entities
    const historicalConversations = this.executeStatementAll<{
      conversation_id: string;
      entity_count: number;
      last_mentioned_at: number;
      title: string | null;
      created_at: number;
      updated_at: number;
      metadata: string;
    }>(
      'find_historical_conversations_with_entities',
      `SELECT 
         c.id as conversation_id,
         COUNT(DISTINCT em.entity_id) as entity_count,
         MAX(em.created_at) as last_mentioned_at,
         c.title, c.created_at, c.updated_at, c.metadata
       FROM conversations c
       JOIN entity_mentions em ON c.id = em.conversation_id
       WHERE em.entity_id IN (${entityIds.map(() => '?').join(',')})
         AND c.id != ?
         AND c.updated_at > ?
       GROUP BY c.id
       HAVING entity_count >= 2
       ORDER BY entity_count DESC, last_mentioned_at DESC
       LIMIT ?`,
      [...entityIds, conversationId, cutoffTimestamp, limit * 2]
    );

    const relevantHistory: RelevantHistory[] = [];

    for (const histConv of historicalConversations) {
      const conversation: Conversation = {
        id: histConv.conversation_id,
        title: histConv.title || undefined,
        createdAt: histConv.created_at,
        updatedAt: histConv.updated_at,
        metadata: this.parseMetadata(histConv.metadata)
      };

      // Get connecting entities and their mentions
      const connectingData = await this.getConnectingEntitiesAndMessages(
        histConv.conversation_id,
        currentEntities
      );

      if (connectingData.entities.length === 0) {
        continue;
      }

      // Calculate relevance score
      const relevanceScore = this.calculateHistoryRelevanceScore(
        connectingData.entities,
        currentEntities,
        connectingData.messages,
        histConv.last_mentioned_at
      );

      if (relevanceScore >= minRelevanceScore) {
        const daysSinceLastMention = Math.floor(
          (Date.now() - histConv.last_mentioned_at) / (24 * 60 * 60 * 1000)
        );

        relevantHistory.push({
          id: this.generateId(),
          conversation,
          relevantMessages: connectingData.messages,
          connectingEntities: connectingData.entities,
          relevanceScore,
          relevanceType: this.determineRelevanceType(connectingData.entities, currentEntities),
          daysSinceLastMention
        });
      }
    }

    return relevantHistory
      .sort((a, b) => b.relevanceScore - a.relevanceScore)
      .slice(0, limit);
  }

  /**
   * Find conflicting information about entities across conversations
   */
  async findConflictingInformation(
    options: {
      conversationId?: string;
      entityIds?: string[];
      minSeverity?: number;
      limit?: number;
    } = {}
  ): Promise<ConflictingInformation[]> {
    const {
      conversationId,
      entityIds,
      minSeverity = this.config.minConflictSeverity,
      limit = 20
    } = options;

    let targetEntities: Entity[];

    if (entityIds) {
      targetEntities = [];
      for (const id of entityIds) {
        const entity = await this.entityRepository.getById(id);
        if (entity) targetEntities.push(entity);
      }
    } else if (conversationId) {
      targetEntities = await this.getCurrentConversationEntities(conversationId);
    } else {
      // Get most mentioned entities
      targetEntities = await this.entityRepository.getMostMentioned(50);
    }

    const conflicts: ConflictingInformation[] = [];

    for (const entity of targetEntities) {
      // Get all messages mentioning this entity
      const mentions = await this.knowledgeGraphRepo.getEntityMentions(entity.id, 100);

      if (mentions.length < 2) continue; // Need at least 2 mentions to find conflicts

      // Group mentions by conversation and analyze for conflicts
      const conversationGroups = new Map<string, any[]>();
      for (const mention of mentions) {
        if (!conversationGroups.has(mention.conversation_id)) {
          conversationGroups.set(mention.conversation_id, []);
        }
        conversationGroups.get(mention.conversation_id)!.push(mention);
      }

      // Detect conflicts between different conversations
      const conflictMessages = await this.detectEntityConflicts(entity, conversationGroups);

      if (conflictMessages.length >= 2) {
        const severity = this.calculateConflictSeverity(conflictMessages);

        if (severity >= minSeverity) {
          conflicts.push({
            id: this.generateId(),
            entity,
            conflictingMessages: conflictMessages,
            conflictType: this.classifyConflictType(conflictMessages),
            conflictSeverity: severity,
            suggestedResolution: this.generateResolutionSuggestion(entity, conflictMessages),
            detectedAt: Date.now()
          });
        }
      }
    }

    return conflicts
      .sort((a, b) => b.conflictSeverity - a.conflictSeverity)
      .slice(0, limit);
  }

  /**
   * Analyze optimal context window for current conversation
   */
  async analyzeContextWindow(
    conversationId: string,
    options: {
      maxTokens?: number;
      includeHistory?: boolean;
    } = {}
  ): Promise<ContextWindow> {
    const {
      maxTokens = this.config.maxContextTokens,
      includeHistory = true
    } = options;

    // Get current conversation entities and their importance
    const coreEntities = await this.getCurrentConversationEntities(conversationId);

    // Get recent messages from current conversation
    const recentMessages = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
    }>(
      'get_recent_conversation_messages',
      `SELECT * FROM messages 
       WHERE conversation_id = ? 
       ORDER BY created_at DESC 
       LIMIT 20`,
      [conversationId]
    ).map(row => this.mapRowToMessage(row));

    let recommendedMessages = recentMessages.reverse(); // Chronological order
    let estimatedTokens = this.estimateTokenCount(recommendedMessages);

    // If including history and we have token budget left
    if (includeHistory && estimatedTokens < maxTokens * 0.7) {
      const relevantHistory = await this.identifyRelevantHistory(conversationId, { limit: 5 });

      for (const history of relevantHistory) {
        const historyTokens = this.estimateTokenCount(history.relevantMessages);
        if (estimatedTokens + historyTokens <= maxTokens) {
          recommendedMessages = [...history.relevantMessages, ...recommendedMessages];
          estimatedTokens += historyTokens;
        }
      }
    }

    // Identify potential entities that might become relevant
    const potentialEntities = await this.identifyPotentialEntities(coreEntities);

    // Calculate context metrics
    const contextRelevance = this.calculateContextRelevance(coreEntities, recommendedMessages);
    const freshness = this.calculateFreshness(recommendedMessages);

    return {
      id: this.generateId(),
      coreEntities,
      recommendedMessages,
      contextRelevance,
      estimatedTokens,
      freshness,
      potentialEntities
    };
  }

  /**
   * Analyze entity patterns within a conversation
   */
  private async analyzeEntityPatterns(messages: Message[]): Promise<EntityPattern[]> {
    const patterns: EntityPattern[] = [];
    const entityMentions = new Map<string, number[]>();

    if (messages.length === 0) return patterns;

    const messageIds = messages.map(m => m.id);

    // Get all entity mentions for these messages
    const allMentions: {
      entity_id: string;
      message_id: string;
      created_at: number;
    }[] = [];

    if (messageIds.length > 0) {
      try {
        // Handle large message sets by batching queries (SQLite has a limit on IN clause size)
        const batchSize = 100;
        for (let i = 0; i < messageIds.length; i += batchSize) {
          const batch = messageIds.slice(i, i + batchSize);
          const batchMentions = this.executeStatementAll<{
            entity_id: string;
            message_id: string;
            created_at: number;
          }>(
            `get_entity_mentions_batch_${Math.floor(i / batchSize)}`,
            `SELECT entity_id, message_id, created_at
             FROM entity_mentions 
             WHERE message_id IN (${batch.map(() => '?').join(',')})
             ORDER BY created_at ASC`,
            batch
          );
          allMentions.push(...batchMentions);
        }
      } catch (error) {
        console.warn('Failed to get entity mentions for messages:', error);
        // Continue with empty mentions array
      }
    }

    // Collect mention positions for each entity
    for (const mention of allMentions) {
      const messageIndex = messages.findIndex(m => m.id === mention.message_id);
      if (messageIndex >= 0) {
        if (!entityMentions.has(mention.entity_id)) {
          entityMentions.set(mention.entity_id, []);
        }
        entityMentions.get(mention.entity_id)!.push(messageIndex);
      }
    }

    for (const [entityId, positions] of entityMentions.entries()) {
      const entity = await this.entityRepository.getById(entityId);
      if (!entity || positions.length < 1) continue;

      const frequency = positions.length;
      const firstMention = positions[0];
      const lastMention = positions[positions.length - 1];

      // Calculate mention trend
      const mentionTrend = this.calculateMentionTrend(positions, messages.length);

      // Calculate average gap between mentions
      const gaps = positions.slice(1).map((pos, i) => pos - positions[i]);
      const averageGap = gaps.length > 0 ? gaps.reduce((a, b) => a + b, 0) / gaps.length : 0;

      patterns.push({
        entity,
        frequency,
        firstMention: messages[firstMention].createdAt,
        lastMention: messages[lastMention].createdAt,
        mentionTrend,
        averageGapBetweenMentions: averageGap,
        coOccurringEntities: [] // Would be populated by analyzing co-occurrences
      });
    }

    return patterns.sort((a, b) => b.frequency - a.frequency);
  }

  /**
   * Analyze potential shift between two message windows
   */
  private async analyzeWindowShift(
    previousWindow: Message[],
    currentWindow: Message[],
    shiftMessage: Message,
    minConfidence: number
  ): Promise<TopicShift | null> {
    const previousPatterns = await this.analyzeEntityPatterns(previousWindow);
    const currentPatterns = await this.analyzeEntityPatterns(currentWindow);

    const previousEntities = previousPatterns.map(p => p.entity);
    const currentEntities = currentPatterns.map(p => p.entity);

    // Calculate entity overlap
    const previousEntityIds = new Set(previousEntities.map(e => e.id));
    const currentEntityIds = new Set(currentEntities.map(e => e.id));

    const intersection = new Set([...previousEntityIds].filter(id => currentEntityIds.has(id)));
    const union = new Set([...previousEntityIds, ...currentEntityIds]);

    const overlapRatio = intersection.size / union.size;
    const shiftConfidence = 1 - overlapRatio; // Lower overlap = higher shift confidence

    if (shiftConfidence < minConfidence) {
      return null;
    }

    // Identify entities that triggered the shift
    const newEntityIds = [...currentEntityIds].filter(id => !previousEntityIds.has(id));
    const triggerEntities = currentEntities.filter(e => newEntityIds.includes(e.id));

    // Classify shift type
    const shiftType = this.classifyShiftType(
      previousEntities.length,
      currentEntities.length,
      intersection.size,
      triggerEntities.length
    );

    return {
      id: this.generateId(),
      shiftMessage,
      previousEntities,
      newEntities: currentEntities,
      shiftConfidence,
      shiftType,
      triggerEntities,
      detectedAt: Date.now()
    };
  }

  /**
   * Get entities currently active in conversation
   */
  private async getCurrentConversationEntities(conversationId: string): Promise<Entity[]> {
    const entityData = this.executeStatementAll<{
      entity_id: string;
      mention_count: number;
      last_mentioned_at: number;
    }>(
      'get_conversation_entities',
      `SELECT entity_id, COUNT(*) as mention_count, MAX(created_at) as last_mentioned_at
       FROM entity_mentions 
       WHERE conversation_id = ?
       GROUP BY entity_id
       ORDER BY mention_count DESC, last_mentioned_at DESC
       LIMIT 20`,
      [conversationId]
    );

    const entities: Entity[] = [];
    for (const row of entityData) {
      const entity = await this.entityRepository.getById(row.entity_id);
      if (entity) entities.push(entity);
    }

    return entities;
  }

  /**
   * Get connecting entities and messages between conversations
   */
  private async getConnectingEntitiesAndMessages(
    historicalConversationId: string,
    currentEntities: Entity[]
  ): Promise<{ entities: Entity[]; messages: Message[] }> {
    const currentEntityIds = currentEntities.map(e => e.id);

    // Find entity mentions in historical conversation
    const mentions = this.executeStatementAll<{
      id: string;
      entity_id: string;
      message_id: string;
      conversation_id: string;
      mention_text: string;
      created_at: number;
      content: string;
      role: string;
      message_created_at: number;
    }>(
      'get_connecting_mentions',
      `SELECT em.*, m.content, m.role, m.created_at as message_created_at
       FROM entity_mentions em
       JOIN messages m ON em.message_id = m.id
       WHERE em.conversation_id = ? 
         AND em.entity_id IN (${currentEntityIds.map(() => '?').join(',')})
       ORDER BY em.created_at DESC
       LIMIT 10`,
      [historicalConversationId, ...currentEntityIds]
    );

    const connectingEntities: Entity[] = [];
    const messages: Message[] = [];
    const seenEntityIds = new Set<string>();
    const seenMessageIds = new Set<string>();

    for (const mention of mentions) {
      // Add entity if not already seen
      if (!seenEntityIds.has(mention.entity_id)) {
        const entity = currentEntities.find(e => e.id === mention.entity_id);
        if (entity) {
          connectingEntities.push(entity);
          seenEntityIds.add(mention.entity_id);
        }
      }

      // Add message if not already seen
      if (!seenMessageIds.has(mention.message_id)) {
        messages.push({
          id: mention.message_id,
          conversationId: mention.conversation_id,
          role: mention.role as 'user' | 'assistant' | 'system',
          content: mention.content,
          createdAt: mention.message_created_at
        });
        seenMessageIds.add(mention.message_id);
      }
    }

    return { entities: connectingEntities, messages };
  }

  /**
   * Calculate relevance score for historical context
   */
  private calculateHistoryRelevanceScore(
    connectingEntities: Entity[],
    currentEntities: Entity[],
    messages: Message[],
    lastMentionTimestamp: number
  ): number {
    let score = 0;

    // Entity overlap score (0.4 weight)
    const overlapRatio = connectingEntities.length / currentEntities.length;
    score += Math.min(overlapRatio, 1) * 0.4;

    // Recency score (0.3 weight)
    const daysSinceLastMention = (Date.now() - lastMentionTimestamp) / (24 * 60 * 60 * 1000);
    const recencyScore = Math.max(0, 1 - daysSinceLastMention / this.config.maxHistoryAgeDays);
    score += recencyScore * 0.3;

    // Message relevance score (0.3 weight)
    const messageScore = Math.min(messages.length / 5, 1); // Up to 5 messages is ideal
    score += messageScore * 0.3;

    return Math.min(score, 1);
  }

  /**
   * Detect conflicts between entity mentions
   */
  private async detectEntityConflicts(
    entity: Entity,
    conversationGroups: Map<string, any[]>
  ): Promise<Array<{ message: Message; extractedClaim: string; confidence: number }>> {
    const conflictMessages: Array<{ message: Message; extractedClaim: string; confidence: number }> = [];

    // Simple conflict detection - look for contradictory patterns
    const conflictPatterns = [
      { pattern: /is\s+(?:a|an)\s+([^.!?]+)/gi, type: 'type_assertion' },
      { pattern: /works\s+(?:at|for)\s+([^.!?]+)/gi, type: 'employment' },
      { pattern: /located\s+(?:in|at)\s+([^.!?]+)/gi, type: 'location' },
      { pattern: /(?:costs?|price[sd]?)\s+(?:is|at)\s*\$?([0-9,]+)/gi, type: 'price' }
    ];

    const claimsByType = new Map<string, Array<{ claim: string; message: Message; confidence: number }>>();

    // Extract claims from all mentions
    for (const [conversationId, mentions] of conversationGroups.entries()) {
      for (const mention of mentions) {
        const message: Message = {
          id: mention.message_id,
          conversationId: mention.conversation_id,
          role: 'user', // Simplified
          content: mention.content || '',
          createdAt: mention.created_at
        };

        for (const { pattern, type } of conflictPatterns) {
          const matches = [...mention.content.matchAll(pattern)];
          for (const match of matches) {
            const claim = match[1]?.trim();
            if (claim && claim.length > 2 && claim.length < 50) {
              if (!claimsByType.has(type)) {
                claimsByType.set(type, []);
              }
              claimsByType.get(type)!.push({
                claim: claim.toLowerCase(),
                message,
                confidence: 0.8
              });
            }
          }
        }
      }
    }

    // Find conflicts within each claim type
    for (const [type, claims] of claimsByType.entries()) {
      if (claims.length < 2) continue;

      // Simple conflict detection - different claims of same type
      const uniqueClaims = new Set(claims.map(c => c.claim));
      if (uniqueClaims.size > 1) {
        // Add all claims as potentially conflicting
        for (const claim of claims) {
          conflictMessages.push({
            message: claim.message,
            extractedClaim: `${entity.name} ${type.replace('_', ' ')}: ${claim.claim}`,
            confidence: claim.confidence
          });
        }
      }
    }

    return conflictMessages;
  }

  /**
   * Calculate conflict severity
   */
  private calculateConflictSeverity(
    conflictMessages: Array<{ message: Message; extractedClaim: string; confidence: number }>
  ): number {
    let severity = 0.3; // Base severity

    // More messages = higher severity
    severity += Math.min(conflictMessages.length * 0.1, 0.3);

    // Higher confidence claims = higher severity
    const avgConfidence = conflictMessages.reduce((sum, cm) => sum + cm.confidence, 0) / conflictMessages.length;
    severity += avgConfidence * 0.2;

    // Recent conflicts are more severe
    const newestMessage = Math.max(...conflictMessages.map(cm => cm.message.createdAt));
    const daysSinceNewest = (Date.now() - newestMessage) / (24 * 60 * 60 * 1000);
    severity += Math.max(0, (30 - daysSinceNewest) / 30) * 0.2;

    return Math.min(severity, 1);
  }

  /**
   * Classify type of conflict
   */
  private classifyConflictType(
    conflictMessages: Array<{ message: Message; extractedClaim: string; confidence: number }>
  ): 'property_contradiction' | 'status_inconsistency' | 'relationship_conflict' | 'temporal_impossibility' {
    // Simple classification based on claim content
    const claims = conflictMessages.map(cm => cm.extractedClaim.toLowerCase());

    if (claims.some(c => c.includes('type') || c.includes('is a'))) {
      return 'property_contradiction';
    }
    if (claims.some(c => c.includes('works') || c.includes('employment'))) {
      return 'relationship_conflict';
    }
    if (claims.some(c => c.includes('location') || c.includes('located'))) {
      return 'property_contradiction';
    }

    return 'status_inconsistency';
  }

  /**
   * Generate resolution suggestion for conflicts
   */
  private generateResolutionSuggestion(
    entity: Entity,
    conflictMessages: Array<{ message: Message; extractedClaim: string; confidence: number }>
  ): string {
    const mostRecent = conflictMessages.reduce((latest, current) => 
      current.message.createdAt > latest.message.createdAt ? current : latest
    );

    return `Consider verifying current information about ${entity.name}. ` +
           `Most recent claim: "${mostRecent.extractedClaim}" ` +
           `(${new Date(mostRecent.message.createdAt).toLocaleDateString()})`;
  }

  /**
   * Identify entities that might become relevant
   */
  private async identifyPotentialEntities(coreEntities: Entity[]): Promise<Entity[]> {
    if (coreEntities.length === 0) return [];

    const coreEntityIds = coreEntities.map(e => e.id);

    // Find entities related to core entities through relationships
    const relatedData = this.executeStatementAll<{
      target_entity_id: string;
      relationship_count: number;
      avg_strength: number;
    }>(
      'find_potential_entities',
      `SELECT 
         er.target_entity_id,
         COUNT(*) as relationship_count,
         AVG(er.strength) as avg_strength
       FROM entity_relationships er
       WHERE er.source_entity_id IN (${coreEntityIds.map(() => '?').join(',')})
         AND er.target_entity_id NOT IN (${coreEntityIds.map(() => '?').join(',')})
       GROUP BY er.target_entity_id
       HAVING avg_strength > 0.4
       ORDER BY relationship_count DESC, avg_strength DESC
       LIMIT 10`,
      [...coreEntityIds, ...coreEntityIds]
    );

    const potentialEntities: Entity[] = [];
    for (const row of relatedData) {
      const entity = await this.entityRepository.getById(row.target_entity_id);
      if (entity) potentialEntities.push(entity);
    }

    return potentialEntities;
  }

  /**
   * Estimate token count for messages
   */
  private estimateTokenCount(messages: Message[]): number {
    // Rough estimation: ~4 characters per token
    const totalChars = messages.reduce((sum, msg) => sum + msg.content.length, 0);
    return Math.ceil(totalChars / 4);
  }

  /**
   * Calculate context relevance score
   */
  private calculateContextRelevance(entities: Entity[], messages: Message[]): number {
    if (entities.length === 0 || messages.length === 0) return 0;

    // Count entity mentions in messages
    let mentionCount = 0;
    const entityNames = entities.map(e => e.name.toLowerCase());

    for (const message of messages) {
      const content = message.content.toLowerCase();
      for (const name of entityNames) {
        if (content.includes(name)) {
          mentionCount++;
        }
      }
    }

    return Math.min(mentionCount / (messages.length * entities.length), 1);
  }

  /**
   * Calculate freshness score based on message ages
   */
  private calculateFreshness(messages: Message[]): number {
    if (messages.length === 0) return 0;

    const now = Date.now();
    const avgAge = messages.reduce((sum, msg) => sum + (now - msg.createdAt), 0) / messages.length;
    const daysSinceAvg = avgAge / (24 * 60 * 60 * 1000);

    // Fresher = higher score (exponential decay)
    return Math.max(0, Math.exp(-daysSinceAvg / 7)); // 7-day half-life
  }

  /**
   * Calculate mention trend for entity
   */
  private calculateMentionTrend(positions: number[], totalMessages: number): 'increasing' | 'decreasing' | 'stable' | 'sporadic' {
    if (positions.length < 3) return 'stable';

    const firstHalf = positions.filter(p => p < totalMessages / 2);
    const secondHalf = positions.filter(p => p >= totalMessages / 2);

    const firstHalfDensity = firstHalf.length / (totalMessages / 2);
    const secondHalfDensity = secondHalf.length / (totalMessages / 2);

    const ratio = secondHalfDensity / (firstHalfDensity || 0.001);

    if (ratio > 1.5) return 'increasing';
    if (ratio < 0.67) return 'decreasing';

    // Check for sporadic pattern (large gaps)
    const gaps = positions.slice(1).map((pos, i) => pos - positions[i]);
    const avgGap = gaps.reduce((a, b) => a + b, 0) / gaps.length;
    const maxGap = Math.max(...gaps);

    if (maxGap > avgGap * 3) return 'sporadic';

    return 'stable';
  }

  /**
   * Classify type of topic shift
   */
  private classifyShiftType(
    prevCount: number,
    currentCount: number,
    intersectionCount: number,
    newEntityCount: number
  ): 'entity_replacement' | 'entity_addition' | 'topic_pivot' | 'context_expansion' {
    const replacementRatio = intersectionCount / prevCount;
    const additionRatio = newEntityCount / currentCount;

    if (replacementRatio < 0.3 && newEntityCount > prevCount * 0.5) {
      return 'topic_pivot';
    }

    if (additionRatio > 0.6 && intersectionCount > 0) {
      return 'context_expansion';
    }

    if (replacementRatio < 0.5) {
      return 'entity_replacement';
    }

    return 'entity_addition';
  }

  /**
   * Determine type of historical relevance
   */
  private determineRelevanceType(
    connectingEntities: Entity[],
    currentEntities: Entity[]
  ): 'entity_overlap' | 'relationship_chain' | 'topic_continuation' | 'problem_resolution' {
    const overlapRatio = connectingEntities.length / currentEntities.length;

    if (overlapRatio > 0.7) return 'entity_overlap';
    if (overlapRatio > 0.3) return 'topic_continuation';

    // Simple heuristic - could be enhanced with relationship analysis
    return 'relationship_chain';
  }

  /**
   * Map database row to Message object
   */
  private mapRowToMessage(row: any): Message {
    return {
      id: row.id,
      conversationId: row.conversation_id,
      role: row.role as 'user' | 'assistant' | 'system',
      content: row.content,
      createdAt: row.created_at,
      parentMessageId: row.parent_message_id || undefined,
      metadata: this.parseMetadata(row.metadata)
    };
  }
}

/**
 * Follow-up Detection System - Advanced commitment language detection and follow-up tracking
 * 
 * This service provides comprehensive tracking of commitments, promises, and action items
 * with intelligent follow-up suggestions and stale action detection.
 * 
 * Features:
 * - Multi-pattern commitment detection (action, temporal, conditional, delegated)
 * - Entity-aware follow-up tracking with relationship context
 * - Configurable staleness detection with urgency awareness
 * - Smart follow-up suggestions based on conversation patterns
 * - Completion indicator detection and resolution tracking
 */

import { BaseRepository } from '../../../storage/repositories/BaseRepository.js';
import { Message } from '../../../types/interfaces.js';
import { Entity, EntityType } from '../../../storage/repositories/EntityRepository.js';
import { EntityRelationship, RelationshipType } from '../../../entities/RelationshipDetector.js';

/**
 * Represents a detected commitment in conversation
 */
export interface DetectedCommitment {
  /** Unique identifier for this commitment */
  id: string;
  /** The message containing the commitment */
  message: Message;
  /** Type of commitment detected */
  commitmentType: CommitmentType;
  /** Extracted commitment text */
  commitmentText: string;
  /** Pattern that matched the commitment */
  matchedPattern: string;
  /** Confidence score (0-1) */
  confidence: number;
  /** Entities mentioned in the commitment */
  entities: Entity[];
  /** Expected resolution timeframe in days */
  expectedTimeframeDays?: number;
  /** Urgency level based on language and context */
  urgencyLevel: UrgencyLevel;
  /** Days since commitment was made */
  daysSinceCommitment: number;
  /** Status of the commitment */
  status: CommitmentStatus;
  /** Related follow-up messages */
  followUps: Message[];
  /** Conversation context */
  conversationContext: {
    conversationId: string;
    conversationTitle?: string;
    participantCount: number;
  };
}

/**
 * Types of commitments that can be detected
 */
export type CommitmentType = 
  | 'action'        // "I'll check", "let me"
  | 'temporal'      // "by Friday", "next week"
  | 'conditional'   // "if X then I'll Y"
  | 'delegated'     // "can you", "please"
  | 'investigation' // "look into", "investigate"
  | 'update'        // "I'll update", "keep you posted"
  | 'follow_up'     // "get back to you", "circle back"

/**
 * Urgency levels for commitments
 */
export type UrgencyLevel = 'low' | 'normal' | 'high' | 'urgent';

/**
 * Status of commitment tracking
 */
export type CommitmentStatus = 'pending' | 'acknowledged' | 'in_progress' | 'completed' | 'overdue' | 'cancelled';

/**
 * Temporal commitment with specific time reference
 */
export interface TemporalCommitment extends DetectedCommitment {
  commitmentType: 'temporal';
  /** Specific deadline extracted from text */
  deadline?: number;
  /** Time reference type */
  timeReference: 'absolute' | 'relative' | 'recurring';
  /** Original time expression */
  timeExpression: string;
}

/**
 * Stale action that needs follow-up
 */
export interface StaleAction {
  /** The original commitment */
  commitment: DetectedCommitment;
  /** Days since it became stale */
  daysSinceStale: number;
  /** Staleness level */
  stalenessLevel: 'mildly_stale' | 'stale' | 'very_stale' | 'abandoned';
  /** Reason why it's considered stale */
  stalenessReason: string;
  /** Suggested follow-up actions */
  suggestedActions: FollowupSuggestion[];
  /** Related entities for context */
  relatedEntities: Entity[];
}

/**
 * Follow-up suggestion generated by the system
 */
export interface FollowupSuggestion {
  /** Unique identifier for the suggestion */
  id: string;
  /** Type of follow-up suggested */
  suggestionType: FollowupType;
  /** Priority level */
  priority: 'low' | 'medium' | 'high' | 'urgent';
  /** Suggested action text */
  suggestionText: string;
  /** Confidence in the suggestion (0-1) */
  confidence: number;
  /** Context for the suggestion */
  context: string;
  /** Related entities */
  relatedEntities: Entity[];
  /** Estimated urgency based on patterns */
  estimatedUrgency: UrgencyLevel;
  /** Whether this suggestion is time-sensitive */
  isTimeSensitive: boolean;
}

/**
 * Types of follow-up suggestions
 */
export type FollowupType = 
  | 'status_check'     // Check on progress
  | 'deadline_reminder' // Remind about deadline
  | 'context_update'   // Request context update
  | 'escalation'       // Escalate to relevant party
  | 'clarification'    // Ask for clarification
  | 'completion_verify' // Verify if completed
  | 'timeline_adjust'  // Suggest timeline adjustment

/**
 * Configuration for staleness detection
 */
export interface StalenessConfig {
  /** Default staleness threshold in days */
  defaultStaleDays: number;
  /** Urgency-based thresholds */
  urgencyThresholds: {
    low: number;      // Days before low urgency items are stale
    normal: number;   // Days before normal items are stale
    high: number;     // Days before high urgency items are stale
    urgent: number;   // Days before urgent items are stale
  };
  /** Entity-specific staleness rules */
  entityBasedRules: {
    [entityType in EntityType]?: number;
  };
  /** Commitment type specific rules */
  commitmentTypeRules: {
    [type in CommitmentType]?: number;
  };
}

/**
 * Follow-up Detection Service
 */
export class FollowupDetector extends BaseRepository {

  /**
   * Comprehensive commitment detection patterns
   */
  private static readonly COMMITMENT_PATTERNS = {
    // Action commitments - immediate or near-term actions
    action: [
      /I'll\s+(?:check|verify|look|see|try|do|make|send|call|contact|review|examine)/i,
      /(?:let me|I need to|I should)\s+(?:check|verify|look|see|try|do|make|send|call|contact|review|examine)/i,
      /I'm going to\s+(?:check|verify|look|see|try|do|make|send|call|contact|review|examine)/i,
      /(?:I plan to|I intend to)\s+(?:check|verify|look|see|try|do|make|send|call|contact|review|examine)/i,
    ],

    // Temporal commitments - time-bound promises
    temporal: [
      /(?:by|before|after|on)\s+(?:tomorrow|today|tonight|this\s+(?:morning|afternoon|evening|week|month))/i,
      /(?:by|before|after|on)\s+(?:monday|tuesday|wednesday|thursday|friday|saturday|sunday)/i,
      /(?:by|before|after|on)\s+(?:next\s+(?:week|month|friday|monday|tuesday|wednesday|thursday))/i,
      /(?:by|before|after|on)\s+(?:end\s+of\s+(?:day|week|month|quarter|year))/i,
      /(?:in|within)\s+(?:an?\s+)?(?:hour|day|week|month|few\s+(?:hours|days|weeks))/i,
      /(?:by|before)\s+\d{1,2}(?::\d{2})?\s*(?:am|pm)?/i,
    ],

    // Conditional commitments - if-then promises
    conditional: [
      /if\s+.*?\s+(?:then\s+)?(?:I'll|I will|let me|I can|I should)/i,
      /(?:assuming|provided|given)\s+.*?(?:I'll|I will|let me|I can)/i,
      /(?:once|when|after)\s+.*?(?:I'll|I will|let me|I can)/i,
    ],

    // Delegated commitments - requests to others
    delegated: [
      /(?:can|could|would)\s+you\s+(?:please\s+)?(?:check|verify|look|see|try|do|make|send|call|contact)/i,
      /please\s+(?:check|verify|look|see|try|do|make|send|call|contact|review|examine)/i,
      /(?:would\s+you\s+mind|could\s+you\s+please)\s+(?:checking|verifying|looking|seeing|trying)/i,
      /I'd appreciate if you could\s+(?:check|verify|look|see|try|do|make|send|call|contact)/i,
    ],

    // Investigation commitments - research and analysis
    investigation: [
      /(?:I'll|let me|I need to)\s+(?:investigate|research|explore|analyze|study|examine)/i,
      /(?:I'll|let me|I need to)\s+(?:look into|dig into|check out|find out)/i,
      /(?:I'll|let me|I need to)\s+(?:get to the bottom of|figure out|determine)/i,
    ],

    // Update commitments - status and progress reports
    update: [
      /(?:I'll|I will)\s+(?:update|inform|notify|let\s+you\s+know|keep\s+you\s+posted)/i,
      /(?:I'll|I will)\s+(?:send\s+an?\s+update|provide\s+an?\s+update|give\s+you\s+an?\s+update)/i,
      /(?:I'll|I will)\s+(?:report\s+back|circle\s+back|touch\s+base)/i,
    ],

    // Follow-up commitments - future engagement
    follow_up: [
      /(?:I'll|I will)\s+(?:follow\s+up|get\s+back\s+to\s+you|circle\s+back)/i,
      /(?:I'll|I will)\s+(?:reach\s+out|be\s+in\s+touch|contact\s+you)/i,
      /(?:let's|we\s+should)\s+(?:follow\s+up|touch\s+base|reconnect)/i,
    ]
  };

  /**
   * Patterns for detecting completion indicators
   */
  private static readonly COMPLETION_PATTERNS = [
    /(?:completed|finished|done|resolved|fixed|solved|implemented)/i,
    /(?:I've|I have)\s+(?:checked|verified|looked|reviewed|examined|contacted|sent)/i,
    /(?:it's|this\s+is)\s+(?:complete|finished|done|resolved|ready)/i,
    /(?:problem\s+(?:solved|resolved|fixed)|issue\s+(?:resolved|fixed|closed))/i,
    /(?:task\s+(?:complete|finished|done)|work\s+(?:complete|finished|done))/i,
  ];

  /**
   * Patterns for detecting acknowledgment
   */
  private static readonly ACKNOWLEDGMENT_PATTERNS = [
    /(?:got\s+it|understood|acknowledged|noted|thanks|okay|ok|sure)/i,
    /(?:I'll\s+(?:work\s+on|start|begin)|working\s+on|started)/i,
    /(?:in\s+progress|ongoing|currently\s+(?:working|checking|looking))/i,
  ];

  /**
   * Default staleness configuration
   */
  private static readonly DEFAULT_STALENESS_CONFIG: StalenessConfig = {
    defaultStaleDays: 7,
    urgencyThresholds: {
      low: 14,
      normal: 7,
      high: 3,
      urgent: 1
    },
    entityBasedRules: {
      person: 5,
      organization: 10,
      product: 7,
      technical: 3,
      event: 1,
      decision: 2
    },
    commitmentTypeRules: {
      action: 3,
      temporal: 0, // Based on deadline
      conditional: 7,
      delegated: 5,
      investigation: 10,
      update: 7,
      follow_up: 7
    }
  };

  /**
   * Detect commitment language in messages
   */
  async detectCommitmentLanguage(
    messages: Message[],
    options: {
      minConfidence?: number;
      includeEntities?: boolean;
      conversationContext?: boolean;
    } = {}
  ): Promise<DetectedCommitment[]> {
    const {
      minConfidence = 0.6,
      includeEntities = true,
      conversationContext = true
    } = options;

    const commitments: DetectedCommitment[] = [];

    for (const message of messages) {
      const detectedCommitments = await this.analyzeMessageForCommitments(
        message,
        { minConfidence, includeEntities, conversationContext }
      );
      commitments.push(...detectedCommitments);
    }

    return commitments
      .filter(commitment => commitment.confidence >= minConfidence)
      .sort((a, b) => b.confidence - a.confidence);
  }

  /**
   * Track temporal promises with specific time references
   */
  async trackTemporalPromises(
    conversationId?: string,
    options: {
      includePast?: boolean;
      includeCompleted?: boolean;
      maxDaysAhead?: number;
    } = {}
  ): Promise<TemporalCommitment[]> {
    const {
      includePast = false,
      includeCompleted = false,
      maxDaysAhead = 30
    } = options;

    // Find messages with temporal commitment patterns
    let whereClause = 'WHERE m.role = ?';
    const params: any[] = ['assistant'];

    if (conversationId) {
      whereClause += ' AND m.conversation_id = ?';
      params.push(conversationId);
    }

    if (!includePast) {
      whereClause += ' AND m.created_at >= ?';
      params.push(Date.now() - (7 * 24 * 60 * 60 * 1000)); // Last 7 days
    }

    const messages = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
    }>(
      'find_temporal_messages',
      `SELECT m.* FROM messages m ${whereClause} ORDER BY m.created_at DESC`,
      params
    );

    const temporalCommitments: TemporalCommitment[] = [];

    for (const row of messages) {
      const message: Message = this.mapRowToMessage(row);
      const commitments = await this.analyzeMessageForCommitments(message, { 
        minConfidence: 0.5, 
        includeEntities: false, 
        conversationContext: false 
      });

      for (const commitment of commitments) {
        if (commitment.commitmentType === 'temporal') {
          const temporal = commitment as TemporalCommitment;

          if (!includeCompleted && temporal.status === 'completed') {
            continue;
          }

          // Parse time references
          const timeInfo = this.extractTemporalInfo(temporal.commitmentText);
          if (timeInfo) {
            temporal.deadline = timeInfo.deadline;
            temporal.timeReference = timeInfo.timeReference;
            temporal.timeExpression = timeInfo.timeExpression;

            // Filter by time window
            if (temporal.deadline && temporal.deadline > Date.now() + (maxDaysAhead * 24 * 60 * 60 * 1000)) {
              continue;
            }

            temporalCommitments.push(temporal);
          }
        }
      }
    }

    return temporalCommitments.sort((a, b) => (a.deadline || 0) - (b.deadline || 0));
  }

  /**
   * Identify stale actions that need follow-up
   */
  async identifyStaleActions(
    options: {
      conversationId?: string;
      stalenessConfig?: Partial<StalenessConfig>;
      includeEntityContext?: boolean;
    } = {}
  ): Promise<StaleAction[]> {
    const config = { ...FollowupDetector.DEFAULT_STALENESS_CONFIG, ...options.stalenessConfig };
    const staleActions: StaleAction[] = [];

    // Get all pending and in-progress commitments
    const allCommitments = await this.getAllCommitments(options.conversationId);
    const activeCommitments = allCommitments.filter(
      c => ['pending', 'acknowledged', 'in_progress'].includes(c.status)
    );

    for (const commitment of activeCommitments) {
      const stalenessThreshold = this.calculateStalenessThreshold(commitment, config);
      const daysSinceCommitment = Math.floor(
        (Date.now() - commitment.message.createdAt) / (24 * 60 * 60 * 1000)
      );

      if (daysSinceCommitment >= stalenessThreshold) {
        const staleness = this.calculateStalenessLevel(daysSinceCommitment, stalenessThreshold);
        const relatedEntities = options.includeEntityContext 
          ? await this.getRelatedEntities(commitment)
          : commitment.entities;

        const suggestedActions = await this.generateFollowupSuggestions(
          commitment,
          relatedEntities,
          staleness
        );

        staleActions.push({
          commitment,
          daysSinceStale: daysSinceCommitment - stalenessThreshold,
          stalenessLevel: staleness,
          stalenessReason: this.generateStalenessReason(commitment, daysSinceCommitment, stalenessThreshold),
          suggestedActions,
          relatedEntities
        });
      }
    }

    return staleActions.sort((a, b) => 
      this.getStalenessScore(b.stalenessLevel) - this.getStalenessScore(a.stalenessLevel)
    );
  }

  /**
   * Generate follow-up suggestions based on context
   */
  async suggestFollowups(
    commitment: DetectedCommitment,
    context: {
      relatedEntities?: Entity[];
      conversationHistory?: Message[];
      entityRelationships?: EntityRelationship[];
    } = {}
  ): Promise<FollowupSuggestion[]> {
    const suggestions: FollowupSuggestion[] = [];
    const daysSince = Math.floor((Date.now() - commitment.message.createdAt) / (24 * 60 * 60 * 1000));

    // Status check suggestions
    if (daysSince >= 3 && commitment.status === 'pending') {
      suggestions.push({
        id: this.generateId(),
        suggestionType: 'status_check',
        priority: daysSince > 7 ? 'high' : 'medium',
        suggestionText: `Check status of: "${commitment.commitmentText.substring(0, 50)}..."`,
        confidence: 0.8,
        context: `No updates received for ${daysSince} days`,
        relatedEntities: context.relatedEntities || commitment.entities,
        estimatedUrgency: commitment.urgencyLevel,
        isTimeSensitive: commitment.commitmentType === 'temporal'
      });
    }

    // Deadline reminder suggestions
    if (commitment.commitmentType === 'temporal' && commitment.expectedTimeframeDays) {
      const daysUntilDeadline = commitment.expectedTimeframeDays - daysSince;
      if (daysUntilDeadline <= 1 && daysUntilDeadline >= 0) {
        suggestions.push({
          id: this.generateId(),
          suggestionType: 'deadline_reminder',
          priority: 'urgent',
          suggestionText: `Deadline approaching: "${commitment.commitmentText.substring(0, 50)}..."`,
          confidence: 0.9,
          context: `Deadline in ${daysUntilDeadline} day(s)`,
          relatedEntities: context.relatedEntities || commitment.entities,
          estimatedUrgency: 'urgent',
          isTimeSensitive: true
        });
      }
    }

    // Entity-based suggestions using relationships
    if (context.entityRelationships && context.relatedEntities) {
      const entitySuggestions = await this.generateEntityBasedSuggestions(
        commitment,
        context.entityRelationships,
        context.relatedEntities
      );
      suggestions.push(...entitySuggestions);
    }

    // Context update suggestions for investigation commitments
    if (commitment.commitmentType === 'investigation' && daysSince >= 5) {
      suggestions.push({
        id: this.generateId(),
        suggestionType: 'context_update',
        priority: 'medium',
        suggestionText: `Request update on investigation: "${commitment.commitmentText.substring(0, 50)}..."`,
        confidence: 0.7,
        context: `Investigation ongoing for ${daysSince} days`,
        relatedEntities: context.relatedEntities || commitment.entities,
        estimatedUrgency: commitment.urgencyLevel,
        isTimeSensitive: false
      });
    }

    // Priority-based sorting
    return suggestions.sort((a, b) => {
      const priorityOrder = { urgent: 4, high: 3, medium: 2, low: 1 };
      return priorityOrder[b.priority] - priorityOrder[a.priority];
    });
  }

  /**
   * Analyze a single message for commitments
   */
  private async analyzeMessageForCommitments(
    message: Message,
    options: {
      minConfidence: number;
      includeEntities: boolean;
      conversationContext: boolean;
    }
  ): Promise<DetectedCommitment[]> {
    const commitments: DetectedCommitment[] = [];

    // Check each commitment type
    for (const [type, patterns] of Object.entries(FollowupDetector.COMMITMENT_PATTERNS)) {
      for (const pattern of patterns) {
        const match = message.content.match(pattern);
        if (match) {
          const commitment = await this.createCommitmentFromMatch(
            message,
            type as CommitmentType,
            pattern,
            match,
            options
          );
          if (commitment && commitment.confidence >= options.minConfidence) {
            commitments.push(commitment);
          }
        }
      }
    }

    return commitments;
  }

  /**
   * Create a commitment object from a pattern match
   */
  private async createCommitmentFromMatch(
    message: Message,
    type: CommitmentType,
    pattern: RegExp,
    match: RegExpMatchArray,
    options: {
      includeEntities: boolean;
      conversationContext: boolean;
    }
  ): Promise<DetectedCommitment | null> {
    const sentences = message.content.split(/[.!?]+/);
    const commitmentSentence = sentences.find(s => pattern.test(s))?.trim();

    if (!commitmentSentence) return null;

    const confidence = this.calculateCommitmentConfidence(commitmentSentence, pattern, type);
    const urgency = this.determineUrgencyLevel(commitmentSentence);
    const entities = options.includeEntities ? await this.extractEntitiesFromText(commitmentSentence) : [];

    let conversationContext = {
      conversationId: message.conversationId,
      participantCount: 1
    };

    if (options.conversationContext) {
      conversationContext = await this.getConversationContext(message.conversationId);
    }

    const daysSince = Math.floor((Date.now() - message.createdAt) / (24 * 60 * 60 * 1000));
    const followUps = await this.findFollowUpMessages(message.conversationId, message.createdAt, commitmentSentence);
    const status = this.determineCommitmentStatus(followUps, daysSince, urgency);

    const commitment: DetectedCommitment = {
      id: this.generateId(),
      message,
      commitmentType: type,
      commitmentText: commitmentSentence,
      matchedPattern: pattern.source,
      confidence,
      entities,
      expectedTimeframeDays: this.extractTimeframe(commitmentSentence),
      urgencyLevel: urgency,
      daysSinceCommitment: daysSince,
      status,
      followUps,
      conversationContext
    };

    return commitment;
  }

  /**
   * Calculate confidence score for a commitment
   */
  private calculateCommitmentConfidence(
    text: string,
    pattern: RegExp,
    type: CommitmentType
  ): number {
    let confidence = 0.6; // Base confidence

    // Type-specific adjustments
    const typeBoosts = {
      action: 0.2,
      temporal: 0.3,
      conditional: 0.1,
      delegated: 0.15,
      investigation: 0.2,
      update: 0.15,
      follow_up: 0.1
    };
    confidence += typeBoosts[type];

    // Pattern specificity boosts
    if (pattern.source.includes("I'll")) confidence += 0.15;
    if (pattern.source.includes("by|before|after")) confidence += 0.2;
    if (text.toLowerCase().includes("urgent") || text.toLowerCase().includes("asap")) confidence += 0.1;

    // Text quality indicators
    if (text.length > 20 && text.length < 150) confidence += 0.05;
    if (/\b(?:check|verify|confirm|update|review)\b/i.test(text)) confidence += 0.05;

    return Math.min(confidence, 1.0);
  }

  /**
   * Determine urgency level from text
   */
  private determineUrgencyLevel(text: string): UrgencyLevel {
    const lowerText = text.toLowerCase();

    if (/\b(?:urgent|asap|immediately|emergency|critical)\b/.test(lowerText)) {
      return 'urgent';
    }
    if (/\b(?:high priority|important|soon|today|tomorrow)\b/.test(lowerText)) {
      return 'high';
    }
    if (/\b(?:when you can|eventually|sometime|later)\b/.test(lowerText)) {
      return 'low';
    }

    return 'normal';
  }

  /**
   * Extract entities from commitment text (simplified implementation)
   */
  private async extractEntitiesFromText(text: string): Promise<Entity[]> {
    // This would typically integrate with an NLP service or entity recognition
    // For now, return empty array - would be implemented with proper entity extraction
    return [];
  }

  /**
   * Get conversation context information
   */
  private async getConversationContext(conversationId: string): Promise<{
    conversationId: string;
    conversationTitle?: string;
    participantCount: number;
  }> {
    const conversation = this.executeStatement<{
      id: string;
      title: string | null;
    }>(
      'get_conversation_context',
      'SELECT id, title FROM conversations WHERE id = ?',
      [conversationId]
    );

    // Count unique participants (roles)
    const { count } = this.executeStatement<{ count: number }>(
      'count_participants',
      'SELECT COUNT(DISTINCT role) as count FROM messages WHERE conversation_id = ?',
      [conversationId]
    );

    return {
      conversationId,
      conversationTitle: conversation?.title || undefined,
      participantCount: count || 1
    };
  }

  /**
   * Find follow-up messages related to a commitment
   */
  private async findFollowUpMessages(
    conversationId: string,
    afterTimestamp: number,
    commitmentText: string
  ): Promise<Message[]> {
    const keywords = this.extractKeywords(commitmentText);
    if (keywords.length === 0) return [];

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
    }>(
      'find_followup_messages',
      `SELECT m.*
       FROM messages_fts
       JOIN messages m ON m.rowid = messages_fts.rowid
       WHERE m.conversation_id = ? 
         AND m.created_at > ?
         AND messages_fts MATCH ?
       ORDER BY m.created_at ASC`,
      [conversationId, afterTimestamp, keywords.join(' OR ')]
    );

    return rows.map(row => this.mapRowToMessage(row));
  }

  /**
   * Determine commitment status based on follow-ups
   */
  private determineCommitmentStatus(
    followUps: Message[],
    daysSince: number,
    urgency: UrgencyLevel
  ): CommitmentStatus {
    if (followUps.length === 0) {
      const urgencyThresholds = { urgent: 1, high: 3, normal: 7, low: 14 };
      return daysSince > urgencyThresholds[urgency] ? 'overdue' : 'pending';
    }

    // Check for completion indicators
    const hasCompletion = followUps.some(msg => 
      FollowupDetector.COMPLETION_PATTERNS.some(pattern => pattern.test(msg.content))
    );
    if (hasCompletion) return 'completed';

    // Check for acknowledgment or progress
    const hasAcknowledgment = followUps.some(msg =>
      FollowupDetector.ACKNOWLEDGMENT_PATTERNS.some(pattern => pattern.test(msg.content))
    );
    if (hasAcknowledgment) return 'in_progress';

    return 'acknowledged';
  }

  /**
   * Extract timeframe from commitment text
   */
  private extractTimeframe(text: string): number | undefined {
    const lowerText = text.toLowerCase();

    if (lowerText.includes('today') || lowerText.includes('immediately')) return 0;
    if (lowerText.includes('tomorrow')) return 1;
    if (lowerText.includes('this week') || lowerText.includes('by friday')) return 7;
    if (lowerText.includes('next week')) return 14;
    if (lowerText.includes('this month') || lowerText.includes('end of month')) return 30;

    // Extract numeric timeframes
    const match = lowerText.match(/(?:in|within)\s+(\d+)\s+(hour|day|week|month)s?/);
    if (match) {
      const value = parseInt(match[1]);
      const unit = match[2];
      const multipliers = { hour: 1/24, day: 1, week: 7, month: 30 };
      return Math.ceil(value * multipliers[unit as keyof typeof multipliers]);
    }

    return undefined;
  }

  /**
   * Extract temporal information from commitment text
   */
  private extractTemporalInfo(text: string): {
    deadline?: number;
    timeReference: 'absolute' | 'relative' | 'recurring';
    timeExpression: string;
  } | null {
    // This is a simplified implementation
    // In practice, this would use more sophisticated NLP for date/time parsing
    const timeframe = this.extractTimeframe(text);
    if (!timeframe) return null;

    const deadline = Date.now() + (timeframe * 24 * 60 * 60 * 1000);

    return {
      deadline,
      timeReference: 'relative', // Could be enhanced to detect absolute dates
      timeExpression: text.match(/(?:by|before|after|on|in|within).+?(?:\.|$|,)/i)?.[0] || ''
    };
  }

  /**
   * Get all commitments for analysis
   */
  private async getAllCommitments(conversationId?: string): Promise<DetectedCommitment[]> {
    let whereClause = 'WHERE m.role = ?';
    const params: any[] = ['assistant'];

    if (conversationId) {
      whereClause += ' AND m.conversation_id = ?';
      params.push(conversationId);
    }

    const messages = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
    }>(
      'get_all_messages_for_commitments',
      `SELECT m.* FROM messages m ${whereClause} ORDER BY m.created_at DESC`,
      params
    );

    const commitments: DetectedCommitment[] = [];
    for (const row of messages) {
      const message = this.mapRowToMessage(row);
      const messageCommitments = await this.analyzeMessageForCommitments(message, {
        minConfidence: 0.5,
        includeEntities: true,
        conversationContext: false
      });
      commitments.push(...messageCommitments);
    }

    return commitments;
  }

  /**
   * Calculate staleness threshold for a commitment
   */
  private calculateStalenessThreshold(
    commitment: DetectedCommitment,
    config: StalenessConfig
  ): number {
    // Start with urgency-based threshold
    let threshold = config.urgencyThresholds[commitment.urgencyLevel];

    // Apply commitment type rules
    const typeRule = config.commitmentTypeRules[commitment.commitmentType];
    if (typeRule !== undefined) {
      threshold = Math.min(threshold, typeRule);
    }

    // Apply entity-based rules
    for (const entity of commitment.entities) {
      const entityRule = config.entityBasedRules[entity.type];
      if (entityRule !== undefined) {
        threshold = Math.min(threshold, entityRule);
      }
    }

    return Math.max(threshold, 1); // Minimum 1 day
  }

  /**
   * Calculate staleness level
   */
  private calculateStalenessLevel(
    daysSinceCommitment: number,
    threshold: number
  ): StaleAction['stalenessLevel'] {
    const ratio = daysSinceCommitment / threshold;

    if (ratio >= 3) return 'abandoned';
    if (ratio >= 2) return 'very_stale';
    if (ratio >= 1.5) return 'stale';
    return 'mildly_stale';
  }

  /**
   * Generate staleness reason
   */
  private generateStalenessReason(
    commitment: DetectedCommitment,
    daysSinceCommitment: number,
    threshold: number
  ): string {
    return `Commitment made ${daysSinceCommitment} days ago (threshold: ${threshold} days). ` +
           `Type: ${commitment.commitmentType}, Urgency: ${commitment.urgencyLevel}`;
  }

  /**
   * Get staleness score for sorting
   */
  private getStalenessScore(level: StaleAction['stalenessLevel']): number {
    const scores = {
      'abandoned': 4,
      'very_stale': 3,
      'stale': 2,
      'mildly_stale': 1
    };
    return scores[level];
  }

  /**
   * Get related entities using relationships
   */
  private async getRelatedEntities(commitment: DetectedCommitment): Promise<Entity[]> {
    // This would query entity relationships to find connected entities
    // Implementation would depend on entity relationship system
    return commitment.entities;
  }

  /**
   * Generate follow-up suggestions based on commitment
   */
  private async generateFollowupSuggestions(
    commitment: DetectedCommitment,
    relatedEntities: Entity[],
    staleness: StaleAction['stalenessLevel']
  ): Promise<FollowupSuggestion[]> {
    // Use the main suggestFollowups method
    return this.suggestFollowups(commitment, { relatedEntities });
  }

  /**
   * Generate entity-based suggestions using relationships
   */
  private async generateEntityBasedSuggestions(
    commitment: DetectedCommitment,
    relationships: EntityRelationship[],
    entities: Entity[]
  ): Promise<FollowupSuggestion[]> {
    const suggestions: FollowupSuggestion[] = [];

    // Find relevant relationships for escalation suggestions
    for (const entity of entities) {
      const entityRelationships = relationships.filter(
        r => r.sourceEntityId === entity.id || r.targetEntityId === entity.id
      );

      for (const rel of entityRelationships) {
        if (rel.relationshipType === 'works_for' || rel.relationshipType === 'part_of') {
          suggestions.push({
            id: this.generateId(),
            suggestionType: 'escalation',
            priority: 'medium',
            suggestionText: `Consider escalating to related ${rel.relationshipType} entity`,
            confidence: 0.6,
            context: `Entity relationship: ${rel.relationshipType}`,
            relatedEntities: [entity],
            estimatedUrgency: commitment.urgencyLevel,
            isTimeSensitive: commitment.commitmentType === 'temporal'
          });
        }
      }
    }

    return suggestions;
  }

  /**
   * Extract keywords from text for searching
   */
  private extractKeywords(text: string): string[] {
    const words = text.toLowerCase().split(/\s+/);
    const stopWords = new Set(['i', 'will', 'ill', 'let', 'me', 'need', 'to', 'the', 'a', 'an', 'and', 'or', 'but']);

    return words
      .filter(word => word.length > 3 && !stopWords.has(word))
      .slice(0, 5);
  }

  /**
   * Map database row to Message object
   */
  private mapRowToMessage(row: any): Message {
    return {
      id: row.id,
      conversationId: row.conversation_id,
      role: row.role as 'user' | 'assistant' | 'system',
      content: row.content,
      createdAt: row.created_at,
      parentMessageId: row.parent_message_id || undefined,
      metadata: this.parseMetadata(row.metadata)
    };
  }
}

/**
 * Pattern Detection Service - Temporal pattern detection and statistical analysis
 * 
 * This service identifies recurring patterns in conversation data including:
 * - Unresolved action items and commitments
 * - Recurring questions without satisfactory answers
 * - Knowledge gaps and missing information
 * - Commitment tracking with temporal analysis
 */

import { BaseRepository } from '../../../storage/repositories/BaseRepository.js';
import { Message } from '../../../types/interfaces.js';

/**
 * Represents an unresolved action item
 */
export interface UnresolvedAction {
  /** Unique identifier for this action */
  id: string;
  /** The original commitment message */
  commitmentMessage: Message;
  /** The extracted commitment text */
  commitmentText: string;
  /** Pattern that matched the commitment */
  matchedPattern: string;
  /** Confidence score (0-1) */
  confidence: number;
  /** Days since the commitment was made */
  daysSinceCommitment: number;
  /** Whether there have been any follow-up mentions */
  hasFollowUp: boolean;
  /** Conversation ID where commitment was made */
  conversationId: string;
  /** Conversation title if available */
  conversationTitle?: string;
}

/**
 * Represents a recurring question pattern
 */
export interface RecurringQuestion {
  /** Unique identifier for this question pattern */
  id: string;
  /** The normalized question text */
  questionText: string;
  /** Number of times this question has been asked */
  frequency: number;
  /** Messages containing this question */
  instances: Message[];
  /** First time this question was asked */
  firstAskedAt: number;
  /** Most recent time this question was asked */
  lastAskedAt: number;
  /** Days between first and last occurrence */
  daysBetweenOccurrences: number;
  /** Average confidence score across instances */
  averageConfidence: number;
  /** Conversations where this question appeared */
  conversationIds: string[];
}

/**
 * Represents a knowledge gap
 */
export interface KnowledgeGap {
  /** Unique identifier for this knowledge gap */
  id: string;
  /** The topic or subject area */
  topic: string;
  /** Number of questions about this topic */
  questionCount: number;
  /** Number of answers provided */
  answerCount: number;
  /** Gap ratio (questions / answers) */
  gapRatio: number;
  /** Related messages for context */
  relatedMessages: Message[];
  /** First occurrence of questions on this topic */
  firstQuestionAt: number;
  /** Most recent question on this topic */
  lastQuestionAt: number;
  /** Confidence score for topic identification */
  topicConfidence: number;
}

/**
 * Represents a tracked commitment
 */
export interface TrackedCommitment {
  /** Unique identifier for this commitment */
  id: string;
  /** The commitment message */
  message: Message;
  /** Type of commitment */
  commitmentType: 'check' | 'follow_up' | 'update' | 'investigate' | 'temporal';
  /** Extracted commitment text */
  commitmentText: string;
  /** Confidence score (0-1) */
  confidence: number;
  /** Status of the commitment */
  status: 'pending' | 'mentioned' | 'resolved' | 'overdue';
  /** Expected resolution timeframe in days */
  expectedTimeframeDays?: number;
  /** Days since commitment was made */
  daysSinceCommitment: number;
  /** Follow-up messages if any */
  followUps: Message[];
}

/**
 * Pattern detection service for analyzing conversation patterns
 */
export class PatternDetectionService extends BaseRepository {

  /**
   * Commitment detection patterns
   */
  private static readonly COMMITMENT_PATTERNS = [
    /I'll\s+(?:check|look into|follow up|get back|update)/i,
    /let me\s+(?:check|look into|find out|investigate)/i,
    /I need to\s+(?:check|verify|confirm|update)/i,
    /(?:by|before|after)\s+(?:tomorrow|friday|next week|end of)/i,
  ];

  /**
   * Question patterns for identifying recurring questions
   */
  private static readonly QUESTION_PATTERNS = [
    /^(?:what|how|when|where|why|who|which|can|could|would|should|is|are|do|does|did)\s+/i,
    /\?$/,
    /^(?:explain|describe|tell me|show me)/i,
  ];

  /**
   * Answer indicators
   */
  private static readonly ANSWER_INDICATORS = [
    /^(?:the answer is|here's|this is|you can|to do this)/i,
    /^(?:according to|based on|in my experience)/i,
    /^(?:yes,|no,|sure,|definitely|certainly)/i,
  ];

  /**
   * Detect unresolved action items and commitments without follow-up
   */
  async detectUnresolvedActions(
    options: {
      conversationId?: string;
      daysSince?: number;
      minConfidence?: number;
      limit?: number;
    } = {}
  ): Promise<UnresolvedAction[]> {
    const {
      conversationId,
      daysSince = 7,
      minConfidence = 0.6,
      limit = 50
    } = options;

    const cutoffTimestamp = Date.now() - (daysSince * 24 * 60 * 60 * 1000);

    // Build query to find messages with commitment patterns
    let whereClause = 'WHERE m.created_at < ? AND m.role = ?';
    const params: any[] = [cutoffTimestamp, 'assistant'];

    if (conversationId) {
      whereClause += ' AND m.conversation_id = ?';
      params.push(conversationId);
    }

    params.push(limit);

    const messages = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
      conversation_title: string | null;
    }>(
      'find_potential_commitments',
      `SELECT m.*, c.title as conversation_title
       FROM messages m
       JOIN conversations c ON c.id = m.conversation_id
       ${whereClause}
       ORDER BY m.created_at DESC
       LIMIT ?`,
      params
    );

    const unresolvedActions: UnresolvedAction[] = [];

    for (const row of messages) {
      const message: Message = {
        id: row.id,
        conversationId: row.conversation_id,
        role: row.role as 'user' | 'assistant' | 'system',
        content: row.content,
        createdAt: row.created_at,
        parentMessageId: row.parent_message_id || undefined,
        metadata: this.parseMetadata(row.metadata),
        embedding: row.embedding ? Array.from(new Float32Array(row.embedding.buffer)) : undefined
      };

      // Check if message contains commitment patterns
      const commitmentMatch = this.findCommitmentPattern(message.content);
      if (!commitmentMatch || commitmentMatch.confidence < minConfidence) {
        continue;
      }

      // Check for follow-up messages
      const hasFollowUp = await this.hasFollowUpMention(
        message.conversationId,
        message.createdAt,
        commitmentMatch.commitmentText
      );

      const daysSinceCommitment = Math.floor(
        (Date.now() - message.createdAt) / (24 * 60 * 60 * 1000)
      );

      unresolvedActions.push({
        id: this.generateId(),
        commitmentMessage: message,
        commitmentText: commitmentMatch.commitmentText,
        matchedPattern: commitmentMatch.pattern,
        confidence: commitmentMatch.confidence,
        daysSinceCommitment,
        hasFollowUp,
        conversationId: message.conversationId,
        conversationTitle: row.conversation_title || undefined
      });
    }

    return unresolvedActions.filter(action => !action.hasFollowUp);
  }

  /**
   * Find recurring questions across conversations
   */
  async findRecurringQuestions(
    options: {
      conversationId?: string;
      minFrequency?: number;
      minDaysBetween?: number;
      limit?: number;
    } = {}
  ): Promise<RecurringQuestion[]> {
    const {
      conversationId,
      minFrequency = 2,
      minDaysBetween = 1,
      limit = 20
    } = options;

    let whereClause = 'WHERE m.role = ?';
    const params: any[] = ['user'];

    if (conversationId) {
      whereClause += ' AND m.conversation_id = ?';
      params.push(conversationId);
    }

    const messages = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      'find_question_messages',
      `SELECT m.*
       FROM messages m
       ${whereClause}
       ORDER BY m.created_at DESC`,
      params
    );

    // Group similar questions
    const questionGroups = new Map<string, Message[]>();

    for (const row of messages) {
      const message: Message = {
        id: row.id,
        conversationId: row.conversation_id,
        role: row.role as 'user' | 'assistant' | 'system',
        content: row.content,
        createdAt: row.created_at,
        parentMessageId: row.parent_message_id || undefined,
        metadata: this.parseMetadata(row.metadata)
      };

      if (this.isQuestion(message.content)) {
        const normalizedQuestion = this.normalizeQuestion(message.content);

        if (!questionGroups.has(normalizedQuestion)) {
          questionGroups.set(normalizedQuestion, []);
        }
        questionGroups.get(normalizedQuestion)!.push(message);
      }
    }

    const recurringQuestions: RecurringQuestion[] = [];

    for (const [questionText, instances] of questionGroups.entries()) {
      if (instances.length < minFrequency) {
        continue;
      }

      const sortedInstances = instances.sort((a, b) => a.createdAt - b.createdAt);
      const firstAskedAt = sortedInstances[0].createdAt;
      const lastAskedAt = sortedInstances[sortedInstances.length - 1].createdAt;
      const daysBetween = Math.floor((lastAskedAt - firstAskedAt) / (24 * 60 * 60 * 1000));

      if (daysBetween < minDaysBetween) {
        continue;
      }

      const conversationIds = [...new Set(instances.map(msg => msg.conversationId))];

      recurringQuestions.push({
        id: this.generateId(),
        questionText,
        frequency: instances.length,
        instances: sortedInstances,
        firstAskedAt,
        lastAskedAt,
        daysBetweenOccurrences: daysBetween,
        averageConfidence: 0.8, // Static confidence for question detection
        conversationIds
      });
    }

    return recurringQuestions
      .sort((a, b) => b.frequency - a.frequency)
      .slice(0, limit);
  }

  /**
   * Identify knowledge gaps where questions exist but answers are inadequate
   */
  async identifyKnowledgeGaps(
    options: {
      conversationId?: string;
      minGapRatio?: number;
      limit?: number;
    } = {}
  ): Promise<KnowledgeGap[]> {
    const {
      conversationId,
      minGapRatio = 1.5,
      limit = 15
    } = options;

    // Get question and answer patterns
    const recurringQuestions = await this.findRecurringQuestions({ conversationId });

    const knowledgeGaps: KnowledgeGap[] = [];
    const topicMap = new Map<string, { questions: Message[], answers: Message[] }>();

    // Group questions by topic
    for (const question of recurringQuestions) {
      const topic = this.extractTopic(question.questionText);

      if (!topicMap.has(topic)) {
        topicMap.set(topic, { questions: [], answers: [] });
      }

      topicMap.get(topic)!.questions.push(...question.instances);
    }

    // Find answers for each topic
    for (const [topic, data] of topicMap.entries()) {
      const answers = await this.findAnswersForTopic(
        topic,
        data.questions,
        conversationId
      );

      data.answers = answers;

      const gapRatio = data.questions.length / Math.max(answers.length, 1);

      if (gapRatio >= minGapRatio) {
        const allMessages = [...data.questions, ...data.answers]
          .sort((a, b) => a.createdAt - b.createdAt);

        const questionTimestamps = data.questions.map(q => q.createdAt);
        const firstQuestionAt = Math.min(...questionTimestamps);
        const lastQuestionAt = Math.max(...questionTimestamps);

        knowledgeGaps.push({
          id: this.generateId(),
          topic,
          questionCount: data.questions.length,
          answerCount: answers.length,
          gapRatio,
          relatedMessages: allMessages,
          firstQuestionAt,
          lastQuestionAt,
          topicConfidence: this.calculateTopicConfidence(topic, data.questions)
        });
      }
    }

    return knowledgeGaps
      .sort((a, b) => b.gapRatio - a.gapRatio)
      .slice(0, limit);
  }

  /**
   * Track all commitments with their status and follow-ups
   */
  async trackCommitments(
    options: {
      conversationId?: string;
      includeResolved?: boolean;
      limit?: number;
    } = {}
  ): Promise<TrackedCommitment[]> {
    const {
      conversationId,
      includeResolved = false,
      limit = 30
    } = options;

    // Find all messages with commitment patterns
    let whereClause = 'WHERE m.role = ?';
    const params: any[] = ['assistant'];

    if (conversationId) {
      whereClause += ' AND m.conversation_id = ?';
      params.push(conversationId);
    }

    params.push(limit * 2); // Get more to filter later

    const messages = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      'find_commitment_messages',
      `SELECT m.*
       FROM messages m
       ${whereClause}
       ORDER BY m.created_at DESC
       LIMIT ?`,
      params
    );

    const commitments: TrackedCommitment[] = [];

    for (const row of messages) {
      const message: Message = {
        id: row.id,
        conversationId: row.conversation_id,
        role: row.role as 'user' | 'assistant' | 'system',
        content: row.content,
        createdAt: row.created_at,
        parentMessageId: row.parent_message_id || undefined,
        metadata: this.parseMetadata(row.metadata)
      };

      const commitmentMatch = this.findCommitmentPattern(message.content);
      if (!commitmentMatch) {
        continue;
      }

      const followUps = await this.findFollowUpMessages(
        message.conversationId,
        message.createdAt,
        commitmentMatch.commitmentText
      );

      const daysSinceCommitment = Math.floor(
        (Date.now() - message.createdAt) / (24 * 60 * 60 * 1000)
      );

      const status = this.determineCommitmentStatus(
        commitmentMatch,
        followUps,
        daysSinceCommitment
      );

      if (!includeResolved && status === 'resolved') {
        continue;
      }

      commitments.push({
        id: this.generateId(),
        message,
        commitmentType: this.classifyCommitmentType(commitmentMatch.commitmentText),
        commitmentText: commitmentMatch.commitmentText,
        confidence: commitmentMatch.confidence,
        status,
        expectedTimeframeDays: this.extractTimeframe(commitmentMatch.commitmentText),
        daysSinceCommitment,
        followUps
      });
    }

    return commitments.slice(0, limit);
  }

  /**
   * Find commitment patterns in text
   */
  private findCommitmentPattern(content: string): {
    commitmentText: string;
    pattern: string;
    confidence: number;
  } | null {
    for (const pattern of PatternDetectionService.COMMITMENT_PATTERNS) {
      const match = content.match(pattern);
      if (match) {
        // Extract the sentence containing the commitment
        const sentences = content.split(/[.!?]+/);
        const commitmentSentence = sentences.find(s => pattern.test(s))?.trim();

        if (commitmentSentence) {
          return {
            commitmentText: commitmentSentence,
            pattern: pattern.source,
            confidence: this.calculateCommitmentConfidence(commitmentSentence, pattern)
          };
        }
      }
    }
    return null;
  }

  /**
   * Calculate confidence score for a commitment
   */
  private calculateCommitmentConfidence(text: string, pattern: RegExp): number {
    let confidence = 0.6; // Base confidence

    // Boost confidence for specific patterns
    if (pattern.source.includes("I'll")) confidence += 0.2;
    if (pattern.source.includes("need to")) confidence += 0.1;
    if (text.includes("tomorrow") || text.includes("next week")) confidence += 0.1;
    if (text.length > 20 && text.length < 100) confidence += 0.1; // Reasonable length

    return Math.min(confidence, 1.0);
  }

  /**
   * Check if there's a follow-up mention of a commitment
   */
  private async hasFollowUpMention(
    conversationId: string,
    afterTimestamp: number,
    commitmentText: string
  ): Promise<boolean> {
    const keywords = this.extractKeywords(commitmentText);
    if (keywords.length === 0) return false;

    const followUps = await this.findFollowUpMessages(conversationId, afterTimestamp, commitmentText);
    return followUps.length > 0;
  }

  /**
   * Find follow-up messages related to a commitment
   */
  private async findFollowUpMessages(
    conversationId: string,
    afterTimestamp: number,
    commitmentText: string
  ): Promise<Message[]> {
    const keywords = this.extractKeywords(commitmentText);
    if (keywords.length === 0) return [];

    // Use FTS to search for messages containing keywords after the commitment
    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      'find_followup_messages',
      `SELECT m.*
       FROM messages_fts
       JOIN messages m ON m.rowid = messages_fts.rowid
       WHERE m.conversation_id = ? 
         AND m.created_at > ?
         AND messages_fts MATCH ?
       ORDER BY m.created_at ASC`,
      [
        conversationId,
        afterTimestamp,
        keywords.join(' OR ')
      ]
    );

    return rows.map(row => ({
      id: row.id,
      conversationId: row.conversation_id,
      role: row.role as 'user' | 'assistant' | 'system',
      content: row.content,
      createdAt: row.created_at,
      parentMessageId: row.parent_message_id || undefined,
      metadata: this.parseMetadata(row.metadata)
    }));
  }

  /**
   * Check if a message is a question
   */
  private isQuestion(content: string): boolean {
    return PatternDetectionService.QUESTION_PATTERNS.some(pattern => 
      pattern.test(content.trim())
    );
  }

  /**
   * Normalize question text for comparison
   */
  private normalizeQuestion(question: string): string {
    return question
      .toLowerCase()
      .replace(/[^\w\s]/g, '')
      .replace(/\s+/g, ' ')
      .trim()
      .substring(0, 100); // Limit length for grouping
  }

  /**
   * Extract topic from question text
   */
  private extractTopic(questionText: string): string {
    // Simple topic extraction - could be enhanced with NLP
    const words = questionText.toLowerCase().split(/\s+/);
    const stopWords = new Set(['what', 'how', 'when', 'where', 'why', 'who', 'which', 'can', 'could', 'would', 'should', 'is', 'are', 'do', 'does', 'did', 'the', 'a', 'an', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by']);

    const topicWords = words
      .filter(word => word.length > 3 && !stopWords.has(word))
      .slice(0, 3);

    return topicWords.length > 0 ? topicWords.join(' ') : 'general';
  }

  /**
   * Find answers related to a topic
   */
  private async findAnswersForTopic(
    topic: string,
    questions: Message[],
    conversationId?: string
  ): Promise<Message[]> {
    const topicKeywords = topic.split(' ');
    const questionIds = questions.map(q => q.id);

    let whereClause = 'WHERE m.role = ? AND messages_fts MATCH ?';
    const params: any[] = ['assistant', topicKeywords.join(' OR ')];

    if (conversationId) {
      whereClause += ' AND m.conversation_id = ?';
      params.push(conversationId);
    }

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      'find_topic_answers',
      `SELECT m.*
       FROM messages_fts
       JOIN messages m ON m.rowid = messages_fts.rowid
       ${whereClause}
       ORDER BY m.created_at ASC`,
      params
    );

    // Filter for messages that look like answers
    return rows
      .map(row => ({
        id: row.id,
        conversationId: row.conversation_id,
        role: row.role as 'user' | 'assistant' | 'system',
        content: row.content,
        createdAt: row.created_at,
        parentMessageId: row.parent_message_id || undefined,
        metadata: this.parseMetadata(row.metadata)
      }))
      .filter(msg => this.looksLikeAnswer(msg.content));
  }

  /**
   * Check if content looks like an answer
   */
  private looksLikeAnswer(content: string): boolean {
    return PatternDetectionService.ANSWER_INDICATORS.some(pattern => 
      pattern.test(content.trim())
    ) || content.length > 50; // Longer responses more likely to be answers
  }

  /**
   * Calculate confidence for topic identification
   */
  private calculateTopicConfidence(topic: string, questions: Message[]): number {
    const topicWords = topic.split(' ');
    let totalMatches = 0;

    for (const question of questions) {
      const questionWords = question.content.toLowerCase().split(/\s+/);
      const matches = topicWords.filter(word => questionWords.includes(word)).length;
      totalMatches += matches;
    }

    return Math.min(totalMatches / (questions.length * topicWords.length), 1.0);
  }

  /**
   * Extract keywords from commitment text
   */
  private extractKeywords(text: string): string[] {
    const words = text.toLowerCase().split(/\s+/);
    const stopWords = new Set(['i', 'will', 'ill', 'let', 'me', 'need', 'to', 'the', 'a', 'an', 'and', 'or', 'but']);

    return words
      .filter(word => word.length > 3 && !stopWords.has(word))
      .slice(0, 5);
  }

  /**
   * Determine commitment status based on follow-ups
   */
  private determineCommitmentStatus(
    commitment: { commitmentText: string; confidence: number },
    followUps: Message[],
    daysSince: number
  ): 'pending' | 'mentioned' | 'resolved' | 'overdue' {
    if (followUps.length === 0) {
      return daysSince > 7 ? 'overdue' : 'pending';
    }

    // Check if any follow-up indicates resolution
    const resolvedIndicators = ['completed', 'done', 'finished', 'resolved', 'found', 'confirmed'];
    const hasResolution = followUps.some(msg => 
      resolvedIndicators.some(indicator => msg.content.toLowerCase().includes(indicator))
    );

    return hasResolution ? 'resolved' : 'mentioned';
  }

  /**
   * Classify the type of commitment
   */
  private classifyCommitmentType(commitmentText: string): 'check' | 'follow_up' | 'update' | 'investigate' | 'temporal' {
    const text = commitmentText.toLowerCase();

    if (text.includes('check')) return 'check';
    if (text.includes('follow') || text.includes('get back')) return 'follow_up';
    if (text.includes('update')) return 'update';
    if (text.includes('investigate') || text.includes('look into')) return 'investigate';
    if (/(?:by|before|after)\s+(?:tomorrow|friday|next week|end of)/i.test(text)) return 'temporal';

    return 'check'; // Default
  }

  /**
   * Extract timeframe from commitment text
   */
  private extractTimeframe(commitmentText: string): number | undefined {
    const text = commitmentText.toLowerCase();

    if (text.includes('tomorrow')) return 1;
    if (text.includes('next week')) return 7;
    if (text.includes('friday')) {
      const today = new Date();
      const friday = 5; // Friday is day 5
      const currentDay = today.getDay();
      return currentDay <= friday ? friday - currentDay : 7 - currentDay + friday;
    }
    if (text.includes('end of week')) return 7;
    if (text.includes('end of month')) return 30;

    return undefined;
  }
}

/**
 * Knowledge Synthesizer - Advanced Entity Knowledge Management
 * 
 * Provides comprehensive knowledge synthesis, conflict detection, and contextual
 * recommendations by analyzing entity information across all conversations.
 * 
 * Features:
 * - Aggregate entity knowledge from multiple sources
 * - Detect conflicting statements and contradictions
 * - Suggest relevant context based on entity relationships
 * - Recommend domain experts and knowledgeable people
 * - Track entity attribute evolution over time
 */

import { DatabaseManager } from '../../../storage/Database.js';
import { EntityRepository, Entity, EntityType } from '../../../storage/repositories/EntityRepository.js';
import { RelationshipDetector, EntityRelationship, RelationshipType } from '../../../entities/RelationshipDetector.js';
import { ConversationRepository } from '../../../storage/repositories/ConversationRepository.js';
import { MessageRepository } from '../../../storage/repositories/MessageRepository.js';
import { Message, Conversation } from '../../../types/interfaces.js';

// Entity Knowledge Aggregation Interfaces
export interface EntityKnowledge {
  entity: Entity;
  attributes: EntityAttribute[];
  relationships: EntityRelationship[];
  mentions: EntityMention[];
  timeline: AttributeChange[];
  knowledgeScore: number;
  lastUpdated: number;
}

export interface EntityAttribute {
  name: string;
  value: string;
  confidence: number;
  sourceMessageId: string;
  sourceConversationId: string;
  extractedAt: number;
  context: string;
}

export interface EntityMention {
  messageId: string;
  conversationId: string;
  content: string;
  context: string;
  timestamp: number;
  sentiment?: 'positive' | 'negative' | 'neutral';
  importance: number; // 0-1 score
}

export interface AttributeChange {
  attribute: string;
  oldValue?: string;
  newValue: string;
  confidence: number;
  sourceMessageId: string;
  timestamp: number;
  changeType: 'addition' | 'modification' | 'contradiction' | 'confirmation';
}

// Conflict Detection Interfaces
export interface EntityConflict {
  id: string;
  entityId: string;
  conflictType: ConflictType;
  description: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  conflictingStatements: ConflictingStatement[];
  detectedAt: number;
  resolutionSuggestion?: string;
}

export type ConflictType = 
  | 'property_contradiction'   // Entity has conflicting attributes
  | 'status_inconsistency'     // Status conflicts (active/inactive, etc.)
  | 'temporal_impossibility'   // Timeline conflicts
  | 'relationship_conflict'    // Contradictory relationships
  | 'existence_dispute'        // Whether entity exists/existed
  | 'identity_confusion'       // Same entity referred to differently
  | 'authority_disagreement';  // Different authoritative sources disagree

export interface ConflictingStatement {
  messageId: string;
  conversationId: string;
  statement: string;
  context: string;
  confidence: number;
  timestamp: number;
  source?: string; // Who made the statement
}

// Context Suggestion Interfaces
export interface ContextSuggestion {
  type: SuggestionType;
  title: string;
  description: string;
  relevanceScore: number;
  entities: Entity[];
  conversations: Conversation[];
  messages: Message[];
  reasoning: string;
}

export type SuggestionType = 
  | 'related_conversation'  // Past conversations about same entities
  | 'expert_insight'        // Messages from domain experts
  | 'similar_context'       // Similar discussion contexts
  | 'temporal_connection'   // Time-related connections
  | 'relationship_network'  // Connected entity networks
  | 'follow_up_needed'      // Requires follow-up discussion
  | 'missing_information'   // Information gaps identified
  | 'contradiction_alert';  // Conflicting information found

// Expert Recommendation Interfaces
export interface ExpertRecommendation {
  person: Entity;
  expertiseAreas: string[];
  credibilityScore: number;
  interactionHistory: ExpertInteraction[];
  recentActivity: number;
  knowledgeDepth: number;
  recommendationReason: string;
}

export interface ExpertInteraction {
  conversationId: string;
  messageId: string;
  topic: string;
  contribution: string;
  timestamp: number;
  impactScore: number; // How valuable was this contribution
}

// Configuration
export interface KnowledgeSynthesizerConfig {
  // Knowledge aggregation settings
  minConfidenceThreshold: number;
  maxAttributesPerEntity: number;
  attributeExtractionPatterns: AttributePattern[];

  // Conflict detection settings
  conflictDetectionEnabled: boolean;
  temporalWindowDays: number;
  conflictSeverityThresholds: {
    low: number;
    medium: number;
    high: number;
  };

  // Context suggestion settings
  maxSuggestions: number;
  relevanceThreshold: number;
  temporalDecayFactor: number;

  // Expert recommendation settings
  expertiseCalculationWindow: number; // days
  minInteractionsForExpert: number;
  credibilityFactors: {
    recency: number;
    frequency: number;
    accuracy: number;
  };
}

export interface AttributePattern {
  name: string;
  pattern: RegExp;
  confidence: number;
  entityTypes: EntityType[];
}

export class KnowledgeSynthesizer {
  private dbManager: DatabaseManager;
  private entityRepository: EntityRepository;
  private relationshipDetector: RelationshipDetector;
  private conversationRepository: ConversationRepository;
  private messageRepository: MessageRepository;
  private config: KnowledgeSynthesizerConfig;

  constructor(dbManager: DatabaseManager, config?: Partial<KnowledgeSynthesizerConfig>) {
    this.dbManager = dbManager;
    this.entityRepository = new EntityRepository(dbManager);
    this.relationshipDetector = new RelationshipDetector(dbManager);
    this.conversationRepository = new ConversationRepository(dbManager);
    this.messageRepository = new MessageRepository(dbManager);
    this.config = this.mergeConfig(config);
  }

  /**
   * Synthesize comprehensive knowledge about an entity
   */
  async synthesizeEntityKnowledge(entityId: string): Promise<EntityKnowledge> {
    const entity = await this.entityRepository.getById(entityId);
    if (!entity) {
      throw new Error(`Entity not found: ${entityId}`);
    }

    // Get all mentions of this entity across conversations
    const mentions = await this.getEntityMentions(entityId);

    // Extract attributes from mentions
    const attributes = await this.extractEntityAttributes(entityId, mentions);

    // Get relationships
    const relationships = await this.relationshipDetector.getEntityRelationships(entityId);

    // Build timeline of attribute changes
    const timeline = this.buildAttributeTimeline(attributes);

    // Calculate knowledge score
    const knowledgeScore = this.calculateKnowledgeScore(entity, attributes, relationships, mentions);

    return {
      entity,
      attributes,
      relationships,
      mentions,
      timeline,
      knowledgeScore,
      lastUpdated: Date.now()
    };
  }

  /**
   * Detect conflicting statements about entities
   */
  async detectConflictingStatements(entityId?: string): Promise<EntityConflict[]> {
    if (!this.config.conflictDetectionEnabled) {
      return [];
    }

    let entitiesToCheck: Entity[];

    if (entityId) {
      const entity = await this.entityRepository.getById(entityId);
      entitiesToCheck = entity ? [entity] : [];
    } else {
      // Check all entities with sufficient mentions
      const searchResult = await this.entityRepository.search({
        minMentions: 3,
        limit: 100
      });
      entitiesToCheck = searchResult.entities;
    }

    const conflicts: EntityConflict[] = [];

    for (const entity of entitiesToCheck) {
      const entityKnowledge = await this.synthesizeEntityKnowledge(entity.id);

      // Detect different types of conflicts
      const propertyConflicts = await this.detectPropertyConflicts(entity, entityKnowledge);
      const statusConflicts = await this.detectStatusConflicts(entity, entityKnowledge);
      const temporalConflicts = await this.detectTemporalConflicts(entity, entityKnowledge);
      const relationshipConflicts = await this.detectRelationshipConflicts(entity, entityKnowledge);

      conflicts.push(...propertyConflicts, ...statusConflicts, ...temporalConflicts, ...relationshipConflicts);
    }

    // Sort by severity and recency
    return conflicts.sort((a, b) => {
      const severityOrder = { critical: 4, high: 3, medium: 2, low: 1 };
      const severityDiff = severityOrder[b.severity] - severityOrder[a.severity];
      if (severityDiff !== 0) return severityDiff;
      return b.detectedAt - a.detectedAt;
    });
  }

  /**
   * Suggest relevant context based on current entities and conversation
   */
  async suggestRelevantContext(
    currentEntities: string[],
    conversationId: string,
    limit: number = 5
  ): Promise<ContextSuggestion[]> {
    const suggestions: ContextSuggestion[] = [];

    // Get entity objects
    const entities = await Promise.all(
      currentEntities.map(id => this.entityRepository.getById(id))
    );
    const validEntities = entities.filter(e => e !== null) as Entity[];

    if (validEntities.length === 0) {
      return suggestions;
    }

    // Find related conversations through entity co-occurrence
    const relatedConversations = await this.findRelatedConversations(validEntities, conversationId);

    // Find expert insights
    const expertInsights = await this.findExpertInsights(validEntities);

    // Find similar contexts
    const similarContexts = await this.findSimilarContexts(validEntities, conversationId);

    // Check for contradictions
    const contradictionAlerts = await this.findContradictionAlerts(validEntities);

    // Find missing information
    const missingInfo = await this.findMissingInformation(validEntities);

    // Combine and score suggestions
    suggestions.push(...relatedConversations, ...expertInsights, ...similarContexts, 
                   ...contradictionAlerts, ...missingInfo);

    // Sort by relevance score and return top results
    return suggestions
      .sort((a, b) => b.relevanceScore - a.relevanceScore)
      .slice(0, limit);
  }

  /**
   * Recommend experts on specific topics or entities
   */
  async recommendExperts(
    entities: string[],
    topic?: string,
    limit: number = 3
  ): Promise<ExpertRecommendation[]> {
    const recommendations: ExpertRecommendation[] = [];

    // Get all people entities who have interacted with the specified entities
    const peopleWithInteractions = await this.findPeopleWithEntityInteractions(entities);

    for (const person of peopleWithInteractions) {
      const expertiseAreas = await this.calculateExpertiseAreas(person.id, entities);
      const credibilityScore = await this.calculateCredibilityScore(person.id, entities, topic);
      const interactionHistory = await this.getExpertInteractionHistory(person.id, entities);
      const recentActivity = await this.calculateRecentActivity(person.id);
      const knowledgeDepth = await this.calculateKnowledgeDepth(person.id, entities);

      if (credibilityScore >= 0.3 && interactionHistory.length >= this.config.minInteractionsForExpert) {
        recommendations.push({
          person,
          expertiseAreas,
          credibilityScore,
          interactionHistory,
          recentActivity,
          knowledgeDepth,
          recommendationReason: this.generateRecommendationReason(
            person, expertiseAreas, credibilityScore, interactionHistory.length
          )
        });
      }
    }

    // Sort by credibility and knowledge depth
    return recommendations
      .sort((a, b) => {
        const scoreA = a.credibilityScore * 0.6 + a.knowledgeDepth * 0.4;
        const scoreB = b.credibilityScore * 0.6 + b.knowledgeDepth * 0.4;
        return scoreB - scoreA;
      })
      .slice(0, limit);
  }

  /**
   * Get all mentions of an entity across conversations
   */
  private async getEntityMentions(entityId: string): Promise<EntityMention[]> {
    const db = this.dbManager.getConnection();

    const query = `
      SELECT 
        m.id as message_id,
        m.conversation_id,
        m.content,
        m.created_at,
        em.start_position,
        em.end_position,
        em.confidence_score
      FROM messages m
      JOIN entity_mentions em ON m.id = em.message_id
      WHERE em.entity_id = ?
      ORDER BY m.created_at DESC
    `;

    const rows = db.prepare(query).all(entityId) as any[];

    return rows.map(row => {
      const startPos = row.start_position || 0;
      const content = row.content || '';
      const endPos = row.end_position || content.length;
      const contextStart = Math.max(0, startPos - 50);
      const contextEnd = Math.min(content.length, endPos + 50);

      return {
        messageId: row.message_id,
        conversationId: row.conversation_id,
        content: content,
        context: content.substring(contextStart, contextEnd),
        timestamp: row.created_at,
        importance: Math.min(1.0, row.confidence_score || 0.5)
      };
    });
  }

  /**
   * Extract attributes from entity mentions using patterns and NLP
   */
  private async extractEntityAttributes(entityId: string, mentions: EntityMention[]): Promise<EntityAttribute[]> {
    const attributes: EntityAttribute[] = [];
    const entity = await this.entityRepository.getById(entityId);

    if (!entity) return attributes;

    for (const mention of mentions) {
      // Apply extraction patterns based on entity type
      const applicablePatterns = this.config.attributeExtractionPatterns.filter(
        pattern => pattern.entityTypes.includes(entity.type)
      );

      for (const pattern of applicablePatterns) {
        const matches = mention.content.match(pattern.pattern);
        if (matches && matches[1]) {
          attributes.push({
            name: pattern.name,
            value: matches[1].trim(),
            confidence: pattern.confidence * mention.importance,
            sourceMessageId: mention.messageId,
            sourceConversationId: mention.conversationId,
            extractedAt: mention.timestamp,
            context: mention.context
          });
        }
      }

      // Apply generic attribute extraction patterns
      const genericPatterns = this.getGenericAttributePatterns(entity.type);
      for (const pattern of genericPatterns) {
        const matches = mention.content.match(pattern.regex);
        if (matches && matches[1]) {
          attributes.push({
            name: pattern.attribute,
            value: matches[1].trim(),
            confidence: pattern.confidence * mention.importance,
            sourceMessageId: mention.messageId,
            sourceConversationId: mention.conversationId,
            extractedAt: mention.timestamp,
            context: mention.context
          });
        }
      }
    }

    // Deduplicate and merge similar attributes
    return this.deduplicateAttributes(attributes);
  }

  /**
   * Build timeline of attribute changes
   */
  private buildAttributeTimeline(attributes: EntityAttribute[]): AttributeChange[] {
    const timeline: AttributeChange[] = [];
    const attributeGroups = new Map<string, EntityAttribute[]>();

    // Group attributes by name
    for (const attr of attributes) {
      if (!attributeGroups.has(attr.name)) {
        attributeGroups.set(attr.name, []);
      }
      attributeGroups.get(attr.name)!.push(attr);
    }

    // Analyze changes for each attribute
    for (const [attributeName, attrs] of attributeGroups) {
      // Sort by extraction time
      attrs.sort((a, b) => a.extractedAt - b.extractedAt);

      let previousValue: string | undefined;

      for (const attr of attrs) {
        const changeType = this.determineChangeType(previousValue, attr.value, attrs);

        timeline.push({
          attribute: attributeName,
          oldValue: previousValue,
          newValue: attr.value,
          confidence: attr.confidence,
          sourceMessageId: attr.sourceMessageId,
          timestamp: attr.extractedAt,
          changeType
        });

        previousValue = attr.value;
      }
    }

    return timeline.sort((a, b) => a.timestamp - b.timestamp);
  }

  /**
   * Detect property conflicts (contradicting attribute values)
   */
  private async detectPropertyConflicts(entity: Entity, knowledge: EntityKnowledge): Promise<EntityConflict[]> {
    const conflicts: EntityConflict[] = [];
    const attributeGroups = new Map<string, EntityAttribute[]>();

    // Group attributes by name
    for (const attr of knowledge.attributes) {
      if (!attributeGroups.has(attr.name)) {
        attributeGroups.set(attr.name, []);
      }
      attributeGroups.get(attr.name)!.push(attr);
    }

    // Check each attribute group for conflicts
    for (const [attributeName, attrs] of attributeGroups) {
      if (attrs.length < 2) continue;

      const uniqueValues = new Set(attrs.map(a => a.value.toLowerCase()));
      if (uniqueValues.size > 1) {
        // Potential conflict found
        const highConfidenceValues = attrs.filter(a => a.confidence > 0.7);

        if (highConfidenceValues.length > 1) {
          const conflictingStatements = attrs.map(attr => ({
            messageId: attr.sourceMessageId,
            conversationId: attr.sourceConversationId,
            statement: `${attributeName}: ${attr.value}`,
            context: attr.context,
            confidence: attr.confidence,
            timestamp: attr.extractedAt
          }));

          const severity = this.calculateConflictSeverity(attrs);

          conflicts.push({
            id: this.generateId(),
            entityId: entity.id,
            conflictType: 'property_contradiction',
            description: `Conflicting values for ${attributeName}: ${Array.from(uniqueValues).join(' vs ')}`,
            severity,
            conflictingStatements,
            detectedAt: Date.now(),
            resolutionSuggestion: this.suggestPropertyResolution(attrs)
          });
        }
      }
    }

    return conflicts;
  }

  /**
   * Detect status inconsistencies
   */
  private async detectStatusConflicts(entity: Entity, knowledge: EntityKnowledge): Promise<EntityConflict[]> {
    const conflicts: EntityConflict[] = [];

    // Look for status-related attributes
    const statusAttributes = knowledge.attributes.filter(attr => 
      ['status', 'state', 'active', 'inactive', 'enabled', 'disabled', 'open', 'closed'].includes(attr.name.toLowerCase())
    );

    if (statusAttributes.length < 2) return conflicts;

    // Check for contradictory statuses
    const statusValues = statusAttributes.map(attr => attr.value.toLowerCase());
    const hasActive = statusValues.some(v => ['active', 'enabled', 'open', 'running'].includes(v));
    const hasInactive = statusValues.some(v => ['inactive', 'disabled', 'closed', 'stopped'].includes(v));

    if (hasActive && hasInactive) {
      const conflictingStatements = statusAttributes.map(attr => ({
        messageId: attr.sourceMessageId,
        conversationId: attr.sourceConversationId,
        statement: `Status: ${attr.value}`,
        context: attr.context,
        confidence: attr.confidence,
        timestamp: attr.extractedAt
      }));

      conflicts.push({
        id: this.generateId(),
        entityId: entity.id,
        conflictType: 'status_inconsistency',
        description: `Conflicting status information for ${entity.name}`,
        severity: 'medium',
        conflictingStatements,
        detectedAt: Date.now(),
        resolutionSuggestion: 'Check recent status updates to determine current state'
      });
    }

    return conflicts;
  }

  /**
   * Detect temporal impossibilities
   */
  private async detectTemporalConflicts(entity: Entity, knowledge: EntityKnowledge): Promise<EntityConflict[]> {
    const conflicts: EntityConflict[] = [];

    // Look for temporal attributes
    const temporalAttributes = knowledge.attributes.filter(attr => 
      ['created', 'founded', 'started', 'ended', 'born', 'died', 'launched', 'closed'].includes(attr.name.toLowerCase())
    );

    // Check for impossible timelines (e.g., end date before start date)
    const startDates = temporalAttributes.filter(attr => 
      ['created', 'founded', 'started', 'born', 'launched'].includes(attr.name.toLowerCase())
    );
    const endDates = temporalAttributes.filter(attr => 
      ['ended', 'died', 'closed'].includes(attr.name.toLowerCase())
    );

    for (const startAttr of startDates) {
      for (const endAttr of endDates) {
        const startDate = this.parseDate(startAttr.value);
        const endDate = this.parseDate(endAttr.value);

        if (startDate && endDate && startDate > endDate) {
          conflicts.push({
            id: this.generateId(),
            entityId: entity.id,
            conflictType: 'temporal_impossibility',
            description: `${entity.name} has end date (${endAttr.value}) before start date (${startAttr.value})`,
            severity: 'high',
            conflictingStatements: [
              {
                messageId: startAttr.sourceMessageId,
                conversationId: startAttr.sourceConversationId,
                statement: `${startAttr.name}: ${startAttr.value}`,
                context: startAttr.context,
                confidence: startAttr.confidence,
                timestamp: startAttr.extractedAt
              },
              {
                messageId: endAttr.sourceMessageId,
                conversationId: endAttr.sourceConversationId,
                statement: `${endAttr.name}: ${endAttr.value}`,
                context: endAttr.context,
                confidence: endAttr.confidence,
                timestamp: endAttr.extractedAt
              }
            ],
            detectedAt: Date.now(),
            resolutionSuggestion: 'Verify the correct dates from authoritative sources'
          });
        }
      }
    }

    return conflicts;
  }

  /**
   * Detect relationship conflicts
   */
  private async detectRelationshipConflicts(entity: Entity, knowledge: EntityKnowledge): Promise<EntityConflict[]> {
    const conflicts: EntityConflict[] = [];

    // Look for contradictory relationships
    const relationships = knowledge.relationships || [];

    // Check for mutually exclusive relationships
    const exclusivePatterns = [
      { type1: 'works_for', type2: 'works_for' }, // Can't work for two competing companies
      { type1: 'created_by', type2: 'created_by' } // Can't be created by multiple sources
    ];

    for (const pattern of exclusivePatterns) {
      const type1Rels = relationships.filter(r => r.relationshipType === pattern.type1);
      const type2Rels = relationships.filter(r => r.relationshipType === pattern.type2);

      if (type1Rels.length > 1 && pattern.type1 === pattern.type2) {
        // Multiple relationships of same type might be problematic
        const targets = new Set(type1Rels.map(r => 
          r.sourceEntityId === entity.id ? r.targetEntityId : r.sourceEntityId
        ));

        if (targets.size > 1 && pattern.type1 === 'works_for') {
          conflicts.push({
            id: this.generateId(),
            entityId: entity.id,
            conflictType: 'relationship_conflict',
            description: `${entity.name} appears to work for multiple organizations`,
            severity: 'medium',
            conflictingStatements: [], // Would need to fetch original statements
            detectedAt: Date.now(),
            resolutionSuggestion: 'Check if these are sequential employment or consulting relationships'
          });
        }
      }
    }

    return conflicts;
  }

  /**
   * Find conversations related to given entities
   */
  private async findRelatedConversations(entities: Entity[], excludeConversationId: string): Promise<ContextSuggestion[]> {
    const suggestions: ContextSuggestion[] = [];
    const entityIds = entities.map(e => e.id);

    const db = this.dbManager.getConnection();

    // Find conversations with high entity overlap
    const query = `
      SELECT 
        c.id,
        c.title,
        c.created_at,
        COUNT(DISTINCT em.entity_id) as entity_count,
        GROUP_CONCAT(DISTINCT e.name) as entity_names
      FROM conversations c
      JOIN messages m ON c.id = m.conversation_id
      JOIN entity_mentions em ON m.id = em.message_id
      JOIN entities e ON em.entity_id = e.id
      WHERE em.entity_id IN (${entityIds.map(() => '?').join(',')})
        AND c.id != ?
      GROUP BY c.id
      HAVING entity_count >= 2
      ORDER BY entity_count DESC, c.created_at DESC
      LIMIT 10
    `;

    const rows = db.prepare(query).all(...entityIds, excludeConversationId) as any[];

    for (const row of rows) {
      const conversation = await this.conversationRepository.findById(row.id);
      if (!conversation) continue;

      const relevanceScore = Math.min(1.0, (row.entity_count / entityIds.length) * 0.8);

      suggestions.push({
        type: 'related_conversation',
        title: `Related discussion: ${conversation.title || 'Untitled conversation'}`,
        description: `Previous conversation mentioning ${row.entity_names}`,
        relevanceScore,
        entities,
        conversations: [conversation],
        messages: [],
        reasoning: `Found ${row.entity_count} overlapping entities`
      });
    }

    return suggestions;
  }

  /**
   * Find expert insights from knowledgeable people
   */
  private async findExpertInsights(entities: Entity[]): Promise<ContextSuggestion[]> {
    const suggestions: ContextSuggestion[] = [];
    const experts = await this.recommendExperts(entities.map(e => e.id), undefined, 3);

    for (const expert of experts) {
      if (expert.interactionHistory.length > 0) {
        const recentInteractions = expert.interactionHistory
          .filter(i => Date.now() - i.timestamp < 30 * 24 * 60 * 60 * 1000) // 30 days
          .slice(0, 3);

        if (recentInteractions.length > 0) {
          const messages = await Promise.all(
            recentInteractions.map(i => this.messageRepository.findById(i.messageId))
          );
          const validMessages = messages.filter((m: Message | null) => m !== null) as Message[];

          suggestions.push({
            type: 'expert_insight',
            title: `Insights from ${expert.person.name}`,
            description: `${expert.person.name} has expertise in ${expert.expertiseAreas.join(', ')}`,
            relevanceScore: expert.credibilityScore,
            entities: [expert.person],
            conversations: [],
            messages: validMessages,
            reasoning: `Expert with ${expert.credibilityScore.toFixed(2)} credibility score`
          });
        }
      }
    }

    return suggestions;
  }

