        low: 0.3,
        medium: 0.6,
        high: 0.8
      },
      maxSuggestions: 5,
      relevanceThreshold: 0.4,
      temporalDecayFactor: 0.1,
      expertiseCalculationWindow: 90,
      minInteractionsForExpert: 3,
      credibilityFactors: {
        recency: 0.3,
        frequency: 0.4,
        accuracy: 0.3
      },
      ...config
    };
  }

  private getDefaultAttributePatterns(): AttributePattern[] {
    return [
      {
        name: 'role',
        pattern: /(?:is a|works as|role of|position of)\s+([^.,]+)/i,
        confidence: 0.7,
        entityTypes: ['person']
      },
      {
        name: 'founded',
        pattern: /(?:founded in|established in|started in)\s+(\d{4})/i,
        confidence: 0.8,
        entityTypes: ['organization', 'product']
      },
      {
        name: 'location',
        pattern: /(?:based in|located in|headquarters in)\s+([^.,]+)/i,
        confidence: 0.7,
        entityTypes: ['organization', 'person']
      },
      {
        name: 'technology',
        pattern: /(?:built with|uses|powered by)\s+([^.,]+)/i,
        confidence: 0.6,
        entityTypes: ['product', 'technical']
      }
    ];
  }

  private getGenericAttributePatterns(entityType: EntityType) {
    const patterns = [];

    switch (entityType) {
      case 'person':
        patterns.push(
          { attribute: 'title', regex: /(?:title|role):\s*([^.,\n]+)/i, confidence: 0.7 },
          { attribute: 'company', regex: /(?:works at|employed by)\s+([^.,\n]+)/i, confidence: 0.8 }
        );
        break;
      case 'organization':
        patterns.push(
          { attribute: 'industry', regex: /(?:industry|sector):\s*([^.,\n]+)/i, confidence: 0.7 },
          { attribute: 'size', regex: /(?:employees|people):\s*([^.,\n]+)/i, confidence: 0.6 }
        );
        break;
      case 'product':
        patterns.push(
          { attribute: 'version', regex: /(?:version|v\.?)\s*([0-9.]+)/i, confidence: 0.9 },
          { attribute: 'platform', regex: /(?:platform|OS):\s*([^.,\n]+)/i, confidence: 0.7 }
        );
        break;
    }

    return patterns;
  }

  private deduplicateAttributes(attributes: EntityAttribute[]): EntityAttribute[] {
    const deduped = new Map<string, EntityAttribute>();

    for (const attr of attributes) {
      const key = `${attr.name}:${attr.value.toLowerCase()}`;
      const existing = deduped.get(key);

      if (!existing || attr.confidence > existing.confidence) {
        deduped.set(key, attr);
      }
    }

    return Array.from(deduped.values());
  }

  private determineChangeType(
    previousValue: string | undefined,
    newValue: string,
    allAttributes: EntityAttribute[]
  ): 'addition' | 'modification' | 'contradiction' | 'confirmation' {
    if (!previousValue) {
      return 'addition';
    }

    if (previousValue === newValue) {
      return 'confirmation';
    }

    // Check if values are contradictory vs just different versions
    const similarity = this.calculateStringSimilarity(previousValue, newValue);
    if (similarity < 0.3) {
      return 'contradiction';
    }

    return 'modification';
  }

  private calculateStringSimilarity(str1: string, str2: string): number {
    // Simple Jaccard similarity
    const set1 = new Set(str1.toLowerCase().split(/\s+/));
    const set2 = new Set(str2.toLowerCase().split(/\s+/));
    const intersection = new Set([...set1].filter(x => set2.has(x)));
    const union = new Set([...set1, ...set2]);
    return intersection.size / union.size;
  }

  private calculateConflictSeverity(attributes: EntityAttribute[]): 'low' | 'medium' | 'high' | 'critical' {
    const avgConfidence = attributes.reduce((sum, attr) => sum + attr.confidence, 0) / attributes.length;

    if (avgConfidence >= this.config.conflictSeverityThresholds.high) return 'critical';
    if (avgConfidence >= this.config.conflictSeverityThresholds.medium) return 'high';
    if (avgConfidence >= this.config.conflictSeverityThresholds.low) return 'medium';
    return 'low';
  }

  private suggestPropertyResolution(attributes: EntityAttribute[]): string {
    const sortedByConfidence = attributes.sort((a, b) => b.confidence - a.confidence);
    const mostRecent = attributes.sort((a, b) => b.extractedAt - a.extractedAt)[0];
    const mostConfident = sortedByConfidence[0];

    if (mostConfident.confidence > 0.8) {
      return `Consider the most confident value: ${mostConfident.value}`;
    } else if (mostRecent.extractedAt > Date.now() - 7 * 24 * 60 * 60 * 1000) {
      return `Consider the most recent value: ${mostRecent.value}`;
    }
    return 'Manual verification needed to resolve conflict';
  }

  private parseDate(dateString: string): Date | null {
    try {
      // Try various date formats
      const patterns = [
        /(\d{4})-(\d{2})-(\d{2})/,
        /(\d{2})\/(\d{2})\/(\d{4})/,
        /(\d{4})/
      ];

      for (const pattern of patterns) {
        const match = dateString.match(pattern);
        if (match) {
          return new Date(match[0]);
        }
      }
      return null;
    } catch {
      return null;
    }
  }

  private async findPeopleWithEntityInteractions(entityIds: string[]): Promise<Entity[]> {
    const db = this.dbManager.getConnection();

    const query = `
      SELECT DISTINCT e.* 
      FROM entities e
      JOIN entity_mentions em1 ON e.id = em1.entity_id
      JOIN entity_mentions em2 ON em1.message_id = em2.message_id
      WHERE e.type = 'person' 
        AND em2.entity_id IN (${entityIds.map(() => '?').join(',')})
        AND e.id NOT IN (${entityIds.map(() => '?').join(',')})
      ORDER BY e.mention_count DESC
    `;

    const rows = db.prepare(query).all(...entityIds, ...entityIds) as any[];

    return rows.map(row => ({
      id: row.id,
      name: row.name,
      normalizedName: row.normalized_name,
      type: row.type as EntityType,
      canonicalForm: row.canonical_form,
      confidenceScore: row.confidence_score,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      metadata: JSON.parse(row.metadata || '{}'),
      mentionCount: row.mention_count,
      lastMentionedAt: row.last_mentioned_at
    }));
  }

  private async calculateExpertiseAreas(personId: string, entityIds: string[]): Promise<string[]> {
    // Analyze topics this person discusses in relation to the entities
    const mentions = await this.getEntityMentions(personId);
    const topics = new Set<string>();

    if (!mentions) {
      return [];
    }

    for (const mention of mentions) {
      // Extract topics from message content (simplified)
      const words = (mention.content || '').toLowerCase().split(/\s+/);
      const techWords = words.filter(word => 
        ['technology', 'development', 'design', 'architecture', 'programming'].includes(word)
      );
      techWords.forEach(word => topics.add(word));
    }

    return Array.from(topics).slice(0, 5);
  }

  private async calculateCredibilityScore(personId: string, entityIds: string[], topic?: string): Promise<number> {
    const mentions = await this.getEntityMentions(personId);
    if (!mentions || mentions.length === 0) {
      return 0;
    }

    const recentMentions = mentions.filter(m => 
      Date.now() - m.timestamp < this.config.expertiseCalculationWindow * 24 * 60 * 60 * 1000
    );

    let score = 0;

    // Recency factor
    score += (recentMentions.length / mentions.length) * this.config.credibilityFactors.recency;

    // Frequency factor
    score += Math.min(1.0, mentions.length / 10) * this.config.credibilityFactors.frequency;

    // Accuracy factor (simplified - could be enhanced with feedback)
    score += 0.7 * this.config.credibilityFactors.accuracy;

    return Math.min(1.0, score);
  }

  private async getExpertInteractionHistory(personId: string, entityIds: string[]): Promise<ExpertInteraction[]> {
    const mentions = await this.getEntityMentions(personId);

    if (!mentions) {
      return [];
    }

    return mentions.map(mention => ({
      conversationId: mention.conversationId,
      messageId: mention.messageId,
      topic: 'General discussion', // Could be enhanced with topic modeling
      contribution: mention.context,
      timestamp: mention.timestamp,
      impactScore: mention.importance
    }));
  }

  private async calculateRecentActivity(personId: string): Promise<number> {
    const mentions = await this.getEntityMentions(personId);
    if (!mentions) return 0;

    const recent = mentions.filter(m => 
      Date.now() - m.timestamp < 7 * 24 * 60 * 60 * 1000
    );
    return recent.length;
  }

  private async calculateKnowledgeDepth(personId: string, entityIds: string[]): Promise<number> {
    const mentions = await this.getEntityMentions(personId);
    if (!mentions || mentions.length === 0) return 0;

    const avgImportance = mentions.reduce((sum, m) => sum + m.importance, 0) / mentions.length;
    return avgImportance || 0;
  }

  private generateRecommendationReason(
    person: Entity,
    expertiseAreas: string[],
    credibilityScore: number,
    interactionCount: number
  ): string {
    return `${person.name} has ${credibilityScore.toFixed(2)} credibility score with ${interactionCount} relevant interactions in ${expertiseAreas.join(', ')}`;
  }

  private identifyMissingAttributes(entity: Entity, knowledge: EntityKnowledge): string[] {
    const expectedAttributes = this.getExpectedAttributesForType(entity.type);
    const existingAttributes = new Set(knowledge.attributes.map(a => a.name.toLowerCase()));

    return expectedAttributes.filter(attr => !existingAttributes.has(attr.toLowerCase()));
  }

  private getExpectedAttributesForType(entityType: EntityType): string[] {
    switch (entityType) {
      case 'person':
        return ['role', 'company', 'title', 'location', 'expertise'];
      case 'organization':
        return ['industry', 'founded', 'location', 'size', 'status'];
      case 'product':
        return ['version', 'platform', 'technology', 'status', 'created'];
      case 'technical':
        return ['type', 'version', 'documentation', 'maintainer'];
      default:
        return ['description', 'category', 'status'];
    }
  }

  private generateId(): string {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
      const r = Math.random() * 16 | 0;
      const v = c === 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  }
}

#!/usr/bin/env node

/**
 * Simple MCP Server Entry Point
 * 
 * A working entry point that demonstrates the MCP server functionality
 * without complex tool dependencies.
 */

import { createSimpleMCPServer } from './server/SimpleMCPServer.js';

async function main(): Promise<void> {
  try {
    const config = {
      name: 'mcp-persistence-server',
      version: '1.0.0',
      databasePath: process.env.PERSISTENCE_DB_PATH || './conversations.db',
      debug: process.env.PERSISTENCE_DEBUG === 'true'
    };

    const server = createSimpleMCPServer(config);

    // Setup graceful shutdown
    const signals = ['SIGINT', 'SIGTERM'] as const;
    signals.forEach(signal => {
      process.on(signal, async () => {
        console.log(`\n[INFO] Received ${signal}, shutting down...`);
        await server.stop();
        process.exit(0);
      });
    });

    await server.start();

    // Keep process alive
    process.stdin.resume();

  } catch (error) {
    console.error('[ERROR] Failed to start server:', error);
    process.exit(1);
  }
}

// For ES modules, we can use import.meta.url to detect if this is the main module
const isMainModule = process.argv[1] && new URL(process.argv[1], 'file://').href === import.meta.url;

if (isMainModule) {
  main().catch(error => {
    console.error('[ERROR] Unexpected error:', error);
    process.exit(1);
  });
}

// Export main function for module use
export { main };

/**
 * SQLite Connection Pool Implementation
 * 
 * Provides connection pooling for SQLite operations to improve performance
 * and handle concurrent requests efficiently.
 */

import Database from 'better-sqlite3';
import { EventEmitter } from 'events';

export interface ConnectionPoolOptions {
  databasePath: string;
  minConnections: number;
  maxConnections: number;
  enableWAL?: boolean;
  enableForeignKeys?: boolean;
  cacheSize?: number;
  busyTimeout?: number;
  readOnly?: boolean;
  create?: boolean;
  enableMetrics?: boolean;
}

export interface ConnectionPoolStatus {
  totalConnections: number;
  activeConnections: number;
  idleConnections: number;
  pendingRequests: number;
}

export interface ConnectionPoolMetrics {
  totalConnections: number;
  activeConnections: number;
  idleConnections: number;
  pendingRequests: number;
  connectionsCreated: number;
  connectionsDestroyed: number;
  requestsServed: number;
  averageWaitTime: number;
  peakConnections: number;
}

interface PooledConnection {
  id: string;
  db: Database.Database;
  inUse: boolean;
  createdAt: number;
  lastUsed: number;
}

interface PendingRequest {
  resolve: (connection: PooledConnection) => void;
  reject: (error: Error) => void;
  requestedAt: number;
}

export class ConnectionPool extends EventEmitter {
  private connections: Map<string, PooledConnection> = new Map();
  private pendingRequests: PendingRequest[] = [];
  private options: Required<ConnectionPoolOptions>;
  private isShuttingDown = false;
  private metrics: ConnectionPoolMetrics;

  constructor(options: ConnectionPoolOptions) {
    super();

    this.options = {
      enableWAL: true,
      enableForeignKeys: true,
      cacheSize: 10000,
      busyTimeout: 5000,
      readOnly: false,
      create: true,
      enableMetrics: true,
      ...options,
      // Set defaults for required properties if not provided
      minConnections: options.minConnections ?? 2,
      maxConnections: options.maxConnections ?? 10
    };

    this.metrics = {
      totalConnections: 0,
      activeConnections: 0,
      idleConnections: 0,
      pendingRequests: 0,
      connectionsCreated: 0,
      connectionsDestroyed: 0,
      requestsServed: 0,
      averageWaitTime: 0,
      peakConnections: 0
    };

    // Initialize minimum connections
    this.initializePool();
  }

  private async initializePool(): Promise<void> {
    for (let i = 0; i < this.options.minConnections; i++) {
      try {
        const connection = await this.createConnection();
        this.connections.set(connection.id, connection);
      } catch (error) {
        console.error(`Failed to create initial connection ${i + 1}:`, error);
      }
    }
  }

  private async createConnection(): Promise<PooledConnection> {
    const id = `conn_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`;

    const db = new Database(this.options.databasePath, {
      readonly: this.options.readOnly
    });

    // Configure connection
    if (this.options.enableWAL) {
      db.exec('PRAGMA journal_mode = WAL');
    }

    if (this.options.enableForeignKeys) {
      db.exec('PRAGMA foreign_keys = ON');
    }

    if (this.options.cacheSize > 0) {
      db.exec(`PRAGMA cache_size = -${this.options.cacheSize}`);
    }

    if (this.options.busyTimeout > 0) {
      db.pragma(`busy_timeout = ${this.options.busyTimeout}`);
    }

    // Additional performance optimizations
    db.exec('PRAGMA temp_store = MEMORY');
    db.exec('PRAGMA mmap_size = 268435456'); // 256MB mmap
    db.exec('PRAGMA synchronous = NORMAL');

    const connection: PooledConnection = {
      id,
      db,
      inUse: false,
      createdAt: Date.now(),
      lastUsed: Date.now()
    };

    this.metrics.connectionsCreated++;
    this.metrics.totalConnections = this.connections.size + 1;
    this.metrics.peakConnections = Math.max(this.metrics.peakConnections, this.metrics.totalConnections);

    this.emit('connection:created', { connectionId: id });
    return connection;
  }

  private async destroyConnection(connection: PooledConnection): Promise<void> {
    try {
      connection.db.close();
      this.connections.delete(connection.id);
      this.metrics.connectionsDestroyed++;
      this.metrics.totalConnections = this.connections.size;
      this.emit('connection:destroyed', { connectionId: connection.id });
    } catch (error) {
      console.error(`Failed to destroy connection ${connection.id}:`, error);
    }
  }

  private getIdleConnection(): PooledConnection | null {
    for (const connection of this.connections.values()) {
      if (!connection.inUse) {
        return connection;
      }
    }
    return null;
  }

  private updateMetrics(): void {
    let active = 0;
    let idle = 0;

    for (const connection of this.connections.values()) {
      if (connection.inUse) {
        active++;
      } else {
        idle++;
      }
    }

    this.metrics.activeConnections = active;
    this.metrics.idleConnections = idle;
    this.metrics.pendingRequests = this.pendingRequests.length;
  }

  private async acquireConnection(): Promise<PooledConnection> {
    if (this.isShuttingDown) {
      throw new Error('Connection pool is shutting down');
    }

    // Try to get an idle connection first
    let connection = this.getIdleConnection();

    if (connection) {
      connection.inUse = true;
      connection.lastUsed = Date.now();
      this.updateMetrics();
      this.metrics.requestsServed++;
      return connection;
    }

    // If no idle connection and we can create more
    if (this.connections.size < this.options.maxConnections) {
      try {
        connection = await this.createConnection();
        connection.inUse = true;
        this.connections.set(connection.id, connection);
        this.updateMetrics();
        this.metrics.requestsServed++;
        return connection;
      } catch (error) {
        console.error('Failed to create new connection:', error);
      }
    }

    // Wait for a connection to become available
    return new Promise((resolve, reject) => {
      const requestStartTime = Date.now();
      const request: PendingRequest = {
        resolve: (conn) => {
          const waitTime = Date.now() - requestStartTime;
          this.metrics.averageWaitTime = 
            (this.metrics.averageWaitTime * (this.metrics.requestsServed - 1) + waitTime) / this.metrics.requestsServed;
          resolve(conn);
        },
        reject,
        requestedAt: requestStartTime
      };

      this.pendingRequests.push(request);
      this.updateMetrics();

      // Timeout after reasonable time
      const timeout = setTimeout(() => {
        const index = this.pendingRequests.indexOf(request);
        if (index > -1) {
          this.pendingRequests.splice(index, 1);
          reject(new Error('Connection request timeout'));
        }
      }, 30000); // 30 second timeout

      request.resolve = (conn) => {
        clearTimeout(timeout);
        const waitTime = Date.now() - requestStartTime;
        this.metrics.averageWaitTime = 
          (this.metrics.averageWaitTime * (this.metrics.requestsServed - 1) + waitTime) / this.metrics.requestsServed;
        resolve(conn);
      };
    });
  }

  private releaseConnection(connection: PooledConnection): void {
    connection.inUse = false;
    connection.lastUsed = Date.now();

    // Check if there are pending requests
    const pendingRequest = this.pendingRequests.shift();
    if (pendingRequest) {
      connection.inUse = true;
      this.metrics.requestsServed++;
      pendingRequest.resolve(connection);
    }

    this.updateMetrics();
  }

  /**
   * Execute a function with a pooled database connection
   */
  async withConnection<T>(fn: (db: Database.Database) => T | Promise<T>): Promise<T> {
    const connection = await this.acquireConnection();

    try {
      const result = await fn(connection.db);
      return result;
    } finally {
      this.releaseConnection(connection);
    }
  }

  /**
   * Execute a function within a transaction using a pooled connection
   */
  async withTransaction<T>(fn: (db: Database.Database) => T | Promise<T>): Promise<T> {
    const connection = await this.acquireConnection();

    try {
      const result = connection.db.transaction(async () => {
        return await fn(connection.db);
      })();
      return result;
    } finally {
      this.releaseConnection(connection);
    }
  }

  /**
   * Get current pool status
   */
  getStatus(): ConnectionPoolStatus {
    this.updateMetrics();
    return {
      totalConnections: this.metrics.totalConnections,
      activeConnections: this.metrics.activeConnections,
      idleConnections: this.metrics.idleConnections,
      pendingRequests: this.metrics.pendingRequests
    };
  }

  /**
   * Get detailed pool metrics
   */
  getMetrics(): ConnectionPoolMetrics {
    this.updateMetrics();
    return { ...this.metrics };
  }

  /**
   * Shutdown the connection pool
   */
  async shutdown(): Promise<void> {
    this.isShuttingDown = true;

    // Reject all pending requests
    const error = new Error('Connection pool shutting down');
    this.pendingRequests.forEach(request => request.reject(error));
    this.pendingRequests.length = 0;

    // Close all connections
    const closePromises = Array.from(this.connections.values()).map(connection => 
      this.destroyConnection(connection)
    );

    await Promise.all(closePromises);
    this.connections.clear();

    this.emit('pool:shutdown');
  }

  /**
   * Health check for the connection pool
   */
  async healthCheck(): Promise<{
    healthy: boolean;
    issues: string[];
    metrics: ConnectionPoolMetrics;
  }> {
    const issues: string[] = [];

    // Check if we have minimum connections
    if (this.connections.size < this.options.minConnections) {
      issues.push(`Below minimum connections: ${this.connections.size}/${this.options.minConnections}`);
    }

    // Check if too many pending requests
    if (this.pendingRequests.length > this.options.maxConnections) {
      issues.push(`Too many pending requests: ${this.pendingRequests.length}`);
    }

    // Test a connection
    try {
      await this.withConnection(db => {
        return db.prepare('SELECT 1').get();
      });
    } catch (error) {
      issues.push(`Connection test failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    return {
      healthy: issues.length === 0,
      issues,
      metrics: this.getMetrics()
    };
  }
}

/**
 * Database Layer - Main database class with connection management
 * 
 * This class provides:
 * - SQLite connection management with better-sqlite3
 * - Connection pooling for concurrent operations
 * - WAL mode, foreign keys, and proper pragmas
 * - Migration support with version tracking
 * - Transaction handling
 * - Connection lifecycle management
 * - Performance monitoring integration
 */

import Database from 'better-sqlite3';
import { promises as fs } from 'fs';
import path from 'path';
import { DatabaseStats, PersistenceServerConfig } from '../types/index.js';
import { MigrationRunner } from './migrations/Migration.js';
import { migrations } from './migrations/index.js';
import { QueryOptimizer } from './QueryOptimizer.js';
import { ConnectionPool, ConnectionPoolOptions } from './ConnectionPool.js';

export interface DatabaseOptions {
  /** Path to the SQLite database file */
  databasePath: string;
  /** Whether to enable WAL mode (default: true) */
  enableWAL?: boolean;
  /** Whether to enable foreign keys (default: true) */
  enableForeignKeys?: boolean;
  /** Cache size in KB (default: 2000) */
  cacheSize?: number;
  /** Whether the database is read-only (default: false) */
  readOnly?: boolean;
  /** Whether to create the database if it doesn't exist (default: true) */
  create?: boolean;
  /** Enable connection pooling (default: true) */
  enableConnectionPool?: boolean;
  /** Maximum number of connections in pool (default: 10) */
  maxConnections?: number;
  /** Minimum number of connections in pool (default: 2) */
  minConnections?: number;
  /** Enable query optimization and caching (default: true) */
  enableQueryOptimization?: boolean;
  /** Query cache TTL in milliseconds (default: 300000 = 5 minutes) */
  queryCacheTTL?: number;
}

export class DatabaseManager {
  private db: Database.Database | null = null;
  private migrationRunner: MigrationRunner | null = null;
  private connectionPool: ConnectionPool | null = null;
  private queryOptimizer: QueryOptimizer | null = null;
  private options: DatabaseOptions;
  private isInitialized = false;
  private performanceMetrics: {
    queryCount: number;
    totalQueryTime: number;
    slowQueryCount: number;
    cacheHitCount: number;
    cacheMissCount: number;
  } = {
    queryCount: 0,
    totalQueryTime: 0,
    slowQueryCount: 0,
    cacheHitCount: 0,
    cacheMissCount: 0
  };

  constructor(options: DatabaseOptions) {
    this.options = {
      enableWAL: true,
      enableForeignKeys: true,
      cacheSize: 2000,
      readOnly: false,
      create: true,
      enableConnectionPool: true,
      maxConnections: 10,
      minConnections: 2,
      enableQueryOptimization: true,
      queryCacheTTL: 300000, // 5 minutes
      ...options
    };
  }

  /**
   * Initialize the database connection and run migrations
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Ensure directory exists
      await this.ensureDirectoryExists();

      // Initialize connection pool if enabled (optional)
      if (this.options.enableConnectionPool) {
        try {
          this.connectionPool = new ConnectionPool({
            databasePath: this.options.databasePath,
            enableWAL: this.options.enableWAL,
            enableForeignKeys: this.options.enableForeignKeys,
            cacheSize: this.options.cacheSize,
            readOnly: this.options.readOnly,
            create: this.options.create,
            maxConnections: this.options.maxConnections!,
            minConnections: this.options.minConnections!,
            enableMetrics: true
          });
        } catch (error) {
          console.warn('Connection pool initialization failed, continuing with single connection:', error);
          this.connectionPool = null;
        }
      }

      // Create primary database connection for migrations and management
      this.db = new Database(this.options.databasePath, {
        readonly: this.options.readOnly,
        fileMustExist: !this.options.create
      });

      // Configure database settings
      this.configurePragmas();

      // Initialize migration runner
      this.migrationRunner = new MigrationRunner(this.db);

      // Run migrations if not read-only
      if (!this.options.readOnly) {
        await this.runMigrations();
      }

      // Initialize query optimizer after migrations
      if (this.options.enableQueryOptimization) {
        this.queryOptimizer = new QueryOptimizer(this, {
          maxCacheSize: 1000,
          defaultTTL: this.options.queryCacheTTL!
        });

        // Create optimized indexes
        await this.queryOptimizer.createOptimizedIndexes();
      }

      // Re-configure pragmas after migrations and optimizations
      this.configurePragmas();

      this.isInitialized = true;
    } catch (error) {
      if (this.db) {
        this.db.close();
        this.db = null;
      }
      if (this.connectionPool) {
        await this.connectionPool.shutdown();
        this.connectionPool = null;
      }
      throw new Error(`Failed to initialize database: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Get the database connection
   */
  getConnection(): Database.Database {
    if (!this.db) {
      throw new Error('Database connection not available. Database may not be initialized.');
    }
    if (!this.isInitialized) {
      console.warn('Warning: Database connection requested before initialization complete');
    }
    return this.db;
  }

  /**
   * Close the database connection
   */
  async close(): Promise<void> {
    // Shutdown connection pool first
    if (this.connectionPool) {
      await this.connectionPool.shutdown();
      this.connectionPool = null;
    }

    // Close primary connection
    if (this.db) {
      this.db.close();
      this.db = null;
    }

    // Clear query optimizer
    if (this.queryOptimizer) {
      this.queryOptimizer.clearCache();
      this.queryOptimizer = null;
    }

    this.isInitialized = false;
  }

  /**
   * Check if the database is initialized
   */
  isConnected(): boolean {
    return this.isInitialized && this.db !== null;
  }

  /**
   * Execute a transaction
   */
  transaction<T>(fn: (db: Database.Database) => T): T {
    const db = this.getConnection();
    const txn = db.transaction(fn);
    return txn(db);
  }

  /**
   * Execute a transaction using connection pool
   */
  async poolTransaction<T>(fn: (db: Database.Database) => Promise<T>): Promise<T> {
    if (!this.connectionPool) {
      // Fallback to regular transaction
      return new Promise((resolve, reject) => {
        try {
          const result = this.transaction((db) => {
            // Convert sync function to promise result
            return fn(db);
          });
          // If result is a promise, wait for it
          if (result instanceof Promise) {
            result.then(resolve).catch(reject);
          } else {
            resolve(result as any);
          }
        } catch (error) {
          reject(error);
        }
      });
    }

    return this.connectionPool.withTransaction(fn);
  }

  /**
   * Execute query with optimization and caching
   */
  async executeOptimized<T>(
    sql: string,
    params: any[] = [],
    options: {
      cacheKey?: string;
      ttl?: number;
      forceRefresh?: boolean;
    } = {}
  ): Promise<T> {
    const startTime = Date.now();

    try {
      let result: T;

      if (this.queryOptimizer) {
        // Use query optimizer with caching
        result = await this.queryOptimizer.executeWithCache<T>(sql, params, options);
        this.performanceMetrics.cacheHitCount++;
      } else {
        // Fallback to direct execution
        const db = this.getConnection();
        const stmt = db.prepare(sql);
        result = stmt.all(...params) as T;
        this.performanceMetrics.cacheMissCount++;
      }

      // Update performance metrics
      const duration = Date.now() - startTime;
      this.performanceMetrics.queryCount++;
      this.performanceMetrics.totalQueryTime += duration;

      if (duration > 1000) { // Queries taking more than 1 second
        this.performanceMetrics.slowQueryCount++;
      }

      return result;
    } catch (error) {
      const duration = Date.now() - startTime;
      this.performanceMetrics.queryCount++;
      this.performanceMetrics.totalQueryTime += duration;
      throw error;
    }
  }

  /**
   * Execute query using connection pool
   */
  async executePooled<T>(sql: string, params: any[] = []): Promise<T> {
    if (!this.connectionPool) {
      // Fallback to direct execution
      const db = this.getConnection();
      const stmt = db.prepare(sql);
      return stmt.all(...params) as T;
    }

    return this.connectionPool.withConnection(async (db: Database.Database) => {
      const stmt = db.prepare(sql);
      return stmt.all(...params) as T;
    });
  }

  /**
   * Get database statistics
   */
  async getStats(): Promise<DatabaseStats & {
    performance?: {
      queryCount: number;
      averageQueryTime: number;
      slowQueryCount: number;
      cacheHitRate: number;
      connectionPool?: {
        totalConnections: number;
        activeConnections: number;
        idleConnections: number;
        pendingRequests: number;
      };
    };
  }> {
    const db = this.getConnection();

    const conversationCount = db.prepare('SELECT COUNT(*) as count FROM conversations').get() as { count: number };
    const messageCount = db.prepare('SELECT COUNT(*) as count FROM messages').get() as { count: number };

    const oldestConv = db.prepare('SELECT MIN(created_at) as timestamp FROM conversations').get() as { timestamp: number | null };
    const newestConv = db.prepare('SELECT MAX(updated_at) as timestamp FROM conversations').get() as { timestamp: number | null };

    // Get database file size
    let databaseSizeBytes = 0;
    try {
      const stats = await fs.stat(this.options.databasePath);
      databaseSizeBytes = stats.size;
    } catch (error) {
      // File might not exist or be accessible
    }

    // Get last embedding index from persistence_state
    const embeddingState = db.prepare('SELECT value FROM persistence_state WHERE key = ?').get('last_embedding_index') as { value: string } | undefined;
    const lastEmbeddingIndex = embeddingState ? parseInt(embeddingState.value, 10) : undefined;

    // Calculate performance metrics
    const performance = {
      queryCount: this.performanceMetrics.queryCount,
      averageQueryTime: this.performanceMetrics.queryCount > 0 
        ? this.performanceMetrics.totalQueryTime / this.performanceMetrics.queryCount 
        : 0,
      slowQueryCount: this.performanceMetrics.slowQueryCount,
      cacheHitRate: (this.performanceMetrics.cacheHitCount + this.performanceMetrics.cacheMissCount) > 0
        ? this.performanceMetrics.cacheHitCount / (this.performanceMetrics.cacheHitCount + this.performanceMetrics.cacheMissCount)
        : 0,
      connectionPool: this.connectionPool ? this.connectionPool.getStatus() : undefined
    };

    return {
      conversationCount: conversationCount.count,
      messageCount: messageCount.count,
      databaseSizeBytes,
      oldestConversation: oldestConv.timestamp || undefined,
      newestConversation: newestConv.timestamp || undefined,
      lastEmbeddingIndex,
      performance
    };
  }

  /**
   * Optimize the database by running ANALYZE and VACUUM
   */
  async optimize(): Promise<void> {
    const db = this.getConnection();

    // Update query planner statistics
    db.prepare('ANALYZE').run();

    // Optimize FTS index
    try {
      db.prepare("INSERT INTO messages_fts(messages_fts) VALUES('optimize')").run();
    } catch (error) {
      // FTS table might not exist yet
    }

    // Run VACUUM to reclaim space (can be slow on large databases)
    db.prepare('VACUUM').run();
  }

  /**
   * Perform a checkpoint on the WAL file
   */
  checkpoint(): void {
    const db = this.getConnection();
    db.prepare('PRAGMA wal_checkpoint(TRUNCATE)').run();
  }

  /**
   * Get the current database schema version
   */
  getSchemaVersion(): number {
    if (!this.migrationRunner) {
      throw new Error('Migration runner not initialized');
    }
    return this.migrationRunner.getCurrentVersion();
  }

  /**
   * Get query optimizer instance
   */
  getQueryOptimizer(): QueryOptimizer | null {
    return this.queryOptimizer;
  }

  /**
   * Get connection pool instance
   */
  getConnectionPool(): ConnectionPool | null {
    return this.connectionPool;
  }

  /**
   * Get performance metrics
   */
  getPerformanceMetrics(): typeof this.performanceMetrics {
    return { ...this.performanceMetrics };
  }

  /**
   * Reset performance metrics
   */
  resetPerformanceMetrics(): void {
    this.performanceMetrics = {
      queryCount: 0,
      totalQueryTime: 0,
      slowQueryCount: 0,
      cacheHitCount: 0,
      cacheMissCount: 0
    };
  }

  /**
   * Get comprehensive performance report
   */
  async getPerformanceReport(): Promise<{
    database: {
      queryCount: number;
      totalQueryTime: number;
      slowQueryCount: number;
      cacheHitCount: number;
      cacheMissCount: number;
    };
    queryOptimizer?: any;
    connectionPool?: any;
  }> {
    const report = {
      database: this.getPerformanceMetrics()
    } as any;

    if (this.queryOptimizer) {
      report.queryOptimizer = this.queryOptimizer.getPerformanceReport();
    }

    if (this.connectionPool) {
      report.connectionPool = this.connectionPool.getMetrics();
    }

    return report;
  }

  /**
   * Run pending migrations
   */
  private async runMigrations(): Promise<void> {
    if (!this.migrationRunner) {
      throw new Error('Migration runner not initialized');
    }

    await this.migrationRunner.runMigrations(migrations);
  }

  /**
   * Configure database pragmas for optimal performance and safety
   */
  private configurePragmas(): void {
    if (!this.db) {
      throw new Error('Database not connected');
    }

    try {
      // Enable WAL mode for better concurrency (if enabled)
      if (this.options.enableWAL) {
        this.db.pragma('journal_mode = WAL');
      }

      // Enable foreign key constraints (if enabled)
      if (this.options.enableForeignKeys) {
        this.db.pragma('foreign_keys = ON');
      }

      // Set cache size (negative value means KB)
      this.db.pragma(`cache_size = -${this.options.cacheSize}`);

      // Set synchronous mode to NORMAL for better performance with WAL
      if (this.options.enableWAL) {
        this.db.pragma('synchronous = NORMAL');
      } else {
        this.db.pragma('synchronous = FULL');
      }

      // Set temp store to memory for better performance
      this.db.pragma('temp_store = MEMORY');

      // Set mmap size for better I/O performance (256MB)
      this.db.pragma('mmap_size = 268435456');

      // Optimize busy timeout
      this.db.pragma('busy_timeout = 10000');

    } catch (error) {
      throw new Error(`Failed to configure database pragmas: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Ensure the database directory exists
   */
  private async ensureDirectoryExists(): Promise<void> {
    const dirPath = path.dirname(this.options.databasePath);
    try {
      await fs.access(dirPath);
    } catch {
      await fs.mkdir(dirPath, { recursive: true });
    }
  }
}

/**
 * Create a database manager from server configuration
 */
export function createDatabaseManager(config: Partial<PersistenceServerConfig>): DatabaseManager {
  const options: DatabaseOptions = {
    databasePath: config.databasePath || './conversations.db',
    enableWAL: true,
    enableForeignKeys: true,
    cacheSize: 2000,
    readOnly: false,
    create: true,
    enableConnectionPool: true,
    maxConnections: 10,
    minConnections: 2,
    enableQueryOptimization: true,
    queryCacheTTL: 300000 // 5 minutes
  };

  return new DatabaseManager(options);
}

/**
 * Query Optimizer - Advanced query optimization and caching
 * 
 * Provides intelligent query optimization, result caching, and
 * performance monitoring for database operations.
 */

import Database from 'better-sqlite3';
import { DatabaseManager } from './Database.js';

interface QueryPlan {
  sql: string;
  estimatedCost: number;
  usesFTS: boolean;
  usesIndexes: string[];
  recommendations: string[];
}

interface QueryCache {
  result: any;
  timestamp: number;
  ttl: number;
  hitCount: number;
  lastAccess: number;
}

interface QueryMetrics {
  query: string;
  executionTime: number;
  resultCount: number;
  cacheHit: boolean;
  timestamp: number;
}

export class QueryOptimizer {
  private dbManager: DatabaseManager;
  private queryCache = new Map<string, QueryCache>();
  private queryMetrics: QueryMetrics[] = [];
  private maxCacheSize: number;
  private defaultTTL: number;

  constructor(dbManager: DatabaseManager, options: {
    maxCacheSize?: number;
    defaultTTL?: number;
  } = {}) {
    this.dbManager = dbManager;
    this.maxCacheSize = options.maxCacheSize || 1000;
    this.defaultTTL = options.defaultTTL || 300000; // 5 minutes
  }

  /**
   * Create optimized indexes for common query patterns
   */
  async createOptimizedIndexes(): Promise<void> {
    const db = this.dbManager.getConnection();

    const optimizedIndexes = [
      // Covering indexes for conversation queries
      {
        name: 'idx_conversations_full',
        sql: `CREATE INDEX IF NOT EXISTS idx_conversations_full 
              ON conversations(id, title, created_at, updated_at, metadata)`
      },

      // Covering index for message queries with content
      {
        name: 'idx_messages_conversation_full',
        sql: `CREATE INDEX IF NOT EXISTS idx_messages_conversation_full 
              ON messages(conversation_id, created_at, id, role, content)`
      },

      // Index for recent messages (optimized for time-based queries)
      {
        name: 'idx_messages_recent',
        sql: `CREATE INDEX IF NOT EXISTS idx_messages_recent 
              ON messages(conversation_id, created_at DESC, role)`
      },

      // Index for embedding-enabled messages
      {
        name: 'idx_messages_with_embeddings',
        sql: `CREATE INDEX IF NOT EXISTS idx_messages_with_embeddings 
              ON messages(conversation_id, created_at DESC) 
              WHERE embedding IS NOT NULL`
      },

      // Composite index for entity queries
      {
        name: 'idx_entities_search',
        sql: `CREATE INDEX IF NOT EXISTS idx_entities_search 
              ON entities(normalized_name, type, mention_count DESC)`
      },

      // Index for high-confidence entity mentions
      {
        name: 'idx_mentions_high_confidence',
        sql: `CREATE INDEX IF NOT EXISTS idx_mentions_high_confidence 
              ON entity_mentions(entity_id, conversation_id, confidence_score DESC) 
              WHERE confidence_score > 0.7`
      },

      // Index for strong entity relationships
      {
        name: 'idx_relationships_strong',
        sql: `CREATE INDEX IF NOT EXISTS idx_relationships_strong 
              ON entity_relationships(source_entity_id, strength DESC, relationship_type) 
              WHERE strength > 0.5`
      }
    ];

    for (const index of optimizedIndexes) {
      try {
        const startTime = Date.now();
        db.exec(index.sql);
        const duration = Date.now() - startTime;
        console.log(`Created index ${index.name} in ${duration}ms`);
      } catch (error) {
        console.error(`Failed to create index ${index.name}:`, error);
      }
    }
  }

  /**
   * Analyze query performance and provide recommendations
   */
  analyzeQuery(sql: string, params: any[] = []): QueryPlan {
    const db = this.dbManager.getConnection();

    try {
      // Get query plan
      const planStmt = db.prepare(`EXPLAIN QUERY PLAN ${sql}`);
      const plan = planStmt.all(...params) as any[];

      let estimatedCost = 0;
      let usesFTS = false;
      const usesIndexes: string[] = [];
      const recommendations: string[] = [];

      for (const step of plan) {
        const detail = step.detail || '';

        // Detect table scans (expensive)
        if (detail.includes('SCAN TABLE')) {
          estimatedCost += 1000;
          const tableName = detail.match(/SCAN TABLE (\w+)/)?.[1];
          if (tableName) {
            recommendations.push(`Consider adding an index for table: ${tableName}`);
          }
        }

        // Detect index usage
        if (detail.includes('USING INDEX')) {
          const indexName = detail.match(/USING INDEX (\w+)/)?.[1];
          if (indexName) {
            usesIndexes.push(indexName);
            estimatedCost += 10; // Index lookups are cheap
          }
        }

        // Detect FTS usage
        if (detail.includes('VIRTUAL TABLE') && detail.includes('fts')) {
          usesFTS = true;
          estimatedCost += 50; // FTS is moderately expensive
        }

        // Detect sorting without index
        if (detail.includes('USE TEMP B-TREE FOR ORDER BY')) {
          estimatedCost += 100;
          recommendations.push('Consider adding an index to eliminate sorting');
        }
      }

      return {
        sql,
        estimatedCost,
        usesFTS,
        usesIndexes,
        recommendations
      };
    } catch (error) {
      return {
        sql,
        estimatedCost: -1,
        usesFTS: false,
        usesIndexes: [],
        recommendations: ['Query analysis failed - check syntax']
      };
    }
  }

  /**
   * Execute query with caching and metrics
   */
  async executeWithCache<T>(
    sql: string, 
    params: any[] = [], 
    options: {
      cacheKey?: string;
      ttl?: number;
      forceRefresh?: boolean;
    } = {}
  ): Promise<T> {
    const cacheKey = options.cacheKey || this.generateCacheKey(sql, params);
    const startTime = Date.now();

    // Check cache first
    if (!options.forceRefresh) {
      const cached = this.getCached<T>(cacheKey);
      if (cached !== null) {
        this.recordMetrics(sql, Date.now() - startTime, 0, true);
        return cached;
      }
    }

    // Execute query
    const db = this.dbManager.getConnection();
    const stmt = db.prepare(sql);
    const result = stmt.all(...params) as T;

    const executionTime = Date.now() - startTime;
    const resultCount = Array.isArray(result) ? result.length : 1;

    // Cache result
    this.setCached(cacheKey, result, options.ttl);

    // Record metrics
    this.recordMetrics(sql, executionTime, resultCount, false);

    return result;
  }

  /**
   * Optimize bulk insert operations
   */
  async bulkInsert(
    tableName: string,
    columns: string[],
    data: any[][],
    options: {
      batchSize?: number;
      useTransaction?: boolean;
    } = {}
  ): Promise<void> {
    const batchSize = options.batchSize || 1000;
    const useTransaction = options.useTransaction !== false;

    const db = this.dbManager.getConnection();
    const placeholders = columns.map(() => '?').join(', ');
    const sql = `INSERT INTO ${tableName} (${columns.join(', ')}) VALUES (${placeholders})`;
    const stmt = db.prepare(sql);

    if (useTransaction) {
      const transaction = db.transaction((batch: any[][]) => {
        for (const row of batch) {
          stmt.run(...row);
        }
      });

      // Process in batches
      for (let i = 0; i < data.length; i += batchSize) {
        const batch = data.slice(i, i + batchSize);
        transaction(batch);
      }
    } else {
      // Process without transaction (faster but less safe)
      for (const row of data) {
        stmt.run(...row);
      }
    }
  }

  /**
   * Get query performance recommendations
   */
  getPerformanceReport(): {
    slowQueries: Array<{
      query: string;
      avgExecutionTime: number;
      executionCount: number;
      recommendations: string[];
    }>;
    cacheStats: {
      hitRate: number;
      totalQueries: number;
      cacheSize: number;
    };
    indexRecommendations: string[];
  } {
    // Analyze slow queries
    const queryStats = new Map<string, {
      totalTime: number;
      count: number;
      resultCounts: number[];
    }>();

    for (const metric of this.queryMetrics) {
      const key = this.normalizeQuery(metric.query);
      if (!queryStats.has(key)) {
        queryStats.set(key, { totalTime: 0, count: 0, resultCounts: [] });
      }

      const stats = queryStats.get(key)!;
      stats.totalTime += metric.executionTime;
      stats.count++;
      stats.resultCounts.push(metric.resultCount);
    }

    const slowQueries = Array.from(queryStats.entries())
      .map(([query, stats]) => ({
        query,
        avgExecutionTime: stats.totalTime / stats.count,
        executionCount: stats.count,
        recommendations: this.analyzeQuery(query).recommendations
      }))
      .filter(q => q.avgExecutionTime > 100) // Only queries taking >100ms
      .sort((a, b) => b.avgExecutionTime - a.avgExecutionTime);

    // Calculate cache stats
    const totalQueries = this.queryMetrics.length;
    const cacheHits = this.queryMetrics.filter(m => m.cacheHit).length;
    const hitRate = totalQueries > 0 ? cacheHits / totalQueries : 0;

    return {
      slowQueries: slowQueries.slice(0, 10), // Top 10 slow queries
      cacheStats: {
        hitRate,
        totalQueries,
        cacheSize: this.queryCache.size
      },
      indexRecommendations: [
        'Consider adding covering indexes for frequently accessed columns',
        'Use partial indexes for commonly filtered subsets',
        'Create composite indexes for multi-column WHERE clauses',
        'Add indexes for ORDER BY and GROUP BY clauses'
      ]
    };
  }

  /**
   * Clear query cache
   */
  clearCache(): void {
    this.queryCache.clear();
  }

  /**
   * Clear performance metrics
   */
  clearMetrics(): void {
    this.queryMetrics = [];
  }

  private generateCacheKey(sql: string, params: any[]): string {
    return `${sql}:${JSON.stringify(params)}`;
  }

  private getCached<T>(key: string): T | null {
    const cached = this.queryCache.get(key);
    if (!cached) return null;

    const now = Date.now();
    if (now - cached.timestamp > cached.ttl) {
      this.queryCache.delete(key);
      return null;
    }

    cached.hitCount++;
    cached.lastAccess = now;
    return cached.result as T;
  }

  private setCached<T>(key: string, result: T, ttl?: number): void {
    // Evict old entries if cache is full
    if (this.queryCache.size >= this.maxCacheSize) {
      this.evictOldEntries();
    }

    this.queryCache.set(key, {
      result,
      timestamp: Date.now(),
      ttl: ttl || this.defaultTTL,
      hitCount: 0,
      lastAccess: Date.now()
    });
  }

  private evictOldEntries(): void {
    // Remove 25% of least recently used entries
    const entries = Array.from(this.queryCache.entries())
      .sort((a, b) => a[1].lastAccess - b[1].lastAccess);

    const toRemove = Math.floor(entries.length * 0.25);
    for (let i = 0; i < toRemove; i++) {
      this.queryCache.delete(entries[i][0]);
    }
  }

  private recordMetrics(
    query: string,
    executionTime: number,
    resultCount: number,
    cacheHit: boolean
  ): void {
    this.queryMetrics.push({
      query: this.normalizeQuery(query),
      executionTime,
      resultCount,
      cacheHit,
      timestamp: Date.now()
    });

    // Keep only last 10000 metrics to prevent memory bloat
    if (this.queryMetrics.length > 10000) {
      this.queryMetrics = this.queryMetrics.slice(-5000);
    }
  }

  private normalizeQuery(sql: string): string {
    return sql
      .replace(/\s+/g, ' ')
      .replace(/\?/g, '?')
      .trim()
      .toLowerCase();
  }
}

/**
 * Migration Test Script
 * 
 * This script tests the enhanced search migration to ensure:
 * - Migration applies successfully
 * - All new tables and indexes are created
 * - FTS triggers work correctly
 * - Rollback works without data corruption
 * - Performance is acceptable
 */

import Database from 'better-sqlite3';
import { MigrationRunner } from './Migration.js';
import { migrations } from './index.js';
import { enhancedSearchMigration } from './002_enhanced_search.js';

interface TestResult {
  test: string;
  passed: boolean;
  error?: string;
  duration?: number;
}

class MigrationTest {
  private db: Database.Database;
  private migrationRunner: MigrationRunner;
  private results: TestResult[] = [];

  constructor() {
    // Use in-memory database for testing
    this.db = new Database(':memory:');
    this.migrationRunner = new MigrationRunner(this.db);
  }

  async runAllTests(): Promise<TestResult[]> {
    console.log('Starting Enhanced Search Migration Tests...\n');

    try {
      await this.testInitialState();
      await this.testMigrationApplication();
      await this.testNewTablesCreated();
      await this.testFTSEnhancements();
      await this.testIndexCreation();
      await this.testTriggerFunctionality();
      await this.testRollback();
      await this.testDataIntegrity();

      console.log('\n=== TEST SUMMARY ===');
      const passed = this.results.filter(r => r.passed).length;
      const total = this.results.length;
      console.log(`Passed: ${passed}/${total}`);

      if (passed < total) {
        console.log('\nFailed tests:');
        this.results.filter(r => !r.passed).forEach(r => {
          console.log(`  - ${r.test}: ${r.error}`);
        });
      }

    } catch (error) {
      console.error('Test suite failed:', error);
    }

    return this.results;
  }

  private async testInitialState(): Promise<void> {
    const test = 'Initial state verification';
    const startTime = Date.now();

    try {
      // Run initial migration first
      await this.migrationRunner.runMigrations([migrations[0]]);

      const version = this.migrationRunner.getCurrentVersion();
      if (version !== 1) {
        throw new Error(`Expected version 1, got ${version}`);
      }

      // Verify initial schema
      const tables = this.getTableNames();
      const requiredTables = ['conversations', 'messages', 'messages_fts', 'persistence_state'];

      for (const table of requiredTables) {
        if (!tables.includes(table)) {
          throw new Error(`Required table ${table} not found`);
        }
      }

      this.addResult(test, true, undefined, Date.now() - startTime);
    } catch (error) {
      this.addResult(test, false, error instanceof Error ? error.message : String(error));
    }
  }

  private async testMigrationApplication(): Promise<void> {
    const test = 'Enhanced search migration application';
    const startTime = Date.now();

    try {
      // Apply the enhanced search migration
      await this.migrationRunner.runMigrations([enhancedSearchMigration]);

      const version = this.migrationRunner.getCurrentVersion();
      if (version !== 2) {
        throw new Error(`Expected version 2, got ${version}`);
      }

      this.addResult(test, true, undefined, Date.now() - startTime);
    } catch (error) {
      this.addResult(test, false, error instanceof Error ? error.message : String(error));
    }
  }

  private async testNewTablesCreated(): Promise<void> {
    const test = 'New tables creation verification';
    const startTime = Date.now();

    try {
      const tables = this.getTableNames();
      const newTables = ['search_config', 'search_metrics'];

      for (const table of newTables) {
        if (!tables.includes(table)) {
          throw new Error(`New table ${table} not created`);
        }
      }

      // Test search_config has default values
      const configCount = this.db.prepare('SELECT COUNT(*) as count FROM search_config').get() as { count: number };
      if (configCount.count === 0) {
        throw new Error('search_config table is empty - default values not inserted');
      }

      // Verify specific config values
      const embeddingModel = this.db.prepare('SELECT value FROM search_config WHERE key = ?').get('embedding_model') as { value: string } | undefined;
      if (!embeddingModel || !embeddingModel.value.includes('all-MiniLM-L6-v2')) {
        throw new Error('embedding_model config not set correctly');
      }

      this.addResult(test, true, undefined, Date.now() - startTime);
    } catch (error) {
      this.addResult(test, false, error instanceof Error ? error.message : String(error));
    }
  }

  private async testFTSEnhancements(): Promise<void> {
    const test = 'FTS enhancements verification';
    const startTime = Date.now();

    try {
      // Test FTS table exists with new configuration
      const ftsInfo = this.db.prepare("SELECT sql FROM sqlite_master WHERE name = 'messages_fts'").get() as { sql: string } | undefined;

      if (!ftsInfo) {
        throw new Error('messages_fts table not found');
      }

      // Check for enhanced tokenizer configuration
      if (!ftsInfo.sql.includes('porter unicode61') || !ftsInfo.sql.includes('remove_diacritics')) {
        throw new Error('Enhanced FTS tokenizer not configured correctly');
      }

      this.addResult(test, true, undefined, Date.now() - startTime);
    } catch (error) {
      this.addResult(test, false, error instanceof Error ? error.message : String(error));
    }
  }

  private async testIndexCreation(): Promise<void> {
    const test = 'Enhanced indexes creation';
    const startTime = Date.now();

    try {
      const indexes = this.getIndexNames();
      const newIndexes = [
        'idx_messages_embedding',
        'idx_messages_embedding_time', 
        'idx_messages_role_embedding',
        'idx_search_metrics_time',
        'idx_search_metrics_type'
      ];

      for (const index of newIndexes) {
        if (!indexes.includes(index)) {
          throw new Error(`New index ${index} not created`);
        }
      }

      this.addResult(test, true, undefined, Date.now() - startTime);
    } catch (error) {
      this.addResult(test, false, error instanceof Error ? error.message : String(error));
    }
  }

  private async testTriggerFunctionality(): Promise<void> {
    const test = 'FTS triggers functionality';
    const startTime = Date.now();

    try {
      // Insert a test message
      const conversationId = 'test-conv-001';
      const messageId = 'test-msg-001';
      const content = 'This is a test message for FTS indexing';

      // Create test conversation
      this.db.prepare(`
        INSERT INTO conversations (id, created_at, updated_at, title, metadata)
        VALUES (?, ?, ?, ?, ?)
      `).run(conversationId, Date.now(), Date.now(), 'Test Conversation', '{}');

      // Insert test message
      this.db.prepare(`
        INSERT INTO messages (id, conversation_id, role, content, created_at, metadata)
        VALUES (?, ?, ?, ?, ?, ?)
      `).run(messageId, conversationId, 'user', content, Date.now(), '{}');

      // Check if FTS was updated automatically
      const ftsResult = this.db.prepare("SELECT content FROM messages_fts WHERE content MATCH 'test'").get() as { content: string } | undefined;

      if (!ftsResult) {
        throw new Error('FTS trigger did not automatically index new message');
      }

      // Test update trigger
      const newContent = 'Updated test message content for verification';
      this.db.prepare('UPDATE messages SET content = ? WHERE id = ?').run(newContent, messageId);

      const updatedFtsResult = this.db.prepare("SELECT content FROM messages_fts WHERE content MATCH 'verification'").get() as { content: string } | undefined;

      if (!updatedFtsResult) {
        throw new Error('FTS update trigger did not work correctly');
      }

      this.addResult(test, true, undefined, Date.now() - startTime);
    } catch (error) {
      this.addResult(test, false, error instanceof Error ? error.message : String(error));
    }
  }

  private async testRollback(): Promise<void> {
    const test = 'Migration rollback functionality';
    const startTime = Date.now();

    try {
      // Rollback to version 1
      await this.migrationRunner.rollbackToVersion(1, migrations);

      const version = this.migrationRunner.getCurrentVersion();
      if (version !== 1) {
        throw new Error(`Expected version 1 after rollback, got ${version}`);
      }

      // Verify enhancement tables are gone
      const tables = this.getTableNames();
      const enhancementTables = ['search_config', 'search_metrics'];

      for (const table of enhancementTables) {
        if (tables.includes(table)) {
          throw new Error(`Table ${table} should be dropped after rollback`);
        }
      }

      // Verify original schema still works
      const originalTables = ['conversations', 'messages', 'messages_fts', 'persistence_state'];
      for (const table of originalTables) {
        if (!tables.includes(table)) {
          throw new Error(`Original table ${table} missing after rollback`);
        }
      }

      this.addResult(test, true, undefined, Date.now() - startTime);
    } catch (error) {
      this.addResult(test, false, error instanceof Error ? error.message : String(error));
    }
  }

  private async testDataIntegrity(): Promise<void> {
    const test = 'Data integrity verification';
    const startTime = Date.now();

    try {
      // Check that our test data still exists after rollback
      const messageCount = this.db.prepare('SELECT COUNT(*) as count FROM messages').get() as { count: number };

      if (messageCount.count === 0) {
        throw new Error('Messages lost during migration/rollback process');
      }

      // Check that FTS still works with original configuration
      const ftsResult = this.db.prepare("SELECT content FROM messages_fts WHERE content MATCH 'Updated'").get() as { content: string } | undefined;

      if (!ftsResult) {
        throw new Error('FTS functionality broken after rollback');
      }

      this.addResult(test, true, undefined, Date.now() - startTime);
    } catch (error) {
      this.addResult(test, false, error instanceof Error ? error.message : String(error));
    }
  }

  private getTableNames(): string[] {
    const tables = this.db.prepare(`
      SELECT name FROM sqlite_master 
      WHERE type = 'table' AND name NOT LIKE 'sqlite_%'
    `).all() as { name: string }[];

    return tables.map(t => t.name);
  }

  private getIndexNames(): string[] {
    const indexes = this.db.prepare(`
      SELECT name FROM sqlite_master 
      WHERE type = 'index' AND name NOT LIKE 'sqlite_%'
    `).all() as { name: string }[];

    return indexes.map(i => i.name);
  }

  private addResult(test: string, passed: boolean, error?: string, duration?: number): void {
    this.results.push({ test, passed, error, duration });
    const status = passed ? ' PASS' : ' FAIL';
    const time = duration ? ` (${duration}ms)` : '';
    console.log(`${status}: ${test}${time}`);
    if (error) {
      console.log(`   Error: ${error}`);
    }
  }

  close(): void {
    this.db.close();
  }
}

