/**
 * Base Repository - Abstract base class with common patterns
 * 
 * This abstract class provides:
 * - Common database connection access
 * - Transaction handling utilities
 * - Error handling patterns
 * - Prepared statement management
 * - Generic CRUD operation patterns
 */

import Database from 'better-sqlite3';
import { DatabaseManager } from '../Database.js';

/**
 * Base repository class with common database operations
 */
export abstract class BaseRepository {
  protected db: DatabaseManager;
  protected preparedStatements: Map<string, Database.Statement> = new Map();

  constructor(databaseManager: DatabaseManager) {
    this.db = databaseManager;
  }

  /**
   * Get the database connection
   */
  protected getConnection(): Database.Database {
    return this.db.getConnection();
  }

  /**
   * Execute a transaction with proper error handling
   */
  protected transaction<T>(fn: (db: Database.Database) => T): T {
    return this.db.transaction(fn);
  }

  /**
   * Get or create a prepared statement for reuse
   */
  protected prepare(key: string, sql: string): Database.Statement {
    if (!this.preparedStatements.has(key)) {
      const connection = this.getConnection();
      this.preparedStatements.set(key, connection.prepare(sql));
    }
    return this.preparedStatements.get(key)!;
  }

  /**
   * Execute a prepared statement with error handling
   */
  protected executeStatement<T = any>(
    key: string, 
    sql: string, 
    params?: any
  ): T {
    try {
      const stmt = this.prepare(key, sql);
      return params ? stmt.get(params) as T : stmt.get() as T;
    } catch (error) {
      throw new Error(`Database query failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Execute a prepared statement that returns all rows
   */
  protected executeStatementAll<T = any>(
    key: string, 
    sql: string, 
    params?: any
  ): T[] {
    try {
      const stmt = this.prepare(key, sql);
      return params ? stmt.all(params) as T[] : stmt.all() as T[];
    } catch (error) {
      throw new Error(`Database query failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Execute a prepared statement that modifies data
   */
  protected executeStatementRun(
    key: string, 
    sql: string, 
    params?: any
  ): Database.RunResult {
    try {
      const stmt = this.prepare(key, sql);
      return params ? stmt.run(params) : stmt.run();
    } catch (error) {
      throw new Error(`Database operation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Generate a UUID v4
   */
  protected generateId(): string {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
      const r = Math.random() * 16 | 0;
      const v = c === 'x' ? r : (r & 0x3 | 0x8);
      return v.toString(16);
    });
  }

  /**
   * Get current timestamp in milliseconds
   */
  protected getCurrentTimestamp(): number {
    return Date.now();
  }

  /**
   * Validate that a string is a valid UUID
   */
  protected isValidUUID(uuid: string): boolean {
    const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
    return uuidRegex.test(uuid);
  }

  /**
   * Parse JSON metadata safely
   */
  protected parseMetadata(metadataJson?: string): Record<string, any> {
    if (!metadataJson) {
      return {};
    }
    try {
      return JSON.parse(metadataJson);
    } catch (error) {
      return {};
    }
  }

  /**
   * Stringify metadata safely
   */
  protected stringifyMetadata(metadata?: Record<string, any>): string {
    if (!metadata || Object.keys(metadata).length === 0) {
      return '{}';
    }
    try {
      return JSON.stringify(metadata);
    } catch (error) {
      return '{}';
    }
  }

  /**
   * Validate pagination parameters
   */
  protected validatePagination(limit?: number, offset?: number): { limit: number; offset: number } {
    // Handle invalid limit values - use 1 for 0 or negative values, 50 for undefined/null
    let validatedLimit: number;
    if (limit === undefined || limit === null) {
      validatedLimit = 50; // Default
    } else if (limit <= 0) {
      validatedLimit = 1; // Minimum for invalid values
    } else {
      validatedLimit = Math.min(limit, 1000); // Cap at maximum
    }

    const validatedOffset = Math.max(offset || 0, 0); // Non-negative
    return { limit: validatedLimit, offset: validatedOffset };
  }

  /**
   * Handle database constraint violations
   */
  protected handleConstraintError(error: Error, entityType: string): never {
    const message = error.message.toLowerCase();

    if (message.includes('unique constraint')) {
      throw new Error(`${entityType} already exists`);
    }

    if (message.includes('foreign key constraint')) {
      throw new Error(`Referenced ${entityType} does not exist`);
    }

    if (message.includes('check constraint')) {
      throw new Error(`Invalid ${entityType} data`);
    }

    // Re-throw original error if not a known constraint violation
    throw error;
  }

  /**
   * Clean up prepared statements
   */
  public cleanup(): void {
    this.preparedStatements.clear();
  }
}

/**
 * Conversation Repository - CRUD operations for conversations
 * 
 * This repository provides:
 * - Full CRUD operations for conversations
 * - Transaction support for complex operations
 * - Cascade operations (deleting conversation deletes messages)
 * - Statistics and metadata management
 * - Pagination support
 */

import { Conversation, PaginatedResult } from '../../types/interfaces.js';
import { BaseRepository } from './BaseRepository.js';

/**
 * Interface for conversation creation parameters
 */
export interface CreateConversationParams {
  id?: string;
  title?: string;
  metadata?: Record<string, any>;
}

/**
 * Interface for conversation update parameters
 */
export interface UpdateConversationParams {
  title?: string;
  metadata?: Record<string, any>;
}

/**
 * Interface for conversation statistics
 */
export interface ConversationStats {
  messageCount: number;
  firstMessageAt?: number;
  lastMessageAt?: number;
  participantRoles: string[];
}

/**
 * Repository for conversation CRUD operations
 */
export class ConversationRepository extends BaseRepository {

  /**
   * Create a new conversation
   */
  async create(params: CreateConversationParams): Promise<Conversation> {
    const id = params.id || this.generateId();
    const now = this.getCurrentTimestamp();

    const conversation: Conversation = {
      id,
      createdAt: now,
      updatedAt: now,
      title: params.title,
      metadata: params.metadata || {}
    };

    try {
      this.executeStatementRun(
        'insert_conversation',
        `INSERT INTO conversations (id, created_at, updated_at, title, metadata)
         VALUES (?, ?, ?, ?, ?)`,
        [
          conversation.id,
          conversation.createdAt,
          conversation.updatedAt,
          conversation.title || null,
          this.stringifyMetadata(conversation.metadata)
        ]
      );

      return conversation;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Conversation');
    }
  }

  /**
   * Find a conversation by ID
   */
  async findById(id: string): Promise<Conversation | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const row = this.executeStatement<{
      id: string;
      created_at: number;
      updated_at: number;
      title: string | null;
      metadata: string;
    }>(
      'find_conversation_by_id',
      `SELECT id, created_at, updated_at, title, metadata
       FROM conversations
       WHERE id = ?`,
      [id]
    );

    if (!row) {
      return null;
    }

    return {
      id: row.id,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      title: row.title || undefined,
      metadata: this.parseMetadata(row.metadata)
    };
  }

  /**
   * Find all conversations with pagination
   */
  async findAll(
    limit?: number,
    offset?: number,
    orderBy: 'created_at' | 'updated_at' = 'updated_at',
    orderDir: 'ASC' | 'DESC' = 'DESC'
  ): Promise<PaginatedResult<Conversation>> {
    const pagination = this.validatePagination(limit, offset);

    // Validate orderBy parameter to prevent SQL injection
    if (orderBy !== 'created_at' && orderBy !== 'updated_at') {
      throw new Error('Invalid orderBy parameter');
    }

    if (orderDir !== 'ASC' && orderDir !== 'DESC') {
      throw new Error('Invalid orderDir parameter');
    }

    const rows = this.executeStatementAll<{
      id: string;
      created_at: number;
      updated_at: number;
      title: string | null;
      metadata: string;
    }>(
      `find_all_conversations_${orderBy}_${orderDir}`,
      `SELECT id, created_at, updated_at, title, metadata
       FROM conversations
       ORDER BY ${orderBy} ${orderDir}
       LIMIT ? OFFSET ?`,
      [pagination.limit + 1, pagination.offset] // Get one extra to check if there are more
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => ({
      id: row.id,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      title: row.title || undefined,
      metadata: this.parseMetadata(row.metadata)
    }));

    return {
      data,
      hasMore,
      totalCount: undefined // We don't calculate total count for performance reasons
    };
  }

  /**
   * Update a conversation
   */
  async update(id: string, params: UpdateConversationParams): Promise<Conversation | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const existing = await this.findById(id);
    if (!existing) {
      return null;
    }

    const now = this.getCurrentTimestamp();
    const updatedConversation: Conversation = {
      ...existing,
      updatedAt: now,
      title: params.title !== undefined ? params.title : existing.title,
      metadata: params.metadata !== undefined ? params.metadata : existing.metadata
    };

    try {
      const result = this.executeStatementRun(
        'update_conversation',
        `UPDATE conversations 
         SET updated_at = ?, title = ?, metadata = ?
         WHERE id = ?`,
        [
          updatedConversation.updatedAt,
          updatedConversation.title || null,
          this.stringifyMetadata(updatedConversation.metadata),
          id
        ]
      );

      if (result.changes === 0) {
        return null;
      }

      return updatedConversation;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Conversation');
    }
  }

  /**
   * Delete a conversation and all its messages (cascade)
   */
  async delete(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    return this.transaction((db) => {
      // First, delete all messages in the conversation
      // This will automatically update the FTS index via triggers
      const deleteMessagesStmt = db.prepare(
        'DELETE FROM messages WHERE conversation_id = ?'
      );
      deleteMessagesStmt.run(id);

      // Then delete the conversation
      const deleteConversationStmt = db.prepare(
        'DELETE FROM conversations WHERE id = ?'
      );
      const result = deleteConversationStmt.run(id);

      return result.changes > 0;
    });
  }

  /**
   * Get conversation statistics
   */
  async getStats(id: string): Promise<ConversationStats | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    // Check if conversation exists
    const conversation = await this.findById(id);
    if (!conversation) {
      return null;
    }

    const stats = this.executeStatement<{
      message_count: number;
      first_message_at: number | null;
      last_message_at: number | null;
    }>(
      'get_conversation_stats',
      `SELECT 
         COUNT(*) as message_count,
         MIN(created_at) as first_message_at,
         MAX(created_at) as last_message_at
       FROM messages
       WHERE conversation_id = ?`,
      [id]
    );

    // Get distinct roles
    const roles = this.executeStatementAll<{ role: string }>(
      'get_conversation_roles',
      `SELECT DISTINCT role
       FROM messages
       WHERE conversation_id = ?
       ORDER BY role`,
      [id]
    );

    return {
      messageCount: stats.message_count,
      firstMessageAt: stats.first_message_at || undefined,
      lastMessageAt: stats.last_message_at || undefined,
      participantRoles: roles.map(r => r.role)
    };
  }

  /**
   * Find conversations by title (fuzzy search)
   */
  async findByTitle(
    titleQuery: string,
    limit?: number,
    offset?: number
  ): Promise<PaginatedResult<Conversation>> {
    const pagination = this.validatePagination(limit, offset);
    const searchPattern = `%${titleQuery}%`;

    const rows = this.executeStatementAll<{
      id: string;
      created_at: number;
      updated_at: number;
      title: string | null;
      metadata: string;
    }>(
      'find_conversations_by_title',
      `SELECT id, created_at, updated_at, title, metadata
       FROM conversations
       WHERE title IS NOT NULL AND title LIKE ?
       ORDER BY updated_at DESC
       LIMIT ? OFFSET ?`,
      [searchPattern, pagination.limit + 1, pagination.offset]
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => ({
      id: row.id,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      title: row.title || undefined,
      metadata: this.parseMetadata(row.metadata)
    }));

    return {
      data,
      hasMore
    };
  }

  /**
   * Get conversations created within a date range
   */
  async findByDateRange(
    startDate: number,
    endDate: number,
    limit?: number,
    offset?: number
  ): Promise<PaginatedResult<Conversation>> {
    const pagination = this.validatePagination(limit, offset);

    const rows = this.executeStatementAll<{
      id: string;
      created_at: number;
      updated_at: number;
      title: string | null;
      metadata: string;
    }>(
      'find_conversations_by_date_range',
      `SELECT id, created_at, updated_at, title, metadata
       FROM conversations
       WHERE created_at >= ? AND created_at <= ?
       ORDER BY created_at DESC
       LIMIT ? OFFSET ?`,
      [startDate, endDate, pagination.limit + 1, pagination.offset]
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => ({
      id: row.id,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      title: row.title || undefined,
      metadata: this.parseMetadata(row.metadata)
    }));

    return {
      data,
      hasMore
    };
  }

  /**
   * Count total conversations
   */
  async count(): Promise<number> {
    const result = this.executeStatement<{ count: number }>(
      'count_conversations',
      'SELECT COUNT(*) as count FROM conversations'
    );
    return result.count;
  }

  /**
   * Check if a conversation exists
   */
  async exists(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatement<{ count: number }>(
      'conversation_exists',
      'SELECT COUNT(*) as count FROM conversations WHERE id = ? LIMIT 1',
      [id]
    );
    return result ? result.count > 0 : false;
  }

  /**
   * Update the timestamp of a conversation to mark it as recently active
   */
  async updateTimestamp(id: string): Promise<void> {
    if (!this.isValidUUID(id)) {
      return;
    }

    const now = this.getCurrentTimestamp();
    this.executeStatementRun(
      'update_conversation_timestamp',
      'UPDATE conversations SET updated_at = ? WHERE id = ?',
      [now, id]
    );
  }

  /**
   * Find the oldest conversation based on filters
   */
  async findOldest(filters?: { startDate?: string; endDate?: string }): Promise<Conversation | null> {
    let whereClause = '';
    const params: any[] = [];

    if (filters?.startDate) {
      const startTimestamp = new Date(filters.startDate).getTime();
      whereClause += 'WHERE created_at >= ?';
      params.push(startTimestamp);
    }

    if (filters?.endDate) {
      const endTimestamp = new Date(filters.endDate).getTime();
      whereClause += whereClause ? ' AND created_at <= ?' : 'WHERE created_at <= ?';
      params.push(endTimestamp);
    }

    const row = this.executeStatement<{
      id: string;
      created_at: number;
      updated_at: number;
      title: string | null;
      metadata: string;
    }>(
      'find_oldest_conversation',
      `SELECT id, created_at, updated_at, title, metadata
       FROM conversations
       ${whereClause}
       ORDER BY created_at ASC
       LIMIT 1`,
      params
    );

    if (!row) {
      return null;
    }

    return {
      id: row.id,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      title: row.title || undefined,
      metadata: this.parseMetadata(row.metadata)
    };
  }

  /**
   * Find the newest conversation based on filters
   */
  async findNewest(filters?: { startDate?: string; endDate?: string }): Promise<Conversation | null> {
    let whereClause = '';
    const params: any[] = [];

    if (filters?.startDate) {
      const startTimestamp = new Date(filters.startDate).getTime();
      whereClause += 'WHERE created_at >= ?';
      params.push(startTimestamp);
    }

    if (filters?.endDate) {
      const endTimestamp = new Date(filters.endDate).getTime();
      whereClause += whereClause ? ' AND created_at <= ?' : 'WHERE created_at <= ?';
      params.push(endTimestamp);
    }

    const row = this.executeStatement<{
      id: string;
      created_at: number;
      updated_at: number;
      title: string | null;
      metadata: string;
    }>(
      'find_newest_conversation',
      `SELECT id, created_at, updated_at, title, metadata
       FROM conversations
       ${whereClause}
       ORDER BY created_at DESC
       LIMIT 1`,
      params
    );

    if (!row) {
      return null;
    }

    return {
      id: row.id,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      title: row.title || undefined,
      metadata: this.parseMetadata(row.metadata)
    };
  }

  /**
   * Get conversations with pagination and optional filters
   */
  getConversations(options: {
    limit?: number;
    offset?: number;
    startDate?: Date;
    endDate?: Date;
  } = {}): PaginatedResult<Conversation> {
    const pagination = this.validatePagination(options.limit, options.offset);
    let whereClause = '';
    const params: any[] = [];

    if (options.startDate) {
      whereClause += 'WHERE created_at >= ?';
      params.push(options.startDate.getTime());
    }

    if (options.endDate) {
      whereClause += whereClause ? ' AND created_at <= ?' : 'WHERE created_at <= ?';
      params.push(options.endDate.getTime());
    }

    const rows = this.executeStatementAll<{
      id: string;
      created_at: number;
      updated_at: number;
      title: string | null;
      metadata: string;
    }>(
      'get_conversations_paginated',
      `SELECT id, created_at, updated_at, title, metadata
       FROM conversations
       ${whereClause}
       ORDER BY updated_at DESC
       LIMIT ? OFFSET ?`,
      [...params, pagination.limit, pagination.offset]
    );

    const conversations: Conversation[] = rows.map(row => ({
      id: row.id,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      title: row.title || undefined,
      metadata: this.parseMetadata(row.metadata)
    }));

    return {
      data: conversations,
      hasMore: conversations.length === pagination.limit,
      totalCount: undefined
    };
  }

}

import { BaseRepository } from './BaseRepository.js';

export interface Entity {
  id: string;
  name: string;
  normalizedName: string;
  type: EntityType;
  canonicalForm?: string;
  confidenceScore: number;
  createdAt: number;
  updatedAt: number;
  metadata: Record<string, any>;
  mentionCount: number;
  lastMentionedAt?: number;
}

export type EntityType = 
  | 'person' 
  | 'organization' 
  | 'product' 
  | 'concept' 
  | 'location' 
  | 'technical' 
  | 'event' 
  | 'decision';

export interface CreateEntityInput {
  name: string;
  type: EntityType;
  canonicalForm?: string;
  confidenceScore?: number;
  metadata?: Record<string, any>;
}

export interface UpdateEntityInput {
  name?: string;
  canonicalForm?: string;
  confidenceScore?: number;
  metadata?: Record<string, any>;
}

export interface EntitySearchOptions {
  query?: string;
  type?: EntityType;
  minConfidence?: number;
  minMentions?: number;
  limit?: number;
  offset?: number;
  orderBy?: 'name' | 'mentions' | 'updated' | 'confidence';
  orderDirection?: 'ASC' | 'DESC';
}

export class EntityRepository extends BaseRepository {
  /**
   * Create a new entity
   */
  async create(input: CreateEntityInput): Promise<Entity> {
    const id = this.generateId();
    const now = this.getCurrentTimestamp();
    const normalizedName = this.normalizeName(input.name);

    const entity: Entity = {
      id,
      name: input.name,
      normalizedName,
      type: input.type,
      canonicalForm: input.canonicalForm,
      confidenceScore: input.confidenceScore ?? 1.0,
      createdAt: now,
      updatedAt: now,
      metadata: input.metadata ?? {},
      mentionCount: 0,
      lastMentionedAt: undefined
    };

    try {
      this.executeStatementRun(
        'create_entity',
        `INSERT INTO entities (
          id, name, normalized_name, type, canonical_form,
          confidence_score, created_at, updated_at, metadata,
          mention_count, last_mentioned_at
        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)`,
        [
          entity.id,
          entity.name,
          entity.normalizedName,
          entity.type,
          entity.canonicalForm || null,
          entity.confidenceScore,
          entity.createdAt,
          entity.updatedAt,
          this.stringifyMetadata(entity.metadata),
          entity.mentionCount,
          entity.lastMentionedAt || null
        ]
      );

      return entity;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Entity');
    }
  }

  /**
   * Get entity by ID
   */
  async getById(id: string): Promise<Entity | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const row = this.executeStatement<{
      id: string;
      name: string;
      normalized_name: string;
      type: string;
      canonical_form: string | null;
      confidence_score: number;
      created_at: number;
      updated_at: number;
      metadata: string;
      mention_count: number;
      last_mentioned_at: number | null;
    }>(
      'find_entity_by_id',
      `SELECT * FROM entities WHERE id = ?`,
      [id]
    );

    return row ? this.mapRowToEntity(row) : null;
  }

  /**
   * Find entity by normalized name and type
   */
  async findByNormalizedName(normalizedName: string, type?: EntityType): Promise<Entity | null> {
    let sql = 'SELECT * FROM entities WHERE normalized_name = ?';
    const params: any[] = [normalizedName];

    if (type) {
      sql += ' AND type = ?';
      params.push(type);
    }

    sql += ' ORDER BY confidence_score DESC LIMIT 1';

    const row = this.executeStatement<{
      id: string;
      name: string;
      normalized_name: string;
      type: string;
      canonical_form: string | null;
      confidence_score: number;
      created_at: number;
      updated_at: number;
      metadata: string;
      mention_count: number;
      last_mentioned_at: number | null;
    }>(
      `find_entity_by_name_${type || 'any'}`,
      sql,
      params
    );

    return row ? this.mapRowToEntity(row) : null;
  }

  /**
   * Update an entity
   */
  async update(id: string, input: UpdateEntityInput): Promise<Entity | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const existing = await this.getById(id);
    if (!existing) {
      return null;
    }

    const updates: string[] = [];
    const params: any[] = [];

    if (input.name !== undefined) {
      updates.push('name = ?');
      params.push(input.name);
      updates.push('normalized_name = ?');
      params.push(this.normalizeName(input.name));
    }

    if (input.canonicalForm !== undefined) {
      updates.push('canonical_form = ?');
      params.push(input.canonicalForm);
    }

    if (input.confidenceScore !== undefined) {
      updates.push('confidence_score = ?');
      params.push(input.confidenceScore);
    }

    if (input.metadata !== undefined) {
      updates.push('metadata = ?');
      params.push(this.stringifyMetadata(input.metadata));
    }

    updates.push('updated_at = ?');
    params.push(this.getCurrentTimestamp());

    params.push(id);

    try {
      this.executeStatementRun(
        'update_entity',
        `UPDATE entities SET ${updates.join(', ')} WHERE id = ?`,
        params
      );

      return this.getById(id);
    } catch (error) {
      this.handleConstraintError(error as Error, 'Entity');
    }
  }

  /**
   * Search entities with various filters
   */
  async search(options: EntitySearchOptions = {}): Promise<{
    entities: Entity[];
    total: number;
    hasMore: boolean;
  }> {
    const limit = options.limit ?? 20;
    const offset = options.offset ?? 0;

    const whereConditions: string[] = [];
    const params: any[] = [];

    if (options.query) {
      // Use FTS5 for text search
      whereConditions.push(`
        id IN (
          SELECT e.id FROM entities e
          JOIN entities_fts ON entities_fts.rowid = e.rowid
          WHERE entities_fts MATCH ?
        )
      `);
      params.push(options.query);
    }

    if (options.type) {
      whereConditions.push('type = ?');
      params.push(options.type);
    }

    if (options.minConfidence !== undefined) {
      whereConditions.push('confidence_score >= ?');
      params.push(options.minConfidence);
    }

    if (options.minMentions !== undefined) {
      whereConditions.push('mention_count >= ?');
      params.push(options.minMentions);
    }

    const whereClause = whereConditions.length > 0 
      ? `WHERE ${whereConditions.join(' AND ')}` 
      : '';

    // Count total results
    const { count } = this.executeStatement<{ count: number }>(
      'count_entities_search',
      `SELECT COUNT(*) as count FROM entities ${whereClause}`,
      params
    );

    // Get paginated results
    const orderBy = this.getOrderByClause(options.orderBy, options.orderDirection);
    const rows = this.executeStatementAll<{
      id: string;
      name: string;
      normalized_name: string;
      type: string;
      canonical_form: string | null;
      confidence_score: number;
      created_at: number;
      updated_at: number;
      metadata: string;
      mention_count: number;
      last_mentioned_at: number | null;
    }>(
      'search_entities',
      `SELECT * FROM entities ${whereClause} ${orderBy} LIMIT ? OFFSET ?`,
      [...params, limit, offset]
    );

    const entities = rows.map(row => this.mapRowToEntity(row));

    return {
      entities,
      total: count,
      hasMore: offset + limit < count
    };
  }

  /**
   * Delete an entity and all related data
   */
  async delete(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    try {
      const result = this.executeStatementRun(
        'delete_entity',
        'DELETE FROM entities WHERE id = ?',
        [id]
      );
      return result.changes > 0;
    } catch (error) {
      console.error('Failed to delete entity:', error);
      return false;
    }
  }

  /**
   * Get most mentioned entities
   */
  async getMostMentioned(limit: number = 10, type?: EntityType): Promise<Entity[]> {
    let sql = 'SELECT * FROM entities';
    const params: any[] = [];

    if (type) {
      sql += ' WHERE type = ?';
      params.push(type);
    }

    sql += ' ORDER BY mention_count DESC, last_mentioned_at DESC LIMIT ?';
    params.push(limit);

    const rows = this.executeStatementAll<{
      id: string;
      name: string;
      normalized_name: string;
      type: string;
      canonical_form: string | null;
      confidence_score: number;
      created_at: number;
      updated_at: number;
      metadata: string;
      mention_count: number;
      last_mentioned_at: number | null;
    }>(
      `most_mentioned_${type || 'all'}`,
      sql,
      params
    );

    return rows.map(row => this.mapRowToEntity(row));
  }

  /**
   * Normalize entity name for consistent matching
   */
  private normalizeName(name: string): string {
    return name
      .toLowerCase()
      .trim()
      .replace(/[^\w\s-]/g, '') // Remove special characters
      .replace(/\s+/g, ' '); // Normalize whitespace
  }

  /**
   * Map database row to Entity object
   */
  private mapRowToEntity(row: any): Entity {
    return {
      id: row.id,
      name: row.name,
      normalizedName: row.normalized_name,
      type: row.type as EntityType,
      canonicalForm: row.canonical_form,
      confidenceScore: row.confidence_score,
      createdAt: row.created_at,
      updatedAt: row.updated_at,
      metadata: this.parseMetadata(row.metadata),
      mentionCount: row.mention_count,
      lastMentionedAt: row.last_mentioned_at
    };
  }

  /**
   * Get ORDER BY clause based on options
   */
  private getOrderByClause(
    orderBy?: string,
    direction: 'ASC' | 'DESC' = 'DESC'
  ): string {
    const column = orderBy === 'name' ? 'name'
      : orderBy === 'mentions' ? 'mention_count'
      : orderBy === 'updated' ? 'updated_at'
      : orderBy === 'confidence' ? 'confidence_score'
      : 'mention_count'; // default

    return `ORDER BY ${column} ${direction}`;
  }
}

/**
 * Knowledge Graph Repository
 *
 * Manages entity recognition, relationship tracking, and graph-based queries
 * for cross-conversation intelligence using SQLite native features.
 */

import Database from 'better-sqlite3';

/**
 * Entity types supported by the knowledge graph
 */
export type EntityType = 'person' | 'organization' | 'product' | 'concept' | 'location' | 'technical' | 'event' | 'decision';

/**
 * Relationship types between entities
 */
export type RelationshipType = 'works_for' | 'created_by' | 'discussed_with' | 'related_to' | 'part_of' | 'mentioned_with' | 'temporal_sequence' | 'cause_effect';

/**
 * Entity extraction methods
 */
export type ExtractionMethod = 'pattern' | 'nlp' | 'manual';

/**
 * Entity evolution types
 */
export type EvolutionType = 'property_added' | 'relationship_added' | 'description_updated' | 'status_changed' | 'alias_added';

/**
 * Entity interface
 */
export interface Entity {
  id: string;
  name: string;
  normalized_name: string;
  type: EntityType;
  canonical_form?: string;
  confidence_score: number;
  created_at: number;
  updated_at: number;
  metadata: Record<string, any>;
  mention_count: number;
  last_mentioned_at?: number;
}

/**
 * Entity mention interface
 */
export interface EntityMention {
  id: string;
  entity_id: string;
  message_id: string;
  conversation_id: string;
  mention_text: string;
  start_position: number;
  end_position: number;
  confidence_score: number;
  extraction_method: ExtractionMethod;
  created_at: number;
}

/**
 * Entity relationship interface
 */
export interface EntityRelationship {
  id: string;
  source_entity_id: string;
  target_entity_id: string;
  relationship_type: RelationshipType;
  strength: number;
  first_mentioned_at: number;
  last_mentioned_at: number;
  mention_count: number;
  context_messages: string[];
  created_at: number;
  updated_at: number;
}

/**
 * Graph traversal result
 */
export interface GraphTraversalResult {
  entity_id: string;
  entity_name: string;
  entity_type: EntityType;
  degree: number;
  relationship_type: RelationshipType;
  strength: number;
  path: string[];
}

/**
 * Entity cluster result
 */
export interface EntityCluster {
  entity_id: string;
  entity_name: string;
  entity_type: EntityType;
  connection_count: number;
  avg_strength: number;
  cluster_members: Array<{
    name: string;
    type: EntityType;
    strength: number;
  }>;
}

/**
 * Knowledge graph repository implementation
 */
export class KnowledgeGraphRepository {
  public db: Database.Database;
  // Prepared statements for performance
  private createEntityStmt!: Database.Statement;
  private createMentionStmt!: Database.Statement;
  private createRelationshipStmt!: Database.Statement;
  private findEntityByNameStmt!: Database.Statement;
  private findEntitiesByTypeStmt!: Database.Statement;
  private getEntityMentionsStmt!: Database.Statement;
  private getEntityRelationshipsStmt!: Database.Statement;
  private updateRelationshipStrengthStmt!: Database.Statement;

  constructor(db: Database.Database) {
    this.db = db;
    this.prepareStatements();
  }

  /**
   * Handle database errors with proper logging and error transformation
   */
  private handleError(error: unknown, context: string): never {
    const message = error instanceof Error ? error.message : 'Unknown error';
    console.error(`[KnowledgeGraphRepository] ${context}: ${message}`);
    throw new Error(`Knowledge graph operation failed: ${message}`);
  }

  /**
   * Generate a unique ID
   */
  private generateId(): string {
    return `kg_${Date.now()}_${Math.random().toString(36).substring(2, 15)}`;
  }

  /**
   * Prepare frequently used SQL statements
   */
  private prepareStatements(): void {
    this.createEntityStmt = this.db.prepare(`
      INSERT INTO entities (id, name, normalized_name, type, canonical_form, confidence_score, created_at, updated_at, metadata, mention_count)
      VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, 0)
    `);

    this.createMentionStmt = this.db.prepare(`
      INSERT INTO entity_mentions (id, entity_id, message_id, conversation_id, mention_text, start_position, end_position, confidence_score, extraction_method, created_at)
      VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
    `);

    this.createRelationshipStmt = this.db.prepare(`
      INSERT OR REPLACE INTO entity_relationships 
      (id, source_entity_id, target_entity_id, relationship_type, strength, first_mentioned_at, last_mentioned_at, mention_count, context_messages, created_at, updated_at)
      VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
    `);

    this.findEntityByNameStmt = this.db.prepare(`
      SELECT * FROM entities WHERE normalized_name = ? LIMIT 1
    `);

    this.findEntitiesByTypeStmt = this.db.prepare(`
      SELECT * FROM entities WHERE type = ? ORDER BY mention_count DESC, updated_at DESC
    `);

    this.getEntityMentionsStmt = this.db.prepare(`
      SELECT em.*, m.content, c.title as conversation_title
      FROM entity_mentions em
      JOIN messages m ON em.message_id = m.id
      JOIN conversations c ON em.conversation_id = c.id
      WHERE em.entity_id = ?
      ORDER BY em.created_at DESC
    `);

    this.getEntityRelationshipsStmt = this.db.prepare(`
      SELECT r.*, e1.name as source_name, e2.name as target_name
      FROM entity_relationships r
      JOIN entities e1 ON r.source_entity_id = e1.id
      JOIN entities e2 ON r.target_entity_id = e2.id
      WHERE r.source_entity_id = ? OR r.target_entity_id = ?
      ORDER BY r.strength DESC, r.last_mentioned_at DESC
    `);

    this.updateRelationshipStrengthStmt = this.db.prepare(`
      UPDATE entity_relationships 
      SET strength = ?, mention_count = mention_count + 1, last_mentioned_at = ?, updated_at = ?
      WHERE source_entity_id = ? AND target_entity_id = ? AND relationship_type = ?
    `);
  }

  /**
   * Create or update an entity
   */
  async createEntity(entity: Omit<Entity, 'id' | 'created_at' | 'updated_at' | 'mention_count'>): Promise<Entity> {
    const now = Date.now();
    const id = this.generateId();

    const entityData: Entity = {
      ...entity,
      id,
      created_at: now,
      updated_at: now,
      mention_count: 0
    };

    try {
      this.createEntityStmt.run(
        entityData.id,
        entityData.name,
        entityData.normalized_name,
        entityData.type,
        entityData.canonical_form || null,
        entityData.confidence_score,
        entityData.created_at,
        entityData.updated_at,
        JSON.stringify(entityData.metadata)
      );

      return entityData;
    } catch (error) {
      this.handleError(error, 'Failed to create entity');
    }
  }

  /**
   * Find entity by normalized name
   */
  async findEntityByName(normalizedName: string): Promise<Entity | null> {
    try {
      const row = this.findEntityByNameStmt.get(normalizedName) as any;
      if (!row) return null;

      return this.mapRowToEntity(row);
    } catch (error) {
      this.handleError(error, 'Failed to find entity by name');
    }
  }

  /**
   * Find entities by type
   */
  async findEntitiesByType(type: EntityType, limit = 100): Promise<Entity[]> {
    try {
      const stmt = this.db.prepare(`
        SELECT * FROM entities 
        WHERE type = ? 
        ORDER BY mention_count DESC, updated_at DESC 
        LIMIT ?
      `);

      const rows = stmt.all(type, limit) as any[];
      return rows.map(row => this.mapRowToEntity(row));
    } catch (error) {
      this.handleError(error, 'Failed to find entities by type');
    }
  }

  /**
   * Create entity mention
   */
  async createEntityMention(mention: Omit<EntityMention, 'id' | 'created_at'>): Promise<EntityMention> {
    const now = Date.now();
    const id = this.generateId();

    const mentionData: EntityMention = {
      ...mention,
      id,
      created_at: now
    };

    try {
      this.createMentionStmt.run(
        mentionData.id,
        mentionData.entity_id,
        mentionData.message_id,
        mentionData.conversation_id,
        mentionData.mention_text,
        mentionData.start_position,
        mentionData.end_position,
        mentionData.confidence_score,
        mentionData.extraction_method,
        mentionData.created_at
      );

      return mentionData;
    } catch (error) {
      this.handleError(error, 'Failed to create entity mention');
    }
  }

  /**
   * Create or update entity relationship
   */
  async createOrUpdateRelationship(
    sourceEntityId: string,
    targetEntityId: string,
    relationshipType: RelationshipType,
    strength: number,
    contextMessageIds: string[] = []
  ): Promise<EntityRelationship> {
    const now = Date.now();

    // Check if relationship already exists
    const existing = this.db.prepare(`
      SELECT * FROM entity_relationships 
      WHERE source_entity_id = ? AND target_entity_id = ? AND relationship_type = ?
    `).get(sourceEntityId, targetEntityId, relationshipType) as any;

    if (existing) {
      // Update existing relationship
      const newStrength = Math.min(1.0, existing.strength + (strength * 0.1)); // Gradual strength increase

      this.updateRelationshipStrengthStmt.run(
        newStrength,
        now,
        now,
        sourceEntityId,
        targetEntityId,
        relationshipType
      );

      return {
        ...existing,
        strength: newStrength,
        last_mentioned_at: now,
        mention_count: existing.mention_count + 1,
        updated_at: now
      };
    } else {
      // Create new relationship
      const id = this.generateId();
      const relationshipData: EntityRelationship = {
        id,
        source_entity_id: sourceEntityId,
        target_entity_id: targetEntityId,
        relationship_type: relationshipType,
        strength,
        first_mentioned_at: now,
        last_mentioned_at: now,
        mention_count: 1,
        context_messages: contextMessageIds,
        created_at: now,
        updated_at: now
      };

      this.createRelationshipStmt.run(
        relationshipData.id,
        relationshipData.source_entity_id,
        relationshipData.target_entity_id,
        relationshipData.relationship_type,
        relationshipData.strength,
        relationshipData.first_mentioned_at,
        relationshipData.last_mentioned_at,
        relationshipData.mention_count,
        JSON.stringify(relationshipData.context_messages),
        relationshipData.created_at,
        relationshipData.updated_at
      );

      return relationshipData;
    }
  }

  /**
   * Find entities connected to a given entity within N degrees
   */
  async findConnectedEntities(entityId: string, maxDegrees = 2, minStrength = 0.3): Promise<GraphTraversalResult[]> {
    try {
      const stmt = this.db.prepare(`
        WITH RECURSIVE entity_graph(entity_id, target_id, path, degree, relationship_type, strength) AS (
          -- Base case: direct relationships
          SELECT 
            r.source_entity_id as entity_id,
            r.target_entity_id as target_id,
            json_array(r.source_entity_id, r.target_entity_id) as path,
            1 as degree,
            r.relationship_type,
            r.strength
          FROM entity_relationships r
          WHERE r.source_entity_id = ? AND r.strength >= ?

          UNION ALL

          -- Recursive case: extend path
          SELECT 
            eg.entity_id,
            r.target_entity_id as target_id,
            json_insert(eg.path, '$[#]', r.target_entity_id) as path,
            eg.degree + 1,
            r.relationship_type,
            r.strength * eg.strength as strength
          FROM entity_graph eg
          JOIN entity_relationships r ON eg.target_id = r.source_entity_id
          WHERE eg.degree < ? 
            AND r.strength >= ?
            AND json_extract(eg.path, '$') NOT LIKE '%' || r.target_entity_id || '%'
        )
        SELECT 
          e.id as entity_id,
          e.name as entity_name,
          e.type as entity_type,
          eg.degree,
          eg.relationship_type,
          eg.strength,
          eg.path
        FROM entity_graph eg
        JOIN entities e ON eg.target_id = e.id
        ORDER BY eg.degree ASC, eg.strength DESC
      `);

      const rows = stmt.all(entityId, minStrength, maxDegrees, minStrength * 0.8) as any[];

      return rows.map(row => ({
        entity_id: row.entity_id,
        entity_name: row.entity_name,
        entity_type: row.entity_type,
        degree: row.degree,
        relationship_type: row.relationship_type,
        strength: row.strength,
        path: JSON.parse(row.path)
      }));
    } catch (error) {
      this.handleError(error, 'Failed to find connected entities');
    }
  }

  /**
   * Find shortest path between two entities
   */
  async findShortestPath(sourceEntityId: string, targetEntityId: string): Promise<GraphTraversalResult | null> {
    try {
      const stmt = this.db.prepare(`
        WITH RECURSIVE entity_paths(entity_id, target_id, path_ids, path_names, distance, total_strength) AS (
          -- Forward search from source
          SELECT 
            r.source_entity_id,
            r.target_entity_id,
            json_array(r.source_entity_id, r.target_entity_id) as path_ids,
            json_array(e1.name, e2.name) as path_names,
            1 as distance,
            r.strength as total_strength
          FROM entity_relationships r
          JOIN entities e1 ON r.source_entity_id = e1.id
          JOIN entities e2 ON r.target_entity_id = e2.id
          WHERE r.source_entity_id = ?

          UNION ALL

          SELECT 
            ep.entity_id,
            r.target_entity_id,
            json_insert(ep.path_ids, '$[#]', r.target_entity_id) as path_ids,
            json_insert(ep.path_names, '$[#]', e.name) as path_names,
            ep.distance + 1,
            ep.total_strength * r.strength
          FROM entity_paths ep
          JOIN entity_relationships r ON ep.target_id = r.source_entity_id
          JOIN entities e ON r.target_entity_id = e.id
          WHERE ep.distance < 4
            AND json_extract(ep.path_ids, '$') NOT LIKE '%' || r.target_entity_id || '%'
        )
        SELECT 
          path_names,
          distance,
          total_strength,
          path_ids
        FROM entity_paths
        WHERE target_id = ?
        ORDER BY distance ASC, total_strength DESC
        LIMIT 1
      `);

      const row = stmt.get(sourceEntityId, targetEntityId) as any;
      if (!row) return null;

      return {
        entity_id: targetEntityId,
        entity_name: JSON.parse(row.path_names)[JSON.parse(row.path_names).length - 1],
        entity_type: 'unknown' as EntityType, // Would need another query to get this
        degree: row.distance,
        relationship_type: 'related_to' as RelationshipType,
        strength: row.total_strength,
        path: JSON.parse(row.path_names)
      };
    } catch (error) {
      this.handleError(error, 'Failed to find shortest path');
    }
  }

  /**
   * Identify entity clusters based on co-occurrence patterns
   */
  async findEntityClusters(minConnectionCount = 3, minAvgStrength = 0.4): Promise<EntityCluster[]> {
    try {
      const stmt = this.db.prepare(`
        WITH entity_connections AS (
          SELECT 
            r.source_entity_id,
            r.target_entity_id,
            r.strength,
            COUNT(DISTINCT em1.conversation_id) as shared_conversations
          FROM entity_relationships r
          JOIN entity_mentions em1 ON r.source_entity_id = em1.entity_id
          JOIN entity_mentions em2 ON r.target_entity_id = em2.entity_id 
            AND em1.conversation_id = em2.conversation_id
          WHERE r.strength > 0.3
          GROUP BY r.source_entity_id, r.target_entity_id, r.strength
          HAVING shared_conversations >= 2
        ),
        entity_clusters AS (
          SELECT 
            source_entity_id as entity_id,
            COUNT(DISTINCT target_entity_id) as connection_count,
            AVG(strength) as avg_strength
          FROM entity_connections
          GROUP BY source_entity_id
          HAVING connection_count >= ? AND avg_strength >= ?
        )
        SELECT 
          e.id as entity_id,
          e.name as entity_name,
          e.type as entity_type,
          ec.connection_count,
          ec.avg_strength,
          json_group_array(
            json_object(
              'name', connected_e.name,
              'type', connected_e.type,
              'strength', conn.strength
            )
          ) as cluster_members
        FROM entity_clusters ec
        JOIN entities e ON ec.entity_id = e.id
        JOIN entity_connections conn ON ec.entity_id = conn.source_entity_id
        JOIN entities connected_e ON conn.target_entity_id = connected_e.id
        GROUP BY ec.entity_id, e.name, e.type, ec.connection_count, ec.avg_strength
        ORDER BY ec.avg_strength DESC, ec.connection_count DESC
      `);

      const rows = stmt.all(minConnectionCount, minAvgStrength) as any[];

      return rows.map(row => ({
        entity_id: row.entity_id,
        entity_name: row.entity_name,
        entity_type: row.entity_type,
        connection_count: row.connection_count,
        avg_strength: row.avg_strength,
        cluster_members: JSON.parse(row.cluster_members)
      }));
    } catch (error) {
      this.handleError(error, 'Failed to find entity clusters');
    }
  }

  /**
   * Search entities using FTS5
   */
  async searchEntities(query: string, limit = 50): Promise<Entity[]> {
    try {
      const stmt = this.db.prepare(`
        SELECT e.*, entities_fts.rank
        FROM entities_fts
        JOIN entities e ON entities_fts.rowid = e.rowid
        WHERE entities_fts MATCH ?
        ORDER BY entities_fts.rank, e.mention_count DESC
        LIMIT ?
      `);

      const rows = stmt.all(query, limit) as any[];
      return rows.map(row => this.mapRowToEntity(row));
    } catch (error) {
      this.handleError(error, 'Failed to search entities');
    }
  }

  /**
   * Get entity mentions for a specific entity
   */
  async getEntityMentions(entityId: string, limit = 100): Promise<any[]> {
    try {
      const stmt = this.db.prepare(`
        SELECT em.*, m.content, c.title as conversation_title
        FROM entity_mentions em
        JOIN messages m ON em.message_id = m.id
        JOIN conversations c ON em.conversation_id = c.id
        WHERE em.entity_id = ?
        ORDER BY em.created_at DESC
        LIMIT ?
      `);

      return stmt.all(entityId, limit) as any[];
    } catch (error) {
      this.handleError(error, 'Failed to get entity mentions');
    }
  }

  /**
   * Get entity relationships for a specific entity
   */
  async getEntityRelationships(entityId: string): Promise<any[]> {
    try {
      return this.getEntityRelationshipsStmt.all(entityId, entityId) as any[];
    } catch (error) {
      this.handleError(error, 'Failed to get entity relationships');
    }
  }

  /**
   * Map database row to Entity object
   */
  private mapRowToEntity(row: any): Entity {
    return {
      id: row.id,
      name: row.name,
      normalized_name: row.normalized_name,
      type: row.type,
      canonical_form: row.canonical_form,
      confidence_score: row.confidence_score,
      created_at: row.created_at,
      updated_at: row.updated_at,
      metadata: JSON.parse(row.metadata || '{}'),
      mention_count: row.mention_count,
      last_mentioned_at: row.last_mentioned_at
    };
  }
}

/**
 * Message Repository - CRUD operations for messages with FTS support
 * 
 * This repository provides:
 * - Full CRUD operations for messages
 * - Full-text search capabilities
 * - Parent-child message relationships (threading)
 * - Embedding vector storage and retrieval
 * - Automatic FTS index maintenance via triggers
 * - Conversation timestamp updates
 */

import { Message, PaginatedResult, SearchOptions, SearchResult } from '../../types/interfaces.js';
import { BaseRepository } from './BaseRepository.js';

/**
 * Interface for message creation parameters
 */
export interface CreateMessageParams {
  id?: string;
  conversationId: string;
  role: 'user' | 'assistant' | 'system';
  content: string;
  parentMessageId?: string;
  metadata?: Record<string, any>;
  embedding?: number[];
}

/**
 * Interface for message update parameters
 */
export interface UpdateMessageParams {
  content?: string;
  metadata?: Record<string, any>;
  embedding?: number[];
}

/**
 * Repository for message CRUD operations with FTS support
 */
export class MessageRepository extends BaseRepository {

  /**
   * Create a new message
   */
  async create(params: CreateMessageParams): Promise<Message> {
    const id = params.id || this.generateId();
    const now = this.getCurrentTimestamp();

    const message: Message = {
      id,
      conversationId: params.conversationId,
      role: params.role,
      content: params.content,
      createdAt: now,
      parentMessageId: params.parentMessageId,
      metadata: params.metadata || {},
      embedding: params.embedding
    };

    try {
      // Convert embedding array to Buffer if provided
      let embeddingBuffer: Buffer | null = null;
      if (params.embedding && params.embedding.length > 0) {
        embeddingBuffer = Buffer.from(new Float32Array(params.embedding).buffer);
      }

      this.executeStatementRun(
        'insert_message',
        `INSERT INTO messages (id, conversation_id, role, content, created_at, parent_message_id, metadata, embedding)
         VALUES (?, ?, ?, ?, ?, ?, ?, ?)`,
        [
          message.id,
          message.conversationId,
          message.role,
          message.content,
          message.createdAt,
          message.parentMessageId || null,
          this.stringifyMetadata(message.metadata),
          embeddingBuffer
        ]
      );

      return message;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Message');
    }
  }

  /**
   * Find a message by ID
   */
  async findById(id: string): Promise<Message | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const row = this.executeStatement<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      'find_message_by_id',
      `SELECT id, conversation_id, role, content, created_at, parent_message_id, metadata, embedding
       FROM messages
       WHERE id = ?`,
      [id]
    );

    if (!row) {
      return null;
    }

    return this.mapRowToMessage(row);
  }

  /**
   * Find messages by conversation ID with pagination
   */
  async findByConversation(
    conversationId: string,
    limit?: number,
    offset?: number,
    orderBy: 'created_at' = 'created_at',
    orderDir: 'ASC' | 'DESC' = 'ASC'
  ): Promise<PaginatedResult<Message>> {
    if (!this.isValidUUID(conversationId)) {
      return { data: [], hasMore: false };
    }

    const pagination = this.validatePagination(limit, offset);

    // Validate orderBy and orderDir to prevent SQL injection
    if (orderBy !== 'created_at') {
      throw new Error('Invalid orderBy parameter');
    }

    if (orderDir !== 'ASC' && orderDir !== 'DESC') {
      throw new Error('Invalid orderDir parameter');
    }

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      `find_messages_by_conversation_${orderDir}`,
      `SELECT id, conversation_id, role, content, created_at, parent_message_id, metadata, embedding
       FROM messages
       WHERE conversation_id = ?
       ORDER BY created_at ${orderDir}
       LIMIT ? OFFSET ?`,
      [conversationId, pagination.limit + 1, pagination.offset]
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => this.mapRowToMessage(row));

    return {
      data,
      hasMore
    };
  }

  /**
   * Update a message
   */
  async update(id: string, params: UpdateMessageParams): Promise<Message | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const existing = await this.findById(id);
    if (!existing) {
      return null;
    }

    const updatedMessage: Message = {
      ...existing,
      content: params.content !== undefined ? params.content : existing.content,
      metadata: params.metadata !== undefined ? params.metadata : existing.metadata,
      embedding: params.embedding !== undefined ? params.embedding : existing.embedding
    };

    try {
      // Convert embedding array to Buffer if provided
      let embeddingBuffer: Buffer | null = null;
      if (updatedMessage.embedding && updatedMessage.embedding.length > 0) {
        embeddingBuffer = Buffer.from(new Float32Array(updatedMessage.embedding).buffer);
      }

      const result = this.executeStatementRun(
        'update_message',
        `UPDATE messages 
         SET content = ?, metadata = ?, embedding = ?
         WHERE id = ?`,
        [
          updatedMessage.content,
          this.stringifyMetadata(updatedMessage.metadata),
          embeddingBuffer,
          id
        ]
      );

      if (result.changes === 0) {
        return null;
      }

      return updatedMessage;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Message');
    }
  }

  /**
   * Delete a message
   */
  async delete(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatementRun(
      'delete_message',
      'DELETE FROM messages WHERE id = ?',
      [id]
    );

    return result.changes > 0;
  }

  /**
   * Count messages in a conversation
   */
  async countByConversation(conversationId: string): Promise<number> {
    if (!this.isValidUUID(conversationId)) {
      return 0;
    }

    const result = this.executeStatement<{ count: number }>(
      'count_messages_by_conversation',
      'SELECT COUNT(*) as count FROM messages WHERE conversation_id = ?',
      [conversationId]
    );

    return result.count;
  }

  /**
   * Full-text search across messages with optimized query
   */
  async search(options: SearchOptions): Promise<PaginatedResult<SearchResult>> {
    const pagination = this.validatePagination(options.limit, options.offset);

    // Build the FTS query
    let ftsQuery = options.query;
    if (options.matchType === 'prefix') {
      ftsQuery = `${options.query}*`;
    } else if (options.matchType === 'exact') {
      ftsQuery = `"${options.query}"`;
    }
    // For fuzzy matching, we use the query as-is (FTS5 default behavior)

    let whereClause = '';
    const params: any[] = [ftsQuery];

    // Add conversation filter if specified
    if (options.conversationId) {
      if (!this.isValidUUID(options.conversationId)) {
        return { data: [], hasMore: false };
      }
      whereClause += ' AND m.conversation_id = ?';
      params.push(options.conversationId);
    }

    // Add date range filters if specified
    if (options.startDate) {
      const startTimestamp = new Date(options.startDate).getTime();
      whereClause += ' AND m.created_at >= ?';
      params.push(startTimestamp);
    }

    if (options.endDate) {
      const endTimestamp = new Date(options.endDate).getTime();
      whereClause += ' AND m.created_at <= ?';
      params.push(endTimestamp);
    }

    // Add pagination parameters
    params.push(pagination.limit + 1, pagination.offset);

    // Optimized query with proper joins and covering indexes
    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
      rank: number;
      snippet: string;
      conversation_title: string | null;
    }>(
      'search_messages_fts_optimized',
      `WITH ranked_results AS (
         SELECT 
           m.id, m.conversation_id, m.role, m.content, m.created_at, 
           m.parent_message_id, m.metadata, m.embedding,
           messages_fts.rank as rank,
           snippet(messages_fts, 0, ?, ?, '...', 32) as snippet
         FROM messages_fts
         STRAIGHT_JOIN messages m ON m.rowid = messages_fts.rowid
         WHERE messages_fts MATCH ?${whereClause}
         ORDER BY messages_fts.rank
         LIMIT ? OFFSET ?
       )
       SELECT 
         r.*, c.title as conversation_title
       FROM ranked_results r
       LEFT JOIN conversations c ON c.id = r.conversation_id
       ORDER BY r.rank`,
      [
        options.highlightStart || '<mark>',
        options.highlightEnd || '</mark>',
        ...params
      ]
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => ({
      message: this.mapRowToMessage(row),
      score: row.rank,
      snippet: row.snippet,
      conversationTitle: row.conversation_title || undefined
    }));

    return {
      data,
      hasMore
    };
  }

  /**
   * Find messages by role
   */
  async findByRole(
    role: 'user' | 'assistant' | 'system',
    conversationId?: string,
    limit?: number,
    offset?: number
  ): Promise<PaginatedResult<Message>> {
    const pagination = this.validatePagination(limit, offset);

    let whereClause = 'WHERE role = ?';
    const params: any[] = [role];

    if (conversationId) {
      if (!this.isValidUUID(conversationId)) {
        return { data: [], hasMore: false };
      }
      whereClause += ' AND conversation_id = ?';
      params.push(conversationId);
    }

    params.push(pagination.limit + 1, pagination.offset);

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      `find_messages_by_role_${conversationId ? 'with_conversation' : 'all'}`,
      `SELECT id, conversation_id, role, content, created_at, parent_message_id, metadata, embedding
       FROM messages
       ${whereClause}
       ORDER BY created_at DESC
       LIMIT ? OFFSET ?`,
      params
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => this.mapRowToMessage(row));

    return {
      data,
      hasMore
    };
  }

  /**
   * Find child messages of a parent message
   */
  async findChildren(parentMessageId: string): Promise<Message[]> {
    if (!this.isValidUUID(parentMessageId)) {
      return [];
    }

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      'find_child_messages',
      `SELECT id, conversation_id, role, content, created_at, parent_message_id, metadata, embedding
       FROM messages
       WHERE parent_message_id = ?
       ORDER BY created_at ASC`,
      [parentMessageId]
    );

    return rows.map(row => this.mapRowToMessage(row));
  }

  /**
   * Get messages with embeddings for vector search
   */
  async findWithEmbeddings(
    conversationId?: string,
    limit?: number,
    offset?: number
  ): Promise<PaginatedResult<Message>> {
    const pagination = this.validatePagination(limit, offset);

    let whereClause = 'WHERE embedding IS NOT NULL';
    const params: any[] = [];

    if (conversationId) {
      if (!this.isValidUUID(conversationId)) {
        return { data: [], hasMore: false };
      }
      whereClause += ' AND conversation_id = ?';
      params.push(conversationId);
    }

    params.push(pagination.limit + 1, pagination.offset);

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      `find_messages_with_embeddings_${conversationId ? 'by_conversation' : 'all'}`,
      `SELECT id, conversation_id, role, content, created_at, parent_message_id, metadata, embedding
       FROM messages
       ${whereClause}
       ORDER BY created_at DESC
       LIMIT ? OFFSET ?`,
      params
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => this.mapRowToMessage(row));

    return {
      data,
      hasMore
    };
  }

  /**
   * Check if a message exists
   */
  async exists(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatement<{ count: number }>(
      'message_exists',
      'SELECT 1 as count FROM messages WHERE id = ? LIMIT 1',
      [id]
    );
    return !!result;
  }

  /**
   * Batch create messages for better performance
   */
  async batchCreate(messages: CreateMessageParams[]): Promise<Message[]> {
    if (messages.length === 0) {
      return [];
    }

    const db = this.getConnection();
    const now = this.getCurrentTimestamp();

    // Prepare batch insert
    const insertStmt = db.prepare(`
      INSERT INTO messages (id, conversation_id, role, content, created_at, parent_message_id, metadata, embedding)
      VALUES (?, ?, ?, ?, ?, ?, ?, ?)
    `);

    const transaction = db.transaction(() => {
      const results: Message[] = [];

      for (const params of messages) {
        const id = params.id || this.generateId();
        let embeddingBuffer: Buffer | null = null;

        if (params.embedding && params.embedding.length > 0) {
          embeddingBuffer = Buffer.from(new Float32Array(params.embedding).buffer);
        }

        const message: Message = {
          id,
          conversationId: params.conversationId,
          role: params.role,
          content: params.content,
          createdAt: now,
          parentMessageId: params.parentMessageId,
          metadata: params.metadata || {},
          embedding: params.embedding
        };

        insertStmt.run(
          message.id,
          message.conversationId,
          message.role,
          message.content,
          message.createdAt,
          message.parentMessageId || null,
          this.stringifyMetadata(message.metadata),
          embeddingBuffer
        );

        results.push(message);
      }

      return results;
    });

    return transaction();
  }

  /**
   * Batch update message embeddings
   */
  async batchUpdateEmbeddings(updates: { id: string; embedding: number[] }[]): Promise<number> {
    if (updates.length === 0) {
      return 0;
    }

    const db = this.getConnection();
    const updateStmt = db.prepare('UPDATE messages SET embedding = ? WHERE id = ?');

    const transaction = db.transaction(() => {
      let updatedCount = 0;

      for (const update of updates) {
        const embeddingBuffer = Buffer.from(new Float32Array(update.embedding).buffer);
        const result = updateStmt.run(JSON.stringify(update.embedding), update.id);
        updatedCount += result.changes;
      }

      return updatedCount;
    });

    return transaction();
  }

  /**
   * Get messages by IDs (optimized batch retrieval)
   */
  async findByIds(ids: string[]): Promise<Message[]> {
    if (ids.length === 0) {
      return [];
    }

    // Validate all IDs
    const validIds = ids.filter(id => this.isValidUUID(id));
    if (validIds.length === 0) {
      return [];
    }

    const placeholders = validIds.map(() => '?').join(',');
    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      role: string;
      content: string;
      created_at: number;
      parent_message_id: string | null;
      metadata: string;
      embedding: Buffer | null;
    }>(
      'find_messages_by_ids',
      `SELECT id, conversation_id, role, content, created_at, parent_message_id, metadata, embedding
       FROM messages
       WHERE id IN (${placeholders})
       ORDER BY created_at ASC`,
      validIds
    );

    return rows.map(row => this.mapRowToMessage(row));
  }

  /**
   * Get conversation message statistics
   */
  async getConversationStats(conversationId: string): Promise<{
    totalMessages: number;
    messagesByRole: { [role: string]: number };
    dateRange: { earliest: number; latest: number } | null;
    hasEmbeddings: boolean;
    avgMessageLength: number;
  }> {
    if (!this.isValidUUID(conversationId)) {
      return {
        totalMessages: 0,
        messagesByRole: {},
        dateRange: null,
        hasEmbeddings: false,
        avgMessageLength: 0
      };
    }

    const stats = this.executeStatement<{
      total_messages: number;
      user_messages: number;
      assistant_messages: number;
      system_messages: number;
      earliest_date: number | null;
      latest_date: number | null;
      messages_with_embeddings: number;
      avg_content_length: number;
    }>(
      'conversation_message_stats',
      `SELECT 
         COUNT(*) as total_messages,
         SUM(CASE WHEN role = 'user' THEN 1 ELSE 0 END) as user_messages,
         SUM(CASE WHEN role = 'assistant' THEN 1 ELSE 0 END) as assistant_messages,
         SUM(CASE WHEN role = 'system' THEN 1 ELSE 0 END) as system_messages,
         MIN(created_at) as earliest_date,
         MAX(created_at) as latest_date,
         SUM(CASE WHEN embedding IS NOT NULL THEN 1 ELSE 0 END) as messages_with_embeddings,
         AVG(LENGTH(content)) as avg_content_length
       FROM messages 
       WHERE conversation_id = ?`,
      [conversationId]
    );

    if (!stats) {
      return {
        totalMessages: 0,
        messagesByRole: {},
        dateRange: null,
        hasEmbeddings: false,
        avgMessageLength: 0
      };
    }

    return {
      totalMessages: stats.total_messages,
      messagesByRole: {
        user: stats.user_messages,
        assistant: stats.assistant_messages,
        system: stats.system_messages
      },
      dateRange: stats.earliest_date && stats.latest_date 
        ? { earliest: stats.earliest_date, latest: stats.latest_date }
        : null,
      hasEmbeddings: stats.messages_with_embeddings > 0,
      avgMessageLength: Math.round(stats.avg_content_length || 0)
    };
  }

  /**
   * Find messages by conversation ID (alias for findByConversation for compatibility)
   */
  async findByConversationId(conversationId: string, options?: {
    limit?: number;
    offset?: number;
    orderBy?: 'created_at';
    orderDir?: 'ASC' | 'DESC';
  }): Promise<Message[]> {
    const result = await this.findByConversation(
      conversationId,
      options?.limit,
      options?.offset,
      options?.orderBy || 'created_at',
      options?.orderDir || 'ASC'
    );
    return result.data;
  }

  /**
   * Map database row to Message object
   */
  private mapRowToMessage(row: {
    id: string;
    conversation_id: string;
    role: string;
    content: string;
    created_at: number;
    parent_message_id: string | null;
    metadata: string;
    embedding: Buffer | null;
  }): Message {
    // Convert embedding Buffer back to number array if present
    let embedding: number[] | undefined;
    if (row.embedding) {
      const float32Array = new Float32Array(row.embedding.buffer, row.embedding.byteOffset, row.embedding.byteLength / 4);
      embedding = Array.from(float32Array);
    }

    return {
      id: row.id,
      conversationId: row.conversation_id,
      role: row.role as 'user' | 'assistant' | 'system',
      content: row.content,
      createdAt: row.created_at,
      parentMessageId: row.parent_message_id || undefined,
      metadata: this.parseMetadata(row.metadata),
      embedding
    };
  }
}

/**
 * Provider Configuration Repository - CRUD operations for LLM providers
 * 
 * This repository provides:
 * - Full CRUD operations for LLM provider configurations
 * - Secure storage of API keys using encryption
 * - Provider activation/deactivation management
 * - Priority-based ordering for provider selection
 * - Type-based filtering (local vs external providers)
 */

import { LLMProvider } from '../../types/interfaces.js';
import { IProviderConfigRepository, CreateProviderParams } from '../../types/repositories.js';
import { BaseRepository } from './BaseRepository.js';

/**
 * Interface for updating provider parameters
 */
export interface UpdateProviderParams {
  name?: string;
  endpoint?: string;
  apiKeyEnv?: string;
  modelName?: string;
  maxTokens?: number;
  temperature?: number;
  isActive?: boolean;
  priority?: number;
  costPer1kTokens?: number;
  metadata?: Record<string, any>;
}

/**
 * Repository for LLM provider configuration operations
 */
export class ProviderConfigRepository extends BaseRepository implements IProviderConfigRepository {

  /**
   * Find all active providers ordered by priority (descending)
   */
  async findActive(): Promise<LLMProvider[]> {
    const rows = this.executeStatementAll<{
      id: string;
      name: string;
      type: string;
      endpoint: string | null;
      api_key_env: string | null;
      model_name: string;
      max_tokens: number;
      temperature: number;
      is_active: number;
      priority: number;
      cost_per_1k_tokens: number | null;
      metadata: string;
    }>(
      'find_active_providers',
      `SELECT id, name, type, endpoint, api_key_env, model_name, max_tokens, 
              temperature, is_active, priority, cost_per_1k_tokens, metadata
       FROM llm_providers
       WHERE is_active = 1
       ORDER BY priority DESC, name ASC`
    );

    const providers: LLMProvider[] = [];
    for (const row of rows) {
      const provider = await this.mapRowToProvider(row);
      providers.push(provider);
    }

    return providers;
  }

  /**
   * Find providers by type (local or external)
   */
  async findByType(type: 'local' | 'external'): Promise<LLMProvider[]> {
    const rows = this.executeStatementAll<{
      id: string;
      name: string;
      type: string;
      endpoint: string | null;
      api_key_env: string | null;
      model_name: string;
      max_tokens: number;
      temperature: number;
      is_active: number;
      priority: number;
      cost_per_1k_tokens: number | null;
      metadata: string;
    }>(
      `find_providers_by_type_${type}`,
      `SELECT id, name, type, endpoint, api_key_env, model_name, max_tokens, 
              temperature, is_active, priority, cost_per_1k_tokens, metadata
       FROM llm_providers
       WHERE type = ?
       ORDER BY priority DESC, name ASC`,
      [type]
    );

    const providers: LLMProvider[] = [];
    for (const row of rows) {
      const provider = await this.mapRowToProvider(row);
      providers.push(provider);
    }

    return providers;
  }

  /**
   * Update provider configuration
   */
  async updateConfig(id: string, config: UpdateProviderParams): Promise<LLMProvider | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const existing = await this.findById(id);
    if (!existing) {
      return null;
    }

    // Build dynamic update query based on provided fields
    const updateFields: string[] = [];
    const updateValues: any[] = [];

    if (config.name !== undefined) {
      updateFields.push('name = ?');
      updateValues.push(config.name);
    }

    if (config.endpoint !== undefined) {
      updateFields.push('endpoint = ?');
      updateValues.push(config.endpoint);
    }

    if (config.apiKeyEnv !== undefined) {
      updateFields.push('api_key_env = ?');
      updateValues.push(config.apiKeyEnv);
    }

    if (config.modelName !== undefined) {
      updateFields.push('model_name = ?');
      updateValues.push(config.modelName);
    }

    if (config.maxTokens !== undefined) {
      updateFields.push('max_tokens = ?');
      updateValues.push(config.maxTokens);
    }

    if (config.temperature !== undefined) {
      updateFields.push('temperature = ?');
      updateValues.push(config.temperature);
    }

    if (config.isActive !== undefined) {
      updateFields.push('is_active = ?');
      updateValues.push(config.isActive ? 1 : 0);
    }

    if (config.priority !== undefined) {
      updateFields.push('priority = ?');
      updateValues.push(config.priority);
    }

    if (config.costPer1kTokens !== undefined) {
      updateFields.push('cost_per_1k_tokens = ?');
      updateValues.push(config.costPer1kTokens);
    }

    if (config.metadata !== undefined) {
      updateFields.push('metadata = ?');
      updateValues.push(this.stringifyMetadata(config.metadata));
    }

    // If no fields to update, return existing
    if (updateFields.length === 0) {
      return existing;
    }

    updateValues.push(id);

    try {
      const result = this.executeStatementRun(
        'update_provider_config',
        `UPDATE llm_providers SET ${updateFields.join(', ')} WHERE id = ?`,
        updateValues
      );

      if (result.changes === 0) {
        return null;
      }

      // Return updated provider
      return await this.findById(id);
    } catch (error) {
      this.handleConstraintError(error as Error, 'Provider');
    }
  }

  /**
   * Toggle provider active status
   */
  async toggleActive(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatementRun(
      'toggle_provider_active',
      'UPDATE llm_providers SET is_active = NOT is_active WHERE id = ?',
      [id]
    );

    return result.changes > 0;
  }

  /**
   * Find provider by ID
   */
  async findById(id: string): Promise<LLMProvider | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const row = this.executeStatement<{
      id: string;
      name: string;
      type: string;
      endpoint: string | null;
      api_key_env: string | null;
      model_name: string;
      max_tokens: number;
      temperature: number;
      is_active: number;
      priority: number;
      cost_per_1k_tokens: number | null;
      metadata: string;
    }>(
      'find_provider_by_id',
      `SELECT id, name, type, endpoint, api_key_env, model_name, max_tokens, 
              temperature, is_active, priority, cost_per_1k_tokens, metadata
       FROM llm_providers
       WHERE id = ?`,
      [id]
    );

    if (!row) {
      return null;
    }

    return await this.mapRowToProvider(row);
  }

  /**
   * Create a new provider configuration
   */
  async create(params: CreateProviderParams): Promise<LLMProvider> {
    const id = params.id || this.generateId();

    const provider: LLMProvider = {
      id,
      name: params.name,
      type: params.type,
      endpoint: params.endpoint,
      apiKeyEnv: params.apiKeyEnv,
      modelName: params.modelName,
      maxTokens: params.maxTokens,
      temperature: params.temperature !== undefined ? params.temperature : 0.7,
      isActive: params.isActive !== undefined ? params.isActive : true,
      priority: params.priority !== undefined ? params.priority : 0,
      costPer1kTokens: params.costPer1kTokens,
      metadata: params.metadata || {}
    };

    try {
      this.executeStatementRun(
        'insert_provider',
        `INSERT INTO llm_providers (
          id, name, type, endpoint, api_key_env, model_name, max_tokens,
          temperature, is_active, priority, cost_per_1k_tokens, metadata
        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)`,
        [
          provider.id,
          provider.name,
          provider.type,
          provider.endpoint || null,
          provider.apiKeyEnv || null,
          provider.modelName,
          provider.maxTokens,
          provider.temperature,
          provider.isActive ? 1 : 0,
          provider.priority,
          provider.costPer1kTokens || null,
          this.stringifyMetadata(provider.metadata)
        ]
      );

      return provider;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Provider');
    }
  }

  /**
   * Delete a provider configuration
   */
  async delete(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatementRun(
      'delete_provider',
      'DELETE FROM llm_providers WHERE id = ?',
      [id]
    );

    return result.changes > 0;
  }

  /**
   * Get all providers (active and inactive)
   */
  async findAll(): Promise<LLMProvider[]> {
    const rows = this.executeStatementAll<{
      id: string;
      name: string;
      type: string;
      endpoint: string | null;
      api_key_env: string | null;
      model_name: string;
      max_tokens: number;
      temperature: number;
      is_active: number;
      priority: number;
      cost_per_1k_tokens: number | null;
      metadata: string;
    }>(
      'find_all_providers',
      `SELECT id, name, type, endpoint, api_key_env, model_name, max_tokens, 
              temperature, is_active, priority, cost_per_1k_tokens, metadata
       FROM llm_providers
       ORDER BY priority DESC, name ASC`
    );

    const providers: LLMProvider[] = [];
    for (const row of rows) {
      const provider = await this.mapRowToProvider(row);
      providers.push(provider);
    }

    return providers;
  }

  /**
   * Count providers by type and status
   */
  async getProviderStats(): Promise<{
    total: number;
    active: number;
    local: number;
    external: number;
  }> {
    const result = this.executeStatement<{
      total: number;
      active: number;
      local: number;
      external: number;
    }>(
      'get_provider_stats',
      `SELECT 
        COUNT(*) as total,
        SUM(CASE WHEN is_active = 1 THEN 1 ELSE 0 END) as active,
        SUM(CASE WHEN type = 'local' THEN 1 ELSE 0 END) as local,
        SUM(CASE WHEN type = 'external' THEN 1 ELSE 0 END) as external
       FROM llm_providers`
    );

    return result || { total: 0, active: 0, local: 0, external: 0 };
  }

  /**
   * Check if a provider exists
   */
  async exists(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatement<{ count: number }>(
      'provider_exists',
      'SELECT 1 as count FROM llm_providers WHERE id = ? LIMIT 1',
      [id]
    );
    return !!result;
  }

  /**
   * Map database row to LLMProvider object
   * Note: API keys are stored as environment variable names, not encrypted values
   */
  private async mapRowToProvider(row: {
    id: string;
    name: string;
    type: string;
    endpoint: string | null;
    api_key_env: string | null;
    model_name: string;
    max_tokens: number;
    temperature: number;
    is_active: number;
    priority: number;
    cost_per_1k_tokens: number | null;
    metadata: string;
  }): Promise<LLMProvider> {
    return {
      id: row.id,
      name: row.name,
      type: row.type as 'local' | 'external',
      endpoint: row.endpoint || undefined,
      apiKeyEnv: row.api_key_env || undefined,
      modelName: row.model_name,
      maxTokens: row.max_tokens,
      temperature: row.temperature,
      isActive: row.is_active === 1,
      priority: row.priority,
      costPer1kTokens: row.cost_per_1k_tokens || undefined,
      metadata: this.parseMetadata(row.metadata)
    };
  }
}

/**
 * State Repository - Key-value state management
 * 
 * This repository provides:
 * - Key-value storage for application state
 * - JSON serialization/deserialization
 * - Atomic operations with transactions
 * - Batch operations for multiple keys
 * - Timestamp tracking for state changes
 */

import { PersistenceState } from '../../types/interfaces.js';
import { BaseRepository } from './BaseRepository.js';

/**
 * Interface for batch state operations
 */
export interface BatchStateOperation {
  key: string;
  value: any;
}

/**
 * Repository for key-value state management
 */
export class StateRepository extends BaseRepository {

  /**
   * Get a value by key
   */
  async get<T = any>(key: string): Promise<T | null> {
    if (!key || typeof key !== 'string') {
      return null;
    }

    const row = this.executeStatement<{
      key: string;
      value: string;
      updated_at: number;
    }>(
      'get_state',
      'SELECT key, value, updated_at FROM persistence_state WHERE key = ?',
      [key]
    );

    if (!row) {
      return null;
    }

    try {
      return JSON.parse(row.value) as T;
    } catch (error) {
      // If JSON parsing fails, return the raw string value
      return row.value as T;
    }
  }

  /**
   * Set a value for a key
   */
  async set(key: string, value: any): Promise<void> {
    if (!key || typeof key !== 'string') {
      throw new Error('Key must be a non-empty string');
    }

    const now = this.getCurrentTimestamp();
    const serializedValue = typeof value === 'string' ? value : JSON.stringify(value);

    try {
      // Use INSERT OR REPLACE for upsert behavior
      this.executeStatementRun(
        'set_state',
        `INSERT OR REPLACE INTO persistence_state (key, value, updated_at)
         VALUES (?, ?, ?)`,
        [key, serializedValue, now]
      );
    } catch (error) {
      throw new Error(`Failed to set state for key '${key}': ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Delete a key-value pair
   */
  async delete(key: string): Promise<boolean> {
    if (!key || typeof key !== 'string') {
      return false;
    }

    const result = this.executeStatementRun(
      'delete_state',
      'DELETE FROM persistence_state WHERE key = ?',
      [key]
    );

    return result.changes > 0;
  }

  /**
   * Get all key-value pairs
   */
  async getAll(): Promise<Record<string, any>> {
    const rows = this.executeStatementAll<{
      key: string;
      value: string;
      updated_at: number;
    }>(
      'get_all_state',
      'SELECT key, value, updated_at FROM persistence_state WHERE key != ? ORDER BY key',
      ['schema_version']
    );

    const result: Record<string, any> = {};
    for (const row of rows) {
      try {
        result[row.key] = JSON.parse(row.value);
      } catch (error) {
        // If JSON parsing fails, store the raw string value
        result[row.key] = row.value;
      }
    }

    return result;
  }

  /**
   * Get all keys matching a pattern
   */
  async getByPattern(pattern: string): Promise<Record<string, any>> {
    if (!pattern || typeof pattern !== 'string') {
      return {};
    }

    const rows = this.executeStatementAll<{
      key: string;
      value: string;
      updated_at: number;
    }>(
      'get_state_by_pattern',
      'SELECT key, value, updated_at FROM persistence_state WHERE key LIKE ? ORDER BY key',
      [pattern]
    );

    const result: Record<string, any> = {};
    for (const row of rows) {
      try {
        result[row.key] = JSON.parse(row.value);
      } catch (error) {
        // If JSON parsing fails, store the raw string value
        result[row.key] = row.value;
      }
    }

    return result;
  }

  /**
   * Get multiple values by keys
   */
  async getMultiple<T = any>(keys: string[]): Promise<Record<string, T | null>> {
    if (!Array.isArray(keys) || keys.length === 0) {
      return {};
    }

    // Filter out invalid keys
    const validKeys = keys.filter(key => key && typeof key === 'string');
    if (validKeys.length === 0) {
      return {};
    }

    // Create placeholders for the IN clause
    const placeholders = validKeys.map(() => '?').join(',');

    const rows = this.executeStatementAll<{
      key: string;
      value: string;
      updated_at: number;
    }>(
      `get_multiple_state_${validKeys.length}`,
      `SELECT key, value, updated_at FROM persistence_state WHERE key IN (${placeholders})`,
      validKeys
    );

    const result: Record<string, T | null> = {};

    // Initialize all requested keys with null
    for (const key of keys) {
      result[key] = null;
    }

    // Fill in the values that were found
    for (const row of rows) {
      try {
        result[row.key] = JSON.parse(row.value) as T;
      } catch (error) {
        // If JSON parsing fails, store the raw string value
        result[row.key] = row.value as T;
      }
    }

    return result;
  }

  /**
   * Set multiple key-value pairs atomically
   */
  async setMultiple(operations: BatchStateOperation[]): Promise<void> {
    if (!Array.isArray(operations) || operations.length === 0) {
      return;
    }

    const now = this.getCurrentTimestamp();

    this.transaction((db) => {
      const stmt = db.prepare(`
        INSERT OR REPLACE INTO persistence_state (key, value, updated_at)
        VALUES (?, ?, ?)
      `);

      for (const operation of operations) {
        if (!operation.key || typeof operation.key !== 'string') {
          throw new Error('All operations must have valid string keys');
        }

        const serializedValue = typeof operation.value === 'string' 
          ? operation.value 
          : JSON.stringify(operation.value);

        stmt.run(operation.key, serializedValue, now);
      }
    });
  }

  /**
   * Delete multiple keys atomically
   */
  async deleteMultiple(keys: string[]): Promise<number> {
    if (!Array.isArray(keys) || keys.length === 0) {
      return 0;
    }

    // Filter out invalid keys
    const validKeys = keys.filter(key => key && typeof key === 'string');
    if (validKeys.length === 0) {
      return 0;
    }

    return this.transaction((db) => {
      const stmt = db.prepare('DELETE FROM persistence_state WHERE key = ?');
      let deletedCount = 0;

      for (const key of validKeys) {
        const result = stmt.run(key);
        deletedCount += result.changes;
      }

      return deletedCount;
    });
  }

  /**
   * Check if a key exists
   */
  async exists(key: string): Promise<boolean> {
    if (!key || typeof key !== 'string') {
      return false;
    }

    const result = this.executeStatement<{ count: number }>(
      'state_exists',
      'SELECT COUNT(*) as count FROM persistence_state WHERE key = ? LIMIT 1',
      [key]
    );

    return result ? result.count > 0 : false;
  }

  /**
   * Get the timestamp when a key was last updated
   */
  async getTimestamp(key: string): Promise<number | null> {
    if (!key || typeof key !== 'string') {
      return null;
    }

    const row = this.executeStatement<{
      updated_at: number;
    }>(
      'get_state_timestamp',
      'SELECT updated_at FROM persistence_state WHERE key = ?',
      [key]
    );

    return row ? row.updated_at : null;
  }

  /**
   * Get all state records with metadata
   */
  async getAllWithMetadata(): Promise<PersistenceState[]> {
    const rows = this.executeStatementAll<{
      key: string;
      value: string;
      updated_at: number;
    }>(
      'get_all_state_with_metadata',
      'SELECT key, value, updated_at FROM persistence_state WHERE key != ? ORDER BY updated_at DESC',
      ['schema_version']
    );

    return rows.map(row => ({
      key: row.key,
      value: row.value,
      updatedAt: row.updated_at
    }));
  }

  /**
   * Clear all state (dangerous operation)
   */
  async clear(): Promise<number> {
    const result = this.executeStatementRun(
      'clear_all_state',
      'DELETE FROM persistence_state WHERE key != ?',
      ['schema_version']
    );

    return result.changes;
  }

  /**
   * Count total number of state keys
   */
  async count(): Promise<number> {
    const result = this.executeStatement<{ count: number }>(
      'count_state_keys',
      'SELECT COUNT(*) as count FROM persistence_state WHERE key != ?',
      ['schema_version']
    );

    return result.count;
  }

  /**
   * Get keys with pagination
   */
  async getKeys(limit?: number, offset?: number): Promise<string[]> {
    const pagination = this.validatePagination(limit, offset);

    const rows = this.executeStatementAll<{ key: string }>(
      'get_state_keys_paginated',
      'SELECT key FROM persistence_state WHERE key != ? ORDER BY key LIMIT ? OFFSET ?',
      ['schema_version', pagination.limit, pagination.offset]
    );

    return rows.map(row => row.key);
  }

  /**
   * Increment a numeric value atomically
   */
  async increment(key: string, delta: number = 1): Promise<number> {
    if (!key || typeof key !== 'string') {
      throw new Error('Key must be a non-empty string');
    }

    if (typeof delta !== 'number' || !isFinite(delta)) {
      throw new Error('Delta must be a finite number');
    }

    return this.transaction((db) => {
      // Get current value
      const currentRow = db.prepare('SELECT value FROM persistence_state WHERE key = ?').get(key) as { value: string } | undefined;

      let currentValue = 0;
      if (currentRow) {
        try {
          const parsed = JSON.parse(currentRow.value);
          if (typeof parsed === 'number' && isFinite(parsed)) {
            currentValue = parsed;
          }
        } catch (error) {
          // If parsing fails, assume 0
        }
      }

      const newValue = currentValue + delta;
      const now = this.getCurrentTimestamp();

      // Update or insert the new value
      const stmt = db.prepare(`
        INSERT OR REPLACE INTO persistence_state (key, value, updated_at)
        VALUES (?, ?, ?)
      `);
      stmt.run(key, JSON.stringify(newValue), now);

      return newValue;
    });
  }
}

/**
 * Summary History Repository - Operations for tracking summary generation
 * 
 * This repository provides:
 * - Tracking summary generation lifecycle (start, complete, failure)
 * - Provider performance statistics and monitoring
 * - Cost tracking and analysis
 * - Historical data cleanup and maintenance
 * - Generation status management
 */

import { SummaryHistory } from '../../types/interfaces.js';
import { 
  ISummaryHistoryRepository, 
  SummaryStartData, 
  SummaryCompleteResult, 
  SummaryStats 
} from '../../types/repositories.js';
import { BaseRepository } from './BaseRepository.js';

/**
 * Repository for summary generation history operations
 */
export class SummaryHistoryRepository extends BaseRepository implements ISummaryHistoryRepository {

  /**
   * Record the start of a summary generation
   */
  async recordStart(data: SummaryStartData): Promise<SummaryHistory> {
    if (!this.isValidUUID(data.summaryId)) {
      throw new Error('Invalid summary ID');
    }

    if (!this.isValidUUID(data.providerId)) {
      throw new Error('Invalid provider ID');
    }

    const id = this.generateId();
    const now = this.getCurrentTimestamp();

    const history: SummaryHistory = {
      id,
      summaryId: data.summaryId,
      providerId: data.providerId,
      startedAt: now,
      status: 'pending',
      inputTokens: data.inputTokens
    };

    try {
      this.executeStatementRun(
        'insert_summary_history',
        `INSERT INTO summary_history (
          id, summary_id, provider_id, started_at, status, input_tokens
        ) VALUES (?, ?, ?, ?, ?, ?)`,
        [
          history.id,
          history.summaryId,
          history.providerId,
          history.startedAt,
          history.status,
          history.inputTokens || null
        ]
      );

      return history;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Summary history');
    }
  }

  /**
   * Record successful completion of summary generation
   */
  async recordComplete(id: string, result: SummaryCompleteResult): Promise<SummaryHistory | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const now = this.getCurrentTimestamp();

    try {
      const updateResult = this.executeStatementRun(
        'complete_summary_history',
        `UPDATE summary_history 
         SET completed_at = ?, status = 'completed', output_tokens = ?, cost = ?
         WHERE id = ? AND status IN ('pending', 'processing')`,
        [
          now,
          result.outputTokens,
          result.cost || null,
          id
        ]
      );

      if (updateResult.changes === 0) {
        return null;
      }

      return await this.findById(id);
    } catch (error) {
      this.handleConstraintError(error as Error, 'Summary history');
    }
  }

  /**
   * Record failure of summary generation
   */
  async recordFailure(id: string, error: string): Promise<SummaryHistory | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const now = this.getCurrentTimestamp();

    try {
      const updateResult = this.executeStatementRun(
        'fail_summary_history',
        `UPDATE summary_history 
         SET completed_at = ?, status = 'failed', error_message = ?
         WHERE id = ? AND status IN ('pending', 'processing')`,
        [now, error, id]
      );

      if (updateResult.changes === 0) {
        return null;
      }

      return await this.findById(id);
    } catch (error) {
      this.handleConstraintError(error as Error, 'Summary history');
    }
  }

  /**
   * Update status to processing (when generation actually starts)
   */
  async markProcessing(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatementRun(
      'mark_processing_summary_history',
      `UPDATE summary_history 
       SET status = 'processing'
       WHERE id = ? AND status = 'pending'`,
      [id]
    );

    return result.changes > 0;
  }

  /**
   * Get generation statistics for a provider (or all providers if none specified)
   */
  async getStats(providerId?: string): Promise<SummaryStats> {
    let whereClause = '';
    const params: any[] = [];

    if (providerId) {
      if (!this.isValidUUID(providerId)) {
        throw new Error('Invalid provider ID');
      }
      whereClause = 'WHERE provider_id = ?';
      params.push(providerId);
    }

    const statsRow = this.executeStatement<{
      total_generations: number;
      successful_generations: number;
      failed_generations: number;
      average_input_tokens: number | null;
      average_output_tokens: number | null;
      total_cost: number | null;
      average_duration_ms: number | null;
    }>(
      `get_summary_stats${providerId ? '_by_provider' : ''}`,
      `SELECT 
        COUNT(*) as total_generations,
        SUM(CASE WHEN status = 'completed' THEN 1 ELSE 0 END) as successful_generations,
        SUM(CASE WHEN status = 'failed' THEN 1 ELSE 0 END) as failed_generations,
        AVG(CASE WHEN input_tokens IS NOT NULL THEN input_tokens END) as average_input_tokens,
        AVG(CASE WHEN output_tokens IS NOT NULL THEN output_tokens END) as average_output_tokens,
        SUM(CASE WHEN cost IS NOT NULL THEN cost ELSE 0 END) as total_cost,
        AVG(CASE WHEN completed_at IS NOT NULL AND started_at IS NOT NULL 
            THEN (completed_at - started_at) ELSE NULL END) as average_duration_ms
       FROM summary_history
       ${whereClause}`,
      params
    );

    return {
      totalGenerations: statsRow?.total_generations || 0,
      successfulGenerations: statsRow?.successful_generations || 0,
      failedGenerations: statsRow?.failed_generations || 0,
      averageInputTokens: statsRow?.average_input_tokens || undefined,
      averageOutputTokens: statsRow?.average_output_tokens || undefined,
      totalCost: statsRow?.total_cost || undefined,
      averageDurationMs: statsRow?.average_duration_ms || undefined
    };
  }

  /**
   * Find history entries by status
   */
  async findByStatus(
    status: SummaryHistory['status'], 
    limit: number = 50
  ): Promise<SummaryHistory[]> {
    const validatedLimit = Math.min(Math.max(limit, 1), 200);

    const rows = this.executeStatementAll<{
      id: string;
      summary_id: string;
      provider_id: string;
      started_at: number;
      completed_at: number | null;
      status: string;
      error_message: string | null;
      input_tokens: number | null;
      output_tokens: number | null;
      cost: number | null;
    }>(
      `find_history_by_status_${status}`,
      `SELECT id, summary_id, provider_id, started_at, completed_at, status,
              error_message, input_tokens, output_tokens, cost
       FROM summary_history
       WHERE status = ?
       ORDER BY started_at DESC
       LIMIT ?`,
      [status, validatedLimit]
    );

    return rows.map(row => this.mapRowToHistory(row));
  }

  /**
   * Find history entries by summary ID
   */
  async findBySummaryId(summaryId: string): Promise<SummaryHistory[]> {
    if (!this.isValidUUID(summaryId)) {
      return [];
    }

    const rows = this.executeStatementAll<{
      id: string;
      summary_id: string;
      provider_id: string;
      started_at: number;
      completed_at: number | null;
      status: string;
      error_message: string | null;
      input_tokens: number | null;
      output_tokens: number | null;
      cost: number | null;
    }>(
      'find_history_by_summary',
      `SELECT id, summary_id, provider_id, started_at, completed_at, status,
              error_message, input_tokens, output_tokens, cost
       FROM summary_history
       WHERE summary_id = ?
       ORDER BY started_at DESC`,
      [summaryId]
    );

    return rows.map(row => this.mapRowToHistory(row));
  }

  /**
   * Find history entries by provider ID
   */
  async findByProviderId(
    providerId: string, 
    limit: number = 50
  ): Promise<SummaryHistory[]> {
    if (!this.isValidUUID(providerId)) {
      return [];
    }

    const validatedLimit = Math.min(Math.max(limit, 1), 200);

    const rows = this.executeStatementAll<{
      id: string;
      summary_id: string;
      provider_id: string;
      started_at: number;
      completed_at: number | null;
      status: string;
      error_message: string | null;
      input_tokens: number | null;
      output_tokens: number | null;
      cost: number | null;
    }>(
      'find_history_by_provider',
      `SELECT id, summary_id, provider_id, started_at, completed_at, status,
              error_message, input_tokens, output_tokens, cost
       FROM summary_history
       WHERE provider_id = ?
       ORDER BY started_at DESC
       LIMIT ?`,
      [providerId, validatedLimit]
    );

    return rows.map(row => this.mapRowToHistory(row));
  }

  /**
   * Clean up old history entries
   */
  async cleanupOldEntries(olderThanDays: number): Promise<number> {
    if (olderThanDays <= 0) {
      return 0;
    }

    const cutoffTimestamp = this.getCurrentTimestamp() - (olderThanDays * 24 * 60 * 60 * 1000);

    const result = this.executeStatementRun(
      'cleanup_old_history',
      'DELETE FROM summary_history WHERE started_at < ?',
      [cutoffTimestamp]
    );

    return result.changes;
  }

  /**
   * Find a history entry by ID
   */
  async findById(id: string): Promise<SummaryHistory | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const row = this.executeStatement<{
      id: string;
      summary_id: string;
      provider_id: string;
      started_at: number;
      completed_at: number | null;
      status: string;
      error_message: string | null;
      input_tokens: number | null;
      output_tokens: number | null;
      cost: number | null;
    }>(
      'find_history_by_id',
      `SELECT id, summary_id, provider_id, started_at, completed_at, status,
              error_message, input_tokens, output_tokens, cost
       FROM summary_history
       WHERE id = ?`,
      [id]
    );

    if (!row) {
      return null;
    }

    return this.mapRowToHistory(row);
  }

  /**
   * Get recent history entries across all providers
   */
  async findRecent(limit: number = 20): Promise<SummaryHistory[]> {
    const validatedLimit = Math.min(Math.max(limit, 1), 100);

    const rows = this.executeStatementAll<{
      id: string;
      summary_id: string;
      provider_id: string;
      started_at: number;
      completed_at: number | null;
      status: string;
      error_message: string | null;
      input_tokens: number | null;
      output_tokens: number | null;
      cost: number | null;
    }>(
      'find_recent_history',
      `SELECT id, summary_id, provider_id, started_at, completed_at, status,
              error_message, input_tokens, output_tokens, cost
       FROM summary_history
       ORDER BY started_at DESC
       LIMIT ?`,
      [validatedLimit]
    );

    return rows.map(row => this.mapRowToHistory(row));
  }

  /**
   * Count history entries by status
   */
  async countByStatus(): Promise<Record<string, number>> {
    const rows = this.executeStatementAll<{
      status: string;
      count: number;
    }>(
      'count_history_by_status',
      `SELECT status, COUNT(*) as count
       FROM summary_history
       GROUP BY status`
    );

    const result: Record<string, number> = {};
    for (const row of rows) {
      result[row.status] = row.count;
    }

    return result;
  }

  /**
   * Check if a history entry exists
   */
  async exists(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatement<{ count: number }>(
      'history_exists',
      'SELECT 1 as count FROM summary_history WHERE id = ? LIMIT 1',
      [id]
    );
    return !!result;
  }

  /**
   * Map database row to SummaryHistory object
   */
  private mapRowToHistory(row: {
    id: string;
    summary_id: string;
    provider_id: string;
    started_at: number;
    completed_at: number | null;
    status: string;
    error_message: string | null;
    input_tokens: number | null;
    output_tokens: number | null;
    cost: number | null;
  }): SummaryHistory {
    return {
      id: row.id,
      summaryId: row.summary_id,
      providerId: row.provider_id,
      startedAt: row.started_at,
      completedAt: row.completed_at || undefined,
      status: row.status as SummaryHistory['status'],
      errorMessage: row.error_message || undefined,
      inputTokens: row.input_tokens || undefined,
      outputTokens: row.output_tokens || undefined,
      cost: row.cost || undefined
    };
  }
}

/**
 * Summary Repository - CRUD operations for conversation summaries
 * 
 * This repository provides:
 * - Full CRUD operations for conversation summaries
 * - Efficient querying by conversation and level
 * - Batch operations for creating multiple summaries
 * - Summary invalidation and cleanup
 * - Quality score tracking and filtering
 */

import { ConversationSummary, PaginatedResult } from '../../types/interfaces.js';
import { BaseRepository } from './BaseRepository.js';

/**
 * Interface for creating summary parameters
 */
export interface CreateSummaryParams {
  id?: string;
  conversationId: string;
  level: 'brief' | 'standard' | 'detailed';
  summaryText: string;
  tokenCount: number;
  provider: string;
  model: string;
  messageCount: number;
  startMessageId?: string;
  endMessageId?: string;
  metadata?: Record<string, any>;
  qualityScore?: number;
}

/**
 * Interface for updating summary parameters
 */
export interface UpdateSummaryParams {
  summaryText?: string;
  tokenCount?: number;
  metadata?: Record<string, any>;
  qualityScore?: number;
}

/**
 * Interface for summary batch creation
 */
export interface BatchSummaryParams {
  summaries: CreateSummaryParams[];
}

/**
 * Repository for conversation summary CRUD operations
 */
export class SummaryRepository extends BaseRepository {

  /**
   * Create a new conversation summary
   */
  async create(params: CreateSummaryParams): Promise<ConversationSummary> {
    const id = params.id || this.generateId();
    const now = this.getCurrentTimestamp();

    const summary: ConversationSummary = {
      id,
      conversationId: params.conversationId,
      level: params.level,
      summaryText: params.summaryText,
      tokenCount: params.tokenCount,
      provider: params.provider,
      model: params.model,
      generatedAt: now,
      messageCount: params.messageCount,
      startMessageId: params.startMessageId,
      endMessageId: params.endMessageId,
      metadata: params.metadata || {},
      qualityScore: params.qualityScore
    };

    try {
      this.executeStatementRun(
        'insert_summary',
        `INSERT INTO conversation_summaries (
          id, conversation_id, level, summary_text, token_count, provider, model,
          generated_at, message_count, start_message_id, end_message_id, metadata, quality_score
        ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)`,
        [
          summary.id,
          summary.conversationId,
          summary.level,
          summary.summaryText,
          summary.tokenCount,
          summary.provider,
          summary.model,
          summary.generatedAt,
          summary.messageCount,
          summary.startMessageId || null,
          summary.endMessageId || null,
          this.stringifyMetadata(summary.metadata),
          summary.qualityScore || null
        ]
      );

      return summary;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Summary');
    }
  }

  /**
   * Find summaries by conversation ID with optional level filter
   */
  async findByConversation(
    conversationId: string,
    level?: 'brief' | 'standard' | 'detailed',
    limit?: number,
    offset?: number
  ): Promise<PaginatedResult<ConversationSummary>> {
    if (!this.isValidUUID(conversationId)) {
      return { data: [], hasMore: false };
    }

    const pagination = this.validatePagination(limit, offset);

    let whereClause = 'WHERE conversation_id = ?';
    const params: any[] = [conversationId];

    if (level) {
      whereClause += ' AND level = ?';
      params.push(level);
    }

    params.push(pagination.limit + 1, pagination.offset);

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      level: string;
      summary_text: string;
      token_count: number;
      provider: string;
      model: string;
      generated_at: number;
      message_count: number;
      start_message_id: string | null;
      end_message_id: string | null;
      metadata: string;
      quality_score: number | null;
    }>(
      `find_summaries_by_conversation_${level || 'all'}`,
      `SELECT id, conversation_id, level, summary_text, token_count, provider, model,
              generated_at, message_count, start_message_id, end_message_id, metadata, quality_score
       FROM conversation_summaries
       ${whereClause}
       ORDER BY generated_at DESC
       LIMIT ? OFFSET ?`,
      params
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => this.mapRowToSummary(row));

    return {
      data,
      hasMore
    };
  }

  /**
   * Find the most recent summary for a conversation at a specific level
   */
  async findByConversationAndLevel(
    conversationId: string,
    level: 'brief' | 'standard' | 'detailed'
  ): Promise<ConversationSummary | null> {
    if (!this.isValidUUID(conversationId)) {
      return null;
    }

    const row = this.executeStatement<{
      id: string;
      conversation_id: string;
      level: string;
      summary_text: string;
      token_count: number;
      provider: string;
      model: string;
      generated_at: number;
      message_count: number;
      start_message_id: string | null;
      end_message_id: string | null;
      metadata: string;
      quality_score: number | null;
    }>(
      `find_summary_by_conversation_and_level_${level}`,
      `SELECT id, conversation_id, level, summary_text, token_count, provider, model,
              generated_at, message_count, start_message_id, end_message_id, metadata, quality_score
       FROM conversation_summaries
       WHERE conversation_id = ? AND level = ?
       ORDER BY generated_at DESC
       LIMIT 1`,
      [conversationId, level]
    );

    if (!row) {
      return null;
    }

    return this.mapRowToSummary(row);
  }

  /**
   * Find recent summaries across all conversations
   */
  async findRecent(
    limit?: number,
    minQualityScore?: number
  ): Promise<ConversationSummary[]> {
    const validatedLimit = Math.min(limit || 50, 100);

    let whereClause = '';
    const params: any[] = [];

    if (minQualityScore !== undefined) {
      whereClause = 'WHERE quality_score >= ?';
      params.push(minQualityScore);
    }

    params.push(validatedLimit);

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      level: string;
      summary_text: string;
      token_count: number;
      provider: string;
      model: string;
      generated_at: number;
      message_count: number;
      start_message_id: string | null;
      end_message_id: string | null;
      metadata: string;
      quality_score: number | null;
    }>(
      `find_recent_summaries${minQualityScore !== undefined ? '_with_quality' : ''}`,
      `SELECT id, conversation_id, level, summary_text, token_count, provider, model,
              generated_at, message_count, start_message_id, end_message_id, metadata, quality_score
       FROM conversation_summaries
       ${whereClause}
       ORDER BY generated_at DESC
       LIMIT ?`,
      params
    );

    return rows.map(row => this.mapRowToSummary(row));
  }

  /**
   * Create multiple summaries in a single transaction
   */
  async createBatch(params: BatchSummaryParams): Promise<ConversationSummary[]> {
    if (!params.summaries || params.summaries.length === 0) {
      return [];
    }

    return this.transaction((_db) => {
      const createdSummaries: ConversationSummary[] = [];

      for (const summaryParams of params.summaries) {
        const id = summaryParams.id || this.generateId();
        const now = this.getCurrentTimestamp();

        const summary: ConversationSummary = {
          id,
          conversationId: summaryParams.conversationId,
          level: summaryParams.level,
          summaryText: summaryParams.summaryText,
          tokenCount: summaryParams.tokenCount,
          provider: summaryParams.provider,
          model: summaryParams.model,
          generatedAt: now,
          messageCount: summaryParams.messageCount,
          startMessageId: summaryParams.startMessageId,
          endMessageId: summaryParams.endMessageId,
          metadata: summaryParams.metadata || {},
          qualityScore: summaryParams.qualityScore
        };

        try {
          this.executeStatementRun(
            'insert_summary_batch',
            `INSERT INTO conversation_summaries (
              id, conversation_id, level, summary_text, token_count, provider, model,
              generated_at, message_count, start_message_id, end_message_id, metadata, quality_score
            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)`,
            [
              summary.id,
              summary.conversationId,
              summary.level,
              summary.summaryText,
              summary.tokenCount,
              summary.provider,
              summary.model,
              summary.generatedAt,
              summary.messageCount,
              summary.startMessageId || null,
              summary.endMessageId || null,
              this.stringifyMetadata(summary.metadata),
              summary.qualityScore || null
            ]
          );

          createdSummaries.push(summary);
        } catch (error) {
          this.handleConstraintError(error as Error, 'Summary');
        }
      }

      return createdSummaries;
    });
  }

  /**
   * Invalidate/delete all summaries for a conversation
   */
  async invalidateForConversation(conversationId: string): Promise<number> {
    if (!this.isValidUUID(conversationId)) {
      return 0;
    }

    const result = this.executeStatementRun(
      'delete_summaries_by_conversation',
      'DELETE FROM conversation_summaries WHERE conversation_id = ?',
      [conversationId]
    );

    return result.changes;
  }

  /**
   * Find a summary by ID
   */
  async findById(id: string): Promise<ConversationSummary | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const row = this.executeStatement<{
      id: string;
      conversation_id: string;
      level: string;
      summary_text: string;
      token_count: number;
      provider: string;
      model: string;
      generated_at: number;
      message_count: number;
      start_message_id: string | null;
      end_message_id: string | null;
      metadata: string;
      quality_score: number | null;
    }>(
      'find_summary_by_id',
      `SELECT id, conversation_id, level, summary_text, token_count, provider, model,
              generated_at, message_count, start_message_id, end_message_id, metadata, quality_score
       FROM conversation_summaries
       WHERE id = ?`,
      [id]
    );

    if (!row) {
      return null;
    }

    return this.mapRowToSummary(row);
  }

  /**
   * Update a summary
   */
  async update(id: string, params: UpdateSummaryParams): Promise<ConversationSummary | null> {
    if (!this.isValidUUID(id)) {
      return null;
    }

    const existing = await this.findById(id);
    if (!existing) {
      return null;
    }

    const updatedSummary: ConversationSummary = {
      ...existing,
      summaryText: params.summaryText !== undefined ? params.summaryText : existing.summaryText,
      tokenCount: params.tokenCount !== undefined ? params.tokenCount : existing.tokenCount,
      metadata: params.metadata !== undefined ? params.metadata : existing.metadata,
      qualityScore: params.qualityScore !== undefined ? params.qualityScore : existing.qualityScore
    };

    try {
      const result = this.executeStatementRun(
        'update_summary',
        `UPDATE conversation_summaries 
         SET summary_text = ?, token_count = ?, metadata = ?, quality_score = ?
         WHERE id = ?`,
        [
          updatedSummary.summaryText,
          updatedSummary.tokenCount,
          this.stringifyMetadata(updatedSummary.metadata),
          updatedSummary.qualityScore || null,
          id
        ]
      );

      if (result.changes === 0) {
        return null;
      }

      return updatedSummary;
    } catch (error) {
      this.handleConstraintError(error as Error, 'Summary');
    }
  }

  /**
   * Delete a summary
   */
  async delete(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatementRun(
      'delete_summary',
      'DELETE FROM conversation_summaries WHERE id = ?',
      [id]
    );

    return result.changes > 0;
  }

  /**
   * Get summaries by provider
   */
  async findByProvider(
    provider: string,
    limit?: number,
    offset?: number
  ): Promise<PaginatedResult<ConversationSummary>> {
    const pagination = this.validatePagination(limit, offset);

    const rows = this.executeStatementAll<{
      id: string;
      conversation_id: string;
      level: string;
      summary_text: string;
      token_count: number;
      provider: string;
      model: string;
      generated_at: number;
      message_count: number;
      start_message_id: string | null;
      end_message_id: string | null;
      metadata: string;
      quality_score: number | null;
    }>(
      'find_summaries_by_provider',
      `SELECT id, conversation_id, level, summary_text, token_count, provider, model,
              generated_at, message_count, start_message_id, end_message_id, metadata, quality_score
       FROM conversation_summaries
       WHERE provider = ?
       ORDER BY generated_at DESC
       LIMIT ? OFFSET ?`,
      [provider, pagination.limit + 1, pagination.offset]
    );

    const hasMore = rows.length > pagination.limit;
    const data = rows.slice(0, pagination.limit).map(row => this.mapRowToSummary(row));

    return {
      data,
      hasMore
    };
  }

  /**
   * Count summaries by conversation
   */
  async countByConversation(conversationId: string): Promise<number> {
    if (!this.isValidUUID(conversationId)) {
      return 0;
    }

    const result = this.executeStatement<{ count: number }>(
      'count_summaries_by_conversation',
      'SELECT COUNT(*) as count FROM conversation_summaries WHERE conversation_id = ?',
      [conversationId]
    );

    return result.count;
  }

  /**
   * Check if a summary exists
   */
  async exists(id: string): Promise<boolean> {
    if (!this.isValidUUID(id)) {
      return false;
    }

    const result = this.executeStatement<{ count: number }>(
      'summary_exists',
      'SELECT 1 as count FROM conversation_summaries WHERE id = ? LIMIT 1',
      [id]
    );
    return !!result;
  }

  /**
   * Map database row to ConversationSummary object
   */
  private mapRowToSummary(row: {
    id: string;
    conversation_id: string;
    level: string;
    summary_text: string;
    token_count: number;
    provider: string;
    model: string;
    generated_at: number;
    message_count: number;
    start_message_id: string | null;
    end_message_id: string | null;
    metadata: string;
    quality_score: number | null;
  }): ConversationSummary {
    return {
      id: row.id,
      conversationId: row.conversation_id,
      level: row.level as 'brief' | 'standard' | 'detailed',
      summaryText: row.summary_text,
      tokenCount: row.token_count,
      provider: row.provider,
      model: row.model,
      generatedAt: row.generated_at,
      messageCount: row.message_count,
      startMessageId: row.start_message_id || undefined,
      endMessageId: row.end_message_id || undefined,
      metadata: this.parseMetadata(row.metadata),
      qualityScore: row.quality_score || undefined
    };
  }
}

/**
 * Trigger Validation Monitor
 * 
 * Utility for monitoring database validation trigger performance and effectiveness:
 * - Track trigger execution times
 * - Monitor validation errors and patterns
 * - Provide insights for trigger optimization
 * - Validate trigger logic consistency
 */

import Database from 'better-sqlite3';

export interface TriggerPerformanceStats {
  triggerName: string;
  tableName: string;
  operationType: 'INSERT' | 'UPDATE' | 'DELETE';
  totalExecutions: number;
  averageExecutionTime: number;
  maxExecutionTime: number;
  minExecutionTime: number;
  errorCount: number;
  lastExecution: number;
}

export interface ValidationError {
  triggerName: string;
  tableName: string;
  operation: string;
  errorMessage: string;
  timestamp: number;
  data?: any;
}

export class TriggerValidationMonitor {
  private db: Database;

  constructor(db: Database) {
    this.db = db;
  }

  /**
   * Log trigger performance for monitoring
   */
  logTriggerExecution(
    triggerName: string,
    tableName: string,
    operation: 'INSERT' | 'UPDATE' | 'DELETE',
    executionTimeMs: number,
    errorMessage?: string
  ): void {
    try {
      const stmt = this.db.prepare(`
        INSERT INTO trigger_performance_log (
          trigger_name, table_name, operation, execution_time_ms, error_message, created_at
        ) VALUES (?, ?, ?, ?, ?, ?)
      `);

      stmt.run(
        triggerName,
        tableName,
        operation,
        executionTimeMs,
        errorMessage || null,
        Date.now()
      );
    } catch (error) {
      console.error('Failed to log trigger performance:', error);
    }
  }

  /**
   * Get trigger performance statistics
   */
  getTriggerPerformanceStats(days: number = 7): TriggerPerformanceStats[] {
    const since = Date.now() - (days * 24 * 60 * 60 * 1000);

    const stmt = this.db.prepare(`
      SELECT 
        trigger_name,
        table_name,
        operation,
        COUNT(*) as total_executions,
        AVG(execution_time_ms) as avg_execution_time,
        MAX(execution_time_ms) as max_execution_time,
        MIN(execution_time_ms) as min_execution_time,
        COUNT(CASE WHEN error_message IS NOT NULL THEN 1 END) as error_count,
        MAX(created_at) as last_execution
      FROM trigger_performance_log
      WHERE created_at >= ?
      GROUP BY trigger_name, table_name, operation
      ORDER BY avg_execution_time DESC
    `);

    const rows = stmt.all(since);

    return rows.map((row: any) => ({
      triggerName: row.trigger_name,
      tableName: row.table_name,
      operationType: row.operation,
      totalExecutions: row.total_executions,
      averageExecutionTime: row.avg_execution_time,
      maxExecutionTime: row.max_execution_time,
      minExecutionTime: row.min_execution_time,
      errorCount: row.error_count,
      lastExecution: row.last_execution
    }));
  }

  /**
   * Get slow-performing triggers that may need optimization
   */
  getSlowTriggers(thresholdMs: number = 100): TriggerPerformanceStats[] {
    const stats = this.getTriggerPerformanceStats(30); // Look at last 30 days
    return stats.filter(stat => stat.averageExecutionTime > thresholdMs);
  }

  /**
   * Get trigger error patterns
   */
  getValidationErrors(days: number = 7): ValidationError[] {
    const since = Date.now() - (days * 24 * 60 * 60 * 1000);

    const stmt = this.db.prepare(`
      SELECT 
        trigger_name,
        table_name,
        operation,
        error_message,
        created_at
      FROM trigger_performance_log
      WHERE error_message IS NOT NULL 
        AND created_at >= ?
      ORDER BY created_at DESC
    `);

    const rows = stmt.all(since);

    return rows.map((row: any) => ({
      triggerName: row.trigger_name,
      tableName: row.table_name,
      operation: row.operation,
      errorMessage: row.error_message,
      timestamp: row.created_at
    }));
  }

  /**
   * Test validation triggers with known good and bad data
   */
  async runValidationTests(): Promise<{
    passed: number;
    failed: number;
    errors: Array<{ test: string; error: string }>;
  }> {
    const testResults = {
      passed: 0,
      failed: 0,
      errors: [] as Array<{ test: string; error: string }>
    };

    const tests = [
      // Test 1: Decision temporal sequence validation
      {
        name: 'Decision temporal sequence - valid',
        test: async () => {
          const now = Date.now();
          const stmt = this.db.prepare(`
            INSERT INTO decision_tracking (
              id, decision_summary, decision_made_at, problem_identified_at
            ) VALUES (?, ?, ?, ?)
          `);
          stmt.run('test-decision-1', 'Test decision', now, now - 1000);
        },
        shouldFail: false
      },
      {
        name: 'Decision temporal sequence - invalid',
        test: async () => {
          const now = Date.now();
          const stmt = this.db.prepare(`
            INSERT INTO decision_tracking (
              id, decision_summary, decision_made_at, problem_identified_at
            ) VALUES (?, ?, ?, ?)
          `);
          stmt.run('test-decision-2', 'Test decision', now, now + 1000);
        },
        shouldFail: true
      },
      // Test 2: Knowledge gap resolution validation
      {
        name: 'Knowledge gap resolution - valid',
        test: async () => {
          const stmt = this.db.prepare(`
            INSERT INTO knowledge_gaps (
              id, gap_type, content, normalized_content, first_occurrence, last_occurrence, 
              resolved, resolution_date, resolution_conversation_id
            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
          `);
          stmt.run(
            'test-gap-1', 'question', 'Test gap', 'test gap', 
            Date.now() - 1000, Date.now(), true, Date.now(), 'test-conv-1'
          );
        },
        shouldFail: false
      },
      {
        name: 'Knowledge gap resolution - invalid (missing date)',
        test: async () => {
          const stmt = this.db.prepare(`
            INSERT INTO knowledge_gaps (
              id, gap_type, content, normalized_content, first_occurrence, last_occurrence, 
              resolved, resolution_date, resolution_conversation_id
            ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)
          `);
          stmt.run(
            'test-gap-2', 'question', 'Test gap', 'test gap',
            Date.now() - 1000, Date.now(), true, null, 'test-conv-1'
          );
        },
        shouldFail: true
      },
      // Test 3: Productivity pattern window validation
      {
        name: 'Productivity pattern window - valid',
        test: async () => {
          const stmt = this.db.prepare(`
            INSERT INTO productivity_patterns (
              id, window_start, window_end, window_type, total_conversations, total_messages
            ) VALUES (?, ?, ?, ?, ?, ?)
          `);
          stmt.run('test-pattern-1', Date.now() - 1000, Date.now(), 'hour', 5, 20);
        },
        shouldFail: false
      },
      {
        name: 'Productivity pattern window - invalid',
        test: async () => {
          const stmt = this.db.prepare(`
            INSERT INTO productivity_patterns (
              id, window_start, window_end, window_type, total_conversations, total_messages
            ) VALUES (?, ?, ?, ?, ?, ?)
          `);
          stmt.run('test-pattern-2', Date.now(), Date.now() - 1000, 'hour', 5, 20);
        },
        shouldFail: true
      },
      // Test 4: Score range validation
      {
        name: 'Analytics score range - valid',
        test: async () => {
          const stmt = this.db.prepare(`
            INSERT INTO conversation_analytics (
              id, conversation_id, analyzed_at, depth_score, circularity_index, productivity_score
            ) VALUES (?, ?, ?, ?, ?, ?)
          `);
          stmt.run('test-analytics-1', 'test-conv-1', Date.now(), 75.5, 0.3, 82.0);
        },
        shouldFail: false
      },
      {
        name: 'Analytics score range - invalid depth_score',
        test: async () => {
          const stmt = this.db.prepare(`
            INSERT INTO conversation_analytics (
              id, conversation_id, analyzed_at, depth_score, circularity_index, productivity_score
            ) VALUES (?, ?, ?, ?, ?, ?)
          `);
          stmt.run('test-analytics-2', 'test-conv-1', Date.now(), 150.0, 0.3, 82.0);
        },
        shouldFail: true
      }
    ];

    // Setup test data
    await this.setupTestData();

    // Run tests
    for (const testCase of tests) {
      try {
        const startTime = Date.now();
        await testCase.test();
        const executionTime = Date.now() - startTime;

        if (testCase.shouldFail) {
          testResults.failed++;
          testResults.errors.push({
            test: testCase.name,
            error: 'Test should have failed but passed'
          });
        } else {
          testResults.passed++;
          this.logTriggerExecution(
            'validation_test', 
            'test', 
            'INSERT', 
            executionTime
          );
        }
      } catch (error) {
        const executionTime = Date.now() - Date.now();

        if (testCase.shouldFail) {
          testResults.passed++;
          this.logTriggerExecution(
            'validation_test', 
            'test', 
            'INSERT', 
            executionTime,
            (error as Error).message
          );
        } else {
          testResults.failed++;
          testResults.errors.push({
            test: testCase.name,
            error: (error as Error).message
          });
        }
      }
    }

    // Cleanup test data
    await this.cleanupTestData();

    return testResults;
  }

  /**
   * Generate validation trigger performance report
   */
  generatePerformanceReport(): string {
    const stats = this.getTriggerPerformanceStats(30);
    const errors = this.getValidationErrors(7);
    const slowTriggers = this.getSlowTriggers(50);

    let report = '# Validation Trigger Performance Report\n\n';

    report += '## Summary\n';
    report += `- Total triggers monitored: ${stats.length}\n`;
    report += `- Total executions (30 days): ${stats.reduce((sum, s) => sum + s.totalExecutions, 0)}\n`;
    report += `- Total errors (7 days): ${errors.length}\n`;
    report += `- Slow triggers (>50ms): ${slowTriggers.length}\n\n`;

    if (slowTriggers.length > 0) {
      report += '## Slow Triggers (Performance Issues)\n';
      report += '| Trigger | Table | Operation | Avg Time (ms) | Executions |\n';
      report += '|---------|-------|-----------|---------------|------------|\n';

      for (const trigger of slowTriggers) {
        report += `| ${trigger.triggerName} | ${trigger.tableName} | ${trigger.operationType} | ${trigger.averageExecutionTime.toFixed(2)} | ${trigger.totalExecutions} |\n`;
      }
      report += '\n';
    }

    if (errors.length > 0) {
      report += '## Recent Validation Errors\n';
      report += '| Trigger | Table | Error | Time |\n';
      report += '|---------|-------|-------|------|\n';

      for (const error of errors.slice(0, 10)) {
        const timeStr = new Date(error.timestamp).toISOString();
        report += `| ${error.triggerName} | ${error.tableName} | ${error.errorMessage} | ${timeStr} |\n`;
      }
      report += '\n';
    }

    report += '## All Trigger Statistics\n';
    report += '| Trigger | Table | Operation | Avg Time (ms) | Executions | Errors |\n';
    report += '|---------|-------|-----------|---------------|------------|--------|\n';

    for (const stat of stats) {
      report += `| ${stat.triggerName} | ${stat.tableName} | ${stat.operationType} | ${stat.averageExecutionTime.toFixed(2)} | ${stat.totalExecutions} | ${stat.errorCount} |\n`;
    }

    return report;
  }

  /**
   * Clean up old performance logs to prevent table bloat
   */
  cleanupOldLogs(olderThanDays: number = 90): number {
    const cutoff = Date.now() - (olderThanDays * 24 * 60 * 60 * 1000);

    const stmt = this.db.prepare(`
      DELETE FROM trigger_performance_log 
      WHERE created_at < ?
    `);

    const result = stmt.run(cutoff);
    return result.changes;
  }

  /**
   * Setup test data for validation tests
   */
  private async setupTestData(): Promise<void> {
    try {
      // Create test conversation
      const convStmt = this.db.prepare(`
        INSERT OR IGNORE INTO conversations (id, created_at, updated_at) 
        VALUES (?, ?, ?)
      `);
      convStmt.run('test-conv-1', Date.now() - 10000, Date.now());
    } catch (error) {
      // Ignore if already exists
    }
  }

  /**
   * Cleanup test data after validation tests
   */
  private async cleanupTestData(): Promise<void> {
    const tables = [
      'decision_tracking',
      'knowledge_gaps',
      'productivity_patterns',
      'conversation_analytics'
    ];

    for (const table of tables) {
      try {
        const stmt = this.db.prepare(`DELETE FROM ${table} WHERE id LIKE 'test-%'`);
        stmt.run();
      } catch (error) {
        // Ignore cleanup errors
      }
    }

    try {
      const stmt = this.db.prepare(`DELETE FROM conversations WHERE id LIKE 'test-%'`);
      stmt.run();
    } catch (error) {
      // Ignore cleanup errors
    }
  }
}

/**
 * Utility function to create and run validation tests
 */
export async function runTriggerValidationTests(db: Database): Promise<void> {
  const monitor = new TriggerValidationMonitor(db);

  console.log('Running validation trigger tests...');
  const results = await monitor.runValidationTests();

  console.log(`\nTest Results:`);
  console.log(`  Passed: ${results.passed}`);
  console.log(`  Failed: ${results.failed}`);

  if (results.errors.length > 0) {
    console.log(`\nErrors:`);
    for (const error of results.errors) {
      console.log(`  - ${error.test}: ${error.error}`);
    }
  }

  console.log('\nGenerating performance report...');
  const report = monitor.generatePerformanceReport();
  console.log(report);
}

/**
 * Analyze Productivity Patterns Tool Implementation
 * 
 * This tool analyzes productivity patterns across conversations to identify:
 * - Peak productivity hours and time-based patterns
 * - Optimal session length and timing
 * - Most effective question patterns and types
 * - Productivity trends over time
 * - Correlation between conversation characteristics and outcomes
 */

import { AnalyzeProductivityPatternsToolDef as AnalyzeProductivityPatternsToolDef } from '../types/mcp.js';
import { AnalyzeProductivityPatternsSchema, AnalyzeProductivityPatternsInput } from '../types/schemas.js';
import { BaseTool, ToolContext, wrapDatabaseOperation } from './BaseTool.js';
import { AnalyticsEngine } from '../analytics/services/AnalyticsEngine.js';
import { ProductivityAnalyzer } from '../analytics/analyzers/ProductivityAnalyzer.js';
import { ConversationRepository } from '../storage/repositories/ConversationRepository.js';
import { MessageRepository } from '../storage/repositories/MessageRepository.js';
import { ProductivityPatternsRepository } from '../analytics/repositories/ProductivityPatternsRepository.js';
import { TimeRange } from '../analytics/repositories/AnalyticsRepository.js';
import { 
  validateDateRange, 
  validateConversationIds,
  validateGranularity,
  ValidationError,
  formatValidationError,
  withEnhancedValidation 
} from '../utils/validation.js';

/**
 * Productivity pattern analysis result
 */
export interface ProductivityPattern {
  /** Pattern identifier */
  id: string;
  /** Pattern type */
  type: 'peak_hour' | 'session_length' | 'question_type' | 'temporal_trend';
  /** Pattern description */
  description: string;
  /** Pattern strength/confidence (0-1) */
  confidence: number;
  /** Supporting data */
  data: any;
  /** Pattern impact score (0-100) */
  impact: number;
}

/**
 * Session analysis result
 */
export interface SessionAnalysis {
  /** Optimal session length in minutes */
  optimalLength: number;
  /** Average session length */
  averageLength: number;
  /** Session length distribution */
  lengthDistribution: { [range: string]: number };
  /** Productivity by session length */
  productivityByLength: { length: number; productivity: number }[];
}

/**
 * Question pattern analysis result
 */
export interface QuestionPatternAnalysis {
  /** Top question patterns by effectiveness */
  topPatterns: Array<{
    pattern: string;
    frequency: number;
    effectiveness: number;
    examples: string[];
  }>;
  /** Question types analysis */
  questionTypes: Array<{
    type: string;
    count: number;
    avgProductivity: number;
  }>;
  /** Best practices identified */
  bestPractices: string[];
}

/**
 * Response interface for analyze_productivity_patterns tool
 */
export interface AnalyzeProductivityPatternsResponse {
  /** Time range analyzed */
  timeRange: TimeRange;
  /** When the analysis was performed */
  analyzedAt: number;

  /** Peak productivity hours (0-23) */
  peakHours: number[];

  /** Session analysis results */
  sessionAnalysis: SessionAnalysis;

  /** Question pattern analysis */
  questionPatterns: QuestionPatternAnalysis;

  /** Identified productivity patterns */
  patterns: ProductivityPattern[];

  /** Temporal trends */
  trends: {
    /** Weekly productivity trend (positive = improving) */
    weeklyTrend: number;
    /** Monthly productivity trend */
    monthlyTrend: number;
    /** Productivity by day of week */
    byDayOfWeek: { [day: string]: number };
    /** Productivity by hour of day */
    byHourOfDay: { [hour: number]: number };
  };

  /** Insights and recommendations */
  insights: {
    /** Key insights discovered */
    keyInsights: string[];
    /** Actionable recommendations */
    recommendations: string[];
    /** Areas of concern */
    concerns: string[];
  };

  /** Analysis metadata */
  metadata: {
    /** Number of conversations analyzed */
    conversationCount: number;
    /** Total messages analyzed */
    messageCount: number;
    /** Analysis duration in milliseconds */
    analysisDuration: number;
    /** Granularity used */
    granularity: string;
    /** Components included */
    componentsIncluded: string[];
  };
}

/**
 * Dependencies required by AnalyzeProductivityPatternsTool
 */
export interface AnalyzeProductivityPatternsDependencies {
  analyticsEngine: AnalyticsEngine;
  conversationRepository: ConversationRepository;
  messageRepository: MessageRepository;
  productivityAnalyzer: ProductivityAnalyzer;
  productivityPatternsRepository: ProductivityPatternsRepository;
}

/**
 * Implementation of the analyze_productivity_patterns MCP tool
 */
export class AnalyzeProductivityPatternsTool extends BaseTool<AnalyzeProductivityPatternsInput, AnalyzeProductivityPatternsResponse> {
  private readonly analyticsEngine: AnalyticsEngine;
  private readonly conversationRepository: ConversationRepository;
  private readonly messageRepository: MessageRepository;
  private readonly productivityAnalyzer: ProductivityAnalyzer;
  private readonly productivityPatternsRepository: ProductivityPatternsRepository;

  constructor(dependencies: AnalyzeProductivityPatternsDependencies) {
    super(AnalyzeProductivityPatternsToolDef, AnalyzeProductivityPatternsSchema);
    this.analyticsEngine = dependencies.analyticsEngine;
    this.conversationRepository = dependencies.conversationRepository;
    this.messageRepository = dependencies.messageRepository;
    this.productivityAnalyzer = dependencies.productivityAnalyzer;
    this.productivityPatternsRepository = dependencies.productivityPatternsRepository;
  }

  /**
   * Execute the analyze_productivity_patterns tool
   */
  protected async executeImpl(input: AnalyzeProductivityPatternsInput, _context: ToolContext): Promise<AnalyzeProductivityPatternsResponse> {
    const startTime = Date.now();
    const componentsIncluded: string[] = [];

    try {
      // Step 1: Enhanced validation with comprehensive input checking
      const validatedInput = withEnhancedValidation(() => {
        // Validate time range with 30-day default for productivity analysis
        const timeRange = validateDateRange(input.startDate, input.endDate, '', {
          maxDays: 180, // Allow up to 6 months for productivity pattern analysis
          defaultDays: 30 // Default to 30 days for focused analysis
        });

        // Calculate time range in days for granularity validation
        const rangeDays = (timeRange.end - timeRange.start) / (1000 * 60 * 60 * 24);

        // Validate conversation IDs if provided
        const conversationIds = validateConversationIds(input.conversationIds, 'conversationIds', 100); // Limit to 100 for performance

        // Validate granularity with business rules
        const granularity = validateGranularity(input.granularity, rangeDays);

        return { 
          timeRange, 
          conversationIds, 
          granularity,
          includePeakHours: input.includePeakHours,
          includeSessionAnalysis: input.includeSessionAnalysis,
          includeQuestionPatterns: input.includeQuestionPatterns
        };
      }, 'productivity patterns input validation');

      // Step 2: Get conversations and messages for analysis
      const { conversations, messages } = await this.getAnalysisData(
        validatedInput.conversationIds, 
        validatedInput.timeRange
      );

      if (conversations.length === 0) {
        // Return empty analysis if no conversations found
        return this.createEmptyResponse(validatedInput.timeRange, validatedInput.granularity, startTime);
      }

      // Step 3: Run analysis components based on input flags
      const [peakHours, sessionAnalysis, questionPatterns, patterns, trends] = await Promise.all([
        validatedInput.includePeakHours ? this.analyzePeakHours(validatedInput.timeRange, componentsIncluded) : Promise.resolve([]),
        validatedInput.includeSessionAnalysis ? this.analyzeSessionPatterns(conversations, messages, componentsIncluded) : Promise.resolve(this.createEmptySessionAnalysis()),
        validatedInput.includeQuestionPatterns ? this.analyzeQuestionPatterns(conversations, messages, componentsIncluded) : Promise.resolve(this.createEmptyQuestionPatterns()),
        this.identifyProductivityPatterns(conversations, messages, validatedInput.timeRange),
        this.analyzeTrends(validatedInput.timeRange, validatedInput.granularity)
      ]);

      // Step 4: Generate insights and recommendations
      const insights = this.generateInsights(peakHours, sessionAnalysis, questionPatterns, patterns, trends);

      // Step 5: Build response metadata
      const analysisDuration = Date.now() - startTime;
      const metadata = {
        conversationCount: conversations.length,
        messageCount: messages.length,
        analysisDuration,
        granularity: validatedInput.granularity,
        componentsIncluded
      };

      return {
        timeRange: validatedInput.timeRange,
        analyzedAt: Date.now(),
        peakHours,
        sessionAnalysis,
        questionPatterns,
        patterns,
        trends,
        insights,
        metadata
      };

    } catch (error) {
      // Enhanced error handling with user-friendly messages
      if (error instanceof ValidationError) {
        throw new Error(JSON.stringify(formatValidationError(error)));
      }

      // Re-throw other errors with context
      throw new Error(`Productivity patterns analysis failed: ${error instanceof Error ? error.message : String(error)}`);
    }
  }


  /**
   * Get conversations and messages for analysis
   */
  private async getAnalysisData(conversationIds?: string[], timeRange?: TimeRange) {
    return wrapDatabaseOperation(async () => {
      let conversations;
      if (conversationIds && conversationIds.length > 0) {
        // Get specific conversations
        conversations = await Promise.all(
          conversationIds.map(id => this.conversationRepository.findById(id))
        );
        conversations = conversations.filter(c => c !== null);
      } else {
        // Get all conversations in time range
        const conversationsResult = await this.conversationRepository.findByDateRange(
          timeRange.start,
          timeRange.end,
          1000, // Large limit
          0
        );
        conversations = conversationsResult.data;
      }

      // Get messages for all conversations
      const messages = [];
      for (const conversation of conversations) {
        const conversationMessages = await this.messageRepository.findByConversationId(conversation.id);
        messages.push(...conversationMessages);
      }

      return { conversations, messages };
    }, 'Failed to retrieve analysis data');
  }

  /**
   * Analyze peak productivity hours
   */
  private async analyzePeakHours(timeRange: TimeRange, componentsIncluded: string[]): Promise<number[]> {
    componentsIncluded.push('peak_hours');
    return wrapDatabaseOperation(async () => {
      return await this.productivityPatternsRepository.getPeakHours(timeRange);
    }, 'Failed to analyze peak hours');
  }

  /**
   * Analyze session patterns and optimal lengths
   */
  private async analyzeSessionPatterns(conversations: any[], messages: any[], componentsIncluded: string[]): Promise<SessionAnalysis> {
    componentsIncluded.push('session_analysis');
    return wrapDatabaseOperation(async () => {
      // Calculate session lengths and productivity correlations
      const sessions = this.calculateSessionMetrics(conversations, messages);

      return {
        optimalLength: this.findOptimalSessionLength(sessions),
        averageLength: sessions.reduce((sum, s) => sum + s.length, 0) / sessions.length || 0,
        lengthDistribution: this.calculateLengthDistribution(sessions),
        productivityByLength: this.calculateProductivityByLength(sessions)
      };
    }, 'Failed to analyze session patterns');
  }

  /**
   * Analyze question patterns and effectiveness
   */
  private async analyzeQuestionPatterns(conversations: any[], messages: any[], componentsIncluded: string[]): Promise<QuestionPatternAnalysis> {
    componentsIncluded.push('question_patterns');
    return wrapDatabaseOperation(async () => {
      const questionMessages = messages.filter(m => m.role === 'user' && m.content.includes('?'));

      // Analyze question patterns
      const patterns = this.extractQuestionPatterns(questionMessages);
      const types = this.categorizeQuestions(questionMessages);
      const bestPractices = this.identifyQuestionBestPractices(patterns, types);

      return {
        topPatterns: patterns.slice(0, 10), // Top 10 patterns
        questionTypes: types,
        bestPractices
      };
    }, 'Failed to analyze question patterns');
  }

  /**
   * Identify productivity patterns
   */
  private async identifyProductivityPatterns(conversations: any[], messages: any[], timeRange: TimeRange): Promise<ProductivityPattern[]> {
    return wrapDatabaseOperation(async () => {
      const patterns: ProductivityPattern[] = [];

      // Pattern 1: Time-based productivity variations
      const hourlyProductivity = this.calculateHourlyProductivity(conversations, messages);
      if (hourlyProductivity.variance > 20) {
        patterns.push({
          id: 'hourly_variation',
          type: 'peak_hour',
          description: 'Significant productivity variations throughout the day',
          confidence: 0.8,
          data: hourlyProductivity,
          impact: 75
        });
      }

      // Pattern 2: Session length correlation
      const sessionLengthCorrelation = this.calculateSessionLengthCorrelation(conversations, messages);
      if (Math.abs(sessionLengthCorrelation) > 0.5) {
        patterns.push({
          id: 'session_length_correlation',
          type: 'session_length',
          description: `${sessionLengthCorrelation > 0 ? 'Longer' : 'Shorter'} sessions tend to be more productive`,
          confidence: Math.abs(sessionLengthCorrelation),
          data: { correlation: sessionLengthCorrelation },
          impact: 60
        });
      }

      return patterns;
    }, 'Failed to identify productivity patterns');
  }

  /**
   * Analyze temporal trends
   */
  private async analyzeTrends(timeRange: TimeRange, granularity: string): Promise<AnalyzeProductivityPatternsResponse['trends']> {
    return wrapDatabaseOperation(async () => {
      return {
        weeklyTrend: await this.calculateWeeklyTrend(timeRange),
        monthlyTrend: await this.calculateMonthlyTrend(timeRange),
        byDayOfWeek: await this.calculateProductivityByDayOfWeek(timeRange),
        byHourOfDay: await this.calculateProductivityByHourOfDay(timeRange)
      };
    }, 'Failed to analyze trends');
  }

  /**
   * Generate insights and recommendations
   */
  private generateInsights(
    peakHours: number[],
    sessionAnalysis: SessionAnalysis,
    questionPatterns: QuestionPatternAnalysis,
    patterns: ProductivityPattern[],
    trends: any
  ): AnalyzeProductivityPatternsResponse['insights'] {
    const keyInsights: string[] = [];
    const recommendations: string[] = [];
    const concerns: string[] = [];

    // Peak hours insights
    if (peakHours.length > 0) {
      keyInsights.push(`Peak productivity occurs at ${peakHours.join(', ')} hours`);
      recommendations.push(`Schedule important conversations during peak hours: ${peakHours.join(', ')}`);
    }

    // Session length insights
    if (sessionAnalysis.optimalLength > 0) {
      keyInsights.push(`Optimal session length is ${Math.round(sessionAnalysis.optimalLength)} minutes`);
      if (sessionAnalysis.optimalLength > 90) {
        concerns.push('Very long optimal session length may indicate inefficient conversations');
      } else if (sessionAnalysis.optimalLength < 15) {
        concerns.push('Very short optimal session length may indicate insufficient depth');
      }
    }

    // Question pattern insights
    if (questionPatterns.topPatterns.length > 0) {
      const bestPattern = questionPatterns.topPatterns[0];
      keyInsights.push(`Most effective question pattern: "${bestPattern.pattern}" (${Math.round(bestPattern.effectiveness * 100)}% effectiveness)`);
    }

    // Pattern-specific insights
    patterns.forEach(pattern => {
      if (pattern.impact > 70) {
        keyInsights.push(pattern.description);
      }
    });

    // Trend insights
    if (trends.weeklyTrend > 10) {
      keyInsights.push('Productivity is improving week over week');
    } else if (trends.weeklyTrend < -10) {
      concerns.push('Declining weekly productivity trend detected');
      recommendations.push('Review and adjust conversation approach to reverse declining trend');
    }

    // Default recommendations if none generated
    if (recommendations.length === 0) {
      recommendations.push('Continue monitoring productivity patterns for optimization opportunities');
    }

    return { keyInsights, recommendations, concerns };
  }

  // Helper methods for calculations
  private calculateSessionMetrics(conversations: any[], messages: any[]) {
    return conversations.map(conv => {
      const convMessages = messages.filter(m => m.conversationId === conv.id);
      const duration = convMessages.length > 0 ? 
        new Date(convMessages[convMessages.length - 1].timestamp).getTime() - 
        new Date(convMessages[0].timestamp).getTime() : 0;

      return {
        conversationId: conv.id,
        length: Math.round(duration / (1000 * 60)), // Convert to minutes
        messageCount: convMessages.length,
        productivity: this.estimateConversationProductivity(convMessages)
      };
    });
  }

  private findOptimalSessionLength(sessions: any[]): number {
    // Simple implementation: find length range with highest average productivity
    const lengthRanges = this.groupSessionsByLength(sessions);
    let optimalLength = 0;
    let maxProductivity = 0;

    for (const [range, rangeSessions] of Object.entries(lengthRanges)) {
      const avgProductivity = rangeSessions.reduce((sum: number, s: any) => sum + s.productivity, 0) / rangeSessions.length;
      if (avgProductivity > maxProductivity) {
        maxProductivity = avgProductivity;
        optimalLength = parseInt(range);
      }
    }

    return optimalLength;
  }

  private calculateLengthDistribution(sessions: any[]): { [range: string]: number } {
    const distribution: { [range: string]: number } = {};
    const ranges = ['0-15', '15-30', '30-60', '60-120', '120+'];

    ranges.forEach(range => distribution[range] = 0);

    sessions.forEach(session => {
      if (session.length <= 15) distribution['0-15']++;
      else if (session.length <= 30) distribution['15-30']++;
      else if (session.length <= 60) distribution['30-60']++;
      else if (session.length <= 120) distribution['60-120']++;
      else distribution['120+']++;
    });

    return distribution;
  }

  private calculateProductivityByLength(sessions: any[]): { length: number; productivity: number }[] {
    const grouped = this.groupSessionsByLength(sessions);
    return Object.entries(grouped).map(([length, sessions]) => ({
      length: parseInt(length),
      productivity: sessions.reduce((sum, s) => sum + s.productivity, 0) / sessions.length
    }));
  }

  private groupSessionsByLength(sessions: any[]): { [key: string]: any[] } {
    const groups: { [key: string]: any[] } = {};

    sessions.forEach(session => {
      const bucketSize = 15; // 15-minute buckets
      const bucket = Math.floor(session.length / bucketSize) * bucketSize;
      const key = bucket.toString();

      if (!groups[key]) groups[key] = [];
      groups[key].push(session);
    });

    return groups;
  }

  private extractQuestionPatterns(questionMessages: any[]) {
    // Simple pattern extraction based on question starters
    const patterns = new Map<string, { count: number; effectiveness: number; examples: string[] }>();

    questionMessages.forEach(msg => {
      const content = msg.content.toLowerCase();
      let pattern = 'Other';

      if (content.startsWith('how')) pattern = 'How questions';
      else if (content.startsWith('what')) pattern = 'What questions';
      else if (content.startsWith('why')) pattern = 'Why questions';
      else if (content.startsWith('when')) pattern = 'When questions';
      else if (content.startsWith('where')) pattern = 'Where questions';
      else if (content.startsWith('can')) pattern = 'Can questions';
      else if (content.startsWith('would')) pattern = 'Would questions';
      else if (content.startsWith('should')) pattern = 'Should questions';

      if (!patterns.has(pattern)) {
        patterns.set(pattern, { count: 0, effectiveness: 0.5, examples: [] });
      }

      const p = patterns.get(pattern)!;
      p.count++;
      if (p.examples.length < 3) {
        p.examples.push(msg.content);
      }
    });

    return Array.from(patterns.entries()).map(([pattern, data]) => ({
      pattern,
      frequency: data.count,
      effectiveness: data.effectiveness,
      examples: data.examples
    }));
  }

  private categorizeQuestions(questionMessages: any[]) {
    const categories = [
      { type: 'Information seeking', count: 0, totalProductivity: 0 },
      { type: 'Clarification', count: 0, totalProductivity: 0 },
      { type: 'Problem solving', count: 0, totalProductivity: 0 },
      { type: 'Brainstorming', count: 0, totalProductivity: 0 }
    ];

    questionMessages.forEach(msg => {
      // Simple categorization logic
      const content = msg.content.toLowerCase();
      let categoryIndex = 0;

      if (content.includes('what is') || content.includes('explain')) categoryIndex = 0;
      else if (content.includes('clarify') || content.includes('mean by')) categoryIndex = 1;
      else if (content.includes('how to') || content.includes('solve')) categoryIndex = 2;
      else if (content.includes('ideas') || content.includes('suggestions')) categoryIndex = 3;

      categories[categoryIndex].count++;
      categories[categoryIndex].totalProductivity += 50; // Default productivity estimate
    });

    return categories.map(cat => ({
      type: cat.type,
      count: cat.count,
      avgProductivity: cat.count > 0 ? cat.totalProductivity / cat.count : 0
    }));
  }

  private identifyQuestionBestPractices(patterns: any[], types: any[]): string[] {
    const practices: string[] = [];

    // Find most effective pattern
    const mostEffective = patterns.reduce((prev, current) => 
      (current.effectiveness > prev.effectiveness) ? current : prev
    );

    if (mostEffective) {
      practices.push(`Use "${mostEffective.pattern}" for higher engagement`);
    }

    // Check for balance in question types
    const typeBalance = types.filter(t => t.count > 0).length;
    if (typeBalance >= 3) {
      practices.push('Good variety in question types maintains engagement');
    }

    return practices;
  }

  private estimateConversationProductivity(messages: any[]): number {
    // Simple productivity estimation based on message length and count
    const avgLength = messages.reduce((sum, m) => sum + m.content.length, 0) / messages.length || 0;
    const questionCount = messages.filter(m => m.content.includes('?')).length;

    // Basic formula: consider message quality and engagement
    return Math.min(100, Math.max(0, 
      (avgLength / 100) + 
      (questionCount * 5) + 
      (messages.length * 2)
    ));
  }

  private calculateHourlyProductivity(conversations: any[], messages: any[]) {
    const hourlyData = new Array(24).fill(0).map(() => ({ 
      count: 0, 
      totalProductivity: 0, 
      messageCount: 0,
      avgMessageLength: 0,
      engagementScore: 0
    }));

    // First pass: collect conversation data by hour
    conversations.forEach(conversation => {
      const convMessages = messages.filter(m => m.conversationId === conversation.id);
      if (convMessages.length === 0) return;

      const startTime = new Date(convMessages[0].timestamp);
      const hour = startTime.getHours();

      // Calculate conversation productivity metrics
      const productivity = this.estimateConversationProductivity(convMessages);
      const avgLength = convMessages.reduce((sum, m) => sum + m.content.length, 0) / convMessages.length;
      const engagement = this.calculateEngagementScore(convMessages);

      hourlyData[hour].count++;
      hourlyData[hour].totalProductivity += productivity;
      hourlyData[hour].messageCount += convMessages.length;
      hourlyData[hour].avgMessageLength += avgLength;
      hourlyData[hour].engagementScore += engagement;
    });

    // Calculate averages and productivity scores
    const hourlyAvg = hourlyData.map((h, hour) => {
      if (h.count === 0) return { hour, productivity: 0 };

      const avgProductivity = h.totalProductivity / h.count;
      const avgMsgLength = h.avgMessageLength / h.count;
      const avgEngagement = h.engagementScore / h.count;

      // Weighted productivity score
      const weightedProductivity = (
        avgProductivity * 0.5 +
        Math.min(100, avgMsgLength / 10) * 0.3 + // Message quality factor
        avgEngagement * 0.2
      );

      return {
        hour,
        productivity: Math.round(weightedProductivity)
      };
    });

    // Calculate variance
    const avgProductivity = hourlyAvg.reduce((sum, h) => sum + h.productivity, 0) / 24;
    const variance = hourlyAvg.reduce((sum, h) => 
      sum + Math.pow(h.productivity - avgProductivity, 2), 0
    ) / 24;

    return { hourlyAvg, variance: Math.round(variance) };
  }

  private calculateEngagementScore(messages: any[]): number {
    if (messages.length === 0) return 0;

    let engagementScore = 0;

    // Question engagement
    const questionCount = messages.filter(m => m.content.includes('?')).length;
    engagementScore += (questionCount / messages.length) * 30;

    // Response depth (longer responses indicate engagement)
    const avgResponseLength = messages
      .filter(m => m.role === 'assistant')
      .reduce((sum, m) => sum + m.content.length, 0) / 
      messages.filter(m => m.role === 'assistant').length || 0;
    engagementScore += Math.min(30, avgResponseLength / 100);

    // Conversation flow (back-and-forth exchanges)
    let exchanges = 0;
    for (let i = 1; i < messages.length; i++) {
      if (messages[i].role !== messages[i-1].role) {
        exchanges++;
      }
    }
    const exchangeRate = exchanges / Math.max(1, messages.length - 1);
    engagementScore += exchangeRate * 40;

    return Math.min(100, Math.max(0, engagementScore));
  }

  private calculateSessionLengthCorrelation(conversations: any[], messages: any[]): number {
    const sessions = this.calculateSessionMetrics(conversations, messages);
    if (sessions.length < 2) return 0;

    // Calculate Pearson correlation coefficient
    const n = sessions.length;
    const sumX = sessions.reduce((sum, s) => sum + s.length, 0);
    const sumY = sessions.reduce((sum, s) => sum + s.productivity, 0);
    const sumXY = sessions.reduce((sum, s) => sum + (s.length * s.productivity), 0);
    const sumXX = sessions.reduce((sum, s) => sum + (s.length * s.length), 0);
    const sumYY = sessions.reduce((sum, s) => sum + (s.productivity * s.productivity), 0);

    const numerator = (n * sumXY) - (sumX * sumY);
    const denominator = Math.sqrt(((n * sumXX) - (sumX * sumX)) * ((n * sumYY) - (sumY * sumY)));

    if (denominator === 0) return 0;

    const correlation = numerator / denominator;

    // Ensure correlation is between -1 and 1
    return Math.max(-1, Math.min(1, correlation));
  }

  private async calculateWeeklyTrend(timeRange: TimeRange): Promise<number> {
    return wrapDatabaseOperation(async () => {
      // Get productivity data by week
      const weeklyData = await this.calculateWeeklyProductivityData(timeRange);

      if (weeklyData.length < 2) return 0;

      // Calculate linear regression for trend
      const n = weeklyData.length;
      const sumX = weeklyData.reduce((sum, _, i) => sum + i, 0);
      const sumY = weeklyData.reduce((sum, data) => sum + data.productivity, 0);
      const sumXY = weeklyData.reduce((sum, data, i) => sum + (i * data.productivity), 0);
      const sumXX = weeklyData.reduce((sum, _, i) => sum + (i * i), 0);

      // Calculate slope (trend)
      const slope = (n * sumXY - sumX * sumY) / (n * sumXX - sumX * sumX);

      // Convert slope to percentage change per week
      const avgProductivity = sumY / n;
      const trendPercentage = avgProductivity > 0 ? (slope / avgProductivity) * 100 : 0;

      return Math.round(trendPercentage * 10) / 10; // Round to 1 decimal place
    }, 'Failed to calculate weekly trend');
  }

  private async calculateWeeklyProductivityData(timeRange: TimeRange) {
    const weekLength = 7 * 24 * 60 * 60 * 1000; // 1 week in milliseconds
    const weeks: Array<{ week: number, productivity: number }> = [];

    for (let weekStart = timeRange.start; weekStart < timeRange.end; weekStart += weekLength) {
      const weekEnd = Math.min(weekStart + weekLength, timeRange.end);
      const weekNumber = Math.floor((weekStart - timeRange.start) / weekLength);

      // Get productivity data for this week (would come from repository in practice)
      const productivity = await this.productivityPatternsRepository.getWeeklyProductivity({
        start: weekStart,
        end: weekEnd
      });

      weeks.push({ week: weekNumber, productivity });
    }

    return weeks;
  }

  private async calculateMonthlyTrend(timeRange: TimeRange): Promise<number> {
    return wrapDatabaseOperation(async () => {
      const monthlyData = await this.calculateMonthlyProductivityData(timeRange);

      if (monthlyData.length < 2) return 0;

      // Calculate trend using linear regression
      const n = monthlyData.length;
      const sumX = monthlyData.reduce((sum, _, i) => sum + i, 0);
      const sumY = monthlyData.reduce((sum, data) => sum + data.productivity, 0);
      const sumXY = monthlyData.reduce((sum, data, i) => sum + (i * data.productivity), 0);
      const sumXX = monthlyData.reduce((sum, _, i) => sum + (i * i), 0);

      const slope = (n * sumXY - sumX * sumY) / (n * sumXX - sumX * sumX);
      const avgProductivity = sumY / n;
      const trendPercentage = avgProductivity > 0 ? (slope / avgProductivity) * 100 : 0;

      return Math.round(trendPercentage * 10) / 10;
    }, 'Failed to calculate monthly trend');
  }

  private async calculateMonthlyProductivityData(timeRange: TimeRange) {
    const monthLength = 30 * 24 * 60 * 60 * 1000; // Approximate month
    const months: Array<{ month: number, productivity: number }> = [];

    for (let monthStart = timeRange.start; monthStart < timeRange.end; monthStart += monthLength) {
      const monthEnd = Math.min(monthStart + monthLength, timeRange.end);
      const monthNumber = Math.floor((monthStart - timeRange.start) / monthLength);

      const productivity = await this.productivityPatternsRepository.getMonthlyProductivity({
        start: monthStart,
        end: monthEnd
      });

      months.push({ month: monthNumber, productivity });
    }

    return months;
  }

  private async calculateProductivityByDayOfWeek(timeRange: TimeRange): Promise<{ [day: string]: number }> {
    return wrapDatabaseOperation(async () => {
      const dayNames = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
      const productivityByDay: { [day: string]: number } = {};

      // Get productivity data from repository
      const dailyData = await this.productivityPatternsRepository.getDailyProductivity(timeRange);

      // Group by day of week and calculate averages
      const dayGroups = new Map<number, number[]>();

      dailyData.forEach(entry => {
        const date = new Date(entry.timestamp);
        const dayOfWeek = date.getDay(); // 0 = Sunday, 1 = Monday, etc.

        if (!dayGroups.has(dayOfWeek)) {
          dayGroups.set(dayOfWeek, []);
        }
        dayGroups.get(dayOfWeek)!.push(entry.productivity);
      });

      // Calculate averages for each day
      dayNames.forEach((dayName, index) => {
        const dayScores = dayGroups.get(index) || [];
        if (dayScores.length > 0) {
          const average = dayScores.reduce((sum, score) => sum + score, 0) / dayScores.length;
          productivityByDay[dayName] = Math.round(average);
        } else {
          productivityByDay[dayName] = 50; // Default if no data
        }
      });

      return productivityByDay;
    }, 'Failed to calculate productivity by day of week');
  }

  private async calculateProductivityByHourOfDay(timeRange: TimeRange): Promise<{ [hour: number]: number }> {
    return wrapDatabaseOperation(async () => {
      const hourlyData: { [hour: number]: number } = {};

      // Get hourly productivity data from repository
      const data = await this.productivityPatternsRepository.getHourlyProductivity(timeRange);

      // Group data by hour and calculate averages
      const hourGroups = new Map<number, number[]>();

      data.forEach(entry => {
        const hour = entry.hour;

        if (!hourGroups.has(hour)) {
          hourGroups.set(hour, []);
        }
        hourGroups.get(hour)!.push(entry.avgScore);
      });

      // Calculate average for each hour
      for (let hour = 0; hour < 24; hour++) {
        const hourScores = hourGroups.get(hour) || [];
        if (hourScores.length > 0) {
          const average = hourScores.reduce((sum, score) => sum + score, 0) / hourScores.length;
          hourlyData[hour] = Math.round(average);
        } else {
          // Use reasonable defaults for hours with no data
          if (hour >= 9 && hour <= 11) hourlyData[hour] = 70; // Morning
          else if (hour >= 14 && hour <= 16) hourlyData[hour] = 65; // Afternoon
          else if (hour >= 19 && hour <= 21) hourlyData[hour] = 60; // Evening
          else if (hour >= 22 || hour <= 6) hourlyData[hour] = 30; // Night
          else hourlyData[hour] = 50; // Other times
        }
      }

      return hourlyData;
    }, 'Failed to calculate productivity by hour of day');
  }

  private createEmptyResponse(timeRange: TimeRange, granularity: string, startTime: number): AnalyzeProductivityPatternsResponse {
    return {
      timeRange,
      analyzedAt: Date.now(),
      peakHours: [],
      sessionAnalysis: this.createEmptySessionAnalysis(),
      questionPatterns: this.createEmptyQuestionPatterns(),
      patterns: [],
      trends: {
        weeklyTrend: 0,
        monthlyTrend: 0,
        byDayOfWeek: {},
        byHourOfDay: {}
      },
      insights: {
        keyInsights: ['No conversations found in the specified time range'],
        recommendations: ['Start conversations to begin productivity analysis'],
        concerns: []
      },
      metadata: {
        conversationCount: 0,
        messageCount: 0,
        analysisDuration: Date.now() - startTime,
        granularity,
        componentsIncluded: []
      }
    };
  }

  private createEmptySessionAnalysis(): SessionAnalysis {
    return {
      optimalLength: 0,
      averageLength: 0,
      lengthDistribution: {},
      productivityByLength: []
    };
  }

  private createEmptyQuestionPatterns(): QuestionPatternAnalysis {
    return {
      topPatterns: [],
      questionTypes: [],
      bestPractices: []
    };
  }

  /**
   * Static factory method to create an AnalyzeProductivityPatternsTool instance
   */
  static create(dependencies: AnalyzeProductivityPatternsDependencies): AnalyzeProductivityPatternsTool {
    return new AnalyzeProductivityPatternsTool(dependencies);
  }
}

/**
 * Abstract base class for all MCP Tools in the persistence system
 * 
 * This class provides common functionality for tool validation, error handling,
 * and response formatting. All concrete tool implementations must extend this class.
 */

import { z } from 'zod';
import { MCPTool, MCPToolResult } from '../types/mcp.js';
import { SuccessResponse, ErrorResponse } from '../types/interfaces.js';

/**
 * Tool execution context passed to all tools
 */
export interface ToolContext {
  /** Unique request ID for tracing */
  requestId: string;
  /** Timestamp when the tool execution started */
  timestamp: number;
  /** Additional context data */
  metadata?: Record<string, any>;
}

/**
 * Base class for all MCP tools providing common functionality
 */
export abstract class BaseTool<TInput = any, TOutput = any> {
  protected readonly tool: MCPTool;
  protected readonly inputSchema: z.ZodSchema<any>;

  constructor(tool: MCPTool, inputSchema: z.ZodSchema<any>) {
    this.tool = tool;
    this.inputSchema = inputSchema;
  }

  /**
   * Get the tool definition for MCP protocol
   */
  public getTool(): MCPTool {
    return this.tool;
  }

  /**
   * Get the tool name
   */
  public getName(): string {
    return this.tool.name;
  }

  /**
   * Get the tool description
   */
  public getDescription(): string {
    return this.tool.description;
  }

  /**
   * Execute the tool with the given input and context
   */
  public async execute(input: unknown, context: ToolContext): Promise<MCPToolResult> {
    try {
      // Validate input using Zod schema
      const validatedInput = this.validateInput(input);

      // Execute the tool-specific implementation
      const result = await this.executeImpl(validatedInput, context);

      // Format and return the response
      return this.formatSuccessResponse(result);
    } catch (error) {
      // Handle errors and format error response
      return this.formatErrorResponse(error, context);
    }
  }

  /**
   * Validate input using the tool's Zod schema
   */
  public validateInput(input: unknown): TInput {
    try {
      return this.inputSchema.parse(input);
    } catch (error) {
      if (error instanceof z.ZodError) {
        throw new ValidationError('Invalid input parameters', error.errors);
      }
      throw error;
    }
  }

  /**
   * Abstract method that concrete tools must implement
   */
  protected abstract executeImpl(input: TInput, context: ToolContext): Promise<TOutput>;

  /**
   * Format a successful response for MCP protocol
   */
  protected formatSuccessResponse(data: TOutput): MCPToolResult {
    const response: SuccessResponse<TOutput> = {
      success: true,
      data
    };

    return {
      content: [{
        type: 'text',
        text: JSON.stringify(response)
      }]
    };
  }

  /**
   * Format an error response for MCP protocol
   */
  protected formatErrorResponse(error: unknown, _context: ToolContext): MCPToolResult {
    let errorResponse: ErrorResponse;

    if (error instanceof ValidationError) {
      errorResponse = {
        success: false,
        error: 'ValidationError',
        message: error.message,
        details: error.details
      };
    } else if (error instanceof NotFoundError) {
      errorResponse = {
        success: false,
        error: 'NotFoundError',
        message: error.message
      };
    } else if (error instanceof ConflictError) {
      errorResponse = {
        success: false,
        error: 'ConflictError',
        message: error.message
      };
    } else if (error instanceof DatabaseError) {
      // Log the full database error but don't expose it to the user
      console.error(`Database error in ${this.tool.name}:`, error.originalError);

      errorResponse = {
        success: false,
        error: 'DatabaseError',
        message: 'A database error occurred while processing your request'
      };
    } else if (error instanceof Error) {
      // Log unexpected errors but don't expose internal details
      console.error(`Unexpected error in ${this.tool.name}:`, error);

      errorResponse = {
        success: false,
        error: 'InternalError',
        message: 'An internal error occurred while processing your request'
      };
    } else {
      // Handle non-Error objects
      console.error(`Unknown error in ${this.tool.name}:`, error);

      errorResponse = {
        success: false,
        error: 'UnknownError',
        message: 'An unknown error occurred while processing your request'
      };
    }

    return {
      content: [{
        type: 'text',
        text: JSON.stringify(errorResponse)
      }],
      isError: true
    };
  }

  /**
   * Create a tool context for execution
   */
  public static createContext(overrides?: Partial<ToolContext>): ToolContext {
    return {
      requestId: generateRequestId(),
      timestamp: Date.now(),
      ...overrides
    };
  }

  /**
   * Utility method to safely extract string from unknown input
   */
  protected safeString(value: unknown, fieldName: string): string {
    if (typeof value === 'string') {
      return value;
    }
    throw new ValidationError(`Field '${fieldName}' must be a string`);
  }

  /**
   * Utility method to safely extract number from unknown input
   */
  protected safeNumber(value: unknown, fieldName: string): number {
    if (typeof value === 'number' && !isNaN(value)) {
      return value;
    }
    throw new ValidationError(`Field '${fieldName}' must be a valid number`);
  }

  /**
   * Utility method to safely extract boolean from unknown input
   */
  protected safeBoolean(value: unknown, fieldName: string): boolean {
    if (typeof value === 'boolean') {
      return value;
    }
    throw new ValidationError(`Field '${fieldName}' must be a boolean`);
  }
}

/**
 * Custom error classes for better error handling
 */
export class ValidationError extends Error {
  constructor(message: string, public details?: any) {
    super(message);
    this.name = 'ValidationError';
  }
}

export class NotFoundError extends Error {
  constructor(message: string) {
    super(message);
    this.name = 'NotFoundError';
  }
}

export class ConflictError extends Error {
  constructor(message: string) {
    super(message);
    this.name = 'ConflictError';
  }
}

export class DatabaseError extends Error {
  constructor(message: string, public originalError?: Error) {
    super(message);
    this.name = 'DatabaseError';
  }
}

/**
 * Generate a unique request ID for tracing
 */
function generateRequestId(): string {
  return `${Date.now()}-${Math.random().toString(36).substring(2, 15)}`;
}

/**
 * Type guard to check if an error is a known error type
 */
export function isKnownError(error: unknown): error is ValidationError | NotFoundError | ConflictError | DatabaseError {
  return error instanceof ValidationError ||
         error instanceof NotFoundError ||
         error instanceof ConflictError ||
         error instanceof DatabaseError;
}

/**
 * Utility function to wrap database operations and convert errors
 */
export async function wrapDatabaseOperation<T>(
  operation: () => Promise<T>,
  errorMessage: string
): Promise<T> {
  try {
    return await operation();
  } catch (error) {
    // Preserve certain error types that have specific meaning
    if (error instanceof NotFoundError || 
        error instanceof ValidationError || 
        error instanceof ConflictError) {
      throw error;
    }

    if (error instanceof Error) {
      throw new DatabaseError(errorMessage, error);
    }
    throw new DatabaseError(errorMessage);
  }
}

/**
