/**
 * Configure LLM Provider Tool implementation
 */
export class ConfigureLLMProviderTool extends BaseTool<ConfigureLLMProviderInput, ConfigureLLMProviderResponse> {
  private providerConfigRepository: ProviderConfigRepository;

  constructor(dependencies: ConfigureLLMProviderDependencies) {
    const tool: MCPTool = {
      name: 'configure_llm_provider',
      description: 'Manage LLM provider configurations at runtime for context generation and summarization.',
      inputSchema: {
        type: 'object',
        properties: {
          operation: {
            type: 'string',
            enum: ['add', 'update', 'remove', 'list'],
            description: 'Operation to perform on provider configurations'
          },
          config: {
            type: 'object',
            properties: {
              id: {
                type: 'string',
                description: 'Provider ID (required for update/remove operations)'
              },
              name: {
                type: 'string',
                minLength: 1,
                description: 'Provider name'
              },
              type: {
                type: 'string',
                enum: ['local', 'external'],
                description: 'Provider type'
              },
              endpoint: {
                type: 'string',
                format: 'uri',
                description: 'API endpoint URL'
              },
              apiKeyEnv: {
                type: 'string',
                description: 'Environment variable name for API key'
              },
              modelName: {
                type: 'string',
                minLength: 1,
                description: 'Model name to use'
              },
              maxTokens: {
                type: 'number',
                minimum: 1,
                description: 'Maximum tokens for the model'
              },
              temperature: {
                type: 'number',
                minimum: 0,
                maximum: 2,
                description: 'Temperature setting (0-2)'
              },
              isActive: {
                type: 'boolean',
                description: 'Whether the provider is active'
              },
              priority: {
                type: 'number',
                description: 'Priority for provider selection (higher = preferred)'
              },
              costPer1kTokens: {
                type: 'number',
                minimum: 0,
                description: 'Cost per 1000 tokens'
              },
              metadata: {
                type: 'object',
                description: 'Additional metadata',
                additionalProperties: true
              }
            },
            additionalProperties: false,
            description: 'Provider configuration (required for add/update operations)'
          }
        },
        required: ['operation'],
        additionalProperties: false
      }
    };

    super(tool, ConfigureLLMProviderSchema);
    this.providerConfigRepository = dependencies.providerConfigRepository;
  }

  /**
   * Execute the tool implementation
   */
  protected async executeImpl(
    params: ConfigureLLMProviderInput, 
    _context: ToolContext
  ): Promise<ConfigureLLMProviderResponse> {
    return await wrapDatabaseOperation(async () => {
      switch (params.operation) {
        case 'add':
          return await this.handleAddProvider(params);
        case 'update':
          return await this.handleUpdateProvider(params);
        case 'remove':
          return await this.handleRemoveProvider(params);
        case 'list':
          return await this.handleListProviders(params);
        default:
          throw new ValidationError(`Unknown operation: ${params.operation}`);
      }
    }, 'Failed to configure LLM provider');
  }

  /**
   * Handle adding a new provider
   */
  private async handleAddProvider(params: ConfigureLLMProviderInput): Promise<ConfigureLLMProviderResponse> {
    if (!params.config) {
      throw new ValidationError('Configuration is required for add operation');
    }

    const config = params.config;

    // Validate required fields for add operation
    if (!config.name || !config.type || !config.modelName) {
      throw new ValidationError('name, type, and modelName are required for add operation');
    }

    // Validate API key environment variable for external providers
    if (config.type === 'external' && !config.apiKeyEnv) {
      throw new ValidationError('apiKeyEnv is required for external providers');
    }

    // Validate endpoint for external providers
    if (config.type === 'external' && !config.endpoint) {
      throw new ValidationError('endpoint is required for external providers');
    }

    const createParams: CreateProviderParams = {
      name: config.name,
      type: config.type,
      endpoint: config.endpoint,
      apiKeyEnv: config.apiKeyEnv,
      modelName: config.modelName,
      maxTokens: config.maxTokens || 4000,
      temperature: config.temperature,
      isActive: config.isActive,
      priority: config.priority,
      costPer1kTokens: config.costPer1kTokens,
      metadata: config.metadata
    };

    const provider = await this.providerConfigRepository.create(createParams);

    return {
      operation: 'add',
      success: true,
      provider: this.formatProvider(provider),
      message: `Provider '${provider.name}' added successfully`
    };
  }

  /**
   * Handle updating an existing provider
   */
  private async handleUpdateProvider(params: ConfigureLLMProviderInput): Promise<ConfigureLLMProviderResponse> {
    if (!params.config || !params.config.id) {
      throw new ValidationError('Configuration with id is required for update operation');
    }

    const config = params.config;
    const providerId = config.id!; // We validated this is not undefined above

    // Check if provider exists
    const existing = await this.providerConfigRepository.findById(providerId);
    if (!existing) {
      throw new NotFoundError(`Provider with id '${providerId}' not found`);
    }

    const updateParams: UpdateProviderParams = {
      name: config.name,
      endpoint: config.endpoint,
      apiKeyEnv: config.apiKeyEnv,
      modelName: config.modelName,
      maxTokens: config.maxTokens,
      temperature: config.temperature,
      isActive: config.isActive,
      priority: config.priority,
      costPer1kTokens: config.costPer1kTokens,
      metadata: config.metadata
    };

    const updatedProvider = await this.providerConfigRepository.updateConfig(providerId, updateParams);

    if (!updatedProvider) {
      throw new Error('Failed to update provider');
    }

    return {
      operation: 'update',
      success: true,
      provider: this.formatProvider(updatedProvider),
      message: `Provider '${updatedProvider.name}' updated successfully`
    };
  }

  /**
   * Handle removing a provider
   */
  private async handleRemoveProvider(params: ConfigureLLMProviderInput): Promise<ConfigureLLMProviderResponse> {
    if (!params.config || !params.config.id) {
      throw new ValidationError('Configuration with id is required for remove operation');
    }

    const providerId = params.config.id!; // We validated this is not undefined above

    // Check if provider exists
    const existing = await this.providerConfigRepository.findById(providerId);
    if (!existing) {
      throw new NotFoundError(`Provider with id '${providerId}' not found`);
    }

    const success = await this.providerConfigRepository.delete(providerId);

    if (!success) {
      throw new Error('Failed to remove provider');
    }

    return {
      operation: 'remove',
      success: true,
      provider: this.formatProvider(existing),
      message: `Provider '${existing.name}' removed successfully`
    };
  }

  /**
   * Handle listing all providers
   */
  private async handleListProviders(_params: ConfigureLLMProviderInput): Promise<ConfigureLLMProviderResponse> {
    const providers = await this.providerConfigRepository.findAll();
    const statistics = await this.providerConfigRepository.getProviderStats();

    return {
      operation: 'list',
      success: true,
      providers: providers.map(provider => this.formatProvider(provider)),
      statistics,
      message: `Found ${providers.length} providers (${statistics.active} active)`
    };
  }

  /**
   * Format provider for response
   */
  private formatProvider(provider: LLMProvider): NonNullable<ConfigureLLMProviderResponse['provider']> {
    return {
      id: provider.id,
      name: provider.name,
      type: provider.type,
      endpoint: provider.endpoint,
      modelName: provider.modelName,
      maxTokens: provider.maxTokens,
      temperature: provider.temperature,
      isActive: provider.isActive,
      priority: provider.priority,
      costPer1kTokens: provider.costPer1kTokens,
      metadata: provider.metadata || {}
    };
  }

  /**
   * Factory method to create the tool
   */
  static create(dependencies: ConfigureLLMProviderDependencies): ConfigureLLMProviderTool {
    return new ConfigureLLMProviderTool(dependencies);
  }
}

/**
 * DeleteConversation Tool Implementation
 * 
 * This tool deletes a conversation and all its messages. It supports both
 * soft deletion (marking as deleted) and permanent deletion.
 */

import { DeleteConversationTool as DeleteConversationToolDef } from '../types/mcp.js';
import { DeleteConversationSchema, DeleteConversationInput } from '../types/schemas.js';
import { BaseTool, ToolContext, NotFoundError, ValidationError, wrapDatabaseOperation } from './BaseTool.js';
import { ConversationRepository, MessageRepository } from '../storage/repositories/index.js';
import { SearchEngine } from '../search/SearchEngine.js';
import { Conversation, Message } from '../types/interfaces.js';

/**
 * Response interface for delete_conversation tool
 */
export interface DeleteConversationResponse {
  /** Whether the deletion was successful */
  success: boolean;
  /** The deleted conversation metadata */
  deletedConversation: {
    id: string;
    title?: string;
    messageCount: number;
    createdAt: number;
    updatedAt: number;
  };
  /** Deletion details */
  details: {
    /** Whether this was a permanent deletion */
    permanent: boolean;
    /** Number of messages deleted */
    messagesDeleted: number;
    /** Timestamp when deletion occurred */
    deletedAt: number;
    /** Whether search index was updated */
    searchIndexUpdated: boolean;
  };
  /** Recovery information (for soft deletes) */
  recovery?: {
    /** Instructions for recovery */
    instructions: string;
    /** Estimated recovery difficulty */
    difficulty: 'easy' | 'moderate' | 'difficult';
  };
}

/**
 * Dependencies required by DeleteConversationTool
 */
export interface DeleteConversationDependencies {
  conversationRepository: ConversationRepository;
  messageRepository: MessageRepository;
  searchEngine: SearchEngine;
}

/**
 * Implementation of the delete_conversation MCP tool
 */
export class DeleteConversationTool extends BaseTool<DeleteConversationInput, DeleteConversationResponse> {
  private readonly conversationRepo: ConversationRepository;
  private readonly messageRepo: MessageRepository;
  private readonly searchEngine: SearchEngine;

  constructor(dependencies: DeleteConversationDependencies) {
    super(DeleteConversationToolDef, DeleteConversationSchema);
    this.conversationRepo = dependencies.conversationRepository;
    this.messageRepo = dependencies.messageRepository;
    this.searchEngine = dependencies.searchEngine;
  }

  /**
   * Execute the delete_conversation tool
   */
  protected async executeImpl(input: DeleteConversationInput, context: ToolContext): Promise<DeleteConversationResponse> {
    const deletedAt = Date.now();

    // Step 1: Verify the conversation exists and get its details
    const conversation = await this.getConversationForDeletion(input.conversationId);

    // Step 2: Get all messages in the conversation
    const messages = await this.getMessagesForDeletion(input.conversationId);

    // Step 3: Validate deletion is allowed
    this.validateDeletion(conversation, messages, input);

    // Step 4: Perform the deletion
    await this.performDeletion(
      conversation,
      messages,
      input.permanent ?? false,
      context
    );

    // Step 5: Update search index
    const searchIndexUpdated = await this.updateSearchIndex(messages, input.permanent ?? false);

    // Step 6: Build response
    return {
      success: true,
      deletedConversation: {
        id: conversation.id,
        title: conversation.title,
        messageCount: messages.length,
        createdAt: conversation.createdAt,
        updatedAt: conversation.updatedAt
      },
      details: {
        permanent: input.permanent ?? false,
        messagesDeleted: messages.length,
        deletedAt,
        searchIndexUpdated
      },
      recovery: input.permanent ? undefined : this.buildRecoveryInfo(conversation, messages)
    };
  }

  /**
   * Get conversation for deletion, ensuring it exists
   */
  private async getConversationForDeletion(conversationId: string): Promise<Conversation> {
    return wrapDatabaseOperation(async () => {
      const conversation = await this.conversationRepo.findById(conversationId);

      if (!conversation) {
        throw new NotFoundError(`Conversation with ID '${conversationId}' not found`);
      }

      // Check if already soft-deleted
      if (conversation.metadata?.deleted === true) {
        throw new ValidationError(`Conversation '${conversationId}' is already deleted`);
      }

      return conversation;
    }, 'Failed to retrieve conversation for deletion');
  }

  /**
   * Get all messages in the conversation
   */
  private async getMessagesForDeletion(conversationId: string): Promise<Message[]> {
    return wrapDatabaseOperation(async () => {
      return await this.messageRepo.findByConversationId(conversationId);
    }, 'Failed to retrieve messages for deletion');
  }

  /**
   * Validate that the deletion is allowed
   */
  private validateDeletion(conversation: Conversation, messages: Message[], input: DeleteConversationInput): void {
    // Check for system conversations that shouldn't be deleted
    if (conversation.metadata?.system === true) {
      throw new ValidationError('System conversations cannot be deleted');
    }

    // Check for very large conversations in non-permanent mode
    if (!input.permanent && messages.length > 1000) {
      throw new ValidationError(
        'Conversations with more than 1000 messages require permanent deletion. ' +
        'Use permanent: true if you want to delete this conversation.'
      );
    }

    // Additional business logic validation could go here
  }

  /**
   * Perform the actual deletion
   */
  private async performDeletion(
    conversation: Conversation,
    messages: Message[],
    permanent: boolean,
    context: ToolContext
  ): Promise<{ messagesDeleted: number }> {
    return wrapDatabaseOperation(async () => {
      if (permanent) {
        // Permanent deletion: remove from database
        await this.performPermanentDeletion(conversation, messages, context);
      } else {
        // Soft deletion: mark as deleted
        await this.performSoftDeletion(conversation, messages, context);
      }

      return { messagesDeleted: messages.length };
    }, 'Failed to perform deletion');
  }

  /**
   * Perform permanent deletion
   */
  private async performPermanentDeletion(
    conversation: Conversation,
    messages: Message[],
    context: ToolContext
  ): Promise<void> {
    // Delete all messages first (foreign key constraints)
    for (const message of messages) {
      await this.messageRepo.delete(message.id);
    }

    // Delete the conversation
    await this.conversationRepo.delete(conversation.id);

    // Log the permanent deletion
    console.info(`Permanently deleted conversation ${conversation.id} with ${messages.length} messages`, {
      conversationId: conversation.id,
      messageCount: messages.length,
      requestId: context.requestId,
      timestamp: context.timestamp
    });
  }

  /**
   * Perform soft deletion
   */
  private async performSoftDeletion(
    conversation: Conversation,
    messages: Message[],
    context: ToolContext
  ): Promise<void> {
    const deletionMetadata = {
      deleted: true,
      deletedAt: Date.now(),
      deletedBy: 'delete_conversation_tool',
      requestId: context.requestId,
      originalMetadata: conversation.metadata
    };

    // Mark conversation as deleted
    await this.conversationRepo.update(conversation.id, {
      metadata: deletionMetadata
    });

    // Mark all messages as deleted
    for (const message of messages) {
      await this.messageRepo.update(message.id, {
        metadata: {
          ...message.metadata,
          deleted: true,
          deletedAt: Date.now(),
          deletedBy: 'delete_conversation_tool',
          requestId: context.requestId
        }
      });
    }

    // Log the soft deletion
    console.info(`Soft deleted conversation ${conversation.id} with ${messages.length} messages`, {
      conversationId: conversation.id,
      messageCount: messages.length,
      requestId: context.requestId,
      timestamp: context.timestamp
    });
  }

  /**
   * Update search index after deletion
   */
  private async updateSearchIndex(messages: Message[], permanent: boolean): Promise<boolean> {
    try {
      if (permanent) {
        // Remove messages from search index
        for (const message of messages) {
          await this.searchEngine.removeMessage(message.id);
        }
      } else {
        // For soft deletion, we might want to mark as deleted in search index
        // but keep for potential recovery. This depends on search engine implementation.
        // For now, we'll remove from search but keep in database.
        for (const message of messages) {
          await this.searchEngine.removeMessage(message.id);
        }
      }
      return true;
    } catch (error) {
      // Log the error but don't fail the entire operation
      console.warn('Failed to update search index after deletion:', error);
      return false;
    }
  }

  /**
   * Build recovery information for soft deletes
   */
  private buildRecoveryInfo(_conversation: Conversation, messages: Message[]): DeleteConversationResponse['recovery'] {
    const messageCount = messages.length;
    let difficulty: 'easy' | 'moderate' | 'difficult';
    let instructions: string;

    if (messageCount <= 10) {
      difficulty = 'easy';
      instructions = 'Recovery is straightforward. Contact support with the conversation ID to restore.';
    } else if (messageCount <= 100) {
      difficulty = 'moderate';
      instructions = 'Recovery is possible but may take some time due to the number of messages. Contact support with the conversation ID.';
    } else {
      difficulty = 'difficult';
      instructions = 'Recovery is complex due to the large number of messages. Contact support immediately if recovery is needed.';
    }

    return {
      instructions,
      difficulty
    };
  }



  /**
   * Static factory method to create a DeleteConversationTool instance
   */
  static create(dependencies: DeleteConversationDependencies): DeleteConversationTool {
    return new DeleteConversationTool(dependencies);
  }
}

/**
 * Detect Knowledge Gaps Tool Implementation
 * 
 * This tool identifies knowledge gaps in conversations by analyzing:
 * - Unresolved questions and information needs
 * - Recurring topics that lack depth or resolution
 * - Areas where additional learning or research is needed
 * - Pattern recognition in knowledge-seeking behavior
 * - Topic coverage analysis and gaps in understanding
 */

import { DetectKnowledgeGapsToolDef as DetectKnowledgeGapsToolDef } from '../types/mcp.js';
import { DetectKnowledgeGapsSchema, DetectKnowledgeGapsInput } from '../types/schemas.js';
import { BaseTool, ToolContext, wrapDatabaseOperation } from './BaseTool.js';
import { AnalyticsEngine } from '../analytics/services/AnalyticsEngine.js';
import { KnowledgeGapDetector, DetectedKnowledgeGap } from '../analytics/analyzers/KnowledgeGapDetector.js';
import { ConversationRepository } from '../storage/repositories/ConversationRepository.js';
import { MessageRepository } from '../storage/repositories/MessageRepository.js';
import { KnowledgeGapsRepository } from '../analytics/repositories/KnowledgeGapsRepository.js';
import { TimeRange } from '../analytics/repositories/AnalyticsRepository.js';
import { 
  validateDateRange, 
  validateStringArray,
  validateFrequency,
  ValidationError,
  formatValidationError,
  withEnhancedValidation 
} from '../utils/validation.js';

/**
 * Knowledge gap category classification
 */
