  /**
   * Build report metadata
   */
  private buildReportMetadata(
    timeRange: TimeRange, 
    conversationCount: number, 
    messageCount: number, 
    generationTime: number,
    sections: string[]
  ): GenerateAnalyticsReportResponse['metadata'] {
    // Calculate coverage percentage based on available data
    const expectedMinConversations = Math.floor((timeRange.end - timeRange.start) / (24 * 60 * 60 * 1000)); // 1 per day minimum
    const coveragePercentage = expectedMinConversations > 0 ? 
      Math.min(100, (conversationCount / expectedMinConversations) * 100) : 100;

    // Data quality assessment
    const dataQuality = {
      completeness: Math.min(100, (conversationCount > 0 ? 90 : 0) + (messageCount > 10 ? 10 : messageCount)),
      accuracy: 95, // Assume high accuracy from structured analytics
      freshness: Math.max(0, 100 - Math.floor((Date.now() - timeRange.end) / (24 * 60 * 60 * 1000)) * 5) // Decrease 5% per day
    };

    return {
      dataSources: ['conversations', 'messages', 'analytics_engine', 'repositories'],
      conversationCount,
      messageCount,
      coveragePercentage: Math.round(coveragePercentage),
      generationTime,
      dataQuality
    };
  }

  /**
   * Generate report title
   */
  private generateReportTitle(format: string, timeRange: TimeRange): string {
    const startDate = new Date(timeRange.start).toLocaleDateString();
    const endDate = new Date(timeRange.end).toLocaleDateString();

    const formatTitles = {
      summary: 'Analytics Summary Report',
      detailed: 'Detailed Analytics Report', 
      executive: 'Executive Analytics Report'
    };

    return `${formatTitles[format as keyof typeof formatTitles]} - ${startDate} to ${endDate}`;
  }

  /**
   * Get real productivity data for peak hours
   */
  private async getPeakHourProductivityData(peakHours: number[]) {
    return wrapDatabaseOperation(async () => {
      const data = [];

      for (const hour of peakHours) {
        // Get actual productivity data for this hour
        const hourlyResult = await this.conversationRepository.findAll(100, 0);
        const hourlyConversations = hourlyResult.data;

        // Calculate productivity for conversations in this hour
        let totalProductivity = 0;
        let conversationCount = 0;

        for (const conversation of hourlyConversations) {
          const messages = await this.messageRepository.findByConversationId(conversation.id);
          const conversationHour = new Date(conversation.createdAt).getHours();

          if (conversationHour === hour) {
            totalProductivity += this.calculateConversationProductivity(messages);
            conversationCount++;
          }
        }

        const avgProductivity = conversationCount > 0 ? totalProductivity / conversationCount : 50;

        data.push({
          label: `${hour}:00`,
          value: Math.round(avgProductivity)
        });
      }

      return data;
    }, 'Failed to get peak hour productivity data');
  }

  /**
   * Calculate conversation productivity score
   */
  private calculateConversationProductivity(messages: any[]): number {
    if (messages.length === 0) return 0;

    let productivityScore = 40; // Base score

    // Message count factor
    const messageCountScore = Math.min(20, messages.length * 2);
    productivityScore += messageCountScore;

    // Average message length factor (indicates depth)
    const avgLength = messages.reduce((sum, m) => sum + m.content.length, 0) / messages.length;
    const lengthScore = Math.min(20, avgLength / 100);
    productivityScore += lengthScore;

    // Question count factor (indicates engagement)
    const questionCount = messages.filter(m => m.content.includes('?')).length;
    const questionScore = Math.min(15, questionCount * 3);
    productivityScore += questionScore;

    // Conversation flow factor (back and forth)
    let flowScore = 0;
    for (let i = 1; i < messages.length; i++) {
      if (messages[i].role !== messages[i-1].role) {
        flowScore += 1;
      }
    }
    const normalizedFlowScore = Math.min(10, (flowScore / Math.max(1, messages.length - 1)) * 20);
    productivityScore += normalizedFlowScore;

    return Math.min(100, Math.max(0, productivityScore));
  }

  /**
   * Static factory method to create a GenerateAnalyticsReportTool instance
   */
  static create(dependencies: GenerateAnalyticsReportDependencies): GenerateAnalyticsReportTool {
    return new GenerateAnalyticsReportTool(dependencies);
  }
}

/**
 * Get Context Summary Tool
 * 
 * MCP tool for retrieving intelligent conversation summaries with context management.
 * Supports hierarchical summarization and token budget optimization.
 */

import { BaseTool, ToolContext } from './BaseTool.js';
import { MCPTool } from '../types/mcp.js';
import { ProviderManager } from '../context/ProviderManager.js';
import { ConversationRepository, MessageRepository } from '../storage/repositories/index.js';
import { createTokenCounter } from '../context/TokenCounter.js';
import { z } from 'zod';

/**
 * Input schema for get_context_summary tool
 */
const GetContextSummarySchema = z.object({
  query: z.string().min(1).describe('Query to contextualize the summary'),
  conversationIds: z.array(z.string()).optional().describe('Specific conversations to summarize'),
  timeRange: z.object({
    start: z.string().datetime().describe('Start date (ISO 8601)'),
    end: z.string().datetime().describe('End date (ISO 8601)')
  }).optional().describe('Time range filter'),
  maxTokens: z.number().min(50).max(8000).default(2000).describe('Maximum tokens for the summary'),
  level: z.enum(['brief', 'standard', 'detailed']).default('standard').describe('Summary detail level'),
  strategy: z.enum(['priority', 'cost-optimal', 'performance', 'quality']).optional().describe('Provider selection strategy'),
  focusTopics: z.array(z.string()).optional().describe('Topics to emphasize in summary'),
  includeMetadata: z.boolean().default(false).describe('Include conversation metadata')
});

type GetContextSummaryInput = z.infer<typeof GetContextSummarySchema>;

/**
 * Tool dependencies
 */
interface GetContextSummaryDependencies {
  providerManager: ProviderManager;
  conversationRepository: ConversationRepository;
  messageRepository: MessageRepository;
}

/**
 * Get Context Summary Tool implementation
 */
export class GetContextSummaryTool extends BaseTool<GetContextSummaryInput, any> {
  private providerManager: ProviderManager;
  private conversationRepository: ConversationRepository;
  private messageRepository: MessageRepository;

  constructor(dependencies: GetContextSummaryDependencies) {
    const tool: MCPTool = {
      name: 'get_context_summary',
      description: 'Get intelligent summary of conversations with context management and token optimization',
      inputSchema: {
        type: 'object',
        properties: {
          query: {
            type: 'string',
            description: 'Query to contextualize the summary'
          },
          conversationIds: {
            type: 'array',
            items: { type: 'string' },
            description: 'Specific conversations to summarize'
          },
          timeRange: {
            type: 'object',
            properties: {
              start: { type: 'string', format: 'date-time' },
              end: { type: 'string', format: 'date-time' }
            },
            description: 'Time range filter'
          },
          maxTokens: {
            type: 'number',
            minimum: 50,
            maximum: 8000,
            default: 2000,
            description: 'Maximum tokens for the summary'
          },
          level: {
            type: 'string',
            enum: ['brief', 'standard', 'detailed'],
            default: 'standard',
            description: 'Summary detail level'
          },
          strategy: {
            type: 'string',
            enum: ['priority', 'cost-optimal', 'performance', 'quality'],
            description: 'Provider selection strategy'
          },
          focusTopics: {
            type: 'array',
            items: { type: 'string' },
            description: 'Topics to emphasize in summary'
          },
          includeMetadata: {
            type: 'boolean',
            default: false,
            description: 'Include conversation metadata'
          }
        },
        required: ['query']
      }
    };

    super(tool, GetContextSummarySchema);
    this.providerManager = dependencies.providerManager;
    this.conversationRepository = dependencies.conversationRepository;
    this.messageRepository = dependencies.messageRepository;
  }

  /**
   * Execute the tool implementation
   */
  protected async executeImpl(params: GetContextSummaryInput, _context: ToolContext): Promise<any> {
    // Find relevant conversations
    const conversations = await this.findRelevantConversations(params);

    if (conversations.length === 0) {
      return {
        summary: 'No conversations found matching the specified criteria.',
        conversationCount: 0,
        tokenCount: 0,
        level: params.level,
        metadata: { query: params.query }
      };
    }

    // Retrieve messages for conversations
    const messages = await this.retrieveMessages(conversations, params);

    if (messages.length === 0) {
      return {
        summary: 'No messages found in the specified conversations.',
        conversationCount: conversations.length,
        tokenCount: 0,
        level: params.level,
        metadata: { query: params.query }
      };
    }

    // Try to generate summary using provider manager
    let summaryResponse;
    try {
      summaryResponse = await this.providerManager.generateSummary({
        messages,
        level: params.level,
        maxTokens: Math.min(params.maxTokens, 4000), // Reasonable limit
        focusTopics: params.focusTopics,
        context: {
          conversationId: conversations[0].id, // Primary conversation
          timeRange: params.timeRange ? {
            start: new Date(params.timeRange.start),
            end: new Date(params.timeRange.end)
          } : undefined
        }
      }, params.strategy);
    } catch (error) {
      // Fallback when no LLM providers are configured
      const fallbackSummary = this.generateFallbackSummary(messages, params);
      summaryResponse = {
        summary: fallbackSummary,
        tokenCount: fallbackSummary.length, // Rough estimate
        inputTokens: messages.reduce((sum, m) => sum + m.content.length, 0),
        outputTokens: fallbackSummary.length,
        cost: 0,
        qualityScore: 0.5,
        processingTime: Date.now(),
        metadata: { model: 'fallback', error: error instanceof Error ? error.message : 'Unknown error' }
      };
    }

    // Prepare response
    return {
      summary: summaryResponse.summary,
      conversationCount: conversations.length,
      messageCount: messages.length,
      tokenCount: summaryResponse.tokenCount,
      inputTokens: summaryResponse.inputTokens,
      outputTokens: summaryResponse.outputTokens,
      level: params.level,
      cost: summaryResponse.cost,
      qualityScore: summaryResponse.qualityScore,
      processingTime: summaryResponse.processingTime,
      metadata: {
        query: params.query,
        conversations: conversations.map((c: any) => ({
          id: c.id,
          title: c.title,
          messageCount: c.message_count,
          createdAt: c.created_at
        })),
        provider: summaryResponse.metadata?.model,
        ...(params.includeMetadata && summaryResponse.metadata)
      }
    };
  }


  /**
   * Find relevant conversations based on parameters
   */
  private async findRelevantConversations(params: GetContextSummaryInput) {
    try {
      // If specific conversation IDs provided, retrieve those
      if (params.conversationIds && params.conversationIds.length > 0) {
        const conversations = [];
        for (const id of params.conversationIds) {
          const conversation = await this.conversationRepository.findById(id);
          if (conversation) {
            conversations.push(conversation);
          }
        }
        return conversations;
      }

      // Otherwise, get recent conversations
      const result = await this.conversationRepository.findAll(10, 0, 'updated_at', 'DESC');
      return result.data;

    } catch (error) {
      console.error('Error finding conversations:', error);
      throw new Error('Failed to find relevant conversations');
    }
  }

  /**
   * Retrieve messages from conversations
   */
  private async retrieveMessages(conversations: any[], params: GetContextSummaryInput) {
    try {
      const allMessages = [];
      const tokenCounter = createTokenCounter('gpt-3.5-turbo'); // Default model for counting

      let totalTokens = 0;
      const tokenBudget = Math.floor(params.maxTokens * 0.8); // 80% for input, 20% for output

      // Sort conversations by relevance/recency
      const sortedConversations = conversations.sort((a, b) => 
        new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime()
      );

      for (const conversation of sortedConversations) {
        const messages = await this.messageRepository.findByConversationId(
          conversation.id,
          { limit: 100, orderBy: 'created_at', orderDir: 'ASC' }
        );

        // Add messages while respecting token budget
        for (const message of messages) {
          const messageTokens = tokenCounter.countText(message.content).count;

          if (totalTokens + messageTokens > tokenBudget) {
            console.log(`Token budget reached: ${totalTokens}/${tokenBudget}`);
            break;
          }

          allMessages.push(message);
          totalTokens += messageTokens;
        }

        if (totalTokens >= tokenBudget) {
          break;
        }
      }

      // Sort messages chronologically
      return allMessages.sort((a, b) => a.createdAt - b.createdAt);

    } catch (error) {
      console.error('Error retrieving messages:', error);
      throw new Error('Failed to retrieve conversation messages');
    }
  }

  /**
   * Generate a basic summary without LLM
   */
  private generateFallbackSummary(messages: any[], params: GetContextSummaryInput): string {
    // Sort messages by timestamp
    const sortedMessages = messages.sort((a, b) => a.createdAt - b.createdAt);

    // Build a basic summary based on the level
    const messageCount = sortedMessages.length;
    const firstMessage = sortedMessages[0];
    const lastMessage = sortedMessages[messageCount - 1];

    let summary = `Context Summary (${params.level} level):\n\n`;

    if (params.level === 'brief') {
      summary += `Found ${messageCount} messages related to "${params.query}".\n`;
      summary += `Time range: ${new Date(firstMessage.createdAt).toLocaleDateString()} to ${new Date(lastMessage.createdAt).toLocaleDateString()}.\n`;

      // Extract key topics from messages
      const topics = this.extractTopics(messages);
      if (topics.length > 0) {
        summary += `Key topics: ${topics.slice(0, 5).join(', ')}.`;
      }

    } else if (params.level === 'standard') {
      summary += `Query: "${params.query}"\n`;
      summary += `Total messages: ${messageCount}\n`;
      summary += `Time period: ${new Date(firstMessage.createdAt).toISOString()} to ${new Date(lastMessage.createdAt).toISOString()}\n\n`;

      // Include first and last message previews
      summary += `First message (${firstMessage.role}): ${firstMessage.content.substring(0, 150)}...\n\n`;
      summary += `Last message (${lastMessage.role}): ${lastMessage.content.substring(0, 150)}...\n\n`;

      // Extract and include topics
      const topics = this.extractTopics(messages);
      if (topics.length > 0) {
        summary += `Main topics discussed: ${topics.slice(0, 10).join(', ')}.`;
      }

    } else { // detailed
      summary += `Detailed Context for: "${params.query}"\n`;
      summary += `Message count: ${messageCount}\n`;
      summary += `Full time range: ${new Date(firstMessage.createdAt).toISOString()} to ${new Date(lastMessage.createdAt).toISOString()}\n\n`;

      // Include more message samples
      const sampleIndices = [0, Math.floor(messageCount / 3), Math.floor(2 * messageCount / 3), messageCount - 1];
      summary += 'Message samples:\n';

      for (const idx of sampleIndices) {
        if (idx < messageCount) {
          const msg = sortedMessages[idx];
          summary += `\n[${new Date(msg.createdAt).toLocaleString()}] ${msg.role}:\n`;
          summary += `${msg.content.substring(0, 200)}...\n`;
        }
      }

      // Extract comprehensive topic list
      const topics = this.extractTopics(messages);
      if (topics.length > 0) {
        summary += `\nAll topics: ${topics.join(', ')}.`;
      }
    }

    if (params.focusTopics && params.focusTopics.length > 0) {
      summary += `\n\nFocus topics requested: ${params.focusTopics.join(', ')}`;
    }

    return summary;
  }

  /**
   * Extract topics from messages (basic implementation)
   */
  private extractTopics(messages: any[]): string[] {
    const wordFreq = new Map<string, number>();

    // Count word frequencies
    for (const msg of messages) {
      const words = msg.content.toLowerCase()
        .replace(/[^a-z0-9\s]/g, ' ')
        .split(/\s+/)
        .filter((word: string) => word.length > 4); // Only words > 4 chars

      for (const word of words) {
        wordFreq.set(word, (wordFreq.get(word) || 0) + 1);
      }
    }

    // Sort by frequency and return top words as topics
    return Array.from(wordFreq.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 20)
      .map(([word]) => word);
  }

  /**
   * Create factory method for tool
   */
  static create(dependencies: GetContextSummaryDependencies): GetContextSummaryTool {
    return new GetContextSummaryTool(dependencies);
  }
}

/**
 * Get Conversation Analytics Tool Implementation
 * 
 * This tool retrieves comprehensive analytics for a specific conversation including:
 * - Flow metrics (topics, transitions, depth, circularity)
 * - Productivity metrics (effectiveness, engagement, insights)
 * - Knowledge gap analysis (unresolved questions, information needs)
 * - Decision tracking (decisions made, outcomes, quality)
 */

import { GetConversationAnalyticsToolDef as GetConversationAnalyticsToolDef } from '../types/mcp.js';
import { GetConversationAnalyticsSchema, GetConversationAnalyticsInput } from '../types/schemas.js';
import { BaseTool, ToolContext, NotFoundError, wrapDatabaseOperation } from './BaseTool.js';
import { AnalyticsEngine } from '../analytics/services/AnalyticsEngine.js';
import { ConversationFlowAnalyzer, ConversationFlowMetrics } from '../analytics/analyzers/ConversationFlowAnalyzer.js';
import { ProductivityAnalyzer, ProductivityMetrics } from '../analytics/analyzers/ProductivityAnalyzer.js';
import { KnowledgeGapDetector, DetectedKnowledgeGap } from '../analytics/analyzers/KnowledgeGapDetector.js';
import { DecisionTracker, Decision } from '../analytics/analyzers/DecisionTracker.js';
import { ConversationRepository } from '../storage/repositories/ConversationRepository.js';
import { MessageRepository } from '../storage/repositories/MessageRepository.js';
import { 
  validateConversationId,
  ValidationError,
  formatValidationError,
  withEnhancedValidation 
} from '../utils/validation.js';

/**
 * Response interface for get_conversation_analytics tool
 */
export interface GetConversationAnalyticsResponse {
  /** Conversation ID that was analyzed */
  conversationId: string;
  /** When the analysis was performed */
  analyzedAt: number;

  /** Flow metrics (optional based on input) */
  flowMetrics?: ConversationFlowMetrics;

  /** Productivity metrics (optional based on input) */
  productivityMetrics?: ProductivityMetrics;

  /** Knowledge gaps identified (optional based on input) */
  knowledgeGaps?: DetectedKnowledgeGap[];

  /** Decisions tracked (optional based on input) */
  decisions?: Decision[];

  /** Summary insights */
  insights: {
    /** Overall conversation quality score (0-100) */
    qualityScore: number;
    /** Key strengths identified */
    strengths: string[];
    /** Areas for improvement */
    improvements: string[];
    /** Notable patterns */
    patterns: string[];
  };

  /** Metadata about the analysis */
  metadata: {
    /** Number of messages analyzed */
    messageCount: number;
    /** Analysis duration in milliseconds */
    analysisDuration: number;
    /** Components included in analysis */
    componentsIncluded: string[];
    /** Conversation title */
    conversationTitle?: string;
  };
}

/**
 * Dependencies required by GetConversationAnalyticsTool
 */
export interface GetConversationAnalyticsDependencies {
  analyticsEngine: AnalyticsEngine;
  conversationRepository: ConversationRepository;
  messageRepository: MessageRepository;
  conversationFlowAnalyzer: ConversationFlowAnalyzer;
  productivityAnalyzer: ProductivityAnalyzer;
  knowledgeGapDetector: KnowledgeGapDetector;
  decisionTracker: DecisionTracker;
}

/**
 * Implementation of the get_conversation_analytics MCP tool
 */
export class GetConversationAnalyticsTool extends BaseTool<GetConversationAnalyticsInput, GetConversationAnalyticsResponse> {
  private readonly analyticsEngine: AnalyticsEngine;
  private readonly conversationRepository: ConversationRepository;
  private readonly messageRepository: MessageRepository;
  private readonly conversationFlowAnalyzer: ConversationFlowAnalyzer;
  private readonly productivityAnalyzer: ProductivityAnalyzer;
  private readonly knowledgeGapDetector: KnowledgeGapDetector;
  private readonly decisionTracker: DecisionTracker;

  constructor(dependencies: GetConversationAnalyticsDependencies) {
    super(GetConversationAnalyticsToolDef, GetConversationAnalyticsSchema);
    this.analyticsEngine = dependencies.analyticsEngine;
    this.conversationRepository = dependencies.conversationRepository;
    this.messageRepository = dependencies.messageRepository;
    this.conversationFlowAnalyzer = dependencies.conversationFlowAnalyzer;
    this.productivityAnalyzer = dependencies.productivityAnalyzer;
    this.knowledgeGapDetector = dependencies.knowledgeGapDetector;
    this.decisionTracker = dependencies.decisionTracker;
  }

  /**
   * Execute the get_conversation_analytics tool
   */
  protected async executeImpl(input: GetConversationAnalyticsInput, _context: ToolContext): Promise<GetConversationAnalyticsResponse> {
    const startTime = Date.now();
    const componentsIncluded: string[] = [];

    try {
      // Step 1: Enhanced validation with comprehensive input checking
      const validatedInput = withEnhancedValidation(() => {
        // Validate conversation ID format and constraints
        const conversationId = validateConversationId(input.conversationId, 'conversationId', true);

        return { 
          conversationId,
          includeFlowMetrics: input.includeFlowMetrics,
          includeProductivityMetrics: input.includeProductivityMetrics,
          includeKnowledgeGaps: input.includeKnowledgeGaps,
          includeDecisionTracking: input.includeDecisionTracking
        };
      }, 'conversation analytics input validation');

      // Step 2: Validate conversation exists and get conversation data
      const conversation = await this.getConversation(validatedInput.conversationId);
      const messages = await this.getMessages(validatedInput.conversationId);

      if (messages.length === 0) {
        throw new NotFoundError(`No messages found for conversation ${validatedInput.conversationId}`);
      }

      // Step 3: Run analytics components based on input flags
      const [flowMetrics, productivityMetrics, knowledgeGaps, decisions] = await Promise.all([
        validatedInput.includeFlowMetrics ? this.analyzeFlowMetrics(conversation, messages, componentsIncluded) : Promise.resolve(undefined),
        validatedInput.includeProductivityMetrics ? this.analyzeProductivityMetrics(conversation, messages, componentsIncluded) : Promise.resolve(undefined),
        validatedInput.includeKnowledgeGaps ? this.analyzeKnowledgeGaps(conversation, messages, componentsIncluded) : Promise.resolve(undefined),
        validatedInput.includeDecisionTracking ? this.analyzeDecisions(conversation, messages, componentsIncluded) : Promise.resolve(undefined)
      ]);

      // Step 4: Generate insights
      const insights = this.generateInsights(flowMetrics, productivityMetrics, knowledgeGaps, decisions);

      // Step 5: Build response metadata
      const analysisDuration = Date.now() - startTime;
      const metadata = {
        messageCount: messages.length,
        analysisDuration,
        componentsIncluded,
        conversationTitle: conversation.title
      };

      return {
        conversationId: validatedInput.conversationId,
        analyzedAt: Date.now(),
        flowMetrics,
        productivityMetrics,
        knowledgeGaps,
        decisions,
        insights,
        metadata
      };

    } catch (error) {
      // Enhanced error handling with user-friendly messages
      if (error instanceof ValidationError) {
        throw new Error(JSON.stringify(formatValidationError(error)));
      }

      // Re-throw other errors with context
      throw new Error(`Conversation analytics failed: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * Get conversation from database
   */
  private async getConversation(conversationId: string) {
    return wrapDatabaseOperation(async () => {
      const conversation = await this.conversationRepository.findById(conversationId);
      if (!conversation) {
        throw new NotFoundError(`Conversation ${conversationId} not found`);
      }
      return conversation;
    }, 'Failed to retrieve conversation');
  }

  /**
   * Get messages for conversation
   */
  private async getMessages(conversationId: string) {
    return wrapDatabaseOperation(async () => {
      const messages = await this.messageRepository.findByConversationId(conversationId);
      return messages;
    }, 'Failed to retrieve messages');
  }

  /**
   * Analyze conversation flow metrics
   */
  private async analyzeFlowMetrics(
    conversation: any, 
    messages: any[], 
    componentsIncluded: string[]
  ): Promise<ConversationFlowMetrics> {
    componentsIncluded.push('flow_metrics');
    return wrapDatabaseOperation(async () => {
      return await this.conversationFlowAnalyzer.analyzeFlow(conversation, messages);
    }, 'Failed to analyze conversation flow metrics');
  }

  /**
   * Analyze productivity metrics
   */
  private async analyzeProductivityMetrics(
    conversation: any, 
    messages: any[], 
    componentsIncluded: string[]
  ): Promise<ProductivityMetrics> {
    componentsIncluded.push('productivity_metrics');
    return wrapDatabaseOperation(async () => {
      return await this.productivityAnalyzer.analyzeConversationProductivity(conversation, messages);
    }, 'Failed to analyze productivity metrics');
  }

  /**
   * Analyze knowledge gaps
   */
  private async analyzeKnowledgeGaps(
    conversation: any, 
    messages: any[], 
    componentsIncluded: string[]
  ): Promise<DetectedKnowledgeGap[]> {
    componentsIncluded.push('knowledge_gaps');
    return wrapDatabaseOperation(async () => {
      return await this.knowledgeGapDetector.detectGaps([{ conversation, messages }]);
    }, 'Failed to analyze knowledge gaps');
  }

  /**
   * Analyze decisions
   */
  private async analyzeDecisions(
    conversation: any, 
    messages: any[], 
    componentsIncluded: string[]
  ): Promise<Decision[]> {
    componentsIncluded.push('decision_tracking');
    return wrapDatabaseOperation(async () => {
      return await this.decisionTracker.trackDecisions(conversation, messages);
    }, 'Failed to track decisions');
  }

  /**
   * Generate insights from analysis results
   */
  private generateInsights(
    flowMetrics?: ConversationFlowMetrics,
    productivityMetrics?: ProductivityMetrics,
    knowledgeGaps?: DetectedKnowledgeGap[],
    decisions?: Decision[]
  ): GetConversationAnalyticsResponse['insights'] {
    const strengths: string[] = [];
    const improvements: string[] = [];
    const patterns: string[] = [];
    let qualityScore = 0;
    let scoreComponents = 0;

    // Flow insights
    if (flowMetrics) {
      qualityScore += flowMetrics.coherenceScore;
      scoreComponents++;

      if (flowMetrics.depthScore > 75) {
        strengths.push('Deep, thorough exploration of topics');
      }
      if (flowMetrics.circularityIndex < 0.3) {
        strengths.push('Good logical progression with minimal repetition');
      } else if (flowMetrics.circularityIndex > 0.6) {
        improvements.push('Reduce circular discussions and topic repetition');
      }

      if (flowMetrics.topicCount > 5) {
        patterns.push(`Wide-ranging discussion covering ${flowMetrics.topicCount} distinct topics`);
      }
    }

    // Productivity insights
    if (productivityMetrics) {
      qualityScore += productivityMetrics.overallProductivityScore;
      scoreComponents++;

      if (productivityMetrics.effectivenessScore > 80) {
        strengths.push('High effectiveness in achieving outcomes');
      }
      if (productivityMetrics.questionMetrics.questionQualityScore > 75) {
        strengths.push('Well-crafted, insightful questions');
      } else if (productivityMetrics.questionMetrics.questionQualityScore < 50) {
        improvements.push('Improve question quality and specificity');
      }

      if (productivityMetrics.outputMetrics.insightCount > 0) {
        patterns.push(`Generated ${productivityMetrics.outputMetrics.insightCount} valuable insights`);
      }
    }

    // Knowledge gap insights
    if (knowledgeGaps && knowledgeGaps.length > 0) {
      const unresolvedGaps = knowledgeGaps.filter(gap => gap.frequency && gap.frequency > 1); // Using frequency as proxy for unresolved
      if (unresolvedGaps.length > 0) {
        improvements.push(`Address ${unresolvedGaps.length} unresolved knowledge gaps`);
        patterns.push(`Identified ${knowledgeGaps.length} knowledge gaps, ${unresolvedGaps.length} unresolved`);
      } else {
        strengths.push('All identified knowledge gaps were resolved');
      }
    }

    // Decision insights
    if (decisions && decisions.length > 0) {
      const highQualityDecisions = decisions.filter(d => d.clarityScore && d.clarityScore > 75);
      if (highQualityDecisions.length > 0) {
        strengths.push(`Made ${highQualityDecisions.length} high-quality decisions`);
      }
      patterns.push(`${decisions.length} decisions identified in conversation`);
    }

    // Calculate final quality score
    const finalQualityScore = scoreComponents > 0 ? Math.round(qualityScore / scoreComponents) : 50;

    // Add general insights based on overall quality
    if (finalQualityScore > 80) {
      strengths.push('Exceptional overall conversation quality');
    } else if (finalQualityScore < 40) {
      improvements.push('Focus on increasing conversation depth and engagement');
    }

    return {
      qualityScore: finalQualityScore,
      strengths: strengths.length > 0 ? strengths : ['Conversation completed successfully'],
      improvements: improvements.length > 0 ? improvements : ['Continue current approach'],
      patterns: patterns.length > 0 ? patterns : ['Standard conversation pattern observed']
    };
  }

  /**
   * Static factory method to create a GetConversationAnalyticsTool instance
   */
  static create(dependencies: GetConversationAnalyticsDependencies): GetConversationAnalyticsTool {
    return new GetConversationAnalyticsTool(dependencies);
  }
}

