  messageRepository: MessageRepository;
}

/**
 * Implementation of the get_conversations MCP tool
 */
export class GetConversationsTool extends BaseTool<GetConversationsInput, GetConversationsResponse> {
  private readonly conversationRepo: ConversationRepository;
  private readonly messageRepo: MessageRepository;

  constructor(dependencies: GetConversationsDependencies) {
    super(GetConversationsToolDef, GetConversationsSchema);
    this.conversationRepo = dependencies.conversationRepository;
    this.messageRepo = dependencies.messageRepository;
  }

  /**
   * Execute the get_conversations tool
   */
  protected async executeImpl(input: GetConversationsInput, _context: ToolContext): Promise<GetConversationsResponse> {
    // Step 1: Validate input parameters
    this.validateInputParameters(input);

    // Step 2: Build query filters
    const filters = this.buildQueryFilters(input);

    // Step 3: Get conversations with pagination
    const conversationResult = await this.getConversations(filters, input);

    // Step 4: Enhance conversations with metadata if requested
    const enhancedConversations = await this.enhanceConversations(
      conversationResult.conversations,
      input.includeMessageCounts ?? false
    );

    // Step 5: Calculate summary statistics
    const summary = await this.calculateSummaryStatistics(
      enhancedConversations,
      input.includeMessageCounts ?? false
    );

    // Step 6: Build pagination information
    const pagination = this.buildPaginationInfo(input, conversationResult.hasMore);

    return {
      conversations: enhancedConversations,
      totalCount: conversationResult.totalCount,
      hasMore: conversationResult.hasMore,
      summary,
      pagination
    };
  }

  /**
   * Validate input parameters
   */
  private validateInputParameters(input: GetConversationsInput): void {
    // Validate date range
    if (input.startDate && input.endDate) {
      const start = new Date(input.startDate);
      const end = new Date(input.endDate);

      if (start > end) {
        throw new ValidationError('Start date must be before end date');
      }
    }

    // Validate pagination parameters
    const limit = input.limit ?? 20;
    const offset = input.offset ?? 0;

    if (limit < 1 || limit > 100) {
      throw new ValidationError('Limit must be between 1 and 100');
    }

    if (offset < 0) {
      throw new ValidationError('Offset must be non-negative');
    }

    if (offset > 10000) {
      throw new ValidationError('Offset cannot exceed 10,000 for performance reasons');
    }
  }

  /**
   * Build query filters from input
   */
  private buildQueryFilters(input: GetConversationsInput): {
    startDate?: number;
    endDate?: number;
    limit: number;
    offset: number;
  } {
    return {
      startDate: input.startDate ? new Date(input.startDate).getTime() : undefined,
      endDate: input.endDate ? new Date(input.endDate).getTime() : undefined,
      limit: input.limit ?? 20,
      offset: input.offset ?? 0
    };
  }

  /**
   * Get conversations with pagination
   */
  private async getConversations(
    _filters: any,
    input: GetConversationsInput
  ): Promise<{
    conversations: Conversation[];
    totalCount: number;
    hasMore: boolean;
  }> {
    return wrapDatabaseOperation(async () => {
      // Get conversations with filters
      const limit = input.limit ?? 20;
      const offset = input.offset ?? 0;

      let conversationResult: any;

      if (_filters.startDate || _filters.endDate) {
        // Use date range filtering if dates are provided
        conversationResult = await this.conversationRepo.findByDateRange(
          _filters.startDate || 0,
          _filters.endDate || Date.now(),
          limit + 1, // Get one extra to check if there are more
          offset
        );
      } else {
        // Use default findAll for no date filtering
        conversationResult = await this.conversationRepo.findAll(
          limit + 1, // Get one extra to check if there are more
          offset,
          'updated_at',
          'DESC'
        );
      }

      // Check if there are more results
      const hasMore = conversationResult.data.length > limit;
      const actualConversations = hasMore ? conversationResult.data.slice(0, limit) : conversationResult.data;

      // Get total count from the paginated result
      const totalCount = conversationResult.totalCount || conversationResult.data.length;

      return {
        conversations: actualConversations,
        totalCount,
        hasMore
      };
    }, 'Failed to retrieve conversations');
  }

  /**
   * Enhance conversations with additional metadata
   */
  private async enhanceConversations(
    conversations: Conversation[],
    includeMessageCounts: boolean
  ): Promise<ConversationWithMetadata[]> {
    if (conversations.length === 0) {
      return [];
    }

    return Promise.all(
      conversations.map(async (conversation) => {
        const enhanced: ConversationWithMetadata = { ...conversation };

        if (includeMessageCounts) {
          // Get message statistics for this conversation
          const messages = await wrapDatabaseOperation(
            () => this.messageRepo.findByConversationId(conversation.id),
            'Failed to get message counts for conversation'
          );

          enhanced.messageCount = messages.length;
          enhanced.roleDistribution = this.calculateRoleDistribution(messages);

          if (messages.length > 0) {
            // Sort messages by creation time
            const sortedMessages = messages.sort((a, b) => a.createdAt - b.createdAt);
            const firstMessage = sortedMessages[0];
            const latestMessage = sortedMessages[sortedMessages.length - 1];

            enhanced.firstMessage = {
              id: firstMessage.id,
              role: firstMessage.role,
              preview: this.createPreview(firstMessage.content),
              createdAt: firstMessage.createdAt
            };

            enhanced.latestMessage = {
              id: latestMessage.id,
              role: latestMessage.role,
              preview: this.createPreview(latestMessage.content),
              createdAt: latestMessage.createdAt
            };

            enhanced.duration = latestMessage.createdAt - firstMessage.createdAt;
          }
        }

        return enhanced;
      })
    );
  }

  /**
   * Calculate role distribution for messages
   */
  private calculateRoleDistribution(messages: any[]): Record<string, number> {
    const distribution: Record<string, number> = {};

    messages.forEach(message => {
      distribution[message.role] = (distribution[message.role] || 0) + 1;
    });

    return distribution;
  }

  /**
   * Create a preview of message content
   */
  private createPreview(content: string): string {
    const cleaned = content.replace(/\s+/g, ' ').trim();
    return cleaned.length > 100 ? cleaned.substring(0, 97) + '...' : cleaned;
  }

  /**
   * Calculate summary statistics
   */
  private async calculateSummaryStatistics(
    conversations: ConversationWithMetadata[],
    includeMessageCounts: boolean
  ): Promise<GetConversationsResponse['summary']> {
    const summary: GetConversationsResponse['summary'] = {};

    if (conversations.length === 0) {
      return summary;
    }

    // Calculate date range
    const createdAts = conversations.map(c => c.createdAt);
    summary.dateRange = {
      earliest: Math.min(...createdAts),
      latest: Math.max(...createdAts)
    };

    // Calculate message statistics if available
    if (includeMessageCounts) {
      const messageCounts = conversations
        .map(c => c.messageCount || 0)
        .filter(count => count > 0);

      if (messageCounts.length > 0) {
        summary.totalMessages = messageCounts.reduce((sum, count) => sum + count, 0);
        summary.averageMessagesPerConversation = summary.totalMessages / messageCounts.length;
      }
    }

    // Calculate time distribution
    summary.timeDistribution = this.calculateTimeDistribution(conversations);

    return summary;
  }

  /**
   * Calculate time distribution of conversations
   */
  private calculateTimeDistribution(conversations: ConversationWithMetadata[]): {
    thisWeek: number;
    thisMonth: number;
    older: number;
  } {
    const now = Date.now();
    const oneWeekAgo = now - (7 * 24 * 60 * 60 * 1000);
    const oneMonthAgo = now - (30 * 24 * 60 * 60 * 1000);

    let thisWeek = 0;
    let thisMonth = 0;
    let older = 0;

    conversations.forEach(conversation => {
      if (conversation.createdAt >= oneWeekAgo) {
        thisWeek++;
      } else if (conversation.createdAt >= oneMonthAgo) {
        thisMonth++;
      } else {
        older++;
      }
    });

    return { thisWeek, thisMonth, older };
  }

  /**
   * Build pagination information
   */
  private buildPaginationInfo(
    input: GetConversationsInput,
    hasMore: boolean
  ): GetConversationsResponse['pagination'] {
    const offset = input.offset ?? 0;
    const limit = input.limit ?? 20;

    return {
      offset,
      limit,
      nextOffset: hasMore ? offset + limit : undefined,
      previousOffset: offset > 0 ? Math.max(0, offset - limit) : undefined
    };
  }


  /**
   * Static factory method to create a GetConversationsTool instance
   */
  static create(dependencies: GetConversationsDependencies): GetConversationsTool {
    return new GetConversationsTool(dependencies);
  }
}

/**
 * Get Entity History Tool
 * 
 * MCP tool to retrieve the complete history of an entity across all conversations,
 * including mentions, relationships, and evolution over time.
 */

import { z } from 'zod';
import { BaseTool, ToolContext } from './BaseTool.js';
import { MCPToolResult, MCPTool } from '../types/mcp.js';
import { KnowledgeGraphService } from '../knowledge-graph/KnowledgeGraphService.js';

/**
 * Schema for get entity history arguments
 */
export const GetEntityHistoryArgsSchema = z.object({
  entity_name: z.string()
    .min(1, 'Entity name must not be empty')
    .max(200, 'Entity name must not exceed 200 characters')
    .describe('Name of the entity to get history for'),

  include_relationships: z.boolean()
    .optional()
    .default(true)
    .describe('Whether to include entity relationships in the response'),

  include_evolution: z.boolean()
    .optional()
    .default(true)
    .describe('Whether to include entity evolution history'),

  max_mentions: z.number()
    .int()
    .min(1)
    .max(500)
    .optional()
    .default(100)
    .describe('Maximum number of mentions to return'),

  time_range: z.object({
    start: z.number().int().positive().describe('Start timestamp (Unix milliseconds)'),
    end: z.number().int().positive().describe('End timestamp (Unix milliseconds)')
  }).optional().describe('Optional time range to filter mentions and relationships')
});

export type GetEntityHistoryArgs = z.infer<typeof GetEntityHistoryArgsSchema>;

/**
 * Get Entity History tool implementation
 */
export class GetEntityHistoryTool extends BaseTool<GetEntityHistoryArgs> {
  readonly name = 'get_entity_history';
  readonly description = 'Get complete history of an entity across all conversations including mentions, relationships, and evolution';
  readonly inputSchema = GetEntityHistoryArgsSchema;

  private knowledgeGraphService: KnowledgeGraphService;

  constructor(knowledgeGraphService: KnowledgeGraphService) {
    const tool: MCPTool = {
      name: 'get_entity_history',
      description: 'Get complete history of an entity across all conversations including mentions, relationships, and evolution',
      inputSchema: {
        type: 'object',
        properties: {
          entity_name: {
            type: 'string',
            description: 'Name of the entity to get history for',
            minLength: 1,
            maxLength: 200
          },
          include_relationships: {
            type: 'boolean',
            description: 'Whether to include entity relationships in the response',
            default: true
          },
          include_evolution: {
            type: 'boolean',
            description: 'Whether to include entity evolution history',
            default: true
          },
          max_mentions: {
            type: 'number',
            description: 'Maximum number of mentions to return',
            minimum: 1,
            maximum: 500,
            default: 100
          },
          time_range: {
            type: 'object',
            properties: {
              start: {
                type: 'number',
                description: 'Start timestamp (Unix milliseconds)'
              },
              end: {
                type: 'number',
                description: 'End timestamp (Unix milliseconds)'
              }
            },
            required: ['start', 'end'],
            additionalProperties: false,
            description: 'Optional time range to filter mentions and relationships'
          }
        },
        required: ['entity_name'],
        additionalProperties: false
      }
    };
    super(tool, GetEntityHistoryArgsSchema);
    this.knowledgeGraphService = knowledgeGraphService;
  }

  /**
   * Execute the tool
   */
  protected async executeImpl(input: GetEntityHistoryArgs, _context: ToolContext): Promise<MCPToolResult> {
    return this.handle(input);
  }

  /**
   * Handle the get entity history request
   */
  async handle(args: GetEntityHistoryArgs): Promise<MCPToolResult> {
    try {
      // Get entity history
      const history = await this.knowledgeGraphService.getEntityHistory(args.entity_name);

      if (!history) {
        return {
          content: [{
            type: 'text',
            text: JSON.stringify({
              success: false,
              error: `Entity '${args.entity_name}' not found in knowledge graph`,
              suggestions: [
                'Check the spelling of the entity name',
                'Try searching for similar entities',
                'The entity might not have been mentioned in any processed conversations'
              ]
            }, null, 2)
          }],
          isError: false
        };
      }

      // Filter mentions by time range if specified
      let filteredMentions = history.mentions;
      if (args.time_range) {
        filteredMentions = history.mentions.filter(mention => 
          mention.createdAt >= args.time_range!.start && 
          mention.createdAt <= args.time_range!.end
        );
      }

      // Limit mentions
      filteredMentions = filteredMentions
        .sort((a, b) => b.createdAt - a.createdAt) // Most recent first
        .slice(0, args.max_mentions);

      // Filter relationships by time range if specified
      let filteredRelationships = history.relationships;
      if (args.time_range) {
        filteredRelationships = history.relationships.filter(rel => 
          rel.firstMentioned >= args.time_range!.start && 
          rel.firstMentioned <= args.time_range!.end
        );
      }

      // Prepare response
      const response = {
        success: true,
        entity: {
          id: history.entity.id,
          name: history.entity.name,
          type: history.entity.type,
          confidence_score: history.entity.confidence_score,
          mention_count: history.entity.mention_count,
          created_at: history.entity.created_at,
          updated_at: history.entity.updated_at,
          last_mentioned_at: history.entity.last_mentioned_at,
          metadata: history.entity.metadata
        },
        mentions: {
          total_count: history.mentions.length,
          filtered_count: filteredMentions.length,
          items: filteredMentions.map(mention => ({
            message_id: mention.messageId,
            conversation_id: mention.conversationId,
            conversation_title: mention.conversationTitle,
            mention_text: mention.mentionText,
            context: mention.content.length > 200 
              ? mention.content.substring(0, 200) + '...'
              : mention.content,
            created_at: mention.createdAt,
            confidence: mention.confidence,
            created_at_formatted: new Date(mention.createdAt).toISOString()
          }))
        },
        relationships: args.include_relationships ? {
          total_count: history.relationships.length,
          filtered_count: filteredRelationships.length,
          items: filteredRelationships
            .sort((a, b) => b.strength - a.strength) // Strongest relationships first
            .map(rel => ({
              related_entity: {
                id: rel.relatedEntity.id,
                name: rel.relatedEntity.name
              },
              relationship_type: rel.relationshipType,
              strength: rel.strength,
              first_mentioned: rel.firstMentioned,
              last_mentioned: rel.lastMentioned,
              first_mentioned_formatted: new Date(rel.firstMentioned).toISOString(),
              last_mentioned_formatted: new Date(rel.lastMentioned).toISOString()
            }))
        } : undefined,
        evolution: args.include_evolution ? {
          total_count: history.evolution.length,
          items: history.evolution.map(evo => ({
            evolution_type: evo.evolutionType,
            previous_value: evo.previousValue,
            new_value: evo.newValue,
            conversation_id: evo.conversationId,
            created_at: evo.createdAt,
            created_at_formatted: new Date(evo.createdAt).toISOString()
          }))
        } : undefined,
        analysis: {
          conversation_span: filteredMentions.length > 0 ? {
            earliest_mention: Math.min(...filteredMentions.map(m => m.createdAt)),
            latest_mention: Math.max(...filteredMentions.map(m => m.createdAt)),
            conversation_count: new Set(filteredMentions.map(m => m.conversationId)).size,
            average_confidence: filteredMentions.reduce((sum, m) => sum + m.confidence, 0) / filteredMentions.length
          } : null,
          relationship_summary: args.include_relationships ? {
            total_relationships: filteredRelationships.length,
            relationship_types: Array.from(new Set(filteredRelationships.map(r => r.relationshipType))),
            strongest_relationship: filteredRelationships.length > 0 
              ? filteredRelationships.reduce((strongest, current) => 
                  current.strength > strongest.strength ? current : strongest
                )
              : null
          } : null
        },
        query_info: {
          entity_name: args.entity_name,
          time_range_applied: !!args.time_range,
          mentions_limit_applied: filteredMentions.length === args.max_mentions,
          generated_at: new Date().toISOString()
        }
      };

      return {
        content: [{
          type: 'text',
          text: JSON.stringify(response, null, 2)
        }],
        isError: false
      };

    } catch (error) {
      console.error('Error in GetEntityHistoryTool:', error);
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: false,
            error: 'Failed to retrieve entity history',
            details: error instanceof Error ? error.message : 'Unknown error',
            entity_name: args.entity_name
          }, null, 2)
        }],
        isError: true
      };
    }
  }

  /**
   * Get tool information for MCP registration
   */
  getToolInfo() {
    return {
      name: this.name,
      description: this.description,
      inputSchema: {
        type: 'object',
        properties: {
          entity_name: {
            type: 'string',
            description: 'Name of the entity to get history for',
            minLength: 1,
            maxLength: 200
          },
          include_relationships: {
            type: 'boolean',
            description: 'Whether to include entity relationships in the response',
            default: true
          },
          include_evolution: {
            type: 'boolean',
            description: 'Whether to include entity evolution history',
            default: true
          },
          max_mentions: {
            type: 'integer',
            description: 'Maximum number of mentions to return',
            minimum: 1,
            maximum: 500,
            default: 100
          },
          time_range: {
            type: 'object',
            description: 'Optional time range to filter mentions and relationships',
            properties: {
              start: {
                type: 'integer',
                description: 'Start timestamp (Unix milliseconds)',
                minimum: 1
              },
              end: {
                type: 'integer', 
                description: 'End timestamp (Unix milliseconds)',
                minimum: 1
              }
            },
            required: ['start', 'end']
          }
        },
        required: ['entity_name']
      }
    };
  }
}

/**
 * Get Index Performance Report Tool
 * 
 * Provides comprehensive index performance monitoring and analysis.
 * Generates detailed reports on index usage, effectiveness, and optimization opportunities.
 */

import { z } from 'zod';
import { BaseTool } from './BaseTool.js';
import { DatabaseManager } from '../storage/Database.js';
import { ProductionPerformanceManager } from '../analytics/performance/ProductionPerformanceManager.js';
import { IndexMonitoringDashboard } from '../analytics/performance/IndexMonitoringDashboard.js';
import { AnalyticsEngine } from '../analytics/services/AnalyticsEngine.js';

const GetIndexPerformanceReportSchema = z.object({
  reportType: z.enum(['overview', 'detailed', 'recommendations', 'health', 'executive']).default('overview'),
  timeframe: z.enum(['hour', 'day', 'week', 'month']).default('day'),
  includeOptimizations: z.boolean().default(true),
  includeAlerts: z.boolean().default(true),
  includeMaintenanceSchedule: z.boolean().default(false),
  format: z.enum(['summary', 'detailed', 'json']).default('summary')
});

export class GetIndexPerformanceReportTool extends BaseTool {
  private performanceManager: ProductionPerformanceManager;
  private dashboard: IndexMonitoringDashboard;

  constructor(databaseManager: DatabaseManager) {
    const toolDef = {
      name: 'get_index_performance_report',
      description: 'Generate comprehensive index performance analysis and monitoring reports',
      inputSchema: {
        type: 'object' as const,
        properties: {
          reportType: { type: 'string', enum: ['overview', 'detailed', 'recommendations', 'health', 'executive'], default: 'overview' },
          timeframe: { type: 'string', enum: ['hour', 'day', 'week', 'month'], default: 'day' },
          includeOptimizations: { type: 'boolean', default: true },
          includeAlerts: { type: 'boolean', default: true },
          includeMaintenanceSchedule: { type: 'boolean', default: false },
          format: { type: 'string', enum: ['summary', 'detailed', 'json'], default: 'summary' }
        },
        additionalProperties: false
      }
    };
    super(toolDef, GetIndexPerformanceReportSchema);

    // Initialize performance monitoring components
    const analyticsEngine = new AnalyticsEngine(databaseManager);
    this.performanceManager = new ProductionPerformanceManager(
      databaseManager,
      analyticsEngine,
      {
        monitoring: { 
          enabled: true, 
          intervalMinutes: 15,
          alertThresholds: {
            slowQueryMs: 1000,
            unusedIndexDays: 30,
            writeImpactThreshold: 0.1,
            memoryUsageThresholdMB: 100
          },
          retentionDays: 30
        },
        optimization: { 
          autoOptimizeEnabled: false,
          autoDropUnusedIndexes: false,
          maxConcurrentOptimizations: 1,
          maintenanceWindowHours: [2, 3, 4], // 2-4 AM
          riskTolerance: 'conservative'
        },
        alerts: { 
          emailNotifications: false,
          escalationThresholds: {
            criticalAlertCount: 5,
            highAlertDurationMinutes: 60
          }
        }
      }
    );
    this.dashboard = new IndexMonitoringDashboard(databaseManager);
  }

  async executeImpl(args: z.infer<typeof GetIndexPerformanceReportSchema>) {
    try {
      console.log(`Generating ${args.reportType} index performance report for ${args.timeframe} timeframe`);

      // Initialize monitoring if not already running
      try {
        await this.performanceManager.initialize();
      } catch (error) {
        // Manager might already be initialized
        const errorMessage = error instanceof Error ? error.message : String(error);
        if (!errorMessage.includes('already running')) {
          console.warn('Performance manager initialization warning:', errorMessage);
        }
      }

      let reportData: any;

      switch (args.reportType) {
        case 'overview':
          reportData = await this.generateOverviewReport(args);
          break;
        case 'detailed':
          reportData = await this.generateDetailedReport(args);
          break;
        case 'recommendations':
          reportData = await this.generateRecommendationsReport(args);
          break;
        case 'health':
          reportData = await this.generateHealthReport(args);
          break;
        case 'executive':
          reportData = await this.generateExecutiveReport(args);
          break;
        default:
          throw new Error(`Unsupported report type: ${args.reportType}`);
      }

      // Format the response based on requested format
      if (args.format === 'json') {
        return {
          success: true,
          reportType: args.reportType,
          timeframe: args.timeframe,
          generatedAt: new Date().toISOString(),
          data: reportData
        };
      } else {
        return {
          success: true,
          report: this.formatReport(args.reportType, reportData, args.format)
        };
      }

    } catch (error) {
      console.error('Error generating index performance report:', error);
      return {
        success: false,
        error: `Failed to generate performance report: ${error instanceof Error ? error.message : String(error)}`,
        reportType: args.reportType
      };
    }
  }

  private async generateOverviewReport(args: any) {
    const status = await this.performanceManager.getPerformanceStatus();
    const metrics = await this.dashboard.getCurrentMetrics();
    const alerts = this.dashboard.getActiveAlerts();

    return {
      systemStatus: status,
      performance: {
        totalIndexes: metrics.overview.totalIndexes,
        activeIndexes: metrics.overview.activeIndexes,
        unusedIndexes: metrics.overview.unusedIndexes,
        averageQueryTime: Math.round(metrics.overview.averageQueryTime),
        indexEffectiveness: Math.round(metrics.performance.indexEffectiveness * 100),
        cacheHitRate: Math.round(metrics.performance.cacheHitRate * 100)
      },
      alerts: {
        total: alerts.length,
        critical: alerts.filter(a => a.severity === 'critical').length,
        high: alerts.filter(a => a.severity === 'high').length
      },
      trends: metrics.trends
    };
  }

  private async generateDetailedReport(args: any) {
    const [healthReport, insights, optimization] = await Promise.all([
      this.dashboard.getIndexHealthReport(),
      this.dashboard.getQueryPerformanceInsights(),
      args.includeOptimizations ? this.dashboard.getOptimizationRecommendations() : []
    ]);

    return {
      indexHealth: healthReport.map(health => ({
        index: health.indexName,
        table: health.tableName,
        health: health.health,
        score: Math.round(health.healthScore),
        issues: health.issues,
        recommendations: health.recommendations,
        usage: {
          frequency: health.metrics.usageFrequency,
          effectiveness: Math.round(health.metrics.effectiveness * 100),
          lastUsed: health.metrics.lastUsed ? new Date(health.metrics.lastUsed).toISOString() : 'Never',
          size: `${Math.round(health.metrics.storageSize / 1024)}KB`
        }
      })),
      queryInsights: insights.slice(0, 10).map(insight => ({
        pattern: this.sanitizeQuery(insight.queryPattern),
        frequency: insight.frequency,
        avgTime: `${Math.round(insight.avgExecutionTime)}ms`,
        impact: insight.impact,
        optimizationPotential: `${Math.round(insight.optimizationPotential)}%`
      })),
      optimizations: args.includeOptimizations ? optimization.slice(0, 5).map(opt => ({
        type: opt.type,
        target: opt.indexName,
        table: opt.tableName,
        reason: opt.reason,
        impact: opt.expectedImpact,
        risk: opt.riskLevel,
        benefit: Math.round(opt.estimatedBenefit),
        complexity: opt.implementationComplexity,
        priority: opt.implementationPriority
      })) : []
    };
  }

  private async generateRecommendationsReport(args: any) {
    const recommendations = await this.dashboard.getOptimizationRecommendations();
    const maintenanceSchedule = args.includeMaintenanceSchedule ? 
      this.dashboard.getMaintenanceSchedule() : [];

    return {
      optimizations: recommendations.map(rec => ({
        priority: rec.implementationPriority,
        type: rec.type,
        target: rec.indexName,
        table: rec.tableName,
        reason: rec.reason,
        expectedImpact: rec.expectedImpact,
        riskLevel: rec.riskLevel,
        estimatedBenefit: `${Math.round(rec.estimatedBenefit)}ms improvement`,
        complexity: rec.implementationComplexity,
        costBenefitScore: Math.round(rec.costBenefitScore),
        riskAssessment: rec.riskAssessment,
        sql: rec.sql
      })),
      maintenance: args.includeMaintenanceSchedule ? maintenanceSchedule.map(task => ({
        task: task.task,
        target: task.target,
        scheduledTime: new Date(task.scheduledTime).toISOString(),
        priority: task.priority,
        estimatedDuration: `${Math.round(task.estimatedDuration / 60000)} minutes`,
        impact: task.impact,
        prerequisites: task.prerequisites
      })) : []
    };
  }

  private async generateHealthReport(args: any) {
    const healthReports = await this.dashboard.getIndexHealthReport();
    const alerts = this.dashboard.getActiveAlerts();

    // Categorize indexes by health
    const healthCategories = {
      excellent: healthReports.filter(h => h.health === 'excellent'),
      good: healthReports.filter(h => h.health === 'good'),
      fair: healthReports.filter(h => h.health === 'fair'),
      poor: healthReports.filter(h => h.health === 'poor'),
      critical: healthReports.filter(h => h.health === 'critical')
    };

    return {
      summary: {
        totalIndexes: healthReports.length,
        healthDistribution: {
          excellent: healthCategories.excellent.length,
          good: healthCategories.good.length,
          fair: healthCategories.fair.length,
          poor: healthCategories.poor.length,
          critical: healthCategories.critical.length
        },
        averageHealthScore: Math.round(
          healthReports.reduce((sum, h) => sum + h.healthScore, 0) / healthReports.length
        )
      },
      criticalIndexes: healthCategories.critical.map(h => ({
        index: h.indexName,
        table: h.tableName,
        score: Math.round(h.healthScore),
        issues: h.issues,
        recommendations: h.recommendations.slice(0, 2) // Top 2 recommendations
      })),
      poorIndexes: healthCategories.poor.map(h => ({
        index: h.indexName,
        table: h.tableName,
        score: Math.round(h.healthScore),
        primaryIssue: h.issues[0] || 'Unknown issue'
      })),
      unusedIndexes: healthReports
        .filter(h => h.issues.includes('Never used') || h.issues.includes('Rarely used'))
        .map(h => ({
          index: h.indexName,
          table: h.tableName,
          size: `${Math.round(h.metrics.storageSize / 1024)}KB`,
          lastUsed: h.metrics.lastUsed ? new Date(h.metrics.lastUsed).toISOString() : 'Never'
        })),
      activeAlerts: args.includeAlerts ? alerts.slice(0, 5).map(alert => ({
        type: alert.type,
        severity: alert.severity,
        message: alert.message,
        age: `${Math.round((Date.now() - alert.timestamp) / (60 * 1000))} minutes`,
        actionPlan: alert.actionPlan.slice(0, 2) // Top 2 actions
      })) : []
    };
  }

  private async generateExecutiveReport(args: any) {
    const executiveSummary = await this.dashboard.generateExecutiveSummary();
    const performanceReport = await this.performanceManager.generatePerformanceReport(
      args.timeframe === 'hour' ? 1/24 : 
      args.timeframe === 'day' ? 1 :
      args.timeframe === 'week' ? 7 : 30
    );

    return {
      executiveSummary: {
        summary: executiveSummary.summary,
        keyMetrics: executiveSummary.keyMetrics,
        overallHealth: `${Math.round(performanceReport.summary.overallHealth)}%`,
        performanceScore: `${performanceReport.summary.performanceScore}%`
      },
      criticalIssues: executiveSummary.criticalIssues.slice(0, 3),
      topRecommendations: executiveSummary.recommendations.slice(0, 3),
      trendAnalysis: {
        performanceTrend: executiveSummary.trendAnalysis.performanceTrend,
        efficiency: `${Math.round(executiveSummary.trendAnalysis.efficiency * 100)}%`,
        capacityUtilization: `${Math.round(executiveSummary.trendAnalysis.capacityUtilization * 100)}%`
      },
      achievements: performanceReport.achievements.slice(0, 3),
      concerns: performanceReport.concerns.slice(0, 3),
      actionItems: this.generateActionItems(executiveSummary),
      nextSteps: this.generateNextSteps(executiveSummary, performanceReport)
    };
  }

  private formatReport(reportType: string, data: any, format: 'summary' | 'detailed'): string {
    const lines: string[] = [];
    const timestamp = new Date().toISOString().replace('T', ' ').substring(0, 19);

    lines.push(`=== Index Performance Report: ${reportType.toUpperCase()} ===`);
    lines.push(`Generated: ${timestamp}`);
    lines.push('');

    switch (reportType) {
      case 'overview':
        lines.push(...this.formatOverviewReport(data, format));
        break;
      case 'detailed':
        lines.push(...this.formatDetailedReport(data, format));
        break;
      case 'recommendations':
        lines.push(...this.formatRecommendationsReport(data, format));
        break;
      case 'health':
        lines.push(...this.formatHealthReport(data, format));
        break;
      case 'executive':
        lines.push(...this.formatExecutiveReport(data, format));
        break;
    }

    return lines.join('\n');
  }

  private formatOverviewReport(data: any, format: string): string[] {
    const lines: string[] = [];

    lines.push(`System Status: ${data.systemStatus.overall.toUpperCase()}`);
    lines.push(`Last Update: ${new Date(data.systemStatus.lastUpdate).toLocaleString()}`);
    lines.push('');

    lines.push(' PERFORMANCE METRICS');
    lines.push(` Total Indexes: ${data.performance.totalIndexes}`);
    lines.push(` Active Indexes: ${data.performance.activeIndexes}`);
    lines.push(` Unused Indexes: ${data.performance.unusedIndexes}`);
    lines.push(` Average Query Time: ${data.performance.averageQueryTime}ms`);
    lines.push(` Index Effectiveness: ${data.performance.indexEffectiveness}%`);
    lines.push(` Cache Hit Rate: ${data.performance.cacheHitRate}%`);
    lines.push('');

    if (data.alerts.total > 0) {
      lines.push(' ACTIVE ALERTS');
      lines.push(` Total: ${data.alerts.total}`);
      lines.push(` Critical: ${data.alerts.critical}`);
      lines.push(` High: ${data.alerts.high}`);
      lines.push('');
    }

    if (format === 'detailed' && data.trends.indexUsageTrend?.length > 0) {
      lines.push(' INDEX USAGE TRENDS');
      data.trends.indexUsageTrend.forEach((trend: any) => {
        const arrow = trend.trend === 'up' ? '' : trend.trend === 'down' ? '' : '';
        lines.push(` ${trend.index}: ${arrow} ${trend.trend}`);
      });
      lines.push('');
    }

    return lines;
  }

  private formatDetailedReport(data: any, format: string): string[] {
    const lines: string[] = [];

    // Index Health Summary
    lines.push(' INDEX HEALTH STATUS');
    const healthCounts = data.indexHealth.reduce((acc: any, index: any) => {
      acc[index.health] = (acc[index.health] || 0) + 1;
      return acc;
    }, {});

    Object.entries(healthCounts).forEach(([health, count]) => {
      const emoji = health === 'critical' ? '' : health === 'poor' ? '' : 
                   health === 'fair' ? '' : health === 'good' ? '' : '';
      lines.push(` ${emoji} ${health}: ${count} indexes`);
    });
    lines.push('');

    // Critical/Poor Indexes
    const problemIndexes = data.indexHealth.filter((i: any) => 
      i.health === 'critical' || i.health === 'poor'
    );

    if (problemIndexes.length > 0) {
      lines.push('  INDEXES REQUIRING ATTENTION');
      problemIndexes.slice(0, 5).forEach((index: any) => {
        lines.push(` ${index.index} (${index.table}): ${index.health} - Score: ${index.score}%`);
        lines.push(`  Issues: ${index.issues.slice(0, 2).join(', ')}`);
        if (format === 'detailed' && index.recommendations.length > 0) {
          lines.push(`  Action: ${index.recommendations[0]}`);
        }
      });
      lines.push('');
    }

    // Query Performance Insights
    if (data.queryInsights.length > 0) {
      lines.push(' SLOW QUERY PATTERNS');
      data.queryInsights.slice(0, 3).forEach((insight: any) => {
        lines.push(` Pattern: ${insight.pattern.substring(0, 60)}...`);
        lines.push(`  Frequency: ${insight.frequency}, Avg Time: ${insight.avgTime}, Impact: ${insight.impact}`);
        if (insight.optimizationPotential > 50) {
          lines.push(`   High optimization potential: ${insight.optimizationPotential}`);
        }
      });
      lines.push('');
    }

    // Top Optimizations
    if (data.optimizations.length > 0) {
      lines.push(' TOP OPTIMIZATION OPPORTUNITIES');
      data.optimizations.slice(0, 3).forEach((opt: any) => {
        const riskEmoji = opt.risk === 'low' ? '' : opt.risk === 'medium' ? '' : '';
        lines.push(` ${opt.type.toUpperCase()}: ${opt.target} (${opt.table})`);
        lines.push(`  ${riskEmoji} ${opt.reason}`);
        lines.push(`  Expected benefit: ${opt.benefit}ms, Risk: ${opt.risk}, Priority: ${opt.priority}`);
      });
    }

    return lines;
  }

  private formatRecommendationsReport(data: any, format: string): string[] {
    const lines: string[] = [];

    lines.push(' OPTIMIZATION RECOMMENDATIONS');
    lines.push('');

    data.optimizations.slice(0, 10).forEach((rec: any, index: number) => {
      const priorityEmoji = rec.priority === 1 ? '' : rec.priority === 2 ? '' : '';
      const riskEmoji = rec.riskLevel === 'low' ? '' : rec.riskLevel === 'medium' ? '' : '';

      lines.push(`${index + 1}. ${priorityEmoji} ${rec.type.toUpperCase()}: ${rec.target}`);
      lines.push(`   Table: ${rec.table}`);
      lines.push(`   Reason: ${rec.reason}`);
      lines.push(`   ${riskEmoji} Risk: ${rec.riskLevel} | Impact: ${rec.expectedImpact} | Benefit: ${rec.estimatedBenefit}`);
      lines.push(`   Complexity: ${rec.complexity} | Score: ${rec.costBenefitScore}`);

      if (format === 'detailed') {
        lines.push(`   Risk Assessment: ${rec.riskAssessment}`);
        if (rec.sql && rec.sql.length < 100) {
          lines.push(`   SQL: ${rec.sql}`);
        }
      }
      lines.push('');
    });

    if (data.maintenance.length > 0) {
      lines.push('  SCHEDULED MAINTENANCE');
      data.maintenance.slice(0, 5).forEach((task: any) => {
        const priorityEmoji = task.priority === 'critical' ? '' : 
                             task.priority === 'high' ? '' : '';
        lines.push(` ${priorityEmoji} ${task.task.toUpperCase()}: ${task.target}`);
        lines.push(`  Scheduled: ${new Date(task.scheduledTime).toLocaleString()}`);
        lines.push(`  Duration: ${task.estimatedDuration} | Impact: ${task.impact}`);
      });
    }

    return lines;
  }

  private formatHealthReport(data: any, format: string): string[] {
    const lines: string[] = [];

    lines.push(' INDEX HEALTH OVERVIEW');
    lines.push(`Total Indexes: ${data.summary.totalIndexes} | Average Health: ${data.summary.averageHealthScore}%`);
    lines.push('');

    // Health Distribution
    lines.push(' HEALTH DISTRIBUTION');
    Object.entries(data.summary.healthDistribution).forEach(([health, count]) => {
      const emoji = health === 'critical' ? '' : health === 'poor' ? '' : 
                   health === 'fair' ? '' : health === 'good' ? '' : '';
      const percentage = Math.round((count as number) / data.summary.totalIndexes * 100);
      lines.push(` ${emoji} ${health}: ${count} (${percentage}%)`);
    });
    lines.push('');

    // Critical Issues
    if (data.criticalIndexes.length > 0) {
      lines.push(' CRITICAL INDEXES (Immediate Attention Required)');
      data.criticalIndexes.forEach((index: any) => {
        lines.push(` ${index.index} (${index.table}) - Score: ${index.score}%`);
        lines.push(`  Issues: ${index.issues.join(', ')}`);
        lines.push(`  Actions: ${index.recommendations.join('; ')}`);
        lines.push('');
      });
    }

    // Poor Performance
    if (data.poorIndexes.length > 0) {
      lines.push('  POOR PERFORMANCE INDEXES');
      data.poorIndexes.forEach((index: any) => {
        lines.push(` ${index.index} (${index.table}) - Score: ${index.score}% - ${index.primaryIssue}`);
      });
      lines.push('');
    }

    // Unused Indexes
    if (data.unusedIndexes.length > 0) {
      lines.push(' UNUSED INDEXES (Storage Optimization Opportunity)');
      data.unusedIndexes.slice(0, 5).forEach((index: any) => {
        lines.push(` ${index.index} (${index.table}) - Size: ${index.size} - Last used: ${index.lastUsed}`);
      });
      if (data.unusedIndexes.length > 5) {
        lines.push(`... and ${data.unusedIndexes.length - 5} more`);
      }
      lines.push('');
    }

    // Active Alerts
    if (data.activeAlerts.length > 0) {
      lines.push(' ACTIVE ALERTS');
      data.activeAlerts.forEach((alert: any) => {
        const emoji = alert.severity === 'critical' ? '' : alert.severity === 'high' ? '' : '';
        lines.push(` ${emoji} ${alert.message} (${alert.age} ago)`);
        if (format === 'detailed' && alert.actionPlan.length > 0) {
          lines.push(`  Next steps: ${alert.actionPlan.join('; ')}`);
        }
      });
    }

    return lines;
  }

  private formatExecutiveReport(data: any, format: string): string[] {
    const lines: string[] = [];

    lines.push(' EXECUTIVE SUMMARY');
    lines.push(data.executiveSummary.summary);
    lines.push('');

    lines.push(' KEY PERFORMANCE INDICATORS');
    lines.push(` System Health: ${data.executiveSummary.overallHealth}`);
    lines.push(` Performance Score: ${data.executiveSummary.performanceScore}`);
    lines.push(` Total Indexes: ${data.executiveSummary.keyMetrics.totalIndexes}`);
    lines.push(` Efficiency Score: ${data.executiveSummary.keyMetrics.efficiencyScore}%`);
    lines.push(` Average Query Time: ${data.executiveSummary.keyMetrics.averageQueryTime}ms`);
    lines.push('');

    lines.push(' TREND ANALYSIS');
    lines.push(` Performance Trend: ${data.trendAnalysis.performanceTrend.toUpperCase()}`);
    lines.push(` System Efficiency: ${data.trendAnalysis.efficiency}`);
    lines.push(` Storage Utilization: ${data.trendAnalysis.capacityUtilization}`);
    lines.push('');

    if (data.achievements.length > 0) {
      lines.push(' ACHIEVEMENTS');
      data.achievements.forEach((achievement: string) => {
        lines.push(` ${achievement}`);
      });
      lines.push('');
    }

    if (data.criticalIssues.length > 0) {
      lines.push(' CRITICAL ISSUES');
      data.criticalIssues.forEach((issue: any) => {
        lines.push(` ${issue.issue}`);
        lines.push(`  Impact: ${issue.impact}`);
        lines.push(`  Urgency: ${issue.urgency}`);
      });
      lines.push('');
    }

    if (data.concerns.length > 0) {
      lines.push('  AREAS OF CONCERN');
      data.concerns.forEach((concern: string) => {
        lines.push(` ${concern}`);
      });
      lines.push('');
    }

    lines.push(' TOP RECOMMENDATIONS');
    data.topRecommendations.forEach((rec: any, index: number) => {
      lines.push(`${index + 1}. ${rec.recommendation}`);
      lines.push(`   Expected Benefit: ${rec.expectedBenefit}`);
    });
    lines.push('');

    lines.push(' IMMEDIATE ACTION ITEMS');
    data.actionItems.forEach((item: string, index: number) => {
      lines.push(`${index + 1}. ${item}`);
    });
    lines.push('');

    lines.push(' NEXT STEPS');
    data.nextSteps.forEach((step: string, index: number) => {
      lines.push(`${index + 1}. ${step}`);
    });

    return lines;
  }

  private generateActionItems(executiveSummary: any): string[] {
    const actions: string[] = [];

    if (executiveSummary.criticalIssues.length > 0) {
      actions.push(`Address ${executiveSummary.criticalIssues.length} critical issues within 24 hours`);
    }

    if (executiveSummary.keyMetrics.unusedIndexes > 0) {
      actions.push(`Review and remove ${executiveSummary.keyMetrics.unusedIndexes} unused indexes`);
    }

    if (executiveSummary.keyMetrics.efficiencyScore < 70) {
      actions.push('Implement top 3 optimization recommendations');
    }

    actions.push('Schedule regular performance review meetings');
    actions.push('Enable automated monitoring and alerting');

    return actions.slice(0, 5);
  }

  private generateNextSteps(executiveSummary: any, performanceReport: any): string[] {
    const steps: string[] = [];

    if (performanceReport.summary.optimizationsExecuted === 0) {
      steps.push('Begin implementing low-risk index optimizations');
    }

    if (executiveSummary.trendAnalysis.performanceTrend === 'degrading') {
      steps.push('Investigate root cause of performance degradation');
    }

    steps.push('Establish baseline performance metrics for trending');
    steps.push('Configure automated maintenance windows');
    steps.push('Develop index lifecycle management procedures');

    return steps.slice(0, 5);
  }

  private sanitizeQuery(query: string): string {
    // Remove sensitive data and normalize query for display
    return query
      .replace(/\b\d+\b/g, '?') // Replace numbers with placeholders
      .replace(/('[^']*'|"[^"]*")/g, '?') // Replace string literals
      .substring(0, 80); // Limit length
  }
}

/**
 * Get Knowledge Graph Tool
 * 
 * MCP tool to explore the knowledge graph around a specific entity,
 * finding connected entities and their relationships within N degrees.
 */

import { z } from 'zod';
import { BaseTool, ToolContext } from './BaseTool.js';
import { MCPToolResult, MCPTool } from '../types/mcp.js';
import { KnowledgeGraphService } from '../knowledge-graph/KnowledgeGraphService.js';

/**
 * Schema for get knowledge graph arguments
 */
export const GetKnowledgeGraphArgsSchema = z.object({
  center_entity: z.string()
    .min(1, 'Center entity name must not be empty')
    .max(200, 'Center entity name must not exceed 200 characters')
    .describe('Name of the entity to center the knowledge graph around'),

  max_degrees: z.number()
    .int()
    .min(1)
    .max(4)
    .optional()
    .default(2)
    .describe('Maximum degrees of separation to explore (1-4)'),

  min_strength: z.number()
    .min(0.0)
    .max(1.0)
    .optional()
    .default(0.3)
    .describe('Minimum relationship strength threshold (0.0 to 1.0)'),

  entity_types: z.array(z.enum([
    'person', 'organization', 'product', 'concept', 
    'location', 'technical', 'event', 'decision'
  ]))
    .optional()
    .describe('Filter connected entities by specific types'),

  relationship_types: z.array(z.enum([
    'works_for', 'created_by', 'discussed_with', 'related_to',
    'part_of', 'mentioned_with', 'temporal_sequence', 'cause_effect'
  ]))
    .optional()
    .describe('Filter relationships by specific types'),

  include_clusters: z.boolean()
    .optional()
    .default(false)
    .describe('Whether to include entity cluster analysis'),

  include_paths: z.boolean()
    .optional()
    .default(true)
    .describe('Whether to include shortest paths to connected entities'),

  max_entities: z.number()
    .int()
    .min(1)
    .max(200)
    .optional()
    .default(50)
    .describe('Maximum number of connected entities to return')
});

export type GetKnowledgeGraphArgs = z.infer<typeof GetKnowledgeGraphArgsSchema>;

/**
 * Get Knowledge Graph tool implementation
 */
export class GetKnowledgeGraphTool extends BaseTool<GetKnowledgeGraphArgs> {
  readonly name = 'get_knowledge_graph';
  readonly description = 'Explore the knowledge graph around a specific entity, finding connected entities and relationships';
  readonly inputSchema = GetKnowledgeGraphArgsSchema;

  private knowledgeGraphService: KnowledgeGraphService;

  constructor(knowledgeGraphService: KnowledgeGraphService) {
    const tool: MCPTool = {
      name: 'get_knowledge_graph',
      description: 'Explore the knowledge graph around an entity, finding connected entities and relationships',
      inputSchema: {
        type: 'object',
        properties: {
          center_entity: {
            type: 'string',
            description: 'Name of the entity to center the knowledge graph around',
            minLength: 1,
            maxLength: 200
          },
          max_degrees: {
            type: 'number',
            description: 'Maximum degrees of separation to explore (1-4)',
            minimum: 1,
            maximum: 4,
            default: 2
          },
          min_strength: {
            type: 'number',
            description: 'Minimum relationship strength threshold (0.0 to 1.0)',
            minimum: 0.0,
            maximum: 1.0,
            default: 0.3
          },
          entity_types: {
            type: 'array',
            items: {
              type: 'string',
              enum: ['person', 'organization', 'product', 'concept', 
                     'location', 'technical', 'event', 'decision']
            },
            description: 'Filter connected entities by specific types'
          },
          relationship_types: {
            type: 'array',
            items: {
              type: 'string',
              enum: ['works_for', 'created_by', 'discussed_with', 'related_to',
                     'part_of', 'mentioned_with', 'temporal_sequence', 'cause_effect']
            },
            description: 'Filter relationships by specific types'
          },
          include_clusters: {
            type: 'boolean',
            description: 'Whether to include entity cluster analysis',
            default: false
          },
          include_paths: {
            type: 'boolean',
            description: 'Whether to include shortest paths to connected entities',
            default: true
          },
          max_entities: {
            type: 'number',
            description: 'Maximum number of connected entities to return',
            minimum: 1,
            maximum: 200,
            default: 50
          }
        },
        required: ['center_entity'],
        additionalProperties: false
      }
    };
    super(tool, GetKnowledgeGraphArgsSchema);
    this.knowledgeGraphService = knowledgeGraphService;
  }

  /**
   * Execute the tool
   */
  protected async executeImpl(input: GetKnowledgeGraphArgs, _context: ToolContext): Promise<MCPToolResult> {
    return this.handle(input);
  }

  /**
   * Handle the get knowledge graph request
   */
  async handle(args: GetKnowledgeGraphArgs): Promise<MCPToolResult> {
    try {
      // Get the center entity history to verify it exists
      const centerEntityHistory = await this.knowledgeGraphService.getEntityHistory(args.center_entity);

      if (!centerEntityHistory) {
        return {
          content: [{
            type: 'text',
            text: JSON.stringify({
              success: false,
              error: `Center entity '${args.center_entity}' not found in knowledge graph`,
              suggestions: [
                'Check the spelling of the entity name',
                'Try searching for the entity first',
                'The entity might not have been mentioned in any processed conversations'
              ]
            }, null, 2)
          }],
          isError: false
        };
      }

      // Search the knowledge graph around this entity
      const searchResults = await this.knowledgeGraphService.searchKnowledgeGraph(
        args.center_entity,
        {
          includeEntities: true,
          includeRelationships: true,
          maxDegrees: args.max_degrees,
          minStrength: args.min_strength,
          limit: args.max_entities
        }
      );

      // Filter by entity types if specified
      let connectedEntities = searchResults.connectedEntities;
      if (args.entity_types && args.entity_types.length > 0) {
        connectedEntities = connectedEntities.filter(entity => 
          args.entity_types!.includes(entity.entity_type)
        );
      }

      // Filter by relationship types if specified
      if (args.relationship_types && args.relationship_types.length > 0) {
        connectedEntities = connectedEntities.filter(entity => 
          args.relationship_types!.includes(entity.relationship_type)
        );
      }

      // Limit results
      connectedEntities = connectedEntities.slice(0, args.max_entities);

      // Group entities by degree
      const entitiesByDegree: Record<number, typeof connectedEntities> = {};
      for (const entity of connectedEntities) {
        if (!entitiesByDegree[entity.degree]) {
          entitiesByDegree[entity.degree] = [];
        }
        entitiesByDegree[entity.degree].push(entity);
      }

      // Get entity clusters if requested
      let clusters: any[] = [];
      if (args.include_clusters) {
        try {
          clusters = await this.knowledgeGraphService.getCrossConversationAnalysis()
            .then(analysis => analysis.entityClusters)
            .catch(() => []); // Fail gracefully if clustering isn't available
        } catch {
          // Clustering feature might not be fully implemented
        }
      }

      // Generate paths if requested
      const pathAnalysis = args.include_paths ? this.analyzeEntityPaths(connectedEntities) : null;

      // Prepare response
      const response = {
        success: true,
        center_entity: {
          id: centerEntityHistory.entity.id,
          name: centerEntityHistory.entity.name,
          type: centerEntityHistory.entity.type,
          confidence_score: centerEntityHistory.entity.confidence_score,
          mention_count: centerEntityHistory.entity.mention_count,
          relationship_count: centerEntityHistory.relationships.length
        },
        graph: {
          total_connected_entities: connectedEntities.length,
          max_degrees_explored: args.max_degrees,
          min_strength_applied: args.min_strength,
          entities_by_degree: Object.keys(entitiesByDegree).map(degree => ({
            degree: parseInt(degree),
            count: entitiesByDegree[parseInt(degree)].length,
            entities: entitiesByDegree[parseInt(degree)].map(entity => ({
              id: entity.entity_id,
              name: entity.entity_name,
              type: entity.entity_type,
              relationship_type: entity.relationship_type,
              strength: entity.strength,
              path: entity.path
            }))
          })).sort((a, b) => a.degree - b.degree),
          relationship_summary: this.summarizeRelationships(connectedEntities),
          entity_type_distribution: this.analyzeEntityTypes(connectedEntities)
        },
        clusters: args.include_clusters ? {
          total_clusters: clusters.length,
          relevant_clusters: clusters.filter(cluster => 
            cluster.cluster_members.some((member: any) => 
              member.name.toLowerCase().includes(args.center_entity.toLowerCase())
            )
          ).map(cluster => ({
            center_entity: cluster.entity_name,
            center_type: cluster.entity_type,
            connection_count: cluster.connection_count,
            avg_strength: cluster.avg_strength,
            members: cluster.cluster_members
          }))
        } : undefined,
        path_analysis: pathAnalysis,
        insights: this.generateInsights(centerEntityHistory.entity, connectedEntities),
        query_info: {
          center_entity: args.center_entity,
          max_degrees: args.max_degrees,
          min_strength: args.min_strength,
          filters_applied: {
            entity_types: args.entity_types,
            relationship_types: args.relationship_types
          },
          generated_at: new Date().toISOString()
        }
      };

      return {
        content: [{
          type: 'text',
          text: JSON.stringify(response, null, 2)
        }],
        isError: false
      };

    } catch (error) {
      console.error('Error in GetKnowledgeGraphTool:', error);
      return {
        content: [{
          type: 'text',
          text: JSON.stringify({
            success: false,
            error: 'Failed to retrieve knowledge graph',
            details: error instanceof Error ? error.message : 'Unknown error',
            center_entity: args.center_entity
          }, null, 2)
        }],
        isError: true
      };
    }
  }

  /**
   * Summarize relationship types and strengths
   */
  private summarizeRelationships(entities: any[]): {
    total_relationships: number;
    relationship_types: Record<string, number>;
    strength_distribution: {
      strong: number; // > 0.7
      medium: number; // 0.4 - 0.7
      weak: number; // < 0.4
    };
    average_strength: number;
  } {
    const relationshipTypes: Record<string, number> = {};
    let strongCount = 0;
    let mediumCount = 0;
    let weakCount = 0;
    let totalStrength = 0;

    for (const entity of entities) {
      relationshipTypes[entity.relationship_type] = (relationshipTypes[entity.relationship_type] || 0) + 1;

      if (entity.strength > 0.7) strongCount++;
      else if (entity.strength >= 0.4) mediumCount++;
      else weakCount++;

      totalStrength += entity.strength;
    }

    return {
      total_relationships: entities.length,
      relationship_types: relationshipTypes,
      strength_distribution: {
        strong: strongCount,
        medium: mediumCount,
        weak: weakCount
      },
      average_strength: entities.length > 0 ? totalStrength / entities.length : 0
    };
  }

  /**
   * Analyze entity type distribution
   */
  private analyzeEntityTypes(entities: any[]): Record<string, number> {
    const distribution: Record<string, number> = {};

    for (const entity of entities) {
      distribution[entity.entity_type] = (distribution[entity.entity_type] || 0) + 1;
    }

    return distribution;
  }

  /**
   * Analyze entity paths for interesting patterns
   */
  private analyzeEntityPaths(entities: any[]): {
    shortest_paths: Array<{
      target_entity: string;
      path_length: number;
      path: string[];
      strength: number;
    }>;
    longest_paths: Array<{
      target_entity: string;
      path_length: number;
      path: string[];
      strength: number;
    }>;
    path_statistics: {
      average_path_length: number;
      max_path_length: number;
      min_path_length: number;
    };
  } {
    const pathData = entities.map(entity => ({
      target_entity: entity.entity_name,
      path_length: entity.path.length,
      path: entity.path,
      strength: entity.strength
    }));

    const sortedByLength = [...pathData].sort((a, b) => a.path_length - b.path_length);

    return {
      shortest_paths: sortedByLength.slice(0, 5),
      longest_paths: sortedByLength.slice(-5).reverse(),
      path_statistics: {
        average_path_length: pathData.reduce((sum, p) => sum + p.path_length, 0) / pathData.length,
        max_path_length: Math.max(...pathData.map(p => p.path_length)),
        min_path_length: Math.min(...pathData.map(p => p.path_length))
      }
    };
  }

  /**
   * Generate insights about the knowledge graph structure
   */
  private generateInsights(centerEntity: any, connectedEntities: any[]): string[] {
    const insights: string[] = [];

    // Connection density insight
    if (connectedEntities.length > 20) {
      insights.push(`${centerEntity.name} is highly connected with ${connectedEntities.length} related entities, suggesting it's a central concept in your conversations.`);
    } else if (connectedEntities.length > 5) {
      insights.push(`${centerEntity.name} has moderate connectivity with ${connectedEntities.length} related entities.`);
    } else if (connectedEntities.length > 0) {
      insights.push(`${centerEntity.name} has limited connections with only ${connectedEntities.length} related entities.`);
    } else {
      insights.push(`${centerEntity.name} appears to be isolated with no strong relationships to other entities.`);
    }

    // Relationship strength insight
    const strongRelationships = connectedEntities.filter(e => e.strength > 0.7).length;
    const weakRelationships = connectedEntities.filter(e => e.strength < 0.4).length;

    if (strongRelationships > weakRelationships) {
      insights.push('Most relationships are strong, indicating clear and frequent associations.');
    } else if (weakRelationships > strongRelationships) {
      insights.push('Many relationships are weak, suggesting indirect or infrequent associations.');
    }

    // Entity type diversity
    const entityTypes = new Set(connectedEntities.map(e => e.entity_type));
    if (entityTypes.size > 4) {
      insights.push(`${centerEntity.name} spans multiple domains with connections across ${entityTypes.size} different entity types.`);
    }

    // Relationship type patterns
    const relationshipTypes = connectedEntities.reduce((acc, e) => {
      acc[e.relationship_type] = (acc[e.relationship_type] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const mostCommonRelType = Object.entries(relationshipTypes)
      .sort(([,a], [,b]) => (b as number) - (a as number))[0];

    if (mostCommonRelType) {
      insights.push(`Most common relationship type is '${mostCommonRelType[0]}' (${mostCommonRelType[1]} occurrences).`);
    }

    return insights;
  }

  /**
   * Get tool information for MCP registration
   */
  getToolInfo() {
    return {
      name: this.name,
      description: this.description,
      inputSchema: {
        type: 'object',
        properties: {
          center_entity: {
            type: 'string',
            description: 'Name of the entity to center the knowledge graph around',
            minLength: 1,
            maxLength: 200
          },
          max_degrees: {
            type: 'integer',
            description: 'Maximum degrees of separation to explore (1-4)',
            minimum: 1,
            maximum: 4,
            default: 2
          },
          min_strength: {
            type: 'number',
            description: 'Minimum relationship strength threshold (0.0 to 1.0)',
            minimum: 0.0,
            maximum: 1.0,
            default: 0.3
          },
          entity_types: {
            type: 'array',
            items: {
              type: 'string',
              enum: [
                'person', 'organization', 'product', 'concept',
                'location', 'technical', 'event', 'decision'
              ]
            },
            description: 'Filter connected entities by specific types'
          },
          relationship_types: {
            type: 'array',
            items: {
              type: 'string',
              enum: [
                'works_for', 'created_by', 'discussed_with', 'related_to',
                'part_of', 'mentioned_with', 'temporal_sequence', 'cause_effect'
              ]
            },
            description: 'Filter relationships by specific types'
          },
          include_clusters: {
            type: 'boolean',
            description: 'Whether to include entity cluster analysis',
            default: false
          },
          include_paths: {
            type: 'boolean',
            description: 'Whether to include shortest paths to connected entities',
            default: true
          },
          max_entities: {
            type: 'integer',
            description: 'Maximum number of connected entities to return',
            minimum: 1,
            maximum: 200,
            default: 50
          }
        },
        required: ['center_entity']
      }
    };
  }
}

/**
 * Hybrid Search Tool - MCP tool for combined semantic and FTS search
 * 
 * This tool provides intelligent search that combines semantic similarity
 * with traditional full-text search for optimal results. It automatically
 * balances between exact keyword matching and conceptual understanding.
 */

import { z } from 'zod';
import { BaseTool, ToolContext } from './BaseTool.js';
import { ToolError, extractErrorInfo } from '../utils/errors.js';
import { EnhancedSearchEngine } from '../search/EnhancedSearchEngine.js';
import { QueryParser } from '../search/QueryParser.js';
import { SearchUtils } from '../search/index.js';
import { HybridSearchTool as HybridSearchToolDef } from '../types/mcp.js';

const hybridSearchSchema = z.object({
  query: z.string()
    .min(1)
    .max(1000)
    .describe('Search query for hybrid matching'),
  limit: z.number()
    .min(1)
    .max(100)
    .default(20)
    .describe('Maximum results to return'),
  offset: z.number()
    .min(0)
    .default(0)
    .describe('Number of results to skip for pagination'),
  conversationId: z.string()
    .optional()
    .describe('Limit search to specific conversation'),
  startDate: z.string()
    .datetime()
    .optional()
    .describe('Filter by start date (ISO 8601 format)'),
  endDate: z.string()
    .datetime()
    .optional()
    .describe('Filter by end date (ISO 8601 format)'),
  strategy: z.enum(['auto', 'semantic', 'fts', 'hybrid'])
    .default('auto')
    .describe('Search strategy: auto-select, semantic-only, FTS-only, or hybrid'),
  weights: z.object({
    semantic: z.number().min(0).max(1).default(0.6),
    fts: z.number().min(0).max(1).default(0.4)
  }).refine(w => Math.abs(w.semantic + w.fts - 1) < 0.01, {
    message: 'Weights must sum to 1.0'
  }).optional()
    .describe('Relative weights for semantic vs FTS scores in hybrid mode'),
  semanticThreshold: z.number()
    .min(0)
    .max(1)
    .default(0.6)
    .describe('Minimum semantic similarity threshold'),
  matchType: z.enum(['fuzzy', 'exact', 'prefix'])
    .default('fuzzy')
    .describe('FTS matching type: fuzzy, exact phrases, or prefix matching'),
  explainResults: z.boolean()
    .default(false)
    .describe('Include detailed explanations for result ranking'),
  includeMetrics: z.boolean()
    .default(false)
    .describe('Include detailed performance metrics in response')
});

type HybridSearchParams = z.infer<typeof hybridSearchSchema>;

export class HybridSearchTool extends BaseTool<HybridSearchParams> {
  private enhancedSearchEngine: EnhancedSearchEngine;

  constructor(enhancedSearchEngine: EnhancedSearchEngine) {
    super(HybridSearchToolDef, hybridSearchSchema);
    this.enhancedSearchEngine = enhancedSearchEngine;
  }

  protected async executeImpl(params: HybridSearchParams, context: ToolContext) {
    try {
      // Sanitize query input to prevent injection attacks
      const sanitizedQuery = this.sanitizeQuery(params.query);

      // Validate and convert date parameters
      const { startDate, endDate } = this.validateAndConvertDates(params);

      // Validate weights if provided (Zod refine already checks this, but handle edge cases)
      if (params.weights) {
        const total = params.weights.semantic + params.weights.fts;
        if (Math.abs(total - 1) > 0.01) {
          throw new ToolError('hybrid_search', 'Search weights must sum to 1.0');
        }
      }

      // Prepare search options with sanitized query
      const searchOptions: any = {
        query: sanitizedQuery,
        strategy: params.strategy,
        limit: params.limit,
        offset: params.offset,
        conversationId: params.conversationId,
        startDate: startDate,
        endDate: endDate,
        semanticThreshold: params.semanticThreshold,
        matchType: params.matchType,
        explainResults: params.explainResults
      };

      // Only include weights if provided
      if (params.weights) {
        searchOptions.weights = params.weights;
      }

      // Perform hybrid search
      const searchResult = await this.enhancedSearchEngine.search(searchOptions);

      // Format response data (without success wrapper - BaseTool handles that)
      const response = {
        results: searchResult.results.map(result => ({
          messageId: result.messageId,
          conversationId: result.conversationId,
          content: result.content,
          score: result.score,
          matchType: result.matchType,
          scores: {
            combined: result.scores.combined,
            ...(result.scores.semantic !== undefined && { semantic: result.scores.semantic }),
            ...(result.scores.fts !== undefined && { fts: result.scores.fts })
          },
          highlights: result.highlights,
          conversationTitle: result.conversationTitle,
          createdAt: result.createdAt,
          preview: this.createPreview(result.content, result.highlights),
          ...(params.explainResults && result.explanation && { explanation: result.explanation })
        })),
        totalCount: searchResult.results.length,
        hasMore: searchResult.hasMore,
        searchStrategy: searchResult.metrics?.strategy,
        queryAnalysis: searchResult.metrics?.queryAnalysis ? {
          complexity: searchResult.metrics.queryAnalysis.complexity,
          termCount: searchResult.metrics.queryAnalysis.termCount,
          hasOperators: searchResult.metrics.queryAnalysis.hasOperators,
          suggestedStrategy: searchResult.metrics.queryAnalysis.suggestedStrategy
        } : undefined,
        metadata: {
          queryId: searchResult.metrics?.queryId,
          executionTime: searchResult.metrics?.totalTime,
          actualStrategy: searchResult.metrics?.strategy,
          weights: params.weights || { semantic: 0.6, fts: 0.4 },
          ...(params.includeMetrics && searchResult.metrics && {
            detailedTiming: searchResult.metrics.timing,
            queryAnalysis: searchResult.metrics.queryAnalysis
          })
        },
        pagination: {
          offset: params.offset,
          limit: params.limit,
          hasMore: searchResult.hasMore
        }
      };

      return response;

    } catch (error) {
      if (error instanceof ToolError) {
        throw error;
      }

      // Log detailed error information internally
      const errorInfo = extractErrorInfo(error);
      console.error('Hybrid search error:', {
        ...errorInfo,
        requestId: context.requestId,
        query: SearchUtils.sanitizeForLogging(params.query),
        strategy: params.strategy
      });

      // Return generic error message to user
      throw new ToolError(
        'hybrid_search',
        'Search operation failed. Please try again with a different query.'
      );
    }
  }

  /**
   * Sanitize user query input to prevent injection attacks
   */
  private sanitizeQuery(query: string): string {
    if (!query || typeof query !== 'string') {
      throw new ToolError('hybrid_search', 'Query must be a non-empty string');
    }

    // Use QueryParser for consistent sanitization based on match type
    try {
      const parsed = QueryParser.parseQuery(query, 'fuzzy'); // Default to fuzzy for sanitization
      return parsed.query; // Use sanitized query for security
    } catch (error) {
      // If parsing fails, apply basic sanitization
      return query
        .trim()
        .replace(/[\u0000-\u001F\u007F]/g, '') // Remove control characters
        .substring(0, 1000); // Enforce max length
    }
  }

  /**
   * Validate and convert date parameters
   */
  private validateAndConvertDates(params: HybridSearchParams): {
    startDate?: string;
    endDate?: string;
  } {
    // Zod already validates datetime format, but handle edge cases
    let startDate: string | undefined;
    let endDate: string | undefined;

    if (params.startDate) {
      const start = new Date(params.startDate);
      if (start.getFullYear() < 1900 || start.getFullYear() > 2100) {
        throw new ToolError('hybrid_search', 'Start date must be between 1900 and 2100');
      }
      startDate = start.toISOString();
    }

    if (params.endDate) {
      const end = new Date(params.endDate);
      if (end.getFullYear() < 1900 || end.getFullYear() > 2100) {
        throw new ToolError('hybrid_search', 'End date must be between 1900 and 2100');
      }
      endDate = end.toISOString();
    }

    // Check date range
    if (startDate && endDate && new Date(startDate) >= new Date(endDate)) {
      throw new ToolError('hybrid_search', 'Start date must be before end date');
    }

    return { startDate, endDate };
  }

  /**
   * Create a standardized preview snippet with intelligent highlighting
   */
  private createPreview(content: string, highlights: string[]): string {
    const maxLength = 300;

    if (highlights && highlights.length > 0) {
      // Find the best highlight - prefer longer highlights up to maxLength
      const validHighlights = highlights.filter(h => h && h.length > 10);

      if (validHighlights.length > 0) {
        // Sort by relevance: prefer highlights that are substantial but not too long
        const bestHighlight = validHighlights
          .sort((a, b) => {
            const aScore = a.length <= maxLength ? a.length : maxLength - (a.length - maxLength);
            const bScore = b.length <= maxLength ? b.length : maxLength - (b.length - maxLength);
            return bScore - aScore;
          })[0];

        if (bestHighlight.length <= maxLength) {
          return bestHighlight;
        }

        return bestHighlight.substring(0, maxLength - 3) + '...';
      }
    }

    // Fallback to content beginning
    if (content.length <= maxLength) {
      return content;
    }

    return content.substring(0, maxLength - 3) + '...';
  }

  /**
   * Validate search engine availability
   */
  async validate(): Promise<{ isValid: boolean; error?: string }> {
    try {
      // Test basic functionality
      await this.enhancedSearchEngine.search({
        query: 'test',
        strategy: 'auto',
        limit: 1
      });

      // Check if both semantic and FTS capabilities are available
      this.enhancedSearchEngine.getConfiguration();

      return { isValid: true };
    } catch (error) {
      // Log detailed error internally
      const errorInfo = extractErrorInfo(error);
      console.error('Hybrid search validation error:', errorInfo);

      return {
        isValid: false,
        error: 'Hybrid search service is currently unavailable'
      };
    }
  }

  /**
   * Get comprehensive usage examples
   */
  getExamples(): Array<{ description: string; params: HybridSearchParams }> {
    return [
      {
        description: 'Auto-strategy search for optimal results',
        params: {
          query: 'machine learning algorithms',
          limit: 15,
          offset: 0,
          matchType: 'fuzzy',
          explainResults: false,
          strategy: 'auto',
          semanticThreshold: 0.6,
          includeMetrics: false
        }
      },
      {
        description: 'Hybrid search with custom weights favoring semantic similarity',
        params: {
          query: 'project planning and management',
          limit: 20,
          offset: 0,
          matchType: 'fuzzy',
          explainResults: true,
          strategy: 'hybrid',
          semanticThreshold: 0.6,
          includeMetrics: false,
          weights: { semantic: 0.8, fts: 0.2 }
        }
      },
      {
        description: 'Search with exact phrase matching and high semantic threshold',
        params: {
          query: '"data science" methodology',
          limit: 10,
          offset: 0,
          matchType: 'exact',
          explainResults: false,
          strategy: 'hybrid',
          semanticThreshold: 0.8,
          includeMetrics: false
        }
      },
      {
        description: 'Recent discussions with detailed performance metrics',
        params: {
          query: 'code review best practices',
          limit: 25,
          offset: 0,
          matchType: 'fuzzy',
          explainResults: true,
          strategy: 'auto',
          semanticThreshold: 0.6,
          includeMetrics: true,
          startDate: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString() // Last 30 days
        }
      },
      {
        description: 'Conversation-specific search with prefix matching',
        params: {
          query: 'deploy',
          limit: 10,
          offset: 0,
          matchType: 'prefix',
          explainResults: false,
          strategy: 'hybrid',
          semanticThreshold: 0.6,
          includeMetrics: false,
          conversationId: 'conv-456'
        }
      },
      {
        description: 'FTS-only search for exact keyword matching',
        params: {
          query: 'API endpoint configuration',
          limit: 15,
          offset: 0,
          matchType: 'fuzzy',
          explainResults: false,
          strategy: 'fts',
          semanticThreshold: 0.6,
          includeMetrics: false
        }
      }
    ];
  }

  /**
   * Get performance and capability information
   */
  getMetadata() {
    return {
      name: this.getName(),
      description: this.getDescription(),
      category: 'search',
      capabilities: [
        'Semantic similarity search',
        'Full-text search (FTS5)',
        'Intelligent strategy selection',
        'Customizable result weighting',
        'Multi-modal result ranking',
        'Performance metrics tracking'
      ],
      averageExecutionTime: '300-800ms',
      memoryUsage: 'Medium-High (dual search engines)',
      accuracy: 'Very High (combines multiple search methods)',
      supportedSearchTypes: [
        'Conceptual similarity',
        'Exact keyword matching',
        'Fuzzy text matching',
        'Prefix matching',
        'Phrase matching'
      ],
      limitations: [
        'Requires both FTS index and embeddings for optimal results',
        'Higher resource usage than single-method searches',
        'Complex queries may take longer to process',
        'Performance depends on database size and embedding coverage'
      ],
      bestUseCases: [
        'General purpose search with unknown query intent',
        'Finding related content across different phrasings',
        'Balancing precision and recall in search results',
        'Discovering connections between concepts',
        'Search result explanation and debugging'
      ]
    };
  }

  /**
   * Get search strategy recommendations based on query characteristics
   */
  async getStrategyRecommendation(query: string): Promise<{
    recommended: 'semantic' | 'fts' | 'hybrid';
    reasoning: string;
    confidence: number;
  }> {
    // Analyze query characteristics
    const terms = query.split(/\s+/).filter(term => term.length > 0);
    const hasOperators = /["()\-+*]/.test(query);
    const hasQuotes = query.includes('"');
    const isShort = terms.length <= 2;
    const isLong = terms.length > 5;

    let recommended: 'semantic' | 'fts' | 'hybrid';
    let reasoning: string;
    let confidence: number;

    if (hasQuotes || hasOperators) {
      recommended = 'fts';
      reasoning = 'Query contains operators or quoted phrases, FTS will provide more precise matching';
      confidence = 0.9;
    } else if (isShort && !hasOperators) {
      recommended = 'semantic';
      reasoning = 'Short conceptual query benefits from semantic similarity';
      confidence = 0.8;
    } else if (isLong || hasOperators) {
      recommended = 'fts';
      reasoning = 'Complex query with multiple terms works better with FTS';
      confidence = 0.7;
    } else {
      recommended = 'hybrid';
      reasoning = 'Balanced query benefits from combining both search methods';
      confidence = 0.8;
    }

    return { recommended, reasoning, confidence };
  }

  /**
   * Legacy handle method for backward compatibility with tests
   * Delegates to the execute method from BaseTool
   */
  async handle(input: unknown, context: any = {}) {
    const toolContext = context.requestId ? context : {
      requestId: context.requestId || 'test-' + Date.now(),
      timestamp: Date.now(),
      ...context
    };
    return this.execute(input, toolContext);
  }
}

/**
 * Manage Index Optimization Tool
 * 
 * Allows users to review, approve, and execute index optimization recommendations.
 * Provides safe controls for managing database index performance improvements.
 */

import { z } from 'zod';
import { BaseTool } from './BaseTool.js';
import { DatabaseManager } from '../storage/Database.js';
import { ProductionPerformanceManager } from '../analytics/performance/ProductionPerformanceManager.js';
import { IndexMonitoringDashboard } from '../analytics/performance/IndexMonitoringDashboard.js';
import { IndexUsageMonitor, IndexOptimizationRecommendation } from '../analytics/performance/IndexUsageMonitor.js';
import { AnalyticsEngine } from '../analytics/services/AnalyticsEngine.js';

const ManageIndexOptimizationSchema = z.object({
  action: z.enum(['list', 'analyze', 'preview', 'execute', 'status', 'rollback']),
  optimizationType: z.enum(['create', 'drop', 'rebuild', 'modify', 'all']).optional(),
  indexName: z.string().optional(),
  tableName: z.string().optional(),
  autoApprove: z.boolean().default(false),
  riskTolerance: z.enum(['conservative', 'moderate', 'aggressive']).default('conservative'),
  dryRun: z.boolean().default(true),
  maxOptimizations: z.number().min(1).max(10).default(5),
  forceExecution: z.boolean().default(false)
});

interface OptimizationResult {
  optimizationId: string;
  indexName: string;
  tableName: string;
  action: string;
  status: 'planned' | 'executing' | 'completed' | 'failed' | 'rolled_back';
  executionTime?: number;
  beforeMetrics?: any;
  afterMetrics?: any;
  actualBenefit?: number;
  error?: string;
}

export class ManageIndexOptimizationTool extends BaseTool {
  private performanceManager: ProductionPerformanceManager;
  private dashboard: IndexMonitoringDashboard;
  private indexMonitor: IndexUsageMonitor;

  constructor(databaseManager: DatabaseManager) {
    const toolDef = {
      name: 'manage_index_optimization',
      description: 'Manage index optimization recommendations - analyze, preview, and execute database index improvements',
      inputSchema: {
        type: 'object' as const,
        properties: {
          action: { type: 'string', enum: ['list', 'analyze', 'preview', 'execute', 'status', 'rollback'] },
          optimizationType: { type: 'string', enum: ['create', 'drop', 'rebuild', 'modify', 'all'] },
          indexName: { type: 'string' },
          tableName: { type: 'string' },
          autoApprove: { type: 'boolean', default: false },
          riskTolerance: { type: 'string', enum: ['conservative', 'moderate', 'aggressive'], default: 'conservative' },
          dryRun: { type: 'boolean', default: true },
          maxOptimizations: { type: 'number', minimum: 1, maximum: 10, default: 5 },
          forceExecution: { type: 'boolean', default: false }
        },
        required: ['action'],
        additionalProperties: false
      }
    };
    super(toolDef, ManageIndexOptimizationSchema);

    // Initialize performance management components
    const analyticsEngine = new AnalyticsEngine(databaseManager);
    this.performanceManager = new ProductionPerformanceManager(
      databaseManager,
      analyticsEngine,
      {
        monitoring: { 
          enabled: true, 
          intervalMinutes: 15,
          alertThresholds: {
            slowQueryMs: 1000,
            unusedIndexDays: 30,
            writeImpactThreshold: 0.1,
            memoryUsageThresholdMB: 100
          },
          retentionDays: 30
        },
        optimization: { 
          autoOptimizeEnabled: false,
          autoDropUnusedIndexes: false,
          maxConcurrentOptimizations: 1,
          maintenanceWindowHours: [2, 3, 4], // 2-4 AM
          riskTolerance: 'conservative'
        },
        alerts: { 
          emailNotifications: false,
          escalationThresholds: {
            criticalAlertCount: 5,
            highAlertDurationMinutes: 60
          }
        }
      }
    );
    this.dashboard = new IndexMonitoringDashboard(databaseManager);
    this.indexMonitor = new IndexUsageMonitor(databaseManager);
  }

  async executeImpl(args: z.infer<typeof ManageIndexOptimizationSchema>) {
    try {
      console.log(`Executing index optimization action: ${args.action}`);

      // Initialize monitoring if not already running
      try {
        await this.performanceManager.initialize();
      } catch (error) {
        const errorMessage = error instanceof Error ? error.message : String(error);
        if (!errorMessage.includes('already running')) {
          console.warn('Performance manager initialization warning:', errorMessage);
        }
      }

      switch (args.action) {
        case 'list':
          return await this.listOptimizations(args);
        case 'analyze':
          return await this.analyzeOptimizations(args);
        case 'preview':
          return await this.previewOptimization(args);
        case 'execute':
          return await this.executeOptimizations(args);
        case 'status':
          return await this.getOptimizationStatus(args);
        case 'rollback':
          return await this.rollbackOptimization(args);
        default:
          throw new Error(`Unsupported action: ${args.action}`);
      }

    } catch (error) {
      console.error('Error in index optimization management:', error);
      return {
        success: false,
        error: `Index optimization failed: ${error instanceof Error ? error.message : String(error)}`,
        action: args.action
      };
    }
  }

  private async listOptimizations(args: any) {
    const recommendations = await this.dashboard.getOptimizationRecommendations();

    // Filter by type if specified
    let filteredRecommendations = recommendations;
    if (args.optimizationType && args.optimizationType !== 'all') {
      filteredRecommendations = recommendations.filter(r => r.type === args.optimizationType);
    }

    // Apply risk tolerance filter
    filteredRecommendations = this.applyRiskFilter(filteredRecommendations, args.riskTolerance);

    // Limit results
    const limitedRecommendations = filteredRecommendations.slice(0, args.maxOptimizations);

    return {
      success: true,
      action: 'list',
      totalRecommendations: recommendations.length,
      filteredCount: filteredRecommendations.length,
      displayedCount: limitedRecommendations.length,
      riskTolerance: args.riskTolerance,
      optimizations: limitedRecommendations.map(rec => ({
        id: this.generateOptimizationId(rec),
        type: rec.type,
        indexName: rec.indexName,
        tableName: rec.tableName,
        reason: rec.reason,
        expectedImpact: rec.expectedImpact,
        riskLevel: rec.riskLevel,
        estimatedBenefit: `${Math.round(rec.estimatedBenefit)}ms`,
        costBenefitScore: Math.round(rec.costBenefitScore),
        implementationComplexity: rec.implementationComplexity,
        priority: rec.implementationPriority,
        riskAssessment: rec.riskAssessment,
        canAutoApprove: this.canAutoApprove(rec, args.riskTolerance),
        sql: args.dryRun ? rec.sql : '*** SQL hidden - set dryRun:false to view ***'
      })),
      filters: {
        optimizationType: args.optimizationType || 'all',
        riskTolerance: args.riskTolerance,
        maxOptimizations: args.maxOptimizations
      },
      summary: this.generateOptimizationSummary(limitedRecommendations)
    };
  }

  private async analyzeOptimizations(args: any) {
    const recommendations = await this.dashboard.getOptimizationRecommendations();
    const healthReports = await this.dashboard.getIndexHealthReport();
    const currentMetrics = await this.dashboard.getCurrentMetrics();

    // Analyze impact by table
    const tableAnalysis = this.analyzeByTable(recommendations, healthReports);

    // Risk analysis
    const riskAnalysis = this.analyzeRisks(recommendations);

    // Performance impact analysis
    const impactAnalysis = this.analyzePerformanceImpact(recommendations, currentMetrics);

    return {
      success: true,
      action: 'analyze',
      analysis: {
        overview: {
          totalRecommendations: recommendations.length,
          highImpactOptimizations: recommendations.filter(r => r.expectedImpact === 'high').length,
          lowRiskOptimizations: recommendations.filter(r => r.riskLevel === 'low').length,
          potentialBenefit: Math.round(recommendations.reduce((sum, r) => sum + r.estimatedBenefit, 0))
        },
        tableAnalysis,
        riskAnalysis,
        impactAnalysis,
        recommendations: {
          immediate: this.getImmediateRecommendations(recommendations, args.riskTolerance),
          shortTerm: this.getShortTermRecommendations(recommendations),
          longTerm: this.getLongTermRecommendations(recommendations)
        }
      }
    };
  }

  private async previewOptimization(args: any) {
    if (!args.indexName) {
      return {
        success: false,
        error: 'indexName is required for preview action'
      };
    }

    const recommendations = await this.dashboard.getOptimizationRecommendations();
    const recommendation = recommendations.find(r => r.indexName === args.indexName);

    if (!recommendation) {
      return {
        success: false,
        error: `No optimization recommendation found for index: ${args.indexName}`
      };
    }

    // Get current index statistics
    const currentStats = await this.getCurrentIndexStats(args.indexName);

    // Simulate the optimization impact
    const impactSimulation = this.simulateOptimizationImpact(recommendation, currentStats);

    return {
      success: true,
      action: 'preview',
      optimization: {
        id: this.generateOptimizationId(recommendation),
        indexName: recommendation.indexName,
        tableName: recommendation.tableName,
        type: recommendation.type,
        reason: recommendation.reason,
        sql: recommendation.sql,
        riskLevel: recommendation.riskLevel,
        riskAssessment: recommendation.riskAssessment,
        implementationComplexity: recommendation.implementationComplexity
      },
      currentState: currentStats,
      expectedOutcome: impactSimulation,
      preExecutionChecks: this.generatePreExecutionChecks(recommendation),
      warnings: this.generateWarnings(recommendation, currentStats),
      rollbackPlan: this.generateRollbackPlan(recommendation),
      executionSafe: this.isExecutionSafe(recommendation, args.riskTolerance)
    };
  }

  private async executeOptimizations(args: any) {
    if (args.dryRun && !args.forceExecution) {
      return {
        success: false,
        error: 'Dry run mode active. Set dryRun:false or forceExecution:true to execute optimizations',
        hint: 'Use preview action to analyze specific optimizations before execution'
      };
    }

    const recommendations = await this.dashboard.getOptimizationRecommendations();
    let targetRecommendations: IndexOptimizationRecommendation[] = [];

    if (args.indexName) {
      // Execute specific optimization
      const specific = recommendations.find(r => r.indexName === args.indexName);
      if (!specific) {
        return {
          success: false,
          error: `No optimization found for index: ${args.indexName}`
        };
      }
      targetRecommendations = [specific];
    } else {
      // Execute multiple optimizations based on criteria
      targetRecommendations = this.selectOptimizationsForExecution(
        recommendations, 
        args.riskTolerance, 
        args.maxOptimizations,
        args.autoApprove
      );
    }

    if (targetRecommendations.length === 0) {
      return {
        success: true,
        action: 'execute',
        message: 'No optimizations selected for execution based on current criteria',
        criteria: {
          riskTolerance: args.riskTolerance,
          autoApprove: args.autoApprove,
          maxOptimizations: args.maxOptimizations
        }
      };
    }

    // Execute optimizations
    const results: OptimizationResult[] = [];
    const executionStart = Date.now();

    for (const recommendation of targetRecommendations) {
      const result = await this.executeIndividualOptimization(recommendation, args.forceExecution);
      results.push(result);

      // Stop on failure if not forcing execution
      if (!result.status.includes('completed') && !args.forceExecution) {
        break;
      }
    }

    const executionTime = Date.now() - executionStart;
    const successful = results.filter(r => r.status === 'completed').length;
    const failed = results.filter(r => r.status === 'failed').length;

    return {
      success: successful > 0,
      action: 'execute',
      executionSummary: {
        totalOptimizations: results.length,
        successful,
        failed,
        totalExecutionTime: executionTime,
        averageExecutionTime: Math.round(executionTime / results.length),
        overallBenefit: results.reduce((sum, r) => sum + (r.actualBenefit || 0), 0)
      },
      results,
      recommendations: this.generatePostExecutionRecommendations(results)
    };
  }

  private async getOptimizationStatus(args: any) {
    // Get recent optimization history from the database
    const db = this.dashboard['databaseManager'].getConnection();

    const recentOptimizations = db.prepare(`
      SELECT 
        index_name,
        table_name,
        action_type,
        status,
        execution_time,
        actual_impact,
        created_at,
        error_message
      FROM index_optimization_log
      WHERE created_at > ?
      ORDER BY created_at DESC
      LIMIT 20
    `).all(Date.now() - (7 * 24 * 60 * 60 * 1000)); // Last 7 days

    const currentRecommendations = await this.dashboard.getOptimizationRecommendations();
    const systemStatus = await this.performanceManager.getPerformanceStatus();

    return {
      success: true,
      action: 'status',
      systemStatus: {
        overall: systemStatus.overall,
        activeOptimizations: systemStatus.activeOptimizations,
        lastUpdate: new Date(systemStatus.lastUpdate).toISOString()
      },
      recentActivity: recentOptimizations.map((opt: any) => ({
        indexName: opt.index_name,
        tableName: opt.table_name,
        action: opt.action_type,
        status: opt.status,
        executionTime: opt.execution_time ? `${Math.round(opt.execution_time)}ms` : null,
        benefit: opt.actual_impact ? `${Math.round(opt.actual_impact)}ms` : null,
        timestamp: new Date(opt.created_at).toISOString(),
        error: opt.error_message
      })),
      pendingRecommendations: {
        total: currentRecommendations.length,
        highPriority: currentRecommendations.filter(r => r.implementationPriority === 1).length,
        lowRisk: currentRecommendations.filter(r => r.riskLevel === 'low').length,
        autoApprovable: currentRecommendations.filter(r => 
          this.canAutoApprove(r, args.riskTolerance || 'conservative')
        ).length
      }
    };
  }

  private async rollbackOptimization(args: any) {
    if (!args.indexName) {
      return {
        success: false,
        error: 'indexName is required for rollback action'
      };
    }

    // This would implement rollback logic - simplified for demo
    return {
      success: false,
      error: 'Rollback functionality not yet implemented - would require detailed audit trail and reverse operations',
      indexName: args.indexName,
      note: 'Manual rollback may be required for complex optimizations'
    };
  }

  // Helper methods

  private applyRiskFilter(recommendations: IndexOptimizationRecommendation[], riskTolerance: string): IndexOptimizationRecommendation[] {
    switch (riskTolerance) {
      case 'conservative':
        return recommendations.filter(r => r.riskLevel === 'low');
      case 'moderate':
        return recommendations.filter(r => r.riskLevel === 'low' || r.riskLevel === 'medium');
      case 'aggressive':
        return recommendations; // Include all risk levels
      default:
        return recommendations.filter(r => r.riskLevel === 'low');
    }
  }

  private canAutoApprove(recommendation: IndexOptimizationRecommendation, riskTolerance: string): boolean {
    if (recommendation.riskLevel === 'high') return false;
    if (recommendation.implementationComplexity === 'high') return false;

    switch (riskTolerance) {
      case 'conservative':
        return recommendation.riskLevel === 'low' && recommendation.implementationComplexity === 'low';
      case 'moderate':
        return recommendation.costBenefitScore > 50;
      case 'aggressive':
        return recommendation.costBenefitScore > 30;
      default:
        return false;
    }
  }

  private generateOptimizationId(recommendation: IndexOptimizationRecommendation): string {
    return `opt_${recommendation.type}_${recommendation.indexName}_${Date.now().toString(36)}`;
  }

  private generateOptimizationSummary(recommendations: IndexOptimizationRecommendation[]) {
    const summary = {
      totalBenefit: Math.round(recommendations.reduce((sum, r) => sum + r.estimatedBenefit, 0)),
      averageCostBenefit: Math.round(recommendations.reduce((sum, r) => sum + r.costBenefitScore, 0) / recommendations.length),
      riskDistribution: {
        low: recommendations.filter(r => r.riskLevel === 'low').length,
        medium: recommendations.filter(r => r.riskLevel === 'medium').length,
        high: recommendations.filter(r => r.riskLevel === 'high').length
      },
      typeDistribution: recommendations.reduce((acc: any, r) => {
        acc[r.type] = (acc[r.type] || 0) + 1;
        return acc;
      }, {}),
      complexityDistribution: {
        low: recommendations.filter(r => r.implementationComplexity === 'low').length,
        medium: recommendations.filter(r => r.implementationComplexity === 'medium').length,
        high: recommendations.filter(r => r.implementationComplexity === 'high').length
      }
    };

    return summary;
  }

  private analyzeByTable(recommendations: IndexOptimizationRecommendation[], healthReports: any[]): any {
    const tableAnalysis = {};

    for (const rec of recommendations) {
      if (!tableAnalysis[rec.tableName]) {
        tableAnalysis[rec.tableName] = {
          optimizations: 0,
          totalBenefit: 0,
          riskLevels: { low: 0, medium: 0, high: 0 },
          types: {},
          averageHealth: 0
        };
      }

      const analysis = tableAnalysis[rec.tableName];
      analysis.optimizations++;
      analysis.totalBenefit += rec.estimatedBenefit;
      analysis.riskLevels[rec.riskLevel]++;
      analysis.types[rec.type] = (analysis.types[rec.type] || 0) + 1;
    }

    // Add health information
    for (const health of healthReports) {
      if (tableAnalysis[health.tableName]) {
        tableAnalysis[health.tableName].averageHealth = health.healthScore;
      }
    }

    return tableAnalysis;
  }

  private analyzeRisks(recommendations: IndexOptimizationRecommendation[]): any {
    return {
      overallRiskScore: this.calculateOverallRiskScore(recommendations),
      highestRiskOptimizations: recommendations
        .filter(r => r.riskLevel === 'high')
        .slice(0, 3)
        .map(r => ({ index: r.indexName, reason: r.riskAssessment })),
      riskMitigations: this.generateRiskMitigations(recommendations)
    };
  }

  private analyzePerformanceImpact(recommendations: IndexOptimizationRecommendation[], currentMetrics: any): any {
    const totalBenefit = recommendations.reduce((sum, r) => sum + r.estimatedBenefit, 0);
    const currentAvgTime = currentMetrics.overview.averageQueryTime;

    return {
      estimatedImprovementPercent: Math.round((totalBenefit / currentAvgTime) * 100),
      projectedAverageQueryTime: Math.max(currentAvgTime - totalBenefit, 50), // Minimum 50ms
      highImpactOptimizations: recommendations
        .filter(r => r.expectedImpact === 'high')
        .sort((a, b) => b.estimatedBenefit - a.estimatedBenefit)
        .slice(0, 3)
    };
  }

  private getImmediateRecommendations(recommendations: IndexOptimizationRecommendation[], riskTolerance: string): any[] {
    return recommendations
      .filter(r => r.implementationPriority === 1 && this.canAutoApprove(r, riskTolerance))
      .slice(0, 3)
      .map(r => ({
        index: r.indexName,
        action: r.type,
        reason: r.reason,
        benefit: `${Math.round(r.estimatedBenefit)}ms`
      }));
  }

  private getShortTermRecommendations(recommendations: IndexOptimizationRecommendation[]): any[] {
    return recommendations
      .filter(r => r.implementationPriority === 2)
      .slice(0, 5)
      .map(r => ({
        index: r.indexName,
        action: r.type,
        reason: r.reason,
        complexity: r.implementationComplexity
      }));
  }

  private getLongTermRecommendations(recommendations: IndexOptimizationRecommendation[]): any[] {
    return recommendations
      .filter(r => r.implementationPriority >= 3)
      .slice(0, 5)
      .map(r => ({
        index: r.indexName,
        action: r.type,
        reason: r.reason,
        strategicValue: r.costBenefitScore > 70 ? 'high' : 'medium'
      }));
  }

  private async getCurrentIndexStats(indexName: string): Promise<any> {
    // Simplified - would get actual index statistics
    return {
      exists: true,
      size: '1.2MB',
      lastUsed: Date.now() - (2 * 60 * 60 * 1000), // 2 hours ago
      usageCount: 1234,
      effectiveness: 0.75
    };
  }

  private simulateOptimizationImpact(recommendation: IndexOptimizationRecommendation, currentStats: any): any {
    return {
      estimatedNewQueryTime: Math.max(100, 1000 - recommendation.estimatedBenefit),
      estimatedStorageChange: recommendation.type === 'drop' ? `-${currentStats.size}` : 
                            recommendation.type === 'create' ? '+500KB' : 'unchanged',
      maintenanceImpact: recommendation.type === 'rebuild' ? 'temporary table lock' : 'minimal',
      rollbackComplexity: recommendation.type === 'drop' ? 'difficult' : 'easy'
    };
  }

  private generatePreExecutionChecks(recommendation: IndexOptimizationRecommendation): string[] {
    const checks = [
      'Verify no critical queries depend solely on this index',
      'Confirm maintenance window availability',
      'Ensure sufficient disk space for operation'
    ];

    if (recommendation.type === 'drop') {
      checks.push('Double-check index is truly unused');
    }

    if (recommendation.type === 'create') {
      checks.push('Verify column selectivity supports index effectiveness');
    }

    return checks;
  }

  private generateWarnings(recommendation: IndexOptimizationRecommendation, currentStats: any): string[] {
    const warnings: string[] = [];

    if (recommendation.riskLevel === 'high') {
      warnings.push('HIGH RISK: This optimization has significant risk factors');
    }

    if (recommendation.type === 'drop' && currentStats.lastUsed && (Date.now() - currentStats.lastUsed) < 7 * 24 * 60 * 60 * 1000) {
      warnings.push('Index was used within the last 7 days - verify it is safe to drop');
    }

    if (recommendation.implementationComplexity === 'high') {
      warnings.push('Complex optimization - consider executing during planned maintenance');
    }

    return warnings;
  }

  private generateRollbackPlan(recommendation: IndexOptimizationRecommendation): any {
    switch (recommendation.type) {
      case 'drop':
        return {
          complexity: 'difficult',
          steps: ['Recreate index with original definition', 'Rebuild statistics'],
          dataRequired: 'Original CREATE INDEX statement'
        };
      case 'create':
        return {
          complexity: 'easy',
          steps: ['DROP INDEX ' + recommendation.indexName],
          dataRequired: 'None'
        };
      case 'rebuild':
        return {
          complexity: 'easy',
          steps: ['Index will return to previous state automatically if failed'],
          dataRequired: 'None'
        };
      default:
        return {
          complexity: 'manual',
          steps: ['Requires case-by-case analysis'],
          dataRequired: 'Before/after metrics'
        };
    }
  }

  private isExecutionSafe(recommendation: IndexOptimizationRecommendation, riskTolerance: string): boolean {
    if (recommendation.riskLevel === 'high' && riskTolerance === 'conservative') {
      return false;
    }

    if (recommendation.implementationComplexity === 'high' && riskTolerance !== 'aggressive') {
      return false;
    }

    return true;
  }

  private selectOptimizationsForExecution(
    recommendations: IndexOptimizationRecommendation[], 
    riskTolerance: string, 
    maxOptimizations: number,
    autoApprove: boolean
  ): IndexOptimizationRecommendation[] {
    let eligible = this.applyRiskFilter(recommendations, riskTolerance);

    if (autoApprove) {
      eligible = eligible.filter(r => this.canAutoApprove(r, riskTolerance));
    }

    // Sort by priority and benefit
    eligible.sort((a, b) => {
      if (a.implementationPriority !== b.implementationPriority) {
        return a.implementationPriority - b.implementationPriority;
      }
      return b.estimatedBenefit - a.estimatedBenefit;
    });

    return eligible.slice(0, maxOptimizations);
  }

  private async executeIndividualOptimization(
    recommendation: IndexOptimizationRecommendation, 
    forceExecution: boolean
  ): Promise<OptimizationResult> {
    const optimizationId = this.generateOptimizationId(recommendation);
    const result: OptimizationResult = {
      optimizationId,
      indexName: recommendation.indexName,
      tableName: recommendation.tableName,
      action: recommendation.type,
      status: 'planned'
    };

    try {
      result.status = 'executing';
      const executionResult = await this.indexMonitor.executeRecommendation(recommendation);

      if (executionResult.success) {
        result.status = 'completed';
        result.executionTime = executionResult.executionTime;
        result.actualBenefit = recommendation.estimatedBenefit; // Simplified
      } else {
        result.status = 'failed';
        result.error = executionResult.error;
      }
    } catch (error) {
      result.status = 'failed';
      result.error = error instanceof Error ? error.message : String(error);
    }

    return result;
  }

  private calculateOverallRiskScore(recommendations: IndexOptimizationRecommendation[]): number {
    if (recommendations.length === 0) return 0;

    const riskScores = recommendations.map(r => {
      switch (r.riskLevel) {
        case 'low': return 1;
        case 'medium': return 2;
        case 'high': return 3;
        default: return 2;
      }
    });

    return Math.round(riskScores.reduce((sum, score) => sum + score, 0) / riskScores.length);
  }

  private generateRiskMitigations(recommendations: IndexOptimizationRecommendation[]): string[] {
    const mitigations = [
      'Execute optimizations during low-traffic periods',
      'Monitor query performance immediately after changes',
      'Keep rollback scripts ready for high-risk operations'
    ];

    if (recommendations.some(r => r.type === 'drop')) {
      mitigations.push('Verify unused indexes with recent query analysis');
    }

    return mitigations;
  }

  private generatePostExecutionRecommendations(results: OptimizationResult[]): string[] {
    const recommendations = [];

    const successful = results.filter(r => r.status === 'completed').length;
    const failed = results.filter(r => r.status === 'failed').length;

    if (successful > 0) {
      recommendations.push(`Monitor performance of ${successful} optimized indexes over next 24 hours`);
    }

    if (failed > 0) {
      recommendations.push(`Investigate ${failed} failed optimizations for root cause`);
    }

    recommendations.push('Update monitoring baseline with new performance metrics');
    recommendations.push('Schedule follow-up analysis in 1 week');

    return recommendations;
  }
}

/**
 * Semantic Search Tool - MCP tool for semantic similarity search
 * 
 * This tool provides semantic search capabilities using local embeddings.
 * It allows users to find conceptually similar messages even when they
 * don't share exact keywords.
 */

import { z } from 'zod';
import { BaseTool, ToolContext } from './BaseTool.js';
import { ToolError, extractErrorInfo } from '../utils/errors.js';
import { EnhancedSearchEngine } from '../search/EnhancedSearchEngine.js';
import { QueryParser } from '../search/QueryParser.js';
import { SearchUtils } from '../search/index.js';
import { SemanticSearchTool as SemanticSearchToolDef } from '../types/mcp.js';

const semanticSearchSchema = z.object({
  query: z.string()
    .min(1)
    .max(1000)
    .describe('Search query for semantic matching'),
  limit: z.number()
    .min(1)
    .max(100)
    .default(20)
    .describe('Maximum results to return'),
  offset: z.number()
    .min(0)
    .default(0)
    .describe('Number of results to skip for pagination'),
  conversationId: z.string()
    .optional()
    .describe('Limit search to specific conversation'),
  startDate: z.string()
    .datetime()
    .optional()
    .describe('Filter by start date (ISO 8601 format)'),
  endDate: z.string()
    .datetime()
    .optional()
    .describe('Filter by end date (ISO 8601 format)'),
  threshold: z.number()
    .min(0)
    .max(1)
    .default(0.7)
    .describe('Minimum similarity threshold (0-1)'),
  explainResults: z.boolean()
    .default(false)
    .describe('Include explanations for why results were selected')
});

type SemanticSearchParams = z.infer<typeof semanticSearchSchema>;

export class SemanticSearchTool extends BaseTool<SemanticSearchParams> {
  private enhancedSearchEngine: EnhancedSearchEngine;

  constructor(enhancedSearchEngine: EnhancedSearchEngine) {
    super(SemanticSearchToolDef, semanticSearchSchema);
    this.enhancedSearchEngine = enhancedSearchEngine;
  }

  protected async executeImpl(params: SemanticSearchParams, context: ToolContext) {
    try {
      // Sanitize query input to prevent injection attacks
      const sanitizedQuery = this.sanitizeQuery(params.query);

      // Validate date parameters (Zod already validates format, but check for edge cases)
      const { startDate, endDate } = this.validateAndConvertDates(params);

      // Perform semantic search with sanitized query
      const searchResult = await this.enhancedSearchEngine.search({
        query: sanitizedQuery,
        strategy: 'semantic',
        limit: params.limit,
        offset: params.offset,
        conversationId: params.conversationId,
        startDate: startDate,
        endDate: endDate,
        semanticThreshold: params.threshold,
        explainResults: params.explainResults
      });

      // Results are already filtered by the search engine based on threshold
      const filteredResults = searchResult.results;

      // Format response
      const response = {
        success: true,
        results: filteredResults.map(result => ({
          messageId: result.messageId,
          conversationId: result.conversationId,
          content: result.content,
          similarity: result.scores.semantic || result.score,
          preview: this.createPreview(result.content, result.highlights),
          conversationTitle: result.conversationTitle,
          createdAt: result.createdAt,
          ...(params.explainResults && { explanation: result.explanation })
        })),
        totalCount: filteredResults.length,
        hasMore: searchResult.hasMore,
        metadata: {
          searchStrategy: 'semantic',
          model: 'all-MiniLM-L6-v2',
          threshold: params.threshold,
          queryId: searchResult.metrics?.queryId,
          executionTime: searchResult.metrics?.totalTime,
          timing: searchResult.metrics?.timing
        },
        pagination: {
          offset: params.offset,
          limit: params.limit,
          hasMore: searchResult.hasMore
        }
      };

      return response;

    } catch (error) {
      if (error instanceof ToolError) {
        throw error;
      }

      // Log detailed error information internally
      const errorInfo = extractErrorInfo(error);
      console.error('Semantic search error:', {
        ...errorInfo,
        requestId: context.requestId,
        query: SearchUtils.sanitizeForLogging(params.query)
      });

      // Return generic error message to user
      throw new ToolError(
        'semantic_search',
        'Search operation failed. Please try again with a different query.'
      );
    }
  }

  /**
   * Sanitize user query input to prevent injection attacks
   */
  private sanitizeQuery(query: string): string {
    if (!query || typeof query !== 'string') {
      throw new ToolError('semantic_search', 'Query must be a non-empty string');
    }

    // Use the same sanitization as QueryParser for consistency
    try {
      const parsed = QueryParser.parseQuery(query, 'fuzzy');
      return parsed.query;
    } catch (error) {
      // If parsing fails, apply basic sanitization
      return query
        .trim()
        .replace(/[\u0000-\u001F\u007F]/g, '') // Remove control characters
        .substring(0, 1000); // Enforce max length
    }
  }

  /**
   * Validate and convert date parameters
   */
  private validateAndConvertDates(params: SemanticSearchParams): {
    startDate?: string;
    endDate?: string;
  } {
    // Zod already validates datetime format, but handle edge cases
    let startDate: string | undefined;
    let endDate: string | undefined;

    if (params.startDate) {
      const start = new Date(params.startDate);
      if (start.getFullYear() < 1900 || start.getFullYear() > 2100) {
        throw new ToolError('semantic_search', 'Start date must be between 1900 and 2100');
      }
      startDate = start.toISOString();
    }

    if (params.endDate) {
      const end = new Date(params.endDate);
      if (end.getFullYear() < 1900 || end.getFullYear() > 2100) {
        throw new ToolError('semantic_search', 'End date must be between 1900 and 2100');
      }
      endDate = end.toISOString();
    }

    // Check date range
    if (startDate && endDate && new Date(startDate) >= new Date(endDate)) {
      throw new ToolError('semantic_search', 'Start date must be before end date');
    }

    return { startDate, endDate };
  }

  /**
   * Create a standardized preview snippet from content and highlights
   */
  private createPreview(content: string, highlights: string[]): string {
    const maxLength = 250;

    if (highlights && highlights.length > 0) {
      // Use the best highlight as preview
      const bestHighlight = highlights
        .filter(h => h && h.length > 10) // Filter out very short highlights
        .sort((a, b) => b.length - a.length)[0] || highlights[0];

      if (bestHighlight && bestHighlight.length <= maxLength) {
        return bestHighlight;
      }

      if (bestHighlight && bestHighlight.length > maxLength) {
        return bestHighlight.substring(0, maxLength - 3) + '...';
      }
    }

    // Fallback to content beginning
    if (content.length <= maxLength) {
      return content;
    }

    return content.substring(0, maxLength - 3) + '...';
  }

  /**
   * Validate that the enhanced search engine is available
   */
  async validate(): Promise<{ isValid: boolean; error?: string }> {
    try {
      // Check if embedding manager is initialized
      await this.enhancedSearchEngine.getSearchMetrics({ limit: 1 });
      return { isValid: true };
    } catch (error) {
      // Log detailed error internally
      const errorInfo = extractErrorInfo(error);
      console.error('Semantic search validation error:', errorInfo);

      return {
        isValid: false,
        error: 'Semantic search service is currently unavailable'
      };
    }
  }

  /**
   * Get usage examples for this tool
   */
  getExamples(): Array<{ description: string; params: SemanticSearchParams }> {
    return [
      {
        description: 'Find messages about machine learning concepts',
        params: {
          query: 'neural networks and deep learning',
          limit: 10,
          offset: 0,
          threshold: 0.75,
          explainResults: false
        }
      },
      {
        description: 'Search for cooking-related discussions with explanations',
        params: {
          query: 'recipes and cooking techniques',
          limit: 15,
          offset: 0,
          threshold: 0.7,
          explainResults: true
        }
      },
      {
        description: 'Find recent messages about a specific topic',
        params: {
          query: 'project management methodologies',
          limit: 20,
          offset: 0,
          startDate: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString(), // Last 7 days
          threshold: 0.8,
          explainResults: false
        }
      },
      {
        description: 'Semantic search within a specific conversation',
        params: {
          query: 'troubleshooting and debugging approaches',
          conversationId: 'conv-123',
          limit: 10,
          offset: 0,
          threshold: 0.6,
          explainResults: false
        }
      }
    ];
  }

  /**
   * Get tool metadata for introspection
   */
  getMetadata() {
    return {
      name: this.getName(),
      description: this.getDescription(),
      category: 'search',
      requiresEmbeddings: true,
      supportedLanguages: ['en'], // Expandable based on model capabilities
      averageExecutionTime: '200-500ms',
      memoryUsage: 'Medium (requires embedding model)',
      accuracy: 'High for conceptual similarity',
      limitations: [
        'Requires pre-generated embeddings',
        'Performance depends on text length',
        'May not work well with very short queries',
        'Limited to supported embedding model languages'
      ]
    };
  }

  /**
   * Legacy handle method for backward compatibility with tests
   * Delegates to the execute method from BaseTool
   */
  async handle(input: unknown, context: any = {}) {
    const toolContext = context.requestId ? context : {
      requestId: context.requestId || 'test-' + Date.now(),
      timestamp: Date.now(),
      ...context
    };
    return this.execute(input, toolContext);
  }
}

/**
 * Track Decision Effectiveness Tool Implementation
 * 
 * This tool tracks decision-making patterns and effectiveness by analyzing:
 * - Decisions made in conversations and their quality
 * - Decision outcomes and follow-up actions
 * - Decision reversal patterns and rates
 * - Decision-making process effectiveness
 * - Factors that influence decision quality
 */

import { TrackDecisionEffectivenessToolDef as TrackDecisionEffectivenessToolDef } from '../types/mcp.js';
import { TrackDecisionEffectivenessSchema, TrackDecisionEffectivenessInput } from '../types/schemas.js';
import { BaseTool, ToolContext, wrapDatabaseOperation } from './BaseTool.js';
import { AnalyticsEngine } from '../analytics/services/AnalyticsEngine.js';
import { DecisionTracker, Decision, DecisionOutcome } from '../analytics/analyzers/DecisionTracker.js';
import { ConversationRepository } from '../storage/repositories/ConversationRepository.js';
import { MessageRepository } from '../storage/repositories/MessageRepository.js';
import { DecisionTrackingRepository } from '../analytics/repositories/DecisionTrackingRepository.js';
import { TimeRange } from '../analytics/repositories/AnalyticsRepository.js';
import { 
  validateDateRange, 
  validateStringArray,
  ValidationError,
  formatValidationError,
  withEnhancedValidation 
} from '../utils/validation.js';

/**
 * Decision quality analysis
 */
export interface DecisionQualityAnalysis {
  /** Average decision quality score (0-100) */
  averageQuality: number;
  /** Quality distribution */
  qualityDistribution: {
    excellent: number; // 80-100
    good: number;      // 60-79
    fair: number;      // 40-59
    poor: number;      // 0-39
  };
  /** Factors affecting quality */
  qualityFactors: Array<{
    factor: string;
    impact: number; // -100 to 100
    frequency: number;
  }>;
  /** Quality trends over time */
  trends: Array<{
    period: string;
    averageQuality: number;
    decisionCount: number;
  }>;
}

/**
 * Decision outcome tracking
 */
export interface DecisionOutcomeAnalysis {
  /** Average outcome score (0-100) */
  averageOutcome: number;
  /** Outcome distribution */
  outcomeDistribution: {
    successful: number;    // 80-100
    partial: number;       // 40-79
    unsuccessful: number;  // 0-39
    unknown: number;       // Not tracked
  };
  /** Time to outcome measurement */
  timeToOutcome: {
    average: number; // days
    median: number;  // days
    range: { min: number; max: number };
  };
  /** Success factors */
  successFactors: string[];
  /** Common failure patterns */
  failurePatterns: string[];
}

/**
 * Decision reversal analysis
 */
export interface DecisionReversalAnalysis {
