    contextConflicts: ConflictingInformation[];
    entityConflicts: EntityConflict[];
  };
  summary: {
    totalConflicts: number;
    severityBreakdown: {
      low: number;
      medium: number;
      high: number;
      critical: number;
    };
    conflictTypeBreakdown: Record<string, number>;
    entitiesWithConflicts: number;
    analysisScope: {
      conversationId?: string;
      entityIds?: string[];
      maxAge: number;
      minSeverity: string;
    };
  };
  analysisTimestamp: number;
}

/**
 * Dependencies required by CheckForConflictsTool
 */
export interface CheckForConflictsDependencies {
  databaseManager: DatabaseManager;
  entityRepository: EntityRepository;
  knowledgeGraphRepository: KnowledgeGraphRepository;
}

/**
 * Implementation of the check_for_conflicts MCP tool
 */
export class CheckForConflictsTool extends BaseTool<CheckForConflictsInput, CheckForConflictsResponse> {
  private readonly contextDetector: ContextChangeDetector;
  private readonly knowledgeSynthesizer: KnowledgeSynthesizer;

  constructor(dependencies: CheckForConflictsDependencies) {
    super(CheckForConflictsToolDef, CheckForConflictsSchema);

    this.contextDetector = new ContextChangeDetector(
      dependencies.databaseManager,
      dependencies.entityRepository,
      dependencies.knowledgeGraphRepository,
      {
        maxHistoryAgeDays: 90, // Will be overridden by input
        minConflictSeverity: 0.5
      }
    );

    this.knowledgeSynthesizer = new KnowledgeSynthesizer(dependencies.databaseManager);
  }

  /**
   * Execute the check_for_conflicts tool
   */
  protected async executeImpl(
    input: CheckForConflictsInput, 
    context: ToolContext
  ): Promise<CheckForConflictsResponse> {

    const conflictDetectionOptions = {
      conversationId: input.conversationId,
      entityIds: input.entityIds,
      minSeverity: this.mapSeverityToNumeric(input.minSeverity),
      limit: input.limit
    };

    // Detect context-level conflicts using ContextChangeDetector
    const contextConflicts = await wrapDatabaseOperation(
      async () => await this.contextDetector.findConflictingInformation(conflictDetectionOptions),
      'Failed to detect context conflicts'
    );

    // Detect entity-level conflicts using KnowledgeSynthesizer
    let entityConflicts: EntityConflict[] = [];

    if (input.entityIds && input.entityIds.length > 0) {
      // Check specific entities
      for (const entityId of input.entityIds) {
        const entitySpecificConflicts = await wrapDatabaseOperation(
          async () => await this.knowledgeSynthesizer.detectConflictingStatements(entityId),
          `Failed to detect conflicts for entity ${entityId}`
        );
        entityConflicts.push(...entitySpecificConflicts);
      }
    } else {
      // Check all entities with sufficient mentions
      entityConflicts = await wrapDatabaseOperation(
        async () => await this.knowledgeSynthesizer.detectConflictingStatements(),
        'Failed to detect entity conflicts'
      );
    }

    // Filter conflicts by type and severity
    const filteredContextConflicts = contextConflicts.filter(conflict => 
      input.conflictTypes.includes(conflict.conflictType) &&
      this.meetsSeverityThreshold(conflict.conflictSeverity, input.minSeverity)
    );

    const filteredEntityConflicts = entityConflicts.filter(conflict => 
      input.conflictTypes.includes(conflict.conflictType) &&
      this.meetsSeverityThreshold(conflict.severity, input.minSeverity)
    );

    // Calculate summary statistics
    const allConflicts = [...filteredContextConflicts, ...filteredEntityConflicts];
    const totalConflicts = allConflicts.length;

    const severityBreakdown = {
      low: 0,
      medium: 0,
      high: 0,
      critical: 0
    };

    const conflictTypeBreakdown: Record<string, number> = {};
    const entitiesWithConflicts = new Set<string>();

    // Count severity and types for context conflicts
    for (const conflict of filteredContextConflicts) {
      const severity = this.mapNumericToSeverity(conflict.conflictSeverity);
      severityBreakdown[severity]++;

      conflictTypeBreakdown[conflict.conflictType] = 
        (conflictTypeBreakdown[conflict.conflictType] || 0) + 1;

      entitiesWithConflicts.add(conflict.entity.id);
    }

    // Count severity and types for entity conflicts
    for (const conflict of filteredEntityConflicts) {
      severityBreakdown[conflict.severity]++;

      conflictTypeBreakdown[conflict.conflictType] = 
        (conflictTypeBreakdown[conflict.conflictType] || 0) + 1;

      entitiesWithConflicts.add(conflict.entityId);
    }

    return {
      conflicts: {
        contextConflicts: filteredContextConflicts.slice(0, input.limit),
        entityConflicts: filteredEntityConflicts.slice(0, input.limit)
      },
      summary: {
        totalConflicts,
        severityBreakdown,
        conflictTypeBreakdown,
        entitiesWithConflicts: entitiesWithConflicts.size,
        analysisScope: {
          conversationId: input.conversationId,
          entityIds: input.entityIds,
          maxAge: input.maxAge,
          minSeverity: input.minSeverity
        }
      },
      analysisTimestamp: Date.now()
    };
  }

  /**
   * Map string severity to numeric value for filtering
   */
  private mapSeverityToNumeric(severity: string): number {
    const severityMap = {
      'low': 0.3,
      'medium': 0.5,
      'high': 0.7,
      'critical': 0.9
    };
    return severityMap[severity as keyof typeof severityMap] || 0.5;
  }

  /**
   * Map numeric severity to string
   */
  private mapNumericToSeverity(numericSeverity: number): 'low' | 'medium' | 'high' | 'critical' {
    if (numericSeverity >= 0.9) return 'critical';
    if (numericSeverity >= 0.7) return 'high';
    if (numericSeverity >= 0.5) return 'medium';
    return 'low';
  }

  /**
   * Check if conflict meets severity threshold
   */
  private meetsSeverityThreshold(
    conflictSeverity: number | 'low' | 'medium' | 'high' | 'critical',
    minSeverity: string
  ): boolean {
    const numericSeverity = typeof conflictSeverity === 'number' 
      ? conflictSeverity 
      : this.mapSeverityToNumeric(conflictSeverity);

    return numericSeverity >= this.mapSeverityToNumeric(minSeverity);
  }

  /**
   * Static factory method to create a CheckForConflictsTool instance
   */
  static create(dependencies: CheckForConflictsDependencies): CheckForConflictsTool {
    return new CheckForConflictsTool(dependencies);
  }
}

/**
 * Get Proactive Insights Tool Implementation
 * 
 * This tool returns unresolved actions, recurring questions, knowledge gaps,
 * and stale commitments by leveraging the Pattern Detection Service.
 */

import { z } from 'zod';
import { BaseTool, ToolContext, wrapDatabaseOperation } from '../BaseTool.js';
import { MCPTool, MCPToolResult } from '../../types/mcp.js';
import { PatternDetectionService, UnresolvedAction, RecurringQuestion, KnowledgeGap, TrackedCommitment } from '../../services/proactive/patterns/PatternDetectionService.js';
import { DatabaseManager } from '../../storage/Database.js';

/**
 * Tool definition for get_proactive_insights
 */
export const GetProactiveInsightsToolDef: MCPTool = {
  name: 'get_proactive_insights',
  description: 'Returns unresolved actions, recurring questions, knowledge gaps, and stale commitments to provide proactive assistance.',
  inputSchema: {
    type: 'object',
    properties: {
      conversationId: {
        type: 'string',
        description: 'Optional conversation ID to limit analysis scope'
      },
      includeTypes: {
        type: 'array',
        items: {
          type: 'string',
          enum: ['unresolved_actions', 'recurring_questions', 'knowledge_gaps', 'stale_commitments']
        },
        description: 'Types of insights to include',
        default: ['unresolved_actions', 'recurring_questions', 'knowledge_gaps', 'stale_commitments']
      },
      daysSince: {
        type: 'number',
        minimum: 1,
        maximum: 365,
        default: 30,
        description: 'Number of days to look back for patterns'
      },
      minConfidence: {
        type: 'number',
        minimum: 0,
        maximum: 1,
        default: 0.6,
        description: 'Minimum confidence threshold for insights'
      },
      limit: {
        type: 'number',
        minimum: 1,
        maximum: 100,
        default: 20,
        description: 'Maximum number of insights per type to return'
      }
    },
    required: [],
    additionalProperties: false
  }
};

/**
 * Input validation schema
 */
export const GetProactiveInsightsSchema = z.object({
  conversationId: z.string().optional(),
  includeTypes: z.array(z.enum(['unresolved_actions', 'recurring_questions', 'knowledge_gaps', 'stale_commitments']))
    .default(['unresolved_actions', 'recurring_questions', 'knowledge_gaps', 'stale_commitments']),
  daysSince: z.number().min(1).max(365).default(30),
  minConfidence: z.number().min(0).max(1).default(0.6),
  limit: z.number().min(1).max(100).default(20)
});

export type GetProactiveInsightsInput = z.infer<typeof GetProactiveInsightsSchema>;

/**
 * Response interface
 */
export interface GetProactiveInsightsResponse {
  insights: {
    unresolvedActions?: UnresolvedAction[];
    recurringQuestions?: RecurringQuestion[];
    knowledgeGaps?: KnowledgeGap[];
    staleCommitments?: TrackedCommitment[];
  };
  summary: {
    totalInsights: number;
    analysisScope: {
      conversationId?: string;
      daysSince: number;
      minConfidence: number;
    };
    detectionTimestamp: number;
  };
}

/**
 * Dependencies required by GetProactiveInsightsTool
 */
export interface GetProactiveInsightsDependencies {
  databaseManager: DatabaseManager;
}

/**
 * Implementation of the get_proactive_insights MCP tool
 */
export class GetProactiveInsightsTool extends BaseTool<GetProactiveInsightsInput, GetProactiveInsightsResponse> {
  private readonly patternService: PatternDetectionService;

  constructor(dependencies: GetProactiveInsightsDependencies) {
    super(GetProactiveInsightsToolDef, GetProactiveInsightsSchema);
    this.patternService = new PatternDetectionService(dependencies.databaseManager);
  }

  /**
   * Execute the get_proactive_insights tool
   */
  protected async executeImpl(
    input: GetProactiveInsightsInput, 
    context: ToolContext
  ): Promise<GetProactiveInsightsResponse> {

    const insights: GetProactiveInsightsResponse['insights'] = {};
    let totalInsights = 0;

    const analysisOptions = {
      conversationId: input.conversationId,
      daysSince: input.daysSince,
      minConfidence: input.minConfidence,
      limit: input.limit
    };

    // Detect unresolved actions
    if (input.includeTypes.includes('unresolved_actions')) {
      insights.unresolvedActions = await wrapDatabaseOperation(
        async () => await this.patternService.detectUnresolvedActions(analysisOptions),
        'Failed to detect unresolved actions'
      );
      totalInsights += insights.unresolvedActions.length;
    }

    // Find recurring questions
    if (input.includeTypes.includes('recurring_questions')) {
      insights.recurringQuestions = await wrapDatabaseOperation(
        async () => await this.patternService.findRecurringQuestions({
          conversationId: input.conversationId,
          minFrequency: 2,
          minDaysBetween: 1,
          limit: input.limit
        }),
        'Failed to find recurring questions'
      );
      totalInsights += insights.recurringQuestions.length;
    }

    // Identify knowledge gaps
    if (input.includeTypes.includes('knowledge_gaps')) {
      insights.knowledgeGaps = await wrapDatabaseOperation(
        async () => await this.patternService.identifyKnowledgeGaps({
          conversationId: input.conversationId,
          minGapRatio: 1.5,
          limit: input.limit
        }),
        'Failed to identify knowledge gaps'
      );
      totalInsights += insights.knowledgeGaps.length;
    }

    // Track stale commitments
    if (input.includeTypes.includes('stale_commitments')) {
      const allCommitments = await wrapDatabaseOperation(
        async () => await this.patternService.trackCommitments({
          conversationId: input.conversationId,
          includeResolved: false,
          limit: input.limit * 2 // Get more to filter for stale ones
        }),
        'Failed to track commitments'
      );

      // Filter for stale commitments (pending/overdue for more than specified days)
      insights.staleCommitments = allCommitments.filter(commitment => 
        (commitment.status === 'pending' || commitment.status === 'overdue') &&
        commitment.daysSinceCommitment >= Math.min(input.daysSince, 7) // At least a week old
      ).slice(0, input.limit);

      totalInsights += insights.staleCommitments.length;
    }

    return {
      insights,
      summary: {
        totalInsights,
        analysisScope: {
          conversationId: input.conversationId,
          daysSince: input.daysSince,
          minConfidence: input.minConfidence
        },
        detectionTimestamp: Date.now()
      }
    };
  }

  /**
   * Static factory method to create a GetProactiveInsightsTool instance
   */
  static create(dependencies: GetProactiveInsightsDependencies): GetProactiveInsightsTool {
    return new GetProactiveInsightsTool(dependencies);
  }
}

