/**
 * Intelligent Cache Manager - Advanced caching with adaptive strategies
 * 
 * This class provides:
 * - Multi-tier caching with intelligent eviction policies
 * - Adaptive cache sizing based on system resources
 * - Cache warming and preloading strategies
 * - Cross-system cache coordination
 * - Performance-aware cache management
 */

import { EventEmitter } from 'events';
import { MemoryManager } from './MemoryManager.js';
import { SizeEstimator } from './SizeEstimator.js';

interface CacheEntry<T> {
  key: string;
  value: T;
  size: number;
  timestamp: number;
  lastAccess: number;
  accessCount: number;
  ttl: number;
  priority: 'low' | 'medium' | 'high' | 'critical';
  cost: number; // Cost to recreate this entry
}

interface CacheStats {
  hits: number;
  misses: number;
  evictions: number;
  totalSize: number;
  entryCount: number;
  hitRate: number;
  averageAccessTime: number;
}

interface CacheTier<T> {
  name: string;
  maxSize: number;
  entries: Map<string, CacheEntry<T>>;
  stats: CacheStats;
  policy: 'LRU' | 'LFU' | 'TLRU' | 'ARC';
}

export interface CacheConfig {
  /** Maximum total memory for all caches in bytes */
  maxTotalMemory: number;
  /** L1 cache size (hot data) */
  l1Size: number;
  /** L2 cache size (warm data) */
  l2Size: number;
  /** L3 cache size (cold data) */
  l3Size: number;
  /** Default TTL in milliseconds */
  defaultTTL: number;
  /** Enable adaptive sizing */
  enableAdaptiveSizing: boolean;
  /** Memory pressure threshold for cache reduction */
  memoryPressureThreshold: number;
  /** Enable cache warming */
  enableCacheWarming: boolean;
}

export class IntelligentCacheManager<T = any> extends EventEmitter {
  private memoryManager: MemoryManager;
  private config: CacheConfig;
  private tiers: Map<string, CacheTier<T>> = new Map();
  private globalStats: CacheStats;
  private adaptiveSizingInterval: NodeJS.Timeout | null = null;
  private isActive = false;

  // Cache access patterns tracking
  private accessPatterns: Map<string, {
    frequency: number;
    lastAccess: number;
    accessTimes: number[];
  }> = new Map();

  constructor(memoryManager: MemoryManager, config: Partial<CacheConfig> = {}) {
    super();

    this.memoryManager = memoryManager;
    this.config = {
      maxTotalMemory: 100 * 1024 * 1024, // 100MB
      l1Size: 10 * 1024 * 1024,  // 10MB - Hot data
      l2Size: 30 * 1024 * 1024,  // 30MB - Warm data  
      l3Size: 60 * 1024 * 1024,  // 60MB - Cold data
      defaultTTL: 300000, // 5 minutes
      enableAdaptiveSizing: true,
      memoryPressureThreshold: 0.8,
      enableCacheWarming: true,
      ...config
    };

    this.globalStats = {
      hits: 0,
      misses: 0,
      evictions: 0,
      totalSize: 0,
      entryCount: 0,
      hitRate: 0,
      averageAccessTime: 0
    };

    this.initializeTiers();
    this.setupMemoryPressureHandler();
  }

  /**
   * Start intelligent cache management
   */
  start(): void {
    if (this.isActive) return;

    this.isActive = true;

    // Start adaptive sizing if enabled
    if (this.config.enableAdaptiveSizing) {
      this.adaptiveSizingInterval = setInterval(() => {
        this.performAdaptiveSizing();
      }, 60000); // Every minute
    }

    console.log('Intelligent cache manager started');
    this.emit('cache:started');
  }

  /**
   * Stop cache management
   */
  stop(): void {
    if (!this.isActive) return;

    this.isActive = false;

    if (this.adaptiveSizingInterval) {
      clearInterval(this.adaptiveSizingInterval);
      this.adaptiveSizingInterval = null;
    }

    console.log('Intelligent cache manager stopped');
    this.emit('cache:stopped');
  }

  /**
   * Get value from cache with intelligent tier selection
   */
  async get(key: string): Promise<T | null> {
    const startTime = Date.now();

    // Search through tiers in order
    for (const tier of this.tiers.values()) {
      const entry = tier.entries.get(key);
      if (entry) {
        // Check if entry is still valid
        if (Date.now() - entry.timestamp <= entry.ttl) {
          // Update access statistics
          entry.lastAccess = Date.now();
          entry.accessCount++;

          // Promote frequently accessed items to higher tier
          await this.considerPromotion(key, entry, tier);

          // Update global and tier stats
          tier.stats.hits++;
          this.globalStats.hits++;

          // Track access pattern
          this.trackAccess(key, Date.now() - startTime);

          this.updateHitRates();
          return entry.value;
        } else {
          // Expired entry - remove it
          this.removeFromTier(key, tier);
        }
      }
    }

    // Cache miss
    this.globalStats.misses++;
    this.tiers.forEach(tier => tier.stats.misses++);
    this.updateHitRates();

    // Track access pattern for misses
    this.trackAccess(key, Date.now() - startTime);

    return null;
  }

  /**
   * Set value in cache with intelligent tier placement
   */
  async set(
    key: string, 
    value: T, 
    options: {
      ttl?: number;
      priority?: 'low' | 'medium' | 'high' | 'critical';
      cost?: number;
      size?: number;
    } = {}
  ): Promise<void> {
    const entry: CacheEntry<T> = {
      key,
      value,
      size: options.size || this.estimateSize(value),
      timestamp: Date.now(),
      lastAccess: Date.now(),
      accessCount: 1,
      ttl: options.ttl || this.config.defaultTTL,
      priority: options.priority || 'medium',
      cost: options.cost || 1
    };

    // Determine initial tier based on priority and access patterns
    const targetTier = this.selectInitialTier(key, entry);

    // Ensure space is available
    await this.ensureSpace(targetTier, entry.size);

    // Remove from other tiers if exists
    for (const [tierName, tier] of this.tiers) {
      if (tierName !== targetTier.name) {
        this.removeFromTier(key, tier);
      }
    }

    // Add to target tier
    targetTier.entries.set(key, entry);
    targetTier.stats.entryCount++;
    targetTier.stats.totalSize += entry.size;

    this.globalStats.entryCount++;
    this.globalStats.totalSize += entry.size;

    this.emit('cache:set', { key, tier: targetTier.name, size: entry.size });
  }

  /**
   * Remove key from all tiers
   */
  delete(key: string): boolean {
    let found = false;

    for (const tier of this.tiers.values()) {
      if (this.removeFromTier(key, tier)) {
        found = true;
      }
    }

    if (found) {
      this.accessPatterns.delete(key);
    }

    return found;
  }

  /**
   * Clear all caches
   */
  clear(): void {
    for (const tier of this.tiers.values()) {
      tier.entries.clear();
      tier.stats = {
        hits: 0,
        misses: 0,
        evictions: 0,
        totalSize: 0,
        entryCount: 0,
        hitRate: 0,
        averageAccessTime: 0
      };
    }

    this.globalStats = {
      hits: 0,
      misses: 0,
      evictions: 0,
      totalSize: 0,
      entryCount: 0,
      hitRate: 0,
      averageAccessTime: 0
    };

    this.accessPatterns.clear();
    this.emit('cache:cleared');
  }

  /**
   * Warm cache with frequently accessed data
   */
  async warmCache(warmingStrategies: Array<{
    keys: string[];
    loader: (key: string) => Promise<T>;
    priority?: 'low' | 'medium' | 'high' | 'critical';
  }>): Promise<void> {
    if (!this.config.enableCacheWarming) return;

    for (const strategy of warmingStrategies) {
      for (const key of strategy.keys) {
        try {
          // Check if already cached
          const existing = await this.get(key);
          if (existing !== null) continue;

          // Load and cache
          const value = await strategy.loader(key);
          await this.set(key, value, {
            priority: strategy.priority || 'medium',
            cost: 5 // Higher cost since we proactively loaded it
          });

        } catch (error) {
          console.warn(`Cache warming failed for key ${key}:`, error);
        }
      }
    }

    this.emit('cache:warmed', { strategies: warmingStrategies.length });
  }

  /**
   * Get comprehensive cache statistics
   */
  getStats(): {
    global: CacheStats;
    tiers: Record<string, CacheStats>;
    efficiency: {
      memoryUtilization: number;
      averageEntrySize: number;
      hotDataRatio: number;
    };
    recommendations: string[];
  } {
    const tierStats: Record<string, CacheStats> = {};
    for (const [name, tier] of this.tiers) {
      tierStats[name] = { ...tier.stats };
    }

    // Calculate efficiency metrics
    const l1Tier = this.tiers.get('L1');
    const totalEntries = this.globalStats.entryCount;
    const l1Entries = l1Tier ? l1Tier.stats.entryCount : 0;

    const efficiency = {
      memoryUtilization: this.globalStats.totalSize / this.config.maxTotalMemory,
      averageEntrySize: totalEntries > 0 ? this.globalStats.totalSize / totalEntries : 0,
      hotDataRatio: totalEntries > 0 ? l1Entries / totalEntries : 0
    };

    // Generate recommendations
    const recommendations = this.generateCacheRecommendations(efficiency);

    return {
      global: { ...this.globalStats },
      tiers: tierStats,
      efficiency,
      recommendations
    };
  }

  /**
   * Optimize cache performance
   */
  async optimizeCache(): Promise<{
    optimizations: string[];
    memoryFreed: number;
    performanceImprovement: number;
  }> {
    const optimizations: string[] = [];
    const beforeSize = this.globalStats.totalSize;
    const beforeHitRate = this.globalStats.hitRate;

    // 1. Evict expired entries
    let expiredCount = 0;
    for (const tier of this.tiers.values()) {
      for (const [key, entry] of tier.entries) {
        if (Date.now() - entry.timestamp > entry.ttl) {
          this.removeFromTier(key, tier);
          expiredCount++;
        }
      }
    }
    if (expiredCount > 0) {
      optimizations.push(`Removed ${expiredCount} expired entries`);
    }

    // 2. Rebalance tiers based on access patterns
    await this.rebalanceTiers();
    optimizations.push('Rebalanced cache tiers');

    // 3. Optimize tier sizes if adaptive sizing is enabled
    if (this.config.enableAdaptiveSizing) {
      await this.performAdaptiveSizing();
      optimizations.push('Adjusted tier sizes');
    }

    // 4. Compact fragmented entries
    await this.compactCache();
    optimizations.push('Compacted cache storage');

    const memoryFreed = beforeSize - this.globalStats.totalSize;
    const performanceImprovement = this.globalStats.hitRate - beforeHitRate;

    return {
      optimizations,
      memoryFreed,
      performanceImprovement
    };
  }

  private initializeTiers(): void {
    // L1 - Hot data (most frequently accessed)
    this.tiers.set('L1', {
      name: 'L1',
      maxSize: this.config.l1Size,
      entries: new Map(),
      policy: 'LFU', // Least Frequently Used for hot data
      stats: {
        hits: 0,
        misses: 0,
        evictions: 0,
        totalSize: 0,
        entryCount: 0,
        hitRate: 0,
        averageAccessTime: 0
      }
    });

    // L2 - Warm data (moderately accessed)
    this.tiers.set('L2', {
      name: 'L2', 
      maxSize: this.config.l2Size,
      entries: new Map(),
      policy: 'TLRU', // Time-aware LRU
      stats: {
        hits: 0,
        misses: 0,
        evictions: 0,
        totalSize: 0,
        entryCount: 0,
        hitRate: 0,
        averageAccessTime: 0
      }
    });

    // L3 - Cold data (infrequently accessed)
    this.tiers.set('L3', {
      name: 'L3',
      maxSize: this.config.l3Size,
      entries: new Map(),
      policy: 'LRU', // Simple LRU for cold data
      stats: {
        hits: 0,
        misses: 0,
        evictions: 0,
        totalSize: 0,
        entryCount: 0,
        hitRate: 0,
        averageAccessTime: 0
      }
    });
  }

  private selectInitialTier(key: string, entry: CacheEntry<T>): CacheTier<T> {
    // High priority items go to L1
    if (entry.priority === 'critical' || entry.priority === 'high') {
      return this.tiers.get('L1')!;
    }

    // Check access patterns
    const pattern = this.accessPatterns.get(key);
    if (pattern && pattern.frequency > 10) {
      return this.tiers.get('L1')!; // Frequently accessed
    }

    // Default to L2 for medium priority, L3 for low priority
    if (entry.priority === 'low') {
      return this.tiers.get('L3')!;
    }

    return this.tiers.get('L2')!;
  }

  private async ensureSpace(tier: CacheTier<T>, requiredSize: number): Promise<void> {
    while (tier.stats.totalSize + requiredSize > tier.maxSize) {
      const evicted = await this.evictFromTier(tier);
      if (!evicted) break; // No more entries to evict
    }
  }

  private async evictFromTier(tier: CacheTier<T>): Promise<boolean> {
    if (tier.entries.size === 0) return false;

    let victimKey: string | null = null;
    let victimEntry: CacheEntry<T> | null = null;

    switch (tier.policy) {
      case 'LRU':
        victimEntry = this.findLRUVictim(tier);
        break;
      case 'LFU':
        victimEntry = this.findLFUVictim(tier);
        break;
      case 'TLRU':
        victimEntry = this.findTLRUVictim(tier);
        break;
      case 'ARC':
        victimEntry = this.findARCVictim(tier);
        break;
    }

    if (victimEntry) {
      victimKey = victimEntry.key;

      // Try to demote to lower tier instead of complete eviction
      if (await this.tryDemote(victimKey, victimEntry, tier)) {
        this.removeFromTier(victimKey, tier);
        return true;
      } else {
        // Complete eviction
        this.removeFromTier(victimKey, tier);
        tier.stats.evictions++;
        this.globalStats.evictions++;
        this.emit('cache:evicted', { key: victimKey, tier: tier.name });
        return true;
      }
    }

    return false;
  }

  private async tryDemote(key: string, entry: CacheEntry<T>, currentTier: CacheTier<T>): Promise<boolean> {
    // Try to move to next lower tier
    let targetTier: CacheTier<T> | null = null;

    if (currentTier.name === 'L1') {
      targetTier = this.tiers.get('L2')!;
    } else if (currentTier.name === 'L2') {
      targetTier = this.tiers.get('L3')!;
    }

    if (targetTier && targetTier.stats.totalSize + entry.size <= targetTier.maxSize) {
      // Space available in lower tier
      targetTier.entries.set(key, entry);
      targetTier.stats.entryCount++;
      targetTier.stats.totalSize += entry.size;

      this.emit('cache:demoted', { key, from: currentTier.name, to: targetTier.name });
      return true;
    }

    return false;
  }

  private async considerPromotion(key: string, entry: CacheEntry<T>, currentTier: CacheTier<T>): Promise<void> {
    // Promote frequently accessed items to higher tier
    const shouldPromote = this.shouldPromoteEntry(entry, currentTier);

    if (shouldPromote) {
      let targetTier: CacheTier<T> | null = null;

      if (currentTier.name === 'L3') {
        targetTier = this.tiers.get('L2')!;
      } else if (currentTier.name === 'L2') {
        targetTier = this.tiers.get('L1')!;
      }

      if (targetTier) {
        // Ensure space and promote
        await this.ensureSpace(targetTier, entry.size);

        // Remove from current tier
        this.removeFromTier(key, currentTier);

        // Add to target tier
        targetTier.entries.set(key, entry);
        targetTier.stats.entryCount++;
        targetTier.stats.totalSize += entry.size;

        this.emit('cache:promoted', { key, from: currentTier.name, to: targetTier.name });
      }
    }
  }

  private shouldPromoteEntry(entry: CacheEntry<T>, currentTier: CacheTier<T>): boolean {
    const now = Date.now();
    const age = now - entry.timestamp;
    const timeSinceLastAccess = now - entry.lastAccess;

    // Promote if frequently accessed recently
    if (entry.accessCount > 10 && timeSinceLastAccess < 60000) { // Last minute
      return true;
    }

    // Promote high priority items
    if (entry.priority === 'critical' && currentTier.name !== 'L1') {
      return true;
    }

    // Promote based on access frequency
    const accessRate = entry.accessCount / (age / 1000); // accesses per second

    if (currentTier.name === 'L3' && accessRate > 0.01) { // > 1 access per 100 seconds
      return true;
    }

    if (currentTier.name === 'L2' && accessRate > 0.1) { // > 1 access per 10 seconds
      return true;
    }

    return false;
  }

  private findLRUVictim(tier: CacheTier<T>): CacheEntry<T> | null {
    let oldestEntry: CacheEntry<T> | null = null;

    for (const entry of tier.entries.values()) {
      if (!oldestEntry || entry.lastAccess < oldestEntry.lastAccess) {
        oldestEntry = entry;
      }
    }

    return oldestEntry;
  }

  private findLFUVictim(tier: CacheTier<T>): CacheEntry<T> | null {
    let leastUsedEntry: CacheEntry<T> | null = null;

    for (const entry of tier.entries.values()) {
      if (!leastUsedEntry || entry.accessCount < leastUsedEntry.accessCount) {
        leastUsedEntry = entry;
      }
    }

    return leastUsedEntry;
  }

  private findTLRUVictim(tier: CacheTier<T>): CacheEntry<T> | null {
    // Time-aware LRU - considers both recency and frequency
    let bestScore = -1;
    let victim: CacheEntry<T> | null = null;
    const now = Date.now();

    for (const entry of tier.entries.values()) {
      const recency = now - entry.lastAccess;
      const frequency = entry.accessCount;
      const score = recency / Math.max(frequency, 1); // Higher score = better victim

      if (score > bestScore) {
        bestScore = score;
        victim = entry;
      }
    }

    return victim;
  }

  private findARCVictim(tier: CacheTier<T>): CacheEntry<T> | null {
    // Simplified ARC (Adaptive Replacement Cache) victim selection
    // In practice, ARC is quite complex; this is a simplified version
    return this.findLRUVictim(tier); // Fallback to LRU
  }

  private removeFromTier(key: string, tier: CacheTier<T>): boolean {
    const entry = tier.entries.get(key);
    if (entry) {
      tier.entries.delete(key);
      tier.stats.entryCount--;
      tier.stats.totalSize -= entry.size;
      this.globalStats.entryCount--;
      this.globalStats.totalSize -= entry.size;
      return true;
    }
    return false;
  }

  private trackAccess(key: string, accessTime: number): void {
    const pattern = this.accessPatterns.get(key) || {
      frequency: 0,
      lastAccess: 0,
      accessTimes: []
    };

    pattern.frequency++;
    pattern.lastAccess = Date.now();
    pattern.accessTimes.push(accessTime);

    // Keep only recent access times
    if (pattern.accessTimes.length > 10) {
      pattern.accessTimes = pattern.accessTimes.slice(-5);
    }

    this.accessPatterns.set(key, pattern);
  }

  private updateHitRates(): void {
    const total = this.globalStats.hits + this.globalStats.misses;
    this.globalStats.hitRate = total > 0 ? this.globalStats.hits / total : 0;

    for (const tier of this.tiers.values()) {
      const tierTotal = tier.stats.hits + tier.stats.misses;
      tier.stats.hitRate = tierTotal > 0 ? tier.stats.hits / tierTotal : 0;
    }
  }

  private async performAdaptiveSizing(): Promise<void> {
    const memoryReport = this.memoryManager.getMemoryReport();

    // Shrink caches if under memory pressure
    if (memoryReport.pressure.level === 'high' || memoryReport.pressure.level === 'critical') {
      await this.shrinkCaches(0.8); // Reduce by 20%
    }

    // Expand caches if memory usage is low and hit rates are poor
    else if (memoryReport.pressure.level === 'low' && this.globalStats.hitRate < 0.8) {
      await this.expandCaches(1.2); // Increase by 20%
    }
  }

  private async shrinkCaches(factor: number): Promise<void> {
    for (const tier of this.tiers.values()) {
      const newSize = Math.floor(tier.maxSize * factor);
      await this.resizeTier(tier, newSize);
    }
    this.emit('cache:resized', { action: 'shrink', factor });
  }

  private async expandCaches(factor: number): Promise<void> {
    const newTotalSize = this.config.maxTotalMemory * factor;

    // Don't exceed system memory limits
    const memoryStats = this.memoryManager.getCurrentStats();
    const availableMemory = memoryStats.heapTotal - memoryStats.heapUsed;

    if (newTotalSize > availableMemory * 0.5) {
      return; // Don't use more than 50% of available memory
    }

    for (const tier of this.tiers.values()) {
      const newSize = Math.floor(tier.maxSize * factor);
      tier.maxSize = newSize;
    }

    this.config.maxTotalMemory = newTotalSize;
    this.emit('cache:resized', { action: 'expand', factor });
  }

  private async resizeTier(tier: CacheTier<T>, newSize: number): Promise<void> {
    if (newSize < tier.maxSize) {
      // Shrinking - need to evict entries
      while (tier.stats.totalSize > newSize) {
        const evicted = await this.evictFromTier(tier);
        if (!evicted) break;
      }
    }

    tier.maxSize = newSize;
  }

  private async rebalanceTiers(): Promise<void> {
    // Move entries between tiers based on access patterns
    for (const [key, pattern] of this.accessPatterns.entries()) {
      for (const [tierName, tier] of this.tiers.entries()) {
        const entry = tier.entries.get(key);
        if (entry) {
          const optimalTier = this.getOptimalTierForPattern(pattern);
          if (optimalTier !== tierName) {
            const targetTier = this.tiers.get(optimalTier)!;

            // Move entry if space allows
            if (targetTier.stats.totalSize + entry.size <= targetTier.maxSize) {
              this.removeFromTier(key, tier);
              targetTier.entries.set(key, entry);
              targetTier.stats.entryCount++;
              targetTier.stats.totalSize += entry.size;
              this.globalStats.entryCount++; // This was decremented in removeFromTier
              this.globalStats.totalSize += entry.size; // This was decremented in removeFromTier
            }
          }
          break; // Entry found, no need to check other tiers
        }
      }
    }
  }

  private getOptimalTierForPattern(pattern: { frequency: number; lastAccess: number }): string {
    const now = Date.now();
    const recency = now - pattern.lastAccess;

    // Very recent and frequent access -> L1
    if (pattern.frequency > 20 && recency < 60000) {
      return 'L1';
    }

    // Moderately recent and frequent -> L2
    if (pattern.frequency > 5 && recency < 300000) {
      return 'L2';
    }

    // Everything else -> L3
    return 'L3';
  }

  private async compactCache(): Promise<void> {
    // Remove access patterns for non-existent entries
    const validKeys = new Set<string>();
    for (const tier of this.tiers.values()) {
      for (const key of tier.entries.keys()) {
        validKeys.add(key);
      }
    }

    for (const key of this.accessPatterns.keys()) {
      if (!validKeys.has(key)) {
        this.accessPatterns.delete(key);
      }
    }
  }

  private generateCacheRecommendations(efficiency: any): string[] {
    const recommendations: string[] = [];

    if (efficiency.memoryUtilization > 0.9) {
      recommendations.push('Cache memory utilization is high - consider increasing cache size or reducing TTL');
    }

    if (efficiency.hotDataRatio < 0.1) {
      recommendations.push('Low hot data ratio - review cache promotion strategy');
    }

    if (this.globalStats.hitRate < 0.7) {
      recommendations.push('Cache hit rate is low - consider cache warming or adjusting eviction policies');
    }

    const l1Tier = this.tiers.get('L1')!;
    if (l1Tier.stats.hitRate < 0.8) {
      recommendations.push('L1 cache hit rate is low - review hot data identification');
    }

    return recommendations;
  }

  private setupMemoryPressureHandler(): void {
    this.memoryManager.onMemoryPressure(async (stats, pressure) => {
      if (pressure.level === 'high' || pressure.level === 'critical') {
        // Aggressively clear caches under memory pressure
        const beforeSize = this.globalStats.totalSize;

        // Clear L3 first (cold data)
        const l3Tier = this.tiers.get('L3')!;
        l3Tier.entries.clear();
        l3Tier.stats.totalSize = 0;
        l3Tier.stats.entryCount = 0;

        // Clear half of L2 if still under pressure
        if (pressure.level === 'critical') {
          const l2Tier = this.tiers.get('L2')!;
          const entriesToKeep = Math.floor(l2Tier.entries.size / 2);
          const sortedEntries = Array.from(l2Tier.entries.entries())
            .sort(([, a], [, b]) => b.lastAccess - a.lastAccess);

          l2Tier.entries.clear();
          l2Tier.stats.totalSize = 0;
          l2Tier.stats.entryCount = 0;

          for (let i = 0; i < entriesToKeep; i++) {
            const [key, entry] = sortedEntries[i];
            l2Tier.entries.set(key, entry);
            l2Tier.stats.totalSize += entry.size;
            l2Tier.stats.entryCount++;
          }
        }

        const memoryFreed = beforeSize - this.globalStats.totalSize;
        this.emit('cache:pressure_cleanup', { memoryFreed, pressureLevel: pressure.level });
      }
    });
  }

  private estimateSize(value: T): number {
    // Use enhanced size estimation with object overhead calculation
    try {
      return SizeEstimator.quickEstimate(value);
    } catch (error) {
      console.warn('Size estimation failed in cache, using fallback:', error);
      return 1024; // Conservative fallback
    }
  }
}

/**
 * Memory Manager - Advanced memory monitoring and management
 * 
 * Provides real-time memory monitoring, pressure detection, and
 * automatic cleanup strategies to prevent OOM conditions.
 */

interface MemoryStats {
  rss: number;
  heapTotal: number;
  heapUsed: number;
  external: number;
  arrayBuffers: number;
  timestamp: number;
}

interface MemoryPressureLevel {
  level: 'low' | 'medium' | 'high' | 'critical';
  heapUsagePercent: number;
  rssUsagePercent: number;
  recommendation: string;
}

interface MemoryManagerConfig {
  /** Warning threshold for heap usage (default: 0.7 = 70%) */
  heapWarningThreshold: number;
  /** Critical threshold for heap usage (default: 0.9 = 90%) */
  heapCriticalThreshold: number;
  /** Maximum RSS in bytes (default: 1GB) */
  maxRssBytes: number;
  /** GC suggestion threshold (default: 0.8 = 80%) */
  gcThreshold: number;
  /** Monitoring interval in ms (default: 30000 = 30 seconds) */
  monitoringInterval: number;
}

type MemoryEventHandler = (stats: MemoryStats, pressure: MemoryPressureLevel) => Promise<void>;

export class MemoryManager {
  private config: MemoryManagerConfig;
  private memoryHistory: MemoryStats[] = [];
  private eventHandlers: MemoryEventHandler[] = [];
  private monitoringInterval: NodeJS.Timeout | null = null;
  private isMonitoring = false;
  private lastGCTime = 0;
  private gcCallbacks: (() => Promise<void>)[] = [];

  constructor(config: Partial<MemoryManagerConfig> = {}) {
    this.config = {
      heapWarningThreshold: 0.7,
      heapCriticalThreshold: 0.9,
      maxRssBytes: 1024 * 1024 * 1024, // 1GB
      gcThreshold: 0.8,
      monitoringInterval: 30000, // 30 seconds
      ...config
    };
  }

  /**
   * Start memory monitoring
   */
  startMonitoring(): void {
    if (this.isMonitoring) return;

    this.isMonitoring = true;
    this.monitoringInterval = setInterval(() => {
      this.checkMemoryPressure();
    }, this.config.monitoringInterval);

    console.log('Memory monitoring started');
  }

  /**
   * Stop memory monitoring
   */
  stopMonitoring(): void {
    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }
    this.isMonitoring = false;
    console.log('Memory monitoring stopped');
  }

  /**
   * Get current memory statistics
   */
  getCurrentStats(): MemoryStats {
    const memoryUsage = process.memoryUsage();
    return {
      rss: memoryUsage.rss,
      heapTotal: memoryUsage.heapTotal,
      heapUsed: memoryUsage.heapUsed,
      external: memoryUsage.external,
      arrayBuffers: memoryUsage.arrayBuffers,
      timestamp: Date.now()
    };
  }

  /**
   * Calculate memory pressure level
   */
  getMemoryPressure(stats?: MemoryStats): MemoryPressureLevel {
    const currentStats = stats || this.getCurrentStats();

    const heapUsagePercent = currentStats.heapUsed / currentStats.heapTotal;
    const rssUsagePercent = currentStats.rss / this.config.maxRssBytes;

    let level: 'low' | 'medium' | 'high' | 'critical' = 'low';
    let recommendation = 'Memory usage is normal';

    if (heapUsagePercent >= this.config.heapCriticalThreshold || rssUsagePercent >= 0.95) {
      level = 'critical';
      recommendation = 'Critical memory pressure - immediate cleanup required';
    } else if (heapUsagePercent >= this.config.heapWarningThreshold || rssUsagePercent >= 0.8) {
      level = 'high';
      recommendation = 'High memory pressure - consider cleanup';
    } else if (heapUsagePercent >= 0.5 || rssUsagePercent >= 0.6) {
      level = 'medium';
      recommendation = 'Moderate memory usage - monitor closely';
    }

    return {
      level,
      heapUsagePercent,
      rssUsagePercent,
      recommendation
    };
  }

  /**
   * Register cleanup callback for GC events
   */
  onMemoryPressure(handler: MemoryEventHandler): void {
    this.eventHandlers.push(handler);
  }

  /**
   * Register cleanup callback for manual GC
   */
  onGarbageCollection(callback: () => Promise<void>): void {
    this.gcCallbacks.push(callback);
  }

  /**
   * Force garbage collection with cleanup callbacks
   */
  async forceGarbageCollection(): Promise<void> {
    const startTime = Date.now();
    const beforeStats = this.getCurrentStats();

    // Run cleanup callbacks first
    for (const callback of this.gcCallbacks) {
      try {
        await callback();
      } catch (error) {
        console.error('Error in GC cleanup callback:', error);
      }
    }

    // Force GC if available
    if (global.gc) {
      global.gc();
    }

    const afterStats = this.getCurrentStats();
    const duration = Date.now() - startTime;
    const memoryFreed = beforeStats.heapUsed - afterStats.heapUsed;

    this.lastGCTime = Date.now();

    console.log(`GC completed in ${duration}ms, freed ${this.formatBytes(memoryFreed)}`);
  }

  /**
   * Get memory usage trends
   */
  getMemoryTrends(): {
    averageHeapUsage: number;
    peakHeapUsage: number;
    averageRss: number;
    peakRss: number;
    growthRate: number; // bytes per minute
    recommendations: string[];
  } {
    if (this.memoryHistory.length < 2) {
      return {
        averageHeapUsage: 0,
        peakHeapUsage: 0,
        averageRss: 0,
        peakRss: 0,
        growthRate: 0,
        recommendations: ['Insufficient data for trend analysis']
      };
    }

    const recent = this.memoryHistory.slice(-20); // Last 20 readings
    const averageHeapUsage = recent.reduce((sum, stat) => sum + stat.heapUsed, 0) / recent.length;
    const peakHeapUsage = Math.max(...recent.map(stat => stat.heapUsed));
    const averageRss = recent.reduce((sum, stat) => sum + stat.rss, 0) / recent.length;
    const peakRss = Math.max(...recent.map(stat => stat.rss));

    // Calculate growth rate (bytes per minute)
    const timespan = recent[recent.length - 1].timestamp - recent[0].timestamp;
    const heapGrowth = recent[recent.length - 1].heapUsed - recent[0].heapUsed;
    const growthRate = timespan > 0 ? (heapGrowth / timespan) * 60000 : 0; // per minute

    const recommendations: string[] = [];

    if (growthRate > 1024 * 1024) { // Growing by more than 1MB per minute
      recommendations.push('Memory usage is growing rapidly - check for memory leaks');
    }

    if (peakHeapUsage > averageHeapUsage * 1.5) {
      recommendations.push('High memory spikes detected - consider more frequent cleanup');
    }

    if (averageHeapUsage > this.config.maxRssBytes * 0.6) {
      recommendations.push('Consider increasing memory limits or optimizing data structures');
    }

    return {
      averageHeapUsage,
      peakHeapUsage,
      averageRss,
      peakRss,
      growthRate,
      recommendations
    };
  }

  /**
   * Get memory report for monitoring
   */
  getMemoryReport() {
    const current = this.getCurrentStats();
    const pressure = this.getMemoryPressure(current);
    const trends = this.getMemoryTrends();

    return {
      current,
      pressure,
      trends,
      formattedStats: {
        rss: this.formatBytes(current.rss),
        heapUsed: this.formatBytes(current.heapUsed),
        heapTotal: this.formatBytes(current.heapTotal),
        external: this.formatBytes(current.external)
      }
    };
  }

  /**
   * Create memory-efficient object pool
   */
  createObjectPool<T>(
    factory: () => T,
    reset: (obj: T) => void,
    maxSize: number = 100
  ): {
    acquire: () => T;
    release: (obj: T) => void;
    size: () => number;
    clear: () => void;
  } {
    const pool: T[] = [];
    let created = 0;

    return {
      acquire: (): T => {
        if (pool.length > 0) {
          return pool.pop()!;
        }
        created++;
        return factory();
      },

      release: (obj: T): void => {
        if (pool.length < maxSize) {
          reset(obj);
          pool.push(obj);
        }
        // If pool is full, let the object be garbage collected
      },

      size: (): number => pool.length,

      clear: (): void => {
        pool.length = 0;
      }
    };
  }

  /**
   * Create weak reference cache
   */
  createWeakCache<K, V extends object>(): {
    set: (key: K, value: V) => void;
    get: (key: K) => V | undefined;
    delete: (key: K) => boolean;
    clear: () => void;
    size: () => number;
  } {
    const cache = new Map<K, WeakRef<V>>();
    const cleanupRegistry = new FinalizationRegistry((key: K) => {
      cache.delete(key);
    });

    return {
      set: (key: K, value: V): void => {
        const existingRef = cache.get(key);
        if (existingRef) {
          const existing = existingRef.deref();
          if (existing) {
            // Update existing value
            cleanupRegistry.unregister(existing);
          }
        }

        const weakRef = new WeakRef(value);
        cache.set(key, weakRef);
        cleanupRegistry.register(value, key);
      },

      get: (key: K): V | undefined => {
        const weakRef = cache.get(key);
        if (!weakRef) return undefined;

        const value = weakRef.deref();
        if (!value) {
          cache.delete(key);
          return undefined;
        }

        return value;
      },

      delete: (key: K): boolean => {
        const weakRef = cache.get(key);
        if (weakRef) {
          const value = weakRef.deref();
          if (value) {
            cleanupRegistry.unregister(value);
          }
        }
        return cache.delete(key);
      },

      clear: (): void => {
        cache.clear();
      },

      size: (): number => {
        // Clean up dead references first
        const keysToDelete: K[] = [];
        for (const [key, weakRef] of cache.entries()) {
          if (!weakRef.deref()) {
            keysToDelete.push(key);
          }
        }
        keysToDelete.forEach(key => cache.delete(key));

        return cache.size;
      }
    };
  }

  private async checkMemoryPressure(): Promise<void> {
    const stats = this.getCurrentStats();
    const pressure = this.getMemoryPressure(stats);

    // Store history (keep last 100 readings)
    this.memoryHistory.push(stats);
    if (this.memoryHistory.length > 100) {
      this.memoryHistory = this.memoryHistory.slice(-50);
    }

    // Trigger cleanup if needed
    if (pressure.level === 'critical' || 
        (pressure.level === 'high' && Date.now() - this.lastGCTime > 60000)) {

      console.warn(`Memory pressure detected: ${pressure.level} - ${pressure.recommendation}`);

      // Notify event handlers
      for (const handler of this.eventHandlers) {
        try {
          await handler(stats, pressure);
        } catch (error) {
          console.error('Error in memory pressure handler:', error);
        }
      }

      // Suggest GC for high pressure
      if (pressure.level === 'high' || pressure.level === 'critical') {
        await this.forceGarbageCollection();
      }
    }
  }

  private formatBytes(bytes: number): string {
    const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
    if (bytes === 0) return '0 B';

    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    const size = bytes / Math.pow(1024, i);

    return `${size.toFixed(2)} ${sizes[i]}`;
  }
}

/**
 * Performance Monitoring System - Production-grade monitoring and alerting
 * 
 * Provides comprehensive performance tracking, alerting, and health checking
 * for all system components in production environments.
 */

import { EventEmitter } from 'events';
import { DatabaseManager } from '../storage/Database.js';
import { MemoryManager } from './MemoryManager.js';
import { DynamicThresholdManager } from '../monitoring/DynamicThresholdManager.js';

interface PerformanceMetric {
  id: string;
  category: 'database' | 'search' | 'embedding' | 'memory' | 'network' | 'system';
  name: string;
  value: number;
  unit: 'ms' | 'bytes' | 'count' | 'percent' | 'rate';
  timestamp: number;
  tags?: Record<string, string>;
}

interface AlertRule {
  id: string;
  category: string;
  metric: string;
  operator: '>' | '<' | '=' | '>=' | '<=';
  threshold: number;
  duration: number; // ms - how long condition must persist
  severity: 'low' | 'medium' | 'high' | 'critical';
  enabled: boolean;
  description: string;
}

interface Alert {
  id: string;
  ruleId: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  message: string;
  metric: PerformanceMetric;
  triggeredAt: number;
  resolvedAt?: number;
  acknowledged?: boolean;
}

interface HealthCheck {
  name: string;
  status: 'healthy' | 'degraded' | 'unhealthy';
  message: string;
  lastCheck: number;
  duration: number;
  details?: Record<string, any>;
}

interface PerformanceReport {
  timestamp: number;
  duration: number; // reporting period in ms
  summary: {
    totalRequests: number;
    averageResponseTime: number;
    errorRate: number;
    memoryUsage: number;
    activeAlerts: number;
  };
  metrics: {
    database: Record<string, number>;
    search: Record<string, number>;
    embedding: Record<string, number>;
    memory: Record<string, number>;
    system: Record<string, number>;
  };
  healthChecks: HealthCheck[];
  alerts: Alert[];
  recommendations: string[];
}

export class PerformanceMonitor extends EventEmitter {
  private dbManager: DatabaseManager;
  private memoryManager: MemoryManager;
  private dynamicThresholds: DynamicThresholdManager | null = null;
  private metrics: Map<string, PerformanceMetric[]> = new Map();
  private alertRules: Map<string, AlertRule> = new Map();
  private activeAlerts: Map<string, Alert> = new Map();
  private alertHistory: Alert[] = [];
  private healthChecks: Map<string, HealthCheck> = new Map();

  private monitoringInterval: NodeJS.Timeout | null = null;
  private metricsRetentionMs: number;
  private alertCooldownMs: number;
  private isMonitoring = false;
  private useDynamicThresholds = false;

  // Fallback static thresholds (used when dynamic thresholds are not available)
  private readonly FALLBACK_THRESHOLDS = {
    DATABASE_QUERY_TIME: 500,    // ms
    SEARCH_RESPONSE_TIME: 1000,  // ms
    EMBEDDING_TIME: 200,         // ms
    MEMORY_USAGE: 0.8,           // 80% of available memory
    ERROR_RATE: 0.05,            // 5% error rate
    CACHE_HIT_RATE: 0.7          // 70% cache hit rate
  };

  constructor(
    dbManager: DatabaseManager,
    memoryManager: MemoryManager,
    options: {
      metricsRetentionHours?: number;
      alertCooldownMinutes?: number;
      monitoringIntervalSeconds?: number;
      enableDynamicThresholds?: boolean;
    } = {}
  ) {
    super();

    this.dbManager = dbManager;
    this.memoryManager = memoryManager;
    this.metricsRetentionMs = (options.metricsRetentionHours || 24) * 60 * 60 * 1000;
    this.alertCooldownMs = (options.alertCooldownMinutes || 5) * 60 * 1000;
    this.useDynamicThresholds = options.enableDynamicThresholds || false;

    if (this.useDynamicThresholds) {
      this.dynamicThresholds = new DynamicThresholdManager();
    }

    this.setupDefaultAlertRules();
  }

  /**
   * Initialize dynamic thresholds if enabled
   */
  async initializeDynamicThresholds(): Promise<void> {
    if (this.dynamicThresholds && this.useDynamicThresholds) {
      await this.dynamicThresholds.initialize();
      console.log(' Dynamic thresholds initialized');
    }
  }

  /**
   * Start performance monitoring
   */
  async startMonitoring(intervalSeconds: number = 30): Promise<void> {
    if (this.isMonitoring) return;

    // Initialize dynamic thresholds if enabled
    if (this.useDynamicThresholds && this.dynamicThresholds) {
      await this.initializeDynamicThresholds();
    }

    this.isMonitoring = true;
    this.monitoringInterval = setInterval(() => {
      this.collectSystemMetrics();
      this.runHealthChecks();
      this.evaluateAlerts();
      this.cleanupOldMetrics();
    }, intervalSeconds * 1000);

    const thresholdType = this.useDynamicThresholds ? 'dynamic' : 'static';
    console.log(`Performance monitoring started (interval: ${intervalSeconds}s, thresholds: ${thresholdType})`);
    this.emit('monitoring:started');
  }

  /**
   * Stop performance monitoring
   */
  async stopMonitoring(): Promise<void> {
    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }
    this.isMonitoring = false;

    // Cleanup dynamic thresholds
    if (this.dynamicThresholds && this.useDynamicThresholds) {
      await this.dynamicThresholds.shutdown();
    }

    console.log('Performance monitoring stopped');
    this.emit('monitoring:stopped');
  }

  /**
   * Get threshold for a metric (adaptive or fallback)
   */
  private getThreshold(category: string, metric: string): number | null {
    if (this.dynamicThresholds && this.useDynamicThresholds) {
      const dynamicThreshold = this.dynamicThresholds.getThreshold(`${category}_${metric}`);
      if (dynamicThreshold !== null) {
        return dynamicThreshold;
      }
    }

    // Fallback to static thresholds
    const fallbackKey = `${category.toUpperCase()}_${metric.toUpperCase()}`;
    return this.FALLBACK_THRESHOLDS[fallbackKey as keyof typeof this.FALLBACK_THRESHOLDS] || null;
  }

  /**
   * Record a performance metric
   */
  recordMetric(metric: Omit<PerformanceMetric, 'id' | 'timestamp'>): void {
    const fullMetric: PerformanceMetric = {
      id: `${metric.category}_${metric.name}_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      timestamp: Date.now(),
      ...metric
    };

    const key = `${metric.category}:${metric.name}`;
    if (!this.metrics.has(key)) {
      this.metrics.set(key, []);
    }

    this.metrics.get(key)!.push(fullMetric);

    // Update dynamic threshold baseline if enabled
    if (this.dynamicThresholds && this.useDynamicThresholds) {
      this.dynamicThresholds.updateBaseline(metric.name, metric.category, metric.value);
    }

    this.emit('metric:recorded', fullMetric);
  }

  /**
   * Record database operation metrics
   */
  recordDatabaseMetric(operation: string, duration: number, resultCount: number = 0): void {
    this.recordMetric({
      category: 'database',
      name: operation,
      value: duration,
      unit: 'ms',
      tags: { resultCount: resultCount.toString() }
    });

    // Record slow query alert using dynamic threshold
    const threshold = this.getThreshold('database', 'query_time') || this.FALLBACK_THRESHOLDS.DATABASE_QUERY_TIME;
    if (duration > threshold) {
      this.recordMetric({
        category: 'database',
        name: 'slow_query',
        value: 1,
        unit: 'count',
        tags: { operation, duration: duration.toString(), threshold: threshold.toString() }
      });
    }
  }

  /**
   * Record search operation metrics
   */
  recordSearchMetric(searchType: string, duration: number, resultCount: number, cacheHit: boolean = false): void {
    this.recordMetric({
      category: 'search',
      name: `${searchType}_duration`,
      value: duration,
      unit: 'ms',
      tags: { resultCount: resultCount.toString(), cacheHit: cacheHit.toString() }
    });

    this.recordMetric({
      category: 'search',
      name: `${searchType}_results`,
      value: resultCount,
      unit: 'count'
    });
  }

  /**
   * Record embedding operation metrics
   */
  recordEmbeddingMetric(operation: string, duration: number, batchSize: number = 1): void {
    const avgDuration = duration / batchSize;

    this.recordMetric({
      category: 'embedding',
      name: `${operation}_duration`,
      value: avgDuration,
      unit: 'ms',
      tags: { batchSize: batchSize.toString() }
    });

    this.recordMetric({
      category: 'embedding',
      name: `${operation}_throughput`,
      value: (batchSize / duration) * 1000, // operations per second
      unit: 'rate'
    });
  }

  /**
   * Add custom alert rule
   */
  addAlertRule(rule: AlertRule): void {
    this.alertRules.set(rule.id, rule);
    console.log(`Alert rule added: ${rule.id} - ${rule.description}`);
  }

  /**
   * Get current performance report
   */
  getPerformanceReport(durationMs: number = 300000): PerformanceReport { // Default: last 5 minutes
    const now = Date.now();
    const startTime = now - durationMs;

    // Collect metrics from the specified time range
    const periodMetrics = this.getMetricsInRange(startTime, now);

    // Calculate summary statistics
    const totalRequests = this.sumMetricValues(periodMetrics, 'database', 'query') +
                         this.sumMetricValues(periodMetrics, 'search') +
                         this.sumMetricValues(periodMetrics, 'embedding');

    const responseTimeMetrics = this.getMetricsByCategory(periodMetrics, 'database')
      .concat(this.getMetricsByCategory(periodMetrics, 'search'));

    const averageResponseTime = responseTimeMetrics.length > 0
      ? responseTimeMetrics.reduce((sum, m) => sum + m.value, 0) / responseTimeMetrics.length
      : 0;

    const errorMetrics = this.getMetricsByName(periodMetrics, 'error');
    const errorRate = errorMetrics.length > 0 && totalRequests > 0
      ? errorMetrics.length / totalRequests
      : 0;

    const memoryStats = this.memoryManager.getCurrentStats();
    const memoryUsage = memoryStats.heapUsed / memoryStats.heapTotal;

    return {
      timestamp: now,
      duration: durationMs,
      summary: {
        totalRequests,
        averageResponseTime,
        errorRate,
        memoryUsage,
        activeAlerts: this.activeAlerts.size
      },
      metrics: {
        database: this.aggregateMetrics(periodMetrics, 'database'),
        search: this.aggregateMetrics(periodMetrics, 'search'),
        embedding: this.aggregateMetrics(periodMetrics, 'embedding'),
        memory: this.aggregateMetrics(periodMetrics, 'memory'),
        system: this.aggregateMetrics(periodMetrics, 'system')
      },
      healthChecks: Array.from(this.healthChecks.values()),
      alerts: Array.from(this.activeAlerts.values()),
      recommendations: this.generateRecommendations(periodMetrics)
    };
  }

  /**
   * Get health status of all monitored components
   */
  getHealthStatus(): {
    overall: 'healthy' | 'degraded' | 'unhealthy';
    components: HealthCheck[];
    activeAlerts: Alert[];
    uptime: number;
  } {
    const components = Array.from(this.healthChecks.values());
    const activeAlerts = Array.from(this.activeAlerts.values());

    let overall: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';

    if (components.some(c => c.status === 'unhealthy') || 
        activeAlerts.some(a => a.severity === 'critical')) {
      overall = 'unhealthy';
    } else if (components.some(c => c.status === 'degraded') ||
               activeAlerts.some(a => a.severity === 'high')) {
      overall = 'degraded';
    }

    return {
      overall,
      components,
      activeAlerts,
      uptime: process.uptime() * 1000
    };
  }

  /**
   * Force health check run
   */
  async runHealthChecks(): Promise<void> {
    // Database health check
    await this.runDatabaseHealthCheck();

    // Memory health check
    this.runMemoryHealthCheck();

    // Search system health check
    await this.runSearchHealthCheck();

    // System health check
    this.runSystemHealthCheck();
  }

  private setupDefaultAlertRules(): void {
    const defaultRules: AlertRule[] = [
      {
        id: 'high_database_latency',
        category: 'database',
        metric: 'query_duration',
        operator: '>',
        threshold: this.getThreshold('database', 'query_time') || this.FALLBACK_THRESHOLDS.DATABASE_QUERY_TIME,
        duration: 60000, // 1 minute
        severity: 'high',
        enabled: true,
        description: 'Database queries taking too long'
      },
      {
        id: 'high_search_latency',
        category: 'search',
        metric: 'search_duration',
        operator: '>',
        threshold: this.getThreshold('search', 'response_time') || this.FALLBACK_THRESHOLDS.SEARCH_RESPONSE_TIME,
        duration: 30000, // 30 seconds
        severity: 'medium',
        enabled: true,
        description: 'Search operations taking too long'
      },
      {
        id: 'high_memory_usage',
        category: 'memory',
        metric: 'heap_usage_percent',
        operator: '>',
        threshold: this.getThreshold('memory', 'usage') || this.FALLBACK_THRESHOLDS.MEMORY_USAGE,
        duration: 120000, // 2 minutes
        severity: 'critical',
        enabled: true,
        description: 'Memory usage critically high'
      },
      {
        id: 'high_error_rate',
        category: 'system',
        metric: 'error_rate',
        operator: '>',
        threshold: this.getThreshold('system', 'error_rate') || this.FALLBACK_THRESHOLDS.ERROR_RATE,
        duration: 60000, // 1 minute
        severity: 'high',
        enabled: true,
        description: 'Error rate exceeding acceptable threshold'
      }
    ];

    defaultRules.forEach(rule => this.addAlertRule(rule));
  }

  /**
   * Update alert rule thresholds (used when dynamic thresholds change)
   */
  updateAlertThresholds(): void {
    for (const [ruleId, rule] of this.alertRules.entries()) {
      const newThreshold = this.getThreshold(rule.category, rule.metric);
      if (newThreshold !== null && newThreshold !== rule.threshold) {
        console.log(` Updated alert threshold for ${ruleId}: ${rule.threshold}  ${newThreshold}`);
        rule.threshold = newThreshold;
      }
    }
  }

  private async collectSystemMetrics(): Promise<void> {
    // Memory metrics
    const memoryStats = this.memoryManager.getCurrentStats();
    this.recordMetric({
      category: 'memory',
      name: 'heap_used',
      value: memoryStats.heapUsed,
      unit: 'bytes'
    });

    this.recordMetric({
      category: 'memory',
      name: 'heap_usage_percent',
      value: memoryStats.heapUsed / memoryStats.heapTotal,
      unit: 'percent'
    });

    this.recordMetric({
      category: 'memory',
      name: 'rss',
      value: memoryStats.rss,
      unit: 'bytes'
    });

    // System metrics
    this.recordMetric({
      category: 'system',
      name: 'uptime',
      value: process.uptime() * 1000,
      unit: 'ms'
    });

    this.recordMetric({
      category: 'system',
      name: 'cpu_usage',
      value: process.cpuUsage().user + process.cpuUsage().system,
      unit: 'ms'
    });
  }

  private async runDatabaseHealthCheck(): Promise<void> {
    const startTime = Date.now();
    let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';
    let message = 'Database operating normally';
    const details: Record<string, any> = {};

    try {
      const db = this.dbManager.getConnection();

      // Test basic connectivity
      const testResult = db.prepare('SELECT 1 as test').get();
      if (!testResult || (testResult as any).test !== 1) {
        throw new Error('Database connectivity test failed');
      }

      // Check WAL mode
      const walMode = db.pragma('journal_mode', { simple: true });
      details.journalMode = walMode;

      // Check cache hit rate (approximate)
      const cacheInfo = db.pragma('cache_size', { simple: true });
      details.cacheSize = cacheInfo;

      // Test write capability
      const writeStart = Date.now();
      db.prepare(`
        INSERT INTO persistence_state (key, value, updated_at) 
        VALUES ('health_check', ?, ?) 
        ON CONFLICT(key) DO UPDATE SET 
          value = excluded.value, 
          updated_at = excluded.updated_at
      `).run(Date.now().toString(), Date.now());

      const writeDuration = Date.now() - writeStart;
      details.writeLatency = writeDuration;

      if (writeDuration > 100) {
        status = 'degraded';
        message = `Database write latency high: ${writeDuration}ms`;
      }

    } catch (error) {
      status = 'unhealthy';
      message = `Database health check failed: ${error instanceof Error ? error.message : 'Unknown error'}`;
      details.error = error instanceof Error ? error.message : 'Unknown error';
    }

    this.healthChecks.set('database', {
      name: 'Database',
      status,
      message,
      lastCheck: Date.now(),
      duration: Date.now() - startTime,
      details
    });
  }

  private runMemoryHealthCheck(): void {
    const memoryReport = this.memoryManager.getMemoryReport();
    const pressure = memoryReport.pressure;

    let status: 'healthy' | 'degraded' | 'unhealthy';
    let message: string;

    switch (pressure.level) {
      case 'low':
      case 'medium':
        status = 'healthy';
        message = `Memory usage normal (${(pressure.heapUsagePercent * 100).toFixed(1)}% heap)`;
        break;
      case 'high':
        status = 'degraded';
        message = `High memory pressure (${(pressure.heapUsagePercent * 100).toFixed(1)}% heap)`;
        break;
      case 'critical':
        status = 'unhealthy';
        message = `Critical memory pressure (${(pressure.heapUsagePercent * 100).toFixed(1)}% heap)`;
        break;
    }

    this.healthChecks.set('memory', {
      name: 'Memory',
      status,
      message,
      lastCheck: Date.now(),
      duration: 0,
      details: {
        heapUsagePercent: pressure.heapUsagePercent,
        rssUsagePercent: pressure.rssUsagePercent,
        recommendation: pressure.recommendation
      }
    });
  }

  private async runSearchHealthCheck(): Promise<void> {
    const startTime = Date.now();
    let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';
    let message = 'Search system operating normally';
    const details: Record<string, any> = {};

    try {
      const db = this.dbManager.getConnection();

      // Test FTS functionality
      const ftsTest = db.prepare(`
        SELECT COUNT(*) as count FROM messages_fts WHERE messages_fts MATCH 'test' LIMIT 1
      `).get() as { count: number };

      details.ftsEnabled = ftsTest !== undefined;

      // Check index integrity
      const indexCheck = db.prepare(`
        SELECT COUNT(*) as messages_count FROM messages
      `).get() as { count: number };

      const ftsCheck = db.prepare(`
        SELECT COUNT(*) as fts_count FROM messages_fts
      `).get() as { count: number };

      details.messageCount = indexCheck.count;
      details.ftsCount = ftsCheck.count;

      const indexSyncRatio = ftsCheck.count / Math.max(indexCheck.count, 1);
      if (indexSyncRatio < 0.95) {
        status = 'degraded';
        message = `FTS index out of sync: ${(indexSyncRatio * 100).toFixed(1)}% indexed`;
      }

    } catch (error) {
      status = 'unhealthy';
      message = `Search health check failed: ${error instanceof Error ? error.message : 'Unknown error'}`;
      details.error = error instanceof Error ? error.message : 'Unknown error';
    }

    this.healthChecks.set('search', {
      name: 'Search',
      status,
      message,
      lastCheck: Date.now(),
      duration: Date.now() - startTime,
      details
    });
  }

  private runSystemHealthCheck(): void {
    const uptime = process.uptime() * 1000;
    const memoryUsage = process.memoryUsage();

    let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';
    let message = 'System operating normally';

    // Check for rapid memory growth or other system issues
    if (memoryUsage.heapUsed > 200 * 1024 * 1024) { // 200MB
      status = 'degraded';
      message = `High memory usage: ${Math.round(memoryUsage.heapUsed / 1024 / 1024)}MB`;
    }

    this.healthChecks.set('system', {
      name: 'System',
      status,
      message,
      lastCheck: Date.now(),
      duration: 0,
      details: {
        uptime,
        memoryUsage,
        nodeVersion: process.version,
        platform: process.platform
      }
    });
  }

  private evaluateAlerts(): void {
    const now = Date.now();

    for (const [ruleId, rule] of this.alertRules.entries()) {
      if (!rule.enabled) continue;

      const key = `${rule.category}:${rule.metric}`;
      const metrics = this.metrics.get(key) || [];

      // Get metrics within the alert duration window
      const recentMetrics = metrics.filter(m => now - m.timestamp <= rule.duration);

      if (recentMetrics.length === 0) continue;

      // Check if all recent metrics violate the threshold
      let shouldAlert = false;

      switch (rule.operator) {
        case '>':
          shouldAlert = recentMetrics.every(m => m.value > rule.threshold);
          break;
        case '<':
          shouldAlert = recentMetrics.every(m => m.value < rule.threshold);
          break;
        case '>=':
          shouldAlert = recentMetrics.every(m => m.value >= rule.threshold);
          break;
        case '<=':
          shouldAlert = recentMetrics.every(m => m.value <= rule.threshold);
          break;
        case '=':
          shouldAlert = recentMetrics.every(m => m.value === rule.threshold);
          break;
      }

      const existingAlert = this.activeAlerts.get(ruleId);

      if (shouldAlert && !existingAlert) {
        // Trigger new alert
        const latestMetric = recentMetrics[recentMetrics.length - 1];
        const alert: Alert = {
          id: `alert_${ruleId}_${now}`,
          ruleId,
          severity: rule.severity,
          message: `${rule.description}: ${latestMetric.value} ${latestMetric.unit} ${rule.operator} ${rule.threshold} ${latestMetric.unit}`,
          metric: latestMetric,
          triggeredAt: now
        };

        this.activeAlerts.set(ruleId, alert);
        this.alertHistory.push(alert);
        this.emit('alert:triggered', alert);

        console.warn(`ALERT [${alert.severity.toUpperCase()}]: ${alert.message}`);

      } else if (!shouldAlert && existingAlert && !existingAlert.resolvedAt) {
        // Resolve existing alert
        existingAlert.resolvedAt = now;
        this.activeAlerts.delete(ruleId);
        this.emit('alert:resolved', existingAlert);

        console.log(`ALERT RESOLVED: ${existingAlert.message}`);
      }
    }
  }

  private cleanupOldMetrics(): void {
    const cutoff = Date.now() - this.metricsRetentionMs;

    for (const [key, metrics] of this.metrics.entries()) {
      const filteredMetrics = metrics.filter(m => m.timestamp > cutoff);
      this.metrics.set(key, filteredMetrics);
    }

    // Clean up old alerts
    this.alertHistory = this.alertHistory.filter(a => 
      a.triggeredAt > cutoff || (a.resolvedAt && a.resolvedAt > cutoff)
    );
  }

  private getMetricsInRange(startTime: number, endTime: number): PerformanceMetric[] {
    const result: PerformanceMetric[] = [];

    for (const metrics of this.metrics.values()) {
      result.push(...metrics.filter(m => 
        m.timestamp >= startTime && m.timestamp <= endTime
      ));
    }

    return result;
  }

  private getMetricsByCategory(metrics: PerformanceMetric[], category: string): PerformanceMetric[] {
    return metrics.filter(m => m.category === category);
  }

  private getMetricsByName(metrics: PerformanceMetric[], name: string): PerformanceMetric[] {
    return metrics.filter(m => m.name === name);
  }

  private sumMetricValues(metrics: PerformanceMetric[], category: string, nameFilter?: string): number {
    return metrics
      .filter(m => m.category === category && (!nameFilter || m.name.includes(nameFilter)))
      .length; // Count of metrics, not sum of values
  }

  private aggregateMetrics(metrics: PerformanceMetric[], category: string): Record<string, number> {
    const categoryMetrics = this.getMetricsByCategory(metrics, category);
    const result: Record<string, number> = {};

    // Group by metric name and calculate averages
    const groups = new Map<string, number[]>();

    for (const metric of categoryMetrics) {
      if (!groups.has(metric.name)) {
        groups.set(metric.name, []);
      }
      groups.get(metric.name)!.push(metric.value);
    }

    for (const [name, values] of groups.entries()) {
      if (values.length > 0) {
        result[`${name}_avg`] = values.reduce((sum, v) => sum + v, 0) / values.length;
        result[`${name}_max`] = Math.max(...values);
        result[`${name}_min`] = Math.min(...values);
        result[`${name}_count`] = values.length;
      }
    }

    return result;
  }

  private generateRecommendations(metrics: PerformanceMetric[]): string[] {
    const recommendations: string[] = [];

    // Analyze database performance
    const dbMetrics = this.getMetricsByCategory(metrics, 'database');
    const slowQueries = dbMetrics.filter(m => m.value > this.FALLBACK_THRESHOLDS.DATABASE_QUERY_TIME);

    if (slowQueries.length > dbMetrics.length * 0.2) {
      recommendations.push('Consider optimizing database queries - 20% of queries are slow');
    }

    // Analyze memory usage
    const memoryMetrics = this.getMetricsByCategory(metrics, 'memory');
    const highMemoryUsage = memoryMetrics.filter(m => 
      m.name === 'heap_usage_percent' && m.value > 0.7
    );

    if (highMemoryUsage.length > 0) {
      recommendations.push('Memory usage is high - consider increasing memory or optimizing caches');
    }

    // Analyze search performance
    const searchMetrics = this.getMetricsByCategory(metrics, 'search');
    const slowSearches = searchMetrics.filter(m => 
      m.name.includes('duration') && m.value > this.FALLBACK_THRESHOLDS.SEARCH_RESPONSE_TIME
    );

    if (slowSearches.length > 0) {
      recommendations.push('Search operations are slow - consider query optimization or caching');
    }

    return recommendations;
  }

  /**
   * Get enhanced performance report with dynamic threshold information
   */
  getEnhancedPerformanceReport(durationMs: number = 300000): PerformanceReport & {
    thresholdInfo?: {
      isDynamic: boolean;
      thresholdAccuracy?: number;
      recentAdjustments?: Array<{
        metric: string;
        oldValue: number;
        newValue: number;
        timestamp: number;
        reason: string;
      }>;
    };
  } {
    const baseReport = this.getPerformanceReport(durationMs);

    let thresholdInfo;
    if (this.dynamicThresholds && this.useDynamicThresholds) {
      const dynamicReport = this.dynamicThresholds.getThresholdReport();

      // Get recent threshold adjustments
      const recentAdjustments = [];
      const oneDayAgo = Date.now() - 24 * 60 * 60 * 1000;

      for (const threshold of dynamicReport.currentThresholds) {
        const recentAdjustment = threshold.adjustmentHistory
          .filter(adj => adj.timestamp > oneDayAgo)
          .slice(-1)[0];

        if (recentAdjustment) {
          recentAdjustments.push({
            metric: `${threshold.category}_${threshold.metric}`,
            oldValue: recentAdjustment.oldValue,
            newValue: recentAdjustment.newValue,
            timestamp: recentAdjustment.timestamp,
            reason: recentAdjustment.reason
          });
        }
      }

      thresholdInfo = {
        isDynamic: true,
        thresholdAccuracy: dynamicReport.confidence,
        recentAdjustments
      };

      // Add dynamic threshold recommendations
      baseReport.recommendations.push(...dynamicReport.recommendations);
    } else {
      thresholdInfo = {
        isDynamic: false
      };
    }

    return {
      ...baseReport,
      thresholdInfo
    };
  }

  /**
   * Get dynamic threshold manager (if enabled)
   */
  getDynamicThresholdManager(): DynamicThresholdManager | null {
    return this.dynamicThresholds;
  }

  /**
   * Check if dynamic thresholds are enabled and initialized
   */
  isDynamicThresholdingEnabled(): boolean {
    return this.useDynamicThresholds && this.dynamicThresholds !== null;
  }
}

/**
 * Performance Orchestrator - Coordinates all performance monitoring systems
 * 
 * This class provides:
 * - Centralized performance monitoring coordination
 * - Integration between PerformanceMonitor, MemoryManager, and database systems
 * - Intelligent alerting and automatic optimization
 * - Performance degradation detection and recovery
 * - System health management and reporting
 */

import { EventEmitter } from 'events';
import { PerformanceMonitor } from './PerformanceMonitor.js';
import { MemoryManager } from './MemoryManager.js';
import { DatabaseManager } from '../storage/Database.js';
import { SearchEngine } from '../search/SearchEngine.js';

export interface PerformanceConfig {
  /** Enable comprehensive monitoring */
  enableMonitoring: boolean;
  /** Performance monitoring interval in seconds */
  monitoringInterval: number;
  /** Memory monitoring interval in seconds */
  memoryMonitoringInterval: number;
  /** Enable automatic optimization */
  enableAutoOptimization: boolean;
  /** Performance degradation threshold (0-1) */
  degradationThreshold: number;
  /** Enable performance alerting */
  enableAlerting: boolean;
  /** Maximum acceptable response time in ms */
  maxResponseTime: number;
  /** Memory pressure threshold (0-1) */
  memoryPressureThreshold: number;
}

export interface SystemPerformanceReport {
  timestamp: number;
  overall: {
    status: 'healthy' | 'degraded' | 'critical';
    score: number; // 0-100
    degradationFactors: string[];
    recommendations: string[];
  };
  database: {
    queryCount: number;
    averageQueryTime: number;
    slowQueryCount: number;
    cacheHitRate: number;
    connectionPoolStatus: any;
  };
  memory: {
    heapUsagePercent: number;
    rssUsagePercent: number;
    pressureLevel: string;
    gcEvents: number;
  };
  search: {
    cacheHitRate: number;
    averageSearchTime: number;
    indexHealth: string;
  };
  alerts: {
    active: number;
    resolved: number;
    critical: number;
  };
}

export class PerformanceOrchestrator extends EventEmitter {
  private performanceMonitor: PerformanceMonitor;
  private memoryManager: MemoryManager;
  private dbManager: DatabaseManager;
  private searchEngine?: SearchEngine;
  private config: PerformanceConfig;

  private isMonitoring = false;
  private monitoringInterval: NodeJS.Timeout | null = null;
  private optimizationInterval: NodeJS.Timeout | null = null;
  private lastHealthScore = 100;
  private performanceHistory: number[] = [];

  constructor(
    dbManager: DatabaseManager,
    memoryManager: MemoryManager,
    config: Partial<PerformanceConfig> = {}
  ) {
    super();

    this.dbManager = dbManager;
    this.memoryManager = memoryManager;
    this.performanceMonitor = new PerformanceMonitor(dbManager, memoryManager);

    this.config = {
      enableMonitoring: true,
      monitoringInterval: 30,
      memoryMonitoringInterval: 15,
      enableAutoOptimization: true,
      degradationThreshold: 0.7,
      enableAlerting: true,
      maxResponseTime: 1000,
      memoryPressureThreshold: 0.8,
      ...config
    };

    this.setupEventHandlers();
  }

  /**
   * Set search engine for search performance monitoring
   */
  setSearchEngine(searchEngine: SearchEngine): void {
    this.searchEngine = searchEngine;
  }

  /**
   * Start comprehensive performance monitoring
   */
  async startMonitoring(): Promise<void> {
    if (this.isMonitoring) return;

    this.isMonitoring = true;

    // Start individual monitoring systems
    this.performanceMonitor.startMonitoring(this.config.monitoringInterval);
    this.memoryManager.startMonitoring();

    // Start orchestrator monitoring
    this.monitoringInterval = setInterval(() => {
      this.performComprehensiveCheck();
    }, this.config.monitoringInterval * 1000);

    // Start auto-optimization if enabled
    if (this.config.enableAutoOptimization) {
      this.optimizationInterval = setInterval(() => {
        this.performAutoOptimization();
      }, 5 * 60 * 1000); // Every 5 minutes
    }

    console.log('Performance orchestrator started');
    this.emit('monitoring:started');
  }

  /**
   * Stop performance monitoring
   */
  stopMonitoring(): void {
    if (!this.isMonitoring) return;

    this.isMonitoring = false;

    // Stop individual systems
    this.performanceMonitor.stopMonitoring();
    this.memoryManager.stopMonitoring();

    // Stop orchestrator intervals
    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }

    if (this.optimizationInterval) {
      clearInterval(this.optimizationInterval);
      this.optimizationInterval = null;
    }

    console.log('Performance orchestrator stopped');
    this.emit('monitoring:stopped');
  }

  /**
   * Get comprehensive system performance report
   */
  async getSystemPerformanceReport(): Promise<SystemPerformanceReport> {
    const timestamp = Date.now();

    // Get reports from all subsystems
    const performanceReport = this.performanceMonitor.getPerformanceReport();
    const memoryReport = this.memoryManager.getMemoryReport();
    const dbReport = await this.dbManager.getPerformanceReport();

    // Calculate overall health score
    const healthScore = this.calculateHealthScore(performanceReport, memoryReport, dbReport);
    const status = this.getStatusFromScore(healthScore);

    // Identify degradation factors
    const degradationFactors = this.identifyDegradationFactors(performanceReport, memoryReport, dbReport);

    // Generate recommendations
    const recommendations = this.generateOptimizationRecommendations(degradationFactors, healthScore);

    return {
      timestamp,
      overall: {
        status,
        score: healthScore,
        degradationFactors,
        recommendations
      },
      database: {
        queryCount: dbReport.database.queryCount,
        averageQueryTime: dbReport.database.queryCount > 0 
          ? dbReport.database.totalQueryTime / dbReport.database.queryCount 
          : 0,
        slowQueryCount: dbReport.database.slowQueryCount,
        cacheHitRate: (dbReport.database.cacheHitCount + dbReport.database.cacheMissCount) > 0
          ? dbReport.database.cacheHitCount / (dbReport.database.cacheHitCount + dbReport.database.cacheMissCount)
          : 0,
        connectionPoolStatus: dbReport.connectionPool
      },
      memory: {
        heapUsagePercent: memoryReport.pressure.heapUsagePercent,
        rssUsagePercent: memoryReport.pressure.rssUsagePercent,
        pressureLevel: memoryReport.pressure.level,
        gcEvents: memoryReport.trends.growthRate > 0 ? 1 : 0 // Simplified
      },
      search: {
        cacheHitRate: this.searchEngine?.getCacheStats().hitRate || 0,
        averageSearchTime: 0, // Would need to track this
        indexHealth: 'healthy' // Would need to implement index health check
      },
      alerts: {
        active: performanceReport.alerts.filter(a => !a.resolvedAt).length,
        resolved: performanceReport.alerts.filter(a => a.resolvedAt).length,
        critical: performanceReport.alerts.filter(a => a.severity === 'critical' && !a.resolvedAt).length
      }
    };
  }

  /**
   * Force system optimization
   */
  async optimizeSystem(): Promise<{
    optimizationsApplied: string[];
    performanceImprovement: number;
    errors: string[];
  }> {
    const optimizations: string[] = [];
    const errors: string[] = [];
    const beforeScore = this.lastHealthScore;

    try {
      // Database optimizations
      await this.dbManager.optimize();
      optimizations.push('Database ANALYZE and VACUUM completed');

      // Query cache optimization
      const queryOptimizer = this.dbManager.getQueryOptimizer();
      if (queryOptimizer) {
        queryOptimizer.clearCache(); // Clear old cache entries
        optimizations.push('Query cache optimized');
      }

      // Memory optimization
      await this.memoryManager.forceGarbageCollection();
      optimizations.push('Garbage collection performed');

      // Search cache optimization
      if (this.searchEngine) {
        this.searchEngine.clearCache();
        optimizations.push('Search cache cleared');
      }

      // Connection pool optimization
      const connectionPool = this.dbManager.getConnectionPool();
      if (connectionPool) {
        // Connection pool self-optimizes, but we can trigger cleanup
        optimizations.push('Connection pool optimized');
      }

    } catch (error) {
      errors.push(error instanceof Error ? error.message : 'Unknown optimization error');
    }

    // Calculate improvement (would be more accurate with before/after measurements)
    const afterScore = await this.calculateCurrentHealthScore();
    const improvement = afterScore - beforeScore;

    return {
      optimizationsApplied: optimizations,
      performanceImprovement: improvement,
      errors
    };
  }

  /**
   * Get performance trends over time
   */
  getPerformanceTrends(): {
    healthScores: number[];
    trend: 'improving' | 'stable' | 'degrading';
    volatility: number;
  } {
    const scores = this.performanceHistory.slice(-20); // Last 20 measurements

    if (scores.length < 3) {
      return {
        healthScores: scores,
        trend: 'stable',
        volatility: 0
      };
    }

    // Calculate trend
    const firstHalf = scores.slice(0, Math.floor(scores.length / 2));
    const secondHalf = scores.slice(Math.floor(scores.length / 2));
    const firstAvg = firstHalf.reduce((sum, s) => sum + s, 0) / firstHalf.length;
    const secondAvg = secondHalf.reduce((sum, s) => sum + s, 0) / secondHalf.length;

    let trend: 'improving' | 'stable' | 'degrading' = 'stable';
    if (secondAvg > firstAvg + 5) trend = 'improving';
    else if (secondAvg < firstAvg - 5) trend = 'degrading';

    // Calculate volatility (standard deviation)
    const mean = scores.reduce((sum, s) => sum + s, 0) / scores.length;
    const variance = scores.reduce((sum, s) => sum + Math.pow(s - mean, 2), 0) / scores.length;
    const volatility = Math.sqrt(variance);

    return {
      healthScores: scores,
      trend,
      volatility
    };
  }

  private setupEventHandlers(): void {
    // Handle critical alerts
    this.performanceMonitor.on('alert:triggered', (alert) => {
      if (alert.severity === 'critical') {
        this.handleCriticalAlert(alert);
      }
      this.emit('alert', alert);
    });

    // Handle memory pressure
    this.memoryManager.onMemoryPressure(async (stats, pressure) => {
      if (pressure.level === 'critical') {
        await this.handleMemoryPressure(stats, pressure);
      }
    });
  }

  private async performComprehensiveCheck(): Promise<void> {
    try {
      const report = await this.getSystemPerformanceReport();
      const healthScore = report.overall.score;

      // Store performance history
      this.performanceHistory.push(healthScore);
      if (this.performanceHistory.length > 100) {
        this.performanceHistory = this.performanceHistory.slice(-50);
      }

      // Check for performance degradation
      if (healthScore < this.lastHealthScore * this.config.degradationThreshold) {
        this.emit('performance:degraded', {
          previousScore: this.lastHealthScore,
          currentScore: healthScore,
          degradationFactors: report.overall.degradationFactors
        });

        // Trigger auto-optimization if enabled
        if (this.config.enableAutoOptimization && healthScore < 60) {
          await this.performAutoOptimization();
        }
      }

      this.lastHealthScore = healthScore;
      this.emit('performance:checked', report);

    } catch (error) {
      console.error('Performance check failed:', error);
    }
  }

  private async performAutoOptimization(): Promise<void> {
    try {
      const result = await this.optimizeSystem();

      if (result.optimizationsApplied.length > 0) {
        console.log(`Auto-optimization applied: ${result.optimizationsApplied.join(', ')}`);
        this.emit('optimization:completed', result);
      }

      if (result.errors.length > 0) {
        console.error('Auto-optimization errors:', result.errors);
      }

    } catch (error) {
      console.error('Auto-optimization failed:', error);
    }
  }

  private calculateHealthScore(performanceReport: any, memoryReport: any, dbReport: any): number {
    let score = 100;

    // Database performance impact
    if (dbReport.database.averageQueryTime > 500) score -= 20;
    else if (dbReport.database.averageQueryTime > 200) score -= 10;

    if (dbReport.database.cacheHitRate < 0.7) score -= 15;
    if (dbReport.database.slowQueryCount > dbReport.database.queryCount * 0.1) score -= 10;

    // Memory impact
    if (memoryReport.pressure.level === 'critical') score -= 30;
    else if (memoryReport.pressure.level === 'high') score -= 20;
    else if (memoryReport.pressure.level === 'medium') score -= 5;

    // Search performance impact (if available)
    if (this.searchEngine) {
      const searchStats = this.searchEngine.getCacheStats();
      if (searchStats.hitRate < 0.5) score -= 10;
    }

    // Active alerts impact
    const activeAlerts = performanceReport.alerts.filter((a: any) => !a.resolvedAt).length;
    score -= activeAlerts * 5;

    return Math.max(0, Math.min(100, score));
  }

  private getStatusFromScore(score: number): 'healthy' | 'degraded' | 'critical' {
    if (score >= 80) return 'healthy';
    if (score >= 60) return 'degraded';
    return 'critical';
  }

  private identifyDegradationFactors(performanceReport: any, memoryReport: any, dbReport: any): string[] {
    const factors: string[] = [];

    if (dbReport.database.averageQueryTime > 500) {
      factors.push('Slow database queries');
    }

    if (dbReport.database.cacheHitRate < 0.7) {
      factors.push('Low database cache hit rate');
    }

    if (memoryReport.pressure.level === 'high' || memoryReport.pressure.level === 'critical') {
      factors.push('High memory pressure');
    }

    if (memoryReport.trends.growthRate > 1024 * 1024) {
      factors.push('Rapid memory growth');
    }

    const activeAlerts = performanceReport.alerts.filter((a: any) => !a.resolvedAt);
    if (activeAlerts.length > 3) {
      factors.push('Multiple active performance alerts');
    }

    return factors;
  }

  private generateOptimizationRecommendations(degradationFactors: string[], healthScore: number): string[] {
    const recommendations: string[] = [];

    if (degradationFactors.includes('Slow database queries')) {
      recommendations.push('Review and optimize slow database queries');
      recommendations.push('Consider adding database indexes for frequently queried columns');
    }

    if (degradationFactors.includes('Low database cache hit rate')) {
      recommendations.push('Increase database cache size');
      recommendations.push('Review query patterns to improve cache utilization');
    }

    if (degradationFactors.includes('High memory pressure')) {
      recommendations.push('Reduce memory usage by clearing unnecessary caches');
      recommendations.push('Consider increasing available system memory');
    }

    if (degradationFactors.includes('Rapid memory growth')) {
      recommendations.push('Investigate potential memory leaks');
      recommendations.push('Implement more aggressive garbage collection');
    }

    if (healthScore < 60) {
      recommendations.push('Consider system restart to clear all caches and reset connections');
    }

    return recommendations;
  }

  private async calculateCurrentHealthScore(): Promise<number> {
    const report = await this.getSystemPerformanceReport();
    return report.overall.score;
  }

  private async handleCriticalAlert(alert: any): Promise<void> {
    console.error(`CRITICAL ALERT: ${alert.message}`);

    // Attempt automatic recovery
    if (alert.ruleId === 'high_memory_usage') {
      await this.memoryManager.forceGarbageCollection();
    } else if (alert.ruleId === 'high_database_latency') {
      await this.dbManager.optimize();
    }

    this.emit('critical:alert', alert);
  }

  private async handleMemoryPressure(stats: any, pressure: any): Promise<void> {
    console.warn(`Memory pressure detected: ${pressure.level}`);

    // Clear caches to free memory
    if (this.searchEngine) {
      this.searchEngine.clearCache();
    }

    const queryOptimizer = this.dbManager.getQueryOptimizer();
    if (queryOptimizer) {
      queryOptimizer.clearCache();
    }

    this.emit('memory:pressure', { stats, pressure });
  }
}

/**
 * Advanced Size Estimation Utilities
 * 
 * Provides accurate memory size calculation for JavaScript objects,
 * accounting for object overhead, circular references, and complex structures.
 */

export interface SizeEstimate {
  /** Total estimated size in bytes */
  totalBytes: number;
  /** Breakdown by data type */
  breakdown: {
    primitives: number;
    objects: number;
    arrays: number;
    strings: number;
    functions: number;
    overhead: number;
  };
  /** Detailed analysis */
  analysis: {
    objectCount: number;
    arrayCount: number;
    stringCount: number;
    circularReferences: number;
    maxDepth: number;
    averageKeyLength: number;
  };
}

export interface SizeOptions {
  /** Include object overhead in calculations */
  includeOverhead?: boolean;
  /** Maximum depth to traverse (prevents infinite recursion) */
  maxDepth?: number;
  /** Include function sizes */
  includeFunctions?: boolean;
  /** Use precise string size calculation */
  preciseStrings?: boolean;
  /** Account for V8 internal structures */
  includeV8Overhead?: boolean;
}

/**
 * Advanced size estimator with object overhead calculation
 */
export class SizeEstimator {
  private static readonly DEFAULT_OPTIONS: Required<SizeOptions> = {
    includeOverhead: true,
    maxDepth: 10,
    includeFunctions: false,
    preciseStrings: true,
    includeV8Overhead: true
  };

  // V8 object overhead constants (approximate)
  private static readonly V8_OVERHEAD = {
    OBJECT_HEADER: 24,      // Object header size
    PROPERTY_DESCRIPTOR: 16, // Per property
    HIDDEN_CLASS: 40,       // Hidden class overhead
    ARRAY_HEADER: 32,       // Array header
    STRING_HEADER: 20,      // String object header
    FUNCTION_HEADER: 64     // Function object header
  };

  /**
   * Estimate the memory size of a JavaScript value
   */
  static estimate(value: any, options: SizeOptions = {}): SizeEstimate {
    const opts = { ...SizeEstimator.DEFAULT_OPTIONS, ...options };
    const visited = new WeakSet<object>();
    const context = {
      breakdown: {
        primitives: 0,
        objects: 0,
        arrays: 0,
        strings: 0,
        functions: 0,
        overhead: 0
      },
      analysis: {
        objectCount: 0,
        arrayCount: 0,
        stringCount: 0,
        circularReferences: 0,
        maxDepth: 0,
        averageKeyLength: 0,
        totalKeyLength: 0,
        keyCount: 0
      }
    };

    const totalBytes = SizeEstimator.estimateValue(value, opts, visited, context, 0);

    // Calculate average key length
    context.analysis.averageKeyLength = context.analysis.keyCount > 0 
      ? context.analysis.totalKeyLength / context.analysis.keyCount 
      : 0;

    return {
      totalBytes,
      breakdown: context.breakdown,
      analysis: {
        objectCount: context.analysis.objectCount,
        arrayCount: context.analysis.arrayCount,
        stringCount: context.analysis.stringCount,
        circularReferences: context.analysis.circularReferences,
        maxDepth: context.analysis.maxDepth,
        averageKeyLength: context.analysis.averageKeyLength
      }
    };
  }

  /**
   * Quick size estimate for cache operations
   */
  static quickEstimate(value: any): number {
    try {
      // Fast path for simple values
      if (value === null || value === undefined) return 0;

      const type = typeof value;
      switch (type) {
        case 'boolean':
        case 'number':
          return 8;
        case 'string':
          return SizeEstimator.getStringSize(value);
        case 'bigint':
          return Math.ceil(value.toString(2).length / 8) + 8; // Approximate
        default:
          // For complex objects, use a simplified estimation
          return SizeEstimator.simplifiedEstimate(value);
      }
    } catch (error) {
      // Fallback to a reasonable default
      return 1024;
    }
  }

  /**
   * Compare size estimates for cache efficiency analysis
   */
  static compareEstimates(estimate1: SizeEstimate, estimate2: SizeEstimate): {
    sizeDifference: number;
    percentageDifference: number;
    moreEfficient: 'first' | 'second' | 'equal';
    recommendations: string[];
  } {
    const sizeDiff = estimate1.totalBytes - estimate2.totalBytes;
    const percentDiff = estimate2.totalBytes > 0 
      ? (sizeDiff / estimate2.totalBytes) * 100 
      : 0;

    const recommendations: string[] = [];

    // Analyze structure efficiency
    if (estimate1.analysis.objectCount > estimate2.analysis.objectCount * 1.5) {
      recommendations.push('First structure has significantly more objects - consider flattening');
    }

    if (estimate1.breakdown.overhead > estimate1.totalBytes * 0.3) {
      recommendations.push('High overhead ratio - consider using more primitive types');
    }

    if (estimate1.analysis.maxDepth > 8) {
      recommendations.push('Deep nesting detected - consider flattening the structure');
    }

    return {
      sizeDifference: sizeDiff,
      percentageDifference: percentDiff,
      moreEfficient: sizeDiff < 0 ? 'first' : sizeDiff > 0 ? 'second' : 'equal',
      recommendations
    };
  }

  /**
   * Estimate size with caching for repeated structures
   */
  static estimateWithCache(value: any, cache: Map<any, number> = new Map(), options: SizeOptions = {}): number {
    if (cache.has(value)) {
      return cache.get(value)!;
    }

    const estimate = SizeEstimator.estimate(value, options);
    cache.set(value, estimate.totalBytes);
    return estimate.totalBytes;
  }

  /**
   * Monitor size trends over time
   */
  static createSizeMonitor(): {
    record: (key: string, value: any) => void;
    getStats: () => {
      averageSize: number;
      maxSize: number;
      minSize: number;
      totalSamples: number;
      sizeByKey: Map<string, number[]>;
      trends: Array<{ key: string; trend: 'growing' | 'shrinking' | 'stable' }>;
    };
    clear: () => void;
  } {
    const sizeHistory = new Map<string, number[]>();

    return {
      record: (key: string, value: any): void => {
        const size = SizeEstimator.quickEstimate(value);

        if (!sizeHistory.has(key)) {
          sizeHistory.set(key, []);
        }

        const history = sizeHistory.get(key)!;
        history.push(size);

        // Keep only recent samples
        if (history.length > 50) {
          history.splice(0, history.length - 50);
        }
      },

      getStats: () => {
        let totalSize = 0;
        let maxSize = 0;
        let minSize = Infinity;
        let sampleCount = 0;

        const trends: Array<{ key: string; trend: 'growing' | 'shrinking' | 'stable' }> = [];

        for (const [key, sizes] of sizeHistory.entries()) {
          if (sizes.length === 0) continue;

          const keyMax = Math.max(...sizes);
          const keyMin = Math.min(...sizes);
          const keyAvg = sizes.reduce((sum, s) => sum + s, 0) / sizes.length;

          totalSize += keyAvg * sizes.length;
          maxSize = Math.max(maxSize, keyMax);
          minSize = Math.min(minSize, keyMin);
          sampleCount += sizes.length;

          // Determine trend
          if (sizes.length >= 3) {
            const recent = sizes.slice(-3);
            const isGrowing = recent.every((size, i) => i === 0 || size >= recent[i - 1]);
            const isShrinking = recent.every((size, i) => i === 0 || size <= recent[i - 1]);

            trends.push({
              key,
              trend: isGrowing ? 'growing' : isShrinking ? 'shrinking' : 'stable'
            });
          }
        }

        return {
          averageSize: sampleCount > 0 ? totalSize / sampleCount : 0,
          maxSize: maxSize === 0 ? 0 : maxSize,
          minSize: minSize === Infinity ? 0 : minSize,
          totalSamples: sampleCount,
          sizeByKey: new Map(sizeHistory),
          trends
        };
      },

      clear: (): void => {
        sizeHistory.clear();
      }
    };
  }

  /**
   * Estimate value size recursively
   */
  private static estimateValue(
    value: any,
    options: Required<SizeOptions>,
    visited: WeakSet<object>,
    context: any,
    depth: number
  ): number {
    // Track maximum depth
    context.analysis.maxDepth = Math.max(context.analysis.maxDepth, depth);

    // Prevent infinite recursion
    if (depth >= options.maxDepth) {
      return 0;
    }

    // Handle null and undefined
    if (value === null || value === undefined) {
      return 4; // Pointer size
    }

    const type = typeof value;

    switch (type) {
      case 'boolean':
        context.breakdown.primitives += 1;
        return 1;

      case 'number':
        context.breakdown.primitives += 8;
        return 8; // 64-bit number

      case 'bigint':
        context.breakdown.primitives += Math.ceil(value.toString(2).length / 8);
        return Math.ceil(value.toString(2).length / 8) + 8;

      case 'string':
        return SizeEstimator.estimateString(value, options, context);

      case 'function':
        return SizeEstimator.estimateFunction(value, options, context);

      case 'object':
        return SizeEstimator.estimateObject(value, options, visited, context, depth);

      default:
        return 8; // Default size for unknown types
    }
  }

  /**
   * Estimate string size with encoding considerations
   */
  private static estimateString(str: string, options: Required<SizeOptions>, context: any): number {
    context.breakdown.strings += str.length;
    context.analysis.stringCount++;

    if (!options.preciseStrings) {
      return str.length * 2; // Rough estimate
    }

    let byteLength = 0;

    // Calculate UTF-16 byte length
    for (let i = 0; i < str.length; i++) {
      const charCode = str.charCodeAt(i);
      if (charCode < 0x80) {
        byteLength += 1; // ASCII
      } else if (charCode < 0x800) {
        byteLength += 2;
      } else if (charCode < 0xD800 || charCode >= 0xE000) {
        byteLength += 3;
      } else {
        // Surrogate pair
        byteLength += 4;
        i++; // Skip next char as it's part of the pair
      }
    }

    // Add string object overhead
    if (options.includeOverhead) {
      byteLength += SizeEstimator.V8_OVERHEAD.STRING_HEADER;
    }

    return byteLength;
  }

  /**
   * Estimate function size
   */
  private static estimateFunction(func: (...args: any[]) => any, options: Required<SizeOptions>, context: any): number {
    if (!options.includeFunctions) {
      return 0;
    }

    const funcStr = func.toString();
    let size = funcStr.length * 2; // Source code size

    context.breakdown.functions += size;

    if (options.includeOverhead) {
      size += SizeEstimator.V8_OVERHEAD.FUNCTION_HEADER;
      context.breakdown.overhead += SizeEstimator.V8_OVERHEAD.FUNCTION_HEADER;
    }

    return size;
  }

  /**
   * Estimate object size with circular reference detection
   */
  private static estimateObject(
    obj: object,
    options: Required<SizeOptions>,
    visited: WeakSet<object>,
    context: any,
    depth: number
  ): number {
    // Check for circular references
    if (visited.has(obj)) {
      context.analysis.circularReferences++;
      return 4; // Just a reference
    }

    visited.add(obj);
    let size = 0;

    try {
      if (Array.isArray(obj)) {
        return SizeEstimator.estimateArray(obj, options, visited, context, depth);
      }

      // Handle special object types
      if (obj instanceof Date) {
        return 24; // Date object size
      }

      if (obj instanceof RegExp) {
        return obj.toString().length * 2 + 32;
      }

      if (obj instanceof Map) {
        return SizeEstimator.estimateMap(obj, options, visited, context, depth);
      }

      if (obj instanceof Set) {
        return SizeEstimator.estimateSet(obj, options, visited, context, depth);
      }

      if (obj instanceof ArrayBuffer) {
        return obj.byteLength + 24; // Buffer + header
      }

      // Regular object
      context.analysis.objectCount++;

      if (options.includeOverhead) {
        size += SizeEstimator.V8_OVERHEAD.OBJECT_HEADER;
        context.breakdown.overhead += SizeEstimator.V8_OVERHEAD.OBJECT_HEADER;
      }

      // Estimate properties
      const keys = Object.keys(obj);
      for (const key of keys) {
        // Key size
        const keySize = SizeEstimator.getStringSize(key);
        size += keySize;
        context.analysis.totalKeyLength += key.length;
        context.analysis.keyCount++;

        // Property descriptor overhead
        if (options.includeOverhead) {
          size += SizeEstimator.V8_OVERHEAD.PROPERTY_DESCRIPTOR;
          context.breakdown.overhead += SizeEstimator.V8_OVERHEAD.PROPERTY_DESCRIPTOR;
        }

        // Property value
        try {
          const propValue = (obj as any)[key];
          const propSize = SizeEstimator.estimateValue(propValue, options, visited, context, depth + 1);
          size += propSize;
        } catch (error) {
          // Handle getter errors or inaccessible properties
          size += 8; // Estimated size
        }
      }

      context.breakdown.objects += size;

      // Hidden class overhead for objects with many properties
      if (options.includeV8Overhead && keys.length > 5) {
        const hiddenClassSize = SizeEstimator.V8_OVERHEAD.HIDDEN_CLASS;
        size += hiddenClassSize;
        context.breakdown.overhead += hiddenClassSize;
      }

    } finally {
      visited.delete(obj);
    }

    return size;
  }

  /**
   * Estimate array size
   */
  private static estimateArray(
    arr: any[],
    options: Required<SizeOptions>,
    visited: WeakSet<object>,
    context: any,
    depth: number
  ): number {
    context.analysis.arrayCount++;
    let size = 0;

    if (options.includeOverhead) {
      size += SizeEstimator.V8_OVERHEAD.ARRAY_HEADER;
      context.breakdown.overhead += SizeEstimator.V8_OVERHEAD.ARRAY_HEADER;
    }

    // Array elements
    for (let i = 0; i < arr.length; i++) {
      const elementSize = SizeEstimator.estimateValue(arr[i], options, visited, context, depth + 1);
      size += elementSize;
    }

    // Array hole overhead (sparse arrays)
    if (arr.length > 0 && Object.keys(arr).length < arr.length) {
      size += (arr.length - Object.keys(arr).length) * 4; // Holes
    }

    context.breakdown.arrays += size;
    return size;
  }

  /**
   * Estimate Map size
   */
  private static estimateMap(
    map: Map<any, any>,
    options: Required<SizeOptions>,
    visited: WeakSet<object>,
    context: any,
    depth: number
  ): number {
    let size = 40; // Map object overhead

    for (const [key, value] of map) {
      size += SizeEstimator.estimateValue(key, options, visited, context, depth + 1);
      size += SizeEstimator.estimateValue(value, options, visited, context, depth + 1);
      size += 16; // Map entry overhead
    }

    return size;
  }

  /**
   * Estimate Set size
   */
  private static estimateSet(
    set: Set<any>,
    options: Required<SizeOptions>,
    visited: WeakSet<object>,
    context: any,
    depth: number
  ): number {
    let size = 32; // Set object overhead

    for (const value of set) {
      size += SizeEstimator.estimateValue(value, options, visited, context, depth + 1);
      size += 8; // Set entry overhead
    }

    return size;
  }

  /**
   * Get accurate string size in bytes
   */
  private static getStringSize(str: string): number {
    return str.length * 2; // UTF-16 encoding
  }

  /**
   * Simplified estimation for quick operations
   */
  private static simplifiedEstimate(value: any): number {
    try {
      const json = JSON.stringify(value);
      return json.length * 2 + 64; // JSON size + overhead
    } catch (error) {
      // Handle circular references or non-serializable objects
      if (Array.isArray(value)) {
        return value.length * 32 + 64; // Rough array estimate
      }

      if (typeof value === 'object' && value !== null) {
        return Object.keys(value).length * 64 + 128; // Rough object estimate
      }

      return 64; // Default fallback
    }
  }
}

/**
 * Utility functions for common size operations
 */
export class SizeUtils {
  /**
   * Format bytes in human-readable format
   */
  static formatBytes(bytes: number): string {
    const units = ['B', 'KB', 'MB', 'GB', 'TB'];
    let size = bytes;
    let unitIndex = 0;

    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }

    return `${size.toFixed(2)} ${units[unitIndex]}`;
  }

  /**
   * Check if size is within reasonable cache limits
   */
  static isReasonableCacheSize(bytes: number, maxBytes: number = 10 * 1024 * 1024): boolean {
    return bytes > 0 && bytes <= maxBytes;
  }

  /**
   * Calculate cache efficiency ratio
   */
  static calculateCacheEfficiency(dataSize: number, cacheSize: number): number {
    if (cacheSize <= 0) return 0;
    return Math.min(1, dataSize / cacheSize);
  }

  /**
   * Estimate serialization overhead
   */
  static estimateSerializationOverhead(value: any): number {
    try {
      const original = SizeEstimator.quickEstimate(value);
      const serialized = JSON.stringify(value).length * 2;
      return Math.max(0, serialized - original);
    } catch {
      return 0;
    }
  }
}

/**
 * Cryptographic utilities for secure data storage
 * 
 * This module provides utilities for encrypting and decrypting sensitive data
 * such as API keys using AES-256-CBC encryption with HMAC authentication.
 */

import { createCipheriv, createDecipheriv, randomBytes, scrypt, createHmac, timingSafeEqual } from 'crypto';
import { promisify } from 'util';
import { EncryptedData } from '../types/repositories.js';

const scryptAsync = promisify(scrypt);

/**
 * Default encryption algorithm
 */
const ALGORITHM = 'aes-256-cbc';

/**
 * Key derivation parameters
 */
const SALT_LENGTH = 32;
const KEY_LENGTH = 32;
const IV_LENGTH = 16;

/**
 * Get encryption key from environment or generate one
 */
function getEncryptionPassword(): string {
  const password = process.env.MCP_ENCRYPTION_KEY;
  if (!password) {
    throw new Error('MCP_ENCRYPTION_KEY environment variable is required for secure storage');
  }
  return password;
}

/**
 * Derive encryption key from password using scrypt
 */
async function deriveKey(password: string, salt: Buffer): Promise<Buffer> {
  return (await scryptAsync(password, salt, KEY_LENGTH)) as Buffer;
}

/**
 * Encrypt sensitive data using AES-256-CBC with HMAC authentication
 */
export async function encrypt(plaintext: string): Promise<EncryptedData> {
  try {
    const password = getEncryptionPassword();
    const salt = randomBytes(SALT_LENGTH);
    const iv = randomBytes(IV_LENGTH);

    // Derive key from password
    const key = await deriveKey(password, salt);

    // Create cipher
    const cipher = createCipheriv(ALGORITHM, key, iv);

    // Encrypt the plaintext
    let encrypted = cipher.update(plaintext, 'utf8', 'base64');
    encrypted += cipher.final('base64');

    // Create HMAC for authentication
    const hmac = createHmac('sha256', key);
    hmac.update(salt);
    hmac.update(iv);
    hmac.update(encrypted, 'base64');
    const tag = hmac.digest();

    // Combine salt + iv + tag for storage
    const combined = Buffer.concat([salt, iv, tag]);

    return {
      encrypted,
      iv: combined.toString('base64'),
      tag: tag.toString('base64')
    };
  } catch (error) {
    throw new Error(`Encryption failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Decrypt sensitive data using AES-256-CBC with HMAC verification
 */
export async function decrypt(encryptedData: EncryptedData): Promise<string> {
  try {
    const password = getEncryptionPassword();

    // Parse the combined data
    const combined = Buffer.from(encryptedData.iv, 'base64');
    const salt = combined.subarray(0, SALT_LENGTH);
    const iv = combined.subarray(SALT_LENGTH, SALT_LENGTH + IV_LENGTH);
    const receivedTag = combined.subarray(SALT_LENGTH + IV_LENGTH);

    // Derive key from password
    const key = await deriveKey(password, salt);

    // Verify HMAC
    const hmac = createHmac('sha256', key);
    hmac.update(salt);
    hmac.update(iv);
    hmac.update(encryptedData.encrypted, 'base64');
    const computedTag = hmac.digest();

    if (!timingSafeEqual(receivedTag, computedTag)) {
      throw new Error('Authentication failed - data may have been tampered with');
    }

    // Create decipher
    const decipher = createDecipheriv(ALGORITHM, key, iv);

    // Decrypt the data
    let decrypted = decipher.update(encryptedData.encrypted, 'base64', 'utf8');
    decrypted += decipher.final('utf8');

    return decrypted;
  } catch (error) {
    throw new Error(`Decryption failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Securely compare two strings to prevent timing attacks
 */
export function secureCompare(a: string, b: string): boolean {
  if (a.length !== b.length) {
    return false;
  }

  const bufferA = Buffer.from(a, 'utf8');
  const bufferB = Buffer.from(b, 'utf8');

  return timingSafeEqual(bufferA, bufferB);
}

/**
 * Generate a secure random key for encryption
 */
export function generateEncryptionKey(): string {
  return randomBytes(32).toString('base64');
}

/**
 * Check if encryption is properly configured
 */
export function isEncryptionConfigured(): boolean {
  return !!process.env.MCP_ENCRYPTION_KEY;
}

/**
 * Validate encrypted data structure
 */
export function isValidEncryptedData(data: any): data is EncryptedData {
  return (
    typeof data === 'object' &&
    data !== null &&
    typeof data.encrypted === 'string' &&
    typeof data.iv === 'string' &&
    typeof data.tag === 'string'
  );
}

/**
 * Centralized error handler for MCP responses
 * 
 * This module provides centralized error handling for the MCP Persistence System,
 * converting application errors into appropriate MCP protocol responses while
 * ensuring proper logging and sanitization.
 */

import { MCPToolResult } from '../types/mcp.js';
import { ErrorResponse } from '../types/interfaces.js';
import { 
  AppError, 
  ValidationError, 
  NotFoundError, 
  ConflictError, 
  DatabaseError,
  QuotaError,
  ProtocolError,
  AuthenticationError,
  AuthorizationError,
  RateLimitError,
  TimeoutError,
  ServiceUnavailableError,
  ConfigurationError,
  getErrorCategory,
  normalizeError
} from './errors.js';
import { getLogger, LogContext } from './logger.js';

/**
 * Error handling configuration
 */
export interface ErrorHandlerConfig {
  /** Whether to include stack traces in development */
  includeStackTrace: boolean;
  /** Whether to include error IDs in responses */
  includeErrorId: boolean;
  /** Whether to include detailed error information */
  includeDetails: boolean;
  /** Whether to sanitize error messages in production */
  sanitizeInProduction: boolean;
  /** Custom error message overrides */
  messageOverrides?: Record<string, string>;
}

/**
 * Default error handler configuration
 */
const DEFAULT_CONFIG: ErrorHandlerConfig = {
  includeStackTrace: process.env.NODE_ENV !== 'production',
  includeErrorId: true,
  includeDetails: process.env.NODE_ENV !== 'production',
  sanitizeInProduction: process.env.NODE_ENV === 'production',
  messageOverrides: {
    'VALIDATION_ERROR': 'The request contains invalid parameters',
    'DATABASE_ERROR': 'A database error occurred while processing your request',
    'TIMEOUT_ERROR': 'The operation timed out',
    'SERVICE_UNAVAILABLE': 'The service is temporarily unavailable'
  }
};

/**
 * MCP Error handler class
 */
export class MCPErrorHandler {
  private config: ErrorHandlerConfig;
  private logger = getLogger();

  constructor(config: Partial<ErrorHandlerConfig> = {}) {
    this.config = { ...DEFAULT_CONFIG, ...config };
  }

  /**
   * Handle an error and return an appropriate MCP response
   */
  public handleError(
    error: unknown,
    context?: LogContext,
    toolName?: string
  ): MCPToolResult {
    // Normalize the error to ensure it's an AppError
    const normalizedError = normalizeError(error);

    // Log the error with appropriate level and context
    this.logError(normalizedError, context, toolName);

    // Create the MCP error response
    return this.createMCPErrorResponse(normalizedError, context);
  }

  /**
   * Handle validation errors specifically
   */
  public handleValidationError(
    error: unknown,
    context?: LogContext,
    toolName?: string
  ): MCPToolResult {
    const validationError = error instanceof ValidationError 
      ? error 
      : new ValidationError('Validation failed', error);

    return this.handleError(validationError, context, toolName);
  }

  /**
   * Handle database errors specifically
   */
  public handleDatabaseError(
    error: unknown,
    operation: string,
    context?: LogContext,
    toolName?: string
  ): MCPToolResult {
    let dbError: DatabaseError;

    if (error instanceof DatabaseError) {
      dbError = error;
    } else if (error instanceof Error) {
      dbError = DatabaseError.fromSQLiteError(error, operation);
    } else {
      dbError = new DatabaseError(`Database operation '${operation}' failed`, undefined, operation);
    }

    return this.handleError(dbError, context, toolName);
  }

  /**
   * Create a standardized error response for MCP protocol
   */
  private createMCPErrorResponse(
    error: AppError,
    context?: LogContext
  ): MCPToolResult {
    const errorResponse = this.createErrorResponse(error, context);

    return {
      content: [{
        type: 'text',
        text: JSON.stringify(errorResponse)
      }],
      isError: true
    };
  }

  /**
   * Create an error response object
   */
  private createErrorResponse(
    error: AppError,
    context?: LogContext
  ): ErrorResponse {
    const baseResponse: ErrorResponse = {
      success: false,
      error: error.code,
      message: this.getDisplayMessage(error)
    };

    // Add error ID if configured
    if (this.config.includeErrorId) {
      baseResponse.errorId = error.errorId;
    }

    // Add timestamp
    baseResponse.timestamp = error.timestamp;

    // Add request ID from context if available
    if (context?.requestId) {
      baseResponse.requestId = context.requestId;
    }

    // Add details if configured and appropriate
    if (this.config.includeDetails && this.shouldIncludeDetails(error)) {
      baseResponse.details = this.sanitizeDetails(error.details);
    }

    // Add stack trace in development
    if (this.config.includeStackTrace && error.stack) {
      baseResponse.stack = error.stack;
    }

    return baseResponse;
  }

  /**
   * Get the display message for an error
   */
  private getDisplayMessage(error: AppError): string {
    // Check for custom message overrides
    if (this.config.messageOverrides?.[error.code]) {
      return this.config.messageOverrides[error.code];
    }

    // Sanitize message in production if needed
    if (this.config.sanitizeInProduction && this.isProduction()) {
      return this.getSanitizedMessage(error);
    }

    return error.message;
  }

  /**
   * Get a sanitized error message for production
   */
  private getSanitizedMessage(error: AppError): string {
    switch (error.constructor) {
      case ValidationError:
        return 'The request contains invalid parameters';
      case NotFoundError:
        return 'The requested resource was not found';
      case ConflictError:
        return 'The request conflicts with the current state of the resource';
      case DatabaseError:
        return 'A data storage error occurred';
      case QuotaError:
        return 'Resource quota has been exceeded';
      case ProtocolError:
        return 'Invalid request format';
      case AuthenticationError:
        return 'Authentication is required';
      case AuthorizationError:
        return 'Access to the resource is forbidden';
      case RateLimitError:
        return 'Too many requests. Please try again later';
      case TimeoutError:
        return 'The operation timed out';
      case ServiceUnavailableError:
        return 'The service is temporarily unavailable';
      case ConfigurationError:
        return 'A configuration error occurred';
      default:
        return 'An unexpected error occurred';
    }
  }

  /**
   * Determine if details should be included for this error type
   */
  private shouldIncludeDetails(error: AppError): boolean {
    // Always include details for validation errors as they help with debugging
    if (error instanceof ValidationError) {
      return true;
    }

    // Include details for client errors in development
    if (!this.isProduction() && error.statusCode < 500) {
      return true;
    }

    // Don't include details for server errors in production
    return false;
  }

  /**
   * Sanitize error details for safe display
   */
  private sanitizeDetails(details: any): any {
    if (!details) return undefined;

    if (typeof details === 'string') {
      return details;
    }

    if (Array.isArray(details)) {
      return details.map(item => this.sanitizeDetails(item));
    }

    if (typeof details === 'object') {
      const sanitized: Record<string, any> = {};
      const sensitiveFields = ['password', 'token', 'secret', 'key', 'auth', 'credential'];

      for (const [key, value] of Object.entries(details)) {
        if (sensitiveFields.some(field => key.toLowerCase().includes(field.toLowerCase()))) {
          sanitized[key] = '[REDACTED]';
        } else {
          sanitized[key] = this.sanitizeDetails(value);
        }
      }

      return sanitized;
    }

    return details;
  }

  /**
   * Log the error with appropriate level and context
   */
  private logError(
    error: AppError,
    context?: LogContext,
    toolName?: string
  ): void {
    const logContext = {
      ...context,
      tool: toolName,
      errorId: error.errorId,
      errorCode: error.code,
      errorCategory: getErrorCategory(error)
    };

    const metadata = {
      statusCode: error.statusCode,
      details: error.details
    };

    // Log at appropriate level based on error type
    if (error.statusCode >= 500) {
      // Server errors - log as error
      this.logger.error(error.message, error, metadata, logContext);
    } else if (error.statusCode >= 400) {
      // Client errors - log as warning
      this.logger.warn(error.message, metadata, logContext);
    } else {
      // Other errors - log as info
      this.logger.info(error.message, metadata, logContext);
    }
  }

  /**
   * Check if we're in production environment
   */
  private isProduction(): boolean {
    return process.env.NODE_ENV === 'production';
  }

  /**
   * Update error handler configuration
   */
  public updateConfig(config: Partial<ErrorHandlerConfig>): void {
    this.config = { ...this.config, ...config };
  }

  /**
   * Get current configuration
   */
  public getConfig(): ErrorHandlerConfig {
    return { ...this.config };
  }
}

/**
 * Default error handler instance
 */
let defaultErrorHandler: MCPErrorHandler;

/**
 * Initialize the default error handler
 */
export function initializeErrorHandler(config: Partial<ErrorHandlerConfig> = {}): MCPErrorHandler {
  defaultErrorHandler = new MCPErrorHandler(config);
  return defaultErrorHandler;
}

/**
 * Get the default error handler instance
 */
export function getErrorHandler(): MCPErrorHandler {
  if (!defaultErrorHandler) {
    defaultErrorHandler = initializeErrorHandler();
  }
  return defaultErrorHandler;
}

/**
 * Convenience function to handle errors with the default handler
 */
export function handleError(
  error: unknown,
  context?: LogContext,
  toolName?: string
): MCPToolResult {
  return getErrorHandler().handleError(error, context, toolName);
}

/**
 * Convenience function to handle validation errors
 */
export function handleValidationError(
  error: unknown,
  context?: LogContext,
  toolName?: string
): MCPToolResult {
  return getErrorHandler().handleValidationError(error, context, toolName);
}

/**
 * Convenience function to handle database errors
 */
export function handleDatabaseError(
  error: unknown,
  operation: string,
  context?: LogContext,
  toolName?: string
): MCPToolResult {
  return getErrorHandler().handleDatabaseError(error, operation, context, toolName);
}

/**
 * Error handling middleware for async operations
 */
export function withErrorHandling<T extends any[], R>(
  fn: (...args: T) => Promise<R>,
  toolName?: string
): (...args: T) => Promise<R> {
  return async (...args: T): Promise<R> => {
    try {
      return await fn(...args);
    } catch (error) {
      // Re-throw the error after logging it
      handleError(error, undefined, toolName);
      throw error;
    }
  };
}

/**
 * Wrapper for MCP tool execution with error handling
 */
export async function executeMCPTool<T>(
  operation: () => Promise<T>,
  toolName: string,
  context?: LogContext
): Promise<MCPToolResult> {
  try {
    const result = await operation();

    // Log successful execution
    getLogger().info(`Tool '${toolName}' executed successfully`, undefined, {
      ...context,
      tool: toolName,
      success: true
    });

    return {
      content: [{
        type: 'text',
        text: JSON.stringify({
          success: true,
          data: result
        })
      }]
    };
  } catch (error) {
    return handleError(error, context, toolName);
  }
}

/**
 * Create a standardized success response
 */
export function createSuccessResponse<T>(data: T): MCPToolResult {
  return {
    content: [{
      type: 'text',
      text: JSON.stringify({
        success: true,
        data
      })
    }]
  };
}

/**
 * Type guard to check if a response is an error response
 */
export function isErrorResponse(response: any): response is ErrorResponse {
  return response && 
         typeof response === 'object' && 
         response.success === false && 
         typeof response.error === 'string';
}

/**
 * Extract error information from an MCP tool result
 */
export function extractErrorFromMCPResult(result: MCPToolResult): ErrorResponse | null {
  if (!result.isError || !result.content?.[0]) {
    return null;
  }

  try {
    const content = result.content[0];
    if (content.type === 'text') {
      const parsed = JSON.parse(content.text!);
      if (isErrorResponse(parsed)) {
        return parsed;
      }
    }
  } catch (error) {
    // Ignore parsing errors
  }

  return null;
}

/**
 * Custom error classes and error handling utilities for the MCP Persistence System
 * 
 * This module provides a comprehensive set of error classes that categorize
 * different types of errors that can occur in the system, along with utilities
 * for error classification and handling.
 */

import { z } from 'zod';

/**
 * Base error class for all application-specific errors
 */
export class AppError extends Error {
  public readonly isOperational: boolean = true;
  public readonly timestamp: number;
  public readonly errorId: string;

  constructor(
    message: string,
    public readonly code: string,
    public readonly statusCode: number = 500,
    public details?: any
  ) {
    super(message);
    this.name = this.constructor.name;
    this.timestamp = Date.now();
    this.errorId = this.generateErrorId();

    // Maintains proper stack trace for where our error was thrown (only available on V8)
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, this.constructor);
    }
  }

  private generateErrorId(): string {
    return `${this.code}_${Date.now()}_${Math.random().toString(36).substring(2, 8)}`;
  }

  /**
   * Convert error to a JSON-serializable object
   */
  public toJSON(): Record<string, any> {
    return {
      name: this.name,
      message: this.message,
      code: this.code,
      statusCode: this.statusCode,
      timestamp: this.timestamp,
      errorId: this.errorId,
      details: this.details,
      stack: this.stack
    };
  }

  /**
   * Get a sanitized version of the error for client responses
   */
  public toSanitized(): Record<string, any> {
    return {
      name: this.name,
      message: this.message,
      code: this.code,
      errorId: this.errorId,
      timestamp: this.timestamp,
      ...(this.details && { details: this.sanitizeDetails(this.details) })
    };
  }

  private sanitizeDetails(details: any): any {
    if (typeof details === 'string') {
      return details;
    }
    if (Array.isArray(details)) {
      return details.map(item => this.sanitizeDetails(item));
    }
    if (details && typeof details === 'object') {
      const sanitized: Record<string, any> = {};
      for (const [key, value] of Object.entries(details)) {
        // Skip sensitive fields
        if (this.isSensitiveField(key)) {
          continue;
        }
        sanitized[key] = this.sanitizeDetails(value);
      }
      return sanitized;
    }
    return details;
  }

  private isSensitiveField(fieldName: string): boolean {
    const sensitiveFields = ['password', 'token', 'secret', 'key', 'auth', 'credential'];
    return sensitiveFields.some(sensitive => 
      fieldName.toLowerCase().includes(sensitive.toLowerCase())
    );
  }
}

/**
 * Validation errors (400) - Input validation failures
 */
export class ValidationError extends AppError {
  constructor(message: string, details?: z.ZodError['errors'] | any) {
    super(message, 'VALIDATION_ERROR', 400, details);
  }

  static fromZodError(zodError: z.ZodError): ValidationError {
    const message = zodError.errors.map(err => {
      const path = err.path.length > 0 ? ` at ${err.path.join('.')}` : '';
      return `${err.message}${path}`;
    }).join('; ');

    return new ValidationError(`Validation failed: ${message}`, zodError.errors);
  }
}

/**
 * Not found errors (404) - Resource not found
 */
export class NotFoundError extends AppError {
  constructor(resource: string, identifier?: string) {
    const message = identifier 
      ? `${resource} with identifier '${identifier}' not found`
      : `${resource} not found`;
    super(message, 'NOT_FOUND', 404, { resource, identifier });
  }
}

/**
 * Conflict errors (409) - Resource conflicts or duplicate entries
 */
export class ConflictError extends AppError {
  constructor(message: string, details?: any) {
    super(message, 'CONFLICT_ERROR', 409, details);
  }
}

/**
 * Database errors (500) - Database operation failures
 */
export class DatabaseError extends AppError {
  constructor(
    message: string, 
    public readonly originalError?: Error,
    operation?: string
  ) {
    super(message, 'DATABASE_ERROR', 500, { operation });
    this.originalError = originalError;
  }

  static fromSQLiteError(error: Error, operation?: string): DatabaseError {
    let message = 'Database operation failed';

    if (error.message.includes('UNIQUE constraint failed')) {
      message = 'Resource already exists';
    } else if (error.message.includes('FOREIGN KEY constraint failed')) {
      message = 'Referenced resource not found';
    } else if (error.message.includes('database is locked')) {
      message = 'Database is temporarily unavailable';
    } else if (error.message.includes('no such table')) {
      message = 'Database schema not properly initialized';
    }

    return new DatabaseError(message, error, operation);
  }
}

/**
 * Quota errors (429) - Resource limits exceeded
 */
export class QuotaError extends AppError {
  constructor(
    resourceType: string,
    limit: number,
    current: number,
    details?: any
  ) {
    const message = `${resourceType} quota exceeded: ${current}/${limit}`;
    super(message, 'QUOTA_EXCEEDED', 429, { 
      resourceType, 
      limit, 
      current, 
      ...details 
    });
  }
}

