/**
 * Flexible logging system for the MCP Persistence System
 * 
 * This module provides a structured logging system with configurable levels,
 * output formats, and contextual information. Supports both development
 * and production environments with appropriate log sanitization.
 */

import * as fs from 'fs';
import * as path from 'path';
import { extractErrorInfo } from './errors.js';

/**
 * Log levels in order of severity
 */
export enum LogLevel {
  DEBUG = 0,
  INFO = 1,
  WARN = 2,
  ERROR = 3,
  SILENT = 4
}

/**
 * Log level names for display
 */
export const LOG_LEVEL_NAMES: Record<LogLevel, string> = {
  [LogLevel.DEBUG]: 'DEBUG',
  [LogLevel.INFO]: 'INFO',
  [LogLevel.WARN]: 'WARN',
  [LogLevel.ERROR]: 'ERROR',
  [LogLevel.SILENT]: 'SILENT'
};

/**
 * Log level colors for console output
 */
const LOG_LEVEL_COLORS: Record<LogLevel, string> = {
  [LogLevel.DEBUG]: '\x1b[36m', // Cyan
  [LogLevel.INFO]: '\x1b[32m',  // Green
  [LogLevel.WARN]: '\x1b[33m',  // Yellow
  [LogLevel.ERROR]: '\x1b[31m', // Red
  [LogLevel.SILENT]: '\x1b[0m'  // Reset
};

/**
 * Performance metrics for operations
 */
export interface PerformanceMetrics {
  operation: string;
  duration: number;
  timestamp: number;
  requestId?: string;
  metadata?: Record<string, any>;
}

/**
 * Structured log entry
 */
export interface LogEntry {
  timestamp: number;
  level: LogLevel;
  message: string;
  context?: LogContext;
  error?: any;
  performance?: PerformanceMetrics;
  metadata?: Record<string, any>;
}

/**
 * Context information for logs
 */
export interface LogContext {
  requestId?: string;
  userId?: string;
  operation?: string;
  tool?: string;
  conversationId?: string;
  messageId?: string;
  component?: string;
  [key: string]: any;
}

/**
 * Logger configuration
 */
export interface LoggerConfig {
  level: LogLevel;
  enableConsole: boolean;
  enableFile: boolean;
  filePath?: string;
  maxFileSize?: number; // in bytes
  maxFiles?: number; // number of rotated files to keep
  enableColors: boolean;
  enableStackTrace: boolean;
  timestampFormat: 'iso' | 'epoch' | 'readable';
  outputFormat: 'json' | 'text';
  sanitizeInProduction: boolean;
}

/**
 * Default logger configuration
 */
const DEFAULT_CONFIG: LoggerConfig = {
  level: LogLevel.INFO,
  enableConsole: true,
  enableFile: false,
  enableColors: true,
  enableStackTrace: true,
  timestampFormat: 'iso',
  outputFormat: 'text',
  sanitizeInProduction: true,
  maxFileSize: 10 * 1024 * 1024, // 10MB
  maxFiles: 5
};

/**
 * Main logger class
 */
export class Logger {
  private config: LoggerConfig;
  private context: LogContext = {};

  constructor(config: Partial<LoggerConfig> = {}) {
    this.config = { ...DEFAULT_CONFIG, ...config };

    // Ensure file directory exists if file logging is enabled
    if (this.config.enableFile && this.config.filePath) {
      this.ensureLogDirectory();
    }
  }

  /**
   * Set the global context for this logger instance
   */
  setContext(context: LogContext): void {
    this.context = { ...this.context, ...context };
  }

  /**
   * Clear the global context
   */
  clearContext(): void {
    this.context = {};
  }

  /**
   * Log a debug message
   */
  debug(message: string, metadata?: Record<string, any>, context?: LogContext): void {
    this.log(LogLevel.DEBUG, message, metadata, context);
  }

  /**
   * Log an info message
   */
  info(message: string, metadata?: Record<string, any>, context?: LogContext): void {
    this.log(LogLevel.INFO, message, metadata, context);
  }

  /**
   * Log a warning message
   */
  warn(message: string, metadata?: Record<string, any>, context?: LogContext): void {
    this.log(LogLevel.WARN, message, metadata, context);
  }

  /**
   * Log an error message
   */
  error(message: string, error?: unknown, metadata?: Record<string, any>, context?: LogContext): void {
    const errorInfo = error ? extractErrorInfo(error) : undefined;
    this.log(LogLevel.ERROR, message, { ...metadata, error: errorInfo }, context);
  }

  /**
   * Log performance metrics
   */
  performance(metrics: PerformanceMetrics, context?: LogContext): void {
    this.log(LogLevel.INFO, `Performance: ${metrics.operation} took ${metrics.duration}ms`, 
      undefined, { ...context, performance: metrics });
  }

  /**
   * Time an operation and log performance metrics
   */
  async timeOperation<T>(
    operation: () => Promise<T>,
    operationName: string,
    context?: LogContext
  ): Promise<T> {
    const startTime = process.hrtime.bigint();
    const startTimestamp = Date.now();

    try {
      const result = await operation();
      const endTime = process.hrtime.bigint();
      const duration = Number(endTime - startTime) / 1000000; // Convert to milliseconds

      this.performance({
        operation: operationName,
        duration,
        timestamp: startTimestamp,
        requestId: context?.requestId || this.context.requestId
      }, context);

      return result;
    } catch (error) {
      const endTime = process.hrtime.bigint();
      const duration = Number(endTime - startTime) / 1000000;

      this.performance({
        operation: `${operationName} (failed)`,
        duration,
        timestamp: startTimestamp,
        requestId: context?.requestId || this.context.requestId,
        metadata: { error: true }
      }, context);

      throw error;
    }
  }

  /**
   * Create a child logger with additional context
   */
  child(context: LogContext): Logger {
    const childLogger = new Logger(this.config);
    childLogger.setContext({ ...this.context, ...context });
    return childLogger;
  }

  /**
   * Main logging method
   */
  private log(
    level: LogLevel,
    message: string,
    metadata?: Record<string, any>,
    context?: LogContext
  ): void {
    // Check if we should log at this level
    if (level < this.config.level) {
      return;
    }

    const entry: LogEntry = {
      timestamp: Date.now(),
      level,
      message,
      context: { ...this.context, ...context },
      metadata
    };

    // Sanitize in production if enabled
    if (this.config.sanitizeInProduction && this.isProduction()) {
      this.sanitizeEntry(entry);
    }

    // Output to console
    if (this.config.enableConsole) {
      this.writeToConsole(entry);
    }

    // Output to file
    if (this.config.enableFile && this.config.filePath) {
      this.writeToFile(entry);
    }
  }

  /**
   * Write log entry to console
   */
  private writeToConsole(entry: LogEntry): void {
    const levelName = LOG_LEVEL_NAMES[entry.level];
    const timestamp = this.formatTimestamp(entry.timestamp);

    if (this.config.outputFormat === 'json') {
      console.log(JSON.stringify(entry));
      return;
    }

    // Text format
    const color = this.config.enableColors ? LOG_LEVEL_COLORS[entry.level] : '';
    const reset = this.config.enableColors ? '\x1b[0m' : '';

    let output = `${color}[${timestamp}] ${levelName}${reset}: ${entry.message}`;

    // Add context information
    if (entry.context && Object.keys(entry.context).length > 0) {
      const contextStr = Object.entries(entry.context)
        .filter(([_, value]) => value !== undefined)
        .map(([key, value]) => `${key}=${value}`)
        .join(' ');
      output += ` | ${contextStr}`;
    }

    // Add metadata
    if (entry.metadata && Object.keys(entry.metadata).length > 0) {
      output += ` | ${JSON.stringify(entry.metadata)}`;
    }

    console.log(output);

    // Log stack trace for errors if enabled
    if (entry.level === LogLevel.ERROR && 
        this.config.enableStackTrace && 
        entry.metadata?.error?.stack) {
      console.log(entry.metadata.error.stack);
    }
  }

  /**
   * Write log entry to file
   */
  private writeToFile(entry: LogEntry): void {
    if (!this.config.filePath) return;

    try {
      // Check file size and rotate if necessary
      this.rotateLogsIfNeeded();

      const logLine = this.config.outputFormat === 'json' 
        ? JSON.stringify(entry) + '\n'
        : this.formatTextEntry(entry) + '\n';

      fs.appendFileSync(this.config.filePath, logLine, 'utf8');
    } catch (error) {
      // If we can't write to file, log to console instead
      console.error('Failed to write to log file:', error);
    }
  }

  /**
   * Format log entry as text
   */
  private formatTextEntry(entry: LogEntry): string {
    const timestamp = this.formatTimestamp(entry.timestamp);
    const levelName = LOG_LEVEL_NAMES[entry.level];

    let output = `[${timestamp}] ${levelName}: ${entry.message}`;

    if (entry.context && Object.keys(entry.context).length > 0) {
      const contextStr = Object.entries(entry.context)
        .filter(([_, value]) => value !== undefined)
        .map(([key, value]) => `${key}=${value}`)
        .join(' ');
      output += ` | ${contextStr}`;
    }

    if (entry.metadata && Object.keys(entry.metadata).length > 0) {
      output += ` | ${JSON.stringify(entry.metadata)}`;
    }

    return output;
  }

  /**
   * Format timestamp according to configuration
   */
  private formatTimestamp(timestamp: number): string {
    switch (this.config.timestampFormat) {
      case 'epoch':
        return timestamp.toString();
      case 'readable':
        return new Date(timestamp).toLocaleString();
      case 'iso':
      default:
        return new Date(timestamp).toISOString();
    }
  }

  /**
   * Sanitize log entry for production
   */
  private sanitizeEntry(entry: LogEntry): void {
    // Remove or redact sensitive information
    if (entry.context) {
      entry.context = this.sanitizeObject(entry.context);
    }

    if (entry.metadata) {
      entry.metadata = this.sanitizeObject(entry.metadata);
    }
  }

  /**
   * Sanitize an object by removing sensitive fields
   */
  private sanitizeObject(obj: Record<string, any>): Record<string, any> {
    const sensitiveFields = ['password', 'token', 'secret', 'key', 'auth', 'credential'];
    const sanitized: Record<string, any> = {};

    for (const [key, value] of Object.entries(obj)) {
      if (sensitiveFields.some(field => key.toLowerCase().includes(field.toLowerCase()))) {
        sanitized[key] = '[REDACTED]';
      } else if (value && typeof value === 'object') {
        sanitized[key] = this.sanitizeObject(value);
      } else {
        sanitized[key] = value;
      }
    }

    return sanitized;
  }

  /**
   * Check if we're in production environment
   */
  private isProduction(): boolean {
    return process.env.NODE_ENV === 'production';
  }

  /**
   * Ensure log directory exists
   */
  private ensureLogDirectory(): void {
    if (!this.config.filePath) return;

    const dir = path.dirname(this.config.filePath);
    if (!fs.existsSync(dir)) {
      fs.mkdirSync(dir, { recursive: true });
    }
  }

  /**
   * Rotate logs if file size exceeds limit
   */
  private rotateLogsIfNeeded(): void {
    if (!this.config.filePath || !this.config.maxFileSize) return;

    try {
      const stats = fs.statSync(this.config.filePath);
      if (stats.size >= this.config.maxFileSize) {
        this.rotateLogs();
      }
    } catch (error) {
      // File doesn't exist yet, no need to rotate
    }
  }

  /**
   * Rotate log files
   */
  private rotateLogs(): void {
    if (!this.config.filePath || !this.config.maxFiles) return;

    const basePath = this.config.filePath;
    const ext = path.extname(basePath);
    const nameWithoutExt = basePath.slice(0, -ext.length);

    // Rotate existing files
    for (let i = this.config.maxFiles - 1; i >= 1; i--) {
      const oldFile = `${nameWithoutExt}.${i}${ext}`;
      const newFile = `${nameWithoutExt}.${i + 1}${ext}`;

      try {
        if (fs.existsSync(oldFile)) {
          if (i === this.config.maxFiles - 1) {
            fs.unlinkSync(oldFile); // Delete oldest file
          } else {
            fs.renameSync(oldFile, newFile);
          }
        }
      } catch (error) {
        // Ignore rotation errors
      }
    }

    // Move current file to .1
    try {
      if (fs.existsSync(basePath)) {
        fs.renameSync(basePath, `${nameWithoutExt}.1${ext}`);
      }
    } catch (error) {
      // Ignore rotation errors
    }
  }

  /**
   * Update logger configuration
   */
  updateConfig(config: Partial<LoggerConfig>): void {
    this.config = { ...this.config, ...config };

    if (this.config.enableFile && this.config.filePath) {
      this.ensureLogDirectory();
    }
  }

  /**
   * Get current configuration
   */
  getConfig(): LoggerConfig {
    return { ...this.config };
  }
}

/**
 * Default logger instance
 */
let defaultLogger: Logger;

/**
 * Initialize the default logger
 */
export function initializeLogger(config: Partial<LoggerConfig> = {}): Logger {
  // Get configuration from environment variables
  const envConfig: Partial<LoggerConfig> = {
    level: parseLogLevel(process.env.LOG_LEVEL),
    enableConsole: process.env.LOG_CONSOLE !== 'false',
    enableFile: process.env.LOG_FILE === 'true',
    filePath: process.env.LOG_FILE_PATH,
    enableColors: process.env.LOG_COLORS !== 'false',
    enableStackTrace: process.env.LOG_STACK_TRACE !== 'false',
    outputFormat: (process.env.LOG_FORMAT as 'json' | 'text') || 'text',
    sanitizeInProduction: process.env.LOG_SANITIZE !== 'false'
  };

  defaultLogger = new Logger({ ...envConfig, ...config });
  return defaultLogger;
}

/**
 * Get the default logger instance
 */
export function getLogger(): Logger {
  if (!defaultLogger) {
    defaultLogger = initializeLogger();
  }
  return defaultLogger;
}

/**
 * Parse log level from string
 */
export function parseLogLevel(level?: string): LogLevel {
  if (!level) return LogLevel.INFO;

  const levelUpper = level.toUpperCase();
  switch (levelUpper) {
    case 'DEBUG': return LogLevel.DEBUG;
    case 'INFO': return LogLevel.INFO;
    case 'WARN': case 'WARNING': return LogLevel.WARN;
    case 'ERROR': return LogLevel.ERROR;
    case 'SILENT': case 'NONE': return LogLevel.SILENT;
    default: return LogLevel.INFO;
  }
}

/**
 * Convenience logging functions using the default logger
 */
export const log = {
  debug: (message: string, metadata?: Record<string, any>, context?: LogContext) => 
    getLogger().debug(message, metadata, context),

  info: (message: string, metadata?: Record<string, any>, context?: LogContext) => 
    getLogger().info(message, metadata, context),

  warn: (message: string, metadata?: Record<string, any>, context?: LogContext) => 
    getLogger().warn(message, metadata, context),

  error: (message: string, error?: unknown, metadata?: Record<string, any>, context?: LogContext) => 
    getLogger().error(message, error, metadata, context),

  performance: (metrics: PerformanceMetrics, context?: LogContext) => 
    getLogger().performance(metrics, context),

  timeOperation: <T>(operation: () => Promise<T>, operationName: string, context?: LogContext) => 
    getLogger().timeOperation(operation, operationName, context)
};

/**
 * Performance timing utility
 */
export class PerformanceTimer {
  private startTime: bigint;
  private startTimestamp: number;

  constructor(private operation: string, private logger?: Logger) {
    this.startTime = process.hrtime.bigint();
    this.startTimestamp = Date.now();
  }

  /**
   * Stop the timer and log the performance metrics
   */
  stop(context?: LogContext, metadata?: Record<string, any>): number {
    const endTime = process.hrtime.bigint();
    const duration = Number(endTime - this.startTime) / 1000000;

    const metrics: PerformanceMetrics = {
      operation: this.operation,
      duration,
      timestamp: this.startTimestamp,
      requestId: context?.requestId,
      metadata
    };

    (this.logger || getLogger()).performance(metrics, context);
    return duration;
  }
}

/**
 * Statistical utility functions for analytics calculations
 * 
 * This module provides production-ready statistical methods to replace
 * placeholder implementations in analytics tools.
 */

/**
 * Statistical summary for a dataset
 */
export interface StatisticalSummary {
  mean: number;
  median: number;
  mode: number[];
  standardDeviation: number;
  variance: number;
  min: number;
  max: number;
  range: number;
  quartiles: {
    q1: number;
    q2: number;
    q3: number;
    iqr: number;
  };
  count: number;
}

/**
 * Correlation analysis result
 */
export interface CorrelationAnalysis {
  pearsonCorrelation: number;
  spearmanCorrelation: number;
  significance: number;
  strength: 'very weak' | 'weak' | 'moderate' | 'strong' | 'very strong';
}

/**
 * Time series analysis result
 */
export interface TimeSeriesAnalysis {
  trend: {
    slope: number;
    intercept: number;
    rSquared: number;
    direction: 'increasing' | 'decreasing' | 'stable';
  };
  seasonality: {
    detected: boolean;
    period?: number;
    strength?: number;
  };
  changePoints: Array<{
    index: number;
    significance: number;
  }>;
}

/**
 * Calculate comprehensive statistical summary for a dataset
 */
export function calculateStatisticalSummary(values: number[]): StatisticalSummary {
  if (values.length === 0) {
    throw new Error('Cannot calculate statistics for empty dataset');
  }

  const sorted = [...values].sort((a, b) => a - b);
  const n = values.length;

  // Basic statistics
  const sum = values.reduce((acc, val) => acc + val, 0);
  const mean = sum / n;
  const min = sorted[0];
  const max = sorted[n - 1];
  const range = max - min;

  // Median
  const median = n % 2 === 0 
    ? (sorted[n / 2 - 1] + sorted[n / 2]) / 2
    : sorted[Math.floor(n / 2)];

  // Mode(s)
  const frequency = new Map<number, number>();
  values.forEach(val => {
    frequency.set(val, (frequency.get(val) || 0) + 1);
  });
  const maxFreq = Math.max(...frequency.values());
  const mode = Array.from(frequency.entries())
    .filter(([_, freq]) => freq === maxFreq)
    .map(([val, _]) => val);

  // Variance and standard deviation
  const variance = values.reduce((acc, val) => acc + Math.pow(val - mean, 2), 0) / n;
  const standardDeviation = Math.sqrt(variance);

  // Quartiles
  const q1 = calculatePercentile(sorted, 25);
  const q2 = median;
  const q3 = calculatePercentile(sorted, 75);
  const iqr = q3 - q1;

  return {
    mean: Math.round(mean * 1000) / 1000,
    median: Math.round(median * 1000) / 1000,
    mode,
    standardDeviation: Math.round(standardDeviation * 1000) / 1000,
    variance: Math.round(variance * 1000) / 1000,
    min,
    max,
    range,
    quartiles: { q1, q2, q3, iqr },
    count: n
  };
}

/**
 * Calculate percentile for a sorted dataset
 */
export function calculatePercentile(sortedValues: number[], percentile: number): number {
  if (percentile < 0 || percentile > 100) {
    throw new Error('Percentile must be between 0 and 100');
  }

  const n = sortedValues.length;
  const index = (percentile / 100) * (n - 1);
  const lower = Math.floor(index);
  const upper = Math.ceil(index);

  if (lower === upper) {
    return sortedValues[lower];
  }

  const weight = index - lower;
  return sortedValues[lower] * (1 - weight) + sortedValues[upper] * weight;
}

/**
 * Calculate Pearson correlation coefficient
 */
export function calculatePearsonCorrelation(xValues: number[], yValues: number[]): number {
  if (xValues.length !== yValues.length || xValues.length < 2) {
    throw new Error('Arrays must have equal length and at least 2 elements');
  }

  const n = xValues.length;
  const sumX = xValues.reduce((sum, val) => sum + val, 0);
  const sumY = yValues.reduce((sum, val) => sum + val, 0);
  const sumXY = xValues.reduce((sum, val, i) => sum + val * yValues[i], 0);
  const sumXX = xValues.reduce((sum, val) => sum + val * val, 0);
  const sumYY = yValues.reduce((sum, val) => sum + val * val, 0);

  const numerator = n * sumXY - sumX * sumY;
  const denominator = Math.sqrt((n * sumXX - sumX * sumX) * (n * sumYY - sumY * sumY));

  if (denominator === 0) return 0;

  return Math.max(-1, Math.min(1, numerator / denominator));
}

/**
 * Calculate Spearman rank correlation coefficient
 */
export function calculateSpearmanCorrelation(xValues: number[], yValues: number[]): number {
  if (xValues.length !== yValues.length || xValues.length < 2) {
    throw new Error('Arrays must have equal length and at least 2 elements');
  }

  // Convert to ranks
  const xRanks = getRanks(xValues);
  const yRanks = getRanks(yValues);

  // Calculate Pearson correlation on ranks
  return calculatePearsonCorrelation(xRanks, yRanks);
}

/**
 * Convert values to ranks
 */
export function getRanks(values: number[]): number[] {
  const indexed = values.map((value, index) => ({ value, index }));
  indexed.sort((a, b) => a.value - b.value);

  const ranks = new Array(values.length);
  for (let i = 0; i < indexed.length; i++) {
    ranks[indexed[i].index] = i + 1;
  }

  return ranks;
}

/**
 * Perform comprehensive correlation analysis
 */
export function analyzeCorrelation(xValues: number[], yValues: number[]): CorrelationAnalysis {
  const pearson = calculatePearsonCorrelation(xValues, yValues);
  const spearman = calculateSpearmanCorrelation(xValues, yValues);

  // Calculate statistical significance (simplified)
  const n = xValues.length;
  const tStat = Math.abs(pearson) * Math.sqrt((n - 2) / (1 - pearson * pearson));
  const significance = 1 - (2 * normalCDF(-Math.abs(tStat))); // Two-tailed test

  // Determine correlation strength
  const absCorr = Math.abs(pearson);
  let strength: CorrelationAnalysis['strength'];
  if (absCorr < 0.1) strength = 'very weak';
  else if (absCorr < 0.3) strength = 'weak';
  else if (absCorr < 0.5) strength = 'moderate';
  else if (absCorr < 0.7) strength = 'strong';
  else strength = 'very strong';

  return {
    pearsonCorrelation: Math.round(pearson * 1000) / 1000,
    spearmanCorrelation: Math.round(spearman * 1000) / 1000,
    significance: Math.round(significance * 1000) / 1000,
    strength
  };
}

/**
 * Perform linear regression analysis
 */
export function performLinearRegression(xValues: number[], yValues: number[]): {
  slope: number;
  intercept: number;
  rSquared: number;
  residuals: number[];
} {
  if (xValues.length !== yValues.length || xValues.length < 2) {
    throw new Error('Arrays must have equal length and at least 2 elements');
  }

  const n = xValues.length;
  const sumX = xValues.reduce((sum, val) => sum + val, 0);
  const sumY = yValues.reduce((sum, val) => sum + val, 0);
  const sumXY = xValues.reduce((sum, val, i) => sum + val * yValues[i], 0);
  const sumXX = xValues.reduce((sum, val) => sum + val * val, 0);
  const _sumYY = yValues.reduce((sum, val) => sum + val * val, 0);

  const slope = (n * sumXY - sumX * sumY) / (n * sumXX - sumX * sumX);
  const intercept = (sumY - slope * sumX) / n;

  // Calculate R-squared
  const yMean = sumY / n;
  const ssTotal = yValues.reduce((sum, val) => sum + Math.pow(val - yMean, 2), 0);
  const residuals = yValues.map((y, i) => y - (slope * xValues[i] + intercept));
  const ssResidual = residuals.reduce((sum, res) => sum + res * res, 0);
  const rSquared = 1 - (ssResidual / ssTotal);

  return {
    slope: Math.round(slope * 1000) / 1000,
    intercept: Math.round(intercept * 1000) / 1000,
    rSquared: Math.round(rSquared * 1000) / 1000,
    residuals
  };
}

/**
 * Analyze time series data for trends and patterns
 */
export function analyzeTimeSeries(values: number[], timestamps?: number[]): TimeSeriesAnalysis {
  if (values.length < 3) {
    throw new Error('Time series must have at least 3 data points');
  }

  // Use indices if no timestamps provided
  const xValues = timestamps || Array.from({ length: values.length }, (_, i) => i);

  // Trend analysis using linear regression
  const regression = performLinearRegression(xValues, values);

  let direction: TimeSeriesAnalysis['trend']['direction'];
  if (Math.abs(regression.slope) < 0.01) direction = 'stable';
  else if (regression.slope > 0) direction = 'increasing';
  else direction = 'decreasing';

  // Simple seasonality detection (autocorrelation)
  const seasonality = detectSeasonality(values);

  // Change point detection (simplified)
  const changePoints = detectChangePoints(values);

  return {
    trend: {
      slope: regression.slope,
      intercept: regression.intercept,
      rSquared: regression.rSquared,
      direction
    },
    seasonality,
    changePoints
  };
}

/**
 * Detect seasonality in time series (simplified autocorrelation)
 */
function detectSeasonality(values: number[]): TimeSeriesAnalysis['seasonality'] {
  const maxPeriod = Math.floor(values.length / 3);
  let bestPeriod = 0;
  let maxCorrelation = 0;

  for (let period = 2; period <= maxPeriod; period++) {
    const correlations = [];

    for (let offset = 0; offset < values.length - period; offset++) {
      const val1 = values[offset];
      const val2 = values[offset + period];
      correlations.push(val1 * val2);
    }

    if (correlations.length > 0) {
      const correlation = correlations.reduce((sum, val) => sum + val, 0) / correlations.length;
      if (Math.abs(correlation) > Math.abs(maxCorrelation)) {
        maxCorrelation = correlation;
        bestPeriod = period;
      }
    }
  }

  const detected = Math.abs(maxCorrelation) > 0.3; // Threshold for seasonality

  return {
    detected,
    period: detected ? bestPeriod : undefined,
    strength: detected ? Math.abs(maxCorrelation) : undefined
  };
}

/**
 * Detect change points in time series
 */
function detectChangePoints(values: number[]): Array<{ index: number; significance: number }> {
  const changePoints = [];
  const windowSize = Math.max(3, Math.floor(values.length / 10));

  for (let i = windowSize; i < values.length - windowSize; i++) {
    const beforeMean = values.slice(i - windowSize, i)
      .reduce((sum, val) => sum + val, 0) / windowSize;
    const afterMean = values.slice(i, i + windowSize)
      .reduce((sum, val) => sum + val, 0) / windowSize;

    const difference = Math.abs(afterMean - beforeMean);
    const overall = calculateStatisticalSummary(values);
    const significance = difference / overall.standardDeviation;

    if (significance > 1.5) { // Threshold for significant change
      changePoints.push({
        index: i,
        significance: Math.round(significance * 100) / 100
      });
    }
  }

  return changePoints;
}

/**
 * Normal cumulative distribution function (approximation)
 */
function normalCDF(x: number): number {
  // Abramowitz and Stegun approximation
  const a1 =  0.254829592;
  const a2 = -0.284496736;
  const a3 =  1.421413741;
  const a4 = -1.453152027;
  const a5 =  1.061405429;
  const p  =  0.3275911;

  const sign = x < 0 ? -1 : 1;
  x = Math.abs(x) / Math.sqrt(2);

  const t = 1 / (1 + p * x);
  const y = 1 - (((((a5 * t + a4) * t) + a3) * t + a2) * t + a1) * t * Math.exp(-x * x);

  return 0.5 * (1 + sign * y);
}

/**
 * Calculate moving average
 */
export function calculateMovingAverage(values: number[], windowSize: number): number[] {
  if (windowSize <= 0 || windowSize > values.length) {
    throw new Error('Window size must be positive and not exceed array length');
  }

  const result = [];
  for (let i = 0; i <= values.length - windowSize; i++) {
    const window = values.slice(i, i + windowSize);
    const average = window.reduce((sum, val) => sum + val, 0) / windowSize;
    result.push(average);
  }

  return result;
}

/**
 * Calculate exponential moving average
 */
export function calculateExponentialMovingAverage(values: number[], alpha: number): number[] {
  if (alpha <= 0 || alpha > 1) {
    throw new Error('Alpha must be between 0 and 1');
  }

  const result = [values[0]];
  for (let i = 1; i < values.length; i++) {
    const ema = alpha * values[i] + (1 - alpha) * result[i - 1];
    result.push(ema);
  }

  return result;
}

/**
 * Normalize values to 0-100 scale
 */
export function normalizeToScale(values: number[], min: number = 0, max: number = 100): number[] {
  if (values.length === 0) return [];

  const dataMin = Math.min(...values);
  const dataMax = Math.max(...values);
  const range = dataMax - dataMin;

  if (range === 0) return values.map(() => min);

  return values.map(val => 
    min + ((val - dataMin) / range) * (max - min)
  );
}

/**
 * Calculate Z-scores for outlier detection
 */
export function calculateZScores(values: number[]): number[] {
  const stats = calculateStatisticalSummary(values);
  return values.map(val => (val - stats.mean) / stats.standardDeviation);
}

/**
 * Detect outliers using Z-score method
 */
export function detectOutliers(values: number[], threshold: number = 2.5): Array<{ index: number; value: number; zScore: number }> {
  const zScores = calculateZScores(values);
  const outliers: Array<{ index: number; value: number; zScore: number }> = [];

  zScores.forEach((zScore, index) => {
    if (Math.abs(zScore) > threshold) {
      outliers.push({
        index,
        value: values[index],
        zScore: Math.round(zScore * 100) / 100
      });
    }
  });

  return outliers;
}

/**
 * Bootstrap confidence interval
 */
export function bootstrapConfidenceInterval(
  values: number[], 
  statistic: (vals: number[]) => number,
  confidence: number = 0.95,
  iterations: number = 1000
): { lower: number; upper: number; estimate: number } {
  const bootstrapStats = [];

  for (let i = 0; i < iterations; i++) {
    // Bootstrap sample
    const sample = [];
    for (let j = 0; j < values.length; j++) {
      const randomIndex = Math.floor(Math.random() * values.length);
      sample.push(values[randomIndex]);
    }
    bootstrapStats.push(statistic(sample));
  }

  bootstrapStats.sort((a, b) => a - b);

  const alpha = 1 - confidence;
  const lowerIndex = Math.floor((alpha / 2) * iterations);
  const upperIndex = Math.floor((1 - alpha / 2) * iterations);

  return {
    lower: bootstrapStats[lowerIndex],
    upper: bootstrapStats[upperIndex],
    estimate: statistic(values)
  };
}
